var textForPages =["","2","2 3\u000d\u000a08\u000d\u000a09 \u000d\u000a17 \u000d\u000a30\u000d\u000a40\u000d\u000a47 \u000d\u000a60\u000d\u000a73 \u000d\u000a83 \u000d\u000a98\u000d\u000a106\u000d\u000a1. \ud504\ub864\ub85c\uadf8\u000d\u000a1-1. \uc778\uc0ac\ub9d0\u000d\u000a1-2. \ucde8\uc9c0\uc640 \uacbd\uacfc\u000d\u000a2. \uc120\uc815\uc774\uc720\uc640 \uc120\uc815\uc791 \ub0b4\uc6a9 \uc694\uc57d\u000d\u000a2-1. \ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4: \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 \uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0\u000d\u000a\ub300\ud55c \uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c\u000d\u000a2-2. \ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098: 2021 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801\u000d\u000a\uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac\u000d\u000a2-3. \uc131 \u00b7 \uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d\uae30\ud68d I: \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30\u000d\u000a2-4. \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uac00 \ub290\ub080 \uc77c\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654\u000d\u000a2-5. \ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c: \uc720\ud615\ubcc4\uc0ac\ub840\uc5f0\u000d\u000a\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c\u000d\u000a2-6. \ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4: \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c\u000d\u000a2-7. \uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798: \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c\u000d\u000a\uc548\ub0b4\uc11c\u000d\u000a2-8. \ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751:\uc775\uc0b0\uc7a5\u000d\u000a\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c\u000d\u000a2-9. \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \uc0b6\uc744 \uac00\ub85c\uc9c0\ub974\ub294 \ubcf5\ud569\ucc28\ubcc4\u000d\u000a2-10. \ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \uadf8\ub9bc\uc790 \ub178\ub3d9\u000d\u000a3. \uc5d0\ud544\ub85c\uadf8\u000d\u000a3-1. \ucee8\ud37c\ub7f0\uc2a4 \uc138\uc158\uc694\uc57d\u000d\u000a04\u000d\u000a05\u000d\u000a06\u000d\u000a117\u000d\u000a118\u000d\u000a\ubaa9\ucc28 & "," & 4\u000d\u000a1\u000d\u000a\ud504\ub864\ub85c\uadf8","4 5\u000d\u000a\uc0ac\ub2e8\ubc95\uc778 \uc2dc\ubbfc \uc774\uc0ac\uc7a5\u000d\u000a\uc0ac\ub2e8\ubc95\uc778 \uc2dc\ubbfc\uc774 \uc62c\ud574 \ucc98\uc74c\uc73c\ub85c \uc9c4\ud589\ud55c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \uacf5\ubaa8\uc804\uc740 \ubaa8\ub450\uac00 \uc544\ub294 \u000d\u000a\uc720\uba85\ud55c \uc0c1\uc774\uc5b4\uc11c \uc601\uc608\ub86d\uac70\ub098 \ud070 \uc0c1\uae08\uc774 \uc8fc\uc5b4\uc9c0\ub294 \uac83\uc740 \uc544\ub2c8\uc9c0\ub9cc, \ubb35\ubb35\ud788 \ud604\uc7a5\uc744 \uc77d\uace0 \uc138\u000d\u000a\uc0c1\uc744 \ubc14\uafb8\ub294 \uc5f0\uad6c\ub97c \uc218\ud589\ud558\ub294 \uc5ec\ub7ec\ubd84\ub4e4\uaed8 \ub4dc\ub9ac\ub294 \uc6b0\ub9ac\uc758 \uc791\uc740 \uaca9\ub824\uc785\ub2c8\ub2e4.\u000d\u000a\ud604\uc7a5\uc5d0\uc11c \uc2dc\uc791\ub41c \ubb38\uc81c\uc758\uc2dd\uc5d0 \uad00\uc2ec\uacfc \uad00\ucc30\uc758 \ub048\uc744 \ub193\uc9c0 \uc54a\uc73c\uba70, \uc2e4\ud5d8\uc801\uc774\uace0 \uc2e4\ucc9c\uc801\uc778 \uc5f0\u000d\u000a\uad6c\ub85c \ud604\uc7a5\uc744 \uc77d\uace0 \uc138\uc0c1\uc744 \ubc14\uafb8\uace0\uc790 \ud558\ub294 \ubaa8\ub4e0 \ubd84\ub4e4\uc744 \uc751\uc6d0\ud569\ub2c8\ub2e4. \u000d\u000a\uae30\ud6c4\uc704\uae30, \ubd88\ud3c9\ub4f1\uacfc \uc591\uadf9\ud654, \uc0ac\ud68c\uac08\ub4f1 \ub4f1 \uc6b0\ub9ac \uc55e\uc5d0 \ub193\uc740 \ub09c\uc81c\ub294 \uadfc\ubcf8\uc801\uc73c\ub85c \uc9c0\uae08\uae4c\uc9c0 \u000d\u000a\uc774\uc5b4\uc838 \uc628 \uc0ac\ud68c\uad6c\uc870\uc5d0\uc11c \ubed7\uc5b4 \ub098\uc628 \uacb0\uacfc\uc774\uae30\uc5d0 \uacfc\uac70\uc640 \uac19\uc740 \ubc29\uc2dd\uc73c\ub85c \ud574\uacb0\ud560 \uc218 \uc5c6\uc2b5\ub2c8\u000d\u000a\ub2e4. \uc6b0\ub9ac\ub294 \uc774 \ubb38\uc81c\ub4e4\uc744 \ud574\uacb0\ud558\uae30 \uc704\ud55c \uc804\ud658\uc801 \uc0ac\ud68c\ud601\uc2e0\uc758 \ucd9c\ubc1c\uc810\uc744 \ud604\uc7a5\uc5d0\uc11c \uc2dc\uc791\ub41c \ubb38\u000d\u000a\uc81c\uc758\uc2dd\uacfc \uc2e4\ud5d8\uc801\uc774\uace0 \uc2e4\ucc9c\uc801\uc778 \ud65c\ub3d9\uc5d0\uc11c \ucc3e\uace0\uc790 \ud569\ub2c8\ub2e4. \u000d\u000a\uc55e\uc73c\ub85c\ub3c4 \ud604\uc7a5\uc5d0\uc11c \uc2dc\uc791\ub41c \uc5f0\uad6c\uc640 \uc2e4\ucc9c\uc801 \uacb0\uacfc\ub4e4\uc774 \uc9c0\uc18d\uc801\uc73c\ub85c \ub098\uc624\uae38 \ubc14\ub77c\uba70, <\ud604\uc7a5\u000d\u000a\uc9c0\uc2ddx\uc88b\uc740\uc5f0\uad6c>\uac00 \uc774\ub860\uacfc \uc2e4\ucc9c\uc758 \ubd84\ub9ac, \ud604\uc7a5\uacfc \uc9c0\uc2dd\uc758 \ub2e8\uc808\uc744 \ub118\uc5b4 \uc0ac\ud68c\ubcc0\ud654\ub97c \ub9cc\ub4e4\uc5b4\u000d\u000a\uac00\ub294 \uc2e4\ucc9c\uc801 \uc5f0\uad6c\ud65c\ub3d9\uacfc \uadf8 \uacb0\uacfc\ubb3c\uc744 \ubaa8\uc73c\uace0, \ub610 \uc11c\ub85c\ub97c \uc774\uc5b4\uc8fc\ub294 \uc7a5\uc774 \ub420 \uc218 \uc788\ub3c4\ub85d \ud558\u000d\u000a\uaca0\uc2b5\ub2c8\ub2e4. \u000d\u000a2023\ub144 1\uc6d4\u000d\u000a\uc591\ud601\uc2b9\u000d\u000a<\ud604\uc7a5\uc9c0\uc2ddx\uc88b\uc740\uc5f0\uad6c>\uc5d0 \uc120\uc815\ub41c \uc5f0\uad6c\uc640\u000d\u000a \uc5f0\uad6c\uc790\ubd84\ub4e4\uaed8 \ucd95\ud558\uc758 \uc778\uc0ac\ub97c \uc804\ud569\ub2c8\ub2e4.\u000d\u000a\uc778\uc0ac\ub9d0"," & 6\u000d\u000a\ucde8\uc9c0\uc640 \u000d\u000a\uacbd\uacfc\u000d\u000a<\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\ub294 \ud65c\ub3d9\uac00\uc640 \uc5f0\uad6c\uc790\ub4e4\uc774 \ud604\uc7a5\uc5f0\uad6c, \uc2e4\ud589\uc5f0\uad6c, \ucc38\uc5ec\uc5f0\uad6c, \uacf5\ub3d9\uc5f0\u000d\u000a\uad6c, \ud559\uc2b5\uacfc \uc2e4\ud5d8 \ub4f1 \ub2e4\uc591\ud55c \ubc29\uc2dd\uc744 \ud1b5\ud574 \ub300\uc548\uc9c0\uc2dd\uc758 \uc0dd\uc0b0\uacfc \uacf5\uc720\ub97c \uc9c0\ud5a5\ud558\uace0, \uc804\ud658\uc801 \uc0ac\ud68c\u000d\u000a\ud601\uc2e0\uc744 \ucd94\uad6c\ud558\ub294 \ubaa8\ub4e0 \uc885\ub958\uc758 \uc2e4\ucc9c\uc801 \uc5f0\uad6c \ud65c\ub3d9\uacfc \uadf8 \uacb0\uacfc\ubb3c\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4.\u000d\u000a\uc0ac\ub2e8\ubc95\uc778 \uc2dc\ubbfc\uc740 \uacf5\uac1c \ucd94\ucc9c\uacfc \uc790\uccb4 \ubc1c\uad74 \uacfc\uc815(10\uc6d4 1\uc77c~10\uc6d4 28\uc77c)\uc744 \uac70\uccd0 <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\u000d\u000a\uad6c> \uacf5\ubaa8\uc804\uc5d0 \ucd1d 237\ud3b8\uc758 \uc5f0\uad6c \uacb0\uacfc\ubb3c\uc744 \ucde8\ud569\ud588\uc2b5\ub2c8\ub2e4. <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\uc758 \ucde8\uc9c0\uc5d0 \u000d\u000a\ubd80\ud569\ud558\ub294 \uc5f0\uad6c \uc131\uacfc\uac00 \ub9ce\uc544 10\ud3b8\uc744 \uc120\uc815\ud558\ub294 \uc791\uc5c5\uc740 \uc27d\uc9c0 \uc54a\uc558\uc2b5\ub2c8\ub2e4. \uc120\uc815\uc704\uc6d0\ud68c\ub294 \ud604\uc7a5\u000d\u000a\uc131, \ub2f9\uc0ac\uc790\uc131\uacfc \uc8fc\uccb4\uc131, \uacf5\ub3d9\uc5f0\uad6c\uc640 \uc774\ud574\uad00\uacc4\uc790\uc640\uc758 \ud611\uc5c5 \uadf8\ub9ac\uace0 \uc0ac\ud68c\uc801 \ud568\uc758\uc640 \ud65c\uc6a9\ub3c4\uc5d0 \u000d\u000a\uc8fc\ubaa9\ud574 \ucd5c\uc885 \uc120\uc815\uc744 \ub9c8\ucce4\uc2b5\ub2c8\ub2e4.(12\uc6d4 7\uc77c)\u000d\u000a<\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\uc5d0 \uc120\uc815\ub41c \uc5f0\uad6c\uc790\ub4e4\uc740 \u201c2023 \uc2dc\ubbfc\uc0ac\ud68c \ud604\uc7a5\uc9c0\uc2dd \ucee8\ud37c\ub7f0\uc2a4\u201d(\uc81c\uc8fc, \u000d\u000a2023\ub144 1\uc6d4 9\uc77c~11\uc77c) \uc138\uc158\uc5d0\uc11c \ucc38\uc5ec\ud558\uc5ec \uc120\uc815\uc791 \uc2dc\uc0c1\uc2dd\uacfc \ubc1c\ud45c\ud68c\ub97c \ud568\uaed8 \ud588\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 <\ud604\uc7a5\u000d\u000a\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\uc758 \uc131\uacfc\ub97c \ucd95\uc801\ud558\uace0 \uc9c0\uc2dd \uacf5\uc720\uc758 \uc800\ubcc0\uc744 \ud655\ub300\ud558\uace0\uc790 \uc120\uc815\uc774\uc720\uc640 \uc120\uc815\ub41c \u000d\u000a\uc5f0\uad6c\uc758 \ubb38\uc81c\uc758\uc2dd, \uc8fc\uc694 \ub0b4\uc6a9, \ud559\ubb38\uc801 \u00b7 \uc0ac\ud68c\uc801 \ubc18\uc751\uacfc \ud65c\uc6a9, \uc131\ucc30\uacfc \ud6c4\uae30 \ub4f1\uc774 \ub2f4\uae34 e-book\u000d\u000a\uc744 \uc81c\uc791\ud588\uc2b5\ub2c8\ub2e4. \uc55e\uc73c\ub85c <\ud604\uc7a5\uc9c0\uc2dd\uacfc \uc88b\uc740\uc5f0\uad6c>\uac00 \ub2e4\uc591\ud55c \uc601\uc5ed\uc5d0\uc11c \uadf8\ub9ac\uace0 \uc0ac\ud68c \uacf3\uacf3\uc5d0\u000d\u000a\uc11c \ud65c\uc131\ud654\ub418\uae38 \uae30\ub300\ud569\ub2c8\ub2e4.\u000d\u000a\uae40\uc18c\uc5f0 (\uacbd\ud76c\ub300\ud559\uad50 \uacf5\uacf5\ub300\ud559\uc6d0 \uacb8\uc784\uad50\uc218), \uae40\uc720\ub9ac (\uc11c\uc6b8\uc2dcNPO\uc9c0\uc6d0\uc13c\ud130 \uc815\ucc45\ud300\uc7a5), \u000d\u000a\uae40\uc724\ucca0 (\uacbd\ud76c\ub300\ud559\uad50 \ud6c4\ub9c8\ub2c8\ud0c0\uc2a4\uce7c\ub9ac\uc9c0 \uad50\uc218), \ubc15\ubc30\uade0 (\uc11c\uc6b8\ub300\ud559\uad50 \uc544\uc2dc\uc544\ub3c4\uc2dc\uc0ac\ud68c\uc13c\ud130\uc7a5),\u000d\u000a\uc774\uc601\uc7ac (\ud55c\uc591\ub300\ud559\uad50 \uc81c3\uc139\ud130\uc5f0\uad6c\uc18c \uc5f0\uad6c\uad50\uc218), \uc774\uc601\uc8fc (\uc544\ub984\ub2e4\uc6b4\uc7ac\ub2e8 \uc5f0\uad6c\uc0ac\uc5c5\ud300\uc7a5)\u000d\u000a\uc120\uc815\uc704\uc6d0\ud68c \uad6c\uc131\u000d\u000a\uc120\uc815\uc704\uc6d0\u000d\u000a\uac04\uc0ac\u000d\u000a\uc5c5\ubb34\uc9c0\uc6d0 \uae40\uc2b9\uc21c(\uc0ac\ub2e8\ubc95\uc778 \uc2dc\ubbfc \uc2e4\uc7a5)\u000d\u000a\uc774\uc815\ud544 (\uc5d0\ub108\uc9c0\uae30\ud6c4\uc815\ucc45\uc5f0\uad6c\uc18c \uc18c\uc7a5), \ubc15\uc9c0\ud6c8 (\uc911\uc559\ub300\ud559\uad50 \uc911\uc559\uc0ac\ud559\uc5f0\uad6c\uc18c \uc5f0\uad6c\uad50\uc218)","6 7\u000d\u000a[ \uc120\uc815\uacb0\uacfc ]\u000d\u000a\uc81c\ubaa9 \uc800\uc790 \ubc1c\ud589\uae30\uad00 \ubc1c\ud589\uc5f0\ub3c4\u000d\u000a\ubc14\ucffc\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4 : \u000d\u000a\ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 \uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0 \ub300\ud55c \u000d\u000a\uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c\u000d\u000a\ud558\uae08\ucca0 \uc11c\uc6b8\uc2dc\u000d\u000aNPO\uc9c0\uc6d0\uc13c\ud130 2020\u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774\ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 :\u000d\u000a2021\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac\u000d\u000a\uc815\uc131\uc870, \uae40\ubcf4\ubbf8\u000d\u000a\uc2ec\uae30\uc6a9, \ud55c\uc131\uc9c4\u000d\u000a\ub2e4\uc591\uc131\uc744 \ud5a5\ud55c\u000d\u000a\uc9c0\uc18d\uac00\ub2a5\ud55c\u000d\u000a\uc6c0\uc9c1\uc784 \ub2e4\uc6c0\u000d\u000a2022\u000d\u000a\uc131\u00b7\uc7ac\uc0dd\uc0b0\uac74\uac15 \uc5f0\uc18d\uae30\ud68dI : \u000d\u000a\uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\uc5d0\uc11c \ubcf4\uc7a5\ud558\uae30\u000d\u000a\uae40\uc0c8\ub86c, \ubc15\uc9c0\uc6d0\u000d\u000a\ubb38\uc8fc\ud604, \uc591\uc608\uc2ac\u000d\u000a\uc624\ub85c\ub77c, \uc7a5\uc740\uc9c0\u000d\u000a(\uc0ac)\uc2dc\ubbfc\uac74\uac15\uc5f0\uad6c\uc18c 2022\u000d\u000a\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uac00 \ub290\ub080 \uc77c\uc758 \uac00\uce58\uc640\u000d\u000a\uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654 \ubb38\uc608\ucc2c, \ucd5c\uc591\uc21c \uc11c\uc6b8\uc2dc\u000d\u000a50\ud50c\ub7ec\uc2a4\uc7ac\ub2e8 2021\u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c :\u000d\u000a\uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c \ub958\ud55c\uc2b9 \uc11c\uc6b8\uc2dc\u000d\u000aNPO\uc9c0\uc6d0\uc13c\ud130 2019\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 : \u000d\u000a\uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c \uae40\uc6b0\ucc3d \uc7ac\ub2e8\ubc95\uc778 \uc232\uacfc\ub098\ub214 2022\u000d\u000a\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798 :\u000d\u000a\ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c \uc774\uc6a9\uc11d \uc11c\uc6b8\uc2dc\u000d\u000aNPO\uc9c0\uc6d0\uc13c\ud130 2019\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 :\u000d\u000a\uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\uad50\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c \uae40\ub3c4\uade0 \ud55c\uad6dNGO\ud559\ud68c 2021\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\ub97c \uc0b6\uacfc \uac00\ub85c\uc9c0\ub974\ub294 \ubcf5\ud569\ucc28\ubcc4 \ud55c\uad6d\uc5ec\uc131\u000d\u000a\ub2e8\uccb4\uc5f0\ud569 \ud55c\uad6d\uc5ec\uc131\ub2e8\uccb4\uc5f0\ud569 2022\u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \u000d\u000a\uadf8\ub9bc\uc790 \ub178\ub3d9 \ubc15\uc218\ubbfc \ube44\ud310\uc0ac\ud68c\ud559\ud68c 2021"," & 8\u000d\u000a2\u000d\u000a\uc120\uc815\uc774\uc720\uc640 \uc120\uc815\uc791 \ub0b4\uc6a9 \uc694\uc57d","8 9\u000d\u000a\ud558\uae08\ucca0 / \uc11c\uc6b8\uc2dcNPO\uc9c0\uc6d0\uc13c\ud130, \ud65c\ub825\ud5a5\uc5f0, 2020\u000d\u000a\ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4 :\u000d\u000a\ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 \uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0 \ub300\ud55c \uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c \u000d\u000a1. \uc5f0\uad6c\uc758 \uacc4\uae30\u000d\u000a\uc800\ub294 2011\ub144\ubd80\ud130 2020\ub144\uae4c\uc9c0, \uc7a5\uc560\uc778\uc57c\ud559 \uad50\uc0ac, \uc7a5\uc560\uc778\ub2e8\uccb4 \ud65c\ub3d9\uac00, \uc7a5\uc560\uc778\uc5b8\ub860 \uae30\uc790, \u000d\u000a\uc7a5\uc560\uc778\ud65c\ub3d9\uc9c0\uc6d0\uc0ac \ub4f1\uc73c\ub85c \uc77c\ud574\uc654\uc2b5\ub2c8\ub2e4. \uadf8 \uc0ac\uc774 \ub300\ud559\uc6d0 \uc11d\uc0ac\ud559\uc704\ub97c \ubc1b\uace0 \ubc15\uc0ac\uacfc\uc815\uc744 \uc218\u000d\u000a\ub8cc\ud558\uae30\ub3c4 \ud588\uc9c0\ub9cc, \uc5b8\uc81c\ub098 \uc800\uc758 1\ucc28\uc801\uc778 \uc815\uccb4\uc131\uc740 \u2018\uc7a5\uc560\uc778\uc6b4\ub3d9 \ud65c\ub3d9\uac00\u2019\uc600\uc2b5\ub2c8\ub2e4. \uadf8 \ub355\ubd84\uc5d0 \u000d\u000a\ub300\ud559\uc6d0 \uacf5\ubd80\ub97c \ud558\uba74\uc11c \uac00\uc9c4 \uac00\uc7a5 \ud070 \uad00\uc2ec\uc0ac \uc5ed\uc2dc \uc7a5\uc560\uc640 \ube48\uace4, \uc218\uc6a9\uc2dc\uc124 \ub4f1\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc218\u000d\u000a\uc6a9\uc2dc\uc124 \ubb38\uc81c\ub85c \uc11d\uc0ac\ub17c\ubb38\uc744 \ub9c8\uce5c \ud6c4, \uc9c0\ub09c 10\uc5ec\ub144 \uac04 \ud65c\ub3d9\ud558\uba74\uc11c \uacc1\uc5d0\uc11c \uc9c0\ucf1c\ubd24\ub358 \uc7a5\uc560\uc778\u000d\u000a\uc6b4\ub3d9 \ub3d9\ub8cc\ub4e4\uc758 \ud65c\ub3d9\uc744 \uc5ed\uc0ac \uae30\ub85d\uc73c\ub85c \ub0a8\uae30\ub294 \uc791\uc5c5\uc744 \ud558\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \u000d\u000a\uadf8\ub7f0 \ubb38\uc81c\uc758\uc2dd\uc5d0\uc11c 2019\ub144 \u20188-90\ub144\ub300 \uc7a5\uc560\uc778\uc6b4\ub3d9 \ud65c\ub3d9\uac00 \uad6c\uc220\ucc44\ub85d \uc0ac\uc5c5\u2019\uc744 \ud1b5\ud574 \uadf8 \uccab\u000d\u000a\uc0ac\ud68c\uc6b4\ub3d9\uc801 \uc2e4\ucc9c\uc5d0 \uc788\uc5b4 \uac00\uc7a5 \uc911\uc694\ud55c \uc804\uc81c\uac00 \uc788\ub2e4. \ud604\uc7a5-\uc0ac\ub78c-\uc0b6-\ucc98\uc9c0\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \ubc14\ub85c \uadf8\u000d\u000a\uac83\uc774\ub2e4. \uc774\uac83\uc774 \ube60\uc9c4 \uc0ac\ud68c\uc6b4\ub3d9\uc774 \uc131\uacf5\ud55c \uc608\ub97c \ubcf4\uae30 \uc5b4\ub835\ub2e4. \ub3c4\uc2dd\ud654\ub41c \uc774\ub150\uc801 \ubaa9\ud45c\uc640 \uadf8\uac83\uc744 \uc815\ub2f9\ud654\u000d\u000a\ud55c \ub2f4\ub860\uc5d0 \uae30\ucd08\ud574 \ud604\uc2e4\uc744 \uba3c\uc800 \uc7ac\ub2e8\ud558\uace0, \ubb3c\ub9ac\uccd0\uc57c \ud560 \uc801\uc744 \ub9cc\ub4e4\uc5b4 \uc2a4\uc2a4\ub85c\ub97c \uac00\uc0c1\uc758 \uc138\uacc4\uc5d0 \uace0\ub9bd\uc2dc\u000d\u000a\ud0a4\uae30 \ub54c\ubb38\uc774\ub2e4. \ubbf8\uad6d\uc758 \uc800\uba85\ud55c \uc815\uce58\ud559\uc790\ub85c\uc11c \uc815\ub2f9\ub860\uacfc \uac08\ub4f1\ub860\uc758 \ub300\uac00\uc778 \uc0e4\uce20\uc288\ub098\uc774\ub354\ub294 \ubbfc\uc8fc\uc8fc\uc758\u000d\u000a\ub97c \uc704\ud574 \uc0ac\ub78c\ub4e4\uc774 \uc0b4\uace0 \uc788\ub294 \uac8c \uc544\ub2c8\ub77c, \uc0b4\uace0 \uc788\ub294 \uc0ac\ub78c\ub4e4\uc744 \uc704\ud574 \ubbfc\uc8fc\uc8fc\uc758\uac00 \uc788\ub2e4\uace0 \ud588\ub2e4. \ub2f9\uc5f0\ud55c \u000d\u000a\uac83 \uac19\uc740\ub370, \uc0ac\ud68c\uc6b4\ub3d9 \uacfc\uc815\uc5d0\uc11c \ub108\ubb34\ub098 \uc27d\uac8c \uc78a\uc5b4\ubc84\ub9ac\ub294 \uc6d0\ub9ac\uc774\uae30\ub3c4 \ud558\ub2e4. \u000d\u000a<\ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4>\ub294 \uc0ac\ud68c\uc6b4\ub3d9\uc774 \uadf8\ub7f0 \uc2e4\ucc45\uc744 \ubc94\ud558\uc9c0 \uc54a\uae30 \uc704\ud574 \ud639\uc740 \uad50\uc815\ud558\uae30 \uc704\ud574\uc11c\u000d\u000a\ub294 \uc5b4\ucc0c\ud574\uc57c \ud558\ub294\uc9c0\ub97c \uc0dd\uc0dd\ud558\uac8c \uc81c\uc2dc\ud574\uc900\ub2e4. \uc0ac\ud68c\uc6b4\ub3d9\uc758 \uc2dc\uc791\uacfc \ub05d\uc740 \u2018\uc0ac\ub78c\uc758 \uc0b6\u2019\uc774\uba70, \uadf8 \uacfc\uc815\uc740 \u000d\u000a\ub2e4\ub984 \uc544\ub2cc \u2018\uc8fc\uccb4\ub418\uae30\u2019\ub77c\ub294 \uac83\uc744. \ud2b9\ud788 <\ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4>\uc5d0\uc11c \uc8fc\ubaa9\ud560 \uac83\uc740 \uc0dd\uc560\uc0ac\uc801 \uc811\uadfc\u000d\u000a\uc744 \ud558\uace0 \uc788\ub2e4\ub294 \uc810\uc774\ub2e4. \uc0ac\ub78c\uc758 \uc0b6\uacfc \uace0\ud1b5\uacfc \ud76c\ub9dd\uc744 \uac00\uc7a5 \uc798 \ub4dc\ub7ec\ub0b4\uc8fc\uae30 \uc704\ud55c \uac83\uc740 \uc0dd\uc560\uc5d0 \uad00\ud55c \u2018\uc11c\u000d\u000a\uc0ac(\uc774\uc57c\uae30)\u2019\uc774\uba70, \uadf8 \uc0dd\uc560\uc5d0 \uac78\uccd0 \uacaa\uace0 \uc811\ud55c \uccb4\ud5d8\uacfc \ub290\ub08c\uacfc \uc9c0\uc2dd\uc774 \uc2e4\ucc9c\uc801 \uc5d0\ub108\uc9c0\uc758 \ubd84\ucd9c\uc774\ub77c\ub294 \uc0ac\u000d\u000a\ud68c\uc6b4\ub3d9\uc744 \ub4f1\uc7a5\uc2dc\ud0a4\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub9ac\uace0 \uadf8 \ub4f1\uc7a5\uacfc\uc815\uc774 \ubc14\ub85c \uc8fc\uccb4\ub418\uae30\uc774\uae30 \ub54c\ubb38\uc774\ub2e4. \uc0dd\uc560\uc0ac\uc801 \uc811\uadfc\u000d\u000a\uc740 \uc0ac\ud68c\uc6b4\ub3d9\uc774 \uace0\ud1b5\uc744 \uacaa\ub294 \uc0ac\ub78c\ub4e4\uc744 \uadf8\ub4e4\uc758 \uc0b6\uacfc \uc6b0\ub9ac \uc0ac\ub294 \uc138\uacc4\uc758 \uba85\uc2e4\uc0c1\ubd80\ud55c \u2018\uc8fc\uc778\uacf5\u2019\uc73c\ub85c \uc870\uba85\u000d\u000a\ud560 \ub54c \uc720\uc758\ubbf8\ud55c \uc778\uac04\uc801-\uc0ac\ud68c\uc801 \uc2e4\ucc9c\uc77c \uc218 \uc788\uc74c\uc744 \uae68\ub2eb\uac8c \ud574\uc900\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720\u000d\u000a\ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4 : \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 \uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0 \ub300\ud55c \uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c "," & 10\u000d\u000a\ubc1c\uc744 \ub5d0\uc2b5\ub2c8\ub2e4. \uc7a5\uc560\uc778\uc6b4\ub3d9 \ud615\uc131 \ucd08\ucc3d\uae30\uc758, \uc8fc\ub85c \uc18c\uc544\ub9c8\ube44 \uc7a5\uc560\uc778 \ud65c\ub3d9\uac00\ub4e4\uc758 \uad6c\uc220\uc744 \ubc14\ud0d5\u000d\u000a\uc73c\ub85c \uc7a5\uc560\uc778\uc6b4\ub3d9 \uc5ed\uc0ac \uae30\ub85d \uc791\uc5c5\uc744 \uc2dc\uc791\ud558\uace0\uc790 \ud588\ub358 \uac83\uc785\ub2c8\ub2e4. \uc774\uc640 \ud568\uaed8 80\ub144\ub300 \ud6c4\ubc18\ubd80\u000d\u000a\ud130 \ubc1c\ud589\ub418\uc5c8\ub358 \uc7a5\uc560\uacc4 \uc2e0\ubb38\uc778 \u2018\uc7a5\uc560\uc778\ubcf5\uc9c0\uc2e0\ubb38\u2019\uacfc \uacc4\uac04\uc9c0 \u2018\ud568\uaed8\uac78\uc74c\u2019 \uae30\uc0ac\ub97c \uc218\uc9d1, \uc815\ub9ac\ud558\u000d\u000a\ub294 \uc791\uc5c5\ub3c4 \ubcd1\ud589\ud588\uc2b5\ub2c8\ub2e4. \uc774 \uc791\uc5c5\uc744 \ud558\uba74\uc11c \ub178\ub3d9\uc2dc\uc7a5\uc5d0 \ucc38\uc5ec\ud558\uc9c0 \ubabb\ud558\uace0 \uad6c\uac78, \ud589\uc0c1, \ub178\uc810 \u000d\u000a\ub4f1\uc73c\ub85c \uc0dd\uacc4\ub97c \uc774\uc5b4\uc624\ub358 \uc18c\uc704 \u2018\uc601\uc138 \uc7a5\uc560\uc778\u2019 \ubb38\uc81c\uac00 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc0ac\uc5d0\uc11c \uac70\uc758 \uc8fc\ubaa9\ubc1b\uc9c0 \ubabb\ud588\u000d\u000a\ub2e4\ub294 \uc810\uc744 \uc54c\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\uc5d0 8-90\ub144\ub300 \uc601\uc138 \uc7a5\uc560\uc778\uc758 \ud604\uc2e4\uacfc \uc774\uc5d0 \ub300\ud55c \uc7a5\uc560\uc778\uc6b4\ub3d9\u000d\u000a\uc758 \ub300\uc751 \uacfc\uc815\uc744 \uc5ed\uc0ac\uc801\uc73c\ub85c \ubd84\uc11d\ud55c \uc18c\ub17c\ubb38 \uc744 \ubc1c\ud45c\ud558\uae30\ub3c4 \ud588\uc2b5\ub2c8\ub2e4. \u000d\u000a\uadf8\ub9ac\uace0 \ubc14\ub85c \uc774\ub4ec\ud574\uc5d0 \ud6c4\uc18d\uc791\uc5c5\uc73c\ub85c 2000\ub144\ub300\ub97c \uc804\ud6c4\ud558\uc5ec \ud0dc\ub3d9\ud55c \uc7a5\uc560\uc778 \uc774\ub3d9\uad8c \ud22c\uc7c1\u000d\u000a\uc758 \uc5ed\uc0ac\ub97c \uc911\uc99d \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778\uc758 \uc0dd\uc560\uc0ac\ub97c \ubc14\ud0d5\uc73c\ub85c \uc7ac\uad6c\uc131\ud558\ub294 \uc791\uc5c5\uc744 \uc2dc\uc791\ud588\uc2b5\ub2c8\ub2e4. \u000d\u000a\uadf8 \uacb0\uacfc\ubb3c\uc774 \ubc14\ub85c \uc11c\uc6b8\uc2dcNPO\uc9c0\uc6d0\uc13c\ud130\uc758 \u20182020\ud65c\ub825\ud5a5\uc5f0\u2019 \uc9c0\uc6d0\uc0ac\uc5c5\uc73c\ub85c \uc9c4\ud589\ub41c \u300c\ubc14\ud034\uc790\u000d\u000a\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4: \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778\uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0 \ub300\ud55c \uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c\u300d\uc785\ub2c8\ub2e4.\u000d\u000a\uc775\ud788 \uc54c\ub824\uc838 \uc788\ub294 \uac83\ucc98\ub7fc, \uc7a5\uc560\uc778 \uc774\ub3d9\uad8c \ud22c\uc7c1\uc758 \uc9c1\uc811\uc801\uc778 \uacc4\uae30\ub294 2001\ub144 \uc9c0\ud558\ucca0 4\ud638\u000d\u000a\uc120 \uc624\uc774\ub3c4\uc5ed \uc7a5\uc560\uc778 \ud720\uccb4\uc5b4 \ub9ac\ud504\ud2b8 \ucd94\ub77d \ucc38\uc0ac\uc640 \uc774\uc5d0 \ub300\uc751\ud558\uae30 \uc704\ud574 \uc870\uc9c1\ub41c \uc7a5\uc560\uc778\uc774\ub3d9\uad8c\u000d\u000a\uc5f0\ub300\uc758 \uc9c0\ud558\ucca0 \uc120\ub85c \ubc0f \ubc84\uc2a4 \uc810\uac70 \ud22c\uc7c1\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub294 \uae30\uc874\uc758 \uc9c4\ubcf4\uc801 \uc0ac\ud68c\uc6b4\ub3d9\uc870\ucc28 \uc778\uc2dd\u000d\u000a\ud558\uc9c0 \ubabb\ud588\ub358 \uad8c\ub9ac \ud56d\ubaa9\ub4e4\uc744 \uc0ac\ud68c\uc801\uc73c\ub85c \uac00\uc2dc\ud654\uc2dc\ud0a4\ub294 \uc911\uc694\ud55c \uacc4\uae30\uc600\uace0, \uc804\ubc18\uc801\uc778 \uc0ac\ud68c\uc6b4\u000d\u000a\ub3d9\uc758 \uce68\uccb4\uae30 \uc18d\uc5d0\uc11c\ub3c4 \uc720\ub3c5 \ub3cb\ubcf4\uc778 \uae09\uc9c4\uc801\uc778 \uc9c1\uc811\ud589\ub3d9\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \ud558\uc9c0\ub9cc \uc9c0\uae08\uae4c\uc9c0 \uc6b4\ub3d9\u000d\u000a\uc0ac \ub2f4\ub860 \uc18d\uc5d0\uc11c \uc774\ub7ec\ud55c \ubcc0\ud654\ub294 \ub2e8\uc9c0 2001\ub144\uc5d0 \uac11\uc791\uc2a4\ub7fd\uac8c \ud130\uc9c4 \ub9ac\ud504\ud2b8 \ucd94\ub77d\ucc38\uc0ac\uc5d0 \uc758\ud55c \u000d\u000a\uc6b0\uc5f0\uc801\uc778 \uc0ac\uac74\uc73c\ub85c\ub9cc \uc124\uba85\ub418\uc5c8\uace0, \uc804\ud1b5\uc801\uc778 \uacc4\uae09 \ub610\ub294 \ubbfc\uc871 \ubaa8\uc21c\uc5d0 \uc5bd\ub9e4\uc774\uc9c0 \uc54a\ub294 \uc2e0\uc0ac\ud68c\u000d\u000a\uc6b4\ub3d9\uc758 \ud55c \uc870\ub958 \uc815\ub3c4\ub85c \uc774\ud574\ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uc5ec\uae30\uc5d0\ub294 \uc790\uae30 \uc0b6\uc744 \uc774 \uc6b4\ub3d9\uc758 \ud604\uc7a5\uc5d0 \uc628\uc804\ud788 \uc3df\u000d\u000a\uc544\ub0c8\ub358 \uc2e4\uc81c \uc0ac\ub78c\ub4e4\uc758 \uc774\uc57c\uae30\uac00 \ube60\uc838 \uc788\uc2b5\ub2c8\ub2e4.\u000d\u000a\uc800\ub294 \uc774\ub3d9\uad8c \ud22c\uc7c1\uc73c\ub85c \ucc98\uc74c \uac00\uc2dc\ud654\ub41c \uc911\uc99d\uc7a5\uc560\uc778\ub4e4\uc758 \uc0ac\ud68c\uc801 \ucd9c\ud604\uc5d0 \ub2f4\uae34 \uc5ed\uc0ac\uc801, \uc0ac\ud68c\u000d\u000a\uc801, \uac1c\uc778\uc0ac\uc801 \uc758\ubbf8\ub97c \uc628\uc804\ud788 \ub2f4\uc544\ubcf4\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \ubaa9\ud45c\uc5d0 \ub530\ub77c 2000\ub144\ub300 \ucd08\ubc18 \uc774\ub3d9\u000d\u000a\uad8c \ud22c\uc7c1\uc73c\ub85c\ubd80\ud130 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc744 \uc2dc\uc791\ud588\ub358 \uc911\uc99d \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778\ub4e4\uc758 \uc0dd\uc560\uc0ac\ub97c \uae30\ub85d\ud558\ub294 \uc791\u000d\u000a\uc5c5\uc774 \uc2dc\uc791\ub418\uc5c8\uc2b5\ub2c8\ub2e4.1)\u000d\u000a1\u3000\ud558\uae08\ucca0, \u300c\u2018\uc575\ubc8c\uc774 \uc7a5\uc560\uc778\u2019\uc758 \uc678\uce68\uc740 \ubaa8\ub450 \uc5b4\ub514\ub85c \uac14\ub294\uac00: 8~90\ub144\ub300 \uc601\uc138 \uc7a5\uc560\uc778 \ubb38\uc81c\uc640 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc758 \ub300\uc751\u300d,\u000d\u000a\uae30\uc5b5\uacfc\uc804\ub9dd \uc81c42\uad8c, \ubbfc\uc8fc\ud654\uc6b4\ub3d9\uae30\ub150\uc0ac\uc5c5\ud68c \ud55c\uad6d\ubbfc\uc8fc\uc8fc\uc758\uc5f0\uad6c\uc18c, 2020.","10 11\u000d\u000a2. \uc65c \uc0dd\uc560\uc0ac\uc778\uac00?\u000d\u000a\uadfc\ubcf8\uc801\uc73c\ub85c \uc7a5\uc560\uc778\uc6b4\ub3d9\uc0ac \uc5f0\uad6c\uac00 \uc65c \ud544\uc694\ud558\ub0d0\uace0 \ubb3b\ub294\ub2e4\uba74, \u2018\uacfc\uac70\ub97c \ud1b5\ud574\uc11c \ud604\uc7ac\ub97c \uc131\ucc30\u000d\u000a\ud558\uae30 \uc704\ud574\uc11c\u2019\ub77c\ub294, \uc77c\ubc18\uc801\uc73c\ub85c \uc5ed\uc0ac\uc5f0\uad6c\uc758 \ud544\uc694\uc131\uc73c\ub85c\uc11c \uc81c\uae30\ub418\ub294 \uc544\uc8fc \uc6d0\ub860\uc801\uc778 \ub300\ub2f5\uc744 \u000d\u000a\ud560 \uc218\ubc16\uc5d0 \uc5c6\uc2b5\ub2c8\ub2e4. \uc911\uc694\ud55c \uac83\uc740 \uc218\ub9ce\uc740 \uacfc\uac70 \uc18d\uc5d0\uc11c \ubb34\uc5c7\uc744 \uae30\uc5b5\ud558\uace0 \ud3c9\uac00\ud560 \uac83\uc778\uac00 \ud558\u000d\u000a\ub294 \uc810\uc785\ub2c8\ub2e4. \uac01 \uc2dc\uae30\ubcc4 \uc6b4\ub3d9\uc774 \ub9cc\ub4e4\uc5b4\ub0b8 \uc815\ucc45\uc801 \uc131\uacfc\uc640 \uc774\ub97c \uc8fc\ub3c4\ud55c \uad75\uc9c1\ud55c \ub2e8\uccb4\ub4e4\uc758 \uc5f0\u000d\u000a\ud601\uc744 \uc911\uc2ec\uc73c\ub85c \ud55c \uae30\uc5b5\uacfc \ud3c9\uac00 \uc5ed\uc2dc \uc911\uc694\ud560 \uac83\uc785\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc800\ub294 \uc801\uc5b4\ub3c4 \u2018\uc6b4\ub3d9\uc0ac\u2019\ub77c\uba74 \u000d\u000a\uc774 \uc6b4\ub3d9\uc5d0 \ucc38\uc5ec\ud55c \uc0ac\ub78c\ub4e4\uc774 \uacaa\uc740 \uc0dd\uc560\uc0ac\uc758 \uacc4\uae30\uc801 \uc0ac\uac74\ub4e4\uc5d0 \uc870\uae08 \ub354 \uad00\uc2ec\uc744 \uae30\uc6b8\uc5ec\uc57c \ud55c\u000d\u000a\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\u000d\u000a\uc624\ub79c \uc2dc\uac04\ub3d9\uc548 \ud55c\uad6d\uc0ac\ud68c\uc5d0\uc11c \uc7a5\uc560\uc778\uc740 \uac00\uc871 \uc774\uc678\uc758 \uc0ac\ud68c\uc801 \uc18c\uc18d\ub2e8\uc704(\ud559\uad50, \uc9c1\uc7a5, \uac01\uc885 \uc9c0\uc5ed\uc0ac\ud68c \uc870\u000d\u000a\uc9c1 \ucc38\uc5ec \ub4f1)\ub97c \uac16\uc9c0 \ubabb\ud55c \ucc44 \uace0\ub9bd\uacfc \ucc28\ubcc4 \uc18d\uc5d0 \uc0b4\uc544\uc57c\ub9cc \ud588\uc2b5\ub2c8\ub2e4. \uc2ec\uc9c0\uc5b4\ub294 \uac00\uc871\uc73c\ub85c\ubd80\ud130\ub3c4 \ubc84\u000d\u000a\ub824\uc838\uc11c \uc2dc\uc124\uc5d0 \uc218\uc6a9\ub418\uc5b4 \uc0b4\uc544\uc57c \ud558\ub294 \uc7a5\uc560\uc778\ub3c4 \ub9ce\uc558\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uc870\uac74\uc740 \uc7a5\uc560\uc778\uc774 \uc790\uc2e0\u000d\u000a\uc758 \ucc28\ubcc4 \uacbd\ud5d8\uc744 \ub4dc\ub7ec\ub0b4\uace0 \uc0ac\ud68c\ud654 \ud560 \uc218 \uc788\ub294 \uac00\uc7a5 \uae30\ubcf8\uc801\uc778 \ud1b5\ub85c\uac00 \ucc28\ub2e8\ub418\uc5b4 \uc788\uc5c8\ub2e4\ub294 \uac83\u000d\u000a\uc744 \ub73b\ud569\ub2c8\ub2e4. \uadf8\ub807\ub2e4\uba74 \uc6b0\ub9ac\ub294 \uc774\ucc98\ub7fc \uace0\ub9bd\ub418\uc5b4 \uc788\ub358 \uc7a5\uc560\uc778\ub4e4\uc774 \uc5b4\ub5a4 \uacc4\uae30\ub97c \ud1b5\ud574 \uc790\uc2e0\u000d\u000a\uc774 \uacaa\uace0 \uc788\ub294 \uc77c\ub4e4\uc774 \ucc28\ubcc4\uc774\ub77c\ub294 \uac83\uc744 \ub290\ub07c\uac8c \ub418\uc5c8\uace0, \uc5b4\ub5a4 \uc0ac\ud68c\uc801 \uc811\ucd09\uc73c\ub85c \uc778\ud574 \uc801\uadf9\uc801\u000d\u000a\uc778 \uc9c1\uc811\ud589\ub3d9\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5c8\ub294\uc9c0, \uadf8\ub9ac\uace0 \uc774\ub7ec\ud55c \uc0dd\uc560\uc0ac\uc801 \uacc4\uae30\ub4e4\uc774 \uad6c\uccb4\uc801\uc73c\ub85c \uc5b4\ub5a4 \ubaa8\u000d\u000a\uc2b5\uc758 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc744 \ud615\uc131\ucf00 \ud588\ub294\uc9c0\uc5d0 \ub300\ud574 \uace0\ucc30\ud574 \ubcfc \ud544\uc694\uac00 \uc788\uc2b5\ub2c8\ub2e4. \u000d\u000a\uc81c\uac00 \uc774\ub7f0 \uc9c8\ubb38\ub4e4\uc744 \ucc98\uc74c\uc73c\ub85c \uc5f0\uad6c\uc5d0 \uc801\uc6a9\ud574 \ubcf8 \uac83\uc740 8~90\ub144\ub300 \uc7a5\uc560\uc778\uc6b4\ub3d9 \ud65c\ub3d9\uac00 \uad6c\uc220\u000d\u000a\ucc44\ub85d \uc0ac\uc5c5\uc744 \ud1b5\ud574\uc11c\uc600\uc2b5\ub2c8\ub2e4. \uc774 \uad6c\uc220\ucc44\ub85d\uc5d0\uc11c \ub9cc\ub09c \ud65c\ub3d9\uac00\ub4e4\uc740 \uc8fc\ub85c \uc18c\uc544\ub9c8\ube44 \uc7a5\uc560\uc778\uc774\u000d\u000a\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub4e4\uc740 \ubcf4\ud589\uc774 \uc5b4\ub824\uc6cc \uc5ec\ub7ec \uac00\uc9c0 \uc0ac\ud68c\uc801 \uc7a5\ubcbd\uc744 \ub290\ub07c\uace0 \ucc28\ubcc4\uc744 \uacaa\uae30\ub3c4 \ud588\uc9c0\ub9cc, \u000d\u000a\ub2e4\ub978 \uc7a5\uc560\uc720\ud615\uc5d0 \ube44\ud558\uba74 \ud559\uad50\uad50\uc721\uc744 \ubc1b\ub294\ub370 \uc788\uc5b4\uc11c\ub294 \ube44\uad50\uc801 \uc5b4\ub824\uc6c0\uc774 \ud06c\uc9c0 \uc54a\uc558\uace0 \uc720\ub144\u000d\u000a\uae30 \ub610\ub798 \uc9d1\ub2e8 \uc548\uc5d0\uc11c \uc790\uc2e0\uc758 \uc7a5\uc560\ub97c \uc758\uc2dd\ud558\ub294 \uc77c\ub3c4 \ub9ce\uc9c0 \uc54a\uc558\ub2e4\uace0 \ub9d0\ud569\ub2c8\ub2e4. \uc774\ub4e4\uc5d0\uac8c \uc0dd\u000d\u000a\uc560\uc0ac\uc801 \uc804\ud658\uc810\uc73c\ub85c\uc11c \ucc28\ubcc4 \uacbd\ud5d8\uc740 \uc8fc\ub85c \ub300\ud559 \uc9c4\ud559 \ub610\ub294 \ucde8\uc5c5 \uc804\uc120\uc5d0 \ub6f0\uc5b4\ub4e4\uba74\uc11c \ubc8c\uc5b4\uc84c\uc2b5\u000d\u000a\ub2c8\ub2e4. \ud55c\ud3b8 \uc11c\uc6b8\uc5d0\uc11c \ud559\ucc3d\uc2dc\uc808\uc744 \ubcf4\ub0b8 \uc18c\uc544\ub9c8\ube44 \uc7a5\uc560\uc778\ub4e4\uc758 \uacbd\uc6b0, \uc721\uc601\uc218 \uc5ec\uc0ac\uc758 \ud6c4\uc6d0\uc73c\ub85c \u000d\u000a1975\ub144 \ud55c\uad6d\uc18c\uc544\ub9c8\ube44\ud611\ud68c\uac00 \uc124\ub9bd\ud55c \u2018\uc815\ub9bd\ud68c\uad00\u2019\uc744 \ud1b5\ud574 \ub610\ub798 \uc7a5\uc560\ud559\uc0dd\ub4e4\uacfc \ud65c\ubc1c\ud788 \uad50\ub958\ud558\u000d\u000a\ub294 \uae30\ud68c\ub97c \uac00\uc9c8 \uc218 \uc788\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub294 \ub2e8\uc9c0 \uc7a5\uc560\ud559\uc0dd\ub4e4 \uac04\uc758 \uce5c\ubaa9 \ud615\uc131\ubfd0\ub9cc \uc544\ub2c8\ub77c 80\ub144\ub300\u000d\u000a\uc758 \uae09\uc9c4\uc801 \uc0ac\ud68c\uc6b4\ub3d9 \ubb38\ud654\ub97c \uc811\ud560 \uc218 \uc788\ub294 \ud1b5\ub85c\ub3c4 \uc81c\uacf5\ud588\uace0, \uc774\ub97c \ubc14\ud0d5\uc73c\ub85c 80\ub144\ub300 \ub4e4\uc5b4 \u2018\uc7a5\u000d\u000a\uc560\uc778\ubb38\uc81c\uc5f0\uad6c\ud68c \uc6b8\ub9bc\ud130\u2019 \ub4f1 \ub2e4\uc591\ud55c \uccad\ub144\uc7a5\uc560\uc778\uc6b4\ub3d9\uc870\uc9c1\uc744 \uac74\uc124\ud558\uac8c \ub429\ub2c8\ub2e4.\u000d\u000a\ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4 : \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 \uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0 \ub300\ud55c \uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c "," & 12\u000d\u000a\ud558\uc9c0\ub9cc, \uc774\ub7ec\ud55c \uacbd\uc99d \uc18c\uc544\ub9c8\ube44 \uc7a5\uc560\uc778\ub4e4\uc758 \uc0dd\uc560\uacbd\ud5d8\uc740 \ub2e4\ub978 \uc720\ud615\uc758 \uc7a5\uc560\uc778\ub4e4\uc5d0\uac8c\ub294 \ub0af\u000d\u000a\uc120 \ud604\uc2e4\uc774\uc5c8\uc2b5\ub2c8\ub2e4. \uad50\uc721\uc758 \uce21\uba74\ub9cc \ubcf4\uc790\uba74, \ud55c\uad6d\ubcf4\uac74\uc0ac\ud68c\uc5f0\uad6c\uc6d0\uc774 2008\ub144\uc5d0 \uc2e4\uc2dc\ud55c \uc7a5\uc560\u000d\u000a\uc778\uc2e4\ud0dc\uc870\uc0ac \uacb0\uacfc\uc5d0\ub294 \uc804\uccb4 \uc7a5\uc560\uc778 \uc911 49.5%\uc758 \ucd5c\uc885\ud559\ub825\uc774 \ucd08\ub4f1\ud559\uad50 \uc878\uc5c5 \uc774\ud558\ub85c \ub098\ud0c0\ub0a9\u000d\u000a\ub2c8\ub2e4. \uc6b8\ub9bc\ud130\uc640 \uac19\uc740 \ub300\ud559 \ud559\uc0dd\ud68c \ubb38\ud654\uc5d0 \uae30\ubc18\ud55c \uccad\ub144\uc7a5\uc560\uc778\uc6b4\ub3d9\uc870\uc9c1\uc758 \ub4f1\uc7a5\uc740 \uc18c\uc544\ub9c8\ube44 \u000d\u000a\uc7a5\uc560\uc778\uc758 \uc99d\uac00\ub77c\ub294 \uc2dc\ub300\uc801 \uc870\uac74\uacfc \uc774\ub4e4\uc5d0 \ub300\ud55c \uc81c\ud55c\uc801\uc778 \uace0\ub4f1\uad50\uc721 \uae30\ud68c \ubcf4\uc7a5\uc774 \uac00\ub2a5\ud574\uc9c4 \u000d\u000a\uc608\uc678\uc801 \uae30\ud68c\uad6c\uc870 \uc18d\uc5d0\uc11c \uac00\ub2a5\ud588\ub358 \uac83\uc785\ub2c8\ub2e4.\u000d\u000a\uc800\ub294 \uadf8\ub7f0 \uc810\uc5d0\uc11c \uc774\ub3d9\uad8c \ud22c\uc7c1\uc758 \uc8fc\ucd95\uc774 \ub418\uc5c8\ub358 (\ud720\uccb4\uc5b4\ub97c \ud0c4) \uc911\uc99d\uc7a5\uc560\uc778\ub4e4\uc740 8~90\ub144\u000d\u000a\ub300 \uc18c\uc544\ub9c8\ube44 \uacbd\uc99d \uc7a5\uc560\uc778 \uc911\uc2ec\uc758 \ud65c\ub3d9\uac00\ub4e4\uacfc\ub294 \uc0ac\ubb47 \ub2e4\ub978 \uc0dd\uc560\uacbd\ud5d8\uc744 \uac00\uc9c0\uace0 \uc788\uc5c8\uace0, \uadf8 \u000d\u000a\uc0dd\uc560\uacbd\ud5d8\uc774 \ub3c5\ud2b9\ud55c \uc0ac\ud68c\uc801 \uc811\ucd09\uc744 \uac00\ub2a5\ucf00 \ud558\uba74\uc11c 2000\ub144\ub300 \uc774\ud6c4 \uc0c8\ub85c\uc6b4 \uc6b4\ub3d9\uc758 \ud750\ub984\uc744 \ub9cc\u000d\u000a\ub4e4 \uc218 \uc788\uc5c8\ub2e4\uace0 \ubcf4\uc558\uc2b5\ub2c8\ub2e4. \ub530\ub77c\uc11c \ub2f9\ub300\uc758 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc0ac \uae30\ub85d\uc5d0 \uc788\uc5b4\uc11c \uc0dd\uc560\uc0ac \uc5f0\uad6c\ub294 \ud544\u000d\u000a\uc218\uc801\uc778 \uc694\uc18c\ub77c\uace0 \uc0dd\uac01\ud558\uc600\uc2b5\ub2c8\ub2e4.\u000d\u000a3. \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778\uc758 \uc0dd\uc560\uc0ac \uacbd\ud5d8\uc758 \ud2b9\uc218\uc131\u000d\u000a\ub1cc\uc131\ub9c8\ube44\u8166\u6027\u9ebb\u75fa, Cerebral Palsy\ub294 \ucd9c\uc0dd \uc804\ud6c4 \uc544\uc9c1 \ub1cc\uac00 \ubbf8\uc131\uc219\ud55c \uc2dc\uae30\uc5d0 \uc0dd\uae30\ub294 \ub1cc\uc758 \ubcd1\ubcc0\u000d\u000a\uc5d0 \uc758\ud574 \ubc1c\uc0dd\ud558\ub294 \uc6b4\ub3d9\uae30\ub2a5 \uc7a5\uc560\ub97c \ucd1d\uce6d\ud558\ub294 \ub2e8\uc5b4\ub85c, \ubcf4\ud589 \uc0c1\uc758 \uc7a5\uc560\ub85c \uc774\uc5b4\uc9c0\ub294 \uacbd\uc6b0\uac00 \u000d\u000a\ub9ce\uace0 \ub530\ub77c\uc11c \ud720\uccb4\uc5b4\ub97c \uc774\uc6a9\ud558\ub294 \ube48\ub3c4\uac00 \ub192\uc740 \ud3b8\uc785\ub2c8\ub2e4. \ubb3c\ub860 \uc0ac\ud68c\uc801 \ud604\uc0c1\uc73c\ub85c\uc11c \uc7a5\uc560\uc778\uc6b4\u000d\u000a\ub3d9\uc744 \uc0b4\ud3b4\ubcf4\ub294 \ub370 \uc788\uc5b4\uc11c \uad73\uc774 \ud2b9\uc815 \uc7a5\uc560 \uc720\ud615\uc5d0\ub9cc \uc9d1\uc911\ud574\uc57c \ud560 \uc774\uc720\ub294 \uc5c6\uc2b5\ub2c8\ub2e4. \uadf8\ub7fc\uc5d0\u000d\u000a\ub3c4 \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778\uc744 \uc8fc\ub41c \uc5f0\uad6c \ub300\uc0c1\uc73c\ub85c \uc0bc\ub294 \uc774\uc720\ub294 (\uc218\uce58\uc801\uc73c\ub85c \uc815\ud655\ud55c \ud655\uc778\uc740 \ubd88\uac00\ub2a5\u000d\u000a\ud558\uc9c0\ub9cc) \uc774\ub4e4\uc774 \uc774\ub3d9\uad8c \ud22c\uc7c1 \ucd09\ubc1c\ub85c \uac00\uc2dc\ud654\ub41c \uc911\uc99d\uc7a5\uc560\uc778 \uc9d1\ub2e8 \uc911\uc5d0\uc11c\ub3c4 \ub098\ub984 \uc0c1\uc9d5\uc131\uc744 \uac00\u000d\u000a\uc9c0\uace0 \uc788\ub2e4\uace0 \ud310\ub2e8\ud588\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4.\u000d\u000a\uc774\ub97c \uc704\ud574 \ubcf8 \uc5f0\uad6c\ub294 \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 4\uc778\uc758 \uad6c\uc220 \ubc0f \uc0dd\uc560\uae30\ub85d\uc744 \uc218\uc9d1\ud588\uc2b5\ub2c8\ub2e4. \uc774\ub4e4\u000d\u000a\uc740 \ubaa8\ub450 2000\ub144\ub300 \ucd08\ubc18 \uc774\ub3d9\uad8c \ud22c\uc7c1\uc5d0 \ucc38\uc5ec\ud588\ub2e4\ub294 \uacf5\ud1b5\uc810\uc744 \uac00\uc9c0\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \uc911 2\uba85\u000d\u000a\uc740 \ub2f9\uc0ac\uc790\uc640 \uc778\ud130\ubdf0\ub97c \uc9c4\ud589\ud574 \uad6c\uc220\uc744 \ucc44\ub85d\ud588\uace0, \ub2e4\ub978 1\uba85\uc740 \uc5b8\uc5b4\uc7a5\uc560\uac00 \uc2ec\ud574 \uc778\ud130\ubdf0\ub97c \uc9c4\u000d\u000a\ud589\ud560 \uc218 \uc5c6\uc5b4 \ubcf8\uc778\uc774 \uadf8\uac04 \uc790\uc2e0\uc758 \uc0dd\uc560\ub97c \uae30\ub85d\ud55c \uae00(A4 20\uc5ec \ud398\uc774\uc9c0 \ubd84\ub7c9)\uc744 \uc81c\uacf5\ubc1b\uc544 \uc778\ud130\ubdf0\ub97c \ub300\u000d\u000a\uccb4\ud588\uc2b5\ub2c8\ub2e4. \ub098\uba38\uc9c0 1\uba85\uc740 2002\ub144 \u2018\ucd5c\uc800\uc0dd\uacc4\ube44 \ud604\uc2e4\ud654\u2019\ub97c \uc704\ud55c \ub2e8\uc2dd \ub18d\uc131 \ub05d\uc5d0 \uc0ac\ub9dd\ud588\ub294\u000d\u000a\ub370, \uadf8\uc758 \uc0dd\uc560 \uae30\ub85d\uc740 \ud3c9\uc804\uacfc \uc5b8\ub860 \uae30\uc0ac \ub4f1\uc73c\ub85c \uc804\ud574\uc9c0\uace0 \uc788\uc5b4 \uc774\ub97c \ucc38\uace0\ud588\uc2b5\ub2c8\ub2e4. \uc774\uc678\uc5d0","12 13\u000d\u000a\ub3c4 90\ub144\ub300 \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778\ub4e4\uc758 \uc870\uc9c1\ud654 \uc591\uc0c1\uc744 \ud30c\uc545\ud558\uae30 \uc704\ud574 \u2018\ub1cc\uc131\ub9c8\ube44\uc5f0\uad6c\ud68c \u1107\u119e\ub86c\u2019 \ucc3d\u000d\u000a\ub9bd\uba64\ubc84 3\uba85\uacfc \ube44\uc7a5\uc560\uc778 \ud65c\ub3d9\uac00 2\uba85\uc744 \uc778\ud130\ubdf0\ud588\uc73c\uba70, \uc7a5\uc560\uc778\uc6b4\ub3d9\uc5d0 \uc9c1\uc811 \uad00\ub828\uc744 \ub9fa\uc9c0\ub294 \uc54a\u000d\u000a\uc558\uc9c0\ub9cc \ub1cc\uc131\ub9c8\ube44 \uad00\ub828 \uac01\uc885 \ubcf5\uc9c0\uccb4\uacc4\uc640 \uc5f0\uacb0\ub41c \uc0b6\uc744 \uc0b4\uc558\ub358 \ub2f9\uc0ac\uc790 1\uba85\uacfc \ud544\ub2f4\uc744 \ud1b5\ud55c \uc778\u000d\u000a\ud130\ubdf0\ub3c4 \uc9c4\ud589\ud588\uc2b5\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \ud30c\uc545\ud55c, 2000\ub144\ub300 \uc774\ub3d9\uad8c \ud22c\uc7c1\uc744 \uacc4\uae30\ub85c \uc7a5\uc560\uc778\uc6b4\ub3d9\uc5d0 \u000d\u000a\ucc38\uc5ec\ud558\uac8c \ub41c \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778\ub4e4\uc758 \uc0dd\uc560\uc0ac\uc801 \uacbd\ud5d8\uc758 \uacf5\ud1b5\uc810\uc744 \uc815\ub9ac\ud558\uba74 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4. \u000d\u000a\u2022 \uc2a4\ubb34\uc0b4\uc774 \ub420 \ub54c\uae4c\uc9c0 \ucd08\ub4f1\ud559\uad50\uc870\ucc28 \ub2e4\ub2c8\uc9c0 \ubabb\ud588\uace0, 10\ub300 \ud6c4\ubc18\uc774 \ub418\uc5b4\uc11c\uc57c \uac80\uc815\uace0\uc2dc\u000d\u000a\ub97c \ud1b5\ud574 \ud559\ub825\uc744 \ucde8\ub4dd\ud560 \uc218 \uc788\uc5c8\ub2e4. \uc774\ub294 \ud559\uad50\uad50\uc721\uc744 \ud1b5\ud574 \uc77c\ubc18\uc801\uc73c\ub85c \uae30\ub300\ub418\ub294 \uc0dd\uc560\u000d\u000a\uc8fc\uae30\ub97c \uc0b4\uc544\uac08 \uc804\ub9dd\uc774 \uc0c1\uc2e4\ub418\uc5c8\uc74c\uc744 \uc758\ubbf8\ud55c\ub2e4. \uadf8\ub7ec\ub098 \uadf8\ub4e4\uc740 \uac80\uc815\uace0\uc2dc\uc640 \uac19\uc740 \uc6b0\u000d\u000a\ud68c\ub85c\ub97c \ud1b5\ud574 \uc131\uc778\uae30\ub97c \ub9de\uc774\ud558\uba74\uc11c, \ub3d9\uc2dc\uc5d0 \uc0c8\ub85c\uc6b4 \uc7a5\uc560\uc778\ubcf5\uc9c0\uc81c\ub3c4\uc640 \uc5f0\uacb0\ub418\uae30 \uc2dc\u000d\u000a\uc791\ud588\ub2e4.\u000d\u000a\u2022 \uc815\ubd80\ub294 1981\ub144 \uc2ec\uc2e0\uc7a5\uc560\uc790\ubcf5\uc9c0\ubc95\uc744 \uc81c\uc815\ud558\uace0, \uc774\ub4ec\ud574\ubd80\ud130 \uc804\uad6d\uc801\uc73c\ub85c \uc7a5\uc560\uc778\ubcf5\uc9c0\u000d\u000a\uad00 \uc124\uce58 \ubc0f \uc7a5\uc560\uc778\uc9c1\uc5c5\uc7ac\ud65c \uc0ac\uc5c5 \ub4f1\uc744 \ucd94\uc9c4\ud588\uc73c\uba70, \uc7a5\uc560\uc778\uac70\uc8fc\uc2dc\uc124\ub3c4 \ub354\uc6b1 \ud655\ub300 \uc124\u000d\u000a\uce58\ud588\ub2e4. \uc774\ub294 \ubd84\uba85 \uc7a5\uc560\uc5d0 \ub300\ud55c \uc7ac\ud65c\ubaa8\ub378\uc5d0 \uae30\ubc18\ud55c \ucc28\ubcc4\uc801\uc778 \uc81c\ub3c4\ub4e4\uc774\uc5c8\uc9c0\ub9cc, \uac00\uc815 \u000d\u000a\uc774\uc678\uc758 \uacf5\uc2dd\uc801 \uc7a5\uc560\uc778\ubcf5\uc9c0 \uc804\ub2ec\uccb4\uacc4\uac00 \ucc98\uc74c\uc73c\ub85c \uc218\ub9bd\ub418\uc5c8\ub2e4\ub294 \uc810\uc5d0\uc11c \uc758\uc758\uac00 \uc788\ub2e4.\u000d\u000a\u2022 \ubcf8 \uc5f0\uad6c\uc758 \uc0dd\uc560\uc0ac \uc870\uc0ac \ub300\uc0c1\uc790 4\uba85\uc740 10\ub300 \ud6c4\ubc18 ~ 20\ub300 \ucd08\ubc18\uc5d0 \uc7a5\uc560\uc778\uac70\uc8fc\uc2dc\uc124\uc5d0 \u000d\u000a\uc785\uc18c\ud55c \uacbd\ud5d8\uc744 \uac00\uc9c0\uace0 \uc788\ub2e4. \u2018\uc2dc\uc124\u2019\uc740 \uae30\ubcf8\uc801\uc73c\ub85c \uc7a5\uc560\uc778\uc758 \uc9c0\uc5ed\uc0ac\ud68c \ud1b5\ud569\uc744 \uac00\ub85c\ub9c9\u000d\u000a\ub294 \uc7a5\ubcbd\uc73c\ub85c \uae30\ub2a5\ud558\uc9c0\ub9cc, \uc774\ub4e4\uc758 \uc0dd\uc560\uc0ac\uc5d0\uc11c \uc2dc\uc124\uc774 \uac16\ub294 \uc758\ubbf8\ub294 \ub2e4\uba74\uc801\uc778 \uc131\uaca9\uc744 \u000d\u000a\uac16\uace0 \uc788\ub2e4. \ubb34\uc5c7\ubcf4\ub2e4 \uadf8\ub4e4\uc5d0\uac8c \uc2dc\uc124\uc740 \uac00\uc871 \uc774\uc678\uc5d0 \ucc98\uc74c\uc73c\ub85c \uc811\ud55c \u2018\uc0ac\ud68c\uc0dd\ud65c\u2019\uc774\uc5c8\u000d\u000a\ub2e4. \uc2dc\uc124\uc774\ub77c\ub294 \ud2b9\uc218\ud55c \uacf5\uac04 \uacbd\ud5d8\uc744 \ud1b5\ud574 \uadf8\ub4e4\uc740 \uc9d1\ub2e8\uc801 \uc815\uccb4\uc131\uc73c\ub85c\uc11c \uc7a5\uc560\ub97c \uccb4\ud5d8\u000d\u000a\ud588\uace0, \uac00\uc815 \ub0b4\uc5d0\uc11c\ub294 \uacaa\uc744 \uc218 \uc5c6\uc5c8\ub358 \uc9d1\ub2e8 \ub0b4\uc5d0\uc11c\uc758 \uc5ed\ud560\uc744 \uc218\ud589\ud588\uc73c\uba70, \uc2dc\uc124 \uc548\ud30e\u000d\u000a\uc758 \uc0ac\ud68c\uc801 \uad00\uacc4\ub9dd\uacfc \uc0c8\ub86d\uac8c \uc5f0\uacb0\ub418\uc5c8\ub2e4.\u000d\u000a\u2022 \uc5ec\uae30\uc11c \ub9d0\ud558\ub294 \u2018\uc0c8\ub85c\uc6b4 \uc0ac\ud68c\uc801 \uad00\uacc4\ub9dd\u2019\uc774\ub780 \uc2dc\uc124 \ub0b4\ubd80\uc758 \ub3d9\ub8cc\uad00\uacc4\ub294 \ubb3c\ub860\uc774\uace0, \uc2dc\uc124 \u000d\u000a\uc678\ubd80\uc5d0\uc11c \ucc3e\uc544\uc624\ub294 \uc790\uc6d0\ubd09\uc0ac\uc790\ub4e4\ub3c4 \ud3ec\ud568\ud55c\ub2e4. \uc774\ub7f0 \uad00\uacc4\ub9dd\uc744 \ud1b5\ud574 \uadf8\ub4e4\uc740 \uc2dc\uc124 \ubc16 \u000d\u000a\uc138\uc0c1\uacfc \uc5f0\uacb0\ub418\uc5b4 \uc0b6\uc758 \uc804\ub9dd\uc744 \uc0c8\ub86d\uac8c \uadf8\ub824\ubcfc \uc218 \uc788\uc5c8\ub2e4. \ub9c8\uce68 90\ub144\ub300 \ud6c4\ubc18\ubd80\ud130 \uc2dc\uc124\u000d\u000a\ube44\ub9ac \ubc0f \uc778\uad8c\uce68\ud574\uc5d0 \ub300\uc751\ud558\ub294 \uc778\uad8c\uc6b4\ub3d9\uc774 \ubcf8\uaca9\ud654\ub418\uc5c8\uace0, \uc911\uc99d\uc7a5\uc560\uc778 \ub2f9\uc0ac\uc790\uac00 \uc8fc\uccb4\u000d\u000a\ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4 : \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 \uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0 \ub300\ud55c \uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c "," & 14\u000d\u000a\uac00 \ub41c \uc790\uc870\uc870\uc9c1(\ub1cc\uc131\ub9c8\ube44\uc5f0\uad6c\ud68c \u1107\u119e\ub86c, \ub178\ub4e4\uc7a5\uc560\uc778\uc57c\ud559 \ub4f1)\ub4e4\uc774 \ub4f1\uc7a5\ud558\uc5ec \uc2dc\uc124 \ub0b4 \uc7a5\uc560\uc778\ub4e4\uc774 \uc5b5\uc555\ub418\u000d\u000a\uc5b4 \uc654\ub358 \uc790\uae30 \uad8c\ub9ac \ubb38\uc81c\ub97c \uc790\uac01\ud560 \uc218 \uc788\ub294 \uacc4\uae30\ub97c \uc81c\uacf5\ud588\ub2e4.\u000d\u000a\u2022 \uc7a5\uc560\uc778 \uc774\ub3d9\uad8c \ud22c\uc7c1\uc740 \uc787\ub2ec\uc544 \ubc8c\uc5b4\uc9c4 \uc9c0\ud558\ucca0 \ub9ac\ud504\ud2b8 \ucd94\ub77d \uc0ac\uace0 \ub4f1\uc5d0 \ub300\uc751\ud558\ub294 \uc911\uc99d\u000d\u000a\uc7a5\uc560\uc778 \ub2f9\uc0ac\uc790 \uc870\uc9c1\uc758 \ubb3c\ub9ac\uc801 \uc800\ud56d\uc744 \ud1b5\ud574 \ucd09\ubc1c\ub418\uc5c8\ub2e4. \uc774\ub294 \uad50\ud1b5\uc57d\uc790\uc758\uc774\ub3d9\ud3b8\uc758\uc99d\u000d\u000a\uc9c4\ubc95 \uc81c\uc815\uacfc \uac19\uc740 \uc785\ubc95\uc801 \uc131\uacfc \ubfd0\ub9cc \uc544\ub2c8\ub77c, \uc911\uc99d\uc7a5\uc560\uc778\uc758 \uc9d1\ub2e8\uc801 \ucd9c\ud604\uc774\ub77c\ub294 \u2018\uacfc\uaca9\u000d\u000a\ud55c \uc9c1\uc811\ud589\ub3d9\u2019\uc744 \ud1b5\ud574 \uc0ac\ub78c\ub4e4\uc758 \uc774\ub3d9\uc744 \uac00\ub2a5\ucf00 \ud558\ub294 \uacf5\uac04\uc801 \uad6c\uc870\uac00 \uc5bc\ub9c8\ub098 \ucc28\ubcc4\uc801\uc778 \u000d\u000a\uac83\uc774\uc5c8\ub294\uc9c0\ub97c \ud3ed\ub85c\ud568\uc73c\ub85c\uc368 \uc6b0\ub9ac\uac00 \ubc1c \ub51b\uace0 \uc11c \uc788\ub294 \uc0ac\ud68c\uc801 \ud1a0\ub300\uc5d0 \uadfc\ubcf8\uc801\uc778 \uc758\ubb38\u000d\u000a\uc744 \ub358\uc9c0\ub294 \uac83\uc774\uc5c8\ub2e4.\u000d\u000a4. \uc0dd\uc560\uc0ac\ub97c \ud1b5\ud574 \ubb34\uc5c7\uc744 \ub9d0\ud558\uace0 \uc2f6\uc5c8\ub098?\u000d\u000a\ucd5c\uadfc \ub4e4\uc5b4 \uc7a5\uc560\uc778 \ub2f9\uc0ac\uc790\uc758 \uc0b6\uc758 \uae30\uc5b5\uc744 \ubc14\ud0d5\uc73c\ub85c \uc7a5\uc560\uc778\uc6b4\ub3d9\uc758 \ud750\ub984\uc744 \uc11c\uc220\ud558\ub824\ub294 \ub178\u000d\u000a\ub825\ub4e4\uc774 \ub298\uc5b4\ub098\uace0 \uc788\uc2b5\ub2c8\ub2e4. \ud2b9\ud788 2010\ub144\ub3c4 \uc774\ub798 \uafb8\uc900\ud788 \uc774\uc5b4\uc9c0\uace0 \uc788\ub294 \ud0c8\uc2dc\uc124 \uc7a5\uc560\uc778\ub4e4\uc758 \u000d\u000a\uc0dd\uc560\uae30\ub85d \uc791\uc5c5\uc740 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc758 \uc8fc\uc694\ud55c \uc2e4\ucc9c \uc591\uc2dd\uc73c\ub85c \uc790\ub9ac\uc7a1\uac8c \ub418\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \uae30\ub85d\u000d\u000a\ubb3c\ub4e4\uc740 \uc7a5\uc560 \ub2f9\uc0ac\uc790\uc758 \uc0b6\uc744 \ud53c\ud574\uc790 \ud639\uc740 \ud22c\uc0ac\ub85c \uc9d1\ub2e8\ud654\ud558\uace0 \uc815\ud615\ud654\ud574\uc654\ub358 \uc804\ud615\uc131\uacfc \uc7a5\uc560\u000d\u000a\uc778\uc6b4\ub3d9\uc758 \uc758\uc81c\ud654 \uc804\ub7b5\uc744 \uc704\ud55c \ubcf4\uc870 \uc790\ub8cc\ub85c \uc0bc\ub294 \uacbd\ud5a5\uc744 \uadf9\ubcf5\ud558\uace0, \uc7a5\uc560 \ub2f9\uc0ac\uc790\uc758 \uace0\uc720\uc131\uc744 \u000d\u000a\ubcf5\uc6d0\ud558\uace0 \uc8fc\uccb4\ud654\ud558\uae30 \uc704\ud55c \uc804\ub7b5\uc774\uc5c8\ub2e4. \ud558\uc9c0\ub9cc \uc0dd\uc560\uae30\ub85d\uc758 \uc774\ub7ec\ud55c \uc758\uc758\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \uadf8\u000d\u000a\uac83\uc744 \u2018\uc5ed\uc0ac\ud654\u2019 \uc791\uc5c5\uc774\ub77c\uace0 \ud558\uae30\uc5d0\ub294 \ubb34\ub9ac\uac00 \uc788\ub2e4\ub294 \uac8c \uc81c \uc0dd\uac01\uc785\ub2c8\ub2e4. \uac1c\uc778\uc758 \uace0\uc720\ud55c \uc11c\uc0ac\u000d\u000a\ub97c \ub4dc\ub7ec\ub0b4\ub294 \uc791\uc5c5\uc774 \ub300\uc911\uc73c\ub85c \ud558\uc5ec\uae08 \uc7a5\uc560\uc5d0 \ub300\ud55c \uc0c8\ub85c\uc6b4 \uc0ac\uc720\ub97c \uc790\uadf9\ud558\uace0 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc758 \u000d\u000a\uc778\uc2dd \ud655\uc7a5\uc5d0 \uae30\uc5ec\ud558\ub294 \uce21\uba74\uc774 \uc788\uc74c\uc740 \ubd84\uba85\ud558\uc9c0\ub9cc, \uc774\uac83\uc774 \uace7 \ud1b5\uc2dc\uc801\uc774\uace0 \uad6c\uc870\uc801\uc778 \ucc28\uc6d0\uc5d0\u000d\u000a\uc11c\uc758 \uc7a5\uc560\uc778 \uc6b4\ub3d9\uc0ac\ub97c \uc7ac\uad6c\uc131\ud558\ub294 \uac83\uc73c\ub85c \uc5f0\uacb0\ub418\ub294 \uac83\uc740 \uc544\ub2d9\ub2c8\ub2e4.\u000d\u000a\ubc18\ub300\ub85c \uc9c0\uae08\uae4c\uc9c0 \uc7a5\uc560\uc778\ub2e8\uccb4\ub4e4\uc774 \uacf5\uc2dd/\ube44\uacf5\uc2dd\uc801 \ud615\ud0dc\ub85c \ubc1c\uac04\ud55c \uc7a5\uc560\uc778\uc6b4\ub3d9\uc0ac \uad00\ub828 \ubb38\u000d\u000a\ud5cc\ub4e4\uc5d0\uc11c\ub294 \ub300\uccb4\ub85c \uc8fc\uc694 \uc815\ucc45\uc758 \ubcc0\ud654, \uc804\uad6d \ub2e8\uc704 \uc6b0\uc0b0\uc870\uc9c1\ub4e4\uc758 \uc774\ud569\uc9d1\uc0b0\uc758 \uacfc\uc815 \uc815\ub3c4\uac00 \ub17c\u000d\u000a\uc758\ub418\uc5b4 \uc654\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\ud55c \ub17c\uc758 \ubc29\uc2dd\uc740 \ud604\uc7ac \uc601\ud5a5\ub825\uc744 \ud589\uc0ac\ud558\uace0 \uc788\ub294 \uc7a5\uc560\uc778\ub2e8\uccb4\uc758 \uc774\ud574\uad00\u000d\u000a\uacc4\uc5d0 \ub530\ub77c \uacfc\uac70\ub97c \uc7ac\ub2e8\ud560 \uc6b0\ub824\uac00 \uc788\uc73c\uba70, \uadf8 \uacb0\uacfc \uc7a5\uc560 \ub2f9\uc0ac\uc790\uc758 \uc8fc\uccb4\uc801 \ud589\uc704\uc131, \uac1c\uc778\uacfc \uc0ac\u000d\u000a\ud68c \uac04\uc758 \ub2e4\uc591\ud55c \uc0c1\ud638\uc791\uc6a9 \ub4f1\uc740 \ub17c\uc758\uc5d0\uc11c \uc18c\uac70\ub418\uace0 \ub9d9\ub2c8\ub2e4. \uc27d\uac8c \ub9d0\ud574\uc11c \uc6b4\ub3d9\uc0ac \uc18d\uc5d0 \uc9c4\uc9dc \u000d\u000a\uc0ac\ub78c\uc758 \uc774\uc57c\uae30\ub294 \ube60\uc838 \uc788\ub294 \uac83\uc785\ub2c8\ub2e4.","14 15\u000d\u000a\uc800\ub294 \uc0ac\ub78c\uc758 \uc774\uc57c\uae30, \uc81c\ub3c4\uc640 \uc815\ucc45\uc758 \uc774\uc57c\uae30, \uc6b4\ub3d9\uc758 \uc774\ub150\uacfc \uc870\uc9c1\ud654\uc5d0 \ub300\ud55c \uc774\uc57c\uae30\uac00 \uc528\u000d\u000a\uc904\uacfc \ub0a0\uc904\uc774 \uc5ee\uc774\ub4ef\uc774 \uc9c1\uc870\ub418\ub294 \uc5ed\uc0ac\ub97c \uc4f0\uace0 \uc2f6\uc5c8\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \uc885\ub958\uc758 \uc5ed\uc0ac \uc4f0\uae30\ub294 100% \u000d\u000a\uc5ed\uc0ac\uac00\uc758 \uc704\uce58\uc5d0 \uc11c \uc788\uc744 \ub54c, \ub610\ub294 \ubc18\ub300\ub85c 100% \ud65c\ub3d9\uac00\uc758 \uc704\uce58\uc5d0 \uc11c \uc788\uc744 \ub54c\uc5d0\ub294 \uc4f0\uc5ec\uc9c8 \u000d\u000a\uc218 \uc5c6\ub294 \uac83\uc774\ub77c \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \uc800\ucc98\ub7fc \ud65c\ub3d9\uac00\uc758 \uc704\uce58\uc5d0\uc11c \uc5ed\uc0ac \uc4f0\uae30\ub97c \uc9c0\ud5a5\ud558\ub294 \uc0ac\ub78c\ub9cc\uc774 \u000d\u000a\uc4f8 \uc218 \uc788\ub294 \uac83\uc774\ub77c \uc0dd\uac01\ud588\uc2b5\ub2c8\ub2e4. \uadf8\ub7ec\ud55c \uc790\ubd80\uc2ec(!)\uc758 \uc131\uacfc\uac00 \ubc14\ub85c \uc774 \uc5f0\uad6c\uc600\uace0, \uc774\ubc88 \uacf5\ubaa8\u000d\u000a\uc804\uc5d0\uc11c \uadf8 \uc790\ubd80\uc2ec\uc758 \uac00\uce58\ub97c \uc778\uc815\ud574 \uc8fc\uc2e0 \uac83 \uac19\uc544 \uc790\ubd80\uc2ec\uc774 \uc870\uae08\uc740 \ub354 \ub2e8\ub2e8\ud574\uc9c4 \uac83 \uac19\uc2b5\ub2c8\u000d\u000a\ub2e4.\u000d\u000a5. \uacfc\uc5f0 <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\uc5d0 \ub2ff\uc558\uc744\uae4c?\u000d\u000a\uc81c\uac00 \uc774\ub7f0 \uc791\uc5c5\uc744 \ubcf8\uaca9\uc801\uc73c\ub85c \uc2dc\uc791\ud558\uae30 \uc704\ud574 \ubc15\uc0ac\uacfc\uc815 \uc9c4\ud559\uc744 \uacb0\uc2ec\ud588\uc744 \ub54c, \uc8fc\uc704 \ud65c\ub3d9\u000d\u000a\uac00\ub4e4\uc5d0\uac8c \ub18d\ub2f4 \uc0bc\uc544 \ud558\ub358 \ub9d0\uc774 \uc788\uc2b5\ub2c8\ub2e4. \u201c\ub098\ub294 \uc7a5\uc560\uc778\uc6b4\ub3d9\uc758 \uc5b4\uc6a9 \uc5ed\uc0ac\ud559\uc790\uac00 \ub420 \uac70\ub2e4.\u201d\u000d\u000a\uc774 \ub9d0\uc740 \ud55c\ud3b8\uc73c\ub85c\ub294 \ubcc0\uba85\uc0bc\uc544 \ub9cc\ub4e4\uc5b4\ub0b8 \ub9d0\uc774\uae30\ub3c4 \ud588\uc2b5\ub2c8\ub2e4. \ubc15\uc0ac\uacfc\uc815 \uc9c4\ud559 \uacb0\uc815\uc744 \ud65c\ub3d9\uac00\u000d\u000a\uc758 \uae38\uc744 \uc811\uace0 \ub2e4\ub978 \uc9c4\ub85c\ub97c \ubaa8\uc0c9\ud558\ub294 \uac83\ucc98\ub7fc \uc0dd\uac01\ud558\ub294 \uc8fc\ubcc0 \uc0ac\ub78c\ub4e4\uc5d0\uac8c, \u2018\ub2f9\uc2e0\uacfc \ub098\uc758 \uae38\uc740 \u000d\u000a\uc55e\uc73c\ub85c\ub3c4 \ub2e4\ub974\uc9c0 \uc54a\uc744 \uac83\uc774\ub2e4\u2019\ub77c\uace0 \uac15\ubcc0\ud558\uace0 \uc2f6\uc5c8\ub358 \uac83\uc785\ub2c8\ub2e4. \ub2e4\ub978 \ud55c\ud3b8\uc73c\ub85c\ub294 \ub2f9\ud30c\uc801\uc778 \u000d\u000a\uac00\uce58\ub97c \ucd94\uad6c\ud558\uba74\uc11c\ub3c4 \uac1d\uad00\uc131\uc744 \ub193\uce58\uc9c0 \uc54a\ub294 \uc5f0\uad6c\uac00 \uac00\ub2a5\ud558\ub2e4\ub294 \uac83\uc744 \uc81c \ud798\uc73c\ub85c \uc785\uc99d\ud574 \ubcf4\u000d\u000a\uc774\uace0 \uc2f6\uae30\ub3c4 \ud588\uc2b5\ub2c8\ub2e4.\u000d\u000a\uac11\uc791\uc2a4\ub7fd\uac8c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \uacf5\ubaa8\uc804\uc5d0 \uc120\uc815\ub418\uc5c8\ub2e4\ub294 \uc18c\uc2dd\uc744 \ub4e3\uace0 \uacfc\uc5f0 \uc81c \uc2a4\uc2a4\ub85c \u000d\u000a\uc124\uc815\ud55c \uc774 \uae30\uc900\uc5d0 \uc5bc\ub9c8\ub098 \ub2ff\uc558\ub294\uc9c0 \ub3cc\uc774\ucf1c \uc0dd\uac01\ud574 \ubcf4\uc558\uc2b5\ub2c8\ub2e4. \uc800\uc758 \uc791\uc5c5\uc774 \u2018\ud604\uc7a5\uc9c0\uc2dd\u2019\uc778 \u000d\u000a\uac83\uc740 \ud2c0\ub9bc\uc5c6\ub294 \uc0ac\uc2e4\uc785\ub2c8\ub2e4. \uc81c\uac00 \uc7a5\uc560\uc778\uc6b4\ub3d9 \ud604\uc7a5\uc5d0 \ubab8\ub2f4\uc9c0 \uc54a\uc558\ub2e4\uba74 \uacb0\ucf54 \uc54c \uc218 \uc5c6\uc5c8\uc744 \u000d\u000a\uc774\ub3d9\uad8c \ud22c\uc7c1\uc758 \uc5ed\uc0ac\uc801 \ub9e5\ub77d\uc5d0 \ub300\ud55c \uc774\ud574\ub97c \ubc14\ud0d5\uc73c\ub85c \uc774 \uc5f0\uad6c\uac00 \uc2dc\uc791\ub418\uc5c8\uace0, \uc9c0\uae08\ub3c4 \uc774 \uc6b4\u000d\u000a\ub3d9\uc758 \ud604\uc7a5\uc5d0\uc11c \ubab8\uc744 \uc544\ub07c\uc9c0 \uc54a\uace0 \ud5cc\uc2e0\ud558\uace0 \uc788\ub294 \uc0ac\ub78c\ub4e4\uc774 \ub4e4\ub824\uc900 \uc774\uc57c\uae30\ub97c \ubc14\ud0d5\uc73c\ub85c \uae00\uc774 \u000d\u000a\uc4f0\uc5ec\uc84c\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uacfc\uc5f0 \uc774\uac83\uc744 \u2018\uc88b\uc740\uc5f0\uad6c\u2019\ub77c\uace0 \ud560 \uc218 \uc788\uc744\uc9c0\ub294 \uc758\ubb38\uc785\ub2c8\ub2e4. \ud604\u000d\u000a\uc7a5\uc758 \uc6b4\ub3d9\uc774 \ud55c \ubc1c \ub354 \ub098\uc544\uac08 \uc218 \uc788\ub3c4\ub85d \uc790\uadf9\ud558\uace0 \uc0c8\ub85c\uc6b4 \uc0ac\uc720\uc758 \uae38\uc744 \uc5f4\uc5b4\uc8fc\ub294 \uc5f0\uad6c\uac00 \uc544\u000d\u000a\ub2c8\ub77c\uba74, (\uc880 \ub098\uc058\uac8c \ub9d0\ud574\uc11c) \uadf8\uc800 \ud604\uc7a5\uc9c0\uc2dd\uc744 \ucc29\ucde8\ud558\uc5ec \uc81c \uc774\ub825\uc11c\uc5d0 \ud55c \uc904\uc744 \ub298\ub824\uc8fc\ub294 \uac83\uc5d0 \u000d\u000a\ubd88\uacfc\ud55c \uac83\uc774 \ub418\uc5b4\ubc84\ub9b4 \uc218\ub3c4 \uc788\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4.\u000d\u000a\ubc14\ud034\uc790\uad6d\uc744 \uc5ed\uc0ac\uc5d0 \uc0c8\uae30\ub2e4 : \ub1cc\uc131\ub9c8\ube44 \uc7a5\uc560\uc778 \uc6b4\ub3d9 \ud615\uc131 \uacfc\uc815\uc5d0 \ub300\ud55c \uc0dd\uc560\uc0ac\uc801 \uc5f0\uad6c "," & 16\u000d\u000a\uc774\ub7f0 \uc758\uad6c\uc2ec\uc744 \ub5a8\uccd0\ubc84\ub9ac\uc9c0 \ubabb\ud55c \uc774\uc720\ub294 \uc774\uc640 \uac19\uc740 \uc5ed\uc0ac\uc4f0\uae30\uc758 \uc791\uc5c5\uc774 \uc544\uc9c1\uc740 \uc7a5\uc560\uc778\uc6b4\u000d\u000a\ub3d9 \ud604\uc7a5\uc758 \uc695\uad6c\uc5d0 \uae30\ubc18\ud558\uc9c0 \uc54a\uc740 \uac83\uc774\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4. \uc800\uc758 \uc791\uc5c5\uc740 \ub2f9\uc7a5 \uc6b4\ub3d9\ub2e8\uccb4\uc758 \ud604\uc548 \u000d\u000a\uc0ac\uc5c5\ub3c4 \uc544\ub2c8\uc5c8\uace0, \uadf8\ub807\ub2e4\uace0 \ub2e8\uccb4\uc758 00\uc8fc\ub144 \ud589\uc0ac\ub97c \uc55e\ub450\uace0 \ubc1c\uac04\ub418\ub294 \ubc31\uc11c \uc791\uc5c5\uc744 \uc758\ub8b0\ubc1b\uc544 \u000d\u000a\uc9c4\ud589\ub41c \uac83\ub3c4 \uc544\ub2c8\uc5c8\uc2b5\ub2c8\ub2e4. \uc7a5\uae30\uc801\uc73c\ub85c \uc774\ub7f0 \uc791\uc5c5\uc774 \ud544\uc694\ud558\ub2e4\ub294 \uc810\uc5d0 \ub300\ud574\uc11c \ubc18\ub860\uc740 \uc81c\uae30\u000d\u000a\ud558\ub294 \uc0ac\ub78c\uc740 \uac70\uc758 \uc5c6\uc5c8\uc9c0\ub9cc, \uadf8\ub807\ub2e4\uace0 \uc9c0\uae08 \ub2f9\uc7a5 \uaf2d \ud574\uc57c\ub9cc \ud55c\ub2e4\uace0 \ub9d0\ud558\ub294 \uc0ac\ub78c\ub3c4 \uc5c6\uc5c8\uc2b5\u000d\u000a\ub2c8\ub2e4. \uad73\uc774 \ub9d0\ud558\uc790\uba74 \u2018\uc218\uc694 \uc5c6\ub294 \uc0dd\uc0b0\u2019\uc774\uc5c8\ub358 \uc148\uc774\uc8e0. \uadf8\ub807\ub2e4\uba74 \uc774 \uc791\uc5c5\uc740 \uc624\uc9c1 \uc800\ub9cc\uc758 \uc9c0\u000d\u000a\uc801 \ud638\uae30\uc2ec\uc774 \ub9cc\ub4e4\uc5b4\ub0b8 \uc18c\uc0b0\uc77c \ubfd0\uc778\uac00, \uc774\ub7f0 \uc758\ubb38\uc740 \uc5ec\uc804\ud788 \uba38\ub9bf\uc18d\uc744 \ub5a0\ub098\uc9c0 \uc54a\uace0 \uc788\uc2b5\ub2c8\ub2e4.\u000d\u000a\uadf8\ub7ec\ub098 \uc5b8\uc820\uac00\ub294 \uc800\uc758 \uc5ed\uc0ac\uc4f0\uae30\uc5d0 \ub300\ud55c \uace0\ubbfc\uc774 <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\ub77c\ub294 \uc120\uc21c\ud658\uc758 \uace0\u000d\u000a\ub9ac\uc5d0 \ub2ff\uc744 \uac83\uc774\ub77c\ub294 \uae30\ub300\ub294 \uc544\uc9c1 \ub193\uc9c0 \uc54a\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc0ac\ud68c\uc6b4\ub3d9, \ud2b9\ud788 \uc18c\uc218\uc790\ub4e4\uc758 \uc6b4\ub3d9\uc5d0 \u000d\u000a\uc788\uc5b4\uc11c \uac00\uc7a5 \uc911\uc694\ud55c \ub3d9\ub825\uc740 \u2018\uc774\uc57c\uae30\uc758 \ud798\u2019\uc774\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc5b5\uc555\ubc1b\uc544 \uc654\ub358 \uc18c\uc218\uc790\ub4e4\uc758 \u000d\u000a\uc774\uc57c\uae30\ub97c \uc804\ud558\uace0, \uadf8 \uc774\uc57c\uae30\ub4e4\uc758 \uc870\uac01\ub4e4\uc744 \uc774\uc5b4 \ubd99\uc5ec \uc5ed\uc0ac\ub85c \uc120\uc5b8\ud558\ub294 \uacfc\uc815\uc744 \ud1b5\ud574\uc11c \uc0ac\ud68c\u000d\u000a\uc6b4\ub3d9\uc740 \uc790\uae30\ub9cc\uc758 \uae38\uc744 \uac1c\ucc99\ud574 \ub098\uac08 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8 \uc774\uc57c\uae30\ub4e4\uc758 \uc870\uac01\ubcf4\ub97c \ub9cc\ub4dc\ub294\ub370 \uc800\uc758 \uc5f0\u000d\u000a\uad6c\uac00 \uc870\uae08\uc758 \ubcf4\ud0ec\uc774\ub77c\ub3c4 \ub420 \uc218 \uc788\ub3c4\ub85d \uc5b8\uc81c \uc5b4\ub514\uc11c\ub4e0 \uc560\uc4f0\uba70 \uc0b4\uc544\uac00\uaca0\uc2b5\ub2c8\ub2e4.\u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","16 17\u000d\u000a\uc815\uc131\uc870, \uae40\ubcf4\ubbf8, \uc2ec\uae30\uc6a9, \ud55c\uc131\uc9c4 / \ub2e4\uc591\uc131\uc744 \ud5a5\ud55c \uc9c0\uc18d\uac00\ub2a5\ud55c \uc6c0\uc9c1\uc784 \ub2e4\uc6c0, 2022\u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 :\u000d\u000a2021\ub144 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac\u000d\u000a1. \uccad\ub144 \uc131\uc18c\uc218\uc790\uc758 \uc0b6\uc744 \uc5f0\uad6c\ud558\uac8c \ub41c \uacc4\uae30\u000d\u000a\ud55c\uad6d\uc5d0\uc11c \uc131\uc18c\uc218\uc790\ub85c \uc0b4\uc544\uac00\ub294 \uac83\uc740 \uadf8\ub9ac \uc26c\uc6b4 \uc77c\uc774 \uc544\ub2c8\ub2e4. \uc131\uc18c\uc218\uc790\ub85c\uc11c \uc81c\ub3c4\uc801\uc73c\ub85c \u000d\u000a\ubcf4\uc7a5\ubc1b\uc544\uc57c \ud560 \uc2dc\ubbfc\uad8c\uc740 \ucca0\uc800\ud788 \uc678\uba74\ubc1b\uace0 \uc788\uace0, \uc77c\uc0c1\uc5d0\uc11c\ub3c4 \uc27d\uac8c \ud610\uc624\uc640 \ucc28\ubcc4\uc5d0 \ub9c8\uc8fc\ud560 \uc218 \u000d\u000a\uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \ud55c\uad6d \uc815\ubd80\ub294 \uc131\uc18c\uc218\uc790\uc758 \uad8c\ub9ac\ub97c \ubcf4\uc7a5\ud558\ub77c\ub294 UN \uad8c\uace0\ub97c \uc9c0\uc18d\uc801\uc73c\ub85c \ubd88\uc218\u000d\u000a\uc6a9\ud558\uace0 \uc788\ub2e4. \ubb38\uc7ac\uc778 \uc815\ubd80 \uc2dc\uae30 \uc131\uc18c\uc218\uc790 \uc778\uad8c\uc740 \u201c\uc0ac\ud68c\uc801 \ud569\uc758\uac00 \uc774\ub904\uc9c0\uc9c0 \uc54a\uc558\ub2e4\u201d\ub294 \uba85\ubaa9 \u000d\u000a\uc774 \uc5f0\uad6c\ub294 \uc6b0\ub9ac \uc0ac\ud68c\uac00 \uccad\ub144\uc138\ub300\uc5d0 \uc8fc\ubaa9\ud558\uc9c0\ub9cc, \uccad\ub144\uc138\ub300\ub97c \ud558\ub098\uc758 \ub3d9\uc9c8\uc801\uc778 \uc9d1\ub2e8\uc73c\ub85c \uac04\uc8fc\ud558\u000d\u000a\ub294 \uccad\ub144\ub2f4\ub860\uc5d0 \ube44\ud310\uc801\uc774\uace0 \uc2e4\ucc9c\uc801\uc73c\ub85c \uac1c\uc785\ud558\uae30 \uc704\ud55c \uc9c0\ud5a5\uc5d0\uc11c \uc218\ud589\ub410\ub2e4. \uccad\ub144 \uc131\uc18c\uc218\uc790\uc758 \uc0b6\uc744 \ub4e4\u000d\u000a\uc5ec\ub2e4\ubcf4\uace0 \ucc28\ubcc4\uc801 \ud658\uacbd\uc744 \uac1c\uc120\ud558\ub294 \ud65c\ub3d9\uc744 \ud558\ub294 \uc778\uad8c\ub2e8\uccb4\uc778 \u2018\ub2e4\uc591\uc131\uc744 \ud5a5\ud55c \uc9c0\uc18d\uac00\ub2a5\ud55c \uc6c0\uc9c1\uc784\u2019(\ub2e4\u000d\u000a\uc6c0)\uc740 \u2018\uccad\ub144 \uc131\uc18c\uc218\uc790\uc758 \uc0b6\u2019\uc5d0 \uc8fc\ubaa9\ud588\ub2e4. \u300c\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098\u300d\ub294 \ub2e4\uc6c0\uc758 \uc778\uad8c\ud65c\ub3d9\uac00\u000d\u000a\ub4e4\uc774 2021\ub144 \uc9c4\ud589\ud55c \uccad\ub144 \uc131\uc18c\uc218\uc790 3,911\uba85\uc5d0 \ub300\ud55c \uc124\ubb38\uc870\uc0ac\uc640 50\uba85\uc758 \uba74\uc811\uc870\uc0ac\ub97c \ud1a0\ub300\ub85c \uc791\uc131\u000d\u000a\ud55c \ubcf4\uace0\uc11c\uc774\ub2e4. \ubcf4\uace0\uc11c\uc5d0\ub294 \uc870\uc0ac \uacb0\uacfc\ub97c \uc815\uccb4\uc131/\ucee4\ubba4\ub2c8\ud2f0, \ud610\uc624/\ucc28\ubcc4 \uacbd\ud5d8, \uad6c\uc9c1\uacfc\uc815/\uc9c1\uc7a5 \uacbd\ud5d8, \ucf54\u000d\u000a\ub85c\ub09819 \ub4f1 7\uac00\uc9c0 \uc8fc\uc81c\ub85c \uc815\ub9ac\ud558\uc5ec \uccad\ub144 \uc131\uc18c\uc218\uc790\uc758 \uc778\uc2dd\uacfc \uacbd\ud5d8\uc744 \ub2e4\uac01\ub3c4\uc5d0\uc11c \ubcf4\uc5ec\uc8fc\uace0 \uc788\ub2e4. \uacb0\u000d\u000a\ub860\uc73c\ub85c \ucc28\ubcc4\uae08\uc9c0\ubc95 \uc81c\uc815\uc744 \ube44\ub86f\ud574 \ubcc0\ud654\uac00 \uc2dc\uae09\ud55c \uac00\uc871, \uad50\uc721, \uad70\ub300, \uc758\ub8cc, \uad6d\uac00\ud1b5\uacc4 \ub4f1 10\uac1c\uc758 \uacfc\u000d\u000a\uc81c\ub97c \uc81c\uc2dc\ud55c\ub2e4. \u000d\u000a\uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \uc81c1\ud68c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \uc758 \uc2ec\uc0ac\uc694\uac74\uc778 \ud604\uc7a5\uc131, \ub2f9\uc0ac\uc790\uc131, \ud65c\uc6a9\ub3c4 \uce21\uba74\uc5d0\uc11c \u000d\u000a\uc774 \uc5f0\uad6c\uac00 \uc694\uac74\uc744 \ucda9\ubd84\ud788 \ucda9\uc871\ud588\ub2e4\uace0 \ud310\ub2e8\ud558\uc600\ub2e4. \ubb34\uc5c7\ubcf4\ub2e4 \uc124\ubb38\uc870\uc0ac\uc5d0 \ucc38\uc5ec\ud55c 3,911\uba85\uacfc \uadf8 \uc808\ubc18\u000d\u000a\uc774 \ub118\ub294 \uc751\ub2f5\uc790\uac00 \ucd94\uac00 \uba74\uc811\uc744 \ud76c\ub9dd\ud588\ub2e4\ub294 \uc810\uc5d0 \uc8fc\ubaa9\ud588\ub2e4. \uc5f0\uad6c\uc790\ub4e4\uc774 \ub2f9\ucd08 \ubaa9\ud45c\ud588\ub358 1,000\uba85\uc744 \ud558\u000d\u000a\ub8e8\uc5d0 \ub6f0\uc5b4\ub118\uc5c8\uace0, \uc57d 3\uc8fc\uac04 \uc9c4\ud589\ub41c \uc124\ubb38\uc870\uc0ac\uc5d0 \ud55c\uad6d\uc5d0\uc11c \uc9c0\uae08\uae4c\uc9c0 \uc218\ud589\ud55c \uc131\uc18c\uc218\uc790 \uc5f0\uad6c \uac00\uc6b4\ub370 \uac00\u000d\u000a\uc7a5 \ub9ce\uc740 \uc0ac\ub78c\uc774 \ucc38\uc5ec\ud588\ub2e4. \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \uc2e4\ud0dc\uc870\uc0ac\uac00 \ubd80\uc871\ud55c \uc5ec\uac74\uc5d0\uc11c \ub2f9\uc0ac\uc790\ub4e4\ub85c\ubd80\ud130 \uc5f0\uad6c\uc758 \ud544\u000d\u000a\uc694\uc640 \uac00\uce58\uac00 \uc778\uc815\ub418\uc5c8\uc74c\uc744 \ubcf4\uc5ec\uc900\ub2e4. \ub610\ud55c, \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \ub3d9\ub8cc\ud65c\ub3d9\uac00\uc640 \uad6d\ub0b4\uc678 \uc5f0\uad6c\uc790\ub4e4\uacfc\uc758 \ud1a0\ub860\u000d\u000a\uc744 \uac70\uccd0 \uc12c\uc138\ud558\uac8c \uc5f0\uad6c\ubc29\ubc95\uc774 \uc124\uacc4\ub41c \uc810\ub3c4 \uc8fc\uc694\ud558\uac8c \ud3c9\uac00\ud558\uc600\ub2e4. \uadf8 \uacfc\uc815\uc744 \uaf3c\uaf3c\ud558\uac8c \ubcf4\uace0\uc11c\uc5d0 \ub2f4\uc544\u000d\u000a\uc870\uc0ac\uac00 \uac00\uc9c4 \ud55c\uacc4\ub97c \uc194\uc9c1\ud558\uac8c \ub4dc\ub7ec\ub0b4\uace0 \ud6c4\uc18d \uc5f0\uad6c\uc5d0\uc11c \uc774\ub97c \ubcf4\uc644\ud560 \uc218 \uc788\uac8c \ud558\uc600\ub2e4. \uad6d\ud68c\uc640 \ub2e8\uccb4 \uac04\u000d\u000a\ub2f4\ud68c \ub4f1\uc744 \ud1b5\ud574 \uc870\uc0ac\uacb0\uacfc\ub97c \uc9c0\uc18d\uc801\uc73c\ub85c \uacf5\ub860\ud654\ud558\ub824\ub294 \uc810\ub3c4 \uc120\uc815\uc758 \uc8fc\uc694 \uc774\uc720\ub85c \uc0bc\uc558\ub2e4. \uc2ec\uc0ac\uc704\uc6d0\ud68c\u000d\u000a\ub294 \uc6b0\ub9ac \uc0ac\ud68c\uac00 \ubb34\uc218\ud55c \ud610\uc624\ubc1c\uc5b8\uacfc \ucc28\ubcc4\uc744 \uc228\uc8fd\uc778 \uc774\ub4e4\uc758 \ubaa9\uc18c\ub9ac\uc5d0 \uadc0\uae30\uc6b8\uc774\uae38 \ud76c\ub9dd\ud558\uba70, \uadc0\ud55c \uc5f0\u000d\u000a\uad6c\ub97c \ud574\uc900 \uc5f0\uad6c\uc790\ub4e4\uc5d0\uac8c \uac10\uc0ac\ub97c \ud45c\ud55c\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720"," & 18\u000d\u000a\ud558\uc5d0 \uacc4\uc18d \ub098\uc911\uc5d0, \uc5b8\uc820\uac00 \ub17c\uc758\ud574\uc57c \ud560 \uc601\uc5ed\uc73c\ub85c \ubc29\uce58\ub418\uc5c8\ub2e4. \ud559\uad50\ub098 \uad70\ub300, \uc9c1\uc7a5, \uac00\uc871, \uce5c\u000d\u000a\uad6c\uad00\uacc4\uc5d0\uc11c\ub3c4 \uc131\uc18c\uc218\uc790\ub4e4\uc740 \ud610\uc624\uc640 \ucc28\ubcc4\uc744 \uacbd\ud5d8\ud588\ub2e4. \uc77c\ubd80 \uc131\uc18c\uc218\uc790\ub4e4\uc740 \uc9c0\uae08\uae4c\uc9c0\ub3c4 \uc131\uc801\u000d\u000a\uc9c0\ud5a5\uacfc \uc131\ubcc4\uc815\uccb4\uc131\uc744 \uce58\ub8cc\uc758 \ub300\uc0c1\uc73c\ub85c \uc0bc\ub294 \u2018\uc804\ud658\uce58\ub8cc\u2019\ub97c \uad8c\uc720\ubc1b\uac70\ub098 \uac15\uc694\ubc1b\uae30\ub3c4 \ud55c\ub2e4. \uc131\u000d\u000a\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \uc774\ub7f0 \ubd80\uc815\uc801\uc778 \uc0ac\ud68c\ud658\uacbd\uc740 \uace7 \uc131\uc18c\uc218\uc790 \uac1c\uc778\uc758 \uc815\uc2e0\uac74\uac15\uc5d0\ub3c4 \ub9e4\uc6b0 \ud06c\uac8c \uc601\u000d\u000a\ud5a5\uc744 \ub07c\uce58\uace0, \ud55c\uad6d \uc131\uc18c\uc218\uc790\uc758 \uc815\uc2e0\uac74\uac15\uc740 \ub9e4\uc6b0 \uc548 \uc88b\uc740 \uac83\uc73c\ub85c \uc77c\uad00\ub418\uac8c \ubcf4\uace0\ub418\uace0 \uc788\ub2e4.\u000d\u000a\uc774\ub7f0 \uc0c1\ud669\uc5d0\uc11c \uccad\uc18c\ub144\uc131\uc18c\uc218\uc790\uc704\uae30\uc9c0\uc6d0\uc13c\ud130 \ub775\ub3d9\uc740 \uc131\uc18c\uc218\uc790 \uccad\uc18c\ub144\uc758 \ucde8\uc57d\uc131\uc5d0 \uc8fc\ubaa9\u000d\u000a\ud558\uc600\uace0, \uadf8 \uacb0\uacfc \ub610\ub798 \uccad\uc18c\ub144\uc5d0 \ube44\ud574 \uc131\uc18c\uc218\uc790 \uccad\uc18c\ub144\uc774 \uc131\uc815\uccb4\uc131\uc744 \uc774\uc720\ub85c \ucc28\ubcc4\uacfc \ud610\uc624\ub97c \u000d\u000a\ub354 \uacbd\ud5d8\ud558\uac70\ub098, \ud559\uad50\ud3ed\ub825, \uc131\ud3ed\ub825, \uac00\uc815\ud3ed\ub825 \ub4f1\uc5d0 \ub354 \ub178\ucd9c\ub418\uae30 \uc27d\ub2e4\ub294 \uc0ac\uc2e4\uc744 \uacf5\ub860\ud654\ud560 \uc218 \u000d\u000a\uc788\uc5c8\ub2e4. \ub2e4\uc6c0 \ud65c\ub3d9\uac00\ub4e4\ub3c4 \uccad\uc18c\ub144 \uc131\uc18c\uc218\uc790\ub85c\uc11c \uc0b4\uc544\uc654\ub358 \uacbd\ud5d8\uc774 \uc788\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uadf8\ub7f0 \uc0c1\ud669\u000d\u000a\ub4e4\uc744 \uc5b4\ub835\uc9c0 \uc54a\uac8c \uacf5\uac10\ud560 \uc218 \uc788\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uc5ec\uae30\uc11c \ud55c \ub2e8\uacc4 \ub098\uc544\uac00\uba74, \ucde8\uc57d\ud588\ub358 \uccad\uc18c\ub144 \uc2dc\u000d\u000a\uae30\uac00 \uc9c0\ub098\uba74 \uc131\uc18c\uc218\uc790\ub4e4\uc740 \uc548\uc815\ud654\ub418\ub294\uac00\uc5d0 \ub300\ud55c \uace0\ubbfc\uc774 \ub0a8\ub294\ub2e4. \uc131\uc18c\uc218\uc790\ub4e4\uc758 \uccad\ub144\uae30\ub294 \u000d\u000a\uc5b4\ub5a0\ud55c\uac00. \ub2e4\uc6c0 \ud65c\ub3d9\uac00\ub4e4\uc774 \uccad\ub144 \ub2f9\uc0ac\uc790\uc600\uae30\uc5d0, \uc9c1\uac10\uc801\uc73c\ub85c \uc77c\ubc18 \uccad\ub144\uacfc \uc131\uc18c\uc218\uc790 \uccad\ub144\uc774 \u000d\u000a\uacaa\ub294 \uc0ac\ud68c\uc801 \ud604\uc2e4\uacfc \uc695\uad6c\uac00 \ub2e4\ub984\uc744 \uc54c\uace0 \uc788\uc5c8\ub2e4. \u000d\u000a\ud55c\ud3b8, \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c\ub294 \uccad\ub144 \ucde8\uc5c5\uc774 \uc0ac\ud68c\uc801\uc73c\ub85c \ud070 \uad00\uc2ec\uac70\ub9ac\uace0, \uccad\ub144\uae30\uac00 \ub300\uccb4\ub85c \u2018\uc790\uc6d0 \u000d\u000a\uc5c6\uc74c\u2019 \ud639\uc740 \u2018\uae30\ud68c\uc758 \ubd80\uc7ac\u2019, \u2018\uacbd\uc7c1\uc0ac\ud68c \uc785\ubb38\ub2e8\uacc4\u2019\uc758 \ud45c\uc0c1\uc73c\ub85c \uc5ec\uaca8\uc9c0\ub294 \uc0c1\ud669\uc5d0\uc11c \uccad\ub144\uc740 \uc77c\u000d\u000a\uc885\uc758 \ucde8\uc57d\uacc4\uce35\uc73c\ub85c \ubb18\uc0ac\ub418\uace4 \ud55c\ub2e4. \uadf8\ub7ec\ub098 \uc815\uce58\uad8c\uc774\ub098 \uc81c\ub3c4\uad8c\uc5d0\uc11c \uccad\ub144 \ud45c\uc2ec\uc744 \uc7a1\uae30 \uc704\ud574 \u000d\u000a\uae30\ud68d\ud55c \uccad\ub144\uc815\ucc45 \uc18d\uc5d0 \uc131\uc18c\uc218\uc790 \uccad\ub144\uc740 \uc5c6\uc5c8\ub2e4. \ub9c8\ucc2c\uac00\uc9c0\ub85c \ub300\ud559, \uad70\ub300, \uae30\uc5c5, \ucde8\uc5c5\ubb38\ud131 \ub4f1\u000d\u000a\uc5d0\uc11c \uc131\uc18c\uc218\uc790 \uc778\uad8c\uc774 \uace0\ub824\ub418\ub294 \uacbd\uc6b0\ub294 \ub9ce\uc9c0 \uc54a\ub2e4. \uacb0\uad6d \uc131\uc18c\uc218\uc790 \uccad\ub144\uc740 \ud2b8\ub79c\uc2a4\uc820\ub354\uac00 \uc544\u000d\u000a\ub2cc \uc774\uc131\uc560\uc790 \ub0a8\uc131/\uc5ec\uc131\uc778 \uccad\ub144\uc758 \ud45c\uc0c1\uc5d0 \uac00\ub824\uc838 \ube44\uac00\uc2dc\ud654\ub418\uc5c8\ub2e4. \uc77c\ubc18 \uccad\ub144 \uc778\uad6c\uc5d0 \ub300\ube44\u000d\u000a\ud588\uc744 \ub54c \uc131\uc18c\uc218\uc790 \uccad\ub144\uc774 \uac00\uc9c4 \uace0\uc720\uc758 \uc695\uad6c\uc640 \ucde8\uc57d\uc131\uc774 \ubd84\uba85 \uc874\uc7ac\ud560 \uac83\uc784\uc5d0\ub3c4 \uc774\ub294 \uc81c\ub3c4\uc801\u000d\u000a\uc73c\ub85c \uc804\ud600 \ubc18\uc601\ub418\uace0 \uc788\uc9c0 \uc54a\uc558\ub2e4.\u000d\u000a\ud2b9\ud788 2020~2021\ub144\uc5d0 \uac78\uce5c \ucf54\ub85c\ub09819 \ubc29\uc5ed\uc744 \uc704\ud55c \uac70\ub9ac\ub450\uae30\ub294 \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0 \uc804\ubc18\u000d\u000a\uc744 \ud06c\uac8c \uc704\ucd95\uc2dc\ucf30\ub2e4. \uc8fc\uc810, \ucd95\uc81c, \uc18c\uaddc\ubaa8 \uc0ac\uc801\ubaa8\uc784\uc744 \uae30\ubc18\uc73c\ub85c \ud55c \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\ub294 \ubaa8\u000d\u000a\uc784 \uc790\uccb4\uac00 \uac70\uc758 \ubd88\uac00\ub2a5\ud574\uc9c0\uba74\uc11c \uc544\uc608 \ubaa8\uc784\uc774 \uc0ac\ub77c\uc9c0\uac70\ub098 \uc720\uc9c0\ud558\uae30 \uc5b4\ub824\uc6cc\uc9c0\uba74\uc11c \uadf8 \uaddc\ubaa8\u000d\u000a\ub098 \ub2e4\uc591\uc131\uc774 \ud06c\uac8c \uc904\uc5b4\ub4e4 \uc218\ubc16\uc5d0 \uc5c6\uc5c8\ub2e4. \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\uac00 \uc704\ucd95\ub418\uba74\uc11c \uc131\uc18c\uc218\uc790 \uac1c\uc778\u000d\u000a\uc740 \uc27d\uac8c \uace0\ub9bd\ub420 \uc218 \uc788\uc5c8\uace0, \uc774 \uace0\ub9bd\ub41c \ud658\uacbd\uc774 \uc131\uc18c\uc218\uc790\ub4e4\uc5d0\uac8c \ubd80\uc815\uc801 \uc601\ud5a5\uc744 \ub07c\uce58\uace0 \uc788\uc744\u000d\u000a\uac83\uc774\ub77c \uc608\uc0c1\ud560 \uc218 \uc788\uc5c8\ub2e4.","18 19\u000d\u000a2021\ub144 \uc774\ub7f0 \uc0ac\ud68c\uc801 \uc870\uac74\ub4e4\uc5d0\uc11c \ub2e4\uc6c0 \uc5f0\uad6c\uc9c4\uc740 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc5f0\uad6c\uc870\uc0ac\ub97c \uc2dc\ud589\ud558\uae30\ub85c \u000d\u000a\ud588\ub2e4. \uccad\ub144 \uc131\uc18c\uc218\uc790\uc758 \ud2b9\uc774\uc131\uc744 \ubc1c\uad74\ud558\uace0, \uc774\ub97c \uc0ac\ud68c\uc801\uc73c\ub85c \uacf5\ub860\ud654\ud558\ub294 \uac83\uc774 \uc5f0\uad6c\uc758 \ubaa9\ud45c\u000d\u000a\uc600\ub2e4. \uadf8\ub7ec\ub098 \uccad\ub144 \uc131\uc18c\uc218\uc790\ub97c \ub2e8\uc21c\ud788 \ubd80\uc815\uc801 \uacbd\ud5d8\ub9cc \uacaa\ub294 \uc874\uc7ac\ub85c \uc124\uc815\ud558\uace0 \uc2f6\uc9c0 \uc54a\uc558\uace0, \u000d\u000a\ucc28\ubcc4\uacfc \ud610\uc624\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \ub2e4\ucc44\ub85c\uc6b4 \uc695\uad6c\uc640 \ubc30\uacbd\uc744 \uac00\uc9c4 \ubcf5\ud569\uc801\uc778 \uc874\uc7ac\ub85c\uc11c \ub4dc\ub7ec\ub0b4\uace0 \uc2f6\u000d\u000a\uc5c8\ub2e4. \uadf8\ub807\uae30 \ub54c\ubb38\uc5d0 \ub2e4\uc6c0 \uc5f0\uad6c\uc870\uc0ac\uc758 \uacb0\ub860\uc5d0\uc11c\ub294 \ucc28\ubcc4 \ud53c\ud574\ub97c \uc608\ubc29\ud558\ub294 \ucc28\uc6d0\ubfd0 \uc544\ub2c8\ub77c, \uccad\u000d\u000a\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc774 \ub2a5\ub3d9\uc801\uc774\uace0 \uc801\uadf9\uc801\uc73c\ub85c \uc6d0\ud558\ub294 \ubc14\ub97c \uc694\uad6c\ud558\ub294 \uc774\uc57c\uae30\ub3c4 \ub2f4\uc744 \uc218 \uc788\uc5c8\ub2e4.\u000d\u000a2. \uc5f0\uad6c\uc870\uc0ac \uc218\ud589\uacfc \uacb0\uacfc\u000d\u000a\ubcf8 \uc5f0\uad6c\uc870\uc0ac\ub294 \ucd1d 3,911\uba85\uc758 \uccad\ub144 \uc131\uc18c\uc218\uc790\uac00 \ucc38\uc5ec\ud558\uc600\ub2e4. \uc5f0\uad6c\uc870\uc0ac\uc758 \ub300\uc0c1\uc740 \ud55c\uad6d\uc5d0\uc11c \u000d\u000a\ucd5c\uadfc 10\ub144\uac04 \uac70\uc8fc\ud55c \ub9cc19\uc138 \uc774\uc0c1 \ub9cc34\uc138 \uc774\ud558 \uccad\ub144\uc774\uc790 \uc2a4\uc2a4\ub85c \uc131\uc18c\uc218\uc790\ub85c\uc11c \uc815\uccb4\ud654\ud55c \uc0ac\u000d\u000a\ub78c\uc774\ub2e4. \uc124\ubb38\uc870\uc0ac\ub294 2021\ub144 8\uc6d4 11\uc77c\ubd80\ud130 8\uc6d4 31\uc77c \ud55c \ucc28\ub840 \uc9c4\ud589\ud55c \ud6c4, \ud45c\ubcf8\uc774 \ubd80\uc871\ud588\ub358 \u000d\u000a\ud2b8\ub79c\uc2a4\uc820\ub354\ub9cc\uc744 \ub300\uc0c1\uc73c\ub85c 9\uc6d4 2\uc77c\ubd80\ud130 9\uc6d4 7\uc77c\uae4c\uc9c0 \ucd94\uac00\ub85c \uc2e4\uc2dc\ud558\uc600\ub2e4. \uba74\uc811\uc870\uc0ac\ub294 2021\u000d\u000a\ub144 9\uc6d4\ubd80\ud130 10\uc6d4 \uc0ac\uc774 \ucd94\uac00 \uba74\uc811\uc870\uc0ac\uc5d0 \ub3d9\uc758\ud55c \uc124\ubb38\uc870\uc0ac \ub300\uc0c1\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \uc9c4\ud589\ud558\uc600\ub2e4.\u000d\u000a2022\ub144 2\uc6d4 \uc911\uac04\ubcf4\uace0\uc11c\ub97c \ubc14\ud0d5\uc73c\ub85c \uad8c\uc778\uc219\u00b7\uc7a5\ud61c\uc601 \ubc0f \uc5ec\ub7ec \uad6d\ud68c\uc758\uc6d0\ub4e4\uacfc\uc758 \uacf5\ub3d9\uc8fc\ucd5c\ub85c \u000d\u000a<\ud55c\uad6d \uc131\uc18c\uc218\uc790 \uccad\ub144\uc744 \ub9d0\ud558\ub2e4> \uad6d\ud68c\ud1a0\ub860\ud68c\ub97c \uc9c4\ud589\ud558\uc600\uace0, \uac19\uc740 \ud574 5\uc6d4 17\uc77c \uad6d\uc81c\uc131\uc18c\uc218\u000d\u000a\uc790\ud610\uc624\ubc18\ub300\uc758\ub0a0\uc744 \ub9de\uc774\ud558\uc5ec \ucd5c\uc885 \uacb0\uacfc\ubcf4\uace0\uc11c\ub97c \ubc1c\uac04\ud558\uc600\ub2e4. \uacb0\uacfc\ubcf4\uace0\uc11c \uc81c\ubaa9\uc740 \ud55c \uba74\uc811\uc870\u000d\u000a\uc0ac \ucc38\uac00\uc790\uc758 \ubc1c\uc5b8\uc744 \ub530\uc11c \u201c\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098\u201d\ub77c\uace0 \uc9d3\uac8c \ub418\uc5c8\ub2e4.\u000d\u000a3. \uc5f0\uad6c\uc870\uc0ac \ub0b4\uc6a9 \uc694\uc57d\u000d\u000a\ub2e4\uc6c0 \uc5f0\uad6c\uc9c4\uc740 \ubcf8 \uc5f0\uad6c\uc870\uc0ac\ub97c 15\uac00\uc9c0 \ud14c\ub9c8\ub85c \ub0b4\uc6a9 \uc694\uc57d\ud558\uc600\ub2e4. \ub2e4\uc74c\uc758 \uc5f0\uad6c\uc870\uc0ac \ub0b4\uc6a9 \uc694\u000d\u000a\uc57d\uc740 \ubcf8 \uc5f0\uad6c\uc870\uc0ac\uc758 \u2018\uacb0\ub860\uacfc \ud568\uc758\u2019 \ubd80\ubd84\uc744 \uc904\uc774\uac70\ub098 \uc218\uc815\ud558\uc5ec \ucca8\ubd80\ud55c \uac83\uc774\ub2e4.\u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 : 2021\ub144 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac "," & 20\u000d\u000a1) \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc758 \uc131\uc815\uccb4\uc131 \ud45c\ud604 \ub2e4\uc591\ud654\u000d\u000a\uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc774 \uc790\uc2e0\uc758 \uc131\ubcc4\uc815\uccb4\uc131\uacfc \uc131\uc801\uc9c0\ud5a5\uc744 \uc774\ud574\ud558\uace0 \ud45c\ud604\ud558\ub294 \ubc29\uc2dd\uc774 \ud6e8\uc52c \u000d\u000a\ub2e4\uc591\ud574\uc84c\ub2e4. \ubcf8 \uc870\uc0ac\uc758 \uac00\uc7a5 \ud070 \ud2b9\uc9d5\uc740 \uc751\ub2f5\uc790\uc758 \uc131\ubcc4\uc815\uccb4\uc131\uacfc \uc131\uc801\uc9c0\ud5a5\uc774 \uacfc\uac70 \ub2e4\ub978 \uc870\u000d\u000a\uc0ac\ub4e4\uc5d0 \ube44\ud574 \ub2e4\ucc44\ub85c\uc6cc\uc84c\ub2e4\ub294 \uac83\uc774\ub2e4. \ud754\ud788 \uc804\ud1b5\uc801\uc778 \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc774\ub77c\uace0 \ud560 \uc218 \uc788\ub294 \u000d\u000aLGBT(\ub808\uc988\ube44\uc5b8, \uac8c\uc774, \ubc14\uc774\uc139\uc288\uc5bc, \ud2b8\ub79c\uc2a4\uc820\ub354) \ubfd0\ub9cc \uc544\ub2c8\ub77c \ubc94\uc131\uc560(\ud32c\uc139\uc288\uc5bc), \ud034\uc5b4, \ubb34\u000d\u000a\uc131\uc560(\uc5d0\uc774\uc139\uc288\uc5bc), \ub17c\ubc14\uc774\ub108\ub9ac\uc640 \uc820\ub354\ud034\uc5b4 \ub4f1 \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc758 \ub2e4\uc591\ud55c \ubd84\ud3ec\ub97c \uad00\ucc30\ud560 \uc218 \u000d\u000a\uc788\uc5c8\ub2e4.\u000d\u000a\uc804\ud1b5\uc801\uc778 \uc131\ubcc4\uc815\uccb4\uc131\uacfc \uc131\uc801\uc9c0\ud5a5 \ubc94\uc8fc \uc774\uc678\uc5d0 \ub2e4\uc591\ud55c \ubc29\uc2dd\uc73c\ub85c \uc2a4\uc2a4\ub85c \uc815\uccb4\ud654\ud558\ub294 \uc80a\uc740 \u000d\u000a\uc138\ub300\uc758 \ub4f1\uc7a5\uc740 \ud55c\uad6d\ub9cc\uc758 \uc608\uc678\uc801\uc778 \uc77c\uc774 \uc544\ub2c8\ub2e4. \ucd5c\uadfc \ud574\uc678 \uc5f0\uad6c\ub4e4\ub3c4 \uc720\uc0ac\ud558\uac8c 10\ub300\uc640 20\u000d\u000a\ub300\uc758 \uc80a\uc740 \uc131\uc18c\uc218\uc790\ub4e4\uc774 \ud32c\uc139\uc288\uc5bc\uc774\ub098 \ub17c\ubc14\uc774\ub108\ub9ac, \uc5d0\uc774\uc139\uc288\uc5bc \ub4f1 \uc804\ud1b5\uc801\uc778 LGBT \ubc94\uc8fc\uc5d0 \u000d\u000a\uad6d\ud55c\ub418\uc9c0 \uc54a\ub294 \ub2e4\uc591\ud55c \ubc29\uc2dd\uc758 \uc815\uccb4\ud654\ub97c \ud55c\ub2e4\uace0 \ubcf4\uace0\ud558\uace0 \uc788\ub2e4. \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc758 \ub2e4\uc591\ud654\u000d\u000a\ub294 \ud5a5\ud6c4 \uc131\uc18c\uc218\uc790 \ub300\uc0c1 \uc5f0\uad6c\uc870\uc0ac\ub294 \ubb3c\ub860 \ucee4\ubba4\ub2c8\ud2f0\uc640 \ubc95\uc81c\ub3c4 \ucc28\uc6d0 \ubaa8\ub450\uc5d0\uc11c \ubcf5\uc7a1\ub2e4\ub2e8\ud558\uace0 \u000d\u000a\uc0c8\ub85c\uc6b4 \ud544\uc694\uc5d0 \ub9de\ub294 \ubcc0\ud654\ub97c \ubaa8\uc0c9\ud574\uc57c \ud558\ub294 \uacfc\uc81c\uac00 \uc8fc\uc5b4\uc84c\uc74c\uc744 \uc758\ubbf8\ud55c\ub2e4.\u000d\u000a2) \uac15\ud55c \ucee4\ubc0d\uc544\uc6c3\uc5d0 \ub300\ud55c \uc695\uad6c\u000d\u000a\ubc29\uc1a1\uc778 \ud64d\uc11d\ucc9c\uacfc \ud558\ub9ac\uc218\uc758 \ucee4\ubc0d\uc544\uc6c3 \ud6c4 20\uc5ec\ub144\uc774 \uc9c0\ub09c \uc9c0\uae08\ub3c4 \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc5d0\uac8c \ucee4\u000d\u000a\ubc0d\uc544\uc6c3\uc740 \uc5ec\uc804\ud788 \uc911\uc694\ud55c \ud654\ub450\ub2e4. \uc131\uc18c\uc218\uc790\uc5d0\uac8c \uc131\ubcc4\uc815\uccb4\uc131\uacfc \uc131\uc801\uc9c0\ud5a5\uc740 \u2018\ub098\u2019\ub77c\ub294 \uc874\uc7ac\ub97c \u000d\u000a\uad6c\uc131\ud558\ub294 \ub9e4\uc6b0 \uc911\uc694\ud55c \ubd80\ubd84\uc774\ub2e4. \uc751\ub2f5\uc790\uc758 88.2%\ub294 \u2018\ub0b4\uac00 \uc131\uc18c\uc218\uc790\uc778 \uac83\uc740 \ub098\uc5d0\uac8c \uc911\uc694\ud558\u000d\u000a\ub2e4\u2019\uace0 \uc751\ub2f5\ud588\ub2e4. \u2018\ub098\u2019\ub97c \uc628\uc804\ud788 \ub4dc\ub7ec\ub0b4\uc9c0 \ubabb\ud558\uace0 \uc228\uaca8\uc57c\ub9cc \ud558\ub294 \ud604\uc2e4\uc740 \ub9ce\uc740 \uccad\ub144 \uc131\uc18c\uc218\uc790\u000d\u000a\uc5d0\uac8c \uad34\ub86d\uace0\ub3c4 \uc9c0\uaca8\uc6b4 \uc77c\uc77c \uc218\ubc16\uc5d0 \uc5c6\ub2e4. \uc9c1\uc7a5\uc0dd\ud65c\uc744 \uc798\ud558\ub294\ub370 \uc131\uc18c\uc218\uc790\ub85c\uc11c \uac00\uc7a5 \ud544\uc694\ud55c \u000d\u000a\uc694\uc18c\uac00 \ubb34\uc5c7\uc778\uc9c0 \ubb3b\ub294 \uc9c8\ubb38\uc5d0 \uc751\ub2f5\uc790 \uc57d 61.6%\uac00 \u2018\ucee4\ubc0d\uc544\uc6c3\uc744 \ud560 \uc218 \uc788\ub294 \ubd84\uc704\uae30\u2019\ub97c \uaf3d\uc740 \u000d\u000a\uac83\uc740 \uc790\uc5f0\uc2a4\ub7ec\uc6b4 \uacb0\uacfc\ub2e4.\u000d\u000a\ucc28\ubcc4\uacfc \ud3ed\ub825\uc744 \uc218\ubc18\ud558\ub294 \uc815\uccb4\uc131\uc758 \ub178\ucd9c\uc5d0 \ub300\ud55c \uc6b0\ub824\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc774 \u000d\u000a\uc77c\uc0c1\uc801\uc73c\ub85c \uc790\uc2e0\uc774 \uc131\uc18c\uc218\uc790\uc784\uc744 \ub4dc\ub7ec\ub0b4\ub294 \uacbd\uc6b0\uac00 \uacfc\uac70\uc5d0 \ube44\ud574 \ub354\uc6b1 \ub298\uc5b4\ub09c \uac83\uc744 \ud655\uc778\ud560 \u000d\u000a\uc218 \uc788\uc5c8\ub2e4. \uc628\ub77c\uc778 \uc18c\uc15c\ubbf8\ub514\uc5b4\ub97c \uc0ac\uc6a9\ud560 \ub54c \u2018\uc815\uccb4\uc131\uc744 \ub4dc\ub7ec\ub0b4\uace0 \uc0ac\uc6a9\ud55c\ub2e4\u2019\ub294 \uc751\ub2f5\uc774 2014\u000d\u000a\ub144 \u300c\ud55c\uad6d LGBTI \ucee4\ubba4\ub2c8\ud2f0 \uc0ac\ud68c\uc801 \uc695\uad6c\uc870\uc0ac\u300d\uc5d0\uc11c\ub294 10.8%\uc5d0 \ubd88\uacfc\ud588\uc9c0\ub9cc, \ubcf8 \uc870\uc0ac\uc5d0\uc11c\ub294 \u000d\u000a26.2%\ub85c \ub298\uc5b4\ub0ac\ub2e4. \ud55c\uad6d \uc0ac\ud68c\uc758 \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \ud3b8\uacac, \ubd88\uad00\uc6a9, \ud3ed\ub825\uc774 \uc904\uc5b4\ub4e4\uc5c8\ub2e4\uace0 \ub2f5","20 21\u000d\u000a\ubcc0\ud55c \uc751\ub2f5\uc790 \uc911 70.0%\ub294 \uae0d\uc815\uc801\uc778 \ubcc0\ud654\uc758 \uc694\uc778\uc73c\ub85c \u2018\uc77c\uc0c1 \uc18d \uc131\uc18c\uc218\uc790\ub4e4\uc758 \uac00\uc2dc\ud654\u2019\ub97c \uaf3d\u000d\u000a\uae30\ub3c4 \ud588\ub2e4. \uc804\ubc18\uc801\uc73c\ub85c \ucee4\ubc0d\uc544\uc6c3, \ub610\ub294 \uc815\uccb4\uc131\uc758 \ub178\ucd9c\uc740 \ub450\ub824\uc6b4 \uc77c\uc774\uae30\ub3c4 \ud558\uc9c0\ub9cc, \uc790\uae30 \uc790\u000d\u000a\uc2e0\uc744 \uae0d\uc815\ud558\uace0\uc790 \ud558\ub294 \uc695\uad6c \uc18d\uc5d0\uc11c \uc774\uac83\uc774 \ubcc0\ud654\ub97c \ucd94\ub3d9\ud558\ub294 \ud589\uc704\uc774\uae30\ub3c4 \ud558\ub2e4\ub294 \uc778\uc2dd\uc744 \uc5ff\u000d\u000a\ubcfc \uc218 \uc788\uc5c8\ub2e4.\u000d\u000a3) \ubaa8\ub450\uac00 \uc548\uc804\ud55c \ucee4\ubba4\ub2c8\ud2f0\u000d\u000a\uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc5d0\uac8c \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\ub294 \uc790\uc2e0\uacfc \ube44\uc2b7\ud55c \uc0ac\ub78c\uc744 \ub9cc\ub0a0 \uc218 \uc788\ub294 \uacf5\uac04\uc77c \u000d\u000a\ubfd0\ub9cc \uc544\ub2c8\ub77c, \u2018\ub098\u2019\ub85c \uc874\uc7ac\ud574\ub3c4 \uad1c\ucc2e\uc740 \uc548\uc804\ud55c \uacf5\uac04\uc774\uba70, \uc815\uccb4\uc131\uacfc \uad00\ub828\ud55c \ub2e4\uc591\ud55c \uc815\ubcf4\ub97c \uc5bb\u000d\u000a\uc744 \uc218 \uc788\ub294 \uacf3\uc73c\ub85c \uc778\uc2dd\ub418\uace0 \uc788\ub2e4. \uadf8\ub7ec\ub098 \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\uac00 \ubaa8\ub450\uc5d0\uac8c \ud3b8\uc548\ud558\uace0 \uc548\uc804\u000d\u000a\ud558\uac8c \ub290\uaef4\uc9c0\uace0 \uc788\ub294 \uac83\uc740 \uc544\ub2c8\ub2e4. \uc751\ub2f5\uc790\uc758 75.4%\ub294 \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\uc5d0 \ucc38\uc5ec\ud558\ub294 \uac8c \uc5b4\u000d\u000a\ub835\ub2e4\uace0 \ub2f5\ud588\ub2e4. \uc548\uc804\ud558\ub2e4\uace0 \ub290\uaef4\uc9c0\uc9c0 \uc54a\uc544\uc11c(16.7%), \uc131\uc18c\uc218\uc790 \uc911\uc5d0\uc11c\ub3c4 \ube44\uc8fc\ub958\uc778 \uc815\uccb4\u000d\u000a\uc131 \ub54c\ubb38\uc5d0 \ubc30\ucc99\ubc1b\uc744\uae4c\ubd10 \ub450\ub824\uc6c0(14.9%), HIV/AIDS \uac10\uc5fc\uc778\uc5d0 \ub300\ud55c \ub099\uc778 \ub54c\ubb38\uc5d0(2.8%) \ub4f1 \u000d\u000a\uc801\uc9c0 \uc54a\uc740 \uc774\ub4e4\uc740 \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0 \ub0b4\uc5d0\ub3c4 \uc5ec\ub7ec \uc704\ud5d8\uacfc \ubc30\uc81c\uac00 \uc874\uc7ac\ud55c\ub2e4\uace0 \ub290\ub07c\uace0 \uc788\uc5c8\u000d\u000a\ub2e4. \ud2b8\ub79c\uc2a4\ub0a8\uc131(36.0%)\uacfc \ud2b8\ub79c\uc2a4\uc5ec\uc131(29.4%), \ub17c\ubc14\uc774\ub108\ub9ac/\uc820\ub354\ud034\uc5b4(39.5%), \uc5d0\uc774\uc139\uc288\uc5bc\u000d\u000a(35.8%)\uc740 \ube44\uc8fc\ub958\uc778 \uc815\uccb4\uc131\uc5d0 \ub300\ud55c \ubc30\uc81c\ub97c \ud06c\uac8c \uccb4\uac10\ud588\uace0, \ubc14\uc774\uc139\uc288\uc5bc \ub0a8\uc131\uc740 \uc0c1\ub300\uc801\uc73c\ub85c \u000d\u000a\uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\uac00 \uc548\uc804\ud558\uc9c0 \uc54a\ub2e4\uace0 \ub290\ub07c\uace0 \uc788\uc5c8\ub2e4(23.8%). \uc774\ub294 \uc2e0\ubd84 \ub178\ucd9c \ub4f1 \uc544\uc6c3\ud305\u000d\u000a\uc758 \uc704\ud5d8\uc5d0 \ub300\ud55c \uac71\uc815\uc774 \uc774\ub4e4\uc5d0\uac8c\uc11c \ud06c\uae30 \ub54c\ubb38\uc73c\ub85c \ucd94\uce21\ub41c\ub2e4(66.9%).\u000d\u000a\ud2b8\ub79c\uc2a4\ud610\uc624\uc5d0 \uad00\ub828\ud574\uc11c\ub3c4 \uc8fc\ubaa9\ud560 \ub9cc\ud558\ub2e4. \uc2a4\uc2a4\ub85c \ud398\ubbf8\ub2c8\uc2a4\ud2b8\ub77c\uace0 \ub2f5\ud55c \uc751\ub2f5\uc790\uc758 36.4%\u000d\u000a\ub294 \uc790\uc2e0\uc774 \uc18d\ud55c \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\uc758 \uc5ec\uc131\ud610\uc624\uac00 \uc2ec\uac01\ud558\ub2e4\uace0 \uc751\ub2f5\ud558\uc600\ub2e4. \ub610, \u2018\ud2b8\ub79c\uc2a4\uc5ec\u000d\u000a\uc131\uc774 \uc5ec\ub300\uc5d0 \uc785\ud559\ud560 \uc218 \uc788\uc5b4\uc57c \ud55c\ub2e4\u2019\ub294 \ubb38\ud56d\uc5d0 \ub300\ud574 \uc2a4\uc2a4\ub85c \ud398\ubbf8\ub2c8\uc2a4\ud2b8\ub77c\uace0 \ub2f5\ud55c \uc751\ub2f5\uc790\u000d\u000a\uc758 81.4%, \ud398\ubbf8\ub2c8\uc2a4\ud2b8\uac00 \uc544\ub2c8\ub77c\uace0 \ub2f5\ud55c \uc751\ub2f5\uc790\uc758 84.1%\uac00 \u2018\ub3d9\uc758\ud55c\ub2e4\u2019\uace0 \ub2f5\ud574 \ub450 \uc9d1\ub2e8 \uc0ac\u000d\u000a\uc774\uc5d0 \ud070 \ucc28\uc774\uac00 \uc5c6\uc5c8\uace0, \uc77c\ubd80 \uc131\uc18c\uc218\uc790 \ub2f9\uc0ac\uc790\ub4e4 \ub610\ud55c \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc758 \uc5ec\ub300 \uc785\ud559\uc5d0 \ub3d9\uc758\ud558\u000d\u000a\uc9c0 \uc54a\ub294 \uac83\uc744 \ubc1c\uacac\ud558\uc600\ub2e4.\u000d\u000a\uc774\ub7ec\ud55c \uacb0\uacfc\ub294 \ub9ce\uc740 \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc774 \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\uc5d0 \ubc14\ub77c\uace0 \uae30\ub300\ud558\ub294 \ubc14\uc640 \ub2ec\u000d\u000a\ub9ac \ucee4\ubba4\ub2c8\ud2f0 \uc548\uc5d0 \uc5ec\ub7ec \ubc30\uc81c\uc640 \uac08\ub4f1\uc774 \ub2e4\uc18c\uac04 \uc874\uc7ac\ud55c\ub2e4\ub294 \uac83\uc744 \ud568\uc758\ud55c\ub2e4. \uc0c1\ub300\uc801\uc73c\ub85c \ube44\uc8fc\u000d\u000a\ub958\uc758 \uc815\uccb4\uc131\uc744 \uc9c0\ub2cc \uc774\ub4e4\uc740 \uc131\uc18c\uc218\uc790 \ucee4\ubba4\ub2c8\ud2f0\uc5d0\uc11c \ud568\uaed8 \uc5b4\uc6b8\ub9ac\ub294 \ub370 \ub9ce\uc740 \uc5b4\ub824\uc6c0\uc744 \uacaa\uace0 \u000d\u000a\uc788\ub294 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4. \u2018\ubaa8\ub450\uc5d0\uac8c\u2019 \ud3b8\uc548\ud558\uace0 \uc548\uc804\ud55c \uacf5\uac04\uc744 \ub9cc\ub4e4\uae30 \uc704\ud55c \ucee4\ubba4\ub2c8\ud2f0 \ucc28\uc6d0\uc758 \ub2e4\u000d\u000a\uc591\ud55c \ub178\ub825\uc774 \uc694\uccad\ub418\ub294 \uc774\uc720\uc774\ub2e4.\u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 : 2021\ub144 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac "," & 22\u000d\u000a4) \uc77c\uc0c1\uc801\uc778 \ucc28\ubcc4\uacfc \ud610\uc624 \ub9cc\uc5f0\u000d\u000a\uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc5d0\uac8c \uc790\uc2e0\uc758 \uc815\uccb4\uc131\uc744 \ub4dc\ub7ec\ub0b4\ub3c4 \uc874\uc911\ubc1b\ub294 \ud658\uacbd\uc774 \uc5ec\uc804\ud788 \uc911\uc694\ud55c \uc774\uc288\u000d\u000a\ub2e4. \uadf8 \uc774\uc720\ub85c \uc790\uc2e0\uc758 \uc815\uccb4\uc131\uc744 \uc0ac\ud68c\uc801 \uc57d\uc810\uc73c\ub85c \uc778\uc9c0\ud558\uace0 \uc544\uc6c3\ud305\uc758 \uc704\ud5d8\uc744 \uc758\uc2dd\ud55c\ub2e4\ub294 \uc810\u000d\u000a\uc774 \uc788\uc5c8\ub2e4. \uc790\uc2e0\uc774 \uc131\uc18c\uc218\uc790\ub77c\ub294 \uc810\uc744 \uc77c\uc885\uc758 \uc57d\uc810\uc73c\ub85c \uc778\uc9c0\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uc774\ub7ec\ud55c \uc2ec\ub9ac\uc801 \u000d\u000a\ucde8\uc57d\uc131\uc740 \uc77c\uc0c1 \uc601\uc5ed\uc5d0\uc11c\uc758 \ubc18\ubcf5\uc801\uc778 \ucc28\ubcc4 \uacbd\ud5d8\uc73c\ub85c\ubd80\ud130 \ud615\uc131\ub41c \uacb0\uacfc\uc77c \uc218 \uc788\ub2e4.\u000d\u000a\uc751\ub2f5\uc790 \uc911 33.6%\uac00 \ucd5c\uadfc 1\ub144 \uac04 \ucc28\ubcc4\uc744 \uacbd\ud5d8\ud558\uc600\ub2e4\uace0 \uc751\ub2f5\ud558\uc600\ub294\ub370, \ud2b8\ub79c\uc2a4\ub0a8\uc131\uc758 \u000d\u000a71%, \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc758 68.8%\ub294 \ub2e4\ub978 \uc131\ubcc4\uc815\uccb4\uc131\ubcf4\ub2e4 \ucc28\ubcc4\uc744 \uacbd\ud5d8\ud588\ub2e4\ub294 \uc751\ub2f5\uc758 \ube44\uc911\uc774 \ub450 \u000d\u000a\ubc30 \uac00\ub7c9 \ub192\uc558\ub2e4. 2020\ub144 \uad6d\uac00\uc778\uad8c\uc704\uc6d0\ud68c\uc758 \u300c\ud2b8\ub79c\uc2a4\uc820\ub354 \ud610\uc624\ucc28\ubcc4 \uc2e4\ud0dc\uc870\uc0ac\u300d\uc5d0\uc11c\ub3c4 \ud2b8\ub79c\u000d\u000a\uc2a4\uc820\ub354 \uc751\ub2f5\uc790\ub294 \ub192\uc740 \ube44\uc728(85.2%)\ub85c \ucd5c\uadfc 1\ub144 \uac04 \ucc28\ubcc4\uc744 \uacbd\ud5d8\ud558\uc600\uc73c\uba70, \ud654\uc7a5\uc2e4, \uad6c\uc9c1 \uacfc\u000d\u000a\uc815, \uc2e0\ubd84\uc744 \uc99d\uba85\ud574\uc57c \ud558\ub294 \uc0c1\ud669\uc5d0\uc11c\uc758 \ucc28\ubcc4 \uacbd\ud5d8\uc774 \uc8fc\ub85c \ubcf4\uace0\ub418\uc5c8\ub2e4. \uc0ac\ud68c \uc804\ubc18\uc5d0 \uacf5\uace0\ud55c \u000d\u000a\uc131\ubcc4\uc774\ubd84\ubc95\uc801 \uc0ac\uace0\uc640 \uad6c\uc870\uac00 \uc77c\uc0c1\uc801\uc778 \uc218\uc900\uc5d0\uc11c \uc601\ud5a5\uc744 \ubbf8\uce58\uace0 \uc788\uc74c\uc744 \ub2e4\uc2dc\uae08 \ud655\uc778\ud558\uc600\u000d\u000a\ub2e4.\u000d\u000a\uccad\ub144 \uc131\uc18c\uc218\uc790\uac00 \uc8fc\ub85c \uc77c\uc0c1\uc744 \uc0b4\uc544\uac00\ub294 \uacf5\uac04\uc5d0\uc11c \ucc28\ubcc4 \uacbd\ud5d8\uc774 \ub192\ub2e4\ub294 \uc810 \ub610\ud55c \uc8fc\ubaa9\ud560\ub9cc\u000d\u000a\ud558\ub2e4. \ud559\uc0dd\uc758 32.4%\uac00 \ub300\ud559(\uc6d0), \ucde8\uc5c5\uc790\uc758 26.8%\uac00 \uc9c1\uc7a5, \ubb34\uc9c1/\uc8fc\ubd80/\uad6c\uc9c1 \uc911\uc778 \uc790 \uc911 20%\u000d\u000a\uac00 \uad6c\uc9c1 \uacfc\uc815\uc5d0\uc11c \uac00\uc7a5 \uc2ec\uac01\ud55c \ucc28\ubcc4\uc744 \uacbd\ud5d8\ud558\uc600\ub2e4\uace0 \uc751\ub2f5\ud588\ub2e4.\u000d\u000a5) \ucc28\ubcc4\uc744 \ubc29\uad00\ud558\ub294 \uad6d\uac00\u000d\u000a\uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc758 \uc77c\uc0c1 \uc18d \ucc28\ubcc4 \uacbd\ud5d8\uc774 \ub192\ub2e4\ub294 \uacb0\uacfc\uac00 \ub354\uc6b1 \ubb38\uc81c\uc801\uc778 \uac83\uc740 \ucc28\ubcc4\uc758 \ud574\uacb0\u000d\u000a\uacfc \ud53c\ud574 \uad6c\uc81c\uac00 \uac1c\uc778\uc758 \ubaab\uc73c\ub85c \ub0a8\uae30 \ub54c\ubb38\uc774\ub2e4. \ucc28\ubcc4\uc744 \uacbd\ud5d8\ud55c \uc0ac\ub78c \uc911 85.7%\ub098 \ud53c\ud574\ub97c \uc2e0\u000d\u000a\uace0\ud558\uc9c0 \uc54a\uc558\ub294\ub370, \uadf8 \uc774\uc720\ub294 \u2018\uc2e0\uace0\ub97c \ud574\ub3c4 \ub2ec\ub77c\uc9c0\ub294 \uac8c \uc5c6\uc5b4\uc11c\u2019(53.0%), \u2018\uc2e0\uace0\ud560 \uac00\uce58\uac00 \uc5c6\u000d\u000a\ub2e4\uace0 \ub290\uaef4\uc838\uc11c(\ud56d\uc0c1 \uc77c\uc5b4\ub098\ub294 \uc77c\uc774\ub2c8\uae4c)\u2019(53.0%), \u2018\ub0b4\uac00 \uc131\uc18c\uc218\uc790\uc784\uc744 \ub4dc\ub7ec\ub0b4\uace0 \uc2f6\uc9c0 \uc54a\uc544\u000d\u000a\uc11c\u2019(38.6%), \u2018\uc0ac\ub78c\ub4e4\uc774 \uc774 \uc0ac\uac74\uc744 \uc9c4\uc9c0\ud558\uac8c \uc5ec\uae30\uc9c0 \uc54a\uc744 \uac83 \uac19\uc544\uc11c\u2019(28.6%) \ub4f1\uc774\ub2e4. \uccad\ub144 \u000d\u000a\uc131\uc18c\uc218\uc790\ub4e4\uc740 \uc790\uc2e0\uc774 \uc18d\ud55c \uc9d1\ub2e8\uc774\ub098 \uc6b0\ub9ac \uc0ac\ud68c\uac00 \uc790\uc2e0\uc774 \uacaa\uc740 \ucc28\ubcc4\uc5d0 \ub300\ud574 \ud568\uaed8 \uace0\ubbfc\ud558\uace0 \u000d\u000a\uc774\ub97c \uc801\uc808\ud558\uac8c \ud574\uacb0\ud574 \uc904 \uac83\uc73c\ub85c \uc804\ud600 \uae30\ub300\ud558\uc9c0 \uc54a\ub294\ub2e4\uace0 \ubcfc \uc218 \uc788\ub2e4. \uc2ec\uc9c0\uc5b4 \ucc28\ubcc4\uc744 \uc2e0\uace0\u000d\u000a\ud558\uace0 \ud574\uacb0\ud558\uae30 \uc704\ud574\uc11c\ub294 \ud53c\ud574 \ub2f9\uc0ac\uc790\ub85c\uc11c \ubcf8\uc778\uc744 \ub4dc\ub7ec\ub0b4\uc57c \ud558\uace0, \uc774 \uacfc\uc815\uc5d0\uc11c \uc790\uc2e0\uc774 \uc131\uc18c\u000d\u000a\uc218\uc790\ub77c\ub294 \uc0ac\uc2e4\uc774 \uc790\uc5f0\uc2a4\ub7fd\uac8c \uc720\ucd94\ub418\uac70\ub098 \uacf5\uac1c\ub420 \uc218 \uc788\ub2e4. \uadf8\ub798\uc11c \uc131\uc18c\uc218\uc790\ub294 \ucc28\ubcc4\uc744 \uacaa\uc5b4\u000d\u000a\ub3c4 \uc2e0\uace0\ud558\uc9c0 \uc54a\uace0 \uadf8\uc800 \ud63c\uc790\uc11c \uc553\uc744 \ubfd0\uc774\ub2e4. ","22 23\u000d\u000a\u201c\ub098\uc758 \ubb38\uc81c\ub97c \ud568\uaed8 \ud574\uacb0\ud574\uc8fc\uc9c0 \uc54a\uc744 \uac83\u201d\uc774\ub77c\ub294 \uc778\uc2dd\uc740 \ub2e8\uc9c0 \uc9c1\uc7a5\uacfc \ud559\uad50 \ub4f1\uc758 \uacf5\uac04 \ub0b4 \u000d\u000a\uc131\uc18c\uc218\uc790 \uc778\uad8c\uc744 \uc9c0\uc9c0\ud558\uace0 \ucc28\ubcc4 \uc2dc\uc815\uc744 \uc9c0\uc6d0\ud558\ub294 \uc790\uc6d0\uc774 \ubd80\uc7ac\ud558\ub2e4\ub294 \ub370\uc11c \uc624\ub294 \uac83\ub9cc\uc740 \uc544\u000d\u000a\ub2c8\ub2e4. \uadfc\ubcf8\uc801\uc73c\ub85c\ub294 \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \ucc28\ubcc4\uc744 \uc778\uad8c\uacfc \uc2dc\ubbfc\uad8c\uc5d0 \ub300\ud55c \uc911\ub300\ud55c \uce68\ud574\ub85c \ubc1b\uc544\ub4e4\u000d\u000a\uc774\uc9c0 \uc54a\ub294 \uc6b0\ub9ac \uc0ac\ud68c\uc758 \uad6c\uc870\uac00 \ubb38\uc81c\uc778 \uc148\uc774\ub2e4. \uc124\ubb38\ucc38\uc5ec\uc790\uc758 \uac70\uc758 \ub300\ubd80\ubd84\uc740 \uad6d\ud68c\uc640 \uc815\ubd80, \u000d\u000a\uc0ac\ubc95\ubd80 \ubaa8\ub450\uac00 \uc131\uc18c\uc218\uc790\uc5d0\uac8c \ube44\uc6b0\ud638\uc801\uc774\ub77c\uace0 \uc751\ub2f5\ud588\ub2e4. \uacbd\ucc30\uc5d0 \ub300\ud55c \uc2e0\ub8b0\ub3c4 \ub9e4\uc6b0 \ub0ae\uc558\ub2e4.\u000d\u000a6) \uad6c\uc9c1\uc758 \ubcbd\u000d\u000a\uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc740 \uccad\ub144 \uc131\uc18c\uc218\uc790\uc758 \uc9c1\uc5c5 \uc120\ud0dd \ubc0f \uad6c\uc9c1\uc5d0 \uc601\ud5a5\uc744 \ubbf8\uce58\ub294 \uc911\uc694\ud55c \uc694\uc18c\ub85c \u000d\u000a\ub4dc\ub7ec\ub0ac\ub2e4. \ucd5c\uadfc 5\ub144\uac04 \uad6c\uc9c1 \uacbd\ud5d8\uc774 \uc788\ub294 \uc751\ub2f5\uc790 \uc911 26.7%\uac00 \uad6c\uc9c1\uc5d0 \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc774 \uc601\u000d\u000a\ud5a5\uc744 \ubbf8\ucce4\ub2e4\uace0 \uc751\ub2f5\ud558\uc600\uc73c\uba70, \uc2dc\uc2a4\uc820\ub354\uc5d0 \ube44\ud574 \ud2b8\ub79c\uc2a4\uc820\ub354(\ud2b8\ub79c\uc2a4\uc5ec\uc131 62.9%, \ud2b8\ub79c\uc2a4\ub0a8\uc131 60.7%) \uc751\ub2f5\uc790\u000d\u000a\ub4e4\uc5d0\uac8c \uadf8 \uc601\ud5a5\ub825\uc774 \ub354 \ud06c\uac8c \ub098\ud0c0\ub0ac\ub2e4. \uba74\uc811\ucc38\uc5ec\uc790\ub4e4\uc758 \uc778\ud130\ubdf0\ub97c \ud1b5\ud574 \uc774\ub4e4\uc774 \ud604\uc2e4 \uc18d\uc5d0\u000d\u000a\uc11c \ub9c8\uc8fc\ud574\uc628 \uc5b4\ub824\uc6c0\uc744 \uad6c\uccb4\uc801\uc73c\ub85c \ud655\uc778\ud560 \uc218 \uc788\uc5c8\ub2e4. \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \uc0ac\ud68c\uc801 \ub099\uc778\uc744 \ub0b4\u000d\u000a\uba74\ud654\ud558\uc5ec \ud2b9\uc815 \uc9c1\uc5c5\uc744 \uac00\uc9c0\uae30 \uc704\ud55c \ub178\ub825\uc744 \uc2a4\uc2a4\ub85c \ud3ec\uae30\ud558\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc5c8\uace0, \ud2b8\ub79c\uc2a4\uc820\ub354 \u000d\u000a\uc751\ub2f5\uc790\ub4e4\uc740 \uc131\ubcc4\uc5d0 \ub530\ub978 \ubcf5\uc7a5/\uc720\ub2c8\ud3fc, \ub450\ubc1c \uaddc\uc815\uc774 \uc788\ub294 \uc9c1\uc5c5\uc740 \uc7ac\uace0\uc758 \uc5ec\uc9c0 \uc5c6\uc774 \uc120\ud0dd\ud560 \u000d\u000a\uc218 \uc5c6\ub294 \uac83\uc73c\ub85c \uc5ec\uae30\uace0 \uc788\uc5c8\ub2e4. HIV \uac10\uc5fc\uc778 \uc751\ub2f5\uc790\ub4e4\uc5d0\uac8c\ub294 \ubcd1\uc801(\u5175\u7c4d) \uc0ac\ud56d\uc73c\ub85c \uc778\ud55c \uace0\u000d\u000a\uc6a9 \ucc28\ubcc4\uacfc, \ucde8\uc9c1\uc774 \ub41c \uc774\ud6c4\uc5d0\ub3c4 \uacc4\uc18d\ub418\ub294 \ubcd1\ub825\uc815\ubcf4\uc758 \uacf5\uac1c\ub85c \uc778\ud55c \ubd88\uc774\uc775\uc744 \uc6b0\ub824\ud558\ub294 \uac83\u000d\u000a\uc73c\ub85c \ub098\ud0c0\ub0ac\ub2e4.\u000d\u000a\uc774\ub7ec\ud55c \uc5b4\ub824\uc6c0\uc740 \uc751\ub2f5\uc790\ub4e4\uc758 \uad6c\uc9c1 \uc120\ud638 \uacbd\ud5a5\uc5d0\ub3c4 \uc601\ud5a5\uc744 \ubbf8\ucce4\ub2e4. \uc2dc\uc2a4\uc820\ub354 \uc5ec\uc131 \uc751\ub2f5\uc790\u000d\u000a\uc758 \uacbd\uc6b0 \u2018\uc5ec\uc131\uc774 \ub9ce\uc740 \uc9c1\uc7a5(57.9%)\u2019\uc744, \uc2dc\uc2a4\uc820\ub354 \ub0a8\uc131 \uc751\ub2f5\uc790\uc758 \uacbd\uc6b0 \u2018\ub2e4\uc591\uc131\uc774 \uc874\uc911\ub418\ub294 \u000d\u000a\uc9c1\uc7a5(36.1%)\u2019\uc744 \uac00\uc7a5 \uc120\ud638\ud558\uc600\ub2e4. \ud2b8\ub79c\uc2a4\uc820\ub354, \ub17c\ubc14\uc774\ub108\ub9ac/\uc820\ub354\ud034\uc5b4\uc758 \uacbd\uc6b0 \u2018\ubcf5\uc7a5/\ub450\ubc1c \u000d\u000a\uc790\uc728\uc131\uc774 \ubcf4\uc7a5\ub418\ub294 \uc9c1\uc7a5\u2019, \u2018\uc131\ubcc4\uc5d0 \ub530\ub978 \uc720\ub2c8\ud3fc\uc774 \uc5c6\ub294 \uc9c1\uc7a5\u2019, \u2018\ud63c\uc790 \uc77c\ud560 \uc218 \uc788\ub294 \uc9c1\uc7a5\u2019 \ub4f1 \u000d\u000a\uc131\ubcc4\uc815\uccb4\uc131\uc5d0 \uad6c\uc560\ubc1b\uc9c0 \uc54a\ub294 \uc9c1\uc7a5\uc744 \uacf5\ud1b5\uc801\uc73c\ub85c \uc120\ud638\ud558\ub294 \ubaa8\uc2b5\uc744 \ubcf4\uc600\ub2e4.\u000d\u000a7) \uc9c1\uc7a5\uc0dd\ud65c\uc5d0\uc11c\uc758 \uc774\uc911\uc0dd\ud65c\u000d\u000a\ub178\ub3d9 \ud658\uacbd\uc5d0\uc11c \uc131\uc18c\uc218\uc790 \uccad\ub144\ub4e4\uc774 \uacaa\ub294 \uc5b4\ub824\uc6c0\uc740 \uc77c\ud68c\uc131\uc73c\ub85c \ub05d\ub098\uc9c0 \uc54a\uc558\ub2e4. \uc5b4\ub835\uc0ac\ub9ac \u000d\u000a\ud611\uc18c\ud55c \uad6c\uc9c1\uacfc\uc815\uc744 \ud1b5\uacfc\ud55c \ud6c4, \uc9c1\uc7a5\uc0dd\ud65c\uc5d0\uc11c\ub3c4 \uc131\uc18c\uc218\uc790 \uccad\ub144\ub4e4\uc740 \uace0\uc720\ud55c \uc5b4\ub824\uc6c0\uc744 \uacaa\uc5c8\u000d\u000a\ub2e4. \uae09\uc5ec\ub97c \ubc1b\uc73c\uba70 \uace0\uc6a9\ub418\uc5b4 \uc788\ub294 \uc751\ub2f5\uc790 1,371\uba85 \uc911 73.3%\uac00 \uc9c1\uc7a5\uc5d0\uc11c \ubcf8\uc778\uc758 \uc131\uc18c\uc218\uc790 \u000d\u000a\uc815\uccb4\uc131\uc744 \uc228\uae30\uac70\ub098 \uc18d\uc600\ub2e4\uace0 \uc751\ub2f5\ud588\ub2e4. \uc774\ub4e4\uc774 \uc9c1\uc7a5\uc5d0\uc11c \uc815\uccb4\uc131\uc744 \uc740\ud3d0\ud558\uac70\ub098 \uc801\uadf9\uc801\uc73c\ub85c \u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 : 2021\ub144 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac "," & 24\u000d\u000a\ubd80\uc815\ud558\uac8c \ub418\ub294 \uc774\uc720\ub294 \uba85\ubc31\ud558\ub2e4. \uc751\ub2f5\uc790\uc758 42.5%\ub294 \uc9c1\uc7a5\uc5d0\uc11c \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud574 \uc804\ubc18\uc801\uc73c\u000d\u000a\ub85c \ubd80\uc815\uc801\uc778 \ud0dc\ub3c4\ub97c \uacbd\ud5d8\ud588\uc73c\uba70, 20.7%\ub294 \ub3d9\ub8cc\uac00 \uadf8\uc758 \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc73c\ub85c \uc778\ud574 \ubd80\uc815\uc801\u000d\u000a\uc778 \uc5b8\ud589\uc744 \uacbd\ud5d8\ud558\ub294 \uac83\uc744 \ub4e3\uac70\ub098 \ubcf4\uc558\uace0, 12.3%\ub294 \ubcf8\uc778\uc758 \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc73c\ub85c \uc778\ud574 \ubd80\uc815\u000d\u000a\uc801\uc778 \uc5b8\ud589\uc744 \uacbd\ud5d8\ud588\ub2e4. \uc131\uc18c\uc218\uc790 \uccad\ub144\ub4e4\uc774 \uc815\uccb4\uc131\uc744 \ub4dc\ub7ec\ub0b4\uae30 \uc5b4\ub824\uc6cc \ud558\ub294 \uac83\uc740 \ub2f9\uc5f0\ud55c \uc77c\u000d\u000a\uc774\ub2e4. \uc790\uc2e0\uc758 \uc815\uccb4\uc131\uc744 \uc228\uae30\ub294 \u2018\uc774\uc911\uc0dd\ud65c\u2019\uc740 \uc131\uc18c\uc218\uc790 \uccad\ub144\ub4e4\uc5d0\uac8c \ud070 \uc2a4\ud2b8\ub808\uc2a4\uc774\uc790 \uc9c1\uc5c5\uc801 \u000d\u000a\uc5ed\ub7c9 \ubc1c\ud718\ub97c \ubc29\ud574\ud558\ub294 \uc694\uc18c\ub85c \uc791\uc6a9\ud55c\ub2e4.\u000d\u000a\uc790\uc2e0\uc758 \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc744 \uc9c1\uc7a5\uc5d0\uc11c \ub4dc\ub7ec\ub0b8 \uc5ec\ubd80\uc640 \ubb34\uad00\ud558\uac8c, \uc808\ubc18 \uc774\uc0c1\uc758 \uc751\ub2f5\uc790\uac00 \ub0a8\u000d\u000a\ub4e4\ubcf4\ub2e4 \ub354 \uc798\ud574\uc57c \ud55c\ub2e4\ub294 \uc2a4\ud2b8\ub808\uc2a4(52.3%) \ubc0f \uc815\uccb4\uc131\uc744 \uc228\uae30\uae30 \uc704\ud574 \uc870\uc2ec\ud558\uba74\uc11c \uacaa\ub294 \uc2a4\u000d\u000a\ud2b8\ub808\uc2a4(51.3%)\ub97c \uacaa\uace0 \uc788\uc5c8\ub2e4. \uc751\ub2f5\uc790\ub4e4\uc740 \uc774\ub7ec\ud55c \uc2a4\ud2b8\ub808\uc2a4\uc640 \uc704\ucd95\uc73c\ub85c \uc778\ud574 \ub418\ub3c4\ub85d \uc5c5\ubb34 \u000d\u000a\uc774\uc678\uc758 \uc9c1\uc7a5 \ubaa8\uc784\uc5d0 \ucc38\uc11d\ud558\uc9c0 \uc54a\uac70\ub098(51.0%), \uc0c1\uc0ac\uac00 \ubd80\ub2f9\ud55c \uba85\ub839\uc774\ub098 \uc9c0\uc2dc\uc5d0 \uac00\uae09\uc801 \ub530\ub974\u000d\u000a\uace0(46.3%) \uc788\uc5c8\ub2e4. \uc9c1\uc7a5\uc778 \uc751\ub2f5\uc790\uc758 42.1%\ub294 \uc774\uc9c1\uc744 \uace0\ubbfc\ud558\uc600\uace0, 37.6%\ub294 \uc9c1\uc7a5\uc5d0 \uc18c\uc18d\uac10\u000d\u000a\uc744 \ub290\ub07c\uc9c0 \ubabb\ud558\uace0 \uc788\uc5c8\ub2e4. \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc5d0\uac8c \uadfc\ubb34\ud558\ub294 \uc9c1\uc7a5 \ub0b4\uc5d0 \uc131\uc18c\uc218\uc790 \uad00\ub828 \uc81c\ub3c4\uac00 \u000d\u000a\uc788\ub294\uc9c0 \ud655\uc778\ud574\ubcf4\uc558\uc9c0\ub9cc, 69.5%\uac00 \uad00\ub828 \uc815\ucc45\uc774 \uc804\ubb34\ud558\ub2e4\uace0 \ub2f5\ud588\ub2e4. \uc774\ub4e4\uc774 \uc131\uc18c\uc218\uc790\ub85c\uc11c \u000d\u000a\uc9c1\uc7a5\uc0dd\ud65c\uc744 \uc798 \ud558\ub294 \ub370 \ud544\uc694\ub85c \ud558\ub294 \uc9c1\uc7a5 \ub0b4 \uc694\uc18c\ub85c \u2018\ucee4\ubc0d\uc544\uc6c3\ud560 \uc218 \uc788\ub294 \ubd84\uc704\uae30\u2019(61.6%), \u000d\u000a\uadf8\ub9ac\uace0 \u2018\uc131\uc18c\uc218\uc790 \uc778\uad8c\uce68\ud574 \ucc28\ubcc4\uc5d0 \ub300\ud55c \uad6c\uc81c\uc808\ucc28\ub098 \uae30\uad6c \uc874\uc7ac\u2019(49.5%), \uadf8\ub9ac\uace0 \u2018\uc9c1\uc7a5 \ub0b4 \u000d\u000a\uc131\uc18c\uc218\uc790 \uc778\uad8c\uc744 \uc704\ud55c \uad50\uc721\ud504\ub85c\uadf8\ub7a8\u2019(36.9%)\ub97c \uaf3d\uc558\ub2e4.\u000d\u000a8) \ub178\ub3d9\uc2dc\uc7a5 \uc18d \ud2b8\ub79c\uc2a4\uc820\ub354\u000d\u000a\ud2b8\ub79c\uc2a4\uc820\ub354\uac00 \uad6c\uc9c1 \uacfc\uc815 \ubc0f \uc9c1\uc7a5\uc5d0\uc11c \uacbd\ud5d8\ud558\ub294 \uc5b4\ub824\uc6c0\uc740 \ub9e4\uc6b0 \ud06c\ub2e4. \uad6c\uc9c1 \uacbd\ud5d8\uc774 \uc788\ub294 \u000d\u000a\uc751\ub2f5\uc790 \uc804\uccb4\uc5d0\uc11c \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc774 \uad6c\uc9c1\uc5d0 \uc601\ud5a5\uc744 \ubbf8\ucce4\ub2e4\uace0 \uc751\ub2f5\ud55c \uc774\ub4e4\uc740 26.7%\uc600\ub294\u000d\u000a\ub370, \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc740 62.9%, \ud2b8\ub79c\uc2a4\ub0a8\uc131\uc740 60.7%\ub85c \ud3c9\uade0\uc758 \ub450 \ubc30\ub97c \uc0c1\ud68c\ud588\ub2e4. \ub17c\ubc14\uc774\ub108\ub9ac/\u000d\u000a\uc820\ub354\ud034\uc5b4 \uc751\ub2f5\uc790\ub3c4 42.5%\uac00 \uc601\ud5a5\uc744 \ubbf8\uce5c\ub2e4\uace0 \uc751\ub2f5\ud588\ub2e4. \uc2e4\uc81c\ub85c \ub9ce\uc740 \ud2b8\ub79c\uc2a4\uc820\ub354\ub294 \uad6c\uc9c1 \u000d\u000a\uacfc\uc815\uc5d0\uc11c \ucc28\ubcc4\uc744 \uacbd\ud5d8\ud55c \uac83\uc73c\ub85c \ub098\ud0c0\ub0ac\ub2e4. \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc758 69.1%, \ud2b8\ub79c\uc2a4\ub0a8\uc131\uc758 66.7%\ub294 \u000d\u000a\uad6c\uc9c1 \uacfc\uc815\uc5d0\uc11c \uc790\uc2e0\uc774 \uc131\uc18c\uc218\uc790\ub77c\ub294 \uc810\uc73c\ub85c \uc778\ud574 \ucc28\ubcc4\uc801\uc778 \ub300\uc6b0\ub97c \uacbd\ud5d8\ud588\ub2e4\uace0 \uc751\ub2f5\ud588\ub294\u000d\u000a\ub370, \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc758 17.9%\ub294 \ud2b8\ub79c\uc2a4\uc820\ub354\ub77c\ub294 \uc810\uc73c\ub85c \uc778\ud574 \uc785\uc0ac\uac00 \ucde8\uc18c\ub418\uac70\ub098 \ucc44\uc6a9\uc774 \uac70\ubd80\u000d\u000a\ub41c \uacbd\ud5d8\uc744 \ud55c \uac83\uc73c\ub85c \ub098\ud0c0\ub0ac\ub2e4. \u000d\u000a\ud2b8\ub79c\uc2a4\uc820\ub354 \uc751\ub2f5\uc790\ub4e4\uc740 \ud63c\uc790 \uc77c\ud560 \uc218 \uc788\ub294 \uc9c1\uc7a5\uc774\ub098 \uc131\ubcc4\uc5d0 \ub530\ub978 \uc720\ub2c8\ud3fc\uc774 \uc5c6\ub294 \uc9c1\uc7a5, \u000d\u000a\uc2e0\ubd84\uc99d\uc744 \uc694\uad6c\ud558\uc9c0 \uc54a\ub294 \uc9c1\uc7a5, \uace0\ud5a5 \uce5c\uad6c\ub098 \ub3d9\ucc3d \uc911 \uc544\ub294 \uc0ac\ub78c\uc744 \ub9cc\ub0a0 \uc77c\uc774 \uc5c6\ub294 \uc9c1\uc7a5, \uc7ac","24 25\u000d\u000a\ud0dd\uadfc\ubb34\ub97c \ud560 \uc218 \uc788\ub294 \uc9c1\uc7a5, \uac1d\uad00\uc801 \uc790\uaca9(\uc2dc\ud5d8\uc774\ub098 \uc790\uaca9\uc99d)\uc744 \uc911\uc2dc\ud558\ub294 \uc9c1\uc5c5 \ub4f1\uc744 \uc120\ud638\ud558\uc600\ub294\ub370, \uc774\u000d\u000a\ub294 \uc804\ubc18\uc801\uc73c\ub85c \ud2b8\ub79c\uc2a4\uc820\ub354\ub77c\ub294 \uc810\uc744 \ub2e4\ub978 \uc0ac\ub78c\ub4e4\uc774 \uc54c\uae30 \uc5b4\ub824\uc6b4 \ud658\uacbd\uc5d0 \ub300\ud55c \uc120\ud638\uac00 \ud06c\ub2e4\u000d\u000a\uace0 \ubcfc \uc218 \uc788\ub2e4.\u000d\u000a\uc870\uc0ac\uc5d0 \ucc38\uc5ec\ud55c \uc751\ub2f5\uc790\ub4e4 \uac00\uc6b4\ub370 \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc758 \uace0\uc6a9\uc0c1\ud0dc\uac00 \uc804\ubc18\uc801\uc73c\ub85c \uc5f4\uc5ed\ud55c \uac83\uc73c\ub85c \ub098\u000d\u000a\ud0c0\ub0ac\ub2e4. \uc804\uccb4 \ud3c9\uade0\uacfc \ube44\uad50\ud574\ubcf4\uba74, \uc0c1\uc6a9\uc9c1\uc5d0 \ube44\ud574 \uc784\uc2dc\uc9c1 \ub610\ub294 \uc77c\uc6a9\uc9c1, \uc804\uc77c\uc81c\uac00 \uc544\ub2cc \uc2dc\uac04\u000d\u000a\uc81c\ub85c \uc77c\ud558\ub294 \uacbd\uc6b0\uac00 \uc0c1\ub300\uc801\uc73c\ub85c \ub9ce\uc73c\uba70, \uc8fc\ud734\uc218\ub2f9\uacfc \ucd5c\uc800\uc2dc\uae09\uc744 \uc801\uc6a9\ubc1b\uc9c0 \ubabb\ud558\ub294 \uacbd\uc6b0 \ub610\u000d\u000a\ud55c \ub2e4\ub978 \uc774\ub4e4\uc5d0 \ube44\ud574 \ub9ce\uc740 \ud3b8\uc774\uc5c8\ub2e4. \uacbd\uc81c\uc801\uc778 \uc0c1\ud669\uc5d0 \ub300\ud55c \ubbf8\ub798\uc804\ub9dd\uc5d0\uc11c\ub3c4 \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc740 \u000d\u000a\ub2e4\ub978 \uccad\ub144 \uc131\uc18c\uc218\uc790\uc5d0 \ube44\ud574 \ubab9\uc2dc \ubd80\uc815\uc801\uc778 \ud3b8\uc774\uc5c8\ub2e4.\u000d\u000a9) \ubd88\uac00\ud53c\ud55c \ub3c5\ub9bd\u000d\u000a\uccad\ub144 \uc131\uc18c\uc218\uc790 \ud3c9\uade0\uc801\uc778 \ub3c5\ub9bd\uc2dc\uae30\ub294 21.8\uc138\ub85c, \uaf64 \uc774\ub978 \ub098\uc774\uc5d0 \ubd80\ubaa8 \ubc0f \uac00\uc871\uc73c\ub85c\ubd80\ud130 \u000d\u000a\uc8fc\uac70\uc0c1 \ub3c5\ub9bd\ud55c \uac83\uc73c\ub85c \ub098\ud0c0\ub0ac\ub2e4. \uc774\ubbf8 \ub3c5\ub9bd\ud558\uc600\ub2e4\uace0 \uc751\ub2f5\ud55c \uc751\ub2f5\uc790(1,784\uba85)\ub4e4 \uc911 \uc131\uc801\uc9c0\u000d\u000a\ud5a5 \ub610\ub294 \uc131\ubcc4\uc815\uccb4\uc131\uc774 \ub3c5\ub9bd \uacb0\uc815\uc5d0 \uc601\ud5a5\uc744 \ubbf8\ucce4\ub2e4\uace0 \ub2f5\ud55c \uc751\ub2f5\uc790\ub294 34%\uc5d0 \ub2ec\ud588\ub2e4. \uc131\ubcc4\u000d\u000a\uc815\uccb4\uc131\uc758 \uacbd\uc6b0 \uc774 \uc601\ud5a5\uc774 \uc0c1\ub2f9\ud788 \ucef8\ub294\ub370, \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc758 \uacbd\uc6b0 62.3%\uac00, \ud2b8\ub79c\uc2a4\ub0a8\uc131\uc758 \uacbd\uc6b0 \u000d\u000a54.2%\uac00 \ub3c5\ub9bd\uc744 \uacb0\uc815\ud558\ub294\ub370 \uc815\uccb4\uc131\uc774 \uc601\ud5a5\uc744 \ubbf8\ucce4\ub2e4\uace0 \uc751\ub2f5\ud588\ub2e4.\u000d\u000a\ub3c5\ub9bd\uc5d0 \uc788\uc5b4 \uc131\uc18c\uc218\uc790 \uc815\uccb4\uc131\uc774 \uc601\ud5a5\uc744 \ubbf8\ucce4\ub2e4\uace0 \ub2f5\ud55c \uc751\ub2f5\uc790\ub4e4\uc740, \ud30c\ud2b8\ub108\uc640\uc758 \uad00\uacc4\ub97c \u000d\u000a\uc228\uae30\ub294 \uac83\uc774 \uc5b4\ub824\uc6cc \uc790\uc720\ub86d\uac8c \uc5f0\uc560\ud558\uae30 \uc704\ud574\uc11c(49.9%), \ud034\uc5b4\uc784\uc774 \ub4dc\ub7ec\ub0a0 \uc218 \uc788\ub294 \ubb3c\ud488\uc744 \u000d\u000a\uc228\uae30\uae30 \uc2eb\uc5b4\uc11c(33.3%), \ucee4\ubc0d\uc544\uc6c3\uc744 \ud558\uc9c0 \uc54a\uc558\uc9c0\ub9cc, \ubd80\ubaa8\uac00 \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \ubd80\uc815\uc801 \ud0dc\ub3c4\u000d\u000a\ub97c \ub4dc\ub7ec\ub0b4\uc11c(31.8), \uc774\uc131\uacfc\uc758 \uc5f0\uc560\ub098 \uacb0\ud63c\uc744 \uac15\uc694\ud558\ub294 \ubd84\uc704\uae30\uac00 \uad34\ub85c\uc6cc\uc11c(22.7%), \uc131\ubcc4\u000d\u000a\ud45c\ud604\uc744 \uc704\ud55c \uac01\uc885 \ubb3c\uac74\ub4e4\uc744 \uc228\uae30\uae30 \uc5b4\ub824\uc6cc\uc11c(17.5%), \uadf8\ub9ac\uace0 \ucee4\ubc0d\uc544\uc6c3/\uc544\uc6c3\ud305 \uc774\ud6c4 \ubd80\ubaa8 \u000d\u000a\ub610\ub294 \ud615\uc81c\uc640\uc758 \uac08\ub4f1(15.7%)\uacfc \ud3ed\ub825\uc801\uc778 \uc5b8\ud589 \ub54c\ubb38\uc5d0(6.6%) \ub3c5\ub9bd\uc744 \uacb0\uc815\ud588\ub2e4\uace0 \ub2f5\ud588\ub2e4.\u000d\u000a\ud604\uc7ac \uac00\uc871\uacfc \ud568\uaed8 \uc0b4\uace0 \uc788\ub294 \uc751\ub2f5\uc790 \uc911 \ub3c5\ub9bd\uc744 \uace0\ub824 \uc911\uc774\ub77c\uace0 \uc751\ub2f5\ud55c 1,591\uba85 \uc911 \uc790\uc2e0\u000d\u000a\uc774 \uc131\uc18c\uc218\uc790\uc778 \uc810\uc774 \ub3c5\ub9bd\uc744 \uace0\ub824\ud558\ub294 \ub370 \uc601\ud5a5\uc744 \ubbf8\uce5c\ub2e4\uace0 \uc751\ub2f5\ud55c \ube44\uc728\uc740 \uc804\uccb4\uc758 63.2%\uc73c\u000d\u000a\ub85c, \uc774\ubbf8 \ub3c5\ub9bd\ud55c \uccad\ub144 \uc131\uc18c\uc218\uc790\uc5d0 \ube44\ud574 \ub192\uac8c \ub098\ud0c0\ub0ac\uc9c0\ub9cc \ub3c5\ub9bd\uc744 \uace0\ub824\ud558\uac8c \ub41c \uc774\uc720\uc640 \uc591\uc0c1\u000d\u000a\uc740 \uc720\uc0ac\ud558\uac8c \ub098\ud0c0\ub0ac\ub2e4.\u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 : 2021\ub144 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac "," & 26\u000d\u000a10) \uc704\ud5d8\ud55c \uac74\uac15\uc0c1\ud0dc\u000d\u000a\uc131\uc18c\uc218\uc790 \uccad\ub144\ub4e4\uc758 \uc8fc\uad00\uc801 \uac74\uac15 \uc0c1\ud0dc\uc640 \uc815\uc2e0 \uac74\uac15\uc774 \ub610\ub798 \uccad\ub144 \ud3c9\uade0\uacfc \ube44\uad50\ud574 \ud604\uc800\ud788 \ub0ae\u000d\u000a\uac8c \ub098\ud0c0\ub0ac\ub2e4. \ud55c\uad6d\ubcf4\uac74\uc0ac\ud68c\uc5f0\uad6c\uc6d0\uc758 2020\ub144 \u300c\uccad\ub144\uce35 \uc0dd\ud65c\uc2e4\ud0dc \ubc0f \ubcf5\uc9c0\uc695\uad6c\uc870\uc0ac\u300d \uccad\ub144 \uc9d1\u000d\u000a\ub2e8 \uc8fc\uad00\uc801 \uac74\uac15 \uc0c1\ud0dc \ud3c9\uade0\uc740 4.28, 2019\ub144 \u300c\ud55c\uad6d\ubcf5\uc9c0\ud328\ub110\u300d\uc758 19~34\uc138 \uc8fc\uad00\uc801 \uac74\uac15 \uc0c1\ud0dc \u000d\u000a\ud3c9\uade0\uc740 4.09\uc778\ub370 \ubc18\ud574, \ubcf8 \uc870\uc0ac \uc751\ub2f5\uc790\uc758 \uc8fc\uad00\uc801 \uac74\uac15 \uc0c1\ud0dc\ub294 \uc804\uccb4 \ud3c9\uade0 3.3\uc73c\ub85c \ubcf4\uace0\ub418\u000d\u000a\uc5c8\ub2e4. \uc774\ub4e4 \uc911 \uc2dc\uc2a4\uc820\ub354 \ub0a8\uc131\uc758 \uc8fc\uad00\uc801 \uac74\uac15 \uc0c1\ud0dc\uac00 3.5\ub85c \ube44\uad50\uc801 \ub192\uac8c \ub098\ud0c0\ub0ac\uc73c\uba70, \uc2dc\uc2a4\u000d\u000a\uc820\ub354 \uc5ec\uc131\uc774 3.2, \ud2b8\ub79c\uc2a4\ub0a8\uc131 3.1, \ub17c\ubc14\uc774\ub108\ub9ac/\uc820\ub354\ud034\uc5b4 3.0, \ud2b8\ub79c\uc2a4\uc5ec\uc131 2.9 \uc21c\uc73c\ub85c, \uc815\u000d\u000a\uccb4\uc131\ubcc4 \ucc28\uc774\ub97c \ubcf4\uc600\ub2e4. \uc6b0\uc6b8\uc99d \ucc99\ub3c4CES-D\ub97c \ud1b5\ud574 \ud655\uc778\ud55c \uc815\uc2e0 \uac74\uac15\uc5d0\uc11c\ub3c4 \uc808\ubc18\uc5d0 \uac00\uae4c\uc6b4 \u000d\u000a49.8%\uac00 \ucd5c\uadfc \uc77c\uc8fc\uc77c \ub3d9\uc548 \uc6b0\uc6b8 \uc99d\uc0c1\uc744 \uacaa\uc740 \uac83\uc73c\ub85c \ub098\ud0c0\ub0ac\ub2e4.\u000d\u000a\uc8fc\uad00\uc801 \uac74\uac15 \uc0c1\ud0dc \uc9c0\ud45c\uac00 \ud604\uc7ac \uac74\uac15 \uc0c1\ud0dc \ubc0f \uc0ac\ub9dd\ub960\uc744 \uc720\uc758\ubbf8\ud558\uac8c \uc608\uce21\ud560 \uc218 \uc788\ub2e4\ub294 \uae30\u000d\u000a\uc874\uc758 \uc5f0\uad6c \uacb0\uacfc\uc5d0 \ub530\ub974\uba74, \uc124\ubb38\uc5d0 \uc751\ub2f5\ud55c \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc740 \ud604\uc7ac \ud55c\uad6d \uccad\ub144 \ud3c9\uade0 \ub300\ube44 \uac74\u000d\u000a\uac15 \uc0c1\ud0dc\uac00 \uc591\ud638\ud558\uc9c0 \ubabb\ud558\uba70 \uc0ac\ub9dd\ub960\ub3c4 \ub192\uc744 \uac83\uc73c\ub85c \ucd94\uc815\ub41c\ub2e4.* \uc0c1\ud669\uc740 \uc2ec\uac01\ud55c \ubc18\uba74, \ud55c\uad6d\uc5d0\u000d\u000a\uc11c \uc131\uc18c\uc218\uc790 \uac74\uac15 \uc5f0\uad6c\ub294 \uc774\uc81c \ub9c9 \ud0dc\ub3d9\ud558\uc600\uc73c\uba70 \uc815\ucc45\uc801 \uac1c\uc785\uc740 \uc804\ubb34\ud55c \uc0c1\ud669\uc774\ub2e4. \uc131\uc18c\uc218\u000d\u000a\uc790 \uccad\ub144\ub4e4\uc758 \uc2e0\uccb4\uc801, \uc815\uc2e0\uc801 \uac74\uac15 \uc0c1\ud0dc\ub97c \uc5f0\uad6c\ud558\uace0 \uc774\ub97c \uc815\ucc45\uc5d0 \ubc18\uc601\ud558\ub294 \uac83\uc740 \uc774 \uc5f0\uad6c\uc870\u000d\u000a\uc0ac \uacfc\uc815\uc5d0\uc11c \ub4dc\ub7ec\ub09c \uac00\uc7a5 \uc2dc\uae09\ud55c \uacfc\uc81c\uc600\ub2e4.\u000d\u000a11) \ub192\uc740 \uc790\uc0b4\uc704\uae30\u000d\u000a\uccad\ub144 \uc131\uc18c\uc218\uc790\uc758 \uac74\uac15 \ubc0f \uc2ec\ub9ac \uc0c1\ud0dc\uac00 \uc804\ubc18\uc801\uc73c\ub85c \ubd80\uc815\uc801\uc73c\ub85c \ud30c\uc545\ub41c \uac00\uc6b4\ub370 \uc790\uc0b4\uc0dd\uac01\u000d\u000a\uacfc \uc790\uc0b4\uc2dc\ub3c4\uc5d0 \ub300\ud55c \uc751\ub2f5\uc774 \ub9e4\uc6b0 \ub192\uac8c \ub098\ud0c0\ub0ac\ub2e4. \uc124\ubb38\uc870\uc0ac\uc5d0 \ucc38\uc5ec\ud55c \uc751\ub2f5\uc790 \uac00\uc6b4\ub370 41.5%\u000d\u000a\ub294 \ucd5c\uadfc 1\ub144\uac04 \uc790\uc0b4\uc744 \uc9c4\uc9c0\ud558\uac8c \uc0dd\uac01\ud574\ubcf8 \uc801\uc774 \uc788\uc73c\uba70, 8.2%\ub294 \uc2e4\uc81c\ub85c \uc790\uc0b4\uc744 \uc2dc\ub3c4\ud574\ubcf8 \uc801\u000d\u000a\uc774 \uc788\ub2e4\uace0 \uc751\ub2f5\ud588\ub2e4. \uc774\ub294 \ud55c\uad6d\ubcf5\uc9c0\ud328\ub110(2020)\uc774\ub098 \uad6d\ubbfc\uac74\uac15\uc601\uc591\uc870\uc0ac(2019)\uc758 \ub9cc 19\uc138~\ub9cc 34\u000d\u000a\uc138 \uacb0\uacfc\uc640 \ube44\uad50\ud574\ubcf4\uc558\uc744 \ub54c \uac70\uc758 \uc5f4 \ubc30 \uc774\uc0c1 \ub192\uc740 \uc218\uce58\ub77c\ub294 \uc810\uc5d0\uc11c \uadf8 \uc2ec\uac01\uc131\uc744 \uc54c \uc218 \uc788\ub2e4.\u000d\u000a\uc804\ubc18\uc801\uc73c\ub85c \uc790\uc0b4\uc0dd\uac01\uacfc \uc790\uc0b4\uc2dc\ub3c4\uc5d0 \ub300\ud55c \uc751\ub2f5\uc774 \ub192\uac8c \ub098\ud0c0\ub09c \uac00\uc6b4\ub370 \ud2b8\ub79c\uc2a4\uc820\ub354\uc640 \ub17c\ubc14\u000d\u000a\uc774\ub108\ub9ac/\uc820\ub354\ud034\uc5b4\uc758 \uc751\ub2f5 \uc218\uce58\uac00 \uc2ec\uc0c1\uce58 \uc54a\ub2e4. \ud2b8\ub79c\uc2a4\uc5ec\uc131\uc758 58.7%, \ud2b8\ub79c\uc2a4\ub0a8\uc131\uc758 59.7%, \u000d\u000a\ub17c\ubc14\uc774\ub108\ub9ac/\uc820\ub354\ud034\uc5b4\uc758 62.9%\ub294 \ucd5c\uadfc 1\ub144\uac04 \uc790\uc0b4\uc744 \uc9c4\uc9c0\ud558\uac8c \uc0dd\uac01\ud55c \uc801\uc774 \uc788\uc73c\uba70, \uc2e4\uc81c\u000d\u000a\ub85c \uc790\uc0b4\uc744 \uc2dc\ub3c4\ud55c \uc801\uc774 \uc788\ub2e4\ub294 \uc751\ub2f5 \ub610\ud55c \ub2e4\ub978 \uc751\ub2f5\uc790\ub4e4\uc5d0 \ube44\ud574 \ub192\uac8c \ub098\ud0c0\ub0ac\ub2e4. \ud2b8\ub79c\uc2a4\uc5ec\u000d\u000a\uc131 \uc751\ub2f5\uc790\ub4e4 \uac00\uc6b4\ub370 20.2%\ub294 \ucd5c\uadfc 1\ub144\uac04 \uc2e4\uc81c\ub85c \uc790\uc0b4\uc744 \uc2dc\ub3c4\ud55c \uc801\uc774 \uc788\ub2e4\uace0 \uc751\ub2f5\ud558\uc600\ub2e4.","26 27\u000d\u000a\ucd5c\uadfc\uc5d0\ub3c4 \ud2b8\ub79c\uc2a4\uc820\ub354\uc5d0 \ub300\ud55c \ucc28\ubcc4\uacfc \uc628\uc624\ud504\ub77c\uc778 \uc0c1\uc758 \ud610\uc624\ud45c\ud604\uc774 \uc774\uc5b4\uc9c0\ub294 \uac00\uc6b4\ub370 \uace0 \u000d\u000a\ubcc0\ud76c\uc218 \ud558\uc0ac\ub97c \ube44\ub86f\ud55c \uc774\ub984\uc774 \uc54c\ub824\uc9c4 \uc5ec\ub7ec \ud2b8\ub79c\uc2a4\uc820\ub354\uac00 \uc138\uc0c1\uc744 \ub5a0\ub098\ub294 \ub4f1 \ud2b8\ub79c\uc2a4\uc820\ub354\uc758 \u000d\u000a\uc790\uc0b4 \ubb38\uc81c\ub294 \uc6b0\ub9ac \uc0ac\ud68c\uac00 \ub9e4\uc6b0 \uc2ec\uac01\ud558\uac8c \ubc1b\uc544\ub4e4\uc774\uace0 \ubcc0\ud654\ub97c \ubaa8\uc0c9\ud574\uc57c \ud560 \uc9c0\uc810 \uc911\uc5d0\uc11c\ub3c4 \uc81c\u000d\u000a\uc77c \uc55e\uc790\ub9ac\uc5d0 \uc704\uce58\ud55c \uac83 \uac19\ub2e4.\u000d\u000a12) \uc7ac\ub09c \uc0c1\ud669\uc5d0\uc11c\uc758 \ub099\uc778\uc5d0 \ub300\ud55c \ub450\ub824\uc6c0\u000d\u000a\uc131\uc815\uccb4\uc131\uc774 \uc6d0\uce58 \uc54a\uac8c \uc54c\ub824\uc9c0\ub294 \uac83\uc5d0 \ub300\ud55c \ub450\ub824\uc6c0\uc740 \ucf54\ub85c\ub09819 \ud32c\ub370\ubbf9 \uc0c1\ud669\uc5d0\uc11c \ub354\uc6b1 \uac15\u000d\u000a\ud654\ub418\uc5c8\ub2e4. \ucf54\ub85c\ub09819\ub85c \uc778\ud55c \uac71\uc815\uacfc \ub450\ub824\uc6c0\uc73c\ub85c \uc804\uccb4 \uc751\ub2f5\uc790\uc758 63%\uac00 \u2018\uc5ed\ud559\uc870\uc0ac\ub85c \uc131\uc18c\uc218\u000d\u000a\uc790 \uc815\uccb4\uc131\uc774 \uc6d0\uce58 \uc54a\uac8c \ubc1d\ud600\uc9c0\ub294 \uac83\u2019\uc744 \uaf3d\uc558\ub2e4. \ud55c\uad6d \uc815\ubd80\ub294 \ucf54\ub85c\ub09819 \ucd08\uae30 \ub300\uc751\uc5d0\uc11c \uc5c4\uaca9\u000d\u000a\ud55c \ub3d9\uc120 \uc5ed\ud559\uc870\uc0ac\ub97c \uc2e4\uc2dc\ud588\ub2e4. \uc774\ub54c \ub3d9\uc120\uc5d0\uc11c \ub9cc\ub09c \uc0ac\ub78c\uacfc \uacf5\uac04\uc774 \uc131\uc18c\uc218\uc790\uc774\uac70\ub098 \uc131\uc18c\uc218\u000d\u000a\uc790 \uc0c1\uc5c5\uc2dc\uc124 \ub4f1\uc77c \uacbd\uc6b0 \ud655\uc9c4\uc790\uc758 \uc9c0\uc778\uacfc \uc77c\ud130\uc758 \ub3d9\ub8cc\ub4e4\uc774 \uadf8 \uc0ac\ub78c\uc758 \uc131\uc815\uccb4\uc131\uc744 \ucd94\uc815\ud560 \uc218\u000d\u000a\uc788\uc5c8\uace0, \uc774\uc5d0 \ub300\ud55c \uc131\uc18c\uc218\uc790\ub4e4\uc758 \ub450\ub824\uc6c0\uc774 \ubb34\ucc99 \ucef8\ub358 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4.\u000d\u000a\uc2e4\uc81c\ub85c 2020\ub144 5\uc6d4 \ucd08\uc911\uc21c\uc5d0 \uc788\uc5c8\ub358 \u2018\uc774\ud0dc\uc6d0 \ud074\ub7fd \ucf54\ub85c\ub09819 \uc9d1\ub2e8 \uac10\uc5fc\u2019 \uc0ac\ud0dc\ub294 \uc131\uc18c\uc218\u000d\u000a\uc790\ub4e4\uc5d0\uac8c \ud06c\ub098\ud070 \ub450\ub824\uc6c0\uacfc \uc0c1\ucc98\ub97c \ub0a8\uacbc\ub2e4. \uc774\ud0dc\uc6d0 \uac8c\uc774\ud074\ub7fd\uc5d0\uc11c \uc9d1\ub2e8\uac10\uc5fc\uc774 \ubc1c\uc0dd\ud558\uc790, \uc77c\u000d\u000a\ubd80 \uc5b8\ub860\uc5d0\uc11c\ub294 \uc774 \uac8c\uc774\ud074\ub7fd\uc758 \uc774\ub984, \uc704\uce58\uc640 \ud2b9\uc131 \ub4f1\uc744 \uc120\uc815\uc801\uc73c\ub85c \ubcf4\ub3c4\ud588\ub2e4. \uadf8\ub7ec\uba74\uc11c \uc131\u000d\u000a\uc18c\uc218\uc790, \ud2b9\ud788 \uac8c\uc774\ub97c \ube44\ub09c\ud558\ub294 \uc5ec\ub860\uc774 \uc870\uc131\ub418\uc5c8\ub2e4. \ub2f9\uc2dc \ud655\uc9c4\ub418\uc5c8\ub358 \uc131\uc18c\uc218\uc790\ub4e4\uc740 \ubcf8\uc778\uc758 \u000d\u000a\uc758\uc9c0\uc640 \uc0c1\uad00\uc5c6\uc774 \uc131\uc815\uccb4\uc131\uc774 \uc54c\ub824\uc9c8 \uc704\uae30\uc5d0 \ucc98\ud588\uace0, \uc131\uc18c\uc218\uc790\ub77c\ub294 \uc774\uc720\ub9cc\uc73c\ub85c \uacfc\ub3c4\ud55c \ube44\u000d\u000a\ub09c\uc744 \ubc1b\uc558\ub2e4. \ud574\ub2f9 \uc0ac\uac74\uc5d0 \uad00\ud574 \ub2e4\uc218\uc758 \uba74\uc811\ucc38\uc5ec\uc790\uac00 \ub450\ub824\uc6c0, \uc2ac\ud514, \ubd84\ub178\ub97c \ub290\uaf08\ub2e4\uace0 \uc9c4\uc220\u000d\u000a\ud588\ub2e4.\u000d\u000a13) \ud55c\uad6d\uc5d0\uc11c \ubabb\uc0b4\uaca0\ub2e4\u000d\u000a97.1%\uc758 \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc774 \u2018\ud55c\uad6d\uc740 \uc131\uc18c\uc218\uc790\uac00 \uc0b4\uae30 \uc548 \uc88b\ub2e4\u2019\uace0 \uc751\ub2f5\ud558\uc600\ub2e4. \uc2ec\uc9c0\uc5b4 \u000d\u000a2014\ub144 \u300c\ud55c\uad6d LGBTI \ucee4\ubba4\ub2c8\ud2f0 \uc0ac\ud68c\uc801 \uc695\uad6c\uc870\uc0ac\u300d\uc640 \ube44\uad50\ud558\uba74 \ubd80\uc815\uc801 \uacac\ud574\uac00 \uc0c1\uc2b9\ud558\uc600\uc73c\u000d\u000a\uba70 \u2018\ub9e4\uc6b0 \uc548 \uc88b\ub2e4\u2019\ub294 \uc751\ub2f5\uc774 17%\ub098 \uc99d\uac00\ud558\uc600\ub2e4.\u000d\u000a\uac70\uc758 \ubaa8\ub4e0 \uc751\ub2f5\uc790\ub85c\ubd80\ud130 \u2018\ud55c\uad6d\uc5d0\uc11c \ubabb \uc0b4\uaca0\ub2e4\u2019\ub294 \ub2f5\ubcc0\uc774 \ubaa8\uc544\uc9c4 \ub370\uc5d0\ub294 \uc6b0\ub9ac \uc0ac\ud68c\uc758 \uc8fc\u000d\u000a\uc694\ud55c \uad8c\ub825 \uae30\uad00\ub4e4\uc774 \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \ucc28\ubcc4\uacfc \ud610\uc624\ub97c \uc608\ubc29\ud558\uac70\ub098 \uc2dc\uc815\ud558\uae30\ub294 \ucee4\ub155 \uc870\uc7a5\u000d\u000a\ud55c\ub2e4\ub294 \ud3c9\uac00\uac00 \uc874\uc7ac\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uc131\uc18c\uc218\uc790\uc5d0\uac8c \ube44\uc6b0\ud638\uc801\uc774\ub77c\ub294 \uc751\ub2f5\uc774 \uc555\ub3c4\uc801\uc73c\ub85c \ub192\u000d\u000a\uc740 \uc9d1\ub2e8(\uc870\uc9c1)\uc73c\ub85c\ub294 \uad70\ub300(91.4%), \uad6d\ud68c(89.0%), \uc815\ubd80(88.4%), \uad6d\ubbfc\uc758\ud798(83.1%), \uc0ac\ubc95\ubd80\u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 : 2021\ub144 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac "," & 28\u000d\u000a(82.4%), \uacbd\ucc30(82.3%), \uc5b8\ub860\ub9e4\uccb4(79.9%), \ub354\ubd88\uc5b4\ubbfc\uc8fc\ub2f9(71.9%)\uac00 \uaf3d\ud614\ub2e4. 2014\ub144\uc5d0 \ube44\u000d\u000a\ud574 \uc774\ub4e4 \uc9d1\ub2e8(\uc870\uc9c1) \ubaa8\ub450\uac00 \uc131\uc18c\uc218\uc790\uc5d0\uac8c \ube44\uc6b0\ud638\uc801\uc774\ub77c\ub294 \uc751\ub2f5\uc758 \ube44\uc728\uc774 \ub192\uc544\uc84c\ub2e4.\u000d\u000a14) \uc131\uc18c\uc218\uc790\uc758 \uac00\uc871\uad6c\uc131\uad8c\u000d\u000a\uc804\uccb4 \uc751\ub2f5\uc790 \uc911 \uc57d 95%\uac00 \ub3d9\uc131\uacb0\ud63c \ub610\ub294 \uc0dd\ud65c\ub3d9\ubc18\uc790 \uc81c\ub3c4 \uc911 \ucd5c\uc18c \ud558\ub098 \uc774\uc0c1 \uc774\uc6a9\ud560 \uc758\u000d\u000a\ud5a5\uc774 \uc788\ub2e4\uace0 \ubc1d\ud614\ub2e4. \ud1b5\uacc4\uccad\uc5d0 \uc758\ud558\uba74 \ud55c\uad6d \uc0ac\ud68c\ub294 1\uc778\uac00\uad6c\uac00 \ub298\uc5b4\ub098\uace0, \ucd08\ud63c \uc5f0\ub839\uc774 \ub192\uc544\u000d\u000a\uc9c0\uace0, \ud63c\uc778 \uac74\uc218\ub294 \uc804\ubc18\uc801\uc73c\ub85c \uc904\uc5b4\ub4dc\ub294 \ucd94\uc138\uc5d0 \uc788\ub2e4. \ub54c\ubb38\uc5d0 \ud604\uc7ac \uccad\ub144\uc138\ub300\ub294 \uacb0\ud63c\uc774\ub098 \u000d\u000a\uac00\uc871\uacb0\ud569\uc5d0 \ub300\ud55c \uc695\uad6c\uac00 \uc0c1\ub300\uc801\uc73c\ub85c \ub0ae\ub2e4\uace0 \ud3c9\uac00\ub418\uae30\ub3c4 \ud55c\ub2e4. \ubc18\uba74 \ubcf8 \uc870\uc0ac\uc5d0\uc11c\ub294 \ud30c\ud2b8\ub108\u000d\u000a\uc2ed \uc81c\ub3c4\uc5d0 \ub300\ud55c \uc695\uad6c\uc640 \uc694\uad6c\uac00 \uc0c1\ub2f9\ud788 \ub192\uc740 \uac83\uc73c\ub85c \ub098\uc654\ub2e4.\u000d\u000a\uc131\uc18c\uc218\uc790 \uad00\ub828 \uc815\ucc45 \uc911 \uac00\uc7a5 \uc2dc\uae09\ud55c \uac83 \uc138 \uac00\uc9c0\ub97c \uaf3d\uc73c\uba74 \ubb34\uc5c7\uc774\ub0d0\ub294 \uc9c8\ubb38\uc5d0\uc11c\ub3c4 2, 3\u000d\u000a\uc704\ub85c \ub098\uc628 \uac83\uc774 \u2018\ub3d9\uc131\ucee4\ud50c\uc5d0 \ub300\ud55c \ubc95\uc801 \uacb0\ud63c \uc778\uc815\u2019(42.5%)\uacfc \u2018\uacb0\ud63c\uc774 \uc544\ub2cc \ub3d9\uc131\ucee4\ud50c\uc744 \uc704\u000d\u000a\ud55c \ud30c\ud2b8\ub108 \uad00\uacc4 \ubc95\uc801 \uc778\uc815\u2019(38%)\uc774\uc5c8\ub2e4. \uc774\ub7ec\ud55c \uacb0\uacfc\uc5d0 \uc758\ud558\uba74 \uccad\ub144 \uc131\uc18c\uc218\uc790\uc5d0 \ud55c\ud574\uc11c\u000d\u000a\ub294 \uac00\uc871\uacfc \uacf5\ub3d9\uccb4\uc758 \uac00\uce58\uac00 \ub0ae\uc544\uc9c0\uace0 \uc788\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \ub2e4\uc591\ud55c \uac00\uc871\uacfc \uacf5\ub3d9\uccb4 \uad6c\uc131\uc5d0 \ub300\u000d\u000a\ud55c \uc695\uad6c\uac00 \ub2e4\uc591\ud574\uc9c0\uace0 \uc788\uc73c\uba70, \uc774\ub97c \ud604\uc2e4\uc801\uc73c\ub85c \ub4b7\ubc1b\uce68\ud558\ub294 \uc81c\ub3c4\uc640 \uc815\ucc45\uc774 \ubbf8\ube44\ud55c \uac83\uc73c\ub85c \u000d\u000a\ubcfc \uc218 \uc788\ub2e4. \ubcf8 \uc870\uc0ac\uc5d0\uc11c \ubb34\uc131\uc560\uc790 \uc911 52.6%\uac00 \uc0dd\ud65c\ub3d9\ubc18\uc790\ubc95\uc744 \uc774\uc6a9\ud560 \uc758\ud5a5\uc774 \uc788\ub2e4\uace0 \ubc1d\u000d\u000a\ud78c \uac83\uc5d0\uc11c \uc0dd\uac01\ud574\ubcfc \uc218 \uc788\ub4ef\uc774, \ud30c\ud2b8\ub108\uc2ed\uc774 \uaf2d \uc131\uc560\ub97c \uae30\ubc18\uc73c\ub85c \ud55c \uacb0\ud569\uc774 \uc758\ubbf8\ud558\uc9c0\ub9cc \uc54a\u000d\u000a\ub294\ub2e4\ub294 \uc810\ub3c4 \ud2b9\uae30\ud560 \ub9cc\ud558\ub2e4. \uc804\ud1b5\uc801\uc778 \uc758\ubbf8\uc758 \uacb0\ud63c\uc744 \ub118\uc5b4 \ub354 \ub2e4\uc591\ud55c \uacb0\ud569\uad00\uacc4\ub97c \uc704\ud55c \uc81c\u000d\u000a\ub3c4\uac00 \uc694\uad6c\ub418\uace0 \uc788\ub294 \uac83\uc774\ub2e4.\u000d\u000a\ud55c\ud3b8 \uccad\ub144 \uc131\uc18c\uc218\uc790\ub4e4\uc758 \uacb0\ud63c\uc81c\ub3c4\uc5d0 \ub300\ud55c \uc695\uad6c \ub610\ud55c \ub0ae\uc740 \ud3b8\uc774 \uc544\ub2c8\ub77c\ub294 \uc810\ub3c4 \uc0b4\ud3b4\ubd10\uc57c \u000d\u000a\ud55c\ub2e4. \uacb0\ud63c\uc81c\ub3c4\ub97c \uc774\uc6a9\ud560 \uc758\ud5a5\uc774 \uc788\ub2e4\uace0 \ub2f5\ud55c \uc0ac\ub78c\uc740 \uc804\uccb4 \uc751\ub2f5\uc790\uc758 \uc57d 70%\uc5d0 \ub2ec\ud560 \uc815\ub3c4\u000d\u000a\ub85c \ub192\uc558\ub2e4. \uba74\uc811\ucc38\uc5ec\uc790\ub4e4\uc740 \ub3d9\uc131\uacb0\ud63c\uc758 \uc778\uc815\uc774 \uc131\uc18c\uc218\uc790\ub4e4\uc5d0\uac8c \uc0ac\ud68c\uc801\uc73c\ub85c \ud070 \uc0c1\uc9d5\uc77c \ubfd0\u000d\u000a\ub9cc \uc544\ub2c8\ub77c \uc2e4\uc9c8\uc801\uc73c\ub85c \ud63c\uc778\uc744 \ud1b5\ud574 \ubcf4\uc7a5\ubc1b\uc744 \uc218 \uc788\ub294 \uad8c\ub9ac\uc640 \uc81c\ub3c4\uc801 \uc9c0\uc6d0\uc774 \uc911\uc694\ud558\ub2e4\uace0 \uc9c0\u000d\u000a\uc801\ud588\ub2e4.","28 29\u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR\u000d\u000a15) \ucc28\ubcc4\uc5c6\ub294 \uc0ac\ud68c, \ud3c9\ub4f1\ud55c \uc2dc\ubbfc\uad8c\u000d\u000a\uc131\uc18c\uc218\uc790\ub85c\uc11c \ud55c\uad6d \uc0ac\ud68c\uc5d0 \uc5b4\ub5a4 \uc810\uc744 \ubc14\ub77c\ub0d0\ub294 \uc8fc\uad00\uc2dd \uc9c8\ubb38\uc5d0 \ub9ce\uc740 \uc751\ub2f5\uc790\uac00 \ucc28\ubcc4 \uc5c6\uc774, \u000d\u000a\ub2e4\uc591\uc131\uc774 \uc778\uc815\ub418\ub294 \uc0ac\ud68c\ub97c \ubc14\ub780\ub2e4\uace0 \ub2f5\ubcc0\ud588\ub2e4. \uc774\ub7f0 \ub9e5\ub77d\uc5d0\uc11c \uc124\ubb38\ucc38\uc5ec\uc790\uc758 \ub300\ubd80\ubd84\uc774 \ud3ec\u000d\u000a\uad04\uc801 \ucc28\ubcc4\uae08\uc9c0\ubc95\uc744 \uac00\uc7a5\ud55c \uc2dc\uae09\ud55c \uc815\ucc45\uc73c\ub85c \uaf3d\uc558\ub2e4\uace0 \ud574\uc11d\ud560 \uc218 \uc788\ub2e4. \uadf8\ubc16\uc5d0\ub3c4 \uccad\ub144 \uc131\u000d\u000a\uc18c\uc218\uc790\ub4e4\uc774 \ubc14\ub77c\ub294 \uc815\ucc45\uc801, \uc81c\ub3c4\uc801 \ubcc0\ud654\ub294 \uc131\uc18c\uc218\uc790\uc758 \uae30\ubcf8\uc801\uc778 \uad8c\ub9ac\ub97c \uad6d\uac00\uac00 \ubcf4\uc7a5\ud558\ub77c\u000d\u000a\ub294 \ub0b4\uc6a9\uc73c\ub85c \uac00\ub4dd\ud588\ub2e4. \ub098\uc544\uac00 \ub2e4\uc591\ud55c \uc601\uc5ed\uc5d0\uc11c \uc131\uc18c\uc218\uc790\ub4e4\uc774 \ub2f9\ub2f9\ud55c \uc0ac\ud68c \uad6c\uc131\uc6d0\uc73c\ub85c \uc0b4\u000d\u000a\uc544\uac08 \uc218 \uc788\ub3c4\ub85d \uc131\uc18c\uc218\uc790\uc5d0 \ub300\ud55c \ud3b8\uacac\uc744 \uc885\uc2dd\ud558\uace0 \ud3ec\uc6a9\uc801\uc778 \ud658\uacbd\uc744 \uc870\uc131\ud558\uae30 \uc704\ud55c \ubb38\ud654\uc801 \u000d\u000a\ubcc0\ud654 \ub610\ud55c \ud544\uc218\uc801\uc774\ub2e4.\u000d\u000a4. \uc5f0\uad6c\uc870\uc0ac \ud6c4\uae30\u000d\u000a\ud604\uc7ac \ub2e4\uc6c0\uc758 \uc5f0\uad6c\uc870\uc0ac\ub294 \ud65c\ubc1c\ud558\uac8c \uc778\uc6a9\ub418\uace0 \uc788\ub2e4. \ub2e4\uc6c0 \uc5f0\uad6c\uc9c4\uc740 \ubcf8 \uc5f0\uad6c\uc870\uc0ac\uac00 \ub354 \ub9ce\uc740 \u000d\u000a\uc5f0\uad6c\uc870\uc0ac\ub85c \ud30c\uc0dd\ub420 \uc218 \uc788\uae30\ub97c \uae30\ub300\ud558\uace0 \uc788\ub2e4. \uadf8\ub9ac\uace0 \ub2e4\uc6c0 \ucc28\uc6d0\uc5d0\uc11c\ub3c4 \uc5f0\uad6c\uc870\uc0ac\ub97c \uc8fc\uc81c\ub97c \u000d\u000a\ub354 \uc881\ud600\uc11c\ub3c4 \uc9c4\ud589\ud558\uace0, \ub610 \uc774\ubc88\ucc98\ub7fc \ud3ec\uad04\uc801\uc778 \uc8fc\uc81c\ub85c\ub3c4 \ub354 \uc9c4\ud589\ud558\uace0 \uc2f6\uc740 \uc695\uad6c\uac00 \uc0dd\uacbc\ub2e4. \u000d\u000a\uc131\uc18c\uc218\uc790\uc758 \uc695\uad6c\uc640 \uc2e4\ud0dc\ub97c \uc870\uc0ac\ud558\uace0, \uc774 \uc774\uc57c\uae30\ub4e4\uc744 \uc798 \uc815\ub9ac\ud558\uc5ec \uc0ac\ud68c\uac00 \ubc18\uc601\ud558\ub3c4\ub85d \ud558\ub294 \u000d\u000a\uc791\uc5c5\uc5d0 \ud765\ubbf8\ub97c \ub290\ub07c\uac8c \ub418\uc5c8\ub2e4. \ud2b9\ud788 \uba74\uc811\uc870\uc0ac\uc5d0\uc11c \u201c\uc774\ub7f0 \uc870\uc0ac\ub97c \ud574\uc8fc\uc5b4\uc11c \uace0\ub9d9\ub2e4\u201d\ub77c\ub294 \ubc18\u000d\u000a\uc751\uc744 \uc815\ub9d0 \ub9ce\uc774 \ub4e4\uc744 \uc218 \uc788\uc5c8\ub2e4. \uadf8\ub9cc\ud07c \uc131\uc18c\uc218\uc790\ub4e4\uc774 \uc131\uc18c\uc218\uc790\ub85c\uc11c \uc0ac\ud68c\uc801\uc73c\ub85c \ubc1c\uc5b8\ud560 \uae30\u000d\u000a\ud68c\ub4e4\uc774 \uc801\uae30 \ub54c\ubb38\uc77c \uac83\uc774\ub2e4. \uadf8 \uc7a5\uc744 \ub9c8\ub828\ud558\uc5ec \uc0b6\uacfc \uc774\uc57c\uae30\ub97c \ubaa8\uc544\ub0b4\uace0, \uadf8\uac83\uc774 \uc0ac\ud68c\uc801 \ubcc0\u000d\u000a\ud654\ub97c \uae30\ubc18\uc774 \ub418\uae30\ub97c \uae30\ub300\ud55c\ub2e4.\u000d\u000a\ub098 \uac19\uc740 \uc0ac\ub78c\uc774 \ud63c\uc790\uac00 \uc544\ub2c8\uad6c\ub098 : 2021\ub144 \uccad\ub144 \uc131\uc18c\uc218\uc790 \uc0ac\ud68c\uc801 \uc695\uad6c \ubc0f \uc2e4\ud0dc\uc870\uc0ac "," & 30\u000d\u000a\uae40\uc0c8\ub86c, \ubc15\uc9c0\uc6d0, \ubb38\uc8fc\ud604, \uc591\uc608\uc2ac, \uc624\ub85c\ub77c, \uc7a5\uc740\uc9c0 /\u000d\u000a\uc2dc\ubbfc\uac74\uac15\uc5f0\uad6c\uc18c \uc820\ub354\uac74\uac15\uc5f0\uad6c\uc13c\ud130, \uc2dc\ubbfc\uac74\uac15\uc774\uc288, 2022\u000d\u000a\uc131 \u00b7 \uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d\uae30\ud68d I :\u000d\u000a\uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30 \u000d\u000a1. \ub099\ud0dc\uc8c4 \ud3d0\uc9c0 \uc9d1\ud68c\uc5d0\uc11c \uc2dc\uc791\ub41c \uc9c8\ubb38\ub4e4\u000d\u000a\uc6b0\ub9ac\ub294 \uc5b4\ub5bb\uac8c \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uad6c\ub97c \uc2dc\uc791\ud558\uac8c \ub410\uc744\uae4c? \uc774 \uc9c8\ubb38\uc5d0 \ub300\ud55c \ub300\ub2f5\uc740 2018\ub144 \u000d\u000a\uc5ec\ub984, \uad11\ud654\ubb38\uc5d0\uc11c \uc5f4\ub838\ub358 \ub099\ud0dc\uc8c4 \ud3d0\uc9c0 \ucd09\uad6c \uc9d1\ud68c\ub85c \ub3cc\uc544\uac04\ub2e4. \ube44\uc601\ub9ac \ubbfc\uac04\uc5f0\uad6c\uc18c\ub85c \ubaa8\ub450\u000d\u000a\uc758 \uac74\uac15\ud560 \uad8c\ub9ac\ub97c \uc639\ud638\ud558\ub294 \uc5f0\uad6c \ud65c\ub3d9\uc744 \ud558\ub294 \uc2dc\ubbfc\uac74\uac15\uc5f0\uad6c\uc18c\uc758 \uc77c\uc6d0\ub4e4\uc740 2018\ub144, \u201c\ubaa8\ub450\u000d\u000a\ub97c \uc704\ud55c \ub099\ud0dc\uc8c4 \ud3d0\uc9c0 \uacf5\ub3d9\ud589\ub3d9\u201d(\uc774\ud6c4 \ubaa8\ub099\ud3d0)\uc758 \uc81c\uc548\uc5d0 \uc5f0\ub300\ud558\uc5ec \uad11\ud654\ubb38\uc5d0 \ubaa8\uc600\ub2e4. \ubaa8\ub099\ud3d0\ub294 \ud5cc\u000d\u000a\ubc95\uc7ac\ud310\uc18c \ud310\uacb0\uc774 \uc9c4\ud589 \uc911\uc774\ub358 \ud615\ubc95\uc0c1 \ub099\ud0dc\uc8c4\uc758 \uc704\ud5cc\uc131\uc744 \uc8fc\uc7a5\ud558\uace0 \uc774\ub97c \ud3d0\uc9c0\ud558\ub77c\uace0 \uc694\uad6c\u000d\u000a\ud558\ub294 \uc9c1\uc811\ud589\ub3d9\uc744 \uae30\ud68d\ud558\uba74\uc11c \uc6b0\ub9ac \uc5f0\uad6c\uc18c\uc5d0\ub3c4 \ucc38\uc5ec\ub97c \uc81c\uc548\ud588\ub2e4. \uc2dc\ubbfc\uac74\uac15\uc5f0\uad6c\uc18c\uc5d0\uc11c\ub3c4 \ud544\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \uc6b0\ub9ac\uc0ac\ud68c\uc758 \uc798\ubabb\ub41c \uc778\uc2dd\uc73c\ub85c \uc778\ud574 \uc81c\ub300\ub85c \uc758\ub8cc\uc801 \uc9c0\uc6d0\uc744 \ubc1b\uace0 \uc788\uc9c0 \ubabb\ud558\ub294 \uc784\uc2e0\uc911\uc9c0\u000d\u000a\uc5d0 \uad00\ud55c \uc774\uc288\ub97c \uc758\ub8cc\uc801 \uad00\uc810\uc5d0\uc11c \uc811\uadfc\ud558\uc600\ub2e4. \ubcf8 \uc5f0\uad6c\ub97c \ud1b5\ud574 \uc784\uc2e0\uc911\uc9c0 \uacfc\uc815\uacfc \ud604\ud669\uc744 \uc0b4\ud3b4\ubcf4\uace0 \ud55c\u000d\u000a\uad6d \uc0ac\ud68c\uc5d0\uc11c \uc81c\ub300\ub85c \ub41c \u2018\uc7ac\uc0dd\uc0b0 \uac74\uac15\uc6b4\ub3d9\u2019\uc744 \uc704\ud55c \uacfc\uc81c\ub97c \ud30c\uc545\ud588\ub2e4. \uc5f0\uad6c\ub97c \uc218\ud589\ud55c \uc2dc\ubbfc\uac74\uac15\uc5f0\uad6c\u000d\u000a\uc18c \ud68c\uc6d0\uacfc \uad6c\uc131\uc6d0 \ucd1d 12\uc778\uc774 \ucc38\uc5ec\ud55c \u2018\uc7ac\uc0dd\uc0b0 \uac74\uac15 \uccb4\uacc4 \uac1c\uc120 \uc5f0\uad6c\ud300\u2019\uc740 2020\ub144 5\uc6d4\ubd80\ud130 7\uc6d4\uae4c\uc9c0 \u000d\u000a\uc758\ub8cc\uc81c\uacf5\uc790, \uc774\uc6a9\uc790, \uadf8\ub9ac\uace0 \uc774 \ubd84\uc57c\uc758 \ud65c\ub3d9\uac00 \ucd1d 28\uc778\uc744 \ub300\uc0c1\uc73c\ub85c \ub2e4\uac01\uc801\uc778 \uba74\ub2f4\uc744 \uc9c4\ud589\ud588\ub2e4. \ud55c\u000d\u000a\uad6d\uc0ac\ud68c\uc5d0\uc11c \uc784\uc2e0\uc911\uc9c0\ub97c \ud558\uae30 \uae4c\uc9c0 \uc5ec\uc131\uc774 \uacaa\uc5b4\uc57c\ud560 \ubb38\uc81c\uc640 \ubc95\uc801\uc73c\ub85c \uc5ec\uc804\ud788 \uc81c\ub300\ub85c \ub41c \uacf5\uc801 \ubcf4\uc7a5\u000d\u000a\uc774 \uc774\ub8e8\uc5b4\uc9c0\uc9c0 \uc54a\uc544 \uc0dd\uae34 \uc758\ub8cc\uacfc\uc815 \uc0c1\uc758 \ubaa8\ub4e0 \uc774\ud574\uad00\uacc4\uc790\uc758 \ud604\uc2e4\uc744 \ub2f4\uc544\ub0b4\uace0 \uc774\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574\u000d\u000aAAAQ(Availabiltiy, Accessibility, Acceptability, Quality)\ubcf4\uc7a5 \ud504\ub808\uc784\uc6cc\ud06c\uc5d0 \ub530\ub978 \ub300\uc548\uc744 \uc81c\u000d\u000a\uc2dc\ud558\uc600\ub2e4.\u000d\u000a2022\ub144 \uc81c1\ud68c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \uc774 \uc5f0\uad6c\uac00 \uc6b0\ub9ac \uc0ac\ud68c\uc5d0\uc11c \ubc1c\uad74\uc774 \uc27d\uc9c0 \uc54a\uc740 \u000d\u000a\ub0b4\ubc00\ud55c \uc8fc\uc81c\ub97c \uc798 \uc218\ud589\ud588\ub2e4\ub294 \uc810\uc5d0 \uc758\uc758\uac00 \uc788\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc778\ud130\ubdf0\uac00 \uc27d\uc9c0 \uc54a\uc740 \ub300\uc0c1\uc744 \ub9cc\ub098 \uc0ac\uac01\u000d\u000a\uc9c0\ub300 \uc774\uc288\ub97c \ubc1c\uad74\ud588\uace0, \uc774\ub97c \uc5f0\uad6c\ub85c \ub04c\uc5b4\ub0c8\ub2e4\ub294 \uce21\uba74\uc740 \ud604\uc7a5\uc774 \uc0ac\ud68c\uc801 \ubcc0\ud654\ub97c \uc774\ub04c\uae30 \uc704\ud55c \uc2e4\uc9c8\uc801\u000d\u000a\uc778 \ubc1c\ud310\uc744 \u2018\uc120\ub3c4\uc801\u2019\uc73c\ub85c \ub9c8\ub828\ud55c \uac83\uc774\ub2e4. \ub610\ud55c \uc784\uc2e0\uc911\uc9c0\uacfc\uc815\uc5d0 \uc788\uc5b4 \uc774\ud574\uad00\uacc4\uc790 \ubaa8\ub450\ub97c \ud3ec\ud568\ud558\uc5ec \ucc98\u000d\u000a\uc74c\uacfc \ub05d\uc5d0 \uad00\ud55c \uc9c0\uc6d0\uacfc \ub0b4\uba74\uc758 \uc120\ud0dd \ub3d9\uae30\uc5d0 \uc788\uc5b4\uc11c \uc0dd\uac01\ud574\ubcf4\uc544\uc57c \ud560 \uc774\uc288\uae4c\uc9c0 \ub2e4\uac01\uc801 \ubd84\uc11d\uc744 \uc2dc\ub3c4\ud588\u000d\u000a\ub2e4. \uc774\ub97c \ubc14\ud0d5\uc73c\ub85c \uad6c\ucd95\ub418\uc5b4\uc57c\ud560 \uc0ac\ud68c\uc801 \uc778\ud504\ub77c\ub3c4 \uc81c\uc5b8\ud558\uc5ec \uc81c\ub3c4\uc801 \uacf5\ubc31\uc774 \ucc44\uc6cc\uc57c\ud560 \ubd80\ubd84\uc744 \uc81c\uc2dc\ud558\u000d\u000a\uace0 \uc788\uc5b4 \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \uc774 \uc5f0\uad6c\uac00 \uc81c1\ud68c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\ub85c \uc120\uc815\ud558\uc600\ub2e4. \u000d\u000a\uc120\uc815\uc774\uc720","30 31\u000d\u000a\uc218 \uc758\ub8cc\uc11c\ube44\uc2a4\ub85c \uc784\uc2e0\uc911\uc9c0 \ud569\ubc95\ud654\ub97c \uc694\uad6c\ud558\ub294 \uc6b4\ub3d9\uc5d0 \ub2f9\uc5f0\ud788 \ucc38\uc5ec\ud574\uc57c \ud55c\ub2e4\uace0 \uc911\uc9c0\ub97c \ubaa8\u000d\u000a\uc558\uace0, \uadf8\ub807\uac8c \uc6b0\ub9ac\ub294 \uc704\uba3c\uc628\uc6f9Women on Web \ub300\ud45c\uc774\uc790 \ub124\ub35c\ub780\ub4dc\uc758 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\uc778 \ub808\ubca0\uce74 \u000d\u000a\uacf0\ud37c\uce20\uac00 \ubaa8\ub450\uc758 \uc131\uacfc \uc7ac\uc0dd\uc0b0 \uac74\uac15\uacfc \uad8c\ub9ac \ubcf4\uc7a5\uc744 \uc678\uce58\ub294 \uad11\ud654\ubb38 \uad11\uc7a5\uc5d0 \ucc38\uc5ec\ud558\uac8c \ub418\uc5c8\ub2e4. \u000d\u000a\ubb34\ub354\uc6b4 \uc5ec\ub984\uc744 \ub354 \ub728\uac81\uac8c \ub9cc\ub4dc\ub294 \uc9d1\ud68c\ub97c \ub9c8\uce58\uace0, \uc2dc\uc6d0\ud55c \ub9e5\uc8fc\ub97c \ub9c8\uc2dc\ub7ec \ucc3e\uc544 \ub4e4\uc5b4\uac04 \uc5b4\ub290 \u000d\u000a\uc220\uc9d1\uc5d0\uc11c \uc6b0\ub9ac\ub294 \ub099\ud0dc\uc8c4 \ud3d0\uc9c0 \uc774\ud6c4\ub97c \uc774\uc57c\uae30\ud588\ub2e4. \ud615\ubc95\uc0c1 \ub099\ud0dc\uc8c4\uac00 \uc704\ud5cc\ud310\uacb0\uc744 \ubc1b\uac8c \ub418\uba74, \u000d\u000a\uc774\ud6c4\uc5d0\ub294 \uc5b4\ub5bb\uac8c \ub418\ub294 \uac70\uc9c0? \uc9c0\uae08\uae4c\uc9c0 \uc0b0\ubd80\uc778\uacfc\uc5d0\uc11c \ud558\ub294 \uc74c\uc131\uc801 \uc784\uc2e0\uc911\uc9c0 \uc2dc\uc220\ub4e4\uc774 \ud569\ubc95\u000d\u000a\uc774 \ub418\ub294 \uac78\uae4c? \uadf8\ub7fc \uadf8\ub0e5 \ub418\ub294 \uac74\uac00? \uac74\uac15\ubcf4\ud5d8 \uae09\uc5ec\ub294? \uc784\uc2e0\uc911\uc9c0 \uc758\uc57d\ud488\uc740? \uc758\ub8cc\uc758 \uc9c8\uc740?\u000d\u000a\uc6b0\ub9ac\ub294 2010\ub144\ubd80\ud130 \uac74\uac15\uacfc \ubcf4\uac74\uc758\ub8cc \uc601\uc5ed\uc5d0\uc11c \ud65c\ub3d9\ud574\uc654\ub358 \uc5f0\uad6c\uc18c\uc5d0\uc11c\ub3c4 \uc5ec\uc131 \uac74\uac15, \uadf8\uc911\u000d\u000a\uc5d0\ub3c4 \uc784\uc2e0\uc911\uc9c0\uc640 \uad00\ub828\ud55c \uad6c\uccb4\uc801\uc778 \uc0ac\uc548\uc744 \ubcf8\uaca9 \uc5f0\uad6c\ud558\uac70\ub098 \ub2e4\ub8ec \uc801\uc774 \uc5c6\uc5c8\ub2e4\ub294 \uc0ac\uc2e4\uc744 \uc0c8\u000d\u000a\uc0bc \uae68\ub2ec\uc558\ub2e4. \ube44\ubc94\uc8c4\ud654\uc640 \ud569\ubc95\ud654\ub294 \uadf8\uc800 \uc2dc\uc791\uc77c \ubfd0\uc774\uad6c\ub098, \ubcf4\uac74\uc758\ub8cc\uc601\uc5ed\uc5d0\uc11c \uc784\uc2e0\uc911\uc9c0\ub97c\u000d\u000a\ud544\uc218\uc758\ub8cc\ub85c, \ub610 \uc591\uc9c8\uc758 \uc801\ud569\ud55c \uc758\ub8cc\ub85c \ub9cc\ub4e4\uc5b4\ub0b4\uae30 \uc704\ud55c \ud22c\uc7c1\uc758 \uc5ed\uc0ac\uac00 \uc804 \uc138\uacc4\uc801\uc73c\ub85c \uae34\ub370,\u000d\u000a\ud55c\uad6d\uc5d0\uc120 \ubb34\uc5bc \ud588\uc9c0? \uc544\uc774\ucfe0 \uc774\ub7f0. \uc88b\uc740 \uc784\uc2e0\uc911\uc9c0 \ubcf4\uc7a5\ud558\uae30 \uc704\ud574 \uac08 \uae38\uc774 \uae4c\ub9c8\ub4dd\ud558\uad6c\ub098.\u000d\u000a\uae5c\uc9dd \ub180\ub780 \uc6b0\ub9ac\ub294 \uacf5\ubd80\ub97c \uc2dc\uc791\ud588\ub2e4. \uadf8\ub807\uac8c \uc2dc\ubbfc\uac74\uac15\uc5f0\uad6c\uc18c\uc758 \uc0c1\uadfc \uc5f0\uad6c\uc6d0\ub4e4\uc774 \uacf5\ubd80\ud55c \u000d\u000a\ub0b4\uc6a9\uc744 \ucd9c\ud310\ud55c \ubcf4\uace0\uc11c\uac00 \uc2dc\ubbfc\uac74\uac15\uc774\uc288 \u201c\uc784\uc2e0\uc911\uc9c0\uc640 \uc7ac\uc0dd\uc0b0 \uc815\uc758: \uc120\ud0dd\uc744 \ub118\uc5b4 \uad8c\ub9ac\ub85c, \uad8c\u000d\u000a\ub9ac\ub97c \ub118\uc5b4 \uc815\uc758\ub85c\u201d \ub2e4. \uc5ec\uae30\uc5d0\ub294 \uc5ec\uc131 \uac74\uac15\uad8c\uc774\ub77c\ub294 \ub80c\uc988\ub97c \uac00\uc9c0\uace0 \uac74\uac15\uad8c\uacfc \uc784\uc2e0\uc911\uc9c0\uc758 \u000d\u000a\uad50\ucc28\uc810\uc744 \uacf5\ubd80\ud55c \ub0b4\uc6a9\uc774 \ub2f4\uaca8 \uc788\ub2e4. \uc6b0\ub9ac\ub294 \uad6d\uc81c\uc801 \uc784\uc2e0\uc911\uc9c0 \ud569\ubc95\ud654 \uc6b4\ub3d9\uc758 \uacfc\uc815\uc744 \uc0b4\ud3b4\u000d\u000a\ubcf4\uba70 \ub9ce\uc740 \uc5ec\uc131\uc774 \uc9c0\uc9c0\ud558\ub294 \uc784\uc2e0\uc911\uc9c0 \ud569\ubc95\ud654\uac00 \ub2e8\uc9c0 \uc784\uc2e0\uc911\uc9c0 \uc2dc\uc220\uc744 \ud5c8\ud558\ub77c\ub294 \uc694\uccad\uc744 \ub118\u000d\u000a\uc5b4 \uc774 \uc0ac\ud68c\uc5d0\uc11c \uc5ec\uc131\uc774 \uc778\uac04\ub2f5\uac8c \uc0b4\uc544\uac08 \uad8c\ub9ac\ub97c \uc694\uad6c\ud558\ub294 \uc6b4\ub3d9\uc784\uc744 \uc54c\uac8c \ub418\uc5c8\ub2e4. \uc784\uc2e0\uc911\uc9c0 \u000d\u000a\ud569\ubc95\ud654 \uc694\uad6c\ub294 \uad6d\uac00\uc640 \uc0ac\ud68c\uc5d0\uac8c \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uacfc \uad8c\ub9acSexual and Reproductive Health and Rights\u000d\u000a\ubcf4\uc7a5\uc744 \uc694\uad6c\ud558\ub294 \ub354 \ub113\uc740 \uac74\uac15\uad8c \ud22c\uc7c1\uc774\uae30\ub3c4 \ud558\ub2e4. \uc544\uc9c1 \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c \ub2e4\uc18c \uc0dd\uc18c\ud55c \uac1c\ub150\u000d\u000a\uc778 \ud558\ub294 \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uacfc \uad8c\ub9ac\ub294 \uad6d\uc81c \uc0ac\ud68c\uc5d0\uc11c \uc778\uc815\ubc1b\ub294 \uc911\uc694\ud55c \uc778\uad8c\uc758 \ubaa9\ub85d\uc5d0 \ud3ec\ud568\ub418\u000d\u000a\uc5b4 \uc788\ub2e4. 2019\ub144 \uc2dc\ubbfc\uac74\uac15\uc774\uc288\uc5d0\uc11c\ub294 \ud5cc\ubc95\uc7ac\ud310\uc18c\uc758 \ub099\ud0dc\uc8c4 \uc704\ud5cc\ud310\uacb0\uc774 \uadf8 \uc790\uccb4\ub85c \uc6d0\uce58 \uc54a\u000d\u000a\ub294 \uc784\uc2e0\uc744 \ud55c \uc5ec\uc131\ub4e4\uc758 \uace4\ub780\ud568\uacfc \uace0\ud1b5\uc744 \uc989\uac01 \ud574\uacb0\ud574\uc8fc\uc9c0 \ubabb\ud55c\ub2e4\uace0 \uac15\uc870\ud588\ub2e4. \uc2e4\uc9c8\uc801\uc778 \uad8c\u000d\u000a\ub9ac \ubcf4\uc7a5\uc744 \uc704\ud574\uc11c\ub294 \uc801\uc808\ud55c \uc81c\ub3c4\uc640 \uc815\ucc45\uc774 \ud544\uc694\ud558\uace0, \ub354 \uc88b\uc740 \uc758\ub8cc\ub97c \uc704\ud55c \uad6c\uccb4\uc801\uc778 \ub178\ub825\u000d\u000a\uc774 \ub4a4\ub530\ub77c\uc57c \ud55c\ub2e4\ub294 \uc810\uc5d0\uc11c \uc774\uc81c \uc6b0\ub9ac\ub294 \ud55c\uad6d\uc758 \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uc744 \ub9d0\ud558\uae30 \uc2dc\uc791\ud558\ub294 \ub2e8\uacc4\u000d\u000a\uc5d0 \uc788\ub2e4\ub294 \uc8fc\uc7a5\uc774\uc5c8\ub2e4.\u000d\u000a\uc131 \u00b7 \uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d\uae30\ud68d I : \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30 "," & 32\u000d\u000a2. \ud5cc\ubc95\uc7ac\ud310\uc18c \ud310\uacb0 \uc774\ud6c4 \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c \ubcf4\uc7a5\ud558\uae30 \uc704\ud55c \uace0\ubbfc\u000d\u000a\uc5f0\uad6c\uc18c\uac00 \uc870\ubc14\uc2ec\uc744 \ub0b4\uba70 \uacf5\ubd80\ub97c \uc2dc\uc791\ud588\ub358 \uac83\ucc98\ub7fc, \ud55c\uad6d \uc0ac\ud68c \uc804\ubc18\uc5d0\uc11c \uc784\uc2e0\uc911\uc9c0\ub97c \ud544\uc218\u000d\u000a\uc758\ub8cc\ub85c \ubcf4\uc7a5\ud558\uae30 \uc704\ud55c \ubcf4\uac74\uc758\ub8cc \uad00\ub828 \uacf5\ubd80\uc640 \ub300\ube44\ub294 \uac70\uc758 \ub418\uc5b4\uc788\uc9c0 \uc54a\uc558\ub358 \uac83 \uac19\ub2e4. \uc774 \uae00\u000d\u000a\uc744 \uc791\uc131\ud558\ub294 2022\ub144 12\uc6d4\uae4c\uc9c0\ub3c4 \uc591\uc9c8\uc758 \uc548\uc804\ud55c \uc784\uc2e0\uc911\uc9c0\ub97c \ubaa8\ub450\uc5d0\uac8c \ubcf4\uc7a5\ud558\uae30 \uc704\ud55c \uc815\ucc45\u000d\u000a\uacfc \uacf5\uacf5\uc11c\ube44\uc2a4\ub294 \ub9c8\ub828\ub418\uc9c0 \uc54a\uc558\ub2e4. 2019\ub144 4\uc6d4 \ud5cc\ubc95\uc7ac\ud310\uc18c\ub294 \ud615\ubc95\uc0c1 \ub099\ud0dc\uc8c4\uc5d0 \ub300\ud574 \ud5cc\ubc95\u000d\u000a\ubd88\ud569\uce58 \uacb0\uc815\uc744 \ub0b4\ub9ac\uba74\uc11c \uad6d\ud68c\uc5d0 2020\ub144 12\uc6d4\uae4c\uc9c0 \ud569\ubc95\uc801\uc778 \uc784\uc2e0\uc911\uc9c0\uc5d0 \ub300\ud55c \uc815\ubd80\uc758 \ucc45\uc784\u000d\u000a\uc744 \uaddc\uc815\ud558\ub294 \ubaa8\uc790\ubcf4\uac74\ubc95 \uac1c\uc815\uc744 \uad8c\uace0\ud588\ub2e4. \uadf8\ub7ec\ub098 \uc6b0\ub9ac\uc758 \uc785\ubc95\uc790\ub4e4\uc740 \uc8fc\uc5b4\uc9c4 \uae30\ud55c\uc5d0\uc11c 2\ub144\u000d\u000a\uc774 \ub118\uc5b4\uac00\ub3c4\ub85d \uc784\uc2e0\uc911\uc9c0\uc640 \uad00\ub828\ud55c \ubc95\ub960 \uac1c\uc815\uc744 \ubbf8\ub904\ub450\uace0 \uc788\ub2e4. \uc785\ubc95\uc744 \ud1b5\ud55c \uc0ac\ud68c\uc801 \ud569\uc758\uac00\u000d\u000a\uba85\ubc31\ud574\uc9c0\uc9c0 \uc54a\uc740 \uc0c1\ud0dc\uc5d0\uc11c \ud589\uc815\ubd80\ub294 \ubc95\uc801 \uadfc\uac70\uac00 \uc5c6\ub2e4\ub294 \uc774\uc720\ub85c \uac74\uac15\ubcf4\ud5d8 \uae09\uc5ec \ub17c\uc758\ub97c \ube44\u000d\u000a\ub86f\ud574 \uc88b\uc740 \uc758\ub8cc\ub97c \uc704\ud55c \uace0\ubbfc\uc744 \ubc29\uae30 \uc911\uc774\ub2e4.\u000d\u000a\uadf8\ub7ec\ub098 \uc785\ubc95\ubd80\uc640 \ud589\uc815\ubd80\uac00 \uc5b4\ub5a4 \ud551\uacc4\ub97c \ub300\uace0, \uc5b4\ub5bb\uac8c \uc0c1\ud669\uc744 \uc778\uc2dd\ud558\ub4e0 \ubcc0\uce58 \uc54a\ub294 \uc0ac\uc2e4\u000d\u000a\uc774 \uc788\ub2e4. \uc6d0\uce58 \uc54a\ub294 \uc784\uc2e0\uc744 \ud558\uace0, \uc784\uc2e0\uc911\uc9c0\ub97c \uc120\ud0dd\ud558\ub294 \uc5ec\uc131\ub4e4\uc740 \uc5b8\uc81c\ub098 \uc788\ub2e4\ub294 \uc0ac\uc2e4\uc774\ub2e4.\u000d\u000a\uadf8\ub807\ub2e4\uba74 \uc774 \uc5ec\uc131\ub4e4\uc740 \uc5b4\ub5a4 \uc0c1\ud669\uc744 \uacaa\uace0 \uc788\ub294 \uac78\uae4c? \uc2dc\uc220\uc744 \uc81c\uacf5\ud558\ub294 \ubcd1\uc6d0\uc774 \uc788\uc73c\ub2c8 \ub418\ub294\u000d\u000a\uac78\uae4c? \uc774\ub807\uac8c \uc74c\uc131\ud654\ub41c \uc601\uc5ed\uc5d0\uc11c \uc774\ub8e8\uc5b4\uc838 \uc654\ub358 \uc2dc\uc220\uc740 \ud1b5\uc0c1\uc758 \uc758\ub8cc\uc640 \uc5b4\ub5bb\uac8c \ube44\uc2b7\ud558\uac70\ub098\u000d\u000a\ub2e4\ub97c\uae4c? \ucc98\ubc8c\ub9cc \ubc1b\uc9c0 \uc54a\uc73c\uba74 \uad1c\ucc2e\uc740 \uac78\uae4c?\u000d\u000a\uc774 \uad81\uae08\uc99d\uc744 \ud574\uacb0\ud558\uae30 \uc704\ud574 \uc6b0\ub9ac\ub294 2020\ub144 \uc6b0\ub9ac \uc5f0\uad6c\uc18c \ud68c\uc6d0\uacfc \uc0c1\uadfc \uad6c\uc131\uc6d0 \ucd1d 12\uc778\uc774\u000d\u000a\ucc38\uc5ec\ud558\ub294 \u201c\uc7ac\uc0dd\uc0b0 \uac74\uac15\uccb4\uacc4 \uac1c\uc120 \uc5f0\uad6c\ud300\u201d\uc744 \uafb8\ub838\ub2e4. \uc790\uc728\uc801\uc778 \uae30\uc5ec\uc640 \ucc38\uc5ec\ub97c \uc694\uccad\ud558\ub294 \uba54\u000d\u000a\uc2dc\uc9c0\ub97c \ubcf4\uace0 \ubaa8\uc778 \uc5f0\uad6c\uc790\ub4e4\uc740 \uc804\uc6d0 \uc5ec\uc131\uc73c\ub85c \uad6c\uc131\ub418\uc5c8\ub2e4. \uc8fc\ub85c \ubcf4\uac74\ud559\uc744 \uacf5\ubd80\ud558\ub294 \ub300\ud559\uc6d0\u000d\u000a\uc0dd\ub4e4\ub85c, \uac13 \ubc15\uc0ac\ud559\uc704\ub97c \ubc1b\uace0 \uc878\uc5c5\ud55c \uc2e0\ucc38 \uc5f0\uad6c\uc790\uc778 \ud544\uc790\uac00 \ucc45\uc784\uc790 \uc5ed\ud560\uc744 \ub9e1\uc558\ub2e4. \uc6b0\ub9ac\ub294 \u000d\u000a\uc9c0\uae08\uae4c\uc9c0 \ud55c\uad6d\uc758 \uac74\uac15 \uc5f0\uad6c\ub4e4\uc774 \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uc744 \uc5b4\ub5bb\uac8c \ub17c\uc758\ud574\uc654\ub294\uc9c0 \uc0b4\ud3b4\ubcf4\uace0, \ud55c\uad6d\uc758 \u000d\u000a\ubcf4\uac74\uc758\ub8cc\uccb4\uacc4\ub97c \uace0\ub824\ud588\uc744 \ub54c \uc784\uc2e0\uc911\uc9c0\ub97c \ube44\ub86f\ud55c \uc5ec\uc131 \uac74\uac15 \ubcf4\uc7a5\uc744 \uc704\ud574 \ubb34\uc5c7\uc774 \uc5b4\ub5bb\uac8c \ubcc0\u000d\u000a\ud574\uc57c \ud558\ub294\uc9c0\ub97c \uace0\ubbfc\ud574\ubcf4\uae30\ub85c \ud588\ub2e4. \u000d\u000a\uace0\ubbfc\uacfc \uad81\ub9ac\uac00 \ub9ce\uc558\ub358 \ub9cc\ud07c \uacf5\ubd80\ud560 \ub0b4\uc6a9\uc774 \ub9ce\uace0 \ubcf5\uc7a1\ud588\ub2e4. \uc784\uc2e0\uc911\uc9c0\ub97c \ubcf4\uac74\uc758\ub8cc\uc640 \uac74\uac15\u000d\u000a\uad8c\uc5d0 \ub300\ud55c \uc9c0\uc2dd\uc5d0\uc11c \ub2e4\ub8e8\uba70 \uc5ec\ub7ec \uc9c8\ubb38\uc744 \ub5a0\uc62c\ub9ac\uac8c \ub418\uc5c8\ub2e4. \uc5b4\uc9f8\uc11c\uc778\uc9c0 \uc131\uacfc \uc7ac\uc0dd\uc0b0 \uac74\uac15 \u000d\u000a\uc601\uc5ed\uc5d0 \uc18d\ud558\ub294 \uc5ec\ub7ec \uac74\uac15 \ud544\uc694(\ud53c\uc784, \uc784\uc2e0\uc911\uc9c0, \ub09c\uc784 \ub4f1)\ub97c \uc0ac\ud68c\uc801 \uad8c\ub9ac\ub85c \uc313\uc544 \uc62c\ub9ac\ub294 \uacfc\uc815\uc740 \ub2e4\uc18c \u000d\u000a\uc0dd\uc18c\ud558\uace0 \uc2e0\uc120\ud588\ub2e4. \uc6d0\ud558\ub294 \uc2dc\uae30\uc5d0 \uc784\uc2e0\ud558\uac70\ub098 \ud558\uc9c0 \uc54a\uace0, \uc874\uc5c4\ud558\uac8c \uc544\uc774\ub97c \ub0b3\uc744 \uad8c\ub9ac\ub97c ","32 33\u000d\u000a\ubcf4\uc7a5\ud558\uc790\uace0 \ub9d0\ud558\ub294 \uc77c\uc740 \u201c\uac74\uac15\uc740 \uad6d\ub825\u201d\uc774\ub77c\uace0 \uc9c4\uc785\ub85c\uc5d0 \uc801\ud600\uc788\ub294 \ud559\uad50\uc5d0\uc11c \ubc30\uc6cc\uc654\ub358 \ud2c0\uacfc \u000d\u000a\uaf34\uc744 \uac16\ucd94\ub294 \ub370\uc5d0 \uc5b4\uca50\uc9c0 \ub354 \ub9ce\uc740 \ub178\ub825\uc774 \ud544\uc694\ud588\ub2e4. \uc784\uc2e0\uc911\uc9c0 \uadf8 \uc790\uccb4\ub294 \uc5ec\uc131\uc758 \uac74\uac15\uc5d0, \uc0b6\u000d\u000a\uc758 \uc9c8\uc5d0, \uc7a5\uae30\uc801\uc778 \uc0b6\uc758 \uc131\ucde8\uc640 \uae30\ud68d\uc5d0 \uc5b4\ub5a4 \uc601\ud5a5\uc744 \ubbf8\uce58\ub294 \uc0ac\uac74\uc778\uc9c0 \uc5ed\uc2dc \uba85\ud655\ud55c \uad6d\ub0b4\uc758 \u000d\u000a\uadfc\uac70\uac00 \uc5c6\uc5c8\ub2e4. \uadf8\ub807\ub2e4\uba74 \uc6b0\ub9ac\ub294 \uc5b4\ub5a4 \uc785\uc7a5\uacfc \uad00\uc810\uc744 \uac00\uc9c0\uace0 \uc774\ub97c \uc774\ud574\ud574\uc57c \ud560\uae4c? \ud55c\uad6d\uc758 \u000d\u000a\uc0c1\ud669\uc774 \uc774\ub7ec\ud558\ub2e4\uba74 \uc784\uc2e0\uc911\uc9c0\ub97c \ub458\ub7ec\uc2fc \uc815\uce58\uac00 \ub354\uc6b1 \uce58\uc5f4\ud558\ub2e4\uace0\ub4e4 \ud558\ub294 \uc678\uad6d\uc5d0\uc11c\ub294 \uc784\uc2e0\u000d\u000a\uc911\uc9c0\uac00 \uc5b4\ub5bb\uac8c \ub2e4\ub8e8\uc5b4\uc9c0\uace0 \uc788\uc744\uae4c? \uc5f0\uad6c\uc18c\uc758 \uc5f0\uad6c\ub4e4\uc740 \u201c\uac74\uac15\uc744 \ubcf4\uc7a5\ud574\uc57c \ud560 \uad6d\uac00\uc758 \ucc45\ubb34\u201d\u000d\u000a\ub97c \uc8fc\uc7a5\ud558\uace4 \ud588\ub294\ub370, \u201c\ub0b4 \ubab8, \ub0b4 \uc120\ud0dd\u201dmy body my choice\uc774\ub77c \uc678\uce58\ub294 \uc784\uc2e0\uc911\uc9c0 \ud569\ubc95\ud654 \uad6c\ud638\uc5d0\u000d\u000a\uc11c \uad6d\uac00\ub294 \uc5b4\ub5a4 \uc5ed\ud560\uc744 \ub9e1\ub294 \uac78\uae4c? \uc2a4\uc2a4\ub85c \uc120\ud0dd\ud558\uace0 \uacb0\uc815\ud558\ub294 \uc790\uc8fc\uc801\uc774\uba70 \ub3c5\ub9bd\uc801\uc778 \uc5ec\uc131\u000d\u000a\uc774\ub77c\ub294 \uc5b4\ub5a4 \ud658\uc0c1\uc744 \ub118\uc5b4\uc130\uc744 \ub54c, \uac01\uc790\uc758 \ud2b9\uc218\ud55c \uc0c1\ud669\uc5d0\uc11c\uc758 \uc784\uc2e0\uc911\uc9c0\ub97c \uc120\ud0dd\ud558\ub294 \uc5ec\uc131\ub4e4\u000d\u000a\uc740 \uc5b4\ub5a4 \uc5b4\ub824\uc6c0\uc744 \uacaa\uc744\uae4c? \uc7a5\uc560\uac00 \uc788\ub294 \uc5ec\uc131, \uccad\uc18c\ub144 \uc5ec\uc131\uc5d0\uc11c \uc784\uc2e0\uc911\uc9c0\ub294 \uc5b4\ub5bb\uace0, \ud30c\ud2b8\ub108\u000d\u000a\ub85c\uc11c \uc784\uc2e0\uc911\uc9c0\ub97c \uacbd\ud5d8\ud558\ub294 \ub0a8\uc131\uc774\ub098 \ud0dc\uc544\uc758 \uc2ec\uac01\ud55c \uc190\uc0c1\uc774 \ubc1c\uacac\ub418\uc5b4 \uc784\uc2e0\uc911\uc9c0\ub97c \uc120\ud0dd\ud558\uac8c \u000d\u000a\ub418\ub294 \ubd80\ubd80\ub4e4\uc758 \uacbd\ud5d8\uc740 \uc5b4\ub5a8\uae4c? \u000d\u000a\uc57c\uc2ec\ucc2c \ubaa9\ud45c\uc640 \uc9c8\ubb38\uc758 \ub2e4\uc591\ud568\ub9cc\ud07c\uc774\ub098 \uc6b0\ub9ac\uc758 \uc5f0\uad6c \uc9c4\ud589 \uacfc\uc815\uc740 \uc0dd\uac01\ubcf4\ub2e4 \ub354\ub3a0\uace0, \uc0c8\ub86d\u000d\u000a\uac8c \uc54c\uac8c \ub41c \ub0b4\uc6a9\uc744 \ud2c0\uc744 \uac16\ucdb0 \uc815\ub9ac\ud558\ub294 \ub370\uc5d0\ub294 \uc624\ub79c \uc2dc\uac04\uc774 \uc18c\uc694\ub410\ub2e4. \uc5ec\ub7ec \uc9c8\ubb38\uacfc \ud574\ub2f5\uc744 \u000d\u000a\ubaa8\uc0c9\ud558\ub294 \uacfc\uc815 \uc911 \uad6c\uccb4\uc801\uc73c\ub85c \uc0ac\ub78c\ub4e4\uc774 \uc758\ub8cc\ub85c\uc11c \uc784\uc2e0\uc911\uc9c0\ub97c \uc5b4\ub5bb\uac8c \uacaa\uace0 \uc788\ub294\uc9c0 \ud655\uc778\ud558\u000d\u000a\uae30 \uc704\ud574 \uc758\ub8cc\uc81c\uacf5\uc790\uc640 \uc758\ub8cc\uc774\uc6a9\uc790, \uc2dc\ubbfc\uc0ac\ud68c\ud65c\ub3d9\uac00 \ucd1d 28\uba85\uc744 \uba74\ub2f4\ud55c \uacb0\uacfc\ub97c \uc815\ub9ac\ud55c \uac83\uc774 \u000d\u000a2022\ub144 10\uc6d4\uc5d0 \ucd9c\ud310\ud55c \ubcf4\uace0\uc11c, \u201c\uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d \uae30\ud68d I \u2013 \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\u000d\u000a\ud558\uae30\u201d\uc774\ub2e4. \u000d\u000a3. \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30: \uc758\ub8cc\ud604\uc7a5\uc758 \uc0c1\ud669\uc774 \uad81\uae08\ud558\ub2e4 \u000d\u000a\uc774 \ubcf4\uace0\uc11c\uac00 \ud2b9\ubcc4\ud55c \uc810\uc774 \uc788\ub2e4\uba74 \uc774\ub294 \uc544\ub9c8 \uc758\ub8cc\uc81c\uacf5\uc790\uc778 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac, \uadf8\uc911\uc5d0\uc11c\ub3c4 \uc784\u000d\u000a\uc2e0\uc911\uc9c0 \uc2dc\uc220\uc744 \uc81c\uacf5\ud588\ub358 \uacbd\ud5d8\uc744 \uac00\uc9c0\uace0 \uc788\ub294 \uc758\uc0ac\ub4e4\uc758 \ubaa9\uc18c\ub9ac\ub97c \ub2f4\uc558\uae30 \ub54c\ubb38\uc774\ub77c\uace0 \uc0dd\uac01\u000d\u000a\ud55c\ub2e4. \uadfc\ub798 \uc2a4\uc2a4\ub85c \ub9d0\ud558\uae30 \uc2dc\uc791\ud55c \uc5ec\uc131\ub4e4, \uadf8\ub9ac\uace0 \ubaa8\ub099\ud3d0 \ub4f1 \uc2dc\ubbfc\uc0ac\ud68c\uc758 \ub178\ub825 \ub355\ud0dd\uc5d0 \uc74c\uc131\u000d\u000a\uc801\uc778 \ubc29\uc2dd\uc73c\ub85c \uc784\uc2e0\uc911\uc9c0\ub97c \ud560 \uc218\ubc16\uc5d0 \uc5c6\uc5c8\ub358 \uc5ec\uc131\ub4e4\uc758 \ubaa9\uc18c\ub9ac\ub098, \uc6d0\uce58 \uc54a\ub294 \uc784\uc2e0\uc744 \ud558\uac8c \u000d\u000a\ub41c \uc5ec\uc131\ub4e4\uc744 \ub3c4\uc640\uc628 \ud65c\ub3d9\uac00\ub4e4\uc758 \uacbd\ud5d8\uc744 \ubcf4\uc5ec\uc8fc\ub294 \ub9d0\uacfc \uae00\ub4e4\uc774 \uc138\uc0c1\uc5d0 \ub098\ud0c0\ub098\uae30 \uc2dc\uc791\ud588\ub2e4. \u000d\u000a\uc131 \u00b7 \uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d\uae30\ud68d I : \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30 "," & 34\u000d\u000a2021\ub144 \ubaa8\ub099\ud3d0\uc758 \uc784\uc2e0\uc911\uc9c0 \uacbd\ud5d8 \uc124\ubb38\u00b7\uc2e4\ud0dc\uc870\uc0ac\ub098,2) \uac19\uc740 \ud574 \ud55c\uad6d\uc5ec\uc131\uc815\ucc45\uc5f0\uad6c\uc6d0\uc5d0\uc11c \ub0b4\u000d\u000a\ub193\uc740 \ubcf4\uace0\uc11c3)\uac00 \ub300\ud45c\uc801\uc774\ub2e4. \ud558\uc9c0\ub9cc \uc784\uc2e0\uc911\uc9c0 \uc11c\ube44\uc2a4\ub97c \uc9c1\uc811 \uc81c\uacf5\ud558\ub294 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub4e4\uc758 \u000d\u000a\uc785\uc7a5\uc744 \ub2f4\uc740 \ub0b4\uc6a9\uc740 \ub9e4\uc6b0 \ub4dc\ubb3c\uc5c8\ub2e4. \u000d\u000a2020\ub144 1\uc6d4 \ub300\ud55c\uc0b0\ubd80\uc778\uacfc \ud559\ud68c \ub4f1\uc774 \uacf5\ub3d9\uc73c\ub85c \uad6c\uc131\ud55c \u201c\ub099\ud0dc\ubc95\ud2b9\ubcc4\uc704\uc6d0\ud68c\u201d\ub294 \uc885\uc124 \ub17c\ubb38 \u000d\u000a\ud615\ud0dc\ub85c \ub099\ud0dc\uc8c4 \ud3d0\uc9c0 \uc774\ud6c4 \uc758\ub8cc \uad00\ub828 \uc774\uc288\uc5d0 \ub300\ud55c \uc785\uc7a5\uc744 \ucd9c\ud310\ud588\ub2e4.4) \uc5ec\uae30\uc5d0\ub294 \ub300\uccb4\ub85c \uc0c1\uae09\u000d\u000a\uc885\ud569\ubcd1\uc6d0\uae09 \uc758\ub8cc\uae30\uad00\uc5d0\uc11c \uc77c\ud558\uba70 \uc911\uc99d\uc758 \ud0dc\uc544\uc640 \uc0b0\ubaa8\ub97c \uc9c4\ub8cc\ud558\ub294 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub4e4\uc774 \uc800\uc790\u000d\u000a\ub85c \ucc38\uc5ec\ud588\ub294\ub370, \uc57d\ubb3c \uc784\uc2e0\uc911\uc9c0\ub97c \ud3ec\ud568\ud574 \uc2dc\uc220\uc790\ub97c \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub85c \ud55c\uc815\ud558\uace0, \uc758\uc0ac\uac00 \ube44\uc758\u000d\u000a\ud559\uc801 \uc0ac\uc720\uc758 \ub099\ud0dc \uacb0\uc815 \uacfc\uc815\uc5d0 \ucc38\uc5ec\ud558\uc9c0 \uc54a\uace0 \uc2dc\uc220\ub9cc\uc744 \ub2f4\ub2f9\ud574\uc57c \ud558\uba70, \ub099\ud0dc \ud5c8\uc6a9 \uc2dc\uae30\ub97c \u000d\u000a\uc784\uc2e0 10\uc8fc \ubbf8\ub9cc\uc73c\ub85c \uc81c\ud55c\ud574\uc57c \ub9c8\ub545\ud558\ub2e4\ub294 \ub4f1 \uad6d\uc81c\uc801 \uc5ec\uc131 \uac74\uac15\uad8c \ub2f4\ub860\uc5d0 \ubc30\uce58\ub418\ub294 \ub0b4\uc6a9\ub4e4\u000d\u000a\uc774 \ud3ec\ud568\ub418\uc5b4 \uc788\uc5c8\ub2e4. \ud558\uc9c0\ub9cc \uc774\uc0c1\ud558\uc9c0 \uc54a\uc740\uac00. \ub099\ud0dc\uc8c4 \ud3d0\uc9c0\uc5d0 \ub300\ud55c \uc5ec\ub860\uc774 \ubb34\ub974\uc775\uae30 \ud55c\ucc38 \u000d\u000a\uc804\ubd80\ud130 \ub099\ud0dc\ub97c \uc2dc\uc220\ud558\ub294 \ub3d9\ub8cc \uc758\uc0ac\ub97c \uc2e0\uace0\ud558\uaca0\ub2e4\uace0 \uc120\uc5b8\ud558\uc5ec \uc720\uba85\ud574\uc9c4 \uc778\ubb3c\uc758 \uc785\uc7a5\uc774 \uc804\u000d\u000a\uccb4 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub4e4\uc758 \uc758\uacac\uc744 \ub300\ubcc0\ud55c\ub2e4\uace0 \ubcfc \uc218 \uc788\uc744\uae4c? \uc784\uc2e0\uc911\uc9c0\ud558\ub7ec \ub300\ud559 \ubcd1\uc6d0\uc5d0 \ucc3e\uc544\uac00\u000d\u000a\ub294 \uc0ac\ub78c\ub4e4\uc774 \uc788\uae30\ub294 \ud55c\uac00? \uc6b0\ub9ac\ub294 \ube44\uc758\ud559\uc801 \uc0ac\uc720\uc758 \uc784\uc2e0\uc911\uc808\uc744 \uacb0\uc815\ud588\uc744 \ub54c \uc0ac\ub78c\ub4e4\uc774 \ub300\uac8c \u000d\u000a\ucc3e\uc544\uac00\uac8c \ub418\ub294 \uc18c\uc704 \ub85c\uceec \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac \uc77c\ubc18\uc774 \ud559\ud68c\uc758 \uc785\uc7a5\uc744 \uacf5\uc720\ud558\ub294\uc9c0 \ub9e4\uc6b0 \uad81\uae08\ud588\ub2e4. \u000d\u000a\uad6c\uccb4\uc801\uc778 \uad81\uae08\uc99d\ub3c4 \ub9ce\uc558\ub2e4. \u201c8\uc8fc \uc784\uc2e0\uc911\uc9c0 70\ub9cc\uc6d0, \uc774\ud6c4 \uc8fc\uc218 1\uc8fc \uc99d\uac00\ud560 \ub54c\ub9c8\ub2e4 10\ub9cc \uc6d0\u000d\u000a\uc529 \uae08\uc561 \ucd94\uac00\u201d \uc774\ub807\uac8c \ud64d\ubcf4\ud558\ub294 \uc0b0\ubd80\uc778\uacfc \ube14\ub85c\uadf8\uc758 \uae00\uc740 \ubb34\uc5bc \uae30\uc900\uc73c\ub85c \uc800\ub7f0 \ube44\uc6a9\uc744 \ub9e4\uae30\u000d\u000a\ub294 \uac78\uae4c? \ud55c\uad6d\uc5d0\uc11c \ubbf8\ud398\ud504\ub9ac\uc2a4\ud1a4\uc774 \uc2b9\uc778\ud5c8\uac00\uac00 \uc548 \ub418\uc5c8\ub294\ub370, \ubcd1\uc6d0\uc5d0\uc11c \uc57d\uc73c\ub85c \uc784\uc2e0\uc911\uc9c0\ub97c \u000d\u000a\ud588\ub2e4\ub294 \uc751\ub2f5\uc740 \ubb34\uc5bc \uc758\ubbf8\ud560\uae4c? \ub354\ubd88\uc5b4 \uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uc744 \uc81c\uacf5\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \uc758\ub8cc\uc81c\uacf5\uc790\ub4e4\u000d\u000a\uc774 \uacaa\uac8c \ub418\ub294 \uc5b4\ub824\uc6c0\uc774\ub098 \uace4\ub780\ud568\uc744 \ud30c\uc545\ud558\ub294 \uac83 \uc5ed\uc2dc \ud5a5\ud6c4 \ud55c\uad6d\uc5d0\uc11c \ubcf4\ud3b8\uc801\uc778 \uc784\uc2e0\uc911\uc9c0 \uc811\u000d\u000a\uadfc\uc744 \ub192\uc774\uae30 \uc704\ud574 \uc544\uc8fc \uc911\uc694\ud55c \ubb38\uc81c\ub77c\uace0 \ud310\ub2e8\ud588\ub2e4. \u000d\u000a\uc774\ub7f0 \uad00\uc810\uc744 \uac00\uc9c0\uace0 \uc2dc\uc791\ud55c \uc5f0\uad6c\uc5d0\uc11c \uc5f0\uad6c\uc9c4\uc740 2020\ub144 5\uc6d4\ubd80\ud130 8\uc6d4 \uc0ac\uc774 6\uba85\uc758 \uc0b0\ubd80\u000d\u000a\uc778\uacfc \uc804\ubb38\uc758, 11\uba85\uc758 \uc784\uc2e0\uc911\uc9c0 \uacbd\ud5d8 \uc5ec\uc131, \uadf8\ub9ac\uace0 11\uba85\uc758 \ud65c\ub3d9\uac00\ub97c \ub9cc\ub098 \uc774\uc57c\uae30\ub97c \ub098\ub204\uc5c8\u000d\u000a\ub2e4. \uba3c\uc800 \uc0b0\ubd80\uc778\uacfc \uc804\ubb38\uc758\ub4e4\uc758 \uc774\uc57c\uae30 \uba3c\uc800 \uc815\ub9ac\ud574\ubcf4\uc790.\u000d\u000a2\u3000\ubaa8\ub450\ub97c\uc704\ud55c\ub099\ud0dc\uc8c4\ud3d0\uc9c0\uacf5\ub3d9\ud589\ub3d9(2021). 2021 \uc784\uc2e0\uc911\uc9c0 \uacbd\ud5d8 \uc124\ubb38\u00b7\uc2e4\ud0dc\uc870\uc0ac \ubc0f \uc2ec\uce35\uc778\ud130\ubdf0 \uacb0\uacfc\ubcf4\uace0\uc11c. \u000d\u000a 3\u3000\uae40\ub3d9\uc2dd \u00b7 \ub3d9\uc81c\uc5f0 \u00b7 \uae40\uc0c8\ub86c (2021). \u201c\uc548\uc804\ud55c \uc784\uc2e0\uc911\ub2e8\uc744 \uc704\ud55c \uc758\ub8cc\uc811\uadfc\uc131 \uc81c\uace0\ubc29\uc548 \uc5f0\uad6c\u201d . \ud55c\uad6d\uc5ec\uc131\uc815\ucc45\uc5f0\uad6c\uc6d0 .\u000d\u000a 4\u3000\ucd5c\uc548\ub098 \uc678 . (2020). \ub099\ud0dc\ubc95 \uac1c\uc815 \uad00\ub828 \uc758\ub8cc\uc801 \uc774\uc288\uc640 \uc0b0\ubd80\uc778\uacfc\uc758 \uc785\uc7a5 . \ud55c\uad6d\ubaa8\uc790\ubcf4\uac74\ud559\ud68c\uc9c0 , 24(1), 9-17. ","34 35\u000d\u000a4. \uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uc744 \uc81c\uacf5\ud558\ub294 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub4e4\uc758 \uc774\uc57c\uae30\u000d\u000a\uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uc744 \ud558\uace0 \uc788\ub2e4 \ub9d0\ud558\ub294 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub97c \uc12d\uc678\ud558\ub294 \uc77c\uc740 \uc0ac\uc2e4 \ub9c9\ub9c9\ud55c \uc77c\uc774\ub2e4. \u000d\u000a\uc784\uc2e0\uc911\uc9c0\uac00 \ud615\ubc95\uc0c1 \ucc98\ubc8c\uc758 \ub300\uc0c1\uc774\uc5c8\ub358 \uc2dc\uae30\uc758 \uacbd\ud5d8\uc744 \ub9d0\ud558\ub294 \uc77c \uc5ed\uc2dc \ub2e4\uc18c \ubd80\ub2f4\uc2a4\ub7ec\uc6b8 \uc218 \u000d\u000a\ubc16\uc5d0 \uc5c6\ub2e4. \uc784\uc2e0\uc911\uc9c0 \ud569\ubc95\ud654\uc640 \uc774\ud6c4\uc758 \uc785\ubc95 \uacfc\uc815\uc5d0 \ub300\ud55c \uc0ac\ud68c\uc801 \ub17c\uc758\uac00 \uce58\uc5f4\ud574\uc838 \uac00\ub294 \uc0c1\ud669\u000d\u000a\uc740 \ud2b9\ud788 \ub354 \uadf8\ub807\ub2e4. \uac70\uae30\uc5d0 \ub354\ud574 \uc5ec\uc131 \uac74\uac15\uc744 \uc639\ud638\ud558\ub294 \ud65c\ub3d9\uac00\ub4e4(=\uc6b0\ub9ac \uc5f0\uad6c\ud300)\uc744 \ub9cc\ub098 \uae30\uaebc\uc774 \uc194\u000d\u000a\uc9c1\ud55c \ub300\ud654\ub97c \ub098\ub204\uaca0\ub2e4\uace0 \ub9d0\ud558\ub294 \uc758\uc0ac\ub97c \uad6c\ud558\uae30\ub294 \uc5b4\ub824\uc6e0\ub2e4. \uacb0\uad6d \uc6b0\ub9ac\uac00 \uba74\ub2f4\ud55c \uc0b0\ubd80\uc778\uacfc \u000d\u000a\uc758\uc0ac\ub4e4\uc740 \uc0ac\uc2e4, \uae30\uc874\uc5d0 \uc784\uc2e0\uc911\uc9c0 \ud569\ubc95\ud654\ub97c \uc639\ud638\ud558\ub294 \ud65c\ub3d9\uc5d0 \ucc38\uc5ec\ud588\uac70\ub098, \uc9c1\uc811 \uc815\ucc45\uc639\ud638\ud65c\u000d\u000a\ub3d9\uc5d0 \uad00\uc5ec\ud558\uc9c0\ub294 \uc54a\uc558\ub354\ub77c\ub3c4 \uc77c\uc815\ud558\uac8c \uc774\uc5d0 \ub300\ud55c \uc9c0\uc9c0\ub97c \ubc1d\ud78c \uc774\ub4e4\uc774\uc5c8\ub2e4. \uc6b0\ub9ac\uac00 \ub9cc\ub0a0 \uc218 \u000d\u000a\uc788\uc5c8\ub358 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub4e4\uc740 \ub300\uccb4\ub85c \uc5f0\ub839\ub300\uac00 \ub0ae\uace0(20\ub300~40\ub300) \uc804\uc6d0 \uc5ec\uc131\uc73c\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\uc5c8\ub2e4. \u000d\u000a\uc0b0\ubd80\uc778\uacfc \uc758\uc0ac\ub4e4\uacfc\uc758 \uba74\ub2f4\uc5d0\uc11c \ud30c\uc545\ud560 \uc218 \uc788\uc5c8\ub358 \uccab \ubc88\uc9f8 \uc0ac\uc2e4\uc740 \ud5cc\ubc95\uc7ac\ud310\uc18c\uc758 \ud310\uacb0 \uadf8 \u000d\u000a\uc790\uccb4\ub294 \uc758\ub8cc \ud604\uc7a5\uc5d0 \ud070 \uc601\ud5a5\uc744 \ubbf8\uce58\uc9c0 \uc54a\ub294\ub2e4\ub294 \uac83\uc774\uc5c8\ub2e4. \uc5ec\uc804\ud788 \ud655\uc2e4\ud574\uc9c4 \uac83\uc774 \uc5c6\uace0, \uac74\u000d\u000a\uac15\ubcf4\ud5d8\uc774\ub098 \uc758\ub8cc\ube44\uc5d0\uc11c \ub2ec\ub77c\uc9c0\ub294 \ub0b4\uc6a9\uc774 \uc5c6\uae30\uc5d0 \uadf8\ub807\ub2e4. \ud615\ubc95\uc0c1 \ub099\ud0dc\uc8c4\uc758 \ubc95\ub960\uc801 \uc0c1\ud0dc\uc5d0 \u000d\u000a\ub300\ud574\uc11c\ub3c4 \uc81c\ub300\ub85c \ud30c\uc545\ud558\uc9c0 \ubabb\ud55c \uc758\ub8cc\uc778\uc774 \ub9ce\ub2e4\ub294 \uc774\uc57c\uae30\ub3c4 \ub4e4\uc744 \uc218 \uc788\uc5c8\ub2e4. \uc784\uc2e0\uc911\uc9c0 \ud569\ubc95\u000d\u000a\ud654 \uacfc\uc815\uc774 \uc911\uc694\ud558\uac8c \uc5ec\uaca8\uc9c0\ub294 \uc601\uc5ed\uc740 \ub300\ud559\ubcd1\uc6d0 \ubcf4\ub2e4\ub294 \uc784\uc2e0\uc911\uc808\uc774 \uc911\uc694\ud55c \uc218\uc785\uc6d0\uc774 \ub418\ub294 \u000d\u000a\uac1c\uc6d0\uac00\uc77c \uac00\ub2a5\uc131\uc774 \ub192\uc558\ub2e4. \ucd5c\uadfc \uc804\ubc18\uc801\uc73c\ub85c \ubd84\ub9cc \uac74\uc218\uac00 \uc904\uc5b4\ub4e4\uba74\uc11c \uc0b0\ubd80\uc778\uacfc \ubcd1\u00b7\uc758\uc6d0\uc774 \u000d\u000a\uaddc\ubaa8\ub97c \ud0a4\uc6cc \uc804\ubb38\ubcd1\uc6d0\uc774 \ub418\uac70\ub098, \ubd84\ub9cc\uc744 \ud3ec\uae30\ud558\uace0 \uc0c1\uc5c5\uc801 \uc2dc\uc220\ub4e4\uc744 \ub354 \uc801\uadf9\uc801\uc73c\ub85c \ud558\uac8c \ub418\u000d\u000a\ub294\ub370, \uc774 \uc911 \ud6c4\uc790\uc5d0\uc11c \uacbd\uc7c1\uc801\uc73c\ub85c \uc784\uc2e0\uc911\uc808 \uc2dc\uc220 \ube44\uc6a9\uc744 \ud64d\ubcf4\ud558\ub294 \uacbd\ud5a5\uc774 \uc788\uc744 \uac83\uc774\ub77c\ub294 \u000d\u000a\uba74\ub2f4 \ucc38\uc5ec\uc790\ub4e4\uc758 \uc608\uc0c1\uc740 \uc2e4\uc81c \uc628\ub77c\uc778 \uad11\uace0\uc5d0\uc11c \ub2e4\uc591\ud558\uac8c \ud655\uc778\ub410\ub2e4. \u000d\u000a\ub450 \ubc88\uc9f8 \ud14c\ub9c8\ub294 \uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uacfc \uad00\ub828\ud55c \uc758\ub8cc\uc81c\uacf5\uc790\ub4e4\uc758 \ubc95\uc801\u00b7\uc2ec\ub9ac\uc801 \ubd80\ub2f4\uc758 \uc0c1\ub2f9 \ubd80\ubd84\uc774 \u000d\u000a\uc2dc\uc220\uc5d0 \ub300\ud55c \ucc98\ubc8c \ubb38\uc81c(\uc9c0\uae08\uc740 \uc0ac\ub77c\uc9c4 \ud615\ubc95\uc758 \ub099\ud0dc\uc8c4) \uc678\uc5d0\ub3c4 \uc720\uc804\uc131 \uc9c8\ud658 \ub4f1 \ud0dc\uc544\uc758 \uac74\uac15\ubb38\uc81c\uc640 \uad00\ub828\ud558\uc5ec \u000d\u000a\uc758\uc0ac\uc758 \ucc45\uc784\uc774 \uc5b4\ub514\uae4c\uc9c0\uc778\uc9c0\uc5d0 \ub300\ud55c \ubb38\uc81c\uc640 \uacb0\ubd80\ub418\uc5b4 \uc788\ub2e4\ub294 \uc0ac\uc2e4\uc774\uc5c8\ub2e4. \ub9c8\ucc2c\uac00\uc9c0\ub85c \ud604\uc7ac \u000d\u000a\ud6a8\ub825\uc744 \uc783\uc740 \ubaa8\uc790\ubcf4\uac74\ubc95 \uc81c14\uc870 \ub099\ud0dc\uc8c4 \uc704\ubc95\uc131 \uc870\uac01\uc0ac\uc720\uc5d0\uc11c \uc720\uc804\ub418\uc9c0 \uc54a\ub294 \ud0dc\uc544\uc758 \uac74\uac15 \ubb38\u000d\u000a\uc81c\ub294 \ubcf8\ub514 \ud569\ubc95\uc801\uc778 \uc784\uc2e0\uc911\uc808\uc758 \uc0ac\uc720\uac00 \uc544\ub2c8\uc5c8\ub2e4. \uc774\ub294 \uc0b0\uc804\uc9c4\ucc30 \uacfc\uc815\uc5d0\uc11c \ud0dc\uc544\uc758 \uac74\uac15\uc0c1 \ubb38\u000d\u000a\uc81c\ub97c \uc608\uce21\ud558\uc9c0 \ubabb\ud55c \uacbd\uc6b0\uc5d0\ub3c4 \uc758\uc0ac\ub4e4\uc774 \ucd9c\uc0dd\ud55c \ud0dc\uc544\uc758 \uac74\uac15 \ubb38\uc81c\uc5d0 \ub300\ud55c \ucc45\uc784\uc744 \ubb3b\ub294 \ubd80\ubaa8\ub85c\u000d\u000a\ubd80\ud130 \uc77c\uc815\ud558\uac8c \ucc45\uc784\uc744 \ud53c\ud560 \uc218 \uc788\ub294 \uc870\uac74\uc744 \uc81c\uacf5\ud588\ub2e4. \uc77c\ubd80 \uba74\ub2f4\ucc38\uc5ec\uc790\ub294 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac \ucee4\u000d\u000a\ubba4\ub2c8\ud2f0\uc5d0 \ubd80\ubaa8\uc758 \uacb0\uc815\uc5d0 \ub530\ub978 \ube44\uc758\ud559\uc801 \uc784\uc2e0\uc911\uc9c0\uac00 \ud569\ubc95\ud654\ub418\uc5c8\uc744 \ub54c, \uc0b0\uc804 \uc9c4\ucc30\uc744 \ub2f4\ub2f9\ud55c \uc758\u000d\u000a\uc0ac\uc5d0\uac8c \uc544\uc774\uc758 \uc7a5\uc560\ub098 \uc9c8\ubcd1\uc5d0 \ub300\ud55c \ucc45\uc784\uc774 \ub3cc\ub824\uc9c8 \uac83\uc5d0 \ub300\ud55c \uc6b0\ub824\uac00 \ub9cc\uc5f0\ud558\ub2e4\uba70 \uac71\uc815\ud588\ub2e4. \u000d\u000a\uc131 \u00b7 \uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d\uae30\ud68d I : \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30 "," & 36\u000d\u000a\uadf8\ub7ec\ub098 \uc774\ub294 \ub099\ud0dc\uac00 \ubd88\ubc95\uc73c\ub85c \uc5ec\uaca8\uc9c8 \ub54c\uc5d0\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\ub85c \ubd80\ub2f4\uc2a4\ub7ec\uc6b4 \ubb38\uc81c\uc600\ub2e4. \uc544\ub9c8\ub3c4 \u000d\u000a\ub9ce\uc740 \uc0ac\ub78c\uc774 \uc7a5\uae30 \uc0dd\uc874\uc774 \ubd88\uac00\ub2a5\ud55c \uce58\uba85\uc801\uc778 \uc5fc\uc0c9\uccb4 \uc774\uc0c1\uc774 \uc788\ub294 \ud0dc\uc544\uc758 \uacbd\uc6b0\uc5d0\ub3c4 \uc5ec\uc131\uc774 \u000d\u000a\uc784\uc2e0\uc744 \ub9cc\uc0ad\uae4c\uc9c0 \uc720\uc9c0\ud558\uace0 \ubd84\ub9cc\uacfc \uc608\uc815\ub41c \ud0dc\uc544\uc758 \uc0ac\ub9dd\uc744 \uacaa\uc5b4\uc57c\ub9cc \ud55c\ub2e4\uace0 \uac15\uc81c\ud558\ub294 \uc77c\uc774 \u000d\u000a\ubd80\ub2f9\ud558\ub2e4\uace0 \uc0dd\uac01\ud560 \uac83\uc774\ub2e4. \ud5cc\ubc95\uc7ac\ud310\uc18c \uc5ed\uc2dc \uc774\ub97c \uac15\uc694\ud558\ub294 \ubc95\ub960\uc774 \uc798\ubabb\ub418\uc5c8\ub2e4\uace0 \ud310\uacb0\ud588\u000d\u000a\ub2e4. \ud558\uc9c0\ub9cc \uc774\ub54c\uc758 \u201c\uc2ec\uac01\ud55c \uac74\uac15 \ubb38\uc81c\u201d\ub294 \ub2e8\uc21c\uba85\ub8cc\ud558\uac8c \uacb0\uc815\ud560 \uc218 \uc5c6\uc73c\uba70, \uc0b0\uc804\uc5d0 \ud655\uc778\ud560 \u000d\u000a\uc218 \uc788\ub294 \ud0dc\uc544\uc758 \uac74\uac15 \ubb38\uc81c\ub294 \uc5b8\uc81c\ub098 \ud655\ub960\uc758 \ubb38\uc81c\ub2e4. \u000d\u000a\uc774\uc640 \uad00\ub828\ud558\uc5ec \ub3c4\ucd9c\ub418\ub294 \uc138 \ubc88\uc9f8 \ud14c\ub9c8\ub294 \ud55c\uad6d\uc5d0\uc11c \uac74\uac15 \ubb38\uc81c\uac00 \uc788\ub294 \ud0dc\uc544\uc758 \uc784\uc2e0\uc911\uc9c0\u000d\u000a\uc640 \uad00\ub828\ud55c \ubc95\u00b7\uc81c\ub3c4 \ubc0f \uc0ac\ud68c\uc801\u00b7\ubb38\ud654\uc801 \uace0\ubbfc\uc774 \ubd80\uc7ac\ud558\ub2e4\ub294 \uc0ac\uc2e4\uc774\ub2e4. \ud1b5\uc0c1 \uc0dd\ubb3c\ud559\uc801\uc73c\ub85c \uc608\u000d\u000a\uc0c1\ub418\ub294 \uc7a5\uc560\uc544 \ucd9c\uc0dd\uc728\uacfc \ud55c\uad6d\uc5d0\uc11c \uc7a5\uc560\uc544 \ucd9c\uc0dd\uc728\uc744 \ube44\uad50\ud558\uba74 \ubb38\uc81c\uc758 \uaddc\ubaa8\ub97c \uc9d0\uc791\ud560 \uc218 \uc788\u000d\u000a\ub2e4. \uadf8\ub7ac\uc744 \ub54c \ud55c\uad6d\uc740 \u201c\ubc95\ub960\uc0c1 \ubd88\ubc95\u201d\uc774\uc5c8\ub358 \uc7a5\uc560\uc544\uc5d0 \ub300\ud55c \uc784\uc2e0\uc911\uc9c0\uac00 \ub9e4\uc6b0 \uc801\uadf9\uc801\uc73c\ub85c \uc774\u000d\u000a\ub8e8\uc5b4\uc9c0\ub294 \uad6d\uac00\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \uc5f0\uad6c \ucc38\uc5ec\uc790\ub4e4\uc740 \ubcf5\uc7a1\ud55c \uac74\uac15 \ubb38\uc81c\uac00 \uc788\ub294 \ud0dc\uc544\ub97c \uc784\uc2e0\ud55c \uc5ec\u000d\u000a\uc131\uc774 \uc784\uc2e0\uc758 \uc720\uc9c0\u00b7\uc885\uacb0 \uc758\uc0ac\uacb0\uc815\uc744 \uc704\ud55c \ucda9\ubd84\ud55c \uc815\ubcf4\ub97c \uc81c\uacf5\ubc1b\uace0 \uc788\ub294\uc9c0\ub97c \uc6b0\ub824\ud588\ub2e4. \uc774\u000d\u000a\ub7f0 \uc0c1\ud669\uc5d0 \ud544\uc694\ud55c \ucda9\ubd84\ud55c \uc11c\ube44\uc2a4(\uc720\uc804 \uc0c1\ub2f4, \uc0c1\uae09 \ubcd1\uc6d0\uc73c\ub85c\uc758 \uc5f0\uacc4, \ub2e4\ud559\uc81c\uc801 \uc9c4\ub8cc, \uc0ac\ud68c\ubcf5\uc9c0 \uc11c\ube44\uc2a4 \uc5f0\uacc4 \ub4f1)\uac00 \uc81c\uacf5\ub418\uace0 \u000d\u000a\uc788\ub294\uc9c0\ub3c4 \uc758\ubb38\uc774\uc5c8\ub2e4. \u000d\u000a\uc6b0\ub9ac\ub294 \uc7a5\uc560\uac00 \uc788\ub294 \uc544\uc774\ub97c \uc628\uc804\ud788 \uc798 \ud0a4\uc6b8 \uc218 \uc788\ub3c4\ub85d \ub3d5\ub294 \uc0ac\ud68c\uc801 \uc548\uc804\ub9dd\uc774 \uc874\uc7ac\ud558\uc9c0 \u000d\u000a\uc54a\ub294 \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c \ud0dc\uc544\uc758 \uc2ec\uac01\ud55c \uac74\uac15 \ubb38\uc81c\uc5d0 \uc5b4\ub5bb\uac8c \ub300\uc751\ud558\uc5ec\uc57c \ud558\ub294\uc9c0, \uc0ac\ud68c\uc801 \uace0\ubbfc\uacfc \u000d\u000a\uc9c0\uc9c0\uac00 \uc808\ub300\uc801\uc73c\ub85c \ubd80\uc871\ud558\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \ud574\uc678 \uc0ac\ub840\ub4e4\uc744 \ucc38\uc870\ud558\uc600\uc744 \ub54c, \ubcf5\uc7a1\ud55c \uac74\uac15 \ubb38\u000d\u000a\uc81c\ub97c \uac00\uc9c4 \ud0dc\uc544\uc5d0 \ub300\ud55c \uc758\uc0ac\uacb0\uc815 \ubd80\ub2f4\uc774 \uadf8\uc758 \ubd80\ubaa8\ub098 \uc0b0\ubd80\uc778\uacfc \uc758\uc0ac \uac1c\uc778\uc5d0\uac8c \uc804\uc801\uc73c\ub85c \ubd80\u000d\u000a\uacfc\ub418\ub294 \ud604\uc7ac\uc758 \uc0c1\ud669\uc740 \ub300\ub2e8\ud788 \ubd80\uc815\uc758\ud558\ub2e4. \ubcf4\ub2e4 \uacf5\uc801 \ubc29\uc2dd\uc73c\ub85c \uc724\ub9ac\uc801 \ub17c\uc758\ub97c \uac70\uccd0 \ubcf5\uc7a1\ud55c \u000d\u000a\uac74\uac15 \ubb38\uc81c\ub97c \uac00\uc9c4 \ud0dc\uc544\uc758 \uc784\uc2e0 \uc720\uc9c0\u00b7\uc885\uacb0 \uacb0\uc815\uc744 \ubcf4\uc870\ud574\uc57c \ud55c\ub2e4. \u000d\u000a\ub124 \ubc88\uc9f8 \ud14c\ub9c8\ub294 \uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uc744 \uc81c\uacf5\ud558\ub294 \uc758\uc0ac\ub4e4\uc758 \uc785\uc7a5\uacfc \uc0c1\ud669\uc774 \ub179\ub85d\uce58 \uc54a\ub2e4\ub294 \uc0ac\uc2e4\u000d\u000a\uc774\uc5c8\ub2e4. \uc758\ub8cc\uc81c\uacf5\uc790\ub4e4\uc740 \uc5ec\uc131\uc758 \uc790\uae30\uacb0\uc815\uc744 \uc9c0\uc9c0\ud558\ub294 \ub9c8\uc74c\uc73c\ub85c, \ud639\uc740 \ub2e4\uc18c \ubd88\ud3b8\ud55c \ub9c8\uc74c\uc744 \u000d\u000a\ub0b4\uc0c9\ud558\uc9c0 \uc54a\uae30 \uc704\ud574 \ub178\ub825\ud558\uba70 \uc790\uc2e0\uc774 \uc9c4\ub8cc\ud558\ub294 \ud658\uc790\uc5d0 \ub300\ud55c \ucc45\uc784\uac10\uc73c\ub85c \uc784\uc2e0\uc911\uc808\uc2dc\uc220\uc5d0 \u000d\u000a\uc751\ud55c\ub2e4\uace0 \ub9d0\ud588\ub2e4. \uadf8\ub7ec\ub098 \uc774 \uc5c5\ubb34\ub294 \ud3c9\uc18c \ud0dc\uc544\uc758 \uac74\uac15\uacfc \uc0dd\uc874\uc744 \uc704\ud574 \uc9c4\ub8cc\ud558\ub294 \ud3c9\uc18c\uc758 \uc5c5\u000d\u000a\ubb34\uc640\ub294 \uc870\uae08 \uacb0\uc774 \ub2e4\ub978 \uc131\uaca9\uc744 \uac00\uc9c0\uace0 \uc788\ub2e4. \uc5ec\uc131\ub4e4\uc758 \uc120\ud0dd\uc744 \uc639\ud638\ud558\ub294 \uc758\uc0ac\ub77c\uace0 \ud558\ub354\ub77c\u000d\u000a\ub3c4 \uc77c\uc815\ud558\uac8c \uc2ec\ub9ac\uc801 \ubd80\ub2f4\uc744 \ub290\ub07c\uac8c \ub418\ub294 \uac83\uc774\ub2e4. \ud45c\uc900\ud654\ub41c \uc808\ucc28\ub098 \ud504\ub85c\ud1a0\ucf5c\uc774 \uc5c6\ub294 \uc0c1\ud669\uc5d0\u000d\u000a\uc11c \uc784\uc2e0\uc911\uc808 \uc11c\ube44\uc2a4\ub97c \uc81c\uacf5\ud558\ub2e4 \ubcf4\ub2c8 \uacaa\uac8c \ub418\ub294 \ud604\uc2e4\uc801\uc778 \uc5b4\ub824\uc6c0\ub3c4 \uc788\uc5c8\ub2e4. \uc784\uc2e0 \uc911\uae30 \uc774","36 37\u000d\u000a\ud6c4 \ubc30\ucd9c\ub41c \ud0dc\uc544\uc758 \uad00\ub9ac\uc640 \ud6c4\uc18d \uc870\uce58\uc5d0 \ub300\ud55c \uace0\ubbfc, \uc758\ub8cc\uae30\uad00 \ub0b4\uc5d0\uc11c \uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uc5d0 \ucc38\uc5ec\u000d\u000a\ud558\ub294 \ub2e4\ub978 \uc758\ub8cc\uc778\ub4e4\uacfc\uc758 \uc18c\ud1b5, \ub3d9\uc758\ud558\uae30 \uc5b4\ub824\uc6b4 \uc774\uc720\ub85c \uc784\uc2e0\uc911\uc808(\uc5ec\uc544\uc120\ubcc4\ub099\ud0dc, \uc0ac\uc18c\ud558\uace0 \uce58\uba85\uc801\uc774\uc9c0 \uc54a\uc740\u000d\u000a\uac74\uac15 \ubb38\uc81c\ub85c \uc778\ud55c \uc784\uc2e0\uc911\ub2e8 \uacb0\uc815 \ub4f1)\uc744 \uc694\uccad\ubc1b\uc558\uc744 \ub54c\uc758 \ub09c\uac10\ud568 \ub4f1 \uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uc790\uc758 \ubd80\ub2f4\uc744 \uac00\uc911\ud558\ub294 \u000d\u000a\uc694\uc778\ub4e4\uc740 \ubb34\ucc99 \ub9ce\uc558\ub2e4. \u000d\u000a5. \uc784\uc2e0\uc911\uc9c0\ub97c \uacbd\ud5d8\ud55c \uc5ec\uc131\ub4e4\uc758 \uc774\uc57c\uae30\u000d\u000a\ub2e4\uc74c\uc740 \uc784\uc2e0\uc911\uc9c0\ub97c \uacbd\ud5d8\ud55c \uc5ec\uc131\ub4e4\uc758 \uc774\uc57c\uae30\ub2e4. \uc6b0\ub9ac\uc5d0\uac8c \uc790\uc2e0\uc758 \uc784\uc2e0\uc911\uc9c0 \uacbd\ud5d8\uc744 \uacf5\uc720\u000d\u000a\ud574\uc900 \uc5f4\ud55c \uba85\uc758 \uc5ec\uc131\uc758 \uc774\uc57c\uae30\ub294 \ub2e4\uc591\ud588\ub2e4. \uc6d0\uce58 \uc54a\uac70\ub098 \uc900\ube44\ub418\uc9c0 \uc54a\uc740 \uc0c1\ud669\uc5d0\uc11c \uc784\uc2e0\uc744 \u000d\u000a\ud558\uac8c \ub418\uc5b4 \uc784\uc2e0\uc911\uc9c0\ub97c \uc120\ud0dd\ud55c \uc5ec\uc131\ub4e4\ub3c4 \uc788\uc5c8\uace0, \uc544\uc774\ub97c \uac00\uc9c8 \uc0dd\uac01\uc774 \uc788\uc5c8\uc9c0\ub9cc \uc784\uc2e0 \uc0ac\uc2e4\uc744 \u000d\u000a\ubaa8\ub974\ub294 \uc0c1\ud0dc\uc5d0\uc11c \uc758\uc57d\ud488\uc744 \ubcf5\uc6a9\ud558\uace0 \uadf8 \uc601\ud5a5\uc774 \ub450\ub824\uc6cc \uc784\uc2e0\uc911\uc9c0\ub97c \uacb0\uc815\ud55c \uc5ec\uc131\ub3c4 \uc788\uc5c8\ub2e4. \u000d\u000a\ud574\uc678\uc5d0\uc11c \ubbf8\ud398\ud504\ub9ac\uc2a4\ud1a4(\ub0b4\uacfc\uc801 \uc784\uc2e0\uc911\uc9c0 \uc758\uc57d\ud488)\uc744 \ubcf5\uc6a9\ud588\ub358 \uc5ec\uc131\ub3c4 \ud3ec\ud568\ub418\uc5c8\uc9c0\ub9cc, \uad6d\ub0b4\uc5d0\uc11c \uc784\uc2e0\uc911\u000d\u000a\uc9c0\ub97c \ud55c \uc5ec\uc131 9\uc778\uc740 \ubaa8\ub450 \uc218\uc220\uc801 \ubc29\ubc95\uc73c\ub85c \uc784\uc2e0\uc744 \uc885\uacb0\ud588\ub2e4. \u000d\u000a\uc758\ub8cc\uc758 \uce21\uba74\uc5d0\uc11c \ud30c\uc545\ud560 \uc218 \uc788\uc5c8\ub358 \uacb0\ub860\uc740 \uc5f4\ud55c \uba85\uc758 \uc5ec\uc131\uc774 \uc774\uc6a9\ud55c \uc784\uc2e0\uc911\uc9c0 \uc11c\ube44\uc2a4\u000d\u000a\uc758 \ubc29\uc2dd\uc774 \ub9e4\uc6b0 \uc81c\uac01\uac01\uc774\uc5c8\ub2e4\ub294 \uc0ac\uc2e4\uc774\ub2e4. \uc784\uc2e0\uc911\uc808 \uc2dc\uc220\uc744 \uc81c\uacf5\ud558\ub294 \ubcd1\uc6d0\uc744 \ucc3e\ub294 \ub2e8\uacc4\uc5d0\u000d\u000a\uc11c\ubd80\ud130 \uc0c1\ud669\uc740 \ub9e4\uc6b0 \ub2ec\ub790\ub2e4. \uac00\uc871\uc774\ub098 \uc9c0\uc778\uc744 \ud1b5\ud574 \uce5c\ubd84 \uad00\uacc4\uac00 \uc788\ub294 \uc758\ub8cc\uc778\uacfc \ubcd1\uc6d0\uc744 \uc18c\u000d\u000a\uac1c\ubc1b\uc544 \uc218\uc6d4\ud558\uac8c \ucc3e\uc544\uac00\ub294 \uacbd\uc6b0\ubd80\ud130 \uba87 \ubc88\uc774\uace0 \uac70\uc808\uc744 \ub2f9\ud558\uace0 \uc218\ub77d\ud558\ub294 \ubcd1\uc6d0\uc744 \uc5b4\ub835\uac8c \uad6c\u000d\u000a\ud558\uac70\ub098, \uc0ac\ub78c\ub4e4\uc758 \uc2dc\uc120\uc5d0\uc11c \ubc97\uc5b4\ub098\uae30 \uc704\ud574 \uc678\uacfd\uc758 \ud5c8\ub984\ud558\uace0 \uc0ac\ub78c \uc5c6\ub294 \ubcd1\uc6d0\uc744 \ucc3e\uc740 \uc5ec\uc131\u000d\u000a\ub3c4 \uc788\uc5c8\ub2e4. \uc2dc\uc220\uc774 \uc774\ub8e8\uc5b4\uc9c0\ub294 \uacfc\uc815\uc5d0\uc11c \uc0c1\ub2f4\uacfc \uc218\uc220 \uad00\ub828 \ub3d9\uc758\uc11c \uc791\uc131, \ubcf4\ud638\uc790\uc758 \ub3d9\uc758 \uc694\u000d\u000a\uad6c, \uc815\uc11c\uc801 \uc9c0\uc9c0\uc758 \uc218\uc900\ub3c4 \uadf8 \ud3b8\ucc28\uac00 \uc0c1\ub2f9\ud588\ub2e4. \uc784\uc2dc\uc911\uc808 \uc2dc\uc220 \uc774\ud6c4\uc5d0 5\uc77c \ub3d9\uc548 \ub9e4\uc77c \ubcd1\uc6d0\u000d\u000a\uc73c\ub85c \uc624\ub77c\uace0 \uc548\ub0b4\ubc1b\uc544 \ub2e4\ub2cc \uc5ec\uc131\ub3c4 \uc788\uc5c8\uace0, \uc218\uc220\uc758 \ubd80\uc791\uc6a9 \uac00\ub2a5\uc131\uc5d0 \ub300\ud55c \uc124\uba85\ub3c4 \ub4e3\uc9c0 \ubabb\u000d\u000a\ud558\uace0, \uc2dc\uc220 \uc774\ud6c4 \ud55c \ubc88\ub3c4 \uc0b0\ubd80\uc778\uacfc\ub97c \ucc3e\uc9c0 \uc54a\uc558\ub2e4\ub294 \uc5ec\uc131\ub3c4 \uc788\uc5c8\ub2e4. \uc758\ub8cc\ub85c\uc11c \uc784\uc2e0\uc911\uc9c0\uac00 \u000d\u000a\ud45c\uc900\ud654\ub41c \uc808\ucc28\uc640 \uc9c8 \uad00\ub9ac\ub97c \uc704\ud55c \uc870\uac74\uc744 \uc544\uc9c1 \uac16\ucd94\uc9c0 \ubabb\ud558\uace0 \uc788\uc74c\uc744 \uc2dc\uc0ac\ud558\ub294 \uc18c\uacac\uc774\ub2e4. \u000d\u000a6. \uc5ec\uc131\uc758 \uc131\u00b7\uc7ac\uc0dd\uc0b0\uad8c\uc744 \uc639\ud638\ud558\ub294 \ud65c\ub3d9\uac00\ub4e4\uc758 \uc774\uc57c\uae30\u000d\u000a\uc5f0\uad6c\uc5d0\uc11c \uba74\ub2f4\ud55c \ud65c\ub3d9\uac00\ub4e4\uc740 \ud55c\uad6d\uc5d0\uc11c \uc5ec\uc131 \uc778\uad8c\uacfc \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uad8c\uc744 \uc639\ud638\ud558\uba70 \uc774\uc640 \u000d\u000a\uc131 \u00b7 \uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d\uae30\ud68d I : \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30 "," & 38\u000d\u000a\uad00\ub828\ud55c \ud65c\ub3d9\uc744 \ud558\ub294 \uc774\ub4e4\uc774\uc5c8\ub2e4. \ud65c\ub3d9\uac00\ub4e4\uc740 \uc5ec\uc131 \ub2f9\uc0ac\uc790\ub4e4\uc744 \uc9c0\uc6d0\ud558\uace0 \uc774\ub4e4\uc758 \uc785\uc7a5\uc744 \ub300\u000d\u000a\ubcc0\ud558\uba70 \ubcf4\ub2e4 \uc5f4\uc545\ud55c \uc0c1\ud669\uc758 \uc5ec\uc131\ub4e4\uc774 \uad8c\ub9ac\ub85c\uc11c \uc784\uc2e0\uc911\uc9c0\ub97c \ubcf4\uc7a5\ubc1b\uc544\uc57c \ud55c\ub2e4\uace0 \ub9d0\ud574 \uc654\uae30\u000d\u000a\uc5d0 \uc6b0\ub9ac \uc5f0\uad6c\uc758 \uba74\ub2f4\ucc38\uc5ec\uc790\ub85c \uc120\uc815\ub418\uc5c8\ub2e4. \ub530\ub77c\uc11c \ud65c\ub3d9\uac00\ub4e4\uc5d0 \ub300\ud55c \uba74\ub2f4\uc740 \ud3c9\uade0\ubcf4\ub2e4 \ub354 \ud544\u000d\u000a\uc694 \uc218\uc900\uc774 \ub192\uace0high-need \uc784\uc2e0\uc911\uc9c0\uac00 \ub354 \ud070 \uc704\uae30\uac00 \ub418\ub294high-ri \uc5ec\uc131\uc758 \uacbd\ud5d8\uc744 \ud655\uc778\ud560 \uc218 \uc788\u000d\u000a\ub294 \uac04\uc811\uc801\uc778 \uc811\uadfc\uc778 \uc148\uc774\uc5c8\ub2e4. \u000d\u000a\uc774\ub4e4\uc5d0 \ub530\ub974\uba74 \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c \uc784\uc2e0\uc911\uc9c0 \ube44\uc6a9\uc740 \uc5ec\uc804\ud788 \uc911\uc694\ud55c \ubb38\uc81c\ub2e4. \ud544\uc694\ud55c \ube44\uc6a9\uc744 \u000d\u000a100\ub9cc \uc6d0\uc774\uace0 200\ub9cc \uc6d0\uc774\uace0 \uad6c\ud560 \uc218 \uc788\ub294 \uc0ac\ub78c\ub4e4\ub3c4 \uc788\uc9c0\ub9cc, \uac11\uc791\uc2a4\ub7fd\uac8c \uc784\uc2e0\uc911\uc9c0 \ube44\uc6a9\uc744 \u000d\u000a\ub9c8\ub828\ud558\uc9c0 \ubabb\ud574 \uace4\uacbd\uc5d0 \ube60\uc9c0\ub294 \uc5ec\uc131\ub4e4\uc740 \uc5b8\uc81c\ub098 \uc788\ub2e4. \ud2b9\ud788 \ubcf8\uc778\uc758 \uc18c\ub4dd\uc774 \uc5c6\ub294 \uc5ec\uc131\ub4e4, \uccad\u000d\u000a\uc18c\ub144\uc774\uac70\ub098 \uc7a5\uc560\uac00 \uc788\ub294 \uc5ec\uc131\ub4e4\uc740 \ub354 \ud070 \uace4\uacbd\uc5d0 \ube60\uc9c0\uac8c \ub418\ub294\ub370, \uc774\ub294 \uc790\uc2e0\uc774 \uc6d0\uce58 \uc54a\ub294 \uc784\u000d\u000a\uc2e0\uc744 \ud558\uac8c \ub418\uc5c8\ub2e4\ub294 \uc0ac\uc2e4\uc744 \uac00\uc7a5 \uac00\uae4c\uc6b4 \ubcf4\ud638\uc790\ub4e4\uc5d0\uac8c\ub3c4 \uc54c\ub9ac\uace0 \uc2f6\uc9c0 \uc54a\uc740 \uacbd\uc6b0\uac00 \ub300\ubd80\ubd84\u000d\u000a\uc774\uae30 \ub54c\ubb38\uc774\ub2e4. \u000d\u000a\uc784\uc2e0\uc911\uc808\uc744 \ud558\ub294 \uc5ec\uc131\ub4e4\uc5d0\uac8c \uc591\uc9c8\uc758 \uc88b\uc740 \uc758\ub8cc\uc640 \uc874\uc911, \ub3cc\ubd04\uc744 \uc81c\uacf5\ud558\uace0 \uc788\ub294\uc9c0\uc5d0 \ub300\ud55c \u000d\u000a\ubb38\uc81c \uc81c\uae30\ub3c4 \ud655\uc778\ud560 \uc218 \uc788\uc5c8\ub2e4. \uc784\uc2e0\uc911\uc808\uc5d0 \ub300\ud55c \uc0ac\ud68c\ubb38\ud654\uc801 \ub099\uc778\uc774 \ub0a8\uc544\uc788\ub294 \uc0c1\ud669\uc5d0\uc11c \uc758\u000d\u000a\ub8cc\uc774\uc6a9\uc790\ub4e4\uc758 \ud504\ub77c\uc774\ubc84\uc2dc\ub97c \ubcf4\ud638\ud558\ub294 \ub3d9\uc2dc\uc5d0 \uc88b\uc740 \uc758\ub8cc\ub97c \uc81c\uacf5\ud558\ub824\uba74 \uc5b4\ub5a4 \uc11c\ube44\uc2a4 \uacf5\uae09 \u000d\u000a\ubc29\uc2dd\uc774 \uc801\uc808\ud55c\uc9c0\uc5d0 \ub300\ud55c \uace0\ubbfc\uc774 \ud544\uc694\ud558\ub2e4\ub294 \uac83\uc774 \uc6b0\ub9ac\uc758 \uacb0\ub860\uc774\ub2e4. \ub610\ud55c, \uc774 \ub54c \uc784\uc2e0\uc911\uc9c0 \u000d\u000a\uc11c\ube44\uc2a4\uc758 \uc9c8\uc5d0 \ub300\ud55c \uace0\ubbfc\uc740 \ub354 \ubcf5\uc7a1\ud55c \ud544\uc694\ub97c \uac00\uc9c0\uace0 \uc788\ub294 \uc5ec\uc131\ub4e4\uc758 \uc0c1\ud669\uc744 \uace0\ub824\ud558\ub294 \uad50\ucc28\u000d\u000a\uc801\uc778 \uc811\uadfc\uc774 \ub418\uc5b4\uc57c \ud55c\ub2e4. \uc608\ucee8\ub300, \uacb0\ud63c\uc774\uc8fc\uc5ec\uc131\uc774 \uc790\uc2e0\uc758 \uacb0\uc815\uc5d0 \ub530\ub77c \uc784\uc2e0\uc911\uc9c0 \uc11c\ube44\uc2a4\uc5d0 \u000d\u000a\uc811\uadfc\ud560 \uc218 \uc788\uace0, \uadf8 \uacfc\uc815\uc5d0\uc11c \uc5b8\uc5b4\uc801 \uc7a5\ubcbd\uacfc \ubb38\ud654\uc801 \ucc28\uc774\ub85c \uc778\ud55c \ucc28\ubcc4\uc744 \ubc1b\uc9c0 \uc54a\uace0 \uc591\uc9c8\uc758 \u000d\u000a\ub3cc\ubd04\uc744 \ubc1b\uc744 \uc218 \uc788\uc5b4\uc57c \ud558\ub294 \uac83\uc774\ub2e4. \u000d\u000a7. \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uacfc \uad8c\ub9ac \ubcf4\uc7a5\uc744 \uc704\ud55c \uc5ec\uc815\uc740 \uc774\uc81c, \uc2dc\uc791\uc774\ub2e4\u000d\u000a\ubcf4\uace0\uc11c\ub97c \uc791\uc131\ud558\uace0 \uc774\ub97c \uc678\ubd80\uc5d0 \uc54c\ub9ac\ub294 \uacfc\uc815\uc5d0\uc11c \uc5f0\uad6c\uc790\ub85c\uc11c \ub2e4\uc18c \ud669\ub2f9\ud55c \uc77c\ub4e4\uc744 \uacaa\uc5c8\u000d\u000a\ub2e4. \ubcf4\uace0\uc11c\uc5d0 \ub4f1\uc7a5\ud558\ub294 \uc5f0\uad6c\ucc38\uc5ec\uc790\ub97c \uc790\uc2e0\uc774 \uc9c1\uc811 \uba74\ub2f4\ud560 \uc218 \uc788\ub3c4\ub85d \uc18c\uac1c\uc2dc\ucf1c\ub2ec\ub77c\ub294 \uae30\uc790\u000d\u000a\uc758 \uc694\uccad\uc744 \ubc1b\uac70\ub098, \uc5ec\uc131\ubfd0\ub9cc \uc544\ub2c8\ub77c \uc758\uc0ac\ub4e4\ub3c4 \ud798\ub4e4\ub2e4, \uc77c\uc120 \uc758\ub8cc\ud604\uc7a5\uc740 \ud63c\ub780\uc774 \uac00\ub4dd\ud558\ub2e4\ub294 \u000d\u000a\ub3d9\uc758\ud558\uae30 \uc5b4\ub824\uc6b4 \uc694\uc57d\uc73c\ub85c \uc5f0\uad6c \uacb0\uacfc\uac00 \ubcf4\ub3c4\ub418\ub294 \ub4f1\uc758 \uc77c\uc744 \uacaa\uc73c\uba70 \uc18d\uc774 \uc0c1\ud558\uace0 \ud6c4\ud68c\uc2a4\ub7ec\u000d\u000a\uc6e0\ub2e4. \uadf8\ub807\uac8c\uae4c\uc9c0 \uae00\uc744 \ubabb \uc4f4 \uac78\uae4c? \uc5b8\ub860\uc740, \ud639\uc740 \uc5b8\ub860\uc774 \uc0c1\uc815\ud558\ub294 \ub3c5\uc790\ub4e4\uc740 \ud53c\ud574\uc790\uac00 \ub418\ub294 ","38 39\u000d\u000a\uc5ec\uc131\uacfc \uac00\uc774\ub4dc\ub77c\uc778\uc744 \ub9cc\ub4e4\uc9c0 \uc54a\uc740 \ub098\uc05c \uc815\ubd80-\uc0c1\uc220\ud558\uc600\ub4ef \ud55c\uad6d\uc5d0\uc11c \uc784\uc2e0\uc911\uc9c0\uc758 \ubb38\uc81c\ub294 \ub2e8\u000d\u000a\uc9c0 \uac00\uc774\ub4dc\ub77c\uc778\uc815\ub3c4\ub85c \ud574\uacb0\ub420 \ubb38\uc81c\uac00 \uacb0\ucf54 \uc544\ub2c8\ub2e4-\uc5d0\ub9cc \uad00\uc2ec\uc774 \uc788\ub294 \uac78\uae4c? \u000d\u000a\ub2e4\ud589\uc2a4\ub7fd\uac8c\ub3c4 \ubcf4\uace0\uc11c \ubc1c\uac04\uc744 \uacc4\uae30\ub85c \uc880 \ub354 \ub113\uc740 \uacf5\uac04\uc744 \ud5a5\ud574 \uc784\uc2e0\uc911\uc9c0\uc640 \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\u000d\u000a\uac15\uc758 \ubb38\uc81c\ub97c \ub9d0\ud560 \uae30\ud68c\ub3c4 \uc5bb\uc5c8\ub2e4. \uc5ec\uc131\ud559\uc744 \uc5f0\uad6c\ud558\ub294 \ub300\ud559\uc758 \uc9d1\ub2f4\ud68c\uc5d0\uc11c \uc5f0\uad6c \uacb0\uacfc\ub97c \uacf5\uc720\u000d\u000a\ud558\uac70\ub098, \uc7ac\uc0dd\uc0b0 \uad8c\ub9ac\uc5d0 \ub300\ud55c \ucc45\uc744 \uc791\uc131\ud558\ub294 \ub370\uc5d0 \uc6d0\uace0\ub97c \uc694\uccad\ubc1b\uac70\ub098 \ud558\ub294 \ub4f1\uc774\ub2e4. \uc0ac\ub2e8\ubc95\u000d\u000a\uc778 \uc2dc\ubbfc\uc5d0\uc11c \uac1c\ucd5c\ud55c \uc81c1\ud68c <\ud604\uc7a5\uc9c0\uc2ddX\uc88b\uc740\uc5f0\uad6c> \uacf5\ubaa8\uc804\uc5d0\uc11c \uce58\uc5f4\ud55c \uacbd\uc7c1\uc744 \ub6ab\uace0 \uc120\ubc1c\ub418\u000d\u000a\uc5b4 \uc5f0\uad6c \uacb0\uacfc\ub97c 2023\ub144 \uc2dc\ubbfc\uc0ac\ud68c \ud604\uc7a5\uc9c0\uc2dd \ucee8\ud37c\ub7f0\uc2a4\uc5d0\uc11c \ubc1c\ud45c\ud558\uac8c \ub41c \uac83\ub3c4 \uc0c8\ub85c \uc5bb\uc740 \uae30\u000d\u000a\ud68c \uc911 \ud558\ub098\ub2e4. \ud55c\uad6d \uc0ac\ud68c\ub97c \uad00\ud1b5\ud558\ub294 \uc544\uc8fc \uc911\uc694\ud55c \ubb38\uc81c\uc640 \ud604\uc7a5\uc744 \ub2e4\ub8e8\ub294 \uc7c1\uc7c1\ud55c \uc5f0\uad6c \uc911 \ud558\u000d\u000a\ub098\ub85c \ud3ec\ud568\ub418\uc5b4 \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uacfc \uad8c\ub9ac\ub97c \ub9d0\ud558\uac8c \ub418\uc5c8\ub2e4\ub294 \uc0ac\uc2e4\uc774 \ub9e4\uc6b0 \ubfcc\ub4ef\ud558\uace0 \uae30\uc058\ub2e4. \u000d\u000a\ube44\ub85d \uc774 \uae00\uc5d0\uc11c \ucda9\ubd84\ud788 \uac15\uc870\ud558\uc9c0 \ubabb\ud588\uc9c0\ub9cc \uc784\uc2e0\uc911\uc9c0\uc640 \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uc758 \ubb38\uc81c\ub294 \ubaa8\ub4e0 \uc0ac\u000d\u000a\ud68c\uc5d0\uc11c \uac00\uc7a5 \uc815\uce58\uc801\uc778 \ubb38\uc81c \uc911 \ud558\ub098\ub2e4. \ud558\uc9c0\ub9cc \uc804\uba74\uc801\uc778 \uacf5\ub860\ud654\uc640 \uc815\uce58\ud654\uac00 \uc774\ub8e8\uc5b4\uc9c4 \ubbf8\u000d\u000a\uad6d \ub4f1\uc758 \uc0c1\ud669\uacfc \ub2ec\ub9ac, \ud55c\uad6d\uc5d0\uc11c \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uc815\uce58\ub294 \uc544\uc9c1 \uc218\uba74 \uc544\ub798 \uac10\ucdb0\uc838 \uc788\ub2e4. \ubd84\uba85 \uc815\ucc45\u000d\u000a\uc744 \uacb0\uc815\ud558\ub294 \uc704\uce58\uc5d0 \uc788\ub294 \ub0a8\uc131\ub4e4\uc758 \uac00\uc871\uacfc \uce5c\uad6c \ub2e4\uc218\uac00 \uc784\uc2e0\uc911\uc9c0\ub97c \uacbd\ud5d8\ud588\uace0, \uadf8 \uacfc\uc815\uc5d0\uc11c \u000d\u000a\uace4\ub780\uacfc \uc5b4\ub824\uc6c0\uc744 \uacaa\uc5c8\uc744 \uac83\uc774\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \ud55c\uad6d\uc5d0\uc11c \uc5b4\ub5a4 \ub0a8\uc131 \uc815\uce58\uc778\ub3c4 \uc784\uc2e0\uc911\uc9c0\uc758 \ubb38\uc81c\u000d\u000a\ub97c \uc9c4\uc9c0\ud558\uba70 \uace0\ubbfc\ud558\uba70 \ub204\uad6c\uc5d0\uac8c\ub098 \ubaa8\uc695\uacfc \uc218\uce58 \uc5c6\uc774 \uc88b\uc740 \uc758\ub8cc\ub97c \ubcf4\uc7a5\ud558\uae30 \uc704\ud574 \ub178\ub825\ud558\uaca0\u000d\u000a\ub2e4 \uc57d\uc18d\ud558\uc9c0 \uc54a\ub294\ub2e4. \ud55c\uad6d\uc0ac\ud68c\uc5d0\uc11c\ub294 \uc5ec\uc804\ud788 \ub9ce\uc740 \uc5ec\uc131\uc774 \uac00\uc7a5 \uce5c\ubc00\ud55c \ud30c\ud2b8\ub108\uc5d0\uac8c\ub3c4 \uc790\uc2e0\u000d\u000a\uc758 \uc784\uc2e0\uc911\uc9c0 \uacbd\ud5d8\uacfc \uad8c\ub9ac\ub97c \ub9d0\ud558\uae30 \uc5b4\ub835\ub2e4\ub294 \uc758\ubbf8\uac00 \uc544\ub2d0\uae4c? \uadf8\ub798\uc11c \uc784\uc2e0\uc911\uc9c0\uc758 \ub9e5\ub77d\uc744 \ub354\u000d\u000a\ub9ce\uc774 \ub9d0\ud558\uace0 \ub354 \uc798 \ub4dc\ub7ec\ub0b4\ub294 \ud65c\ub3d9\uc774 \uc911\uc694\ud558\ub2e4 \uc0dd\uac01\ud55c\ub2e4. \uc131\u00b7\uc7ac\uc0dd\uc0b0 \uac74\uac15\uacfc \uad8c\ub9ac\ub97c \ud5a5\ud55c \uc6b0\u000d\u000a\ub9ac\uc758 \uc5ec\uc815\uc740 \uc774\uc81c \uc2dc\uc791\uc774\uace0, \ud604\uc7a5\uc5d0\uc11c \uc2dc\uc791\ud558\ub294 \uc9c0\uc2dd\uacfc \uc774\uc5d0 \uae30\ubc18\ud55c \ud65c\ub3d9\uc774 \ud55c\uad6d\uc758 \uc131\u00b7\uc7ac\u000d\u000a\uc0dd\uc0b0 \uc815\uce58\ub97c \ubc14\uafb8\uc5b4\ub0bc \uc218 \uc788\ub2e4\uace0 \ubbff\ub294\ub2e4.\u000d\u000a\uc131 \u00b7 \uc7ac\uc0dd\uc0b0 \uac74\uac15 \uc5f0\uc18d\uae30\ud68d I : \uc784\uc2e0\uc911\uc9c0\ub97c \uc758\ub8cc\ub85c\uc11c \ubcf4\uc7a5\ud558\uae30 \u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR"," & 40\u000d\u000a\ubb38\uc608\ucc2c, \ucd5c\uc591\uc21c / \uc11c\uc6b8\ud2b9\ubcc4\uc2dc50\ud50c\ub7ec\uc2a4\uc7ac\ub2e8, 50+\ub2f9\uc0ac\uc790\uc5f0\uad6c\uacf5\ubaa8\uc0ac\uc5c5, 2021\u000d\u000a\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uac00 \ub290\ub080\u000d\u000a\uc77c\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654\u000d\u000a1. \uc5f0\uad6c\uc758 \ud544\uc694\uc131\u000d\u000a\u2022 50+\uc138\ub300\uc758 \uc77c\uc790\ub9ac \ucc38\uc5ec\ub294 \uc9c0\uc18d\uc801\uc73c\ub85c \uc99d\uac00\u000d\u000a\u00bb \uc11c\uc6b8\uc2dc\ub294 <\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uc7a5\ub144\uce35 \uc778\uc0dd\uc774\ubaa8\uc791 \uc9c0\uc6d0\uc5d0 \uad00\ud55c \uc870\ub840>\ub97c \uc81c\uc815\ud558\uc5ec 50\uc138 \uc774\u000d\u000a\uc0c1 65\uc138 \ubbf8\ub9cc \uc7a5\ub144\uce35\uc758 \uc77c\uc790\ub9ac \ucc38\uc5ec \ub3c5\ub824\u000d\u000a\u00bb \uc11c\uc6b8\uc2dc\ub294 \uc7a5\ub144\uce35\uc744 \u201850+\uc138\ub300\u2019\ub85c \uc9c0\uce6d\ud558\uba70, \uc11c\uc6b8\uc2dc50\ud50c\ub7ec\uc2a4\uc7ac\ub2e8\uc740 \uc704 \uc870\ub840\ub97c \uadfc\uac70\u000d\u000a\ub85c \u201850+\ubcf4\ub78c\uc77c\uc790\ub9ac\uc0ac\uc5c5\u2019\uc744 \ucd94\uc9c4\u000d\u000a\u00bb \uc11c\uc6b8\uc2dc50\ud50c\ub7ec\uc2a4\uc7ac\ub2e8\uc774 \uc6b4\uc601\ud558\ub294 \uc77c\uc790\ub9ac \ucc38\uc5ec\uc790\ub294 2016\ub144 \uc124\ub9bd \uc774\ud6c4 \uc9c0\uc18d\uc801\uc73c\ub85c\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \ub2f9\uc0ac\uc790 \ucc38\uc5ec\uc5f0\uad6c\uc774\ub2e4. \ub2f9\uc0ac\uc790\uac00 \uacf5\ub3d9\uc5f0\uad6c\uc790\ub85c \ucc38\uc5ec\ud558\uc5ec \uc2a4\uc2a4\ub85c \uc77c\uc790\ub9ac \uacbd\ud5d8\uc744 \ud1b5\ud574 \uc77c\u000d\u000a\uc744 \ud568\uc73c\ub85c\uc368 \uc5bb\uc744 \uc218 \uc788\ub294 \uc77c\uc0dd\uc0dd\ud65c\uc758 \ubcc0\ud654\ub97c \uc0b4\ud3b4\ubcf4\uace0, \uacf5\uacf5\uc77c\uc790\ub9ac \ucc38\uc5ec\uac00 50+\uc138\ub300\uc5d0\uac8c \uc904 \uc218 \uc788\u000d\u000a\ub294 \uc758\ubbf8\uc640 \uc774\ub7ec\ud55c \ub178\ub3d9\uc758 \uacbd\ud5d8\uc774 \uac1c\uc778\uc758 \uc0b6\uc5d0 \ubbf8\uce58\ub294 \uc601\ud5a5\uc744 \uc870\uc0ac\ud558\uc600\ub2e4. \uc870\uc0ac\uacfc\uc815\uc5d0\uc11c \uc0ac\uc5c5 \ud604\ud669\u000d\u000a\ubfd0\ub9cc \uc544\ub2c8\ub77c \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc758 \ud6a8\uacfc\uc131\uc81c\uace0\ubc29\uc548\ub3c4 \ud568\uaed8 \uc81c\uc5b8\ud558\uace0 \uc788\ub2e4. \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc774 50+\uc138\u000d\u000a\ub300\uc758 \uc77c\uc790\ub9ac \ucc3d\ucd9c\uc744 \uc704\ud55c \uacbd\uc81c\uc801 \uc9c0\uc6d0\uc774\ub77c\ub294 \ub2e8\ud3b8\uc801\uc778 \uc0ac\uc5c5 \uc131\uacfc\uc5d0\uc11c \ubc97\uc5b4\ub098 \uc77c\uc744 \ud558\ub294 \uacbd\ud5d8\uc744 \ud1b5\u000d\u000a\ud574 \ucc38\uc5ec\uc790 \uc0b6\uc758 \uc804\ubc18\uc774 \ud5a5\uc0c1\ub420 \uc218 \uc788\ub294 \uc9c0\uc6d0\uc758 \ubc29\uc2dd\uc73c\ub85c \ubcc0\ud654\ud574\uc57c \ud568\uc5d0 \ub300\ud55c \ud544\uc694\uc131\uc744 \ud568\uaed8 \uc81c\uc2dc\ud558\u000d\u000a\uace0 \uc788\ub2e4. \uc5f0\uad6c\ubc29\ubc95\uc740 \uc0ac\uc5c5 \ucc38\uc5ec\ub300\uc0c1\uc790 \uc2ec\uce35\uba74\ub2f4\uacfc \ubb38\ud5cc\uc870\uc0ac\ub85c \uc218\ud589\ub418\uc5c8\uc73c\uba70 \uc2ec\uce35\uba74\ub2f4\uc740 2021\ub144 5\u000d\u000a\uc6d4\ubd80\ud130 7\uc6d4\uae4c\uc9c0 \uc9c4\ud589\ub418\uc5c8\ub2e4. \u000d\u000a\uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \ubcf8 \uc5f0\uad6c\uac00 <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \ub77c\ub294 \ucde8\uc9c0\uc5d0 \ub9de\uac8c \ud604\uc7a5\uc744 \uc911\uc2ec\uc5d0 \ub450\uace0 \uc218\ud589\ub418\uc5c8\u000d\u000a\uc74c\uc744 \ubc1d\ud788\uace0\uc790 \ud55c\ub2e4. \ud604\uc7a5 \ucc38\uc5ec\uc790\uac00 \uc2a4\uc2a4\ub85c \uc0ac\uc5c5\uc744 \uacbd\ud5d8\ud558\uace0 \uc9c1\uc811 \uc5f0\uad6c\uc5d0 \ucc38\uc5ec\ud558\uace0 \uc788\uc5b4 \ub354\uc6b1 \uc0dd\uacbd\u000d\u000a\ud55c \uc774\uc288\uc640 \ub300\uc548\uc744 \ub9c8\ub828\ud55c \uc810, \uc774\ub294 \ub2f9\uc0ac\uc790 \uc5f0\uad6c\ub9cc\uc774 \uac00\uc9c8 \uc218 \uc788\ub294 \uac15\uc810\uc774\uba70 \ud604\uc7a5\uc758 \uace0\ubbfc\uc774 \ucda9\ubd84\ud788 \ub2f4\u000d\u000a\uaca8 \uc788\ub2e4\ub294 \uc810\uc5d0\uc11c \uc2dc\uc0ac\ud560 \ub9cc\ud558\ub2e4. \uc774\ub7ec\ud55c \uc5f0\uad6c\uc758 \uae30\ud68d\uc740 \uc911\uac04\uc9c0\uc6d0\uc870\uc9c1\uc774 \ud604\uc7a5\uacfc \uc815\ucc45\uc744 \uc787\ub294 \uac00\uad50\u000d\u000a\ub85c\uc11c \uc5b4\ub5a4 \uc5ed\ud560\uc744 \ud574\uc57c \ud558\ub294 \uc9c0\ub97c \ubcf4\uc5ec\uc900 \uc88b\uc740 \uc0ac\ub840\ub77c\ub294 \uc810\ub3c4 \uace0\ub824\ub418\uc5c8\ub2e4. \ub9c8\uc9c0\ub9c9\uc73c\ub85c \uc5f0\uad6c\uc790 \uad6c\uc131\u000d\u000a\uc774 20\ub300 \ucc45\uc784\uc5f0\uad6c\uc6d0\uacfc 50\ub300 \uacf5\ub3d9\uc5f0\uad6c\uc6d0\uc774 \ud568\uaed8 \ucc38\uc5ec\ud558\uc5ec \uc138\ub300 \uac04 \ub178\ub3d9\uacfc \uc0b6\uc758 \uac00\uce58\ub97c \ub17c\uc758\ud588\ub2e4\ub294\u000d\u000a\uc810\ub3c4 \ud604\uc7ac \uc6b0\ub9ac \uc0ac\ud68c\uc5d0 \ud544\uc694\ud55c \uc2dc\ub3c4\ub85c \ud310\ub2e8\ub418\uc5b4 \ubcf8 \uc5f0\uad6c\ub97c \uc120\uc815\ud558\uc600\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720","40 41\u000d\u000a\uc99d\uac00\ud558\uc600\uace0, 2020\ub144\uc5d0\ub294 \ucd1d 2,027\uba85\uc774 \uc77c\uc790\ub9ac\uc0ac\uc5c5\uc5d0 \ucc38\uc5ec \u000d\u000a\u00bb \uc774 \uc678\uc5d0\ub3c4 \uc559\ucf54\ub974 \uc804\uc9c1\uc9c0\uc6d0 \ud504\ub85c\uadf8\ub7a8 \ucc38\uc5ec\uc790, \ucc3d\uc5c5\ucc3d\uc9c1 \uc9c0\uc6d0 \ud69f\uc218\uac00 \uc9c0\uc18d\uc801\uc73c\ub85c \uc99d\u000d\u000a\uac00\ud558\uace0 \uc788\uc73c\uba70, 2020\ub144\uc5d0 \uac01\uac01 1,059\uba85, 220\uac74\uc744 \uae30\ub85d \u000d\u000a\u2022 \ub2e4\uc591\ud55c \uae30\uad00\uc5d0\uc11c 50+\uc138\ub300 \uc77c\uc790\ub9ac\uc0ac\uc5c5 \uc9c4\ud589\u000d\u000a\u00bb \uc11c\uc6b8\uc8fc\ud0dd\ub3c4\uc2dc\uacf5\uc0ac(\uc774\ud558 SH\uacf5\uc0ac)\ub294 \uc9c0\ub09c 2010\ub144\ubd80\ud130 \uc77c\uc790\ub9ac\ucc3d\ucd9c \uae30\ubcf8\uacc4\ud68d\u00b7\uc138\ubd80\u000d\u000a\uacc4\ud68d\uc744 \uadfc\uac70\ub85c \uc784\ub300\uc8fc\ud0dd \uc785\uc8fc\ubbfc\uc744 \ub300\uc0c1\uc73c\ub85c \uc77c\uc790\ub9ac\uc0ac\uc5c5\uc778 \u2018\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\u2019 \ucd94\uc9c4\u000d\u000a\u00bb \uace0\ub839\uce35 \ucc38\uc5ec\uae30\ud68c \ud655\ub300\uc5d0 \ucd08\uc810\uc744 \ub9de\ucd94\uc5b4 \uc0ac\uc5c5\uc744 \uc9c4\ud589\ud558\uace0 \uc788\uc73c\uba70, 50+\uc138\ub300\uac00 \ud76c\ub9dd\u000d\u000a\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc5d0 \ucc38\uc5ec\u000d\u000a\u00bb SH\uacf5\uc0ac\uc758 \uacbd\uc6b0, \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc744 \uc911\uc2ec\uc73c\ub85c \uc784\ub300\uc8fc\ud0dd \uc785\uc8fc\ubbfc\uc744 \ub300\uc0c1\uc73c\ub85c \ud55c \uacf5\uacf5\u000d\u000a\uc77c\uc790\ub9ac \uc0ac\uc5c5\uc744 \uc9c4\ud589\ud558\uace0 \uc788\uc73c\uba70, \uc784\ub300\uc8fc\ud0dd \uacf5\uae09\ub7c9\uc774 \uc9c0\uc18d\uc801\uc73c\ub85c \ud655\ub300\ub428\uc5d0 \ub530\ub77c \uc77c\u000d\u000a\uc790\ub9ac\uc0ac\uc5c5 \uc5ed\uc2dc \ud655\ub300\ub420 \uac00\ub2a5\uc131 \uc81c\uace0\u000d\u000a\u00bb \uc11c\uc6b8\uc2dc \uacf5\uacf5\uc8fc\ud0dd \uacf5\uae09\uc774 SH\uacf5\uc0ac\ub97c \uc911\uc2ec\uc73c\ub85c \ucd94\uc9c4\ub418\uace0 \uc788\ub2e4\ub294 \uc810\uc5d0\uc11c SH\uacf5\uc0ac \uc784\ub300\u000d\u000a\uc8fc\ud0dd\uacfc \uc774\uc5d0 \ub530\ub978 \uc785\uc8fc\ubbfc \uc77c\uc790\ub9ac\uc0ac\uc5c5\uc774 \uc9c0\uc18d\uc801\uc73c\ub85c \ud655\ub300\ub420 \uac83\uc73c\ub85c \uc804\ub9dd\u000d\u000a2. \uc5f0\uad6c\uc758 \ubaa9\uc801\u000d\u000a\u2022 \ub178\ub3d9\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654 \uce21\uba74\uc5d0\uc11c \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc774 \uc9c0\ub2cc \ud2b9\uc131\uc744 \ub17c\uc758\u000d\u000a\u00bb \uc5f0\uad6c\uc790\ub294 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc744 \uac1c\uc778\uc801\uc73c\ub85c \uacbd\ud5d8\ud588\uc73c\uba70, \ub178\ub3d9\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c \ubcc0\u000d\u000a\ud654\ub97c \uc778\uc2dd\u000d\u000a\u00bb \uc774\ub97c \ud1b5\ud574, \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc774 \uac00\uc9c0\uace0 \uc788\ub294 \ud2b9\uc131\uacfc \ucc38\uc5ec\uc790\ub4e4\uc5d0\uac8c \ubbf8\uce5c \uae0d\uc815\uc801\uc778 \uc601\u000d\u000a\ud5a5\uc744 \uc0b4\ud3b4\ubcf4\uace0, 50+\uc138\ub300\uc758 \uc77c\uc790\ub9ac\uc0ac\uc5c5\uc758 \ud6a8\uacfc\uc131\uc744 \ub192\uc77c \uc218 \uc788\ub294 \ubc29\uc548 \ub3c4\ucd9c\uc5d0 \uae30\uc5ec\u000d\u000a\u2022 \uacf5\uacf5\uc77c\uc790\ub9ac \ucc38\uc5ec\uc790\uac00 \ub290\ub07c\ub294 \uac00\uce58 \ud0d0\uc0c9\uc758 \ud544\uc694\uc131 \uc81c\uae30\u000d\u000a\u00bb \uacf5\uacf5\uc77c\uc790\ub9ac\ub294 \uacbd\uc81c\uc801 \uce21\uba74\uc5d0 \ucd08\uc810\uc744 \ub9de\ucd94\uc5b4 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uc758 \uad00\uc810\uc774 \uc544\ub2cc, \ub178\uc778 \ube48\uace4 \u000d\u000a\ub4f1\uc758 \uc0ac\ud68c\ubb38\uc81c \ud574\uacb0\uc744 \uc704\ud55c \uad00\uc810\uc5d0\uc11c\uc758 \uc811\uadfc\uc774 \uc8fc\ub97c \uc774\ub8e8\uba70, 50+\uc138\ub300\uc758 \uacf5\uacf5\uc77c\uc790\u000d\u000a\ub9ac \ucc38\uc5ec\ub294 \uadf8\ub4e4\uc758 \uc131\ucde8\uac10\uc774\ub098 \uc790\uc544\uc2e4\ud604\uc758 \uce21\uba74\uc5d0\uc11c \ud55c\uacc4 \uc874\uc7ac\u000d\u000a\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uac00 \ub290\ub080 \uc77c\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654"," & 42\u000d\u000a\u00bb 50+\uc138\ub300\uac00 \ucc38\uc5ec\ud558\ub294 \uacf5\uacf5\uc77c\uc790\ub9ac \uc0ac\uc5c5\uc774 \uc9c0\uc18d\uc801\uc73c\ub85c \ud655\ub300\ub418\ub294 \uc0c1\ud669\uc5d0\uc11c, \ud76c\ub9dd\ub3cc\ubcf4\u000d\u000a\ubbf8 \uc0ac\uc5c5\uc744 \uc911\uc2ec\uc73c\ub85c \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uac00 \ub290\ub07c\ub294 \ub178\ub3d9\uc758 \uac00\uce58\ub97c \ubc1c\uacac\ud558\uc5ec \uacf5\uacf5\uc77c\uc790\ub9ac\uac00\u000d\u000a\uc804\ub2ec\ud558\ub294 \ub2e4\uc591\ud55c \uac00\uce58\ub97c \ub17c\uc758\u000d\u000a\u2022 \uacf5\uacf5\uc77c\uc790\ub9ac \ucc38\uc5ec\uc790\ub4e4\uc758 \ub178\ub3d9 \uacbd\ud5d8\uc744 \uccad\ucde8\ud558\uc5ec \uc77c \uacbd\ud5d8\uc758 \uac00\uce58 \ubc1c\uacac\u000d\u000a\u00bb \uc5f0\uad6c\uc790\ub294 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc5d0 \ucc38\uc5ec\ud558\uc5ec \ud658\uacbd\uad00\ub9ac \uc5c5\ubb34\ub97c \uc218\ud589\ud558\uc600\uace0, \uae68\ub057\ud574\uc9c4 \uac70\u000d\u000a\ub9ac\uc640 \uc544\ud30c\ud2b8 \ub2e8\uc9c0\ub97c \ubcf4\uba70 \uc131\ucde8\uac10\uacfc \ubfcc\ub4ef\ud568\uc744 \ub290\ub08c\u000d\u000a\u00bb \uc5f0\uad6c\uc790 \uac1c\uc778\uc758 \uacbd\ud5d8\uc744 \ud1b5\ud574 \ucc38\uc5ec\uc790\ub4e4\uc740 \uacf5\uacf5\uc77c\uc790\ub9ac\ub97c \ud1b5\ud55c \uc790\uc544\uc2e4\ud604, \ub0b4\uc801 \ub9cc\uc871\uac10\u000d\u000a\uc81c\uace0 \ub4f1\uc774 \uc774\ub8e8\uc5b4\uc9c8 \uc218 \uc788\uc74c\uc5d0 \uc8fc\ubaa9, \uacf5\uacf5\uc77c\uc790\ub9ac \ucc38\uc5ec\uc790\ub4e4\uc758 \ub178\ub3d9 \uacbd\ud5d8\uc774 \uac1c\uc778\uc758\u000d\u000a\uc0b6\uc5d0 \ubbf8\uce58\ub294 \uc601\ud5a5\uc744 \ub17c\uc758\ud558\ub294 \uac83\uc5d0 \ub300\ud55c \ud544\uc694\uc131 \uc81c\uae30\u000d\u000a\u00bb \uc2ec\uce35\uba74\uc811\uc744 \ud1b5\ud574 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\ub4e4\uc758 \ub178\ub3d9 \uacbd\ud5d8\uc758 \uac00\uce58\ub97c \ud0d0\uc0c9\ud558\uace0 \ub2e4\ub978\u000d\u000a\uacf5\uacf5\uc77c\uc790\ub9ac \uc0ac\uc5c5\uacfc \uad6c\ubcc4\ub418\ub294 \ud2b9\uc131\uc744 \ub3c4\ucd9c\ud558\uc5ec \uc9c0\uae08\uae4c\uc9c0\uc758 \uacf5\uacf5\uc77c\uc790\ub9ac \uc0ac\uc5c5\uc774 \uacbd\uc81c\u000d\u000a\uc801 \uc9c0\uc6d0\uc744 \uc911\uc2ec\uc73c\ub85c \ubcf5\uc9c0\uc801 \ucc28\uc6d0\uc5d0 \ucd08\uc810\uc744 \ub9de\ucd94\uc5c8\ub2e4\ub294 \ud55c\uacc4\ub97c \uadf9\ubcf5\u000d\u000a3. \uc5f0\uad6c\uc758 \ub0b4\uc6a9\u000d\u000a\u2022 \ucc38\uc5ec\uc790\uc758 \uc77c\uc790\ub9ac \uacbd\ud5d8\uacfc \ucc38\uc5ec\uc790\uac00 \ub290\ub080 \uc77c\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654\ub97c \uc911\uc2ec\uc73c\ub85c\u000d\u000a\uc5f0\uad6c \ub0b4\uc6a9 \uad6c\uc131\u000d\u000a\u00bb \uc5f0\uad6c \uc9c4\ud589\uc744 \ud1b5\ud574 \ucc38\uc5ec\uc790\ub4e4\uc758 \uc774\uc57c\uae30\ub97c \ucd5c\ub300\ud55c \ub9ce\uc774 \ub4e3\uace0 \uac1c\uc778\uc801 \uccb4\ud5d8 \ub17c\uc758\u000d\u000a\u00bb \ucc38\uc5ec\uc790\uc758 \uc77c\uc790\ub9ac \uacbd\ud5d8\uc5d0 \uad00\ud55c \ub2e4\uc591\ud55c \uc774\uc57c\uae30\ub97c \ub4e4\uc73c\uba70, \uc0ac\uc5c5 \ucc38\uc5ec \ub3d9\uae30, \ub290\ub080\uc810 \ub4f1\u000d\u000a\uc744 \ub17c\uc758\u000d\u000a\u00bb \ub610\ud55c \uacbd\uc81c\uc801, \uac1c\uc778\uc801 \uce21\uba74 \ub4f1 \ub2e4\uc591\ud55c \uce21\uba74\uc5d0\uc11c \ub178\ub3d9\uc758 \uac00\uce58\ub97c \ubc1c\uacac, \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\u000d\u000a\uc5c5\uc774 \uc9c0\ub2cc \ud2b9\uc131\uc744 \ub3c4\ucd9c\u000d\u000a\u00bb \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec \uc774\ud6c4, \uc77c\uc0c1\uc0dd\ud65c\uc5d0\uc11c \uacbd\ud5d8\ud55c \ud589\ub3d9\uc758 \ubcc0\ud654, \uc0dd\uac01\uc774\ub098 \uad00\uc810\uc758\u000d\u000a\ubcc0\ud654\ub97c \ubc1c\uacac\u000d\u000a\u00bb 20\ub300\uc758 \uc2dc\uac01\uc5d0\uc11c 50+\uc138\ub300\uc758 \uc77c\uacfc \uc0b6\uc758 \uac00\uce58\ub97c \uad00\ucc30\ud558\uc5ec 20\ub300\uc758 \uad00\uc810\uc744 \ud22c\uc601\ud55c \ubd84\u000d\u000a\uc11d \uc9c4\ud589","42 43\u000d\u000a4. \uc5f0\uad6c\uc758 \ubc29\ubc95\u000d\u000a\u2022 \ubcf8 \uc5f0\uad6c\ub294 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \uc2ec\uce35\uba74\ub2f4\uc744 \uc9c4\ud589\ud558\uc600\uace0, \ubb38\ud5cc\uc870\uc0ac\u000d\u000a\ub3c4 \ud568\uaed8 \uc218\ud589\u000d\u000a\u2022 \uc2ec\uce35\uba74\ub2f4\uc740 \ubc18\uad6c\uc870\ud654\ub41c \uba74\ub2f4\uc744 \ud1b5\ud574 \uc774\ub8e8\uc5b4\uc84c\uc73c\uba70, \uad6c\uc870\ud654\ub41c \uba74\ub2f4\uacfc \ube44\uad6c\uc870\ud654\ub41c \uba74\u000d\u000a\ub2f4\uc758 \uc911\uac04 \uc131\uaca9\uc744 \ub760\ub294 \ubc29\uc2dd\uc73c\ub85c \uba74\ub2f4 \uc9c4\ud589\u000d\u000a\u2022 \uba74\ub2f4 \ub300\uc0c1\uc790 \ucc38\uc5ec \uc120\uc815\uc740 \uc9c0\uc778\uc758 \uc18c\uac1c\uc640 \ucd94\ucc9c\uc744 \ubc1b\uc544 \ub208\ub369\uc774 \ud45c\uc9d1(Snowballing\u000d\u000aSampling) \ubc29\ubc95\uc744 \ucc44\ud0dd\ud558\uc5ec \ud48d\ubd80\ud55c \uc815\ubcf4\ub97c \uac00\uc9c4 \uc0ac\ub78c\uc744 \uc5f0\uad6c \ub300\uc0c1\uc790\ub85c \uc120\uc815\u000d\u000a\u2022 \ubb38\ud5cc\uc870\uc0ac\uc758 \uacbd\uc6b0, SH\uacf5\uc0ac \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \uc790\ub8cc\ub97c \uc911\uc2ec\uc73c\ub85c \ud55c\uad6d\ubcf4\uac74\uc0ac\ud68c\uc5f0\uad6c\uc6d0,\u000d\u000a\uc11c\uc6b8\uc2dc\ubcf5\uc9c0\uc7ac\ub2e8, LH\ud1a0\uc9c0\uc8fc\ud0dd\uc5f0\uad6c\uc6d0 \ub4f1\uc758 \uc790\ub8cc\ub97c \ud65c\uc6a9\u000d\u000a<\uadf8\ub9bc 1> \uc5f0\uad6c\ubd84\uc11d\ud2c0(Framework)\u000d\u000a\uc790\ub8cc \uc77d\uae30/\uc815\ub9ac\u000d\u000a\uac1c\ubc29 \ucf54\ub529: \uc5f0\uad6c \ubb38\uc81c\uc640 \uad00\ub828\ub41c \uc790\ub8cc\uc5d0 \ud45c\uc2dc \ubc0f \uc774\ub984 \ubd80\uc5ec\u000d\u000a\ubc94\uc8fc\ud654: \ucf54\ub529\ub41c \uc790\ub8cc\ub97c \ubc94\uc8fc \ubc0f \ud558\uc704 \uc18d\uc131\uc73c\ub85c \ubd84\ub958\u000d\u000a\ubc94\uc8fc \ud655\uc778: \uad6c\uc131\ub41c \ubc94\uc8fc\ub97c \ucf54\ub529 \uc804 \uc6d0\uc790\ub8cc\uc640 \ube44\uad50\ud558\uba74\uc11c \ubc94\uc8fc \ud655\uc778 \ubc0f \uc218\uc815\u000d\u000a\ubd84\uc11d\uc801 \uba54\ubaa8 \uc4f0\uae30\u000d\u000a\uc804\uc0ac \uc791\uc5c5(transcribing)\u000d\u000a\ubc18\ubcf5\uc801 \ube44\uad50\u000d\u000a\ubc18\ubcf5\uc801 \ube44\uad50\u000d\u000a\uadc0\ub0a9\uc801 \uacfc\uc810\u000d\u000a\uc5f0\uc5ed\uc801 \uacfc\uc815\u000d\u000a1\ucc28~3\ucc28 \ucf54\ub529\u000d\u000a\uc5f0\uad6c \uacb0\uacfc \uc7ac\ud604\u000d\u000a\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uac00 \ub290\ub080 \uc77c\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654"," & 44\u000d\u000a5. \uacb0\ub860\u000d\u000a\u2022 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uc758 \uc77c\uc790\ub9ac \uacbd\ud5d8\uc744 \ub4e4\uc73c\uba70, \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc758 \ud2b9\uc131 \ubc1c\uad74\u000d\u000a\u00bb \uc2ec\uce35\uba74\ub2f4 \uacb0\uacfc, \uc0ac\uc5c5 \ucc38\uc5ec\uc790\ub4e4\uc740 \uacf5\ud1b5\uc801\uc73c\ub85c \ud658\uacbd\uad00\ub9ac \uc5c5\ubb34\uc758 \ud2b9\uc131\uc5d0 \uae30\uc778\ud55c \ubcf4\ub78c\u000d\u000a\uacfc \ubfcc\ub4ef\ud568\uc744 \uacbd\ud5d8\u000d\u000a\u00bb \uccad\uc18c \uc5c5\ubb34\uc758 \ud2b9\uc131\uc0c1, \uccad\uc18c \uc774\ud6c4\uc758 \uae68\ub057\ud574\uc9c4 \ubaa8\uc2b5\uc744 \uac00\uc2dc\uc801\uc774\uace0 \uc989\uac01\uc801\uc73c\ub85c \ud655\uc778\ud560\u000d\u000a\uc218 \uc788\uc73c\uba70, \uc774\ub97c \ud1b5\ud574 \ucc38\uc5ec\uc790\ub4e4\uc740 \ubcf4\ub78c\uc744 \uc9c0\uc18d\uc801\uc73c\ub85c \uacbd\ud5d8\u000d\u000a\u00bb \ud2b9\ud788, \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc774 \uc784\ub300\uc8fc\ud0dd \ub2e8\uc9c0\uc5d0\uc11c \uc774\ub8e8\uc5b4\uc9c0\ub294 \ub178\ub3d9\uc774\ub77c\ub294 \uc810\uc5d0\uc11c, \ub3d9\ub124\u000d\u000a\uc8fc\ubbfc\ub4e4\ub85c\ubd80\ud130 \uce6d\ucc2c\uacfc \uaca9\ub824\ub97c \ubc1b\uc744 \uc218 \uc788\ub294 \ud658\uacbd\uc774 \uc870\uc131\ub418\uc5b4 \uc788\uc73c\uba70 \uc774\uc5d0 \ub530\ub77c \uac1c\u000d\u000a\uc778\uc774 \ub290\ub07c\ub294 \ubcf4\ub78c \uc81c\uace0\u000d\u000a\u2022 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\ub4e4\uc740 \ub2e4\uc591\ud55c \uce21\uba74\uc5d0\uc11c \uc77c\uc0c1\uc0dd\ud65c\uc5d0\uc11c\uc758 \ubcc0\ud654\ub97c \uacbd\ud5d8\u000d\u000a\u00bb \uc0ac\uc5c5 \ucc38\uc5ec\uc790\ub294 \ud658\uacbd\uad00\ub9ac \uc5c5\ubb34\ub97c \ud558\uba70, \uc77c\uc0c1\uc0dd\ud65c\uc5d0\uc11c\uc758 \ud589\ub3d9 \ubcc0\ud654\ub97c \uc778\uc2dd\u000d\u000a\u00bb \uc5c5\ubb34\uc2dc\uac04 \uc774\uc678\uc5d0\ub3c4 \uc4f0\ub808\uae30\ub97c \uc90d\uace0 \uc4f0\ub808\uae30\uc5d0 \uad00\ud55c \uc774\uc57c\uae30\ub97c \uc9c0\uc778\uc5d0\uac8c \uc804\ub2ec\ud558\uba70, \uac00\u000d\u000a\uc871\ub4e4\uc5d0\uac8c \uc4f0\ub808\uae30\ub97c \ubc84\ub9ac\uc9c0 \ub9d0\ub77c\uace0 \uc774\uc57c\uae30\ud558\ub294 \ub4f1 \uc0ac\uc5c5 \uc774\uc804\uacfc\ub294 \ub2e4\ub978 \ud589\ub3d9 \uc591\uc2dd\u000d\u000a\uc744 \ubcf4\uc784\u000d\u000a\u00bb \uad00\uc810 \ubc0f \uc0dd\uac01\uc758 \uacbd\uc6b0, \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc5d0 \ucc38\uc5ec\ud558\uba70 \ub2e4\ub978 \uc77c\uc790\ub9ac\uc0ac\uc5c5 \ucc38\uc5ec\uc5d0 \uad00\ud55c \uad00\u000d\u000a\uc2ec\uacfc \uc790\uc2e0\uac10\uc774 \uc0dd\uae40\u000d\u000a\u00bb \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc740 \ub2e4\ub978 \uc77c\uc790\ub9ac\uc0ac\uc5c5 \uc9c4\uc785\uc744 \uc704\ud55c \ub514\ub524\ub3cc\ub85c \uc791\uc6a9, \uc2a4\uc2a4\ub85c \ub178\ub3d9\uc744 \uc9c0\u000d\u000a\uc18d\ud560 \uc218 \uc788\ub2e4\ub294 \uc0dd\uac01\uc758 \uc804\ud658\uc744 \uc804\ub2ec\u000d\u000a\u2022 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc740 \uae30\uc874 \uc5f0\uad6c\uac00 \ub17c\uc758\ud55c \uc77c\uc790\ub9ac\uc0ac\uc5c5\uacfc \ub2e4\ub978 \ud2b9\uc131\uc774 \uc874\uc7ac, \ucc38\uc5ec\uc790\ub4e4\u000d\u000a\uc740 \ub2e4\ub978 \uc77c\uc790\ub9ac\uc5d0 \ube44\ud574 \ubcf4\ub78c\uc744 \ub354 \ub9ce\uc774 \ub290\ub07c\uace0, \uc77c\uc0c1\uc0dd\ud65c\uc5d0\uc11c\ub3c4 \ub2e4\uc591\ud55c \ubcc0\ud654\ub97c \uccb4\ud5d8","44 45\u000d\u000a6. \uc2dc\uc0ac\uc810\u000d\u000a\u2022 \ubcf8 \uc5f0\uad6c\ub294 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc758 \ud2b9\uc131\uc744 \ud1b5\ud574 50+\uc138\ub300 \uc77c\uc790\ub9ac\uc0ac\uc5c5 \ucc38\uc5ec\uc790\ub4e4\uc774 \uac00\uc871\ub3cc\u000d\u000a\ubd04 \ub4f1\uacfc \uac19\uc740 \uc0b6\uc758 \ub9e5\ub77d \uc18d\uc5d0\uc11c \uc77c\uc790\ub9ac \uc9c4\ud589\uc744 \ubcd1\ud589\ud560 \uc218 \uc788\ub3c4\ub85d \uc81c\ub3c4\uc801 \uae30\ubc18\uc744 \ub9c8\u000d\u000a\ub828\ud574\uc8fc\ub294 \uac83\uc744 \uc81c\uc5b8\u000d\u000a\u00bb \uc6b0\uc120 \uc5c5\ubb34\uc2dc\uac04\uc5d0 \uc788\uc5b4 \ube44\uc804\uc77c\uc81c \uadfc\ubb34\ub85c \uc9c4\ud589\u000d\u000a\u00bb \ub2e4\ub9cc, \uacbd\uc81c\uc801 \ubd80\ubd84\uc5d0 \ub300\ud55c \uc544\uc26c\uc6c0\uc744 \ub098\ud0c0\ub0b8 \ucc38\uc5ec\uc790\ub4e4\uc774 \ub2e4\uc218 \uc788\uc5c8\uae30 \ub54c\ubb38\uc5d0, \uc804\uc77c\u000d\u000a\uc81c\uc640 \ube44\uc804\uc77c\uc81c \uc911\uc5d0\uc11c \uc120\ud0dd\ud560 \uc218 \uc788\ub294 \u2018\uc120\ud0dd\uc801 \uc2dc\uac04\uc81c\u2019\ub85c \uc6b4\uc601\ud558\ub294 \uac83\uc774 \ubc14\ub78c\uc9c1\ud568\u000d\u000a\u00bb \uc5c5\ubb34 \ub0b4\uc6a9\uc5d0 \uc788\uc5b4\uc11c\ub294 \ud658\uacbd\uad00\ub9ac\uc640 \uac19\uc774 \uc804\ud6c4 \ubaa8\uc2b5\uc744 \uc989\uac01\uc801\uc73c\ub85c \ube44\uad50\ud558\uba70 \ub9cc\uc871\uac10\u000d\u000a\uc744 \uc5bb\uc744 \uc218 \uc788\ub294 \uc5c5\ubb34\ub97c \uc801\uc6a9\u000d\u000a\u2022 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc740 \ucc38\uc5ec\uc790\ub4e4\uc758 \uc0b6\uc758 \ub9e5\ub77d \uc18d\uc5d0\uc11c \uc9d1\uc548\uc77c, \uc544\uc774\ub3cc\ubd04 \ub4f1\uacfc \ub178\ub3d9 \ubcd1\ud589\u000d\u000a\uc774 \uac00\ub2a5\ud55c \uc77c\uc790\ub9ac \ubaa8\ub378\ub85c\uc11c \ucc38\uc5ec\uc790\ub4e4\uc758 \ub9cc\uc871\ub3c4\uac00 \uc804\ubc18\uc801\uc73c\ub85c \ub192\uc74c\u000d\u000a\u00bb \ub192\uc740 \ub9cc\uc871\ub3c4\ub294 \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc774 \uc9c0\ub2c8\uace0 \uc788\ub294 \uc5c5\ubb34\uc2dc\uac04, \ub0b4\uc6a9, \uacf5\uac04\uc758 \ud2b9\uc131\uc5d0\uc11c \ube44\u000d\u000a\ub86f\ub418\uc5c8\uc74c\uc744 \uc2ec\uce35\uba74\ub2f4\uc744 \ud1b5\ud574 \ud655\uc778\u000d\u000a\uc77c\uc744 \ud1b5\ud55c \ubcf4\ub78c\u000d\u000a\uc0ac\ub78c\uc744 \ub9cc\ub098\uac8c \ud574\uc8fc\ub294 \uc77c\u000d\u000a\uc0b6\uc758 \ub9e5\ub77d\uc5d0 \uc2a4\uba70\ub4e0 \uc77c\u000d\u000a\ub2e4\ub978 \uacbd\uc81c\ud65c\ub3d9\uacfc\u000d\u000a\ubcd1\ud589\ud560 \uc218 \uc788\ub294 \uc77c\u000d\u000a\uc9d1 \uc55e\uc5d0\uc11c\u000d\u000a\uc774\ub8e8\uc5b4\uc9c0\ub294 \uc77c\u000d\u000a\uac00\uc815 \ub3cc\ubd04\uacfc\u000d\u000a\ubcd1\ud589\ud560 \uc218 \uc788\ub294 \uc77c\u000d\u000a\ub3c8\uc744 \ubc8c \uc218 \uc788\ub294 \uc77c\u000d\u000a<\uadf8\ub9bc 2> \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc744 \ud1b5\ud574 \uacbd\ud5d8\ud55c \uc77c\uc758 \uac00\uce58\u000d\u000a\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5 \ucc38\uc5ec\uc790\uac00 \ub290\ub080 \uc77c\uc758 \uac00\uce58\uc640 \uc77c\uc0c1\uc0dd\ud65c\uc758 \ubcc0\ud654"," & 46\u000d\u000a\u00bb \ud5a5\ud6c4 50+\uc138\ub300 \uc77c\uc790\ub9ac\uc0ac\uc5c5\uc5d0\ub3c4 \uc774\ub7ec\ud55c \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc758 \ud2b9\uc131\uc744 \uc801\uc6a9\ud558\uc5ec \uc0b6\uacfc \ub178\u000d\u000a\ub3d9\uc774 \uacf5\uc874\ud558\ub294 \uc77c\uc790\ub9ac \ubaa8\ub378\uc744 \ubc1c\uad74\u000d\u000a\u00bb \uc774\ub97c \ud1b5\ud574, 50+\uc138\ub300 \uc77c\uc790\ub9ac \ucc38\uc5ec\uc790\ub4e4\uc740 \ub178\ub3d9\uc744 \ud1b5\ud55c \ub9cc\uc871\uac10\uc744 \ub290\ub07c\ub294 \ub3d9\uc2dc\uc5d0, \uc77c\uc0c1\u000d\u000a\uc5d0\uc11c \ub204\ub824\uc654\ub358 \uae30\uc068\ub3c4 \ud568\uaed8 \ub290\ub084 \uc218 \uc788\uc744 \uac83\uc73c\ub85c \uae30\ub300\u000d\u000a\u2022 \ubcf8 \uc5f0\uad6c\ub294 \uc11c\uc6b8\uc2dc50\ud50c\ub7ec\uc2a4\uc7ac\ub2e8 \uc77c\uc790\ub9ac\uc0ac\uc5c5\uacfc\uc758 \uc5f0\uacc4\ub97c \ud1b5\ud55c \uc0c8\ub85c\uc6b4 \uc77c\uc790\ub9ac \ubaa8\ub378\u000d\u000a\ub85c \uc81c\uc5b8\u000d\u000a<\uadf8\ub9bc 3> \ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc758 \ud2b9\uc131\uacfc \ucc38\uc5ec\uc790\ub4e4\uc5d0\uac8c \ubbf8\uce5c \uc601\ud5a5\u000d\u000a\ud658\uacbd\uad00\ub9ac\u000d\u000a\"\ub2e4\ub978 \uc77c\uc790\ub9ac\uc5d0 \ube44\ud574\u000d\u000a\ub354 \ub9ce\uc740 \ub9cc\uc871\uac10\"\u000d\u000a\u201c\uc804\ubb38\uc131 \ud655\ubcf4\uc640\u000d\u000a\uc77c\uc0c1\uc0dd\ud65c\uc5d0\uc758 \uc801\uc6a9\"\u000d\u000a\uc8fc 3~4\uc77c \uadfc\ubb34\u000d\u000a\ube44\uc804\uc77c\uc81c \uadfc\ubb34\u000d\u000a\u3008\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\uc758 \ud2b9\uc131>\u000d\u000a\u3008\ucc38\uac00\uc790\ub4e4\uc5d0 \ubbf8\uce5c \uc601\ud5a5>\u000d\u000a\"\uc9d1\uc548\uc77c, \uac00\uc871\ub3cc\ubd04,\u000d\u000a\ub2e4\ub978 \uc77c\uc790\ub9ac\uc640 \ubcd1\ud589\"\u000d\u000a\u201c\uac1c\uc778 \uc2dc\uac04 \ud655\ubcf4\"\u000d\u000a\uc8fc\uac70\uacf5\uac04\uacfc\u000d\u000a\uac00\ub07c\uc6c0\u000d\u000a\"\ucd9c\ud1f4\uadfc \uc2dc\uac04\uc774 \uc9e7\uc544 \uc9d1\uc548\uc77c,\u000d\u000a\uac00\uc871 \ub3cc\ubd04\uc5d0 \ub354 \ub9ce\uc740 \uc2dc\uac04 \ud22c\uc790\"\u000d\u000a\"\uc774\uc6c3\uacfc\uc758 \ub124\ud2b8\uc6cc\ud06c \ud615\uc131\"\u000d\u000a\ud76c\ub9dd\ub3cc\ubcf4\ubbf8 \uc0ac\uc5c5\u000d\u000a\uc5c5\ubb34\uc2dc\uac04 \uc5c5\ubb34\ub0b4\uc6a9 \uc5c5\ubb34\uacf5\uac04\u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","46 47\u000d\u000a\ub958\ud55c\uc2b9 / \uc11c\uc6b8\uc2dcNPO\uc9c0\uc6d0\uc13c\ud130, \ud65c\ub825\ud5a5\uc5f0, 2019\u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \u000d\u000a\uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c \u000d\u000a1. \ubb38\uc81c\uc758\uc2dd\u000d\u000a\ucd5c\uadfc \uc5ec\uc758\ub3c4\uc5c5\ubb34\uc9c0\uad6cYBD\uc5d0\uc11c \uc77c\ud558\ub294 \uccad\uc18c\ub178\ub3d9\uc790\ub4e4\uc5d0 \ub300\ud55c \uc2e4\ud0dc\uc870\uc0ac\ub97c \uc218\ud589\ud558\uac8c \ub418\uc5c8\u000d\u000a\ub2e4. \uac70\uc758\uac00 \uc911\uace0\ub839 \ube44\uc815\uaddc\uc9c1 \uc5ec\uc131\ub178\ub3d9\uc790\uc774\uace0 \ud558\ub8e8\uc758 \uc808\ubc18 \uac00\uae4c\uc774\ub97c \uc0ac\uc5c5\uc7a5\uc5d0 \ubb36\uc5ec \uc788\uc73c\uba70 \u000d\u000a\uc2ec\uc9c0\uc5b4 \uadfc\ub85c\uacc4\uc57d\uc11c\uc5d0 \uc815\ud574\uc9c4 \uac83\ubcf4\ub2e4 1\uc2dc\uac04\uc529 \u2018\uc790\ubc1c\uc801\uc73c\ub85c\u2019 \uc77c\ucc0d \ucd9c\uadfc\ud574\uc11c \uc77c\ud558\uace0 \uc788\ub294\ub370 \u000d\u000a\uae30\ubcf8\uae09\uc740 \ud3c9\uade0\ud574\uc11c 175\ub9cc\uc6d0\uc5d0 \ubd88\uacfc\ud558\ub2e4. \ucd5c\uc800\uc784\uae08\uc744 \ubc1b\uace0 \uc788\uc9c0\ub9cc \uacc4\uc57d\uc11c\uc5d0 \uc801\ud78c \ub178\ub3d9\uc2dc\u000d\u000a\uac04\uc740 5.5~7\uc2dc\uac04\uc774\uae30 \ub54c\ubb38\uc774\ub2e4. \ud734\uac8c\uc2dc\uac04\ub3c4 \ud734\uac00\ub3c4 \ub9d8\ub300\ub85c \uc4f8 \uc218 \uc5c6\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \uc77c\uc790\ub9ac\uc5d0\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \ud55c\uad6d\uc5d0\uc11c \ubc8c\uc5b4\uc9c4 \ub178\uc870\ud30c\uad34\uc758 \ub2e4\uc591\ud55c \uc0ac\ub840\ub4e4\uc744 \uc815\ub9ac\ud558\uace0 \ubd84\uc11d\ud558\uc5ec, \uadf8 \uc6d0\uc778\uacfc \uacfc\uc815\uc744\u000d\u000a\uc2ec\uce35\uc801\uc73c\ub85c \ubd84\uc11d\ud558\uace0 \uc124\uba85\ud55c \uc5f0\uad6c\uc774\ub2e4. 1987\ub144 \ub178\ub3d9\uc790 \ub300\ud22c\uc7c1 \uc774\ud6c4 \ub178\ub3d9\uad8c\uc774 \uae09\uaca9\ud788 \uc2e0\uc7a5\ub418\uc5c8\ub2e4\u000d\u000a\ub294 \uc0ac\ud68c \uc77c\uac01(\ud2b9\ud788 \uc790\ubcf8 \uc9c4\uc601\uacfc \uc8fc\ub958 \ubcf4\uc218 \uc5b8\ub860)\uc758 \uacfc\uc7a5\ub41c \uc8fc\uc7a5\uacfc \ub2ec\ub9ac \ub178\ub3d9\uc6b4\ub3d9\uc758 \uc2dc\ubbfc\uad8c\uc774 \uc5ec\uc804\u000d\u000a\ud788 \ucda9\ubd84\ud788 \ubcf4\uc7a5\ub418\uc9c0 \uc54a\ub294 \ud55c\uad6d\uc758 \ud604\uc2e4\uc744 \uac10\uc548\ud558\uba74, \uae30\uc5c5\ub4e4\uc758 \ub178\uc870\ud30c\uad34 \ud589\ud0dc\ub97c \ubd84\uc11d\ud55c \uc774 \uc5f0\uad6c\uc758 \uc2e4\u000d\u000a\ucc9c\uc801 \uc758\ubbf8\ub294 \ub9e4\uc6b0 \ud06c\ub2e4. \ub178\uc870\ud30c\uad34 \uacfc\uc815\uc5d0 \uac1c\uc785\ud55c \ub2e4\uc591\ud55c \ud589\uc704\uc790\ub4e4\uacfc \uadf8\ub4e4\uc758 \uc804\ub7b5, \uadf8\ub9ac\uace0 \uc774 \uacfc\uc815\u000d\u000a\uc5d0\uc11c \ub178\ub3d9\uc790\ub4e4\uc758 \ub300\uc751 \ub4f1\uc744 \uc798 \uc815\ub9ac\ud558\uc5ec \ud5a5\ud6c4 \ub178\uc870\ud30c\uad34\uc5d0 \uc5b4\ub5bb\uac8c \ub300\uc751\ud560 \uac83\uc778\uc9c0 \ud0d0\uad6c\ud558\ub294\ub370 \ud070 \uc2dc\u000d\u000a\uc0ac\uc810\uc744 \ub358\uc838\uc900\ub2e4. \u000d\u000a\ub610\ud55c \ub178\uc870\ud30c\uad34 \uc0ac\ub840\ub4e4\uc5d0 \ub300\ud55c \uc790\ub8cc\uc640 \uc815\ubcf4\ub4e4\uc744 \uccb4\uacc4\uc801\uc73c\ub85c \uc815\ub9ac\ud558\uace0 \ubd84\uc11d\ud55c \uc5f0\uad6c\uac00 \ubd80\uc7ac\ud55c \uc0c1\ud669\u000d\u000a\uc5d0\uc11c \uc774 \uc5f0\uad6c\ub294 \uadf8 \ubd80\uc871\ud55c \ubd80\ubd84\uc744 \ucc44\uc6b0\ub294 \uc120\ub3c4\uc801 \uc5ed\ud560\uc744 \ucda9\uc2e4\ud788 \uc218\ud589\ud558\uace0 \uc788\ub2e4. \ub178\ub3d9\uc870\ud569\uc758 \ubc31\uc11c, \ud1a0\u000d\u000a\ub860\ud68c, \uc99d\uc5b8\ub300\ud68c \ub4f1 \uc790\ub8cc\uc9d1, \ubcf4\ub3c4\uc790\ub8cc, \uc0ac\uce21\uc758 \uc790\ub8cc, \ubc95\uc815 \uae30\ub85d, \uc5b8\ub860 \ubcf4\ub3c4 \ub4f1 \ub9e4\uc6b0 \ub2e4\uc591\ud55c \ubb38\ud5cc \uc790\ub8cc\u000d\u000a\ub97c \uad11\ubc94\uc704\ud558\uac8c \ud65c\uc6a9\ud558\uc5ec \ub370\uc774\ud130\uc640 \uc815\ubcf4\ub97c \uc218\uc9d1\ud558\uc600\uace0, \uc774\ub97c \ubc14\ud0d5\uc73c\ub85c 6\uac1c\uc758 \ub300\ud45c\uc801 \ub178\uc870\ud30c\uad34 \uc0ac\ub840\u000d\u000a\ub97c \uc120\ud0dd\ud558\uc5ec \uc2ec\uce35 \uc5f0\uad6c\ud558\uc600\ub2e4. \ud604\uc7a5\uc131\uc774 \ud48d\ubd80\ud55c \uc790\ub8cc\ub4e4\uc744 \ubc14\ud0d5\uc73c\ub85c \ub178\uc870\ud30c\uad34\uac00 \uc790\ud589\ub418\ub294 \ub9e4\uc6b0 \uad6c\uccb4\u000d\u000a\uc801\uc774\uace0 \ub2e4\uc591\ud55c \ubc29\uc2dd\uacfc \uacfc\uc815\ub4e4\uc5d0 \ub300\ud574 \ub9e4\uc6b0 \uae4a\uc774\uc788\uac8c \ubd84\uc11d\ud558\uc600\uace0, \ub354\ubd88\uc5b4 \ub178\uc870\ud30c\uad34\uac00 \ubc8c\uc5b4\uc9c0\ub294 \ud604\uc7a5\u000d\u000a\uc758 \ubcf5\uc7a1\ud55c \uad00\uacc4\uc640 \uac08\ub4f1\uc758 \ub258\uc559\uc2a4\ub97c \uc798 \uc804\ub2ec\ud558\uace0 \uc788\ub2e4. \u000d\u000a\ubcf8 \uc5f0\uad6c\uc758 \uc774\ub7ec\ud55c \ubb38\uc81c\uc758\uc2dd\uacfc \uc811\uadfc\ubc95\uc740 \ub178\ub3d9\uc870\ud569 \ud604\uc7a5\uc5d0\uc11c\uc758 \uc0dd\uc0dd\ud55c \uacbd\ud5d8\uc5d0 \uae30\ubc18\ud55c \uc2e4\ucc9c\uc801 \ubb38\uc81c\u000d\u000a\uc758\uc2dd\uc774 \uc5c6\uc73c\uba74 \ubd88\uac00\ub2a5\ud588\uc73c\ub9ac\ub77c \uc0dd\uac01\ub418\uc5b4 \ud604\uc7a5 \uc5f0\uad6c\uc758 \uc88b\uc740 \ubaa8\ubc94\uc774\ub77c \uc0dd\uac01\ub41c\ub2e4. \uc774 \uc5f0\uad6c\ub294 \ud5a5\ud6c4 \ud55c\uad6d\u000d\u000a\uc758 \ub178\uc0ac\uad00\uacc4, \ubc18\ub178\uc870\uc815\uc11c \ub4f1\uc5d0 \ub300\ud55c \ubcf4\ub2e4 \ud655\uc7a5\ub41c \uc5f0\uad6c\ub97c \uc704\ud55c \uc911\uc694\ud55c \ucd9c\ubc1c\uc810\uc774 \ub420 \uac83\uc774\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720\u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 48\u000d\u000a\uc11c \u2018\uc798\ub9b4\uae4c\ubd10\u2019 \ub9d0 \ud55c\ub9c8\ub514 \uaebc\ub0b4\uc9c0 \ubabb\ud55c\ub2e4.\u000d\u000a\uc774 \ub178\ub3d9\uc790\ub4e4\uc774 \uc9c0\uae08\uc758 \ud0c8\ubc95\uc801\uc778 \ubb34\uad8c\ub9ac\uc0c1\ud0dc\ub97c \ubc97\uc5b4\ub098\ub824\uba74 \ubb34\uc5c7\uc774 \ud544\uc694\ud55c\uac00? \uc9c0\uae08\uae4c\uc9c0 \u000d\u000a\uc54c\ub824\uc9c4 \uac70\uc758 \uc720\uc77c\ud55c \ub300\uc548\uc740 \ubc14\ub85c \ub178\ub3d9\uc870\ud569\uc774\ub2e4. \uc5ec\uc758\ub3c4\uc5d0\ub3c4 \uba87\uba87 \uc0ac\uc5c5\uc7a5\uc5d0\ub294 \uccad\uc18c\ub178\ub3d9\uc790\u000d\u000a\ub4e4\uc758 \ub178\ub3d9\uc870\ud569\uc774 \ub9cc\ub4e4\uc5b4\uc84c\ub2e4. \uc774 \uc0ac\uc5c5\uc7a5\ub4e4\uc744 \ubcf4\uba74 \ub178\ub3d9\uc790\ub4e4\uc774 \ub2e8\uacb0\ud574\uc11c \uc0ac\uce21\uc5d0\uac8c \ub300\ud56d\ub825\u000d\u000a\uc744 \uac00\uc9c0\uace0 \ub178\ub3d9\ubc95\uc774 \uadf8 \ucde8\uc9c0\ub300\ub85c \uc801\uc6a9\ub418\ub3c4\ub85d \ud558\ub294 \uac83\ub9cc\uc73c\ub85c\ub3c4 \uc0c1\ub2f9\ud55c \ub178\ub3d9\uc870\uac74\uc758 \uac1c\uc120\ud6a8\u000d\u000a\uacfc\ub97c \uac70\ub458 \uc218 \uc788\ub2e4\ub294 \uac83\uc744 \uc54c \uc218 \uc788\ub2e4. \u000d\u000a\uadf8\ub7ec\ub098 \uac04\uc811\uace0\uc6a9 \uccad\uc18c\ub178\ub3d9\uc790\ub4e4\uc774 \ub178\ub3d9\uc870\ud569\uc744 \ub9cc\ub098\uae30\ub294 \uc815\ub9d0\ub85c \uc5b4\ub824\uc6b4 \uc77c\uc774\ub2e4. \ubc14\ub85c \u000d\u000a2021\ub144\uc5d0\ub3c4 \ub178\uc870\uc5d0 \uac00\uc785\ud55c LG\ud2b8\uc708\ud0c0\uc6cc \uccad\uc18c\ub178\ub3d9\uc790\ub4e4\uc774 \uc9d1\ub2e8\ud574\uace0\ub418\ub294 \uc0ac\uac74\uc774 \uc788\uc5c8\ub2e4. \uc5ec\u000d\u000a\uc758\ub3c4\uc5d0 \uc788\ub294 \ub300\ubd80\ubd84\uc758 \ub300\uae30\uc5c5\uacfc \uacf5\uacf5\ubd80\ubb38\uc5d0 \ub178\ub3d9\uc870\ud569\uc774 \ub4e4\uc5b4\uc120 \uac83\uacfc \ub300\uc870\uc801\uc73c\ub85c, \uc5ec\uc804\ud788 \u000d\u000a\ube44\uc815\uaddc\uc9c1\uc774\ub098 \uc601\uc138\uc0ac\uc5c5\uc7a5\uc758 \ub178\uc870 \uc870\uc9c1\ub960\uc740 \ubd88\uacfc 2% \uc218\uc900\uc778\ub370 \uc560\uc368 \ub9cc\ub4e0 \ub178\uc870\uc758 \uc0dd\uc874\ub960\u000d\u000a\uc740 \uc5bc\ub9c8\ub098 \ub420\uae4c. \uc0ac\uc6a9\uc790\uc758 \ubd80\ub2f9\ub178\ub3d9\ud589\uc704\ub098 \ub178\uc870\ud30c\uad34 \uc0ac\uac74\uc740 \ub04a\uc784\uc5c6\uc774 \uc5b8\ub860\uc9c0\uba74\uc5d0 \uc624\ub974\ub0b4\u000d\u000a\ub9b0\ub2e4.\u000d\u000a\uc774\ub978\ubc14 87\ub144 \uccb4\uc81c\uac00 \uc131\ub9bd\ud558\uace0 \uc218\ud3c9\uc801 \uc815\uad8c\uad50\uccb4\uac00 \uc774\ub8e8\uc5b4\uc9c0\ub294 \ub4f1 \ubbfc\uc8fc\uc8fc\uc758\uac00 \uafb8\uc900\ud788 \uc9c4\u000d\u000a\uc804\ub418\uc5c8\ub2e4\uace0 \ud55c\ub2e4. \uadf8\ub7f0\ub370 \uc5b4\uc9f8\uc11c \uc815\ubd80\uc758 \ub178\ub3d9 \ubc30\uc81c\uc801 \uc815\ucc45\uc774\ub098 \uc0ac\uc6a9\uc790\uc758 \ub54c\ub85c \uc720\ud608\uc801 \ud3ed\ub825\u000d\u000a\uc744 \uc218\ubc18\ud558\ub294 \uc801\ub300\uc801 \ub178\uc870\ud30c\uad34 \uc2dc\ub3c4\uac00 \ubcf8\uc9c8\uc801 \ubcc0\ud654 \uc5c6\uc774 \uc9c0\uc18d\ud558\uace0 \uc788\ub294\uac00? \uc2ec\uc9c0\uc5b4 \uc815-\uacbd-\uad00 \u000d\u000a\uc720\ucc29\uc774 \uc5c6\uc774\ub294 \ubd88\uac00\ub2a5\ud55c \uc18c\uc704 \u2018\ubb34\ub178\uc870\u2019 \uacbd\uc601\uc774 \uc0c1\ub2f9\uc218 \uc7ac\ubc8c\u00b7\ub300\uae30\uc5c5\uc5d0\uc11c \ud3ed\ub825\uc801 \ubc29\ubc95\uc744 \uc218\u000d\u000a\ubc18\ud558\uc5ec \uad00\ucca0\ub418\uace0 \uc788\uc73c\uba70 \uc0c1\ub2f9\uc218 \uc5c5\uc885\uc5d0\uc11c\ub294 \ub178\uc870 \ud65c\ub3d9\uc744 \uc774\uc720\ub85c \ud55c \ud45c\uc801 \ud574\uace0\uc640 \ube14\ub799\ub9ac\uc2a4\u000d\u000a\ud2b8\uac00 \uad00\ucc30\ub418\uace0 \uc788\ub294\uac00?\u000d\u000a\uc774 \uc9c8\ubb38\uc5d0 \ub2f5\ud558\uae30 \uc704\ud574\uc11c\ub294 \uc9c0\uae08\uae4c\uc9c0 \uc5f0\uad6c \uc790\uccb4\uac00 \uac70\uc758 \uc774\ub8e8\uc5b4\uc9c0\uc9c0 \uc54a\uc558\ub358 \ub178\uc870\ud30c\uad34\uc5d0 \u000d\u000a\ub300\ud55c \uc0ac\ub840 \ubd84\uc11d\uacfc \uc720\ud615 \uc815\ub9ac, \uc774\ub97c \ud1b5\ud55c \uc6d0\uc778 \uaddc\uba85\uc774 \ud544\uc694\ud560 \uac83\uc774\ub2e4. \ubd80\uc871\ud558\ub098\ub9c8 \uadf8\uac04 \ub098\uc654\u000d\u000a\ub358 \uad00\ub828 \uc790\ub8cc\uc640 \ubb38\ud5cc\uc744 \uc815\ub9ac\ud558\uace0 \uae30\ubcf8\uc801\uc778 \uc2dc\uae30\uad6c\ubd84\uc744 \ud558\uace0\uc790 \uc2dc\ub3c4\ud558\uc600\uace0 \uadf8 \uacb0\uacfc\uac00 2019\u000d\u000a\ub144\uc5d0 \uc11c\uc220\ud55c \ubcf4\uace0\uc11c\uc774\ub2e4. \u000d\u000a2. \uc8fc\uc694\ub0b4\uc6a9\u000d\u000a1) \ub178\uc870\ud30c\uad34 \uc0ac\ub840\uc640 \uc720\ud615\u000d\u000a\ub178\uc870\ud30c\uad34\uc758 \uc0ac\ub840\ub294 \ub9e4\uc6b0 \ub9ce\uc9c0\ub9cc \uadf8\uc911 \ub178\uc870\ud30c\uad34\uc758 \uc720\ud615\uacfc \uc790\ub8cc\uc870\uc0ac\uc758 \ud3b8\uc758\uc131\uc744 \uace0\ub824\ud588","48 49\u000d\u000a\ub2e4. \uace0\uc6a9\ub178\ub3d9\ud589\uc815\uac1c\ud601\uc704\uc6d0\ud68c\uc5d0\uc11c \ub2e4\ub8ec 11\uac1c \uc0ac\uc5c5\uc7a5\uc744 \uc6b0\uc120\uc801\uc73c\ub85c \uace0\ub824\ud588\uace0, \uacf5\uacf5\uae30\uad00, \uc7ac\u000d\u000a\ubc8c \ubb34\ub178\uc870 \uacbd\uc601, \ube44\uc815\uaddc\uc9c1\uc758 \uacbd\uc6b0, \uae30\uc5c5\ubcc4 \ubcf5\uc218\ub178\uc870 \ucc3d\uad6c\ub2e8\uc77c\ud654\uc81c\ub3c4 \uc774\ud6c4 \uc774\ub97c \ud65c\uc6a9\ud55c \uacbd\u000d\u000a\uc6b0\ub97c \uace0\ub824\ud558\uc5ec <\ud45c1>\uc5d0 \uc815\ub9ac\ud55c \uac83\uacfc \uac19\uc774 6\uac1c \uc0ac\uc5c5\uc7a5 \uc0ac\ub840\ub97c \uace8\ub77c\uc11c \uc11c\uc220\ud558\uc600\ub2e4. \u000d\u000a\u2460 \uc870\ud3d0\uacf5\uc0ac\u000d\u000a\uc870\ud3d0\uacf5\uc0ac \ud30c\uc5c5\uc720\ub3c4 \uc0ac\uac74\uc740 \uad6d\uac00\uad8c\ub825\uc774 \ub178\ub3d9\uc870\ud569 \ud30c\uad34\uc5d0 \uc9c1\uc811\uc801\uc73c\ub85c \uac1c\uc785\ud588\ub2e4\ub294 \uc0ac\uc2e4\uc774 \u000d\u000a\ub4dc\ubb3c\uac8c \ud3ed\ub85c\ub41c \uc0ac\ub840\ub2e4. \uadf8\uac83\ub3c4 \ub300\uac80\ucc30\uccad \uacf5\uc548\ubd80\uc7a5\uc758 \uc785\uc73c\ub85c \uc790\ub791\uc2a4\ub7fd\uac8c \ubc1c\uc124\ud588\ub2e4\ub294 \uc810\uc5d0\u000d\u000a\uc11c \uac80\ucc30, \ud2b9\ud788 \uacf5\uc548\ubd80\uc758 \ubc18\ub178\uc870\uc8fc\uc758anti-unionism\ub97c \uadf9\uba85\ud558\uac8c \ubcf4\uc5ec\uc92c\ub2e4.\u000d\u000a\uacf5\uc5482\uacfc\uac00 \uc791\uc131\ud55c <\uacf5\uae30\uc5c5\uad6c\uc870\uc870\uc815 \uacfc\uc815\uc0c1\uc758 \ubb38\uc81c\uc810 \ubc0f \ub300\ucc45>\uc774\ub77c\ub294 \ubb38\uc11c\uc5d0 \ub530\ub974\uba74 \u201c(\uc870\u000d\u000a\ud3d0)\uacf5\uc0ac \ucabd\uc758 \ub300\ud3ed \uc591\ubcf4\ub85c \ud569\uc758 \ud0c0\uacb0\ub420 \uacbd\uc6b0 \ud5a5\ud6c4 \ud0c0\uae30\uc5c5\uc758 \uad6c\uc870\uc870\uc815\uc5d0 \uc545\uc601\ud5a5\u201d\uc744 \uc904 \uc218 \uc788\u000d\u000a\uae30 \ub54c\ubb38\uc5d0 \u201c\uad6c\uc870\uc870\uc815 \uacc4\ud68d(\uc744) \uc81c\uc2dc \uc720\ub3c4\u201d\ud558\uc5ec \ub178\uc870\uc758 \ud30c\uc5c5\uc744 \uc758\ub3c4\uc801\uc73c\ub85c \uc720\ub3c4\ud588\ub2e4\ub294 \uac83\uc774\u000d\u000a\ub2e4. \uc2e4\uc81c\ub85c \uc870\ud3d0\ucc3d \ud1b5\ud3d0\ud569 \uac15\ud589\uc740 \uc608\uc0c1\ub300\ub85c \ub178\uc870\uc758 \uaca9\ub82c\ud55c \uc800\ud56d\uc744 \ubd88\ub7ec\uc654\uc73c\uba70 \uacbd\ucc30 \ubcd1\ub825\uc744 \u000d\u000a\ud22c\uc785, \uc800\ud56d\ud558\ub294 \ub178\ub3d9\uc790\ub4e4\uc744 \ubb3c\ub9ac\ub825\uc73c\ub85c \uc81c\uc555\ud558\uc600\ub2e4. \ub178\uc870\uc704\uc6d0\uc7a5 \ub4f1 \ub178\uc870\uac04\ubd80 7\uba85\uc774 \uad6c\uc18d\ub418\u000d\u000a\uad6c\ubd84 \uc120\uc815 \uc0ac\ub840 \uc8fc\uc694 \ud2b9\uc9d5\u000d\u000a\uacf5\uacf5\uae30\uad00 \ub178\uc870\ud30c\uad34 \ud55c\uad6d\uc870\ud3d0\uacf5\uc0ac\u000d\u000a\u2022 \uc815\ubd80\uac00 \uc2e4\uc9c8\uc801 \uc0ac\uc6a9\uc790\ub85c\uc11c \ub178\uc870\ud30c\uad34 \uc8fc\uccb4\u000d\u000a\u2022 \uc815\ubd80 \uc815\ucc45, \uc9d1\uad8c\uc790 \uc758\uc911\uc5d0 \ub530\ub77c \uae34\ubc00\ud55c \uc601\ud5a5\uc744 \uc8fc\u000d\u000a\uace0\ubc1b\uc74c\u000d\u000a\ubb34\ub178\uc870\uacbd\uc601 \ubc29\uce68\u000d\u000a\uad00\ucca0\uc744 \uc704\ud55c \ub178\uc870\ud30c\uad34\u000d\u000a\uc0bc\uc131 \uc5d0\ubc84\ub79c\ub4dc\u000d\u000a\uc0bc\uc131\uc804\uc790\uc11c\ube44\uc2a4\u000d\u000a\u2022 \ubc18\ud5cc\ubc95\uc801 \uac00\uce58\ub97c \ub178\uace8\uc801\uc73c\ub85c \ud45c\ubc29\ud568\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0\u000d\u000a\uc5b4\ub5a0\ud55c \ubc95\uc801 \uc81c\uc7ac\uc5c6\uc774 \ubb34\ub178\uc870 \uad00\ucca0\u000d\u000a\u2022 \uc790\uccb4 \ub178\ubb34\uad00\ub9ac \uae30\uad6c\uac00 \ub178\uc870\ud30c\uad34 \uae30\ud68d, \uc9d1\ud589\u000d\u000a\u2022 \uad6d\uac00\uae30\uad6c\uc640\uc758 \uc720\ucc29\u000d\u000a\ube44\uc815\uaddc\uc9c1 \ub178\uc870 \uacb0\uc131\u000d\u000a\uc2dc\ub3c4\uc5d0 \ub530\ub978 \ub178\uc870\ud30c\uad34 \uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0\u000d\u000a\u2022 \ube44\uc815\uaddc\uc9c1\uc758 \ubc95\uc81c\ub3c4\uc801 \ucde8\uc57d\uc131, \uace0\uc6a9\ubd88\uc548\uc815\u000d\u000a\u2022 \uae30\uac04\uc81c, \uac04\uc811\uace0\uc6a9, \ud2b9\uc218\uace0\uc6a9 \ub4f1 \ud615\ud0dc\uc5d0 \ub530\ub77c \ub2e4\ub978\u000d\u000a\uc591\uc0c1\uc73c\ub85c \ub098\ud0c0\ub0a8\u000d\u000a\u2022 \uc815\uaddc\uc9c1-\ube44\uc815\uaddc\uc9c1\uac04 \uac08\ub4f1\uad6c\uc870\uc758 \uc774\uc6d0\ud654\u000d\u000a\ubcf5\uc218\ub178\uc870 \uc2dc\ud589 \uc774\ud6c4\u000d\u000a\uc81c\uc870\uc5c5 \ub178\uc870\ud30c\uad34\u000d\u000a\ubc1c\ub808\uc624\uc804\uc7a5\uc2dc\uc2a4\ud15c\u000d\u000a\uc720\uc131\uae30\uc5c5\u000d\u000a\u2022 \uc81c\ub3c4\uc801 \ubcc0\ud654\ub97c \ud65c\uc6a9\ud55c \uacf5\uaca9\uc801 \ub178\uc870\ud30c\uad34 \uc2dc\ub3c4\u000d\u000a\u2022 \uc678\ubd80 \ub178\uc870\ud30c\uad34 \uae30\ud68d\uc804\ubb38\uac00 \ud65c\uc6a9\u000d\u000a\u2022 \uc81c\uc870\uc5c5 \uc6d0\uccad\uc0ac\uc640\uc758 \uacf5\ubaa8 \uad00\uacc4, \uad6d\uac00\uae30\uad6c\uc640\uc758 \uc720\ucc29\u000d\u000a<\ud45c 1> 2000\ub144\ub300 \uc774\ud6c4 \ub178\uc870\ud30c\uad34\uc758 \uc720\ud615\u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 50\u000d\u000a\uace0 \ud30c\uc5c5\uac00\ub2f4\uc790 7\ubc3130\uc5ec\uba85\uc5d0\uac8c \ud30c\uba74, \uc9c1\uc704\ud574\uc81c, \uc815\uc9c1, \uac10\ubd09, \uacbd\uace0 \ub4f1 \uc9d5\uacc4\uc870\uce58\uac00 \ub4a4\ub530\ub974\uba74\uc11c \u000d\u000a\uc870\ud3d0\uacf5\uc0ac\ub178\uc870\ub294 \uc640\ud574\ub418\ub2e4\uc2dc\ud53c \ud558\uc600\ub2e4. \u000d\u000a\uc774 \uc0ac\uac74\uc5d0\uc11c \ub4f1\uc7a5\ud55c \uacf5\uc548\ub300\ucc45\ud611\uc758\ud68c\ub294 \uac80\ucc30\uc774 \uad00\uacc4\uae30\uad00\uc744 \u2018\uc9c0\ud718\u2019\ud558\uc5ec \ub178\ub3d9\ubb38\uc81c\uc5d0 \uac1c\uc785\u000d\u000a\ud558\ub294 \uae30\uad6c\ub85c \uadf8 \ubfcc\ub9ac\ub294 \uc548\uae30\ubd80\uac00 \uc8fc\ub3c4\ud588\ub358 1980\ub144\ub300\uc758 \uad00\uacc4\uae30\uad00\ub300\ucc45\ud68c\uc758\uc774\ub2e4. \ud30c\uc5c5\uc720\ub3c4 \u000d\u000a\uc0ac\uac74 \ub2f9\uc2dc \uae08\uc18d\uc5f0\ub9f9 \ubc1c\ud45c\uc5d0 \ub530\ub974\uba74 \uac80\ucc30 \uacf5\uc548\ubd80\uac00 \uac1c\uc785\ud55c \uc758\ud639\uc774 \uc788\ub294 \uc0ac\uc5c5\uc7a5\uc740 \uc0ac\uce21\uc870\ucc28 \u000d\u000a\ubd88\uad6c\uc18d \uc694\uccad\uc744 \ud588\ub294\ub370\ub3c4 \uc704\uc6d0\uc7a5\uc744 \uae34\uae09\uccb4\ud3ec \ud55c\uad6d\uc911\uacf5\uc5c5, \uacbd\ucc30 \ud22c\uc785\ud558\uc5ec 2600\uba85\uc744 \uc5f0\ud589\ud558\u000d\u000a\uace0 46\uba85\uc744 \uad6c\uc18d\ud55c \ub9cc\ub3c4\uae30\uacc4 \ub4f1 21\uac1c \uc0ac\uc5c5\uc7a5\uc5d0 \ub2ec\ud588\ub2e4. \u000d\u000a\ubd80\ub2f9\ub178\ub3d9\ud589\uc704\uc5d0 \ub300\ud574 \ub178\ub3d9\ubd80\uac00 \uc0ac\uc5c5\uc8fc \uad6c\uc18d\uc744 \ud488\uc2e0\ud588\uc73c\ub098 \uacf5\uc548\uac80\ucc30\uc774 \uae30\uac01\ud558\uace0 \ud56d\uc758\ud558\u000d\u000a\ub294 \ub178\ub3d9\uc790\ub4e4\uc744 \uad6c\uc18d\ud558\uc5ec \ubd80\ub2f9\ub178\ub3d9\ud589\uc704\uac00 \ud655\uc0b0\ub418\uace0 \ub178\uc870\uac00 \uc640\ud574\ub41c \uc0ac\ub840\ub3c4 \ubd80\uc9c0\uae30\uc218\ub2e4. \uacb0\u000d\u000a\uad6d \uc870\ud3d0\uacf5\uc0ac \ud30c\uc5c5\uc720\ub3c4 \uc0ac\uac74\uc740 \uc9c4\ud615\uad6c \uacf5\uc548\ubd80\uc7a5\uc758 \uac1c\uc778\uc801 \ubc94\uc8c4\ub77c\uae30\ubcf4\ub2e4 \uac80\ucc30 \uacf5\uc548\ubd80\uac00 \ub178\u000d\u000a\uc0ac\uad00\uacc4\ub97c \uc0ac\ucc30\ud558\uace0 \uac1c\uc785\ud574\uc624\ub358 \uad00\uc131\uc5d0 \ub530\ub77c \uc790\uc5f0\uc2a4\ub7fd\uac8c \ud55c\ubc1c \ub354 \ub0b4\ub51b\uc740 \uacb0\uacfc\uc600\ub2e4\uace0 \ubcf4\uc544\u000d\u000a\uc57c \ud560 \uac83\uc774\ub2e4. \u000d\u000a\ud30c\uc5c5\uc720\ub3c4 \uc0ac\uac74 \uc774\ud6c4 \uac80\ucc30\uc758 \uc774\ub7ec\ud55c \uacf5\uc548\uc801 \uc2dc\uac01\uc740 \uacfc\uc5f0 \ub2ec\ub77c\uc84c\uc744\uae4c? 2007\ub144 \ud3ec\ud56d \uac74\uc124\u000d\u000a\ub178\uc870 \uc218\uc0ac\uacb0\uacfc\uc5d0\uc11c \uac80\ucc30\uc774 \uc790\ub791\uc2a4\ub7fd\uac8c \u201c\uac15\ub825\ud55c \uac80\ucc30\uad8c\uc744 \ud589\uc0ac\ud558\uc600\uc2b5\ub2c8\ub2e4. \u2026 \uc774\ubc88 \ud30c\uc5c5 \uacfc\u000d\u000a\uc815\uc5d0\uc11c \uac80\ucc30\uc758 \uac15\ub825\ud55c \uc870\uce58\uc5d0 \ub530\ub77c \ub178\uc870\uc9d1\ud589\ubd80\uac00 \uc640\ud574(\ub418\uc5c8\ub2e4)\u201d\uace0 \uc4f0\uace0\uc788\ub294 \uc810, 2009\ub144 \u000d\u000a\uc30d\uc6a9\uc790\ub3d9\ucc28 \uc815\ub9ac\ud574\uace0\ub098 \ucca0\ub3c4\ub178\uc870 \ud30c\uc5c5\uc5d0\uc11c \uac80\ucc30\uacfc \uc0ac\uce21, \uc815\ubd80\uae30\uad00 \ub4f1\uc774 \uacf5\uc870\ud558\uace0 \uad00\uacc4\uae30\u000d\u000a\uad00 \ud68c\uc758\ub97c \uc5ec\ub294 \ubaa8\uc2b5 \ub4f1\uc744 \ubcf4\uba74 \uc804\ud600 \ubcc0\ud558\uc9c0 \uc54a\uc740 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4. \u000d\u000a\u2461 \ubc1c\ub808\uc624\uc804\uc7a5\uc2dc\uc2a4\ud15c\uc2a4\ucf54\ub9ac\uc544 \u000d\u000a\ubc1c\ub808\uc624\uc804\uc7a5\uc2dc\uc2a4\ud15c\uc2a4\ucf54\ub9ac\uc544(\uc774\ud558 \ubc1c\ub808\uc624)\ub294 \uacbd\uc8fc\uc2dc\uc5d0 \uc18c\uc7ac\ud55c \uc790\ub3d9\ucc28\ubd80\ud488 \uc0dd\uc0b0\uc5c5\uccb4\uc774\ub2e4. \uc804\uccb4 \u000d\u000a780\uc5ec\uba85 \uc911 620\uc5ec\uba85\uc774 \uac00\uc785\ud55c \uae08\uc18d\ub178\uc870 \ubc1c\ub808\uc624\ub9cc\ub3c4\uc9c0\ud68c(\uc774\ud558 \ubc1c\ub808\uc624\uc9c0\ud68c)\ub294 \uae08\uc18d\ub178\uc870 \uacbd\uc8fc\uc9c0\ubd80\u000d\u000a\uc758 \ud575\uc2ec\uc0ac\uc5c5\uc7a5\uc774\uae30\ub3c4 \ud588\ub2e4. \u000d\u000a2010\ub144 \ubc1c\ub808\uc624 \uc0ac\uce21\uc774 \ub2e8\ud611\uc744 \uc704\ubc18\ud558\uc5ec \uc81c1\uacf5\uc7a5 \uacbd\ube44\uc5c5\ubb34\ub97c \uc678\uc8fc\ud654\ud558\uba74\uc11c \uc774\uc5d0 \ubc18\ubc1c\u000d\u000a\ud55c \ubc1c\ub808\uc624\uc9c0\ud68c\ub294 \uc5f0\uc7a5\uadfc\ub85c \uac70\ubd80\uc5d0 \ub3cc\uc785\ud55c\ub2e4. \uadf8\ub7ec\uc790 \uc0ac\uce21\uc740 \uc9c1\uc7a5\ud3d0\uc1c4\ub97c \ub2e8\ud589\ud558\uc5ec \ubc1c\ub808\uc624 \u000d\u000a\uacf5\uc7a5 \uc804\uccb4\uc5d0 \ub300\ud574 \uc870\ud569\uc6d0\uc758 \ucd9c\uc785\uc744 \uc804\uba74\uae08\uc9c0\ud558\ub294 \ucd08\uac15\uacbd \ub300\uc751\uc5d0 \ub098\uc130\ub2e4. ","50 51\u000d\u000a\ubc1c\ub808\uc624\ub294 \ucc3d\uc870\ucee8\uc124\ud305\uacfc \uacc4\uc57d\uc744 \uccb4\uacb0\ud558\uace0 \ub178\uc870\ud30c\uad34 \uae30\ubcf8\uacc4\ud68d\uc744 \uc218\ub9bd\ud55c \ubc14, \uadf8 \ubc29\uc548\uc73c\ub85c\ub294 \u000d\u000a\ub450\uac00\uc9c0\uac00 \uc81c\uc2dc\ub418\uc5c8\ub2e4. 1\uc548(\uc870\ud569\ud569\ub9ac\ud654 \ubc29\uc548)\uc740 \ud0c8\ud1f4\uacf5\uc791\uc744 \ud1b5\ud574 \uc870\ud569\uc6d0\uc744 \ucd95\uc18c\ud558\ub294 \ubc29\uc548\uc73c\ub85c, \ub2e8\u000d\u000a\uae30\uac04\uc5d0 \uc870\ud569\uc758 \ud798\uc744 \ubb34\ub108\ub728\ub9b4 \uc218 \uc788\uc73c\uba70, 2\uc548(\uc870\uc9c1\ud615\ud0dc \ubcc0\uacbd \ubc29\uc548)\uc740 \uc0b0\ubcc4\ub178\uc870(\uae08\uc18d\ub178\uc870)\ub97c \uae30\uc5c5\ubcc4 \ub178\uc870\u000d\u000a\ub85c \uc870\uc9c1\ud615\ud0dc\ub97c \ubcc0\uacbd\ud558\ub294 \ubc29\uc548\uc774\uc5c8\ub2e4.\u000d\u000a\uacb0\uad6d \uc7a5\uae30\uac04 \uc9c1\uc7a5\ud3d0\uc1c4\ub97c \ud1b5\ud574 \ud30c\uc5c5\uc911\uc778 \uc870\ud569\uc6d0\uc758 \ucd9c\uc785\uc744 \ub9c9\uc544\ub193\uc740 \uc0c1\ud0dc\uc5d0\uc11c \uac1c\ubcc4\uc801\uc73c\u000d\u000a\ub85c \uc5c5\ubb34 \ubcf5\uadc0\ud55c \uc870\ud569\uc6d0 \uc911\uc2ec\uc73c\ub85c \u2018\uc870\ud569\uc6d0\uc744 \uc704\ud55c \uc870\ud569\uc6d0\ub4e4\uc758 \ubaa8\uc784\u2019\uc774\ub780 \uac83\uc744 \ub9cc\ub4e4\uac8c \ud558\uace0 \u000d\u000a\uc774\ub97c \ud1b5\ud574 \uc870\uc9c1\ud615\ud0dc \ubcc0\uacbd\uc744 \uacb0\uc758\ud588\ub2e4. 600\uba85\uc774\ub358 \uae08\uc18d\ub178\uc870 \uc870\ud569\uc6d0\uc740 30\uc5ec\uba85\uc774 \ub0a8\uc558\uace0 \ud574\u000d\u000a\uace0\uc790\ub4e4\uc740 \uacf5\uc7a5 \uc55e\uc5d0\uc11c \ub18d\uc131\uc5d0 \ub4e4\uc5b4\uac14\ub2e4. \u000d\u000a\ub098\uc911\uc5d0 \ubc1d\ud600\uc9c4 \ubc14\ub85c \uacf5\uc548\uae30\uad00\ub300\ucc45\ud68c\uc758\uc5d0 \uc81c\ucd9c\ub41c \u201c\ubc1c\ub808\uc624\ub9cc\ub3c4\uc804\uc7a5(\uc8fc) \uc9c1\uc7a5\ud3d0\uc1c4\ub97c \ub458\ub7ec\u000d\u000a\uc2fc \ubc95\uc801 \uc81c\ubb38\uc81c \uac80\ud1a0\u201d\ub77c\ub294 \ubb38\uac74\uc744 \ucc3d\uc870\ucee8\uc124\ud305\uc5d0\uc11c\ub3c4 \uac00\uc9c0\uace0 \uc788\uc5c8\uc9c0\ub9cc \uadf8 \uacbd\uc704\ub294 \uc81c\ub300\ub85c \uc870\u000d\u000a\uc0ac\ub418\uc9c0 \uc54a\uc558\ub2e4. \uac80\ucc30\uc740 \ubc1c\ub808\uc624 \uc0ac\uce21\uc758 \ubd80\ub2f9\ub178\ub3d9\ud589\uc704 \ub300\ubd80\ubd84\uc744 \ubd88\uae30\uc18c \ucc98\ubd84\ud588\uc9c0\ub9cc \ubc95\uc6d0\uc774 \u000d\u000a\ub178\uc870\uc758 \uc7ac\uc815\uc2e0\uccad\uc744 \uc778\uc6a9\ud568\uc5d0 \ub530\ub77c \uacb0\uad6d \uacf5\uc18c\uc81c\uae30\ub97c \ud558\uac8c \ub41c\ub2e4. 2019\ub144 \ub300\ubc95\uc6d0\uc5d0\uc11c \ub178\uc870\u000d\u000a\ubc95 \uc704\ubc18 \uc720\uc8c4\uac00 \ud655\uc815\ub418\uc5c8\ub2e4.\u000d\u000a2010\ub144 \ubc1c\ub808\uc624\uc5d0\uc11c \ud589\ud574\uc9c4 \ub178\uc870\ud30c\uad34 \ubc29\uc2dd\uc740 \uc774\ud6c4 \uc81c\uc870\uc5c5 \ub178\uc870\ud30c\uad34\uc758 \uc804\ubc94\uc774 \ub418\uc5c8\ub2e4. \u000d\u000a\uc815\ub9ac\ud558\uba74 \u201c\uae30\uc2b5\uc801 \uc9c1\uc7a5\ud3d0\uc1c4 \uacf5\uace0 \u2192 \uc6a9\uc5ed\uae61\ud328 \ud22c\uc785, \uacf5\uc7a5\uc9c4\uc785 \ubd09\uc1c4 \u2192 \uc784\uc2dc\uc9c1 \ucc44\uc6a9, \ub300\uccb4\u000d\u000a\uc778\ub825 \ud22c\uc785 \u2192 \ub178\uc870\uac04\ubd80\uc5d0 \ub300\ud55c \uac00\uc555\ub958, \uace0\uc18c\uace0\ubc1c \u2192 \uc120\ubcc4 \ubcf5\uadc0 \u2192 \uae30\uc5c5\ub178\uc870 \uac74\uc124\uacfc \uce5c\ud68c\uc0ac \u000d\u000a\uc9d1\ud589\ubd80 \uad6c\uc131, \ub178\uc870\uac04\ubd80 \uc9d5\uacc4\uc640 \ub2e8\ud611\ud574\uc9c0\u201d\uc758 \uc21c\uc11c\ub85c \uc9c4\ud589\ub418\ub294 \uac83\uc774\ub2e4. \uc774 \uacfc\uc815\uc5d0\uc11c \uc0ac\uc5c5\uc8fc\u000d\u000a\uc640 \ub178\uc870\ud30c\uad34 \ucee8\uc124\ud305 \uc5c5\uccb4\uac00 \uc5b4\ub5a4 \uc5ed\ud560\uc744 \ud588\ub294\uc9c0\ub294 \uc218\uc0ac\uc640 \uc7ac\ud310\uacfc\uc815\uc5d0\uc11c \uc5b4\ub290 \uc815\ub3c4 \ubc1d\ud600\u000d\u000a\uc84c\uc9c0\ub9cc, \uad00\uacc4\uae30\uad00\ub300\ucc45\ud68c\uc758\uc5d0 \ubaa8\uc600\ub358 \uac80\ucc30, \uace0\uc6a9\ub178\ub3d9\ubd80, \uad6d\uac00\uc815\ubcf4\uc6d0, \uc6d0\uccad\uc0ac\uc778 \ud604\ub300\ucc28 \ub4f1\u000d\u000a\uc774 \ub178\uc870\ud30c\uad34\uc5d0 \uc5b4\ub5bb\uac8c \uac1c\uc785\ud588\ub294\uc9c0\ub294 \uc77c\ubd80 \uc815\ud669\uc99d\uac70\ub85c \ucd94\ub860\ud560 \uc218 \uc788\uc744 \ubfd0\uc774\uace0 \uc5ec\uc804\ud788 \uc758\ud639\u000d\u000a\uc774 \ub0a8\uc544\uc788\ub2e4.\u000d\u000a\u2462 \uc720\uc131\uae30\uc5c5\u000d\u000a\uc720\uc131\uae30\uc5c5\uc740 \uc790\ub3d9\ucc28 \uc5d4\uc9c4\ubd80\ud488\uc778 \ud53c\uc2a4\ud1a4\ub9c1\uacfc \uc2e4\ub9b0\ub354 \ub77c\uc774\ub108 \ub4f1\uc744 \uc0dd\uc0b0\ud558\ub294 \ud68c\uc0ac\uc774\ub2e4. \u000d\u000a2011\ub144 \ub2f9\uc2dc \uc544\uc0b0\uacf5\uc7a5\uacfc \uc601\ub3d9\uacf5\uc7a5 \ub4f1 \uc0c1\uc2dc \ub178\ub3d9\uc790 755\uba85\uc774 \uc77c\ud558\uace0 \uc788\uc5c8\uace0 566\uba85\uc774 \uae08\uc18d\u000d\u000a\ub178\uc870 \uc720\uc131\uae30\uc5c5\uc9c0\ud68c\uc5d0 \uac00\uc785\ub418\uc5b4 \uc788\uc5c8\ub2e4. \u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 52\u000d\u000a\uc720\uc131\uae30\uc5c5 \ub178\ub3d9\uc790\ub4e4\uc740 30\ub144 \uac00\uae4c\uc774 \ub178\ub3d9 \uac15\ub3c4\uac00 \ub192\uace0 \uc704\ud5d8\ud55c \uc7a5\uc2dc\uac04 \ub178\ub3d9\uc744 \uc218\ud589\ud574\uc654\ub294\u000d\u000a\ub370 \uc6d4 \ub178\ub3d9\uc2dc\uac04\uc740 276\uc2dc\uac04\uc5d0 \ub2ec\ud588\ub2e4. \uc774\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574 \ub178\uc0ac\ub294 2010\ub144 1\uc6d4 \u2018\uad50\ub300\uc81c \uac1c\u000d\u000a\uc120 \ud569\uc758\uc11c\u2019\ub97c \ud1b5\ud574 \uc8fc\uac04\uc5f0\uc18d 2\uad50\ub300\uc81c\uc640 \uc6d4\uae09\uc81c \ub3c4\uc785\uc744 \ud569\uc758\ud588\ub2e4. \ub178\uc870\ub294 \uacc4\uc18d\ud574\uc11c \uc548\uc744 \uc900\u000d\u000a\ube44\ud558\uace0 \uad50\uc12d\uc744 \uc9c4\ud589\ud558\uba74\uc11c \ub178\ub825\ud588\uc9c0\ub9cc \uc0ac\uce21\uc740 \uc544\ubb34\ub7f0 \uc548\ub3c4 \uc5c6\uc774 \ub178\uc870 \uc81c\uc548\uc744 \uac70\ubd80\ud558\uba70 \ubb34\u000d\u000a\uc131\uc758\ud55c \ud0dc\ub3c4\ub97c \ubcf4\uc600\ub2e4. \ub098\uc911\uc5d0 \ubc1d\ud600\uc84c\uc9c0\ub9cc \uc0ac\uce21\uc740 \ucc3d\uc870\ucee8\uc124\ud305\uc758 \uc81c\uc548\uc11c\ub97c \ubc1b\uace0 5\uc6d4\uc5d0\ub294 \u000d\u000a\ub178\uc870\ud30c\uad34\ub97c \uc704\ud55c \uacc4\uc57d\uc744 \uccb4\uacb0\ud55c \uc0c1\ud669\uc774\uc5c8\ub2e4.\u000d\u000a\uc720\uc131\uae30\uc5c5 \uc5ed\uc2dc \ub178\uc870\uac00 \uc7c1\uc758 \uc808\ucc28\ub97c \ubc1f\uc790 \uc989\uc2dc \uc9c1\uc7a5\ud3d0\uc1c4\uac00 \ub2e8\ud589\ub418\uc5c8\uace0, \uc6a9\uc5ed\uae61\ud328\uc758 \ud3ed\ub825\u000d\u000a\uacfc \uac1c\ubcc4\ubcf5\uadc0 \uc885\uc6a9, 2011\ub144 7\uc6d4\uc758 \uae30\uc5c5\ubcc4 \ubcf5\uc218\ub178\uc870 \ud5c8\uc6a9\uc5d0 \ub9de\ucd98 \uc81c2\ub178\uc870 \uc124\ub9bd \ucd94\uc9c4\uc774 \uc774\uc5b4\u000d\u000a\uc84c\ub2e4. \ucc3d\uc870\ucee8\uc124\ud305\uc740 \uc81c2\ub178\uc870\uac00 \uc4f8 \ud604\uc218\ub9c9 \ubb38\uad6c\uae4c\uc9c0 \uc608\uc2dc\ud588\ub294\ub370, \u201c\uc678\ubd80 \ubd88\uc21c\uc138\ub825\uc758 \uc120\ub3d9\uc804\u000d\u000a\uc220\uc5d0 \ud76c\uc0dd\ub9cc \ub2f9\ud588\ub358 \uc6b0\ub9ac! \uc870\ud569\uc6d0\uc744 \uc704\ud55c \ub178\ub3d9\uc870\ud569\uc73c\ub85c \uac70\ub4ed\ub098\uc790!\u201d \ub4f1\uc774\uc5c8\ub2e4.\u000d\u000a\uadf8\ub7ec\ub098 \uc774\ub4e4\uc740 \uc720\uc131\uc9c0\ud68c\uc5d0\uc11c \ub2e4\uc218 \uc870\ud569\uc6d0\uc744 \ud0c8\ud1f4\uc2dc\ucf1c \uad50\uc12d\uad8c\uc744 \ubc15\ud0c8\ud558\ub294\ub370 \uc2e4\ud328\ud588\uace0 \u000d\u000a2012\ub144 \ucc3d\uc870\ucee8\uc124\ud305\uc758 \ub178\uc870\ud30c\uad34\uac00 \ud3ed\ub85c\ub418\uba74\uc11c \ub178\ub3d9\ubd80\uac00 \uc218\uc0ac\uc5d0 \ub4e4\uc5b4\uac00\uac8c \ub41c\ub2e4. \uc774 \uacfc\uc815\uc5d0\u000d\u000a\uc11c\ub3c4 \uac80\ucc30\uc740 \ub178\uc0ac \uc591\uce21\uc758 \uc218\uc0ac\uc5d0 \uc9c0\uadf9\ud788 \ud3b8\ud30c\uc801\uc778 \ud0dc\ub3c4\ub97c \ubcf4\uc600\uace0, \ub178\ub3d9\ubd80\uac00 \uc0ac\uce21 \uc778\ubb3c\ub4e4\uc758 \u000d\u000a\uad6c\uc18d \ub4f1\uc744 \uc694\uccad\ud55c \uac83\uc5d0\ub3c4 \ubd88\ud5c8\ud558\ub294 \uc218\uc0ac\uc9c0\ud718\ub97c \ub0b4\ub838\uc73c\uba70 \uacb0\uad6d\uc740 \ud575\uc2ec\uc801\uc778 \ud610\uc758 \uc804\ubd80\uc5d0 \ub300\u000d\u000a\ud574 \ubd88\uae30\uc18c \ucc98\ubd84\uc744 \ub0b4\ub838\ub2e4.\u000d\u000a\uac80\ucc30\uc758 \uc720\uc131\uae30\uc5c5 \ubd88\uae30\uc18c \ucc98\ubd84\uc740 \ubc95\uc6d0 \uc7ac\uc815\uc2e0\uccad\uc744 \uac70\uccd0 2015\ub144\uc5d0\uc57c \ube44\ub85c\uc18c \uacf5\uc18c\uc81c\uae30, \u000d\u000a2017\ub144 \uc720\uc8c4\ud310\uacb0\uc774 \ub098\uc654\ub2e4. \uac80\ucc30\uc740 \uc6d0\uccad\uc778 \ud604\ub300\uc790\ub3d9\ucc28\uc758 \uac1c\uc785 \ud610\uc758\uc5d0 \ub300\ud574\uc11c\ub3c4 \uc81c\ub300\ub85c \uc218\u000d\u000a\uc0ac\ub098 \uae30\uc18c\ub97c \ud558\uc9c0 \uc54a\uace0 \ubc84\ud2f0\ub2e4\uac00 2017\ub144 \uc815\uad8c\uad50\uccb4 \ud6c4\uc5d0\uc57c 6\ub144\ub9cc\uc5d0 \uae30\uc18c\ud55c\ub2e4. \ucc3d\uc870\ucee8\uc124\ud305\u000d\u000a\uacfc \uad6d\uac00\uae30\uad00\uc758 \uc720\ucc29 \uc758\ud639\ub3c4 \ubb34\uc218\ud788 \uc81c\uae30\ub418\uc5c8\uc9c0\ub9cc \uacb0\uad6d \uc81c\ub300\ub85c \uc870\uc0ac\ub418\uc9c0 \uc54a\uc558\ub2e4. \u000d\u000a\u2463 \uc0bc\uc131 \uc5d0\ubc84\ub79c\ub4dc\u000d\u000a\uc0bc\uc131 \uc5d0\ubc84\ub79c\ub4dc\ub294 \uc0bc\uc131\uc758 \ub2e4\ub978 \ubaa8\ub4e0 \uacc4\uc5f4\uc0ac\ub4e4\uc774 \uadf8\ub807\ub4ef\uc774 \u2018\ubb34\ub178\uc870 \uc0ac\uc5c5\uc7a5\u2019\uc774\uc5c8\uc9c0\ub9cc, \u000d\u000a2011\ub144 \uae30\uc5c5\ubcc4 \ubcf5\uc218\ub178\uc870 \ud5c8\uc6a9\uacfc \ud568\uaed8 \uc0c8\ub86d\uac8c \ub178\uc870 \uc124\ub9bd\uc2dc\ub3c4\uac00 \uc77c\uc5b4\ub0ac\ub2e4. \u000d\u000a2002\ub144\ubd80\ud130 \ub178\uc0ac\ud611\uc758\ud68c \uadfc\ub85c\uc790\uc704\uc6d0\uc73c\ub85c \ud65c\ub3d9\ud574\uc654\ub358 \uc870\uc7a5\ud76c\ub97c \ube44\ub86f\ud55c \uc5d0\ubc84\ub79c\ub4dc \ub178\ub3d9\u000d\u000a\uc790 4\uc778\uc740 2011\ub144 7\uc6d4 12\uc77c \uc0bc\uc131\ub178\uc870 \uc124\ub9bd\ucd1d\ud68c\ub97c \ud558\uace0 18\uc77c \uc124\ub9bd\ud544\uc99d\uc744 \uad50\ubd80\ubc1b\uc558\ub2e4. \uadf8","52 53\u000d\u000a\ub7ec\ub098 \uadf8\ub4e4\ubcf4\ub2e4 \ud55c \ubc1c \uc55e\uc11c \uc5d0\ubc84\ub79c\ub4dc\uc5d0 \ub2e4\ub978 \ub178\uc870\uac00 \uba3c\uc800 \uc124\ub9bd\ub418\uc5c8\uc74c\uc744 \uc54c\uac8c \ub41c\ub2e4. \ubd88\uacfc 19\u000d\u000a\uc77c \uc804\uc778 6\uc6d4 23\uc77c \uc0bc\uc131\uc5d0\ubc84\ub79c\ub4dc\ub178\uc870\uac00 \uc124\ub9bd\uc2e0\uace0\ub97c \ub9c8\ucce4\uc73c\uba70 \uaca8\uc6b0 \uc77c\uc8fc\uc77c\ub9cc\uc778 6\uc6d4 29\uc77c\uc5d0 \u000d\u000a\ub2e8\uccb4\ud611\uc57d\uae4c\uc9c0 \uccb4\uacb0\ud55c \uc0c1\ud0dc\uc600\ub2e4. \uc0c8\ub85c \ub9cc\ub4e4\uc5b4\uc9c4 \uc0bc\uc131\ub178\uc870\ub294 \ub2f9\ubd84\uac04 \uad50\uc12d\ub3c4 \ud611\uc57d \uccb4\uacb0\ub3c4 \ud560 \u000d\u000a\uc218 \uc5c6\uac8c \ub41c \uac83\uc774\ub2e4. \uc774\uc5d0 \ub354\ud574 \uc0ac\uce21\uc740 \ub178\ub3d9\uc870\ud569\uc744 \uc124\ub9bd\ud55c \ud575\uc2ec \uc8fc\uccb4\ub4e4\uc5d0 \ub300\ud55c \uc9d5\uacc4\uc5d0 \ucc29\uc218\u000d\u000a\ud558\uc5ec \ubd80\uc704\uc6d0\uc7a5 \uc870\uc7a5\ud76c\ub97c \ud574\uace0\ud558\uace0 \uc704\uc6d0\uc7a5 \ubc15\uc6d0\uc6b0\ub97c \ube44\ub86f\ud55c \uc870\ud569\uc6d0\ub4e4\uc744 \uc5ec\ub7ec \uba85\ubaa9\uc73c\ub85c \uc9d5\u000d\u000a\uacc4\ud558\uac70\ub098 \uace0\uc18c\uace0\ubc1c \ud588\ub2e4. \u000d\u000a2013\ub144 \uc2ec\uc0c1\uc815 \uc758\uc6d0\uc774 \u20182012\ub144 S\uadf8\ub8f9 \ub178\uc0ac\uc804\ub7b5\u2019 \ubb38\uac74\uc744 \ud3ed\ub85c\ud558\uba74\uc11c \ubc18\uc804\uc774 \uc77c\uc5b4\ub0ac\ub2e4. \u000d\u000a\uc774 \ubb38\uac74\uc740 \uc0bc\uc131\uadf8\ub8f9\uc758 \ubd88\ubc95\uc801\uc778 \ub178\uc870\ud30c\uad34 \uc804\ub7b5\uc744 \uc0c1\uc138\ud558\uac8c \ub2f4\uc558\uc744 \ubfd0\ub9cc\uc544\ub2c8\ub77c \ud2b9\ud788 \ubcf5\uc218\u000d\u000a\ub178\uc870 \uc2dc\ud589\uc5d0 \ub300\ube44\ud55c \ub0b4\uc6a9\uc744 \uc9d1\uc911\uc801\uc73c\ub85c \ub2e4\ub8e8\uc5c8\ub294\ub370 \uadf8 \ud575\uc2ec \uc0ac\ub840\ub85c \uc5d0\ubc84\ub79c\ub4dc \ub178\uc870\uc124\ub9bd \uc2dc\u000d\u000a\ub3c4\ub97c \ub4e4\uace0 \uc788\ub2e4.\u000d\u000a\uad00\ub828 \ub0b4\uc6a9\uc744 \ubcf4\uba74, 2011\ub144 \ub2f9\uc2dc \u2018\ubb38\uc81c\uc778\ub825\u2019\ub4e4\uc774 \uc740\ubc00\ud788 \ub178\uc870 \uc124\ub9bd\uc744 \uc900\ube44\ud588\uc9c0\ub9cc \ud68c\uc0ac\uac00 \u000d\u000a\u2018\uc8fc\ub3d9\uc790\u2019 \ucc45\uc0c1\uc744 \ub4a4\uc838 \ubbf8\ub9ac \uc124\ub9bd\ucd1d\ud68c \ub300\uc790\ubcf4\ub97c \uc785\uc218\ud55c\ub2e4. \uc0ac\uce21\uc740 \u201c\uc2dc\ub098\ub9ac\uc624\uc5d0 \ub530\ub978 \uc2e0\uc18d\ud55c \u000d\u000a\ub300\uc751\u201d\uc5d0 \ub098\uc11c\ub294\ub370 \uace7\ubc14\ub85c \uce5c\uc0ac\ub178\uc870\ub97c \uc124\ub9bd\ud558\uace0 \ub2e8\ud611\uae4c\uc9c0 \uccb4\uacb0\ud558\uc5ec \ud5a5\ud6c4 2\ub144\uac04 \uc0c8 \ub178\uc870\uc758 \u000d\u000a\ub2e8\uccb4\uad50\uc12d \uc694\uad6c\uac00 \ubd88\uac00\ub2a5\ud574\uc9c0\ub3c4\ub85d \ub9cc\ub4e0 \uac83\uc774\ub2e4. \uc774 \uc0ac\ub840\uc758 \uc2dc\uc0ac\uc810\uc740, \uc0bc\uc131\uc5d0 \ub530\ub974\uba74 \uccab\uc9f8 \u000d\u000a\ubb38\uc81c\uc778\ub825\uc5d0 \ub300\ud55c \uc815\ubc00 \ucc44\uc99d\uacfc \uc720\uc0ac\uc2dc \uc9d5\uacc4 \uc900\ube44\uac00 \uc911\uc694\ud558\ub2e4, \ub458\uc9f8 \ub178\uc0ac\ud611\uc758\ud68c\ub97c \uc798 \uc721\uc131\ud574\u000d\u000a\uc57c \ud55c\ub2e4, \uc14b\uc9f8 \uc0c1\ud669 \ubc1c\uc0dd\uc2dc \uc804 \ubd80\ubb38 \uc5ed\ub7c9\uc744 \uc9d1\uc911\ud574\uc11c \ub300\uc751\ud55c\ub2e4\ub294 \uac83\uc774\ub2e4. \u000d\u000a\uc0bc\uc131\uc758 \ubb34\ub178\uc870\uacbd\uc601\uc740 \ud55c\ubc88\ub3c4 \ube44\ubc00\uc774\uc5c8\ub358 \uc801\uc774 \uc5c6\ub2e4. \uc2ec\uc9c0\uc5b4 \ub178\uace8\uc801\uc778 \uc804\ub7b5 \ubb38\uac74\uc774 \ub098\uc654\u000d\u000a\ub2e4. \uadf8\ub7f0\ub370\ub3c4 \uace0\uc6a9\ub178\ub3d9\ubd80\ub294 \uc774 \uc0ac\uac74\uc5d0\uc11c \uc0bc\uc131 \uce21\uc758 \uc55e\ub4a4\uac00 \ub9de\uc9c0\uc54a\ub294 \uc9c4\uc220\uacfc \ubd80\ubd84\uc801\uc778 \uc0ac\u000d\u000a\uc2e4 \uc778\uc815\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \uc18c\uadf9\uc801\uc778 \uc218\uc0ac\ub85c \uc77c\uad00\ud558\ub2e4\uac00 \uacb0\uad6d \ubb34\ud610\uc758 \ucc98\ubd84\uc73c\ub85c \uc885\uacb0\ud558\uace0 \ub9cc\ub2e4. \u000d\u000a\u2464 \uc0bc\uc131\uc804\uc790\uc11c\ube44\uc2a4\u000d\u000a\uc0bc\uc131\uc804\uc790\uc11c\ube44\uc2a4\ub294 \uc0bc\uc131\uc804\uc790\uc758 \uc790\ud68c\uc0ac\uc774\uace0 2013\ub144 \ub2f9\uc2dc \uc804\uad6d 48\uac1c \uc9c0\uc810\uacfc \ucf5c\uc13c\ud130 \ub4f1\uc744 \u000d\u000a\ud3ec\ud568\ud558\uc5ec 1,400\uba85\uc758 \uc815\uaddc\uc9c1\uc774 \uc77c\ud558\uace0 \uc788\uc5c8\uc9c0\ub9cc \u2018\ud611\ub825\uc5c5\uccb4\u2019\ub97c \ud1b5\ud55c \uac04\uc811\uace0\uc6a9 \uc778\ub825\uc740 \uadf8 6\ubc30\u000d\u000a\uac00 \ub118\ub294 9,000\uc5ec\uba85\uc5d0 \ub2ec\ud588\ub2e4. \u000d\u000a2013\ub144 \uc0bc\uc131\uc804\uc790\uc11c\ube44\uc2a4\uc758 \ud558\uccad\uc5c5\uccb4\uc5d0 \uc18d\ud55c \uc124\uce58\uc218\ub9ac\uae30\uc0ac\ub4e4\uc774 \ubaa8\uc5ec \uae08\uc18d\ub178\uc870 \uc0bc\uc131\uc804\uc790\u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 54\u000d\u000a\uc11c\ube44\uc2a4\uc9c0\ud68c\uac00 \ucd9c\ubc94\ud558\uc600\uace0 \uace7\ubc14\ub85c \uc0bc\uc131\uc804\uc790\uc11c\ube44\uc2a4\uc758 \ubd88\ubc95\ud30c\uacac \ubb38\uc81c\ub97c \uc81c\uae30\ud558\uc600\ub2e4. \ub178\ub3d9\ubd80\u000d\u000a\ub294 \ud55c \ub2ec \ub3d9\uc548 \uc804\uad6d\uc801\uc73c\ub85c 35\uba85\uc758 \uadfc\ub85c\uac10\ub3c5\uad00\uc744 \ud22c\uc785\ud558\uc5ec \uc0bc\uc131\uc804\uc790\uc11c\ube44\uc2a4\uc5d0 \ub300\ud55c \uac10\ub3c5\uc744 \u000d\u000a\uc2e4\uc2dc\ud558\uc600\uace0 \uc0ac\uc2e4\uc0c1 \ubd88\ubc95\ud30c\uacac\uc73c\ub85c \uacb0\ub860\uc9c0\uc740 \ubcf4\uace0\uc11c\uac00 \uc81c\ucd9c\ub418\uc5c8\ub2e4.\u000d\u000a\uadf8\ub7ec\ub098 \ub178\ub3d9\ubd80 \ub0b4\uc758 \u89aa\uc0bc\uc131 \uad00\ub8cc\ub4e4\uc774 \ub098\uc11c\uba74\uc11c \uc774 \ubcf4\uace0\uc11c\ub294 \ub0b4\ubd80\uc758 \uc555\ub825\uc744 \ubc1b\uac8c \ub418\uace0, \u000d\u000a\uc870\uc0ac\uae30\uac04\uc774 \uc5f0\uc7a5\ud574\ub193\uc740 \ub2e4\uc74c \uad00\ub8cc\ub4e4\uacfc \uc0bc\uc131 \uce21\uc758 \uc740\ubc00\ud55c \ud611\uc0c1\uacfc \uac70\ub798\uac00 \uc2dc\uc791\ub41c\ub2e4. \uc774\ubbf8 \uac10\u000d\u000a\ub3c5\ubc29\ud5a5\uc774 \ud754\ub4e4\ub9b0 \uc0c1\ud669\uc5d0\uc11c \uc0bc\uc131 \uce21\uc740 \ub178\ub3d9\ubd80\uac00 \ubcf4\uae30\uc5d0\ub3c4 \ubbf8\ud761\ud55c \uc218\uc900\uc758 \u2018\uac1c\uc120\uc548\u2019\uc774\ub780 \uac83\uc744 \u000d\u000a\uc81c\uc2dc\ud588\uc744 \ubfd0\uc774\uc9c0\ub9cc \uacb0\ub860\uc744 \ubc14\uafb8\uae34 \uc5b4\ub824\uc6e0\ub2e4. \uc774\ub807\uac8c \ud574\uc11c \ub178\ub3d9\ubd80\uc758 \ubd88\ubc95\ud30c\uacac \uac10\ub3c5\uc740 \uc0bc\uc131\u000d\u000a\uc758 \uc758\ub3c4\ub300\ub85c \uacb0\ub860\uc774 \ub098\uace0 \uc0bc\uc131\uc740 \ub9c8\uc2a4\ud130 \ud50c\ub79c\uc5d0 \ub530\ub77c \ub178\uc870\ud30c\uad34\uc5d0 \ub3cc\uc785\ud588\ub2e4.\u000d\u000a\uc0bc\uc131 \uce21\uc740 \uacbd\ucd1d\uacfc \uacf5\ub3d9\uc73c\ub85c \ub2e8\uccb4\uad50\uc12d\uc744 \ud558\uba74\uc11c \uac00\uae09\uc801 \uad50\uc12d\uc744 \uc9c0\uc5f0\ud558\uba74\uc11c \uc2dc\uac04\uc744 \ub04c\uace0 \u000d\u000a\uadf8\ub3d9\uc548 \ud45c\uc801\uac10\uc0ac\ub97c \ud558\uc5ec \uc870\ud569\uc6d0\uc744 \uc555\ubc15\ud588\ub2e4. \uc870\ud569\uc6d0\uc774 \ub9ce\uc740 \ud611\ub825\uc5c5\uccb4\ub97c \uae30\ud68d \ud3d0\uc5c5\uc2dc\ud0a4\uae30\u000d\u000a\ub3c4 \ud588\uace0 \ud3d0\uc5c5\uc73c\ub85c \uc77c\uc790\ub9ac\ub97c \uc783\uc740 \uc870\ud569\uc6d0\uc758 \uc7ac\ucde8\uc5c5\ub3c4 \ub9c9\uc558\ub2e4. \ud0c8\ud1f4\uacf5\uc791\uc740 \uc8fc\ub85c \ucc28\ubcc4\ub300\uc6b0\uc640 \u000d\u000a\uac1c\ubcc4 \uba74\ub2f4\uc744 \ud1b5\ud574 \uc774\ub8e8\uc5b4\uc84c\ub294\ub370 \ub178\uc870\ud0c8\ud1f4\ub97c \u201cGreen\ud654\u201d\ub77c\uace0 \ubd88\ub800\ub2e4. \ub178\uc870 \uac00\uc785\uc728\uc774 50% \u000d\u000a\uc774\uc0c1\uc774\uac70\ub098 \ub178\uc870\uc6d0 \uc218\uac00 5\uba85 \uc774\ud558\uc778 \uc13c\ud130\ub294 \u201c\uc6b0\uc120 Target \ud611\ub825\uc0ac\u201d\ub85c \uc9c0\uc815\ud558\uc5ec \uc9d1\uc911 \uad00\ub9ac\u000d\u000a\ud588\uace0 \uadf8\ub9b0\ud654 \uc791\uc5c5\uc744 \uc704\ud574 \ubd88\ubc95\uc801\uc778 \uc870\ud569\uc6d0 \uc0ac\ucc30\uacfc \uc815\ubcf4\uad00\ub9ac\ub3c4 \uc11c\uc2b4\uce58 \uc54a\uc558\ub2e4. \uc774\ub7ec\ud55c \ud0c8\ud1f4\u000d\u000a\uacf5\uc791\uc758 \uacb0\uacfc\ub85c \uc124\ub9bd \uc774\ud6c4 1600\uba85\uae4c\uc9c0 \ub298\uc5b4\ub0ac\ub358 \uc0bc\uc131\uc804\uc790\uc11c\ube44\uc2a4\uc9c0\ud68c \uc870\ud569\uc6d0 \uc218\ub294 2018\ub144 \u000d\u000a700\uc5ec\uba85 \uc218\uc900\uc73c\ub85c \ub5a8\uc5b4\uc84c\ub2e4.\u000d\u000a2014\ub144\uc5d0\ub294 \uc774\ub7ec\ud55c \ub178\uc870\ud30c\uad34 \uacfc\uc815\uc5d0\uc11c \uc870\ud569\uc6d0 1\uba85\uc774 \uc790\uacb0\ud558\ub294 \uc0ac\uac74\uc774 \ubc8c\uc5b4\uc9c4\ub2e4. \uc0bc\uc131 \u000d\u000a\uce21\uc740 \uacbd\ucc30 \uc815\ubcf4\ub9dd\uc744 \uc774\uc6a9\ud558\uc5ec \uc0ac\ub9dd \uc18c\uc2dd\uacfc \uad00\ub828 \uc815\ubcf4\ub97c \ucde8\ub4dd\ud558\uace0, \uace0\uc778\uc758 \uc544\ubc84\uc9c0\ub97c \ud68c\uc720\ud558\u000d\u000a\uc5ec 6\uc5b58\ucc9c\ub9cc\uc6d0\uc744 \ub300\uac00\ub85c \uc720\uc5b8\uacfc \ub2ec\ub9ac \ub178\uc870\uc7a5\uc744 \uce58\ub974\uc9c0 \ubabb\ud558\uac8c \uacf5\ubaa8, \uacbd\ucc30\uc758 \ub3c4\uc6c0\uc744 \ubc1b\uc544 \u000d\u000a\uc2dc\uc2e0\uc744 \ube7c\uc557\uace0 \ud654\uc7a5\ud558\uc600\ub2e4.\u000d\u000a\u2465 \uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0 \uccad\uc18c\uc6a9\uc5ed\u000d\u000a\uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0 \uccad\uc18c\uc6a9\uc5ed \ub178\ub3d9\uc790\ub4e4\uc758 \ub178\uc870\ud30c\uad34 \uc0ac\uac74\uc740 \uac04\uc811\uace0\uc6a9 \uc0ac\uc5c5\uc7a5\uc5d0\uc11c \ub178\uc870\ud30c\uad34\uac00 \u000d\u000a\uc774\ub8e8\uc5b4\uc9c0\ub294 \uc77c\ubc18\uc801\uc778 \ud615\ud0dc\ub97c \uad6c\uccb4\uc801\uc73c\ub85c \ubcf4\uc5ec\uc8fc\ub294 \uc0ac\ub840\uc774\ub2e4. \u000d\u000a\uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0\uc758 \uc9c1\uc811\uace0\uc6a9 \uc815\uaddc\uc9c1 \ub178\ub3d9\uc790\ub4e4\uc740 \ud55c\uad6d\ub178\ucd1d \ubcd1\uc6d0\ub178\ub828 \uc18c\uc18d\uc774\ub2e4. \ud0dc\uac00\ube44\uc5e0(\uc8fc)","54 55\u000d\u000a\ub77c\ub294 \uc6a9\uc5ed\uc5c5\uccb4 \uc18c\uc18d \uccad\uc18c\ub178\ub3d9\uc790 \ub2e4\uc218\ub294 \ud55c\uad6d\ub178\ucd1d \uc2e0\ucd0c\uc5f0\uc138\ub178\ub3d9\uc870\ud569\uc5d0 \uac00\uc785\ub3fc \uc788\uc5c8\uc73c\ub098 \uc774 \u000d\u000a\ub178\uc870\ub294 \uc0ac\uc2e4\uc0c1 \uc544\ubb34\ub7f0 \ud65c\ub3d9\ub3c4 \ud558\uc9c0\uc54a\ub294 \ud734\uba74\ub178\uc870\uc5d0 \uac00\uae4c\uc6e0\ub2e4. \ucd5c\uc800\uc784\uae08 \uc218\uc900\uc758 \uc800\uc784\uae08\uc5d0 \ud734\u000d\u000a\ubb34\ub294 \uaca9\uc8fc 1\uc77c\uc5d0 \ubd88\uacfc\ud558\uace0 \uac15\uc555\uc801\uc778 \ub178\ubb34\uad00\ub9ac\uc5d0 \ubb38\uc81c\uc758\uc2dd\uc744 \ub290\ub080 \ub178\ub3d9\uc790\ub4e4\uc740 \uc804\uccb4 \uc57d 200\u000d\u000a\uba85\uc911 136\uba85\uc774 2016\ub144 6\uc6d4 \uc0c1\ub2f4\uc744 \ud1b5\ud574 \ubbfc\uc8fc\ub178\ucd1d \uacf5\uacf5\uc6b4\uc218\ub178\uc870 \uc11c\uc6b8\uc9c0\uc5ed\uacf5\uacf5\uc11c\ube44\uc2a4\uc9c0\ubd80\u000d\u000a\uc5d0 \uac00\uc785\ud558\uac8c \ub41c\ub2e4. \u000d\u000a\ubbfc\uc8fc\ub178\ucd1d \uac00\uc785 \uc0ac\uc2e4\uc774 \uc54c\ub824\uc9c0\uba74\uc11c \ud0dc\uac00\ube44\uc5e0(\uc8fc)\uc758 \uad00\ub9ac\uc790\ub4e4\uc774 \ubbfc\uc8fc\ub178\ucd1d \ud0c8\ud1f4\ub97c \uc885\uc6a9\ud558\u000d\u000a\uace0, \uc774 \uacfc\uc815\uc5d0\uc11c \u2018\uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0 \uce21\uc774 \ubbfc\uc8fc\ub178\ucd1d\uc740 \uc808\ub300 \uc548\ub41c\ub2e4\uace0 \ud55c\ub2e4\u2019\ub294 \ub9d0\uc774 \ud758\ub7ec\ub098\uc624\uae30 \u000d\u000a\uc2dc\uc791\ud588\ub2e4. \uacf5\uacf5\uc6b4\uc218\ub178\uc870 \uc0b0\ubcc4\uac04\ubd80\uac00 \ub178\ub3d9\uc790 \ud734\uac8c\uc2e4\uc744 \ubc29\ubb38\ud558\ub294 \uac83\ub3c4 \ub9c9\uace0, \uc720\uc778\ubb3c\uc744 \ub098\ub220\u000d\u000a\uc8fc\ub294 \uac83\ub3c4 \ube7c\uc557\uc544\uac00\ub294 \uc77c\uc774 \ubc1c\uc0dd\ud588\ub2e4. \ubbfc\uc8fc\ub178\ucd1d \uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0\ubd84\ud68c\uc758 \ucd9c\ubc94\uc2dd\uc744 \ubc29\ud574\ud558\uae30 \u000d\u000a\uc704\ud574 \uac19\uc740 \uc2dc\uac04\uc5d0 \ubcc4\ub3c4\uc758 \uc9c1\uc6d0\ubaa8\uc784\uc744 \uc18c\uc9d1\ud558\uae30\ub3c4 \ud588\ub2e4. \u000d\u000a\uc774\ud6c4 \ub178\ub3d9\ubd80 \uc218\uc0ac\uc5d0\uc11c \ubc1d\ud600\uc9c4 \ubc14\uc5d0 \ub530\ub974\uba74 \uccad\uc18c\ub178\ub3d9\uc790\ub4e4\uc758 \ubbfc\uc8fc\ub178\ucd1d \uac00\uc785 \uc0ac\uc2e4\uc744 \uc54c\uac8c \u000d\u000a\ub41c \ub2f9\uc2dc \ubcd1\uc6d0 \uc0ac\ubb34\uad6d\uc7a5\uc774 \uc6a9\uc5ed\uc5c5\uccb4 \uc774\uc0ac\ub4e4\uc744 \ubd88\ub7ec\uc11c \ub178\uc870\ud30c\uad34\ub97c \uc9c0\uc2dc\ud588\uace0, \uac19\uc774 \ub300\ucc45\ud68c\uc758\u000d\u000a\ub97c \ud558\uba74\uc11c \u201c\ubbfc\ub178(\ubbfc\uc8fc\ub178\ucd1d) \ud0c8\ud1f4 3\ub2e8\uacc4 \ub2e8\uae30\uc804\ub7b5\u201d\uc774\ub780 \uac83\uc744 \uc791\uc131\ud558\uc600\ub2e4. \uadf8 \ub0b4\uc6a9\uc740 \uccab\uc9f8 \uc870\ud569\u000d\u000a\uc6d0 \uad6c\uc131 \ud2b9\uc131 \uc804\uc218\ud30c\uc545, \ub458\uc9f8 \uad6c\uc5ed\ubcc4 \uc774\uc288 \ucc44\uc9d1 \ud6c4 \ud0c8\ud1f4\uc720\ub3c4(\ud604\uc7a5\uad00\ub9ac\uc790 \ubc0f \uacbd\uc601\uc790 \uc804\ubc29\uc704 \ub178\ub825), \uc14b\uc9f8 \ube44\u000d\u000a\ud575\uc2ec\uc138\ub825 \uc911\uc2ec \ud754\ub4e4\uae30 \ubc0f \uc7ac\ubc30\uce58 \ud65c\uc6a9\uc774\ub2e4. \ub610 \ud55c\uad6d\ub178\ucd1d \ub178\uc870\uc758 \uc9d1\ud589\ubd80\ub97c \uad50\uccb4\ud558\uace0 \uc0c1\uae09\ub2e8\u000d\u000a\uccb4\ub3c4 \ubcc0\uacbd\ud55c\ub2e4\ub294 \uacb0\uc815\ub3c4 \ub0b4\ub838\ub2e4. \u000d\u000a\uc774 \uc804\ub7b5\uc5d0 \ub530\ub77c \uac01 \uad6c\uc5ed\ubcc4\ub85c \uc870\ud569\uc6d0 \ud0c8\ud1f4 \ubc0f \ud55c\uad6d\ub178\ucd1d \uac00\uc785 \uacf5\uc791\uc774 \ucd94\uc9c4\ub418\uc5c8\uace0 \ud0c8\ud1f4\ud558\u000d\u000a\uc9c0 \uc54a\uc73c\uba74 \uc77c\ud558\ub358 \uc790\ub9ac\uc5d0\uc11c \ub2e4\ub978 \uacf3\uc73c\ub85c \ucad3\uc544\ubcf4\ub0b4\uac70\ub098 \uc218\ub2f9\uc744 \uc8fc\uc9c0 \uc54a\ub294 \ub4f1 \ud0c4\uc555\uc774 \uac00\ud574\u000d\u000a\uc84c\ub2e4. \ud0c8\ud1f4\uc0c1\ud669\uc740 \ub9e4\uc8fc \uac04\ubd80\ud68c\uc758\uc5d0\uc11c \uc810\uac80\ud558\uace0 \uc2e4\uc2dc\uac04\uc73c\ub85c \ubcf4\uace0\ud558\uc600\uc73c\uba70, \u201c\ubd80\ub2f9\ub178\ub3d9\ud589\uc704\u000d\u000a\ub97c \uc758\uc2dd\ud558\uc5ec \ub178\ub178\ub300\uc751(\ub300\ub9bd)\uc73c\ub85c \uc9c4\ud589\u201d\ud558\ub77c\uace0 \ud558\uc5ec \ubd88\ubc95\ud589\uc704\ub97c \uc740\ud3d0\ud558\ub824\ub294 \uc2dc\ub3c4\ub3c4 \ud558\uc600\ub2e4. \u000d\u000a\ub178\uc870\ub294 \ub179\ucde8\ub85d\uacfc \ub178\uc870\ud30c\uad34 \uc9c0\uc2dc\uac00 \uc801\ud78c \uc5c5\ubb34\uc77c\uc9c0\ub97c \ud655\ubcf4\ud558\uc5ec 2016\ub144 9\uc6d4\uc5d0 \uc0ac\uce21\uc744 \ubd80\u000d\u000a\ub2f9\ub178\ub3d9\ud589\uc704\ub85c \uace0\uc18c\ud588\uc73c\ub098 \ub178\ub3d9\ubd80\ub294 \ud615\uc2dd\uc801\uc778 \uc218\uc0ac \ub05d\uc5d0 \ubb34\ud610\uc758 \ucc98\ubd84\ud588\uace0, \uacb0\uad6d 130\uc5ec\uba85\uc73c\u000d\u000a\ub85c \uc2dc\uc791\ud55c \ub178\uc870\ub294 40\uc5ec\uba85\uc758 \uc18c\uc218\ub178\uc870\uac00 \ub418\uba74\uc11c \uad50\uc12d\uad8c\uc744 \ubc15\ud0c8\ub2f9\ud558\uac8c \ub41c\ub2e4.\u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 56\u000d\u000a2) \ub178\uc870\ud30c\uad34\uc758 \ud750\ub984\uacfc \uad6c\uc870\u000d\u000a\ud55c\uad6d\uc758 \ub178\ub3d9\uc6b4\ub3d9\uc740 \ud574\ubc29\uc815\uad6d\uacfc \uc804\uc7c1\uc744 \uacaa\uc73c\uba74\uc11c \uc0ac\uc2e4\uc0c1 \uc808\uba78\ud558\uace0 \uae34 \ub2e8\uc808\uc744 \uac70\ucce4\ub2e4. \uc774 \u000d\u000a\uc810\uc744 \uace0\ub824\ud558\uc5ec 60\ub144\ub300 \uc774\ud6c4 \ud604\uc7ac\uae4c\uc9c0\uc758 \uc5ed\uc0ac\uc801 \ud750\ub984\uc744 \uc544\ub798\uc640 \uac19\uc774 \ub124 \uc2dc\uae30\ub85c \uad6c\ubd84\ud558\uc600\ub2e4.\u000d\u000a1960\ub144\ub300 \uc774\ud6c4 \uc0b0\uc5c5\ud654\ub294 \uc720\ud608\uc801 \ud14c\uc77c\ub7ec\ub9ac\uc998\uacfc \uc5b5\uc555\uc801 \ub178\ub3d9\ubc30\uc81c\ub97c \uc218\ubc18\ud558\uc600\ub294\ub370, \ud55c\uad6d \u000d\u000a\uc815\ubd80\ub294 \uc544\uc2dc\uc544\uc758 \ub2e4\ub978 \ub098\ub77c\ub4e4\uc5d0 \ube44\ud574\uc11c\ub3c4 \ub69c\ub837\ud558\uac8c \uce5c\uc790\ubcf8 \ubc18\ub178\ub3d9\uc801 \ud0dc\ub3c4\ub85c \uc77c\uad00\ud588\ub2e4\uace0 \u000d\u000a\ud3c9\uac00\ub41c\ub2e4. \uc815\ubd80\uc758 \uc5b5\uc555\uc801 \uc0b0\uc5c5\uc815\ucc45\uc740 \uc0ac\uc5c5\uc8fc\uc758 \ub178\uc870\ud30c\uad34\ub97c \ubd80\ucd94\uacbc\ub2e4. \uc0ac\uc5c5\uc8fc\ub294 \uc720\ub839\ub178\uc870 \u000d\u000a\uc124\ub9bd, \ub178\uc870\uacb0\uc131 \uc8fc\uccb4\uc5d0 \ub300\ud55c \ubd80\ub2f9\ud574\uace0, \ube14\ub799\ub9ac\uc2a4\ud2b8 \ub4f1\uc758 \uc218\ub2e8\uc744 \uc801\uadf9 \ud65c\uc6a9\ud558\uc5ec \ub178\ub3d9\uc790\ub4e4\u000d\u000a\uc758 \ub2e8\uacb0 \uc2dc\ub3c4\ub97c \uc640\ud574\uc2dc\ucf30\ub2e4. \u000d\u000a\uc774\ub7ec\ud55c \uc0c1\ud669\uc740 1987\ub144 \ub178\ub3d9\uc790\ub300\ud22c\uc7c1\uc744 \uac70\uce58\uba74\uc11c \ub2e4\uc18c \ubcc0\ud654\ub97c \uacaa\uc5c8\ub2e4. \uad6d\uac00\uc758 \uc5b5\uc555\uc740 \uc774\u000d\u000a\uc644\ub418\uc5c8\uace0, \ub178\uc870\ud30c\uad34 \uc804\ubb38\uac00\ub97c \ub3d9\uc6d0\ud558\uac70\ub098 \uad6c\uc0ac\ub300\ub97c \uc870\uc9c1\ud558\ub294 \ub4f1 \uc0ac\uc6a9\uc790\uc758 \ub178\uc870\ud30c\uad34 \uc2dc\ub3c4\u000d\u000a\uac00 \uacc4\uc18d\ub418\uc5c8\uc9c0\ub9cc \ub178\ub3d9\uc6b4\ub3d9\uc758 \uc131\uc7a5\uc138 \uc790\uccb4\ub97c \ub418\ub3cc\ub9b4 \uc218\ub294 \uc5c6\uc5c8\ub2e4.\u000d\u000a\uadf8\ub7ec\ub098 \uc790\uc5f0\ubc1c\uc0dd\uc801 \ud22c\uc7c1\uc758 \ud55c\uacc4\ub3c4 \ub69c\ub837\ud588\uc73c\uba70, \uc81c\ub3c4\uc801\uc73c\ub85c\ub3c4 \uad6d\uac00\uc758 \uac04\uc12d, \uc5f0\ub300\ud65c\ub3d9\uc758\u000d\u000a\uc2dc\uae30\uad6c\ubd84 \ud2b9\uc9d5 \uad00\ub828 \uc0ac\ub840\u000d\u000a\u524d\u53f2: 87\ub144 \uc774\uc804 \uad8c\uc704\uc8fc\uc758\uc801 \uad6d\uac00, \uc720\ud608\uc801 \ud14c\uc77c\ub7ec\ub9ac\uc998\uc5d0 \uae30\ubc18\ud55c\u000d\u000a\uc5b5\uc555\uc801 \uad00\ub9ac, \ub178\uace8\uc801 \ub178\uc870\ud30c\uad34\u000d\u000a\uccad\ud53c\ub178\uc870, \ub3d9\uc77c\ubc29\uc9c1,\u000d\u000a\uad6c\ub85c\ub3d9\ub9f9\ud30c\uc5c5 \ub4f1\u000d\u000a\ub178\ub3d9\ud1b5\uc81c \ubcc0\ud654\u000d\u000a1987~1997\u000d\u000a\ubbfc\uc8fc\ub178\uc870 \uc131\uc7a5 \uacac\uc81c\uc640 \uad6c\uc0ac\ub300\ub97c \ub300\uccb4\ud55c\u000d\u000a\uc0ac\uc124 \uc6a9\uc5ed\ud3ed\ub825\uc758 \ud655\uc0b0\u000d\u000a\ud604\ub300\ucc28 \u00b7 \ud604\ub300\uc911\uacf5\uc5c5,\u000d\u000a\ub300\uc6b0\uc870\uc120, \ucca0\ub3c4\ub178\uc870 \ub4f1\u000d\u000a\ube44\uc815\uaddc\ub178\ub3d9 \ud655\uc0b0\u000d\u000a1998~2010\u000d\u000a\uacbd\uc81c\uc704\uae30 \uc774\ud6c4 \uad6c\uc870\uc870\uc815 \uacfc\uc815\uc5d0\uc11c \uad6d\uac00\uae30\uad6c \uc5ed\ud560, \u000d\u000a\ube44\uc815\uaddc\ub178\ub3d9 \ud655\uc0b0\uacfc \ub178\uc870 \uc124\ub9bd \uc2dc\ub3c4\uc5d0 \ub300\uc751\ud55c\u000d\u000a\ub178\uc870\ud30c\uad34 \u000d\u000a\ud55c\uad6d\ud1b5\uc2e0 \uacc4\uc57d\uc9c1,\u000d\u000a\uc7ac\ub2a5\uad50\uc721, \uae30\ub96d\uc804\uc790,\u000d\u000a\ud604\ub300 \u00b7 \uae30\uc544\ucc28 \uc0ac\ub0b4\ud558\uccad, \u000d\u000a\ubc1c\uc804\uc0b0\uc5c5 \ub4f1\u000d\u000a\ubcf5\uc218\ub178\uc870 \uc774\ud6c4\u000d\u000a2011~\u000d\u000a\uc0ac\uc5c5\uc7a5\ub2e8\uc704 \ubcf5\uc218\ub178\uc870 \ud5c8\uc6a9 \uc774\ud6c4\u000d\u000a\uc0ac\uc6a9\uc790\uc758 \uc801\uadf9\uc801 \ub178\uc870 \ubd09\uc1c4\uc804\ub7b5 \uc2e4\ud589\u000d\u000a\ubc1c\ub808\uc624\uc804\uc7a5, KEC,\u000d\u000a\uc720\uc131\uae30\uc5c5, \ubcf4\uc26c\uc804\uc7a5,\u000d\u000a\uac11\uc744\uc624\ud1a0\ud14d, SJM \ub4f1\u000d\u000a<\ud45c 2> \ud55c\uad6d \ub178\uc870\ud30c\uad34\uc758 \uc2dc\uae30 \uad6c\ubd84\uacfc \uc8fc\uc694 \ud2b9\uc9d5","56 57\u000d\u000a\ubd88\ubc95\ud654\ub85c \uc778\ud574 \ub178\ub3d9\uc870\ud569 \ud65c\ub3d9\uc740 \uc0c1\ub2f9\ud55c \uc81c\uc57d\uc744 \ubc1b\uc558\ub2e4. \ud2b9\ud788 \uae30\uc5c5\ubcc4\ub178\uc870 \uccb4\uc81c\uc5d0\uc11c \ub178\uc870 \ub300\u000d\u000a\uaddc\ubaa8 \uc0ac\uc5c5\uc7a5\uc5d0\uc11c\ub294 \uc5b4\ub290\uc815\ub3c4 \ub178\uc0ac\uad00\uacc4\uc758 \uc548\uc815\ud654\uc640 \uae30\uc5c5 \ub0b4\ubd80\ud611\uc0c1\uc774 \uc790\ub9ac\uc7a1\uc740 \ubc18\uba74\uc5d0 \uc911\uc18c\u000d\u000a\uc0ac\uc5c5\uc7a5\uc5d0\uc11c\ub294 \ud611\uc0c1\ubcf4\ub2e4 \uc624\ud788\ub824 \ub178\uc870\uc758 \ubb34\ub825\ud654\ub97c \uc120\ud638\ud558\ub294 \uc0ac\uc6a9\uc790\ub4e4\uc758 \uc555\ub825\uc73c\ub85c \uc870\uc9c1\ub960\uc774 \u000d\u000a\ub454\ud654, \uac10\uc18c \uacbd\ud5a5\uc744 \ubcf4\uc600\ub2e4. \ubcf5\uc218\ub178\uc870\uac00 \uae08\uc9c0\ub41c \uac83\uc744 \uc774\uc6a9\ud558\uc5ec \uc77c\ubd80 \uc7ac\ubc8c\uc740 \uc774\ub978\ubc14 \u2018\uce5c\uc0ac\ub178\u000d\u000a\uc870\u2019\ub97c \uc774\uc6a9\ud55c \uc0ac\uc2e4\uc0c1\uc758 \u2018\ubb34\ub178\uc870\u2019 \uacbd\uc601\uc744 \uad00\ucca0\ud588\ub2e4.\u000d\u000a1997\ub144\uc758 IMF \uc678\ud658\uc704\uae30\ub294 \ub2e4\ub978 \uc5b4\ub5a4 \uacc4\uce35\ubcf4\ub2e4\ub3c4 \ub178\ub3d9\uc790\ub4e4\uc5d0\uac8c \ucee4\ub2e4\ub780 \uc0ac\ud68c\uacbd\uc81c\uc801 \ucda9\u000d\u000a\uaca9\uc744 \uc8fc\uba74\uc11c \u201887\ub144 \ub178\ub3d9\uccb4\uc81c\u2019\uc758 \ubcc0\ud654\ub97c \uac00\uc838\uc654\ub2e4. \uacbd\uc81c\uc704\uae30 \uc774\ud6c4 \uad6c\uc870\uc870\uc815\uacfc \uc774\uc5d0 \uc800\ud56d\ud558\u000d\u000a\ub294 \ub178\uc870\uc758 \uac08\ub4f1\uad6d\uba74\uc5d0\uc11c \uad6d\uac00\uae30\uad6c\uac00 \uc8fc\ub3c4\ud558\ub294 \ub178\uace8\uc801 \ub178\uc870\ud30c\uad34\uac00 \uc7ac\ub4f1\uc7a5\ud558\ub294\ub370 \uadf8 \ub300\ud45c\uc801\u000d\u000a\uc778 \uc0ac\ub840\uac00 \ubc14\ub85c \uc870\ud3d0\uacf5\uc0ac \ud30c\uc5c5\uc720\ub3c4 \uc0ac\uac74\uc774\ub77c \ud560 \uc218 \uc788\ub2e4.\u000d\u000a\uc704\uae30 \uc774\ud6c4 \ube44\uc815\uaddc\uc9c1\uc774 \uae09\uaca9\ud788 \uc99d\uac00\ud558\uace0 \ub178\ub3d9\uc758 \ubd88\uc548\uc815\uc131, \uad8c\ub9ac \uce68\ud574, \ucc28\ubcc4\uc758 \ubb38\uc81c\uac00 \uc2ec\u000d\u000a\uac01\ud574\uc9c0\uba74\uc11c \ube44\uc815\uaddc\uc9c1 \ub0b4\uc5d0\uc11c \uc0c8\ub85c\uc6b4 \uc870\uc9c1\ud654 \uc6c0\uc9c1\uc784\uc774 \ub4f1\uc7a5\ud558\uae30 \uc2dc\uc791\ud588\ub2e4. \uadf8\ub7ec\ub098 \ubc95\uc81c\ub3c4\u000d\u000a\uc758 \ucde8\uc57d\uc131\uacfc \uace0\uc6a9\uc758 \ubd88\uc548\uc815\uc131 \ub54c\ubb38\uc5d0 \ucd08\uae30 \ube44\uc815\uaddc\uc9c1\uc758 \ub178\uc870 \uc870\uc9c1\ud654 \uc2dc\ub3c4\ub294 \ub178\uc870 \uc124\ub9bd\ub2e8\uacc4\u000d\u000a\uc5d0\uc11c \ub300\ubd80\ubd84 \uc640\ud574\ub418\uac70\ub098 \uc7a5\uae30\ud22c\uc7c1\uc744 \ud558\uac8c \ub418\uc5c8\uace0, \uc77c\uc2dc\uc801\uc73c\ub85c \uc870\uc9c1\ud654\uc5d0 \uc131\uacf5\ud558\ub354\ub77c\ub3c4 \uc9c0\u000d\u000a\uc18d\uc801\uc778 \ub178\uc870\uc640\ud574 \uc2dc\ub3c4\uc5d0 \uacac\ub514\uc9c0 \ubabb\ud558\uace0 \uc18c\uc218\ud654\ub418\ub294 \uacbd\uc6b0\uac00 \ub300\ubd80\ubd84\uc774\uc5c8\ub2e4. \ube44\uc815\uaddc\uc9c1\uc758 \ub178\u000d\u000a\uc870 \uc870\uc9c1\ud654\uc640 \uc774\uc5d0 \ub300\uc751\ud55c \ub178\uc870\ud30c\uad34\ub294 \ubc14\uc57c\ud750\ub85c \ucca8\uc608\ud55c \uc804\uc120\uc774 \ub418\uc5c8\uace0, \uc774\ub294 2006\ub144 \ube44\uc815\u000d\u000a\uaddc\uc9c1 \ubcf4\ud638\uc785\ubc95\uc744 \ub458\ub7ec\uc2fc \uacf5\ubc29\uc73c\ub85c \uc774\uc5b4\uc84c\ub2e4. \u000d\u000a2011\ub144 \uae30\uc5c5\ubcc4 \ubcf5\uc218\ub178\uc870\uac00 \ud5c8\uc6a9\ub418\uace0 \ub3d9\uc2dc\uc5d0 \uad50\uc12d\ucc3d\uad6c \ub2e8\uc77c\ud654\uc81c\ub3c4\uac00 \ub3c4\uc785\ub418\uc5c8\ub2e4. \uc774\ubbf8 \u000d\u000a\uc0c1\uae09\ub2e8\uccb4\uc5d0\ub294 \ubcf5\uc218\ub178\uc870\uac00 \ud5c8\uc6a9\ub418\uc5b4 \uc788\uc5c8\uace0 \uc0ac\uc5c5\uc7a5\uc5d0\uc11c\ub3c4 \uc0b0\ubcc4\ub178\uc870\ub85c \uc778\ud574 \ubcf5\uc218\ub178\uc870 \uae08\uc9c0\u000d\u000a\uc870\ud56d\uc774 \uc0ac\uc2e4\uc0c1 \uc0ac\ubb38\ud654\ub418\uace0 \uc788\ub358 \uc2dc\uc810\uc774\uc5c8\ub2e4. \uc774 \uc81c\ub3c4\ub294 \ub178\uc0ac\uad00\uacc4\uc5d0\uc11c \uc0ac\uc6a9\uc790 \uc8fc\ub3c4\uc131\uc744 \uac15\u000d\u000a\ud654\ud558\uace0 \uae30\uc5c5\uad50\uc12d\uc744 \uac15\uc81c\ud558\ub294 \ud6a8\uacfc\uac00 \uc788\uc5c8\ub2e4. \uae30\uc874 \ub178\uc870\uc5d0\uc11c \uac08\ub77c\uc838 \ub098\uc628 \u2018\ubd84\ud560\ud615 \ubcf5\uc218\ub178\u000d\u000a\uc870\u2019\ub4e4\uc774 \ud3ed\uc99d\ud588\uc73c\uba70 \uc774\uc911 \uc0c1\ub2f9\uc218\uac00 \uae30\uc874 \ub178\uc870\uc758 \uad50\uc12d\uad8c \ubc15\ud0c8\uc744 \ud1b5\ud55c \ubb34\ub825\ud654 \ub0b4\uc9c0 \uc640\ud574\ub97c \u000d\u000a\uc758\ub3c4\ud55c \uac83\uc774\uc5c8\ub2e4. \u000d\u000a\uace0\uc6a9\ub178\ub3d9\ubd80\uc758 2014\ub144 \ubb38\uac74\uc5d0\uc11c\ub3c4 \u201c\ubcf5\uc218\ub178\uc870 \ud5c8\uc6a9 \uc774\ud6c4 \u2018\uce5c\uae30\uc5c5 \ub178\uc870\u2019 \uc124\ub9bd \uc9c0\uc6d0, \ud2b9\uc815\u000d\u000a\ub178\uc870 \uac00\uc785\u2027\ud0c8\ud1f4 \uc885\uc6a9 \ubc0f \ub178\ub178\uac04 \ubd88\ud569\ub9ac\ud55c \ucc28\ubcc4 \ub4f1 \ub2e4\uc591\ud55c \ud615\ud0dc\uc758 \ubd80\ub2f9\ub178\ub3d9\ud589\uc704\u201d\uac00 \ub300\ub450\ud558\u000d\u000a\uace0 \uc788\ub2e4\uace0 \ud55c\ub2e4. \uc774 \uc2dc\uae30 \uc0ac\uc6a9\uc790\uc640 \uad6d\uac00\uae30\uad00\uc774 \uacf5\ubaa8 \ub0b4\uc9c0 \uc720\ucc29\ud55c \uad11\ubc94\uc704\ud558\uace0 \uacf5\uaca9\uc801\uc778 \ub178\u000d\u000a\uc870\ud30c\uad34\uac00 \uc774\ub8e8\uc5b4\uc84c\uace0, \ubc1c\ub808\uc624\uc804\uc7a5\uacfc \uc720\uc131\uae30\uc5c5\uc774 \ub300\ud45c\uc801\uc778 \uc0ac\ub840\uc774\ub2e4. \u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 58\u000d\u000a3. \uc131\ucc30\uacfc \ud6c4\uae30: \uc9c0\uc5f0\ub41c \uc815\uc758\ub3c4 \uc815\uc758\uc778\uac00?\u000d\u000a\uc9c0\ub09c 2021\ub144 3\uc6d4 \ub9c8\uce68\ub0b4 \uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0 \ub178\uc870\ud30c\uad34 \uc0ac\uac74\uc5d0 \ub300\ud55c \uac80\ucc30 \uae30\uc18c\uac00 \uc774\ub8e8\uc5b4\uc84c\ub2e4. \u000d\u000a\uac80\ucc30\uc740 \uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0 \ub2f9\uc2dc \uc0ac\ubb34\uad6d\uc7a5, \uc0ac\ubb34\ud300\uc7a5, \ud30c\ud2b8\uc7a5, \uc6a9\uc5ed\uc5c5\uccb4\uc778 \ud0dc\uac00\ube44\uc5e0\uc758 \ub300\ud45c\uc774\uc0ac, \u000d\u000a\uc774\uc0ac, \ub2f9\uc2dc \uc18c\uc7a5\uacfc \ubc18\uc7a5 \ub4f1 \ubaa8\ub450 9\uba85\uc744 \ub178\uc870\ubc95 \uc704\ubc18\uc758 \ubd80\ub2f9\ub178\ub3d9\ud589\uc704\ub85c \uc7ac\ud310\uc5d0 \ub118\uacbc\ub2e4. \uc0ac\u000d\u000a\uac74\ubc1c\uc0dd \uc2dc\uc810\uc778 2016\ub144 6\uc6d4\ub85c\ubd80\ud130 4\ub144 9\uac1c\uc6d4\ub9cc\uc774\uace0 \ud604\uc7ac \uc9c4\ud589 \uc911\uc778 \ud615\uc0ac\uc7ac\ud310\uc774 \uc18d\ub3c4\ub97c \ub0b4\u000d\u000a\uc11c \ub0b4\ub144 5\uc6d4\ucbe4 \uc120\uace0\uac00 \ub098\uc628\ub2e4\uace0 \ud574\ub3c4 7\ub144\uc774 \uac78\ub9ac\ub294 \uac83\uc774\ub2e4. \uc774\ubbf8 \ub2f9\uc2dc\uc758 \ub178\ub3d9\uc790\ub4e4 \ub300\ubd80\ubd84\u000d\u000a\uc774 \uc815\ub144\uc744 \ub9de\uc544 \ubcd1\uc6d0\uc744 \ud1f4\uc9c1\ud55c \uc0c1\ud669\uc774\ub2e4. \uc138\ube0c\ub780\uc2a4\ubcd1\uc6d0\ubd84\ud68c\ub294 \uc774\uc81c 5\uba85\uc774 \ub0a8\uc544\uc11c \ub9e4\uc77c \ubcd1\u000d\u000a\uc6d0 \ub85c\ube44\uc5d0\uc11c \ub178\uc870\ud30c\uad34 \uc0ac\uacfc\uc640 \ud574\uacb0\uc744 \ucd09\uad6c\ud558\ub294 \ud53c\ucf13\ud305\uc744 \ud558\uace0 \uc788\uace0 \ubcd1\uc6d0 \uce21\uc740 \uc774\uac78 \uae08\uc9c0\uc2dc\u000d\u000a\ucf1c\ub2ec\ub77c\uace0 \uac00\ucc98\ubd84\uc744 \uac78\uc5c8\ub2e4.\u000d\u000a2022\ub144 5\uc6d4\uc5d0\ub294 \uc0bc\uc131 \uc5d0\ubc84\ub79c\ub4dc\uc5d0\uc11c \uc0ac\uce21\uc774 \ub178\uc870\uc640\ud574\ub97c \uc704\ud574 \ub9cc\ub4e0 \ub178\uc870 \uc124\ub9bd\uc774 \ubb34\ud6a8\ub77c\u000d\u000a\ub294 \ud56d\uc18c\uc2ec \ud310\uacb0\uc774 \ub098\uc654\ub2e4. \uc218\uc6d0\uace0\ub4f1\ubc95\uc6d0\uc740 \uae08\uc18d\ub178\uc870\uac00 \uc5d0\ubc84\ub79c\ub4dc\ub178\uc870\ub97c \uc0c1\ub300\ub85c \ub0b8 \ub178\ub3d9\uc870\u000d\u000a\ud569 \uc124\ub9bd\ubb34\ud6a8\ud655\uc778 \uc18c\uc1a1 \ud56d\uc18c\uc2ec\uc5d0\uc11c \uae30\uac01 \ud310\uacb0\uc744 \ub0b4\ub838\ub2e4. \uc5d0\ubc84\ub79c\ub4dc\ub178\uc870\ub294 \uc0bc\uc131\uc758 \ubb34\ub178\uc870 \uacbd\u000d\u000a\uc601\uc744 \uc720\uc9c0\ud558\uace0 \uc790\uc0dd\uc801 \ub178\uc870\uc758 \ud65c\ub3d9\uc744 \ubc29\ud574\ud560 \ubaa9\uc801\uc73c\ub85c \uc0ac\uc6a9\uc790\uce21\uc758 \uc804\uc801\uc778 \uacc4\ud68d\uacfc \uc8fc\ub3c4\ub85c \u000d\u000a\uc124\ub9bd\ub418\uc5c8\ub2e4\ub294 \uac83\uc774\uace0 \uc704\uc6d0\uc7a5\uc758 \uc120\uc815\uacfc \uad50\uccb4\ub098 \uc870\ud569\uc6d0 \uc22b\uc790\uc758 \uad00\ub9ac\ub3c4 \uc0ac\uc6a9\uc790\uc758 \uc9c0\uc2dc\u2027\uac1c\u000d\u000a\uc785\uc5d0 \ub530\ub77c \uc774\ub8e8\uc5b4\uc84c\ub2e4\ub294 \uac83\uc774\ub2e4. \uc5d0\ubc84\ub79c\ub4dc \ub178\ub3d9\uc790\ub4e4\uc774 \uad50\uc12d\uad8c\uc744 \ube8f\uae30\uace0 \ud574\uace0\ub2f9\ud55c\uc9c0 11\ub144\u000d\u000a\ub9cc\uc774\ub2e4.\u000d\u000a\uc9c0\ub09c 12\uc6d48\uc77c \uc11c\uc6b8\uc911\uc559\uc9c0\ubc95\uc740 \uc774\uba85\ubc15 \uc815\ubd80\uc758 \u2018\ub178\uc870\ud30c\uad34\u2019 \uacf5\uc791\uc5d0 \ub300\ud574 \uad6d\uac00 \ucc45\uc784\uc744 \uc778\u000d\u000a\uc815\ud558\uace0 \ud53c\ud574\ub178\uc870\uc5d0\uac8c \ubc30\uc0c1\ud558\ub77c\uace0 \ud310\uacb0\ud588\ub2e4.(\ub9e4\uc77c\ub178\ub3d9\ub274\uc2a4 2022.12.09.\uc790 \u201cMB\uc815\ubd80\ub294 \ub178\uc870\ud30c\uad34 \uac00\ud574\uc790\u201d \ubc95\uc6d0 \ucd5c\ucd08 \uc778\uc815)\u000d\u000a2018\ub144 \ud574\ub2f9 \ub178\ub3d9\uc870\ud569\ub4e4\uc774 \uad6d\uac00\ubc30\uc0c1\uccad\uad6c \uc18c\uc1a1\uc744 \ub0b8 \uc9c0 4\ub144 6\uac1c\uc6d4\ub9cc\uc758 \uc77c\uc774\ub2e4. \ubc95\uc6d0\uc740 \uad6d\u000d\u000a\uac00\uac00 \uac01 \ub178\uc870\uc5d0 2\uc5b56\ucc9c\ub9cc\uc6d0(\ubbfc\uc8fc\ub178\ucd1d 1\uc5b5\uc6d0, \uc804\uad50\uc870 7\ucc9c\ub9cc\uc6d0, \uacf5\ubb34\uc6d0\ub178\uc870 5\ucc9c\ub9cc\uc6d0, \uae08\uc18d\ub178\uc870 3\ucc9c\ub9cc\uc6d0, \uc11c\uc6b8\uad50\ud1b5\uacf5\uc0ac\ub178\uc870 1\ucc9c\ub9cc\uc6d0)\u000d\u000a\uc744 \ubc30\uc0c1\ud560 \uc758\ubb34\uac00 \uc788\ub2e4\uace0 \ud310\uacb0\ud588\ub2e4. \u000d\u000a\ubb34\uc218\ud788 \ub9ce\uc740 \ub178\uc870\ud30c\uad34 \uc0ac\ub840 \uc911\uc5d0 \uc77c\ubd80\uc5d0 \ubd88\uacfc\ud558\uc9c0\ub9cc \uc5b4\uca0c\ub4e0 \uc0ac\ud68c\uc801 \ubd84\ub178\ub97c \uc77c\uc73c\ud0ac \uc815\ub3c4\u000d\u000a\ub85c \u2018\uc9c0\ub098\uce58\uac8c\u2019 \ub178\uace8\uc801\uc774\uace0 \uba85\ubc31\ud55c \ub178\uc870\ud30c\uad34\uc5d0 \ub300\ud574\uc11c\ub294 \uc0ac\ubc95\uc801 \uc2ec\ud310\uc774 \uc774\ub8e8\uc5b4\uc9c0\uace0 \uc788\ub2e4\uace0 \u000d\u000a\ubcfc \uc218 \uc788\uc744\uae4c? \uc774\ub7ec\ud55c \ud310\uacb0\ub4e4\uc774 \ucd95\uc801\ub418\uba74 \ub178\uc870\ud30c\uad34 \ud589\uc704\uac00 \uadfc\uc808\ub418\uac70\ub098 \uc801\uc5b4\ub3c4 \ub354 \ubc88\uc131\ud558\u000d\u000a\uc9c0\ub294 \uc54a\uc744 \uac83\uc774\ub77c\uace0 \ub099\uad00\uc801\uc73c\ub85c \uc804\ub9dd\ud574\ub3c4 \uad1c\ucc2e\uc744\uae4c?","58 59\u000d\u000a\ucc3d\uc870\ucee8\uc124\ud305\uacfc \ud568\uaed8 \uae08\uc18d\ub178\uc870 \ub9cc\ub3c4\uc9c0\ubd80 \ub178\uc870\ud30c\uad34 \ub4f1\uc5d0 \uac00\ub2f4\ud588\ub358 \ud64d\ubaa8\uc528\ub294 \ud558\uc774\ud2b8\uc9c4\ub85c \ub178\u000d\u000a\uc0ac\uad00\uacc4 \ucc45\uc784\uc790, \ubd80\uc0ac\uc7a5\uc774 \ub418\uc5c8\ub2e4. 2022\ub144 \ub178\uc870\uac00 \ud30c\uc5c5\uc5d0 \ub3cc\uc785\ud558\uc790 \uae30\ub2e4\ub838\ub2e4\ub294 \ub4ef\uc774 \u2018\uad50\uc12d\u000d\u000a\ud68c\ud53c-\uc9d1\ub2e8\ud574\uace0-\uc190\ubc30 \uccad\uad6c-\ubcf5\uadc0\uc790 \uba74\ucc45\ud68c\uc720\u2019\uc758 \ub178\uc870\ud30c\uad34 \uc804\ub7b5\uc774 \ub4f1\uc7a5\ud588\uace0, \ud30c\uc5c5\uc740 \ub2e4\uc12f\ub2ec\u000d\u000a\uc744 \ub118\uae30\uba74\uc11c \ucd5c\uc545\uc758 \uac08\ub4f1\uc73c\ub85c \uc774\uc5b4\uc84c\ub2e4. \ucc3d\uc870\ucee8\uc124\ud305\uc740 \ucc98\ubc8c\ubc1b\uc558\uc9c0\ub9cc \ucc3d\uc870\uc2dd \ub178\uc870\ud30c\uad34\ub294 \u000d\u000a\uadf8 \ud6a8\uc6a9\uc131\uc744 \u2018\uac80\uc99d\u2019\ubc1b\uace0 \uc99d\uc2dd\ud558\uace0 \uc788\ub294 \uc148\uc774\ub2e4. \u000d\u000a2021\ub144 \ub178\uc870\uac00 \ubd80\ub2f9\ub178\ub3d9\ud589\uc704\ub85c \uace0\uc18c\ud55c SPC\uadf8\ub8f9\uc740 \uc5b4\ub5a4\uac00? 2017\ub144 \ub178\ub3d9\ubd80\ub294 \ud30c\ub9ac\ubc14\u000d\u000a\uac8c\ub728\ub97c \ubd88\ubc95\ud30c\uacac\uc73c\ub85c \uaddc\uc815\ud55c \ubc14 \uc788\uc9c0\ub9cc \uc774\ub978\ubc14 \u2018\uc0ac\ud68c\uc801 \ud569\uc758\u2019\ub77c\ub294 \uc774\ub984\uc73c\ub85c \uacfc\uc9d5\uae08\uc774 \uba74\uc81c\u000d\u000a\ub418\uace0 3\ub144 \ub0b4\uc5d0 \ub178\ub3d9\uc870\uac74\uc744 \uac1c\uc120\ud558\uae30\ub85c \ud588\ub2e4. \uadf8\ub9ac\uace0 3\ub144\uc774 \uc9c0\ub09c \ud6c4, \ub178\ub3d9\ubd80\uac00 \uc218\uc0ac\ud558\uace0 \uae30\u000d\u000a\uc18c\uc758\uacac\uc73c\ub85c \uac80\ucc30 \uc1a1\uce58\uae4c\uc9c0 \ud558\ub294 \uc640\uc911\uc5d0\ub3c4 SPC\uc758 \ub178\uc870\ud0c8\ud1f4 \uacf5\uc791\uc740 \uacc4\uc18d\ub418\uc5c8\ub2e4. \uacb0\uad6d \uc0ac\uc5c5\u000d\u000a\uc8fc\ub294 \uc9c0\ub09c 10\ub144\uac04\uc758 \ub178\uc870\ud30c\uad34 \uc0ac\uac74\ub4e4\uc744 \ud1b5\ud574, \ub178\uc870\ud30c\uad34\ub294 \uc27d\uac8c \ub4dc\ub7ec\ub098\uc9c0\ub3c4 \uc54a\uace0 \ucc98\ubc8c\ub418\uc9c0\u000d\u000a\ub3c4 \uc54a\ub294 \u2018\ud574\ubcfc\ub9cc\ud55c \uc0ac\uc5c5\u2019\uc774\ub77c\ub294 \uad50\ud6c8\uc744 \uc5bb\uc740 \uac83\uc740 \uc544\ub2cc\uac00?\u000d\u000a\ucd5c\uadfc\uc5d0\ub294 \ub300\ud1b5\ub839\uacfc \uc7a5\uad00\uc758 \ub178\uc870 \ub54c\ub9ac\uae30\uac00 \uc5f0\uc77c \uc5b8\ub860\uc5d0 \uc624\ub974\ub0b4\ub838\ub2e4. 14\uc2dc\uac04 \uc6b4\uc804\ud558\ub294 \ub178\u000d\u000a\ub3d9\uc790\ub4e4\uc774 \u201c\ub178\ub3d9\uadc0\uc871\u201d\uc774 \ub418\ub294\uac00 \ud558\uba74 \u201c\ubbfc\ud3d0\ub178\ucd1d \uc190\uc808 \ucd95\ud558\u201d\ud55c\ub2e4\ub294 \ub9d0\ub3c4 \ub098\uc628\ub2e4. \ud55c\uad6d\ub9cc\uc758 \u000d\u000a\uc77c\uc740 \uc544\ub2c8\uace0, \uc870\uc9c1\ub178\ub3d9\uc744 \uacf5\uaca9\ud558\uace0 \uace0\ub9bd\uc2dc\ud0a4\uba74\uc11c \ubbf8\uc870\uc9c1 \ub178\ub3d9\uc790\ub4e4\uc744 \uc6b0\uacbd\ud654\uc2dc\ud0a4\ub294 \uac83\uc740 \u000d\u000a\uc6b0\uc775 \ud3ec\ud4f0\ub9ac\uc998\uc758 \ubcf4\ud3b8\uc801\uc778 \uc804\ub7b5\uc774\uae30\ub3c4 \ud558\ub2e4. \uc624\ub7ab\ub3d9\uc548 \uc9c0\uc18d\ub41c \ub178\uc870\ud610\uc624\uc640 \ub178\uc870\ud30c\uad34\ub294 \uc774\u000d\u000a\uc81c \uc0c8\ub85c\uc6b4 \uc815\uce58\uc801 \uc804\ub7b5\uc73c\ub85c \ud658\uace8\ud0c8\ud0dc\ud558\uace0 \uc788\ub294\ub370, \uc774\ub807\uac8c \uc9c4\ud654\ud558\ub294 \ub178\uc870\ud30c\uad34\uc758 \ud750\ub984 \uc18d\uc5d0\u000d\u000a\uc11c \uacfc\uc5f0 \uc5b4\ub5bb\uac8c \uc774 \ubb38\uc81c\ub97c \ub2e4\ub8e8\uc5b4\uc57c \ud560\uc9c0 \ub2f5\uc744 \ucc3e\uae30\ub294 \uc27d\uc9c0 \uc54a\ub2e4. \u000d\u000a\uc624\ub7ab\ub3d9\uc548 \uc9c0\uc18d\ub41c \uad6d\uac00\uc758 \uc804\uc81c \uc544\ub798\uc5d0\uc11c \uc6d0\uc790\ud654\ub41c \uac1c\uc778\ub4e4\uc774 \uac01\uc790\ub3c4\uc0dd\uc5d0 \ubab0\ub450\ud558\ub294 \u2018\uc18c\uc6a9\u000d\u000a\ub3cc\uc774\uc758 \uc0ac\ud68c\u2019. \uadf8 \uc18d\uc5d0\uc11c \ub178\ub3d9\uc870\ud569\uc740 \ub4dc\ubb3c\uac8c \uc5f0\ub300\uc640 \uc2e4\uc9c8\uc801 \ubbfc\uc8fc\uc8fc\uc758\ub97c \ud45c\uc0c1\ud55c\ub2e4. \uae30\uc5c5\ubcc4 \u000d\u000a\ub178\uc870\uccb4\uc81c\uc758 \ud55c\uacc4\uac00 \ub69c\ub837\ud558\uace0 \uadf8 \uc548\uc5d0\uc11c \uc0c1\uce35 \uc911\uc2ec\uc758 \ub2f4\ud569\uc801 \uad00\uacc4\uac00 \ubb38\uc81c\uac00 \ub418\uae30\ub3c4 \ud558\uc9c0\ub9cc \u000d\u000a\uc5ec\uc804\ud788 \ub354 \uc544\ub798\ucabd\uc744 \uc870\uc9c1\ud558\uace0 \ub098\uc544\uac00 \uc0b0\uc5c5 \uc804\uccb4\ub97c \ub300\ubcc0\ud560 \uac00\ub2a5\uc131 \ub610\ud55c \ub0a8\uc544\uc788\ub2e4. \ubbfc\uc8fc\ub178\uc870 \u000d\u000a\uc6b4\ub3d9\uc740 \uad6d\uac00\uc5d0 \uae30\uc0dd\ud558\uac70\ub098 \ud0c8\uc8fc\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c, \ubbfc\uc8fc\uc801 \ud1b5\uc81c\uc640 \uc6b4\uc601\uc744 \uc9c0\ud5a5\ud560 \uc218\ubc16\uc5d0 \uc5c6\ub294 \u000d\u000a\uc9c4\ubcf4\uc801 \uc2dc\ubbfc\uc0ac\ud68c\uc758 \uc77c\ubd80\uc774\ub2e4. \uacb0\uad6d \uadf8\uac83\uc774 \ub178\uc870\ud610\uc624\uc640 \ub178\uc870\ud30c\uad34\uac00 \uc774\ub807\uac8c \ub048\uc9c8\uae30\uac8c \uacc4\uc18d\u000d\u000a\ub418\ub294 \uc774\uc720\uc774\uace0, \uadf8\ub7fc\uc5d0\ub3c4 \ub178\ub3d9\uc870\ud569\uc744 \ud3ec\uae30\ud560 \uc218 \uc5c6\ub294 \uc720\uc77c\ud55c \uc774\uc720\uc77c \uac83\uc774\ub2e4. \u000d\u000a\ud55c\uad6d\uc758 \ub178\uc870\ud30c\uad34, \uc6d0\uc778\uacfc \uacfc\uc81c\uc5d0 \ub300\ud55c \uae30\ucd08\uc5f0\uad6c : \uc720\ud615\ubcc4 \uc0ac\ub840\uc5f0\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c \u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","60 & \u000d\u000a\uae40\uc6b0\ucc3d / (\uc7ac)\uc232\uacfc\ub098\ub214, \uc778\uc7ac\uc591\uc131 \ud504\ub85c\uadf8\ub7a8, 2022\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 :\u000d\u000a\uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c \u000d\u000a1. \ubb38\uc81c\uc758\uc2dd\u000d\u000a\ucc98\uc74c \uc774 \uc5f0\uad6c\ub294 <\ud0c8\ud575\uacfc \uc815\uc758\ub85c\uc6b4 \uc804\ud658: \uc6d4\uc131\uc6d0\uc804\uc5d0\uc11c \ubc14\ub77c\ubcf8 \ud0c8\ud575\uc758 \uacfc\uc815\uacfc \uadf8 \ud55c\uacc4\ub97c \u000d\u000a\uc911\uc2ec\uc73c\ub85c>\ub77c\ub294 \uc81c\ubaa9\uc73c\ub85c 2021\ub144 \uc232\uacfc\ub098\ub214 \ud2b9\uc815\uc8fc\uc81c\uc5f0\uad6c\uc5d0 \uc120\uc815\ub418\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \ud604\uc7a5(\uacbd\uc8fc \uc6d4\u000d\u000a\uc131)\uc5d0 8\uac1c\uc6d4\uc5ec\ub97c \uba38\ubb3c\uba70 \ub2e4\uc591\ud55c \uc774\ud574\uad00\uacc4\uc790\ub4e4\uc744 \ub9cc\ub098 \uc778\ud130\ubdf0\ub97c \uc9c4\ud589\ud558\uba74\uc11c, \uc544\uc9c1\uc740 \ud0c8\ud575\uc774\u000d\u000a\ub77c\ub294 \uc815\ucc45\uc774 \uad6c\uccb4\uc801\uc774\uc9c0\ub3c4 \uc54a\uace0 \uacbd\uc8fc\uc5d0 \uc758\ubbf8\uc788\ub294 \uc601\ud5a5\uc744 \uc904 \uc815\ub3c4\ub85c \uc9c4\ud589\ub418\uc9c0 \uc54a\uc558\ub2e4\ub294 \uac83\u000d\u000a\uc744 \ud655\uc778\ud558\uc600\ub2e4. \uc774\ud6c4, \uc5f0\uad6c\uc790\uac00 \uc8fc\ubaa9\ud55c \uac83\uc740 \uc18c\uc218 \uc8fc\ubbfc\uc774 8\ub144\uc9f8 \uc774\uc8fc\ub97c \uc694\uccad\ud558\uba70 \ud22c\uc7c1\ud558\ub294 \u000d\u000a\uc774 \uc5f0\uad6c\ub294 \uacbd\ubd81 \uacbd\uc8fc\uc2dc \uc591\ub0a8\uba74\uc5d0 \uc704\uce58\ud55c \uc6d0\uc790\ub825\ubc1c\uc804\uc18c \ucd5c\uc778\uc811\uc9c0\uc5ed \uc8fc\ubbfc\ub4e4\uc774 \uc9c1\uba74\ud55c \uc18c\uc704 \ub290\ub9b0 \ud3ed\u000d\u000a\ub825\uc758 \ubb38\uc81c\ub97c \ud604\uc7a5\uc758 \uad00\uc810\uc744 \uc911\uc2ec\uc73c\ub85c \ucd94\uc801\ud55c \uc5f0\uad6c\uc774\ub2e4. \uc5f0\uad6c\uc790\ub294 2020\ub144 10\uc6d4\ubd80\ud130 2021\ub144 6\uc6d4\uae4c\u000d\u000a\uc9c0 8 \uac1c\uc6d4 \ub3d9\uc548 \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c \ucd5c\uc778\uc811\uc9c0\uc5ed\uc5d0\uc11c \ud604\uc7a5\uc870\uc0ac\ub97c \uc9c4\ud589\ud558\uace0, \uc8fc\ubbfc\ub4e4\uc744 \ube44\ub86f\ud55c \ub2e4\uc591\ud55c \u000d\u000a\uc774\ud574\uad00\uacc4\uc790\ub4e4\uc744 \ucc38\uc5ec\uad00\ucc30\ud558\uba74\uc11c, \uc8fc\ubbfc\ub4e4\uc774 \uc6d4\uc131\uc6d0\uc804\uc778\uc811\uc9c0\uc5ed \uc8fc\ubbfc\uc774\uc8fc\uc774\ub300\ucc45\uc704\uc6d0\ud68c\ub97c \ub9cc\ub4e4\uac8c \ub41c \u000d\u000a\uacfc\uc815, \uc8fc\ubbfc\ub4e4\uc758 \uc800\ud56d\ud589\ub3d9\uc758 \uacc4\uae30, \ubaa9\uc801\uc744 \uc0b4\ud3b4\ubcf4\uc558\ub2e4. \ud2b9\ud788 \ud658\uacbd \uad00\ub828 \uc2dc\ubbfc(\ucc38\uc5ec)\ubbfc\uc8fc\uc8fc\uc758 \ub17c\uc758\uac00 \uc9c1\u000d\u000a\uba74\ud558\ub294 \uc7c1\uc810\ub4e4\uc778 \uc704\ud5d8\ubb3c\uc9c8\uc744 \ub458\ub7ec\uc2f8\uace0 \ubc8c\uc5b4\uc9c0\ub294 \uacfc\ud559\uc131 \ub17c\uc7c1, \uc6d0\uc790\ub825\uc758 \ud2b9\uc131\uc0c1 \ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uc758 \u000d\u000a\uacf5\ub860\ud654, \uc548\uc804\uc2e0\ud654\uc758 \uc7a5\ubcbd \ub4f1\uacfc \uac19\uc740 \ub09c\uc810\ub4e4\uc744 \uc774 \ub17c\ubb38\uc740 \ucda9\uc2e4\ud55c \ud604\uc7a5\uc870\uc0ac \ubc0f \ucc38\uc5ec\uad00\ucc30\ub85c \uadf9\ubcf5\ud588\ub2e4. \u000d\u000a2022\ub144 \uc81c1\ud68c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \ubc1c\uad74 \ubc0f \uc2ec\uc0ac \uacfc\uc815\uc5d0\uc11c \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \uc774 \uc5f0\uad6c\ub97c \ud1b5\ud574 \ud604\u000d\u000a\uc7a5\uc5f0\uad6c\uc758 \uc804\ud615\uc744 \ubc1c\uacac\ud588\ub2e4\uace0 \uc758\uacac\uc758 \ud569\uce58\ub97c \ubcf4\uc558\ub2e4. \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \ud604\uc7a5\uc744 \ubc18\uc601\ud55c \uc88b\uc740 \uc5f0\uad6c\ub77c\ub294 \ucd94\u000d\u000a\uc0c1\uc744 \uad6c\uccb4\uc801 \uc131\uacfc\ub85c \ubcf4\uc5ec\uc900 \uc5f0\uad6c\ub77c\ub294 \uc810\uc5d0\uc11c \uc5f0\uad6c\uc790\uc5d0\uac8c \uac10\uc0ac\ub97c \uc804\ud55c\ub2e4. \uc774 \uc5f0\uad6c\ub97c \uc120\uc815\ud558\uac8c \ub41c \uc774\u000d\u000a\uc720\ub294 \ub2e4\uc74c\uacfc \uac19\ub2e4. \uc774 \uc5f0\uad6c\ub294 \uccab\uc9f8, \ud604\uc7a5\uc5f0\uad6c\uc758 \ubaa8\ubc94\uc774\ub77c\uace0 \ud560 \uc815\ub3c4\ub85c \ud604\uc7a5\uc5d0 \ubc00\ucc29\ud55c \uc5f0\uad6c\ubc29\ubc95\ub860\uc744 \u000d\u000a\uad6c\uc0ac\ud55c \uc810, \ub458\uc9f8, \ube44\uac00\uc2dc\uc801\uc774\uace0 \uc9c0\uccb4\ub41c \uc6d0\uc804\uc758 \ub290\ub9b0 \ud3ed\ub825\uc744 \ud559\uc220\uc801\uc73c\ub85c \uac1c\ub150\ud654\ud55c \uc810, \uc14b\uc9f8, \uc5f0\uad6c\uc9c0\u000d\u000a\uc6d0\uc744 \ubc1b\uc544 \uacb0\uacfc\ubb3c\uc744 \ub3c4\ucd9c\ud55c \uc774\ud6c4\uc5d0\ub3c4 \uc774 \uc5f0\uad6c\uc131\uacfc\ub97c \uc0ac\ud68c\uc801\uc73c\ub85c \uacf5\ub860\ud654\ud558\uae30 \uc704\ud574 \uc9c0\uc18d\uc801\uc73c\ub85c \uc678\ud654 \u000d\u000a\uc791\uc5c5\uc744 \uc9c4\ud589\ud588\ub2e4\ub294 \uc810\uc5d0\uc11c \uc6b0\uc218\ud55c \ud3c9\uac00\ub97c \ubc1b\uc558\ub2e4. \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \uc81c1\ud68c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \uc2ec\uc0ac\u000d\u000a\uc5d0\uc11c \ud604\uc7a5\uc131, \ub2f9\uc0ac\uc790\uc131, \ud65c\uc6a9\ub3c4 \uce21\uba74\uc744 \uc911\uc694\ud558\uac8c \uace0\ub824\ud588\uace0, \uc774 \uc5f0\uad6c\uac00 \ucda9\ubd84\ud788 \uc774 \uc694\uac74\uc744 \ucda9\uc871\ud588\ub2e4\uace0 \u000d\u000a\ud310\ub2e8\ud558\uc600\uae30\uc5d0 <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\ub85c \uc120\uc815\ud588\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720","60 61\u000d\u000a\ubaa8\uc2b5\uc774\uc5c8\uace0, \uadf8\ub4e4\uacfc \ud568\uaed8 \ub9e4\uc8fc \uc6d4\uc694\uc77c \uc9c1\uc811 \uc0c1\uc5ec\uc2dc\uc704\uc5d0 \ucc38\uc5ec\ud558\uae30\ub3c4 \ud558\uc600\ub2e4. \uc774\uc8fc\ub300\ucc45\uc704\ub97c \u000d\u000a\ub9cc\ub098 \uadf8\ub4e4\uc774 \uc2f8\uc6b0\uace0 \ubb38\uc81c\ub97c \uc81c\uae30\ud558\ub294 \u2018\ubc29\uc0ac\uc131\ubb3c\uc9c8\u2019\uc774 \ub208\uc5d0 \ubcf4\uc774\uc9c0\ub3c4 \uc54a\uace0, \uc554\uc774\ub098 \ubc31\ud608\ubcd1\u000d\u000a\uacfc \uac19\uc740 \ud53c\ud574\uc640\uc758 \uc778\uacfc\uc131\uc744 \uaddc\uba85\ud558\ub294 \uac83 \uc790\uccb4\uac00 \uc5b4\ub835\ub2e4\ub294 \uac83\uc744 \uc54c\uac8c \ub418\uc5c8\ub2e4. \uadf8\ub798\uc11c \uc774 \uc5f0\u000d\u000a\uad6c\ub294 \u2018\ub290\ub9b0 \ud3ed\ub825\u2019\uc73c\ub85c \ubd80\ub97c \uc218 \uc788\ub294 \uc704\ud5d8\uc744 \uc5b4\ub5bb\uac8c \uc8fc\ubbfc\ub4e4\uc774 \uc54c\uac8c \ub418\uc5c8\uace0, \uc774\uc8fc\ub300\ucc45\uc704\ub97c \ub9cc\u000d\u000a\ub4e4\uc5b4 \uc0dd\ubb3c\ud559\uc801 \uc2dc\ubbfc\uad8c\uc744 \uc7c1\ucde8\ud574 \ub098\uac00\uace0 \uc788\ub294\uc9c0\ub97c \ubd84\uc11d\ud558\uc600\ub2e4.\u000d\u000a\ud55c\ud3b8\uc73c\ub85c\ub294 \uc774\uc8fc\ub300\ucc45\uc704\ub97c \ube44\ub86f\ud558\uc5ec \uc6d4\uc131\uc6d0\uc804\uc744 \ube44\ud310\ud558\ub294 \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc740 \uc790\uc2e0\ub4e4\uc758 \ud22c\uc7c1\u000d\u000a\uc5d0 \ub300\ud574 \u2018\ud0c8\ud575(\uc6b4\ub3d9)\uc740 \uc544\ub2c8\ub2e4\u2019\ub77c\uace0 \uc778\uc2dd\ud558\uace0 \uc788\ub2e4\ub294 \ud765\ubbf8\ub85c\uc6b4 \ud604\uc0c1\uc744 \ucc38\uc5ec\uad00\ucc30\uc744 \ud1b5\ud574 \uc54c\u000d\u000a\uac8c \ub418\uc5c8\ub2e4. \ub450 \ubc88\uc9f8 \uc7a5\uc740 \uc6d4\uc131 \ud575\ubc1c\uc804\uc18c \ucd5c\uc778\uc811 \uc9c0\uc5ed \uc8fc\ubbfc\ub4e4\uc774 \uc774\uc8fc \ub300\ucc45\uc704\ub97c \ub9cc\ub4e4\uc5b4 \u2018\ud0c8\u000d\u000a\ud575\u2019\uc774 \uc544\ub2cc \u2018\uc774\uc8fc\ub300\ucc45\u2019\uc744 \uc694\uad6c\ud558\ub294 \uc774\uc720\uc640 \uadf8 \uc758\ubbf8\ub97c \ubd84\uc11d\ud558\uc600\ub2e4. \ud2b9\ud788 \uc6b8\uc0b0 \ubd81\uad6c \uc5f0\ub300\uc790\uac00 \u000d\u000a\u2018\ud0c8\ud575\u2019\uc774\ub098 \u2018\uc6d4\uc131 2,3,4\ud638\uae30 \ud3d0\uc1c4\u2019\uac00 \ub2f4\uae34 \uc870\ub07c\ub97c \uc785\uace0 \uc62c \ub54c, \uc774\uc5d0 \ub300\ud574 \uc608\ubbfc\ud558\uac8c \ubc18\uc751\ud568\u000d\u000a\uc73c\ub85c\uc368 \ub9cc\ub4e4\uc5b4\uc9c4 \uae34\uc7a5\uc758 \uc21c\uac04\ub4e4\uc774 \uc774 \uc5f0\uad6c\ub97c \uc2dc\uc791\ud55c \uacc4\uae30\uc774\ub2e4. \ub300\ucc45\uc704\ub294 \ud06c\uac8c \uc138 \uac00\uc9c0 \uac01\u000d\u000a\uae30 \ub2e4\ub978 \uc704\ud5d8\uacbd\uad00\uc744 \ub9cc\ub4e4\uba74\uc11c \uc774\uc8fc\ub97c \uc694\uad6c\ud558\uac8c \ub418\uc5c8\ub294\ub370, \uc0dd\uacc4\ubb38\uc81c, \ud0c8\ud575\uc758 \ud604\uc2e4\uac00\ub2a5\uc131 \uadf8\u000d\u000a\ub9ac\uace0 \uac70\ub300\ub2f4\ub860\uc778 \ud0c8\ud575(\uc6b4\ub3d9\uc9c4\uc601)\uc5d0\uc11c \uc8fc\ub958\uac00 \ub418\uc9c0 \ubabb\ud55c \ub85c\uceec\uc758 \ubaa9\uc18c\ub9ac\ub77c \uadf8\uac83\uc774\ub2e4. \uc774 \uc9c0\uc5ed\uc740 \u000d\u000a\ud55c\uc218\uc6d0\uacfc \uc6d4\uc131 \ud575\ubc1c\uc804\uc18c\ub85c\ubd80\ud130 \uacbd\uc81c\u00b7\uc0ac\ud68c\uc801\uc73c\ub85c \uc885\uc18d\ub418\uc5b4 \uc788\uc73c\uba70, \uc8fc\ubbfc\ub4e4\uc758 \ub300\ubd80\ubd84\uc740 \ud575\u000d\u000a\ubc1c\uc804\uc18c \ub178\ub3d9\uc790\uc774\uac70\ub098 \uc2dd\ub2f9 \ub4f1\uc744 \uc6b4\uc601\ud558\ub294 \ub4f1 \ud55c\uc218\uc6d0\uc744 \uc9c1\uc811\uc801\uc73c\ub85c \ube44\ud310\ud560 \uc218 \uc5c6\ub294 \uc0c1\ud669\uc5d0 \u000d\u000a\ub193\uc5ec\uc788\ub2e4. \uadf8\ub7ec\ub098 \ub300\ucc45\uc704\ub294 \u2018\uc0dd\uacc4\u2019\ub97c \uac71\uc815\ud558\uba74\uc11c\ub3c4 \u2018\uc548\uc804\ud558\uac8c \uc0b4 \uad8c\ub9ac\u2019\ub97c \uc7c1\ucde8\ud558\uae30 \uc704\ud574 \ud0c8\u000d\u000a\ud575\uacfc \ucc2c\ud575\uc758 \uc774\ubd84\ubc95\uc73c\ub85c \uc124\uba85\ud558\uae30 \uc5b4\ub824\uc6b4 \uc704\ud5d8\uacbd\uad00\uc744 \ud1a0\ub300\ub85c \u2018\uc774\uc8fc\u2019\ub97c \uc694\uccad\ud558\uc600\ub2e4. \ub610\ud55c, \u000d\u000a\ud604\uc7ac\uae4c\uc9c0 \u2018\ud0c8\ud575\u2019\uc774 \uac00\uc9c0\ub294 \uc758\ubbf8\uc640 \ubc94\uc704\uc5d0 \ub300\ud55c \uc0ac\ud68c\uc801 \ud569\uc758\uac00 \ubd80\uc7ac\ud55c \uc0c1\ud669\uc5d0\uc11c \ud0c8\ud575\uc774 \uc758\u000d\u000a\ubbf8\ud558\ub294 \ubc14\ub294 \ud575\ubc1c\uc804\uc18c\ub97c \uc810\uc9c4\uc801\uc73c\ub85c \uc904\uc5ec\ub098\uac00\uace0 \ub300\uc548\uc744 \uc81c\uc2dc\ud558\ub294 \uac83\uc5d0 \ubd88\uacfc\ud558\ub2e4. \uadf8\ub7ec\ub098 \u000d\u000a\uc8fc\ubbfc\ub4e4\uc5d0\uac8c \ud0c8\ud575\uc774\ub780 \ud3d0\ub85c\uc640 \ud575\ud3d0\uae30\ubb3c \ubcf4\uad00 \ubc0f \ucc98\ub9ac\uae4c\uc9c0 \ud3ec\ud568\ud574\uc57c \ud558\ub294 \ubb38\uc81c\ub85c \ud604\uc7ac\uae4c\uc9c0 \u000d\u000a\uc774 \ubb38\uc81c\ub97c \ud574\uacb0\ud560 \uae30\uc220\uc774\ub098 \ub300\uc548\uc774 \ubd80\uc7ac\ud558\uba70 \ub204\uad6c\ub3c4 \ucc45\uc784\uc77c \uc218 \uc5c6\ub294 \uc704\ud5d8\uc778 \uc148\uc774\ub2e4. \uc774\ub7ec\u000d\u000a\ud55c \uc0c1\ud669\uc5d0\uc11c \uc8fc\ubbfc\ub4e4\uc774 \ub9c9\uc5f0\ud55c \ud0c8\ud575\uc774 \uc544\ub2cc \uad6c\uccb4\uc801\uc778 \u2018\uc774\uc8fc\u2019\ub97c \uc694\uccad\ud558\ub294 \uac83\uc740 \uc720\uc77c\ud558\uba74\uc11c\u000d\u000a\ub3c4 \ud604\uc2e4\uc801\uc778 \uc694\uad6c\uc774\ub2e4. \ub05d\uc73c\ub85c, \ud0c8\ud575\uc6b4\ub3d9\uc740 \uc9c0\uae08\uae4c\uc9c0 \ud575\ubc1c\uc804\uc18c\ub97c \ubc18\ub300\ud558\ub294 \u2018\ubc18\ud575\u2019 \ud639\uc740 \ud0c8\u000d\u000a\ud575\uc744 \uc8fc\ub41c \uba54\uc2dc\uc9c0\ub85c \uc0ac\ud68c\uc5d0 \ub358\uc838\uc654\uc73c\ub098, \uc9c0\uc5ed\uc8fc\ubbfc\uc758 \ub2e4\ub978 \uc694\uad6c\ub098 \ubaa9\uc18c\ub9ac\uac00 \uc8fc\ub958\uac00 \ub41c \uc801\uc740\u000d\u000a\uc5c6\ub2e4. \uc8fc\ubbfc\uc5d0\uac8c \ud0c8\ud575\uc6b4\ub3d9(\uc9c4\uc601)\uc774 \uc678\uce58\ub294 \u2018\ud0c8\ud575\u2019\uc774\ub780 \u2018\uc0ac\ub78c\uc774 \ube60\uc838 \uc788\ub294 \uac70\ub300\ub2f4\ub860\u2019\uc5d0 \ubd88\uacfc\ud558\u000d\u000a\uba70 \ud2b9\ud788 \uc8fc\ubbfc\ub4e4\uc774 \ub9e4\uc77c, \uc77c\uc0c1\uc5d0\uc11c \ub9c8\uc8fc\ud558\ub294 \ud53c\ud3ed, \uac74\uac15\ubb38\uc81c \ub4f1\uc744 \ube44\ub86f\ud55c \uad6c\uccb4\uc801\uc774\uace0 \ub2e4\uc591\u000d\u000a\ud55c \ubb38\uc81c\ub4e4\uc740 \uac70\uc758 \ubc30\uc81c\ub418\uc5b4\uc654\ub2e4.\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 : \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 62\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \ubc18\ud575/\ud0c8\ud575\uacfc \ucc2c\ud575\uc774\ub77c\ub294 \uc774\ubd84\ubc95\uc801\uc778 \uad6c\ubd84\uc73c\ub85c \uc774\ud574\ud558\uae30 \uc5b4\ub824\uc6b4 \uc8fc\ubbfc\ub4e4\uc758 \uc704\u000d\u000a\ud5d8\uacbd\uad00\uc5d0 \ucd08\uc810\uc744 \ub9de\ucd94\uc5b4 \ubd84\uc11d\ud588\ub2e4\ub294 \ub370 \uc758\ubbf8\uac00 \uc788\ub2e4. \ud2b9\ud788 \uc9c0\uc5ed\uc5d0\uc11c \uc8fc\uccb4\uc801\uc778 \uc704\ud5d8\uacbd\uad00\uc744 \u000d\u000a\uc0dd\uc0b0\ud558\uc5ec, \ub54c\ub85c\ub294 \ucc2c\ud575\uc9c4\uc601\uc774\ub098 \uad6d\uac00 \ud639\uc740 \ud0c8\ud575(\uc6b4\ub3d9)\uc9c4\uc601\uacfc\uc758 \uc704\ud5d8\uacbd\uad00\uacfc \ubd80\ub52a\ud788\uba70 \u2018\uc774\uc8fc\ub300\u000d\u000a\ucc45\u2019\uc744 \uc904\uae30\ucc28\uac8c \uc694\uad6c\ud558\uace0 \uc788\ub2e4\ub294 \uac83\uc744 \ud1b5\ud574, \uc8fc\ubbfc\ub4e4\uc774 \ucc98\ud55c \ub9e5\ub77d, \uc870\uac74\uacfc \uadf8\ub4e4\uc774 \uc0dd\uc0b0\ud55c \uc704\u000d\u000a\ud5d8\uacbd\uad00\uc744 \uc774\ud574\ud558\uace0 \uacf5\uac10\ud560 \ud544\uc694\uac00 \uc788\uc74c\uc744 \uc81c\uc2dc\ud558\uc600\ub2e4.\u000d\u000a2. \uc8fc\uc694 \ub0b4\uc6a9: \ubcf4\uace0\uc11c \uc694\uc57d\u000d\u000a1) \uc11c\ub860\u000d\u000a2021\ub144 12\uc6d4\uc744 \uae30\uc900\uc73c\ub85c, \uad6d\ub0b4\uc5d0\ub294 24\uae30\uc758 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c(\uc774\ud558 \uc6d0\uc804)5)\uac00 \uac00\ub3d9 \uc911\uc774\uba70, \u000d\u000a\ud55c\uad6d\uc740 \uc138\uacc4\uc5d0\uc11c \uc5ec\uc12f \ubc88\uc9f8\ub85c \ub9ce\uc740 \uc6d0\uc804\uc744 \ubcf4\uc720\ud558\uace0 \uc788\ub2e4. \uc624\uc9c1, \ubbf8\uad6d(94), \ud504\ub791\uc2a4(56), \u000d\u000a\uc911\uad6d(50), \ub7ec\uc2dc\uc544(38)\uc640 \uc77c\ubcf8(33)\ub9cc\uc774 \uc804\uc138\uacc4\uc5d0\uc11c \uc6b0\ub9ac\ub098\ub77c\ubcf4\ub2e4 \ub9ce\uc740 \uc6d0\uc804\uc744 \uac00\uc9c0\uace0 \uc788\u000d\u000a\ub2e4.World Nuclear Association, 2021 \ud2b9\ud788 \uc6d0\uc804\uc774 \uc788\ub294 \ud55c\uad6d\uc758 \ub124 \uac1c \uc9c0\uc5ed\uc740 \uc804\uc138\uacc4\uc5d0\uc11c\ub3c4 \uc190\uc5d0 \uaf3d\u000d\u000a\ud790 \uc815\ub3c4\ub85c \ub2e4\uc218\ud638\uae30\uc758 \uc6d0\uc804\uc774 \uc790\ub9ac \uc7a1\uace0 \uc788\uc73c\uba70, \u2019\uc6d0\uc804\ubc00\uc9d1\ub3c4\uac00 \uac00\uc7a5 \ub192\ub2e4\u2018\ub294 \uc624\uba85\uc744 \uc5bb\uace0 \u000d\u000a\uc788\ub2e4. \uce90\ub098\ub2e4 Bruce \uc6d0\uc804\ub2e8\uc9c0\ub294 \ucd1d 8\uac1c\ub85c \uc804 \uc138\uacc4\uc5d0\uc11c \uac00\uc7a5 \ub9ce\uc740 \uc6d0\uc804\uc774 \ud55c \uc9c0\uc5ed\uc5d0 \ubc00\uc9d1\u000d\u000a\ud574 \uc788\uace0, \uc6b0\ub9ac\ub098\ub77c\uc758 \uace0\ub9ac \uc6d0\uc804(7\uac1c), \uc6b8\uc9c4\uacfc \uc601\uad11(6\uae30) \uadf8\ub9ac\uace0 \uc6d4\uc131(5\uae30)\uc774 \uadf8 \ub4a4\ub97c \uc774\uc5c8\u000d\u000a\ub2e4.(IAEA PRIS \ud1b5\uacc4\uc790\ub8cc \ucc38\uace0) \u000d\u000a\uc6b0\ub9ac\ub098\ub77c\ub294 \uc6d0\uc804\ub9cc \ub9ce\uc740 \uac83\uc774 \uc544\ub2c8\ub77c \uc6d0\uc804 \ubc18\uacbd 30km \uc774\ub0b4\uc5d0 \uac70\uc8fc\ud558\ub294 \uc778\uad6c\ub3c4 \ucd1d 529\u000d\u000a\ub9cc \uba85\uc73c\ub85c, \uad6d\ubbfc 10\uba85 \uc911 1\uba85 \uc774\uc0c1\uc774 \uc6d0\uc804\uc73c\ub85c\ubd80\ud130 30km \uc774\ub0b4\uc5d0 \uac70\uc8fc\ud558\uace0 \uc788\ub2e4.(\uc624\ub9c8\uc774\ub274\uc2a4, \u000d\u000a2021-09-23) \uace0\ub9ac \uc6d0\uc804\uc740 322\ub9cc \uba85, \uc6d4\uc131 109\ub9cc \uba85, \uc601\uad11 14\ub9cc \uba85, \uc6b8\uc9c4 6\ub9cc \uba85\uc73c\ub85c, \uc77c\ubcf8 \ud6c4\u000d\u000a\ucfe0\uc2dc\ub9c8 \uc6d0\uc804 30km \uc778\uadfc\uc5d0 \uc0ac\ub294 \uc8fc\ubbfc \uc57d 17\ub9cc \uba85\uc5d0 \ube44\uad50\ud558\uba74 \uace0\ub9ac\ub294 20\ubc30, \uc6d4\uc131\uc740 5\ubc30\uac00\u000d\u000a\ub7c9 \ub9ce\ub2e4. \ub610\ud55c \uc6d0\uc804 \uc8fc\ubcc0\uc5d0 \uac70\uc8fc\ud558\ub294 \uc8fc\ubbfc\ub4e4\uc740 \uc801\uc9c0 \uc54a\uc740 \uc0ac\uace0\uc640 \uc6d0\uc778\ubd88\uba85\uc758 \uc9c8\ubcd1\uc5d0 \uc2dc\ub2ec\u000d\u000a\ub824 \uc624\uae30\ub3c4 \ud558\uc600\ub2e4. 1989\ub144 \uc601\uad11 \uc6d0\uc804\uc5d0\uc11c \uc77c\ud558\ub294 \uc9c1\uc6d0\uc774 \ub450 \ucc28\ub840\ub098 \uc720\uc0b0\uc744 \ud558\uba74\uc11c \uc815\ubd80\ub294 \u000d\u000a\uc6d0\uc804\uc758 \uac74\uac15 \uc601\ud5a5\uc5d0 \ub300\ud55c \uc5ed\ud559\uc870\uc0ac\ub97c \uc218\ud589\ud558\uc600\ub2e4.Kim, Jeong-Min, et al, 2018 \ucd5c\uadfc \uace0\ub9ac \uc6d0\uc804 \uadfc\u000d\u000a5\u3000\uc774 \uae00\uc5d0\uc11c\ub294 \u2018\ud575\ubc1c\uc804\u2019 \ub610\ub294 \u2018\ud575\ubc1c\uc804\uc18c\u2019 , \u2018\ud0c8\ud575\u2019 \uc774\ub780 \uc6a9\uc5b4 \ub300\uc2e0 \u2018\uc6d0\uc790\ub825 \ubc1c\uc804\u2019 , \u2018\uc6d0\uc790\ub825 \ubc1c\uc804\uc18c ( \uc6d0\uc804 )\u2019 \uc640 \u2018\ud0c8\uc6d0\uc804\u2019\u000d\u000a\uc774\ub780 \uc6a9\uc5b4\ub97c \uc0ac\uc6a9\ud560 \uac83\uc774\ub2e4 . \ud575\ubd84\uc5f4\uc744 \ud1b5\ud574 \uc5d0\ub108\uc9c0\ub97c \uc5bb\uae30 \ub54c\ubb38\uc5d0 \ud575\ubc1c\uc804\uc18c (Nuclear power plant) \ub85c \ubd80\ub974\ub294 \uac83\uc774 \u000d\u000a\uacfc\ud559\uc801\uc73c\ub85c \ud0c0\ub2f9\ud558\uc9c0\ub9cc , \uc5ec\uc804\ud788 \ud55c\uad6d\uc758 \ubc95\uacfc \ud589\uc815\uccb4\uacc4\uc5d0\uc11c\ub294 \u2018\uc6d0\uc804\u2019 \ud639\uc740 \u2018\uc6d0\uc790\ub825\ubc1c\uc804\uc18c\u2019 \ub77c \ubd80\ub974\uba70 , \ud2b9\ud788 \ud55c\uad6d\uc0ac\ud68c\u000d\u000a\uc5d0\uc11c\ub3c4 \u2018\ud575\ubc1c\uc804\u2019 \ubcf4\ub2e4\ub294 \u2018\uc6d0\uc804\u2019 \uc73c\ub85c \ubd80\ub974\ub294 \uac83\uc774 \uc77c\ubc18\ud654\ub418\uc5b4 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4 .","62 63\u000d\u000a\ucc98\uc5d0 \uc0ac\ub294 \uade0\ub3c4\uc758 \uac00\uc871\ub4e4\ub3c4 \uc6d0\uc804\uc774 \uc8fc\ubbfc \uac74\uac15\uc5d0 \ubbf8\uce58\ub294 \uc601\ud5a5\uc5d0 \ub300\ud574 \ube44\ud310\ud558\uace0 \uad6d\ub0b4\uc5d0\uc11c\ub294 \u000d\u000a\ucc98\uc74c\uc73c\ub85c \ud55c\uc218\uc6d0\uc744 \uc0c1\ub300\ub85c \uc18c\uc1a1\uc744 \uc9c4\ud589\ud558\uba74\uc11c \uc810\ucc28 \uc0ac\ud68c\uc801\uc778 \ubb38\uc81c\ub85c \uc778\uc2dd\ub418\uace0 \uc788\ub2e4. \uc774\ub4e4\u000d\u000a\uc758 \uac1c\uc778\uc18c\uc1a1\uc740 \uc6d0\uc804 \ubd80\uadfc\uc5d0 \uc0b4\uba74\uc11c \uac11\uc0c1\uc120 \uc554\uc5d0 \uac78\ub9b0 600\uba85\uc774 \ub118\ub294 \uc8fc\ubbfc\ub4e4\uc758 \uacf5\ub3d9\uc18c\uc1a1\uc73c\u000d\u000a\ub85c \uc774\uc5b4\uc84c\ub2e4. \uc774\ub807\ub4ef, \ud604\uc7ac\uae4c\uc9c0 \ub300\ud55c\ubbfc\uad6d\uc740 \uc6d0\uc804\uc744 \ud655\ub300\ud558\uc5ec \uc5d0\ub108\uc9c0\uc758 \uc548\uc815\uc801\uc778 \uacf5\uae09\uc744 \ub2ec\u000d\u000a\uc131\ud558\uae30 \uc704\ud55c \uc5d0\ub108\uc9c0\uc815\ucc45\uc744 \uc911\uc694\ud558\uac8c \uc0dd\uac01\ud558\uc600\uc9c0\ub9cc, \uc0c1\ub300\uc801\uc73c\ub85c \uc8fc\ubbfc\uac74\uac15\uc774\ub098 \uc6d0\uc804\uc758 \uc704\ud5d8\u000d\u000a\uc5d0 \ub300\ud574 \uc9c0\uc801\ud558\ub294 \uc8fc\ubbfc\ub4e4\uc758 \ubaa9\uc18c\ub9ac\uc5d0\ub294 \ub35c \uad00\uc2ec\uc744 \uac00\uc838\uc654\ub2e4.\u000d\u000a\uc774 \uc5f0\uad6c\uc5d0\uc11c\ub294 \uc6d0\uc804 \ubb38\uc81c\uc5d0 \ube44\ud310\uc801 \ubaa9\uc18c\ub9ac\ub97c \ub0b4\ub294 \uc6d4\uc131\uc6d0\uc804 \ucd5c\uc778\uc811\uc9c0\uc5ed \uc8fc\ubbfc\ub4e4\uc5d0 \uc8fc\ubaa9\u000d\u000a\ud55c\ub2e4. \uc774\ub4e4\uc740 2011\ub144 \ud6c4\ucfe0\uc2dc\ub9c8 \uc0ac\uace0\uc640 2012\ub144 \uad6d\ub0b4 \uc6d0\uc804 \ubd80\ud488 \ube44\ub9ac \uc0ac\uac74 \uc774\ud6c4 \uc774\uc8fc\ub97c \uc694\u000d\u000a\uad6c\ud558\uae30 \uc704\ud574 \uc6d4\uc131\uc6d0\uc804\uc778\uc811\uc9c0\uc5ed \uc8fc\ubbfc\uc774\uc8fc\ub300\ucc45\uc704\uc6d0\ud68c(\uc774\ud558 \ub300\ucc45\uc704)\ub97c \ub9cc\ub4e4\uc5c8\ub2e4. \uc8fc\ubbfc\ub4e4\uc740 \uc218\uc2ed \u000d\u000a\ub144 \ub3d9\uc548 \u2018\uc544\ubb34 \ubb38\uc81c \uc5c6\uc774\u2019 \uc6d0\uc804 \uadfc\ucc98\uc5d0\uc11c \uc0b4\uc544\uc624\ub2e4\uac00 \uc5b8\uc81c, \uc65c, \uc5b4\ub5bb\uac8c \ub300\ucc45\uc704\ub97c \ub9cc\ub4e4\uc5c8\uc744\u000d\u000a\uae4c? \uc8fc\ubbfc\ub4e4\uc740 \ubcf4\uc774\uc9c0\ub3c4 \uc54a\uace0 \ub0c4\uc0c8\ub3c4 \ub098\uc9c0 \uc54a\uc9c0\ub9cc, \uc790\uc2e0\ub4e4\uc758 \ubab8\uacfc \ud658\uacbd\uc5d0 \ud53c\ud574\uc758 \uc871\uc801\uc744 \ub0a8\u000d\u000a\uae30\ub294 \uc774 \ud3ed\ub825\uc5d0 \uc624\ub79c \uae30\uac04 \uc2dc\ub2ec\ub824\uc654\uc9c0\ub9cc \uc774\ub7f0 \uc0ac\uc2e4\uc5d0 \ub300\ud55c \uc0ac\ud68c\uc801 \uc2b9\uc778\uc744 \uc5bb\uc9c0 \ubabb\ud55c \uac74 \ubb3c\u000d\u000a\ub860\uc774\uace0 \uc2a4\uc2a4\ub85c\ub3c4 \uc778\uc2dd\ud558\uc9c0 \ubabb\ud55c \ucc44 \uc0b4\uc544\uc654\ub2e4. \ud55c\ud3b8, \uadf8\ub4e4\uc740 \uc774\uc8fc\uc694\uad6c\ub97c \uc911\uc2ec\uc73c\ub85c \ud604\uc7ac\uae4c\u000d\u000a\uc9c0 \uc2dc\uc704, \uc6b4\ub3d9\uc744 \uc9c4\ud589\ud558\uc600\ub294\ub370, \uc774\ub7ec\ud55c \uc694\uad6c\ub294 \ud55c\uc218\uc6d0\uacfc \uc6d0\uc804\uc744 \uc9c0\uc9c0\ud558\ub294 \uc8fc\ubbfc\ub4e4\uc758 \uc694\uad6c\uc640\u000d\u000a\ub294 \ub300\ucc99\uc810\uc5d0 \uc130\uc9c0\ub9cc, \ubb34\uc5c7\ubcf4\ub2e4 \uc8fc\ub958\uc758 \ud0c8\ud575\uc6b4\ub3d9(\uc11c\uc6b8\u00b7\uc218\ub3c4\uad8c \uc911\uc2ec\uc758 \ud0c8\ud575\ub2e8\uccb4)\uc774 \uc678\uce58\ub294 \uba54\uc2dc\uc9c0(\ubc18\ud575\u00b7\ud0c8\ud575)\u000d\u000a\uc640\ub3c4 \ub2ec\ub790\ub2e4. \uc774\ub7ec\ud55c \ud604\uc0c1\uc740 \u2018\uadf8\ub4e4\uc740 \uc65c \ud0c8\ud575\uc774 \uc544\ub2cc \uc774\uc8fc\ub97c \uc694\uad6c\ud558\ub294\uac00\u2019\ub77c\ub294 \ub450 \ubc88\uc9f8 \uc5f0\uad6c \u000d\u000a\uc9c8\ubb38\uc73c\ub85c \uc774\uc5b4\uc84c\ub2e4. \uc989, \uc774 \uc5f0\uad6c\uc5d0\uc11c\ub294 \ubcf4\uc774\uc9c0 \uc54a\ub294 \ud3ed\ub825\uc758 \uc591\uc0c1\ub4e4\uc744 \ub4dc\ub7ec\ub0b4\uae30 \uc704\ud55c \uc8fc\ubbfc\u000d\u000a\uc6b4\ub3d9\uc758 \ud615\uc131\uacfc\uc815\uacfc \uadf8\ub4e4\uc758 \ud65c\ub3d9\uc774 \uc8fc\ub958 \ud0c8\ud575\uc6b4\ub3d9\uacfc\ub294 \ub2e4\ub974\uac8c \uc65c \u2018\uc774\uc8fc\u2019\ub97c \uc911\uc2ec\uc5d0 \ub450\uace0 \uc788\u000d\u000a\ub294\uc9c0\ub97c \u2018\uc774\uc911\uc758 \ube44\uac00\uc2dc\ud654\u2019\ub77c\ub294 \ud2c0\uc744 \ud1b5\ud574 \ubd84\uc11d\ud560 \uac83\uc774\ub2e4. \ub610\ud55c, \ud55c\uc218\uc6d0\uc744 \ube44\ub86f\ud558\uc5ec \uc9c0\uc5ed\uc0ac\u000d\u000a\ud68c\uc5d0\uc11c \uc6d0\uc804\uc744 \uc9c0\uc9c0\ud558\ub294 \ub2e4\uc218\uc758 \uc9d1\ub2e8\uc774 \ud53c\ud574\ub97c \uac00\uc2dc\ud654\ud558\ub824\ub294 \uc774\ub4e4\uc758 \ub178\ub825\uc5d0 \uc5b4\ub5bb\uac8c \ubc18\uc751\u000d\u000a\ud558\uace0 \ub300\uc751\ud574\uc654\ub294\uc9c0\ub3c4 \ud568\uaed8 \ubd84\uc11d\ud560 \uac83\uc774\ub2e4.\u000d\u000a2) \uc774\ub860\uacfc \uc5f0\uad6c\ubc29\ubc95\u000d\u000a\ub86d \ub2c9\uc2a8(2020)\uc740 \uacf5\uc2dd\uc801\uc73c\ub85c \uc778\uc815\ubc1b\uc9c0 \ubabb\ud558\ub294 \uc704\ud5d8\uc5d0 \uc2dc\ub2ec\ub9ac\ub294 \uacf5\ub3d9\uccb4\ub294, \uc790\uc2e0\ub4e4 \uc0c1\ud669\uc5d0 \u000d\u000a\ub300\ud55c \uc678\ubd80\uc778\ub4e4\uc758 \uc774\ud574\ub97c \uc704\ud55c \ud3ed\ub113\uc740 \uc138\uacc4\uc801 \ud22c\uc7c1\uc5d0 \ub6f0\uc5b4\ub4e4\uc5b4 \uc790\uc2e0\ub4e4\uc758 \ub450\ub824\uc6c0\uc744 \ub110\ub9ac \uc54c\u000d\u000a\ub9ac\ub294 \uac83\uc774 \uc911\uc694\ud558\ub2e4\uace0 \uac15\uc870\ud588\ub2e4. \uc704\ud5d8\uacfc \uc704\uae30\uc5d0 \ucc98\ud588\ub2e4\ub294 \uac83\uc758 \uc758\ubbf8\ub97c \u201c\uc2dc\uac04\uc801\uc73c\ub85c\ub294 \uc989\u000d\u000a\uac01\uc801\uc774\uace0 \uacf5\uac04\uc801\uc73c\ub85c\ub294 \ud3ed\ubc1c\uc801\uc774\uac70\ub098 \uadf9\uc801\uc778, \ubc14\ub85c \ub208\uc55e\uc5d0\uc11c \ucda9\uaca9\uc801\uc73c\ub85c \ud3bc\uccd0\uc9c0\ub294 \uc0ac\uac74\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 : \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 64\u000d\u000a\uc774\ub098 \ud589\ub3d9\u201d(\ub2c9\uc2a8, 2020: 18)\uc73c\ub85c \ud310\ub2e8\ud560 \uac83\uc774 \uc544\ub2c8\ub77c \ub290\ub9b0 \ud3ed\ub825\uc758 \uad00\uc810\uc5d0\uc11c \uc7ac\uace0\ud558\uace0 \uc7ac\uad6c\uc131\ud574\u000d\u000a\uc57c \ud55c\ub2e4\uace0 \uac15\uc870\ud558\uc600\ub2e4. \uc774\ub294 \u201c\uacbd\ud569\ud558\ub294 \uc9c8\ubcd1\u201dcontested illness\uc774\ub77c\ub294 \ud45c\ud604\uc744 \ud1b5\ud574 \ud53c\ud574\ub098 \uc544\u000d\u000a\ud514\uc740 \ub2e8\uc21c\ud788 \uc9c0\ubc30\uc801 \uc5ed\ud559 \ud328\ub7ec\ub2e4\uc784\uc5d0 \uc758\ud574 \uacb0\uc815\ub418\ub294 \uac83\uc774 \uc544\ub2c8\ub77c\ub294 \ud544 \ube0c\ub77c\uc6b4(2007: 11)\uc758 \uc9c0\u000d\u000a\uc801\uacfc\ub3c4 \uc77c\uce58\ud55c\ub2e4. \ub2c9\uc2a8\uc740 \uc9c8\ubcd1\uc758 \uc6d0\uc778\uc744 \ubc1d\ud788\ub294 \uc791\uc5c5\uc740 \uc5ec\uc804\ud788 \ud3ed\ub113\uc740 \ud569\uc758\ub97c \uc5bb\uae30\ubcf4\ub2e4\ub294 \u000d\u000a\ub17c\uc7c1 \ud639\uc740 \ub17c\ub780controversy\uc744 \uc218\ubc18\ud55c\ub2e4\uace0 \ub9d0\ud558\uba70, \ud53c\ud574\ub97c \uc785\uc99d \ubc1b\uc9c0 \ubabb\ud558\ub294 \ud53c\ud574\uc790(\uc8fc\ubbfc)\ub4e4\uc774 \u000d\u000a\uc2dc\ubbfc-\uacfc\ud559-\uc804\ubb38\uac00-\ud65c\ub3d9\uac00\ub4e4\uacfc \ud568\uaed8 \ub300\uc548\uc801 \uc5ed\ud559 \ud328\ub7ec\ub2e4\uc784\uc744 \uad6c\ucd95\ud558\ub294 \uac83\uc774 \uc911\uc694\ud558\ub2e4\uace0 \u000d\u000a\uac15\uc870\ud588\ub2e4. \uae40\uc885\uc601\u00b7\uae40\ud76c\uc724(2013: 305)\ub3c4 \u201c\uacfc\ud559\uc758 \uc911\ub9bd\uc131\uc774\ub77c\ub294 \ub3c4\uad6c\ub85c \uacfc\ud559\uc801 \ubd88\ud655\uc2e4\uc131\uc774\ub77c\ub294 \u000d\u000a\uc758\uc2ec\uc744 \uc0dd\uc0b0\ud558\uc5ec \ubb38\uc81c\uac00 \ub418\ub294 \uc9c8\ubcd1\uc744 \u2018\ud0c8\ubb38\uc81c\ud654\u2019\ud588\ub2e4.\u201d\ub77c\uace0 \ube44\ud310\ud558\uba74\uc11c \uac74\uac15, \uc9c8\ubcd1\uc744 \ub458\ub7ec\u000d\u000a\uc2fc \ubb38\uc81c\ub294 \ub2e4\uc591\ud55c \uc815\uce58\uc801\u00b7\uacbd\uc81c\uc801\u00b7\ubc95\ub960\uc801\u00b7\uacfc\ud559\uc801 \uad00\uacc4 \uc0ac\uc774\uc5d0\uc11c \ub204\uad6c\uc758 \ubaa9\uc18c\ub9ac\uac00 \ubc30\uc81c\ub418\uace0 \u000d\u000a\uac15\ud654\ub418\ub294\uc9c0\ub97c \ud30c\uc545\ud558\ub294 \uac83\uc774 \uc911\uc694\ud568\uc744 \uc9c0\uc801\ud588\ub2e4. \ub86d \ub2c9\uc2a8\uacfc \ud544 \ube0c\ub77c\uc6b4\uc774 \ub290\ub9b0 \ud3ed\ub825, \uacbd\ud569\u000d\u000a\ud558\ub294 \uc9c8\ubcd1\uc774\ub77c\uace0 \uac01\uae30 \ub2e4\ub974\uac8c \ubd80\ub974\uc9c0\ub9cc, \uacb0\uad6d \uacf5\ud1b5\uc73c\ub85c \uc9c0\uc801\ud558\ub294 \uac83\uc740 \ud53c\ud574\uc758 \uaddc\uc815\uacfc \uc815\uc758 \u000d\u000a\uc790\uccb4\uac00 \uc0ac\ud68c\uc801\uc73c\ub85c \uad6c\uc131\ub418\uace0 \uacbd\uc7c1\ud55c\ub2e4\ub294 \uc810\uc774\ub2e4. \uc774 \uc5f0\uad6c\uc5d0\uc11c\ub294 \ub86d \ub2c9\uc2a8\uc774 \uc815\uc758\ud55c \ub290\ub9b0 \ud3ed\u000d\u000a\ub825Slow Violence\uc744 \uc911\uc2ec\uc73c\ub85c \uc774\uc8fc\ub300\ucc45\uc704\uac00 \uc5b4\ub5bb\uac8c \ube44\uac00\uc2dc\uc801\uc774\uace0 \ud53c\ud574\uac00 \ucc9c\ucc9c\ud788 \uc9c4\ud589\ub418\uc5b4 \uc778\u000d\u000a\uacfc\uad00\uacc4\ub97c \ud30c\uc545\ud558\uae30 \uc5b4\ub824\uc6b4 \ubd88\ud655\uc2e4\uc131uncertainty\uc5d0 \ub300\ud55c \ud655\uc2e4\ud55ccertain \uae68\ub2ec\uc74c\uc744 \uc5bb\uc5b4, \ub290\ub9b0 \ud3ed\u000d\u000a\ub825\uc744 \uc0ac\ud68c\uc5d0 \ub4dc\ub7ec\ub0c8\ub294\uc9c0 \ubd84\uc11d\ud560 \uac83\uc774\ub2e4. \uc774\ub54c, \ud55c\uc218\uc6d0\uacfc \uc9c0\uc5ed \ub0b4 \uc6d0\uc804\uc744 \uc9c0\uc9c0\ud558\ub294 \uc774\ud574\uad00\u000d\u000a\uacc4\uc790\ub4e4\uc740 \uc704\ud5d8\uacfc \ub290\ub9b0 \ud3ed\ub825\uc5d0 \uac00\uc2dc\uc131\uc744 \ubd80\uc5ec\ud558\ub824\ub294 \ub300\ucc45\uc704\uc758 \ud22c\uc7c1\uc744 \uc5b4\ub5bb\uac8c \ubd80\uc778, \ubd80\uc815 \ud639\u000d\u000a\uc740 \ube44\ud310\ud558\uc5ec \ub2e4\uc2dc \uc774\ub4e4\uc758 \ub178\ub825\uc744 \ube44\uac00\uc2dc\uc801\uc774\uace0 \ubd88\ud655\uc2e4\ud558\uba70 \uacb0\uad6d \uc544\ubb34\uac83\ub3c4 \uc544\ub2cc (\uc815\uc0c1) \uc0c1\u000d\u000a\ud0dc\ub85c \ub418\ub3cc\ub9ac\ub824\uace0 \ud588\ub294\uc9c0 \ud568\uaed8 \uc81c\uc2dc\ud560 \uac83\uc774\ub2e4.\u000d\u000a\uc774\uc8fc\ub300\ucc45\uc704\ub294 \ud64d\ubcf4\uad00 \uc55e\uc5d0 \uc124\uce58\ud55c \ub18d\uc131\uc7a5\uc5d0\uc11c 300m\ub3c4 \ucc44 \ub5a8\uc5b4\uc9c0\uc9c0 \uc54a\uc740 \uc6d4\uc131\uc6d0\uc804 \uc815\u000d\u000a\ubb38\uae4c\uc9c0 \ub9e4\uc8fc \uc6d4\uc694\uc77c\uacfc \ubaa9\uc694\uc77c\uc5d0 \uc0c1\uc5ec\ub97c \ub04c\uc5c8\ub2e4.6) \uadf8\ub4e4\uc740 \u2018\uc774\uc8fc\ub9cc\uc774 \uc0b4\uae38\uc774\ub2e4\u2019\ub77c\ub294 \ubb38\uad6c\uac00 \u000d\u000a\uc801\ud78c \ub178\ub780\uc0c9 \uc870\ub07c\ub97c \uc785\uace0 \ubc14\ud034\uac00 \ub2ec\ub9b0 \uad00\uacfc \uc0c1\uc5ec\ub97c \ub04c\uc5c8\uace0, \uc0ac\ub78c\uc774 \ub9ce\uc740 \ub0a0\uc5d0\ub294 \ud575\ud3d0\uae30\ubb3c \u000d\u000a\ub4dc\ub7fc\ud1b5 \ubaa8\ud615\uacfc \ud33b\ub9d0\ub3c4 \ud568\uaed8 \uc0ac\uc6a9\ud588\ub2e4. \uc624\uc804 8\uc2dc 20\ubd84, \uc6d4\uc131\uc6d0\uc804 \uc815\ubb38\uae4c\uc9c0 \uace7\uac8c \ub09c \uc655\ubcf5 2\u000d\u000a\ucc28\uc120 \ub3c4\ub85c \uc704\uc5d0 \ud55c\uc218\uc6d0 \uc9c1\uc6d0\ub4e4\uc758 \uac1c\uc778\ucc28\ub7c9\uacfc \ub300\ud615\ud1b5\uadfc\ubc84\uc2a4, \uacf5\uc0ac \ucc28\ub7c9 \ub4f1\uc774 \ub9ce\uc544\uc84c\ub2e4. \uadf8 \u000d\u000a\ubc14\uc05c \ub3c4\ub85c \uc704\ub97c \uc7a0\uae50\uc774\ub098\ub9c8 \ucc28\uc9c0\ud558\ub294 \uac74 \ub300\ucc45\uc704\uac00 \ub044\ub294 \uc0c1\uc5ec\uc640 \uad00, \uadf8\ub9ac\uace0 \uc2a4\ud53c\ucee4\uc5d0\uc11c \ub098\u000d\u000a\uc624\ub294 \uc0c1\uc5ec\uace1\uc774\uc5c8\ub2e4. \uc801\uac8c\ub294 \ub300\uc5ec\uc12f \uba85, \ub9ce\uac8c\ub294 \uc2a4\ubb34 \uba85\uc774 \ub118\ub294 \uc0ac\ub78c\ub4e4\uc774 \uc0c1\uc5ec, \uad00, \ub4dc\ub7fc\ud1b5\u000d\u000a6\u3000\uc6d4\uc694\uc77c\uc5d0\ub294 \uc6b8\uc0b0 \ubd81\uad6c \uc8fc\ubbfc\ub4e4\uc774 , \ubaa9\uc694\uc77c\uc5d0\ub294 \uacbd\uc8fc \uc2dc\ub0b4\uc640 \uc591\ub0a8\uba74 \uc8fc\ubbfc\ub4e4\uc774 \ubc29\ubb38\ud558\uc5ec \ud568\uaed8 \uc0c1\uc5ec\uc2dc\uc704\uc5d0 \ucc38\uc5ec\ud558\uc600\u000d\u000a\ub2e4 . \uadf8\ub7ec\ub098 2021 \ub144 3 \uc6d4 \uc911\uc21c\ubd80\ud130\ub294 \uc774\uc8fc\ub300\ucc45\uc704 \uc8fc\ubbfc\ub4e4\uc774 \ub18d\uc0ac\uc77c\ub85c \ubc14\ube60\uc838 \ud604\uc7ac\ub294 \ub9e4\uc8fc \uc6d4\uc694\uc77c\uc5d0\ub9cc\uc9d1\uc911\ud558\uc5ec \uc0c1\uc5ec\u000d\u000a\uc2dc\uc704\ub97c \uc9c4\ud589\ud558\uace0 \uc788\ub2e4 .","64 65\u000d\u000a\uacfc \ub2e4\uc591\ud55c \uad6c\ud638\uac00 \uc801\ud78c \ud33b\ub9d0\uc744 \uc190\uc5d0 \ub4e0 \ucc44, \ub18d\uc131\uc7a5\uc5d0\uc11c \uc815\ubb38 \uc55e\uae4c\uc9c0 300\ubbf8\ud130 \uac00\ub7c9\uc758 \ub3c4\ub85c \u000d\u000a\uc704\ub97c \uac77\uae30 \uc2dc\uc791\ud588\ub2e4. \uc2dc\uc704 \ud589\ub82c \ub4a4\uc5d0\ub294 \ucd9c\uadfc\uae38\ub85c \uae38\uac8c \ub298\uc5b4\uc120 \ucc28\ub4e4\uc774 \uc788\uc5c8\uace0, \ub54c\ub85c\ub294 \uadc0\ucc2e\u000d\u000a\ub2e4\ub294 \ub4ef, \ub54c\ub85c\ub294 \uc774\ub4e4\uc758 \uc2dc\uc704\uac00 \ub9c8\uc74c\uc5d0 \ub4e4\uc9c0 \uc54a\ub294\ub2e4\ub294 \ub4ef \u2018\ube75\u2019 \uacbd\uc801\uc744 \uc6b8\ub9ac\uba70 \ube60\ub974\uac8c \uadf8\ub4e4\uc744 \u000d\u000a\uc55e\uc9c8\ub7ec \uac00\ub294 \ucc28\ub7c9\ub3c4 \uc788\uc5c8\ub2e4. \ub300\ucc45\uc704 \uc2dc\uc704\ub300\uc5d0\uac8c \ud5c8\uc6a9\ub41c \uacf5\uac04\uc740 \uace0\uc791 \uc815\ubb38 \ubc14\ub85c \uc55e\uae4c\uc9c0\uc600\uace0 \u000d\u000a\uadf8\uacf3\uc744 \ub3cc\uace0 \ub098\uba74, \uc2dc\uc704\uac00 \ub05d\ub098\uae30\ub97c \uae30\ub2e4\ub9ac\ub358 \ucc28\ub4e4\uc774 \ubcc4\uc77c \uc5c6\ub2e4\ub294 \uc2dd\uc73c\ub85c \ub0b4\uc9c8\ub7ec \uc815\ubb38\uc744 \ud1b5\u000d\u000a\uacfc\ud588\ub2e4. \uc5f0\uad6c\uc790\ub294 2020\ub144 11\uc6d4\ubd80\ud130 2021\ub144 6\uc6d4 \ub9d0\uae4c\uc9c0 \ub9e4\uc8fc \uc0c1\uc5ec\uc2dc\uc704\uc5d0 \ucc38\uc5ec\ud558\uc5ec \uadf8\ub4e4\u000d\u000a\uacfc \ub77c\ud3ec\ub97c \uc313\uace0, \ucc38\uc5ec\uad00\ucc30\uc744 \uc218\ud589\ud558\uc600\ub2e4. \uc0c1\uc5ec\uc2dc\uc704\ub97c \ud3ec\ud568\ud558\uc5ec \ub300\ucc45\uc704\uac00 \uc5b8\uc81c \ub9cc\ub4e4\uc5b4\uc838\uc11c \u000d\u000a\uc5b4\ub5a0\ud55c \ud65c\ub3d9\ub4e4\uc744 \ud574\uc654\ub294\uc9c0\ub97c \ud30c\uc545\ud558\uae30 \uc704\ud574 \uc774 \uc5f0\uad6c\uc5d0\uc11c\ub294 \uc9c8\uc801 \uc5f0\uad6c\ub97c \ud65c\uc6a9\ud558\uc600\ub2e4. \ub610\ud55c, \u000d\u000a\uc774\uc8fc\ub300\ucc45\uc704\uac00 \uc5b8\uc81c, \uc5b4\ub5bb\uac8c \ub9cc\ub4e4\uc5b4\uc838\uc11c \uc5b4\ub5a0\ud55c \ud65c\ub3d9\ub4e4\uc744 \ud574\uc654\ub294\uc9c0\ub97c \ub4e3\uace0 \uc774\ud574\ud558\ub294 \uc791\uc5c5\uc740 \u000d\u000a\uc9c1\uc811 \ucc38\uc5ec\ud558\uace0 \ud65c\ub3d9\ud588\ub358 \uc8fc\ubbfc\ub4e4\uc758 \uad6c\uc220\uc0acoral history\ub97c \ud1b5\ud574 \uc7ac\uad6c\uc131\ud558\uc600\ub2e4. \ud2b9\ud788 \uc5ed\uc0ac\uc778\ub958\ud559\u000d\u000a\uc5d0\uc11c \uad6c\uc220\uc0ac\ub780 \uad6c\uc220\uc790\ub97c \uc5ed\uc0ac\uc758 \uc8fc\uccb4\ub85c \uc778\uc815\ud558\uace0 \uad6c\uc220\uc790\uc758 \uc2dc\uac01\uc73c\ub85c\ubd80\ud130 \uc0ac\ud68c\uc640 \uad6c\uc870, \uc5ed\u000d\u000a\uc0ac\ub97c \uc774\ud574\ud558\ub824\ub294 \u2018\ubc11\uc73c\ub85c\ubd80\ud130\uc758 \uc5ed\uc0ac\u2019 \ub4e4\uc5ec\ub2e4\ubcf4\uae30\uc758 \uc77c\ud658\uc73c\ub85c, \uc774\ub294 \uace7 \uae30\uc874 \uc5ed\uc0ac\uc5d0 \ub300\ud55c \u000d\u000a\ub300\uc548\uc801\uc778 \uc5ed\uc0ac\ub97c \uc4f0\ub294 \uacfc\uc815\uc774\ub2e4.(\uc724\ud0dd\ub9bc\u00b7\ud568\ud55c\ud76c, 2006:37) \ub300\ucc45\uc704\uc5d0 \ucc38\uc5ec\ud558\ub294 \uc8fc\ubbfc\ub4e4\uc758 \uc804 \uc0dd\uc560\ub97c \ud30c\u000d\u000a\uc545\ud558\uc9c0\ub294 \ubabb\ud588\uc9c0\ub9cc, \uadf8\ub4e4\uc774 \uc5b8\uc81c, \uc65c \uc774 \ub9c8\uc744\uc5d0 \uc0b4\uac8c \ub418\uc5c8\uace0, \uc5b4\ub5a4 \uc0ac\uac74\uc774\ub098 \uacc4\uae30\ub97c \ud1b5\ud574 \ub300\u000d\u000a\ucc45\uc704\ub97c \ub9cc\ub4e4 \uc218\ubc16\uc5d0 \uc5c6\uc5c8\ub294\uc9c0\ub97c \uc911\uc2ec\uc73c\ub85c \uc9c8\ubb38\ud588\ub2e4. \uc774 \uacfc\uc815\uc740 \ub9c8\uce58 \ub208\uc5d0 \ubcf4\uc774\uc9c0 \uc54a\ub294 \ubc29\uc0ac\u000d\u000a\uc131 \ubb3c\uc9c8\uc744 \uc8fc\ubbfc\ub4e4\uc774 \ub290\ub9b0 \ud3ed\ub825\uc73c\ub85c \uac00\uc2dc\ud654\ud574\ub0b4\ub294 \uac83\ucc98\ub7fc, \ud55c\uc218\uc6d0\uc774\ub098 \uc804\ubb38\uac00, \ub610\ub294 \uc5f0\uad6c\uc790 \u000d\u000a\ub4f1\uc774 \uae30\uc220\ud55c \uacf5\uc2dd\uc801\uc778 \uc5ed\uc0ac\uc5d0\uc11c \ubc30\uc81c\ub418\uace0 \uc5b5\uc555\ub418\uc5c8\ub358 \ud55c\uc218\uc6d0\uacfc \uc6d0\uc804\uc5d0 \uc800\ud56d\ud574 \uc628 \uc8fc\ubbfc\ub4e4\u000d\u000a\uc758 \ub300\ud56d \uae30\uc5b5counter memory\uc744 \ub4dc\ub7ec\ub0b4\ub294 \uc791\uc5c5\uc774\uc5c8\ub2e4.(\uc724\ud0dd\ub9bc, 2020) \uc8fc\ubbfc\ub4e4\uc758 \uc0b6\uacfc \uc774\uc8fc\ub300\ucc45\uc704 \uc5ed\u000d\u000a\uc0ac\ub97c \uc774\ud574\ud558\uae30 \uc704\ud574 \ub9e4\uc8fc \uc0c1\uc5ec\uc2dc\uc704\uc5d0 \ucc38\uc5ec\ud558\ub294 \uc8fc\ubbfc\ub4e4\uc744 \uc778\ud130\ubdf0\ud558\uc600\ub2e4. \u000d\u000a3) \ub290\ub9b0 \ud3ed\ub825 \ub4dc\ub7ec\ub0b4\uae30\u000d\u000a\uc774\uc8fc\ub300\ucc45\uc704\ub294 \ucd08\uae30 30\ub9cc \uc6d0\uc758 \uac00\uc785\ube44\uc640 \ub9e4\ub2ec 3\ub9cc \uc6d0\uc758 \ud68c\ube44\ub97c \ud1b5\ud574 \uc870\uc9c1\uc744 \uc720\uc9c0\ud588\ub2e4. \u000d\u000a\ucc98\uc74c\ubd80\ud130 \uadf8\ub4e4\uc774 \uc0c1\uc5ec\uc2dc\uc704\ub97c \ud588\ub358 \uac83\uc740 \uc544\ub2c8\ub2e4. \ucd08\uae30 \ub300\ucc45\uc704\ub294 \ud55c\uc218\uc6d0 \uc9c1\uc6d0\ub4e4\uc774 \ucd9c\uadfc\ud558\ub294 \u000d\u000a\uc2dc\uac04\uc5d0 \ub9de\ucdb0 50\uc5ec \uba85\uc758 \uc8fc\ubbfc\ub4e4\uc774 \ub3c4\ub85c \uc591\uc606\uc5d0 \uc11c\uc11c \ud53c\ucf13\uc744 \ub4e4\uc5c8\ub2e4. \uc8fc\ubbfc\ub4e4\uc740 \ud55c\uc218\uc6d0\uc774 \uc624\u000d\u000a\ud788\ub824 \u201c\ub290\uadf8\uac00 \ud574\ubd10\uc57c \uc5bc\ub9c8\ub098 \uac00\uaca0\ub098? 1\ub144\uc744 \ubc84\ud2f0\uaca0\ub098...\u201d\ub77c\ub294 \uc0dd\uac01\uc73c\ub85c \uc790\uc2e0\ub4e4\uc744 \ubb34\uc2dc\ud558\u000d\u000a\ub294 \uac83\ucc98\ub7fc \ub290\uaf08\ub2e4\uace0 \ud55c\ub2e4.(B, 2\uc6d4 2\uc77c \uc778\ud130\ubdf0) \ucc98\uc74c\uc5d0 \ubaa8\uc778 72\uac00\uad6c \uc911 \ub9ce\uc740 \uc8fc\ubbfc\ub4e4\uc740 \u201c\uc6b0\ub9ac\uac00 \uc774\ub807\u000d\u000a\uac8c \uba70\uce60\ub9cc \ub370\ubaa8\ud558\uba74, \uc800 \uc0ac\ub78c\ub4e4\uc774 \ub098\uc640\uc11c \ud574\uacb0\ud574\uc8fc\uc9c0 \uc54a\uaca0\ub098?\u201d\ub77c\ub294 \uc0dd\uac01\uc73c\ub85c \ucc38\uc5ec\ud588\uc73c\ub098, \u000d\u000a\uc77c \ub144\uc774 \uc9c0\ub098\ub3c4 \ud574\uacb0\ub418\ub294 \uac83\uc740 \uc5c6\uc5c8\ub2e4. C\uc528\ub294 \u201c\uc77c \ub144\ucbe4 \ud574\ubcf4\ub2c8\uae4c \uc548\ub41c\ub2e8 \ub9d0\uc774\uc57c. \uc548 \ub418\ub2c8\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 : \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 66\u000d\u000a\uae4c, \uc774 \uc0ac\ub78c\ub4e4\uc774 \uadf8 \uc810\ub9cc \uc0dd\uac01\ud558\uace0 \ub4a4\ub85c \uc2ac\uc2ac \ube60\uc9c0\ub354\ub77c\uace0. \uadf8\ub798\uc11c \uadf8\ub54c \ub0b4\uac00 \ub4e4\uc5b4\uac14\uc9c0.\u201d\ub77c\u000d\u000a\uace0 \ub2f9\uc2dc \uc0c1\ud669\uc744 \uc124\uba85\ud588\ub2e4. \uc0bc\uc911\uc218\uc18c\ub77c\ub294 \ube44\uac00\uc2dc\uc801\uc778 \uc704\ud5d8\uc5d0 \ub300\ud56d\ud558\ub824\ub358 \uc774\ub4e4\uc758 \ud65c\ub3d9\uc740 \uc774 \u000d\u000a\ub9c8\uc744\uc774 \ub9de\ub2e5\ub728\ub9b0 \ubcf4\ub2e4 \uac00\uc2dc\uc801\uc774\uace0 \uc989\uac01\uc801\uc778 \u2018\uc704\ud611\u2019, \uc989 \uc6d0\uc804\uc0b0\uc5c5\uc5d0 \uc758\uc874\ud558\ub294 \uc774\ub4e4\uc758 \uc0dd\uacc4 \u000d\u000a\ubb38\uc81c\ub85c \uc778\ud574 \ub300\ucc45\uc704 \ud65c\ub3d9\uc744 \uadf8\ub9cc\ub450\ub294 \uc0ac\ub78c\uc774 \ub298\uc5b4\ub09c \uac83\uc774\ub2e4. \u201c\ucc98\uc74c\uc5d0\ub294 \uac19\uc774 \uc2dc\uc704\ud588\ub358 \uc0ac\u000d\u000a\ub78c\ub4e4 \uc911\uc5d0\uc11c \uc790\uc2dd\uc774 \ud55c\uc218\uc6d0\uc5d0 \ub2e4\ub2cc \uacbd\uc6b0\uac00 \uc880 \uc788\uc5c8\uac70\ub4e0? \u2018\ub108\uadf8 \uc5c4\ub9c8\uac00 \ubc18\ub300\ud55c\ub2e4\uba70?, \ub108\ud76c \u000d\u000a\uc544\ube60\ub3c4 \ub370\ubaa8\uc5d0 \ub098\uc628\ub2e4\uba70?\u2019\ub77c\ub294 \uc2dd\uc758 \uc774\uc57c\uae30\ub97c \ub4e3\uac8c \ub418\uba74 \uc774\uc81c \ubd80\ubaa8\ub4e4\uc740 \uc790\uc2dd\uc774 \ud639\uc2dc \uc798\ub9ac\u000d\u000a\uac70\ub098 \ub0b4\ub144\uc5d0 \uacc4\uc57d\uc5f0\uc7a5 \ubabb\ud560\uae4c \ubd10 \uac71\uc815\ud558\ub294 \uac70\uc9c0. \uadf8 \uc0ac\ub78c\ub4e4\uc774 \uadf8\ub7f0 \uc774\uc720\ub85c \ubabb\ud558\uaca0\ub2e4\ub294\ub370 \u000d\u000a\uc5b4\uca4c\uaca0\uc5b4. \uc6b0\ub9ac\uac00 \uadf8 \uc0ac\ub78c\ub4e4 \uc0dd\uacc4\ub97c \ucc45\uc784\uc838\uc904 \uc218 \uc788\ub294 \uac83\ub3c4 \uc544\ub2c8\uace0.\u201d \ub098\uc544\ub9ac\uc5d0 \uc0ac\ub294 \uc8fc\ubbfc\u000d\u000a\uc758 \uc808\ubc18 \uc774\uc0c1\uc740 \uc2dd\ub2f9\uc744 \uc6b4\uc601\ud558\uac70\ub098 \ud55c\uc218\uc6d0\uc758 \uc815\uaddc\uc9c1\u00b7\ube44\uc815\uaddc\uc9c1\uc73c\ub85c \uc77c\ud558\ub294 \ub4f1 \uc6d0\uc804\uc5d0 \uc9c1\uac04\u000d\u000a\uc811\uc801\uc73c\ub85c \uad00\uacc4\ub41c \uc77c\uc744 \ud558\uace0 \uc788\ub2e4. 2\ub144\uc774 \ucc44 \ub418\uc9c0 \uc54a\uc544 \ub300\ucc45\uc704\ub294 \uc790\ubc1c\uc801, \ube44\uc790\ubc1c\uc801 \uc774\uc720\ub85c \u000d\u000a15\uac00\uad6c \uc815\ub3c4\ub9cc\uc774 \ub0a8\uc558\ub2e4. \ud55c\uc218\uc6d0\uc744 \ube44\ud310\ud558\uace0 \uadf8\ub4e4\uc5d0\uac8c \ub4f1\uc744 \uc9c0\uace0\uc120 \uc774\uacf3\uc5d0\uc11c \uba39\uace0 \uc0b4 \uc218 \u000d\u000a\uc5c6\ub294 \uc0ac\ub78c\ub4e4\uc740 \uc5b4\uca54 \uc218 \uc5c6\uc774 \ub300\ucc45\uc704\ub97c \uadf8\ub9cc\ub450\uc5c8\ub2e4. \ub3d9\uc2dc\uc5d0 \uc774\uc8fc\ub97c \uc694\uad6c\ud558\uace0 \uc6d0\uc804\uc758 \uc704\ud5d8\uc131\u000d\u000a\uc744 \ub9d0\ud558\ub294 \ub300\ucc45\uc704\ub97c \ub178\uace8\uc801\uc73c\ub85c \ube44\ud310\ud558\ub294 \ub9c8\uc744 \ub0b4 \uc8fc\ubbfc\ub4e4\ub3c4 \uc0dd\uacbc\ub2e4. \uadf8\ub4e4\uc740 \u2018\uc0dd\uacc4\ub300\ucc45\uc704\u2019\u000d\u000a\ub77c\ub294 \uc774\ub984\uc758 \ub2e8\uccb4\ub97c \uacb0\uc131\ud558\uc5ec \uc6d0\uc804\uc774 \uc704\ud5d8\ud558\ub2e4\uace0 \ud37c\ub728\ub9ac\ub294 \ub300\ucc45\uc704\uac00 \uc624\ud788\ub824 \ubb38\uc81c\ub77c\uace0 \uc9c0\u000d\u000a\uc801\ud558\uae30 \uc2dc\uc791\ud588\ub2e4. B \uc528\ub294 \u201c\uadf8\ub4e4\uc740 \ud2b9\ud788 \ub300\ucc45\uc704\uac00 \uacb0\uc131\ub418\uace0 \uc6b0\ub9ac\uac00 \u2018\uc6d0\uc804\uc774 \uc704\ud5d8\ud558\ub2e4, \ubd88\uc548\u000d\u000a\ud574\uc11c \ubabb \uc0b4\uaca0\ub2e4\u2019\ub77c\uace0 \ub9d0\uc744 \ud574\uc11c \uad00\uad11\uac1d\uc758 \ubc1c\uae38\uc774 \ub69d \ub04a\uacbc\ub2e4\uace0 \ub9d0\ud588\uac70\ub4e0. \uadf8\uac8c \uc6b0\ub9ac \ub54c\ubb38\uc774\u000d\u000a\ub77c\ub294 \uac70\uc57c. \uadf8\ub9ac\uace0 \uc9c0\uc6d0\uae08\uc744 \ubc1b\uace0 \uc6d0\uc804\uc5d0\uc11c \uc77c\ub3c4 \ud558\ub294\ub370 \ud55c\uc218\uc6d0\uc744 \ube44\ud310\ud574\uc11c \uc6b0\ub9ac\uac00 \ubb50 \uc5bb\u000d\u000a\uc744 \uac83\uc774 \uc788\ub0d0\ub294 \ub9d0\uc774 \ub9c8\uc744\uc5d0 \ub3cc\uae30 \uc2dc\uc791\ud588\uc5b4.\u201d\ub77c\uace0 \ub9d0\ud588\ub2e4. \ub208\uc5d0 \ubcf4\uc774\uc9c0 \uc54a\uc9c0\ub9cc \uc9c0\uc5ed\uacfc \uc8fc\u000d\u000a\ubbfc\ub4e4\uc5d0\uac8c \ud53c\ud574\ub97c \ub07c\uce60 \uc218 \uc788\ub294 \uc0bc\uc911\uc218\uc18c\uc758 \ub290\ub9b0 \ud3ed\ub825\uc131\uc744 \ub9d0\ud558\ub294 \uc0ac\ub78c\uacfc \u201c\uc704\ud5d8\uc744 \uc9c0\uc801\ud558\u000d\u000a\ub294 \uc0ac\ub78c\ub4e4\uc774 \uc77c\ubc18\uc801\uc73c\ub85c \ubd88\uc548\uc744 \uc720\ubc1c\ud55c\ub2e4.\u201d(\ubca1, 2006: 137)\uace0 \ub9d0\ud558\uba70 \uac00\uc2dc\uc801\uc774\uace0 \ud53c\ubd80\ub85c \ub290\ub084 \uc218 \u000d\u000a\uc788\ub294 \uc9c0\uc5ed \uacbd\uc81c \ubd88\ud669\uacfc \ud55c\uc218\uc6d0\uacfc\uc758 \ubc00\uc811\ud55c \uad00\uacc4\ub97c \uac15\uc870\ud558\ub294 \uc0ac\ub78c\uc774 \ubd80\ub52a\uce58\uc790 \ud6c4\uc790\ub97c \ub530\ub974\u000d\u000a\ub294 \uc0ac\ub78c\uc774 \ub9ce\uc544\uc84c\ub2e4.\u000d\u000a\ub9c8\uc744 \ub0b4\uc5d0\uc11c\ub3c4 \ub354\uc6b1 \uc785\uc9c0\uac00 \uc881\uc544\uc9c4 \uadf8\ub4e4\uc740 \uae30\uc874\uc5d0 \ud558\ub358 \ud589\ub82c\uc2dc\uc704\uac00 \uc544\ub2c8\ub77c \ub2e4\ub978 \uc2dc\uc704\ubc29\u000d\u000a\uc2dd\uc744 \uace0\ubbfc\ud560 \uc218\ubc16\uc5d0 \uc5c6\uc5c8\ub2e4. \uc801\uc740 \uc778\uc6d0\uc73c\ub85c\ub294 \ucc98\uc74c\uc5d0 \ud558\ub358 \ubc29\uc2dd\uc758 \uc2dc\uc704\ub97c \ud560 \uc218\ub3c4 \uc5c6\uace0, \ubb34\u000d\u000a\uc5c7\ubcf4\ub2e4 \uc8fc\ubbfc\ub4e4\uc744 \uce68\ubb35\ud558\uac8c \ub9cc\ub4dc\ub294 \u2018\uc0dd\uacc4\u2019 \uc774\uc288\ub97c \uadf9\ubcf5\ud558\uace0 \uc774\uc8fc\ub97c \uc694\uad6c\ud558\ub294 \uc790\uc2e0\ub4e4\uc758 \uc808\ubc15\u000d\u000a\ud55c \ub9c8\uc74c\uc744 \ub2f4\uc544 \uc2f8\uc6b0\uae30\ub85c \ud588\ub2e4. \ub2c9\uc2a8\uc740 \ub290\ub9b0 \ud3ed\ub825 \ubb38\uc81c\ub97c \ub4dc\ub7ec\ub0b4\uae30 \uc704\ud574\uc11c\ub294 \uadf8 \uad6c\uc870\ub97c \uc9c0\u000d\u000a\ud0f1\ud558\ub294 \u201c\ube44\uac00\uc2dc\uc801 \uad8c\ub825\uc758 \uc815\uc0c1\ud654\ub41c \uce68\ubb35\u201d\uc744 \uae68\ub728\ub824 \uacf5\uc801 \uac00\uc2dc\uc131\uc744 \ub192\uc5ec\uc57c \ud55c\ub2e4\uace0 \uac15\uc870\ud588\u000d\u000a\ub2e4. \uc774\ub54c, \ud3ed\ub825\uc5d0 \ub300\ud55c \ubd84\ub178, \uc99d\uc5b8 \uc131\uaca9\uc744 \ub760\ub294 \uc800\ud56d, \ub3c5\ucc3d\uc801 \ub0b4\ub7ec\ud2f0\ube0c\ub4e4\uacfc \ubc18-\uc5ed\uc0accounter-","66 67\u000d\u000ahistory\ub97c \uc368 \ub0b4\ub824\uac00\ub294 \ubaa9\uc18c\ub9ac \ub0b4\uae30\uc640 \uc6b4\ub3d9\uc774 \ud544\uc694\ud55c\ub370,(\ub2c9\uc2a8, 2020) \ub0a8\uc740 \uc18c\uc218\uc758 \uc8fc\ubbfc\uc740 \uae30\uc874 \ubc29\u000d\u000a\uc2dd\ubcf4\ub2e4 \ub354 \uac15\ud558\uac8c \uc790\uc2e0\ub4e4\uc758 \uc774\uc8fc \uc694\uad6c\ub97c \uad00\ucca0\ud560 \uc218 \uc788\ub294 \uc0c1\uc5ec\uc2dc\uc704\ub97c \uc81c\uc548\ud588\ub2e4. \uc6d0\uc804 \uadfc\ucc98\u000d\u000a\uc5d0\uc11c \uc0ac\ub294 \uac83\uc740 \u2018\uc8fd\uc740 \uac83\uc774\ub098 \ub2e4\ub984\uc5c6\ub2e4\u2019\ub294 \uacb0\uc5f0\ud55c \uc758\uc9c0\ub97c \ub2f4\uc740 \uac83\uc774\uba70, \uc2e4\uc81c \uc7a5\ub840\ucc98\ub7fc \u2018\uc704\u000d\u000a\uc6d0\uc7a5\u2019, \u2018\ubd80\uc704\uc6d0\uc7a5\u2019, \u2018\uc0ac\ubb34\uad6d\uc7a5\u2019 \ub4f1\uc758 \uc9c1\ud568\uc744 \uad00 \uc704\uc5d0 \uc801\uc5c8\ub2e4. \uad00\uacfc \ud568\uaed8 \uc0c1\uc5ec\ub97c \ud568\uaed8 \ub04c\uc5c8\ub294\u000d\u000a\ub370, \uadf8 \uc704\uc5d0\ub294 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c \ub3d4 \ubaa8\ud615\uc744 \uc62c\ub824\ub193\uc558\ub2e4. \ub300\ucc45\uc704\ub294 \uc0c1\uc5ec\uc2dc\uc704\ub97c \ud1b5\ud574 \ub9e4\uc77c \uc704\ud5d8 \u000d\u000a\uc18d\uc5d0 \ub178\ucd9c\ub41c \uc790\uc2e0\ub4e4\uc758 \uc7a5\ub840\uc2dd\uacfc \uc6d0\uc804\uc758 \uc7a5\ub840\uc2dd\uc744 \uce58\ub974\uba70 \uc2f8\uc6cc\uc628 \uac83\uc774\ub2e4. \uc774\uc81c\ub294 \uc704\ud5d8\ud55c \uc6d0\u000d\u000a\uc804\uc744 \uba48\ucd94\uace0, \uc5ec\uae30 \u2018\uc8fd\uc9c0 \ubabb\ud574 \uc0ac\ub294 \uc0ac\ub78c\ub4e4\u2019\uc774 \uc788\ub2e4\ub294 \uac83\uc744 \uc54c\ub9ac\uae30 \uc704\ud55c \ubcf4\ub2e4 \uadf9\uc801\uc778 \uc800\ud56d\u000d\u000a\ubc29\uc2dd\uc774\uc5c8\ub2e4. \uc704\ud5d8\ud55c \uacf3\uc5d0\uc11c \uc0b4 \uc218 \uc5c6\ub2e4\uba70 \uc774\uc8fc\ub97c \uc694\uccad\ud558\ub294 \uadf8\ub4e4\uc5d0\uac8c \ud55c\uc218\uc6d0\uc740 \uc0c1\uc0dd\uacfc \ubc1c\uc804\u000d\u000a\uc744 \uc5b8\uae09\ud558\uba70 \uc774\ub904\uc9c8 \uc218 \uc5c6\ub294 \uc720\ud1a0\ud53c\uc544\ub97c \uc81c\uc2dc\ud588\ub2e4. \uc624\uc740\uc815(2020)\uc740 \uc77c\ubcf8\uc758 \uc6d0\uc804 \uc778\ud504\ub77c \uad6c\ucd95\u000d\u000a\uc5d0\ub294 \ud53c\ud3ed\uad6d\uc778 \uc77c\ubcf8\ub9cc\uc774 \uc6d0\uc790\ub825\uc758 \ud3c9\ud654\uc801 \uc774\uc6a9\uc744 \uc8fc\uc7a5\ud560 \uc218 \uc788\ub2e4\ub294 \u2018\uad6c\uc6d0\u2019 \ub2f4\ub860, \uacbd\uc81c \uc131\uc7a5\u000d\u000a\uc744 \uc704\ud574\uc11c\ub294 \uafc8\uc758 \uc5d0\ub108\uc9c0\uc778 \uc6d0\uc804\uc774 \ud544\uc694\ud558\ub2e4\ub294 \u2018\uc131\uc7a5\u2019 \ub2f4\ub860, \uc6d0\uc790\ub825\uc740 \ud3b8\ub9ac\ud558\uace0 \ud48d\uc694\ub85c\uc6b4 \u000d\u000a\uc0b6\uc744 \uc81c\uacf5\ud55c\ub2e4\ub294 \u2018\ud589\ubcf5\u2019 \ub2f4\ub860, \uc6d0\uc804\uc740 \uc790\uc5f0\uacfc \uc870\ud654\ub97c \uc774\ub8e8\ub294 \uae68\ub057\ud55c \uc5d0\ub108\uc9c0\ub77c\ub294 \u2018\uccad\uc815\u2019 \ub2f4\ub860\u000d\u000a\uc774 \uac15\ud558\uac8c \uc791\ub3d9\ud588\ub2e4\uace0 \ub9d0\ud55c\ub2e4. \uc774\uc640 \uc720\uc0ac\ud558\uac8c \ub300\ucc45\uc704\ub97c \ub9cc\ub09c \uc6d4\uc131\uc6d0\uc804 \ubcf8\ubd80\uc7a5 \uc5ed\uc2dc \u2018\uc131\uc7a5\u2019\u000d\u000a\uacfc \u2018\ud589\ubcf5\u2019 \ub2f4\ub860\uc5d0 \ubc84\uae08\uac00\ub294, \uc0c1\uc0dd\uacfc \ubc1c\uc804\uc744 \ud1b5\ud574 \ub300\ucc45\uc704\uc758 \uc774\uc8fc \uc694\uad6c\ub97c \uac70\ubd80\ud558\ub294 \ub3d9\uc2dc\uc5d0 \uc6d0\u000d\u000a\uc790\ub825\ubc1c\uc804 \uc9c0\uc18d\uc5d0 \ub300\ud55c \ub3d9\uc758\uc758 \uc815\uce58\uacbd\uc81c\ud559political economy of consent, Ribault, 2019\uc744 \ubc00\uc5b4\ubd99\uc774\uace0\u000d\u000a\uc788\uc5c8\ub2e4. \uc790\uc2e0\ub4e4\uc758 \ubab8\uc5d0 \uc0c8\uaca8\uc9c4 \uc9c8\ubcd1\uacfc \uc554\uc774\ub098 \ubc31\ud608\ubcd1\uc5d0 \uac78\ub824 \uc8fd\uc740 \ub9c8\uc744\uc8fc\ubbfc\uc774 \ub9ce\ub2e4\ub294 \uc9c1\u00b7\u000d\u000a\uac04\uc811\uc801\uc778 \uacbd\ud5d8\uc73c\ub85c \uc778\ud574 \ub208\uc5d0 \ubcf4\uc774\uc9c0\ub3c4, \uc778\uacfc\uad00\uacc4\ub97c \uc99d\uba85\ud560 \uc218\ub3c4 \uc5c6\ub294 \ub290\ub9b0 \ud3ed\ub825\uc758 \uc2e4\uccb4\ub97c\u000d\u000a\uc870\uae08\uc529 \uc758\uc2ec\ud558\uac8c \ub418\uc5c8\ub2e4. \uc0bc\uc911\uc218\uc18c\uc758 \uc2e4\uccb4\ub97c \uc54c\uac8c \ub41c \uadf8\ub4e4\uc740 \uc0c1\uc5ec\uc2dc\uc704\uc640\ub294 \ub610 \ub2e4\ub978 \ubc29\uc2dd\uc758\u000d\u000a\uc2f8\uc6c0\uc744 \uc900\ube44\ud588\ub2e4. \uadf8\uac83\uc740 \ubc14\ub85c \u2018\uc0bc\uc911\uc218\uc18c \uac80\uc0ac\u2019\uc600\ub2e4. \ub204\uad6c\ub3c4 \uc778\uc815\ud558\uc9c0 \uc54a\uc9c0\ub9cc, \uc7ac\uc0b0\uad8c\uc774\ub098\u000d\u000a\uac70\uc8fc\uc774\uc804\uc758 \uc790\uc720 \ubb38\uc81c \uc678\uc5d0\ub3c4 \ube44\uac00\uc2dc\uc801\uc778 \ub290\ub9b0 \ud3ed\ub825\uc774 \ucd08\ub798\ud55c \uc790\uc2e0\uc758 \ubab8\uacfc \uc9c0\uc5ed \uc8fc\ubbfc\ub4e4\uc758\u000d\u000a\ubd84\uba85\ud55c \ud53c\ud574\ub97c \u2018\ub4dc\ub7ec\ub0b4 \ubcf4\uc774\uaca0\ub2e4\u2019\ub77c\ub294 \uc758\uc9c0\uc758 \ubc1c\ub85c\uc600\ub358 \uc148\uc774\ub2e4.\u000d\u000a4) \uc65c \ud0c8\ud575\uc774 \uc544\ub2cc \uc774\uc8fc\ub97c \uc694\uad6c\ud558\ub098\u000d\u000a\uc774 \uc808\uc5d0\uc11c\ub294 \uacbd\uc8fc\uc5d0\uc11c \ud575\ubc1c\uc804\uc18c\ub97c \ube44\ud310\ud558\ub294 \uc8fc\ubbfc\uc870\ucc28 \ud0c8\ud575\uc774 \uc544\ub2cc \uc774\uc8fc\ub97c \uc694\uad6c\ud558\ub294 \uc774\u000d\u000a\uc720\ub97c \uadf8\ub4e4\uc758 \uad00\uc810\uc5d0\uc11c \ubd84\uc11d\ud560 \uac83\uc774\ub2e4. \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc758 \uad00\uc810\uc5d0\uc11c \uc774\uc8fc\ub300\ucc45\uc744 \uc694\uad6c\ud558\ub294 \uc774\uc720, \u000d\u000a\uc870\uac74\uacfc \ub9e5\ub77d\uc744 \ubd84\uc11d\ud558\uba70 \ub098\uc544\uac00 \uadf8\ub4e4\uc740 \uc5b4\ub5a4 \uc704\ud5d8\uacbd\uad00\uc744 \ud615\uc131\ud574\ub098\uac00\uace0 \uc788\ub294\uc9c0\ub97c \ud30c\uc545\ud560 \u000d\u000a\uac83\uc774\ub2e4. \ubb34\uc5c7\ubcf4\ub2e4 \uc9c0\uae08\uae4c\uc9c0 \ud0c8\ud575(\uc6b4\ub3d9)\uc774\ub780 \ud558\ub098\uc758 \ubaa9\ud45c(\ud575\ubc1c\uc804\uc18c\ub97c \uba48\ucd94\uace0, \uc804\uccb4 \ud575\ubc1c\uc804\uc18c \uac1c\uc218\ub97c \uc904\uc5ec\ub098\uac00\ub294 \uac83)\ub97c \u000d\u000a\uac00\uc9c4 \ub2e8\uc77c\ud55c \uc774\ub150\uc774\ub098 \uc8fc\uc7a5\uc774\ub77c\uace0 \uc0dd\uac01\ud588\ub358 \uc5f0\uad6c\uc790\uc758 \uace0\uc815\uad00\ub150\uacfc \ud55c\uacc4\ub97c \ub4dc\ub7ec\ub0b4\uace0 \uc131\ucc30\ud568\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 : \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 68\u000d\u000a\uc73c\ub85c\uc368 \ud0c8\ud575(\uc6b4\ub3d9)\uc774\ub780 \uacb0\ucf54 \uace0\uc815\ub41c \ud558\ub098\uc758 \ubaa9\uc18c\ub9ac\uac00 \uc544\ub2c8\ub77c \uc624\ud788\ub824 \uc9c0\uc5ed\uc8fc\ubbfc\uc744 \ud3ec\ud568\ud558\uc5ec \ub354\u000d\u000a\uc6b1 \ub2e4\uc591\ud558\uace0 \uc138\ubd84\ud654\ub41c \uc694\uad6c\ub4e4\uc744 \uc774\ud574\ud558\uace0 \uace0\ub824\ud560 \ud544\uc694\uc131\uc744 \uc81c\uae30\ud560 \uac83\uc774\ub2e4.\u000d\u000a\uadf8\ub4e4\uc774 \ud0c8\ud575\uc774 \uc544\ub2c8\ub77c \uc774\uc8fc\ub97c \uc694\uad6c\ud558\ub294 \uccab \ubc88\uc9f8 \uc774\uc720\ub294 \u2018\ud0c8\ud575\u2019\uc744 \uc678\uce58\ub294 \uc21c\uac04, \uc9c0\uc5ed \ub18d\u000d\u000a\uc218\uc0b0\ubb3c\uc774\ub098 \ud1a0\uc591 \uc624\uc5fc\u00b7\ud53c\ud3ed\ubb38\uc81c\ub97c \ube44\ub86f\ud558\uc5ec \uadf8\ub3d9\uc548 \ud130\ubd80taboo\uc2dc \ub418\uc5c8\ub358 \ubb38\uc81c\ub4e4\ub3c4 \ud568\uaed8 \ubd80\u000d\u000a\uc0c1\ud558\ub294 \uac83\uc744 \uc5fc\ub824\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uae40\uc740\ud61c\u00b7\ubc15\ubc30\uade0(2016)\uc740 \uc77c\ubcf8 \uc6d0\uc790\ub825\ubcf5\ud569\uccb4nuclear industrial\u000d\u000acomplex\ub77c\ub294 \uc774\uc775\uc9d1\ub2e8\uc774 \uc911\uc2ec\uc774 \ub418\uc5b4 \ud575\ubc1c\uc804\uc18c \uc911\ud765 \uc815\ucc45\uc744 \ucd94\uc9c4\ud558\uc600\uc9c0\ub9cc, \uc9c0\uc5ed \ucc28\uc6d0\uc758 \uac1c\u000d\u000a\ubc1c\uc8fc\uc758 \uc815\uce58\ub098 \uc774\uc5d0 \ub300\ud55c \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc758 \ubc18\ub300\uc640 \ucc2c\uc131 \ub4f1\uc758 \ub300\uc751\uc744 \ud568\uaed8 \ubd10\uc57c\ud55c\ub2e4\uace0 \uac15\uc870\ud588\u000d\u000a\ub2e4. \ud2b9\ud788, \uc77c\ubcf8 \uc9c0\uc5ed\uc0ac\ud68c\uc758 \ub2e4\uce35\uc131\uc744 \uac15\uc870\ud558\uba70, \ud575\ubc1c\uc804\uc18c\uc5d0 \uc758\uc874\ud558\uac70\ub098 \uc9c0\uc9c0\ud558\ub294 \ubcf5\uc7a1\ud558\uace0 \u000d\u000a\ub2e4\uc591\ud55c \uc758\ub3c4\uc640 \ud589\uc704\ub97c \ub2e8\uc21c\ud55c \u2018\uac1c\ubc1c \uc695\ub9dd\u2019\uc73c\ub85c\ub9cc \uc77c\uc6d0\ud654\ud558\ub294 \ud574\uc11d\uc744 \uacbd\uacc4\ud558\uc600\ub2e4. \uc790\uc2e0\ub4e4\uc758 \u000d\u000a\uc0dd\uacc4\ub9cc\uc774 \uc544\ub2c8\ub77c \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc758 \uba39\uace0\uc0ac\ub294 \ubb38\uc81c\uae4c\uc9c0 \uce58\uba85\uc801\uc778 \uc601\ud5a5\uc744 \uc904 \uc218 \uc788\uae30\uc5d0, \ud0c8\ud575\uc744 \u000d\u000a\ub354 \uac15\ub825\ud558\uac8c \uc678\uce58\uc9c0 \ubabb\ud558\uace0 \uc774\uc8fc\ub300\ucc45\uc744 \uc694\uad6c\ud558\ub294 \uac83\uc774\ub2e4. \ub098\uc544\uac00 \ub300\ucc45\uc704\ub294 \ubbfc\uac10\ud55c \uc774\uc288\ub97c \u000d\u000a\uac74\ub4dc\ub9ac\uc9c0 \uc54a\uace0 \u2018\uc790\uc2e0\uc758 \ubab8, \ud6fc\uc190\ub418\uace0 \ubcd1\ub4e0 \uc790\uc2e0\uc758 \ubab8\u2019\uc744 \uadfc\uac70\ub85c \ud53c\ud574\ub97c \uc785\uc99d\ud558\uace0 \uc774\uc8fc\ub97c \uc694\u000d\u000a\uad6c\ud558\uace0 \uc788\ub2e4. \uc989, \uc774\uc8fc\ub300\ucc45\uc704\ub294 \uc0dd\uacc4\uc5d0 \ub300\ud55c \uc704\ud611\uc744 \uc778\uc9c0\ud558\uace0 \uc788\uc74c\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0, \uac74\uac15/\ud53c\u000d\u000a\ud3ed\ubb38\uc81c \ubc0f \uc548\uc804\ud558\uac8c \uc0b4 \uad8c\ub9ac\ub97c \uc7c1\ucde8\ud558\uae30 \uc704\ud574 \ud55c\uc218\uc6d0\uacfc \uc6d4\uc131 \uc6d0\uc804\uc5d0 \uc774\uc8fc\ub300\ucc45\uc744 \ud558\ub294 \ub4f1 \u000d\u000a\ube44\ud310\uc801\uc73c\ub85c \ubc14\ub77c\ubcf4\uace0 \uc788\ub2e4. \uadf8\ub7ec\ub098, \ud0c8\ud575\uc774\ub098 \uc6d0\uc804\uc758 \ud3d0\uc1c4\ub97c \uc8fc\uc7a5\ud558\ub294 \uac83\uc740 \uc9c0\uc5ed\uc758 \uc0dd\uacc4\ub098 \u000d\u000a\ubc1c\uc804\ubb38\uc81c\uc5d0 \uc5ed\ud589\ud558\ub294 \uac83\uc774\uc790, \uc9c0\uc5ed\uc5d0\uc11c \ud130\ubd80\uc2dc\ud574\uc654\ub358 \ubb38\uc81c\ub4e4(\ud1a0\uc591\uc624\uc5fc, \ub18d\uc218\uc0b0\ubb3c \ud53c\ud3ed\ubb38\uc81c)\uc744 \ud568\uaed8 \uc9c0\u000d\u000a\uc801\ud560 \uc218\ubc16\uc5d0 \uc5c6\ub294 \uc0c1\ud669\uc774\uae30\uc5d0 \ud0c8\ud575\uc774\ub098 \uc6d0\uc804\ud3d0\uc1c4\ubcf4\ub2e4\ub294 \uc790\uc2e0\uc758 \ubab8\ub9cc\uc73c\ub85c\ub3c4 \uc694\uad6c\ud560 \uc218 \uc788\u000d\u000a\ub294 \uc774\uc8fc\ub300\ucc45\uc5d0 \uc9d1\uc911\ud558\uac8c \ub418\uc5c8\ub2e4.\u000d\u000a\ud0c8\ud575\ubcf4\ub2e4 \uc774\uc8fc\ub97c \uac15\uc870\ud558\ub294 \ub450 \ubc88\uc9f8 \uc774\uc720\ub294, \u2018\ud0c8\ud575\u2019\uc5d0 \ub300\ud55c \uc11c\ub85c \ub2e4\ub978 \uad00\uc810\uacfc \uac1c\ub150 \uadf8\ub9ac\u000d\u000a\uace0 \uc2e4\ud604 \uac00\ub2a5\uc131\uacfc \uad00\ub828\uc774 \uc788\ub2e4. \ud604\uc7ac\uae4c\uc9c0 \uc0ac\ud68c\uc801\uc73c\ub85c \ud569\uc758\ub41c \ud0c8\ud575\uc758 \uc815\uc758 \ud639\uc740 \ubc94\uc704\uac00 \ubd80\u000d\u000a\uc7ac\ud55c \uc0c1\ud669\uc5d0\uc11c, \uc815\ubd80/\uc0ac\ud68c\uc758 \ud1b5\ub150\uc740 \ud604 \ubb38\uc7ac\uc778 \uc815\uad8c\uc774 \uc5d0\ub108\uc9c0 \uc804\ud658 \ud639\uc740 \ud0c8\uc6d0\uc804 \uc815\ucc45\uc758 \uae30\u000d\u000a\uc870\ub85c \uc0bc\uace0 \uc788\ub294 \u2018\uc7a5\uae30\uac04\uc5d0 \uac78\uccd0 \ud575\ubc1c\uc804\uc18c\ub97c \uc810\ucc28 \uc904\uc774\uace0, \uc7ac\uc0dd\uc5d0\ub108\uc9c0\ub97c \ud655\ub300\ud558\ub294 \uac83\u2019\uc774\ub77c \u000d\u000a\ubcfc \uc218 \uc788\ub2e4. \uadf8\ub7ec\ub098, \uc8fc\ubbfc\ub4e4\uc774 \uc0dd\uac01\ud558\ub294 \ud0c8\ud575\uc758 \ubc94\uc704\ub294 \uc774\ub97c \ub118\uc5b4 \u2018\ud3d0\ub85c\uc640 \ud575\ud3d0\uae30\ubb3c \ucc98\ub9ac \u000d\u000a\ubc0f \ubcf4\uad00\uc758 \ubb38\uc81c\u2019\uae4c\uc9c0 \ud3ec\ud568\ud558\uace0 \uc788\ub2e4. \uae40\ud604\uc6b0\u00b7\uc774\uc815\ud544(2017)\uc740 \ud55c\uad6d \ud575\ubc1c\uc804 \ub808\uc9d0\uc758 \uad6c\uc131\uacfc \ub3d9\u000d\u000a\ud559\uc744 \uae30\uc874 \uc911\uc559\uc758 \ud575\ub9c8\ud53c\uc544\ub97c \uc704\uc8fc\ub85c \ubd84\uc11d\ud588\ub358 \uac83\uc5d0\uc11c \ub098\uc544\uac00 \uc9c0\uc5ed\uc758 \ud575\ub9c8\ud53c\uc544 \ud639\uc740 \uc131\uc7a5\u000d\u000a\uc5f0\ud569 \uc138\ub825\uc744 \ud568\uaed8 \ubcfc \ud544\uc694\uac00 \uc788\ub2e4\uace0 \uc8fc\uc7a5\ud558\uc600\ub2e4. \ub610\ud55c, \uc774\uc5d0 \uc800\ud56d\ud558\ub294 \u2018\ud0c8\ud575\ub3d9\ub9f9\u2019\uc740 \uc804\uad6d \u000d\u000a\uc218\uc900\uc5d0\uc11c \uc874\uc7ac\ud558\uc9c0\ub9cc \uc678\uc5f0\uc740 \ub2e4\uc18c \ubd88\ubd84\uba85\ud558\uba70 \uc9c0\uc5ed\uc5d0\uc11c\ub294 \ub2e8\uc77c\ud55c \u2018\ud0c8\ud575\u2019\uc73c\ub85c \uad6d\ud55c\ub418\uc9c0 \uc54a","68 69\u000d\u000a\ub294 \uac1c\uc778\uacfc \uc9d1\ub2e8\uc774 \uc120\ud0dd\uc801\uc73c\ub85c \ud0c8\ud575 \uc774\uc288\uc640 \uacb0\ud569\ud55c\ub2e4\uace0 \uc124\uba85\ud558\uc600\ub2e4.(\uae40\ud604\uc6b0\u00b7\uc774\uc815\ud544, 2017: 44) \uc774\uc8fc\ub300\u000d\u000a\ucc45\uc704\uc8fc\ubbfc\uc740 \ud575\ubc1c\uc804\uc18c\ub97c \uba48\ucd94\ub294 \uac83\ub9cc\uc774 \ud0c8\ud575\uc774 \uc544\ub2c8\ub77c, \uc774\uc8fc\ub97c \uc2dc\ud0a4\ub294 \uac83\uc774\ub098 \uac74\uac15\ubb38\uc81c\ub97c \u000d\u000a\uc81c\uae30\ud558\ub294 \uac83\ub3c4 \ud0c8\ud575\uc758 \ub2e4\ub978 \ubc29\ud5a5\uc774\ub77c\uace0 \uc8fc\uc7a5\ud558\uc600\ub2e4. \uae40\uc138\uc601 \uc678(2019)\ub294 \uc2e0\uace0\ub9ac 5,6\ud638\uae30 \uacf5\ub860\u000d\u000a\ud654 \uc774\ud6c4 \uc131\uacfc\uc640 \ud55c\uacc4\ub97c \uc815\ub9ac\ud558\uba74\uc11c \ud0c8\ud575\uc6b4\ub3d9 \ub0b4\ubd80\uc758 \ubbfc\uc8fc\uc8fc\uc758\uac00 \ud544\uc694\ud558\ub2e4\ub294 \uc9c0\uc801\uc744 \ud558\uc600\u000d\u000a\ub2e4. \uc989, \ud0c8\ud575\uc6b4\ub3d9\uc740 \ud558\ub098\uac00 \uc544\ub2c8\ub77c \ub2e4\uc591\ud55c \uc694\uad6c\ub4e4\uc774 \uacf5\uc874\ud558\uba70 \ub354 \ub9ce\uc740 \uac83\ub4e4\uc744 \uc694\uad6c\ud574\uc57c\ud558\u000d\u000a\ub294\ub370, \ud604\uc7ac\uc758 \ud0c8\ud575\uc6b4\ub3d9\uc740 \uace0\uc815\ub418\uace0 \ub2e8\uc77c\ud55c \ud558\ub098\uc758 \ubaa9\uc18c\ub9ac\ub85c\uc11c \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc758 \uad6c\uccb4\uc801\uc778 \ub9e5\u000d\u000a\ub77d\uc744 \uac70\uc138\ud55c\ub2e4\ub294 \uac83\uc774\ub2e4.(\uae40\uc138\uc601 \uc678, 2019: 98-99) \ub610\ud55c, 2017\ub144 10\uc6d4 30\uc77c \ud0c8\ud575\uc2e0\ubb38\ub3c4 \uc774\uc81c\ub294 \ud0c8\u000d\u000a\ud575\uc9c4\uc601\uc774 \uacfc\uac70\ucc98\ub7fc \u2018\ud55c \ub369\uc5b4\ub9ac\ub85c \ubaa8\ub4e0 \uac83\uc744 \ubc1b\uc544 \uc548\uae30\uc5d0\ub294 \ud798\ub4e0 \uc0c1\ud0dc\u2019\uac00 \ub418\uc5c8\ub2e4\uace0 \uc9c0\uc801\ud558\u000d\u000a\uc600\ub2e4. \uc774\ucc98\ub7fc \ud0c8\ud575\uc6b4\ub3d9 \uc548\uc5d0\uc11c\ub3c4 \uc911\uc559, \uc218\ub3c4\uad8c \uc911\uc2ec\uc758 \ud0c8\ud575\uc6b4\ub3d9(\uc9c4\uc601)\uc774 \ub354 \ubd84\ud654\ub418\uace0 \ub2e4\uc591\ud574\u000d\u000a\uc9c8 \ud544\uc694\uc131\uc744 \uc5b8\uae09\ud55c \uac83\uc774\ub2e4. \uc774\ucc98\ub7fc \uc704\ud5d8\uc740 \ub2e8\uc77c\ud55c \uac83\uc774 \uc544\ub2c8\uba70 \ub2e4\ub978 \ud589\uc704\uc790\ub4e4\uc758 \uad00\uc810\uacfc \u000d\u000a\uc804\ub7b5\uc758 \ubaa8\uc21c\uc801\uc778 \uad00\uacc4 \uc18d\uc5d0\uc11c \uc8fc\uccb4\uc801\uc778 \uc704\ud5d8\uacbd\uad00(\ub4e4)\uc774 \ub9cc\ub4e4\uc5b4\uc9c4\ub2e4. \ud658\uacbd\ub2e8\uccb4/\ud0c8\ud575\uc6b4\ub3d9\uac00/\u000d\u000a\ud0c8\ud575\uc6b4\ub3d9\uc758 \u2018\ud0c8\ud575\u2019\uc774\ub77c\ub294 \uac70\ub300\ub2f4\ub860\uc740 \ub54c\ub85c\ub294 \uc815\ubd80\uc815\ucc45\uc5d0 \uc601\ud5a5\uc744 \uc8fc\uae30\ub3c4 \ud558\uc9c0\ub9cc, \ud575\ubc1c\uc804\uc18c\u000d\u000a\uc640 \uc0b4\uc544\uac00\ub294 \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc758 \uc704\ud5d8\uacbd\uad00\uc744 \uc81c\ub300\ub85c \ub2f4\uac70\ub098 \uad6c\ud604\ud558\uc9c0 \ubabb\ud55c\ub2e4\ub294 \ud55c\uacc4\uac00 \uc788\ub2e4. \ud0c8\u000d\u000a\ud575\uc9c4\uc601\uc758 \uc8fc\uc7a5\ub3c4 \ud558\ub098\uc758, \ub2e8\uc77c\ud55c \uace0\uc720\ud55c \u2018\ud0c8\ud575\u2019\uc774 \uc544\ub2c8\ub77c \uc774\uc8fc\ubb38\uc81c, \uacc4\ud68d\uc801 \ub204\ucd9c\uacfc \uc774\uc5d0 \ub530\u000d\u000a\ub978 \uac74\uac15, \ud53c\ud3ed\ubb38\uc81c, \ud3d0\ub85c\ubb38\uc81c, \ud575\ud3d0\uae30\ubb3c \ucc98\ub9ac \ubc0f \ubcf4\uad00 \ubb38\uc81c \ub4f1 \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc758 \uc758\uacac\uc744 \uace0\ub824\ud560 \u000d\u000a\ud544\uc694\uac00 \uc788\uc73c\uba70, \uc774\ub294 \uae30\uc874 \ud0c8\ud575\uc774 \uc758\ubbf8\ud558\ub294 \ud575\ubc1c\uc804\uc18c \uc124\uce58 \uacc4\ud68d \ubc0f \uac74\uc124 \ubc18\ub300, \uc624\ub798\ub41c \uc6d0\uc804\u000d\u000a\uc758 \uc218\uba85\uc5f0\uc7a5 \ubc18\ub300, \uc2e0\uaddc\uc6d0\uc804 \uae08\uc9c0 \ubc0f \uc601\uad6c\uc815\uc9c0 \uc694\uad6c\ub97c \ub118\uc5b4\uc124 \ud544\uc694\uac00 \uc788\uc74c\uc744 \ubcf4\uc5ec\uc900\ub2e4. \uc774\u000d\u000a\ub54c \uc911\uc694\ud55c \uac83\uc740 \ud575\ubc1c\uc804\uc18c\uc640 \ud568\uaed8 \uc0b4\uc544\uac00\ub294 \uc0ac\ub78c\ub4e4\uc774 \uc77c\uc0c1\uc5d0\uc11c \ub290\ub07c\ub294 \ubd88\uc548, \uc704\ud5d8\uc744 \ud1a0\ub300\u000d\u000a\ub85c \ub9cc\ub4e4\uc5b4\uc9c4 \ub300\ucc45\uc774\ub098 \uc804\ub7b5\uc744 \uc5b4\ub5bb\uac8c, \uc5bc\ub9c8\ub098 \ud488\uace0 \uadf8\ub4e4\uacfc \ud568\uaed8 \ub2e4\uc591\ud55c \ud0c8\ud575\uc6b4\ub3d9 \ubc0f \uc8fc\uc7a5\u000d\u000a\uc744 \ud574\ub098\uac00\ub294\uac00\uc774\ub2e4. \uc815\ud604\uc8fc\u00b7\ud669\uc9c4\ud0dc(2020)\ub294 \ud55c\uad6d \uc815\ubd80 \uc2a4\uc2a4\ub85c \u201cK-\ubc29\uc5ed\u201d\uc73c\ub85c \uc9c0\uce6d\ud55c \uc131\uacf5\u000d\u000a\uc801\uc778 \ubc29\uc5ed\ubaa8\ub378\uc744 \ud1b5\ud558\uc5ec \uac15\uc870\ub41c \ub300\ubb38\uc790 K\ub85c \uc0c1\uc9d5\ub418\ub294 \uad6d\uac00\uc758 \uc5ed\ud560\ubfd0\ub9cc \uc544\ub2c8\ub77c \uc18c\ubb38\uc790 k(\u000d\u000a\ub300\ud55c\uc758\uc0ac\ud611\ud68c, \uc790\uc6d0\ubd09\uc0ac\ub2e8\uccb4, \uc9c0\uc790\uccb4, \uc18c\uc0c1\uacf5\uc778 \ub4f1\uc758 \ub2e4\uc591\ud55c \ud589\uc704\uc790\ub4e4)\ub85c\uc11c \uc0ac\ud68c\ub97c \uad6c\uc131\ud558\ub294 \ub2e4\uc591\ud55c \uac1c\uc778, \uc870\uc9c1\uacfc \uc774\ub4e4\u000d\u000a\uc774 \ud615\uc131\ud558\ub294 \uc0ac\ud68c\uacf5\uac04\uc801 \ud2b9\uc131\ub4e4\ub85c \uc2dc\uc57c\ub97c \ud655\uc7a5\ud560 \ud544\uc694\uc131\uc744 \uac15\uc870\ud55c\ub2e4. \uc989, \u2018\ud575\ubc1c\uc804\uc18c \uac00\ub3d9\u000d\u000a\uc911\uc9c0\uc640 \uc2e0\uaddc\uc6d0\uc804 \uae08\uc9c0\u2019\ub9cc\uc774 \uc544\ub2c8\ub77c \uc9c0\uae08\uae4c\uc9c0 \u2018\ud0c8\ud575\u2019\uc774\ub77c\ub294 \uac70\ub300\ub2f4\ub860\uc5d0\uc11c \ubc30\uc81c\ub418\uc5c8\ub358 \uc9c0\uc5ed\u000d\u000a\uc8fc\ubbfc\ub4e4\uc758 \uc774\uc8fc, \uac74\uac15/\ud53c\ud3ed\ubb38\uc81c, \ud3d0\ub85c\uc640 \ud575\ud3d0\uae30\ubb3c \ubcf4\uad00 \ubc0f \ucc98\ub9ac \ub4f1 \ub354 \ub2e4\uc591\ud55c \u2018\ud0c8\ud575\u2019\ub108\uba38\uc758\u000d\u000a\uc774\uc57c\uae30\ub4e4\uc744 \ud560 \ud544\uc694\uac00 \uc788\ub2e4.\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 : \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 70\u000d\u000a3. \ud559\ubb38\uc801 \u00b7 \uc0ac\ud68c\uc801 \ubc18\uc751\uacfc \ud65c\uc6a9\u000d\u000a\ud604\uc7a5\uc5f0\uad6c\uc640 \ucd5c\uc885\ubcf4\uace0\uc11c\ub97c \ubc14\ud0d5\uc73c\ub85c 2\ud3b8\uc758 \ud559\uc220\ub17c\ubb38(\uadf8\ub4e4\uc740 \uc65c \uc0c1\uc5ec\ub97c \ub044\ub294\uac00, \uc2e4\ucc9c\uc9c0\ud5a5\uc5f0\uad6c\uc5d0\uc11c \uc2e4\ucc9c\uc744 \ub0b4\uc7ac\ud55c\u000d\u000a\uc5f0\uad6c\ub85c)\uacfc 1\uad8c\uc758 \ub2e8\ud589\ubcf8(\uc6d0\uc804\ub9c8\uc744)\uc744 \uc37c\uace0, \ucc45\uacfc \uc5f0\uad6c\ub97c \ubc14\ud0d5\uc73c\ub85c \uc5b8\ub860 \uc778\ud130\ubdf0, \uadf8\ub9b0\ud53c\uc2a4 \ud31f\uce90\uc2a4\ud2b8 \u000d\u000a\ub179\uc74c \ub4f1\uc744 \uc9c4\ud589\ud588\ub2e4. \uc9c0\uae08\uae4c\uc9c0\ub294 \uc5f0\uad6c\ub97c \ubc14\ud0d5\uc73c\ub85c \uc8fc\ub85c \ud559\uc220\ub17c\ubb38\uc744 \uc791\uc131\ud588\ub2e4\uba74, \uc774\ubc88 \uc5f0\uad6c\u000d\u000a\ub97c \ud1b5\ud574\uc11c\ub294 \ub2e4\uc591\ud55c \uacb0\uacfc\ubb3c(\ub17c\ubb38, \ubcf4\uace0\uc11c, \ucc45, \uc2e0\ubb38\uae30\uc0ac, \ud31f\uce90\uc2a4\ud2b8 \ub179\uc74c\uacfc \uc601\uc0c1)\uc744 \ud1b5\ud574 \uc18c\uc218\uc758 \uc5f0\uad6c\uc790\ub9cc\uc774 \uc544\ub2c8\u000d\u000a\ub77c \ub2e4\uc218 \ub2e4\uc591\ud55c \uc0ac\ub78c\ub4e4\uc744 \ub9cc\ub0a0 \uc218 \uc788\uc5c8\ub2e4. \ubcf4\ud1b5\uc740 \ud575\ubc1c\uc804\uc18c \uadfc\ucc98\uc5d0 \uc0ac\ub78c\uc774 \uc0ac\ub294 \uc904 \ubab0\ub790\uac70\u000d\u000a\ub098, \uc5b4\ub5a4 \uc0c1\ud669\uc5d0 \uc788\ub294\uc9c0\ub97c \ubab0\ub790\ub294\ub370, \ucc45\uc774\ub098 \uae30\uc0ac, \ud31f\uce90\uc2a4\ud2b8\ub97c \ud1b5\ud574 \ud575\ubc1c\uc804\uc18c\uac00 \uc5b4\ub5a4 \ubb38\uc81c,\u000d\u000a\uc704\ud5d8\uc744 \uc8fc\ubbfc\ub4e4\uc5d0\uac8c \uc804\uac00\ud558\ub294\uc9c0 \uc54c\uac8c\ub418\uc5c8\ub2e4\ub294 \ub9d0\uc744 \uac00\uc7a5 \ub9ce\uc774 \ub4e4\uc5c8\ub2e4. \ub17c\ubb38\uc744 \ud1b5\ud574 \ud559\uc220\uacf5\u000d\u000a\ub3d9\uccb4 \ub0b4\uc5d0\uc11c \uc18c\ud1b5\ud560 \uc218\ub3c4 \uc788\uc9c0\ub9cc, \ud559\uc220\uc801 \ud639\uc740 \uc804\ubb38\uc801\uc778 \ud2b9\uc9d5 \uc678\uc5d0\ub3c4 \uc2e4\ucc9c\uc801\uc774\uba70 \ub300\uc911\uc801\uc778\u000d\u000a\ubc29\uc2dd\uc744 \ud1b5\ud574 \ub354 \ub9ce\uc740 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \uc81c\ub300\ub85c \uc54c\ub824\uc9c0\uc9c0 \uc54a\uc740 \uc0ac\ub78c\ub4e4, \uc9c0\uc5ed, \ubb38\uc81c\ub4e4\uc744 \uc54c\ub9b4 \ub54c \ube44\u000d\u000a\ub85c\uc18c \ub098\uc758 \uacf5\ubd80\uc640 \uc5f0\uad6c\uac00 \ub354 \ud070 \ud798\uc744 \ubc1c\ud718\ud560 \uc218\ub3c4 \uc788\uaca0\ub2e4\ub294 \uc0dd\uac01\uc744 \ud558\uac8c \ub418\uc5c8\ub2e4. \uc774\ub7ec\ud55c \uc2e4\u000d\u000a\ucc9c\uc801\uc778 \uc5f0\uad6c\uc5d0 \ub300\ud55c \uace0\ubbfc\uc740 \uc232\uacfc\ub098\ub214\uc5d0\uc11c \ud574\ub9c8\ub2e4 \uc5ec\ub294 <\ud658\uacbd\ud559\uc220\ud3ec\ub7fc>\uc5d0\uc11c \ubc1c\ud45c\ub97c \ud588\uace0,\u000d\u000a\uc7a5\ub824\uc0c1\uc744 \ubc1b\uc558\ub2e4. \ub610\ud55c, <\uc6d0\uc804\ub9c8\uc744>\uc774\ub77c\ub294 \ub2e8\ud589\ubcf8\uc774 \ub098\uc628 \uc774\ud6c4 \ubd81\ucf58\uc11c\ud2b8\ub97c \uc9c4\ud589\ud588\ub294\ub370, \uadf8\u000d\u000a\uc790\ub9ac\uc5d0\uc11c \ub098\ub204\uc5c8\ub358 \uc0dd\uc0dd\ud55c \uc9c8\ubb38, \ucd94\uc0c1\uc801\uc774\uace0 \uac70\ub300\ub2f4\ub860 \uc2dd\uc758 \uc9c8\ubb38\uc774 \uc544\ub2cc \ud604\uc2e4\uc5d0\uc11c \ub290\ub07c\ub294\u000d\u000a\ud0c8\ud575\uc758 \uc5b4\ub824\uc6c0\uc744 \ub4e4\uc73c\uba74\uc11c \uc6b0\ub9ac\uc758 \uc5f0\uad6c\ub3c4 \uc120\ud589\uc5f0\uad6c\ub97c \ucda9\ubd84\ud788 \uc77d\ub418, \ud604\uc2e4/\ud604\uc7a5/\uc0ac\ub78c\uc744 \ub354\u000d\u000a\uc6b1 \uc911\uc694\ud558\uac8c \ub2e4\ub8e8\uc5b4\uc57c \ud560 \ud544\uc694\uc131\uc744 \ub290\uaf08\ub2e4.\u000d\u000a4. \uc131\ucc30\uacfc \ud6c4\uae30\u000d\u000a\uc6d0\uc804\uad00\ub828 \uc5f0\uad6c\ub4e4\uc740 \uc801\uc9c0 \uc54a\ub2e4. \uc6d0\uc804 \uc758\uc874\uc801\uc778 \uc0ac\ud68c, \uc81c\ub3c4, \ubc95\uc774 \uc5b4\ub5bb\uac8c \ub9cc\ub4e4\uc5b4\uc84c\ub294\uc9c0\ub97c \u000d\u000a\ucd94\uc801\ud55c \uc5f0\uad6c, \uc8fc\ubbfc\ub4e4\uc774 \uc6d0\uc804 \uac74\uc124\uc5d0 \ud56d\uc758, \ubc18\ub300\ud558\uc5ec \uc2f8\uc6b0\ub294 \ub0b4\uc6a9 \ub4f1 \uc9c0\uae08\uae4c\uc9c0\ub294 \uc8fc\ub85c \uac70\uc2dc\u000d\u000a\uc801\uc778 \ucc28\uc6d0\uc5d0\uc11c \uc6d0\uc804\uc744 \ub2e4\ub8e8\uac70\ub098, \ubc18\ub300\ud558\ub294 \uc18c\uc218 \uc8fc\ubbfc\uc758 \uc6b4\ub3d9 \ud639\uc740 \uac08\ub4f1\uc744 \ub2e4\ub8ec \uac83\uc774 \ub300\ubd80\u000d\u000a\ubd84\uc774\uc5c8\ub2e4. \ud575\ubc1c\uc804\uc18c\uac00 \uac74\uc124\ub418\uace0 \ub09c \ud6c4, \uc8fc\ubbfc\ub4e4\uc774 \uc6d0\uc804\uc73c\ub85c\ubd80\ud130 \uc5b4\ub5a4 \uc601\ud5a5\uc744 \ubc1b\uac70\ub098, \uad00\uacc4\ub97c \u000d\u000a\ub9fa\uc73c\uba70 \uc0b4\uc544\uac00\ub294\uc9c0\ub97c \ucd94\uc801\ud55c \uc5f0\uad6c\ub294 \uac70\uc758 \uc5c6\uae30\uc5d0 \ud604\uc7a5\uc5d0 \uba38\ubb3c\uba70 \uc8fc\ubbfc\ub4e4\uc774 \uc6d0\uc804\uacfc \ud568\uaed8 \uc0b4\u000d\u000a\uc544\uac00\ub294 \ubaa8\uc2b5, \uadf8\ub4e4\uc758 \uc77c\uc0c1\uc744 \uc5f0\uad6c\ud558\uace0 \uc2f6\uc5c8\ub2e4. \ubb3c\ub860, 8\uac1c\uc6d4\uc774\ub77c\ub294 \uc2dc\uac04\uc740 \uc6d4\uc1311\ud638\uae30\uac00 \ucc98\u000d\u000a\uc74c \uc9c0\uc5b4\uc9c4 \ud6c4 \uac00\ub3d9\ub41c 30~40\ub144\uac04 \uc9c0\uc5ed\uc0ac\ud68c\uc640 \uc8fc\ubbfc\ub4e4\uc5d0\uac8c \ubbf8\uce5c \uc601\ud5a5 \ubaa8\ub450\ub97c \uc774\ud574\ud558\uae30\uc5d4 \ubd80\u000d\u000a\uc871\ud55c \uc2dc\uac04\uc774\uc5c8\uc9c0\ub9cc, \ub2e4\ub978 \uc5f0\uad6c\uc5d0\uc11c \uc880\ucc98\ub7fc \uc2dc\ub3c4\ud558\uc9c0 \uc54a\uc558\ub358 \uc5d0\uc2a4\ub178\uadf8\ub77c\ud53c\ub97c \ud65c\uc6a9\ud558\uc5ec 8\uac1c","70 71\u000d\u000a\uc6d4\uc744 \uc6d4\uc131\uc5d0 \uba38\ubb3c\ub800\ub2e4. \uadf8 \uacfc\uc815\uc5d0\uc11c \uc8fc\ubbfc\ub4e4\uc774 \ub208\uc5d0 \ubcf4\uc774\uc9c0 \uc54a\ub294 \uc704\ud5d8\uacfc \ud568\uaed8 \uc0b4\uc544\uac00\uace0, \ube44\u000d\u000a\uac00\uc2dc\uc801\uc774\uba70 \ub290\ub9b0\ud3ed\ub825\uc744 \ub4dc\ub7ec\ub0b4\uae30 \uc704\ud574 \uc2f8\uc6b0\ub294 \uacfc\uc815\uc744 \ub4e4\uc73c\uba70, \uc870\uae08\uc529 \uadf8\ub4e4\uacfc \uce5c\ud574\uc84c\uace0, \ub300\u000d\u000a\ubd80\ubd84\uc758 \uc778\ud130\ubdf0\ub4e4\uc740 \uc8fc\ub85c \uc8fc\ubbfc\ub4e4\uc758 \uc9d1\uc5d0\uc11c \ud588\ub2e4. \uadf8\ub7ec\ub098, \uc8fc\ubbfc\ub4e4\uc774 \uc74c\uc2dd\uc744 \uc8fc\uac70\ub098, \uc790\uc2e0\ub4e4\uc774 \u000d\u000a\uc7ac\ubc30\ud55c \ucc28\ub97c \ub0b4\uc904 \ub54c, \ub098\ub3c4 \ubaa8\ub974\ub294 \ucc30\ub098\uc5d0 \u2018\uc800 \uc548\uc5d0\ub3c4 \ubc29\uc0ac\uc131\ubb3c\uc9c8\uc774 \ub4e4\uc5b4\uc788\uaca0\uc9c0\u2019\ub77c\ub294 \uc0dd\uac01\u000d\u000a\uc744 \ud558\uac8c \ub418\uc5c8\uace0, \uc801\uc5b4\ub3c4 \uc8fc\ubbfc\ub4e4\uc774 \ub9e4\uc77c, \ub9e4\uc21c\uac04 \ud588\uc744 \uc77c\uc0c1\uc5d0\uc11c\uc758 \uace0\ubbfc\uacfc \uc704\ud5d8\uacfc \ud568\uaed8 \ud558\ub294 \u000d\u000a\uc0b6\uc744 \ub354 \uc798 \uc774\ud574\ud558\uac8c \ub41c \uac83\uc774 \uc544\ub2cc\uac00 \uace0\ubbfc\ud574\ubcf8\ub2e4.\u000d\u000a\uc11c\uc6b8, \uc218\ub3c4\uad8c\uc5d0\uc11c \uc6d0\uc804\ubb38\uc81c\ub97c \ubc14\ub77c\ubcf4\uace0, \uc0ac\uace0\uac00\ub2a5\uc131, \uc724\ub9ac/\ub3c4\ub355\uc801\uc778 \uc774\uc720\ub85c \ud0c8\ud575\uc744 \uc8fc\u000d\u000a\uc7a5\ud574\uc654\ub358 \uacfc\uac70\uc640\ub294 \ub2e4\ub974\uac8c, \ud604\uc7a5\uc5d0\uc11c, \uc6d0\uc804\uacfc \ud568\uaed8 \uc0b4\uc544\uac00\ub294 \uc77c\uc0c1\uc5d0\uc11c\ub294 \ucd94\uc0c1\uc801\uc774\uace0 \uac70\uc2dc\u000d\u000a\uc801\uc778 \uc774\uc720\uac00 \uc544\ub2c8\ub354\ub77c\ub3c4, \ub9e4\uc77c, \ub9e4\uc21c\uac04 \uc704\ud5d8\uacfc \ud568\uaed8 \uc0b4\uc544\uac00\ub294 \ubd88\uc548\uc815\ud55c \uc77c\uc0c1 \uadf8 \uc790\uccb4\ub85c \u2018\ud0c8\u000d\u000a\ud575\u2019\uc774\ub77c\ub294 \uc8fc\uc7a5\ub3c4 \ub2e4\ub974\uac8c \uc0dd\uac01\ud558\uac8c \ub418\uc5c8\ub2e4. 8\uac1c\uc6d4\uc740 \ube44\ub85d \uae34 \uc2dc\uac04\uc740 \uc544\ub2c8\uc5c8\uc9c0\ub9cc, \uc11c\uc6b8\uc5d0\uc11c \u000d\u000a\ub098\uace0 \uc790\ub780 \ub0b4\uac00 \uc801\uc5b4\ub3c4, \uc9c0\uc5ed\uacfc \uc8fc\ubbfc\uc758 \uad00\uc810\uc5d0\uc11c \uc6d0\uc804\ubb38\uc81c\uc640 \u2018\ud0c8\ud575\u2019\uc774\ub77c\ub294 \ubaa9\uc18c\ub9ac\ub97c \uc0dd\uac01\u000d\u000a\ud558\uac8c \ub41c \uacc4\uae30\uac00 \ub418\uc5c8\ub2e4\ub294 \uac83\uc740 \ubd80\uc815\ud560 \uc218 \uc5c6\ub2e4. \ubab8, \uc704\uce58\uac00 \ubc14\ub00c\uba74\uc11c, \ubb38\uc81c\ub97c \ubc14\ub77c\ubcf4\ub294 \uad00\u000d\u000a\uc810\ub3c4 \uc790\uc5f0\uc2a4\ub808 \ubc14\ub00c\uac8c \ub418\uc5c8\uace0, \ud0c8\ud575/\ucc2c\ud575\uc774\ub77c\ub294 \uc774\ubd84\ubc95\uc73c\ub85c \uc124\uba85\ud560 \uc218 \uc5c6\ub294 \ud68c\uc0c9\uc9c0\ub300 \uc18d\u000d\u000a\uc758 \ubaa9\uc18c\ub9ac\ub4e4\uc744 \uc870\uae08\uc529 \ud574\uc11d\ud558\uac8c \ub418\uc5c8\ub2e4.\u000d\u000a<\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4>\uc744 \ud1b5\ud574\uc11c\ub294 \ub208\uc5d0 \ubcf4\uc774\uc9c0\ub3c4, \ub0c4\uc0c8\ub3c4 \ub098\uc9c0 \uc54a\uc73c\uba70 \uadf8 \uc704\ud5d8\u000d\u000a\uc131\uc744 \uc785\uc99d\ud558\ub294 \uac83\ub3c4 \uc27d\uc9c0 \uc54a\uc740 \ub290\ub9b0\ud3ed\ub825\uacfc \uc2f8\uc6b0\uba70, \uc778\uc815\ud22c\uc7c1\ud558\ub294 \uc8fc\ubbfc\ub4e4\uc744 \ub9cc\ub0ac\uace0, \u2018\ud0c8\ud575\u2019\u000d\u000a\uc774 \uc544\ub2cc \u2018\uc774\uc8fc\u2019\ub97c \uac15\uc870\ud560 \uc218\ubc16\uc5d0 \uc5c6\ub358 \ud604\uc2e4\uc744 \uc774\ud574\ud558\uac8c \ub418\uc5c8\ub2e4. \ud604\uc7ac\ub294, \uc774\ub7ec\ud55c \ud604\uc7a5\uc758 \ubaa9\u000d\u000a\uc18c\ub9ac\uc640 \uc5f0\uad6c\ub4e4\uc744 \ud1b5\ud574 \u2018\uc5d0\ub108\ud1a0\ud53c\uc544\ub97c \uc704\ud55c \uacf5\uac04\uacfc \uc8fc\uccb4\uc758 \ud615\uc131\u2019\uc774\ub77c\ub294 \uc81c\ubaa9\uc758 \ubc15\uc0ac\ub17c\ubb38\uc744 \u000d\u000a\uc900\ube44\ud558\uace0 \uc788\ub2e4. \ud559\uc704\ub17c\ubb38\uc5d0\uc11c\ub294 \uc6d0\uc804\uc774 \ub2e8\uc21c\ud788 \uc704\ud5d8/\uc0ac\uace0\ub97c \uc0c1\uc9d5\ud558\ub294 \ubc1c\uc804\uc18c\ub9cc\uc774 \uc544\ub2c8\ub77c, \u000d\u000a\uc9c0\uc5ed\uc0ac\ud68c\uc5d0\uc11c \ucee4\ub2e4\ub780 \uc601\ud5a5\ub825\uacfc \uacbd\uc81c,\uc0ac\ud68c,\uc815\uce58\uc801 \uc9c0\uc6d0\uc744 \uc81c\uacf5\ud558\ub294 \uae30\uc5c5\uc774\uba70, \ub54c\ub85c\ub294 \uc815\ubd80\u000d\u000a\uac00 \uc81c\uacf5\ud574\uc57c \ud558\ub294 \ubcf5\uc9c0, \ub178\uc778, \uad50\uc721, \uc778\ud504\ub77c \ub4f1 \uc900 \uc815\ubd80\uc758 \uc5ed\ud560\ub3c4 \ub300\uc2e0\ud558\ub294 \ub4f1 \uc9c0\uc5ed\uc5d0\uc11c \uc6d0\u000d\u000a\uc804\uc758 \uc5ed\ud560, \uc9c0\uc704 \ud639\uc740 \ud589\uc704\ub97c \ud568\uaed8 \ubd84\uc11d\ud558\uace0 \uc788\ub2e4. 30-40\ub144\uac04 \uc9c0\uc5ed\uc0ac\ud68c\uc5d0 \ubfcc\ub9ac\ubc15\ud788\uace0, \uacc4\u000d\u000a\uc18d\ud574\uc11c \uc601\ud5a5\ub825\uc744 \ud589\uc0ac\ud558\ub294 \uc6d0\uc804\uacfc \uadf8 \uc6d0\uc804\uc744 \uc870\uae08\uc529 \u2018\uc9c0\uc9c0\ud558\uace0 \ucc2c\uc131\u2019\ud558\uac8c \ub418\ub294 \uc9c0\uc5ed\uc0ac\ud68c\uc640 \u000d\u000a\uc8fc\ubbfc\ub4e4 \uac04\uc758 \uad00\uacc4\ub97c \ud568\uaed8 \ud3ec\ucc29\ud558\uba70, \uacb0\uad6d \ud575\ubc1c\uc804\uc18c\uac00 \uc5c6\ub294 \uc0b6/\uc9c0\uc5ed\ubc1c\uc804\uc744 \uc0c1\uc0c1\ud558\uae30 \uc5b4\ub824\u000d\u000a\uc6b4 \ud604\uc7ac\uc758 \uc0c1\ud669\uc744 \ud1b5\ud574 \uc6d0\uc804\uc744 \uc9c0\uc9c0\ud558\ub294 \uac83\uc758 \uc758\ubbf8\ub97c \uc8fc\ubbfc, \uc9c0\uc5ed\uc758 \uad00\uc810\uc5d0\uc11c \ud574\uc11d\ud558\ub294 \uc791\u000d\u000a\uc5c5\uc744 \uc2dc\ub3c4\ud558\uace0 \uc788\ub294 \uc148\uc774\ub2e4. \uc989, \uc6d0\uc804\uc744 \ucc2c\uc131\uacfc \ubc18\ub300\ub77c\ub294 \uc774\ubd84\ubc95\uc801 \uc2dc\uac01\uc73c\ub85c \ubc14\ub77c\ubd10 \uc654\ub358 \u000d\u000a\uad00\uc810\uc758 \ud55c\uacc4\ub97c \uc9c0\uc801\ud558\uace0, \uc774\uc640 \ub3d9\uc2dc\uc5d0 \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc774 \uc6d0\uc804\uc744 \uc9c0\uc9c0\ud558\uace0 \ucc2c\uc131\ud558\ub294 \uc774\uc720\ub97c \u2018\uacbd\u000d\u000a\ube44\uac00\uc2dc\ud654\ub41c \uc704\ud5d8\uacfc \uc874\uc7ac\ub4e4 : \uc6d4\uc131 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c\ub97c \uc911\uc2ec\uc73c\ub85c "," & 72\u000d\u000a\uc81c\uc801\uc73c\ub85c \uc885\uc18d\u2019\ub418\uc5c8\ub2e4\ub294 \uacbd\uc81c\ud658\uc6d0\ub860\uc801 \uc124\uba85\uc744 \uc9c0\uc591 \ud639\uc740 \uadf9\ubcf5\ud568\uc73c\ub85c\uc368 \ud55c\uc218\uc6d0\uc758 \uc5ed\ud560, \uc9c0\u000d\u000a\uc704, \ud589\uc704\uc758 \ubcc0\ud654 \ub4f1\uc744 \ud1b5\ud574 \uc9c0\uc5ed\uc0ac\ud68c\uc5d0\uc11c \ud55c\uc218\uc6d0\uc758 \uc601\ud5a5\ub825\uc744 \ubd84\uc11d\ud558\uace0 \uc788\ub2e4. \uc8fc\ubbfc\uacfc \uc9c0\uc5ed\u000d\u000a\uc758 \uad00\uc810\uc5d0\uc11c \uc218\ud589\ud588\ub358 \ud604\uc7a5\uc5f0\uad6c\ub97c \ud1b5\ud574 \uc9c0\uc5ed\uc0ac\ud68c\uac00 \ud55c\uc218\uc6d0\uc5d0 \uacbd\uc81c\uc801\uc73c\ub85c \uc885\uc18d\ub41c \uc874\uc7ac\ub9cc\uc774 \u000d\u000a\uc544\ub2c8\ub77c \uc801\uadf9\uc801 \ud639\uc740 \ub178\uace8\uc801\uc73c\ub85c \ud55c\uc218\uc6d0\uc744 \uc9c0\uc5ed\uac1c\ubc1c\uc758 \ub3c4\uad6c\ub85c \ud65c\uc6a9\ud558\uae30\ub3c4 \ud55c \uacfc\uc815\uacfc \ub9e5\ub77d\u000d\u000a\uc744 \ud1b5\ud574 \uc6d0\uc804\uacfc \ud568\uaed8 \uc0b4\uc544\uc628 \ubcf5\uc7a1\ud55c \uc5ed\uc0ac\uc640 \ud604\uc2e4\uc744 \ud568\uaed8 \uc870\uba85\ud560 \ud544\uc694\uc131\uc744 \ub290\uaf08\uace0, \ud0c8\uc6d0\uc804\u000d\u000a\uc815\ucc45\uc744 \uc2dc\ud589\ud560 \ub54c, \ub2e8\uc21c\ud788 \ucc2c\uc131\uacfc \ubc18\ub300, \ud639\uc740 \u2018\uc704\ud5d8\u2019\ud558\uace0 \u2018\ub098\uc05c\u2019\uc5d0\ub108\uc9c0\uc6d0\uc73c\ub85c \uc811\uadfc\ud558\uae30\ubcf4\ub2e4\u000d\u000a\ub294, \u2018\uc815\uc758\ub85c\uc6b4 \uc804\ud658\u2019\uacfc \uac19\uc740 \uad00\uc810\uc744 \ud1b5\ud558\uc5ec \uc9c0\uc5ed\uc0ac\ud68c, \uc8fc\ubbfc\ub4e4\uc774 \ub9fa\uace0 \uc788\ub294 \ud55c\uc218\uc6d0\uacfc\uc758 \uad00\uacc4 \u000d\u000a\ud639\uc740 \ud55c\uc218\uc6d0\uc758 \uc601\ud5a5\ub825\uc73c\ub85c\ubd80\ud130 \u2018\uc6d0\uc804 \uc5c6\ub294 \uc0b6\u2019\uc744 \uc0c1\uc0c1\ud560 \uc218 \uc788\ub294 \uadfc\uac70 \ubc0f \ud604\uc2e4\uc744 \uc81c\uacf5\ud558\uc600\u000d\u000a\ub2e4\ub294 \uc758\ubbf8\uac00 \uc788\ub2e4\ub294 \uac83\uc744 \uc81c\uc2dc\ud560 \uac83\uc774\ub2e4.\u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","72 73\u000d\u000a\uc774\uc6a9\uc11d / \uc11c\uc6b8\uc2dc NPO \uc9c0\uc6d0\uc13c\ud130, 2019\u000d\u000a\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798 :\u000d\u000a\ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c\u000d\u000a1. \ubb38\uc81c\uc758\uc2dd\uacfc \uc5f0\uad6c \ub3d9\uae30 \u000d\u000a\ub098\ub294 \ud65c\ub3d9\uac00\ub2e4. \uc0ac\ud68c\ubcc0\ud654\ub97c \uc704\ud574 \ucea0\ud398\uc778\uc744 \uc870\uc9c1\ud558\ub294 \uc77c\uc744 \uc8fc\ub85c \ud55c\ub2e4. \ub0b4\uac00 \ud65c\ub3d9\ud558\ub294 \uc804\u000d\u000a\uc7c1\uc5c6\ub294\uc138\uc0c1\uc740 \uc5ec\ub7ec \ubc29\uc2dd \uc911\uc5d0\uc11c \uc9c1\uc811\ud589\ub3d9\uc744 \uc870\uc9c1\ud558\uace0 \uc2e4\ud589\ud558\ub294 \uac83\uc744 \ub2e8\uccb4\uc758 \uc8fc\uc694\ud55c \ud65c\ub3d9 \u000d\u000a\ubc29\uc2dd\uc73c\ub85c \uc0bc\uace0 \uc788\ub2e4. \ub2e4\uc2dc \ub9d0\ud558\uba74 \ub098\ub294 \uc5f0\uad6c\uc790\ub3c4 \uc544\ub2c8\uace0, \uc5f0\uad6c\ub97c \uc8fc\ub85c \ud558\ub294 \ud65c\ub3d9\uac00\ub3c4 \uc544\ub2c8\u000d\u000a\ub2e4. \ub2e4\ub9cc \uc870\uadf8\ub9cc \ub2e8\uccb4 \ud65c\ub3d9\uac00\ub2e4 \ubcf4\ub2c8 \uc5b4\uca54 \uc218 \uc5c6\uc774 \uc774\uac83\uc800\uac83 \ub2e4 \ud574\uc57c\ud560 \ub54c\uac00 \ub9ce\uc73c\uba70, \uadf8\uc911\u000d\u000a\uc5d0 \uc5f0\uad6c\uc640 \uad00\ub828\ub41c \uc77c\ub3c4 \uc788\ub2e4. \uac00\ub839, \uc804\uc7c1\uc5c6\ub294\uc138\uc0c1\uc774 \uc624\ub7ab\ub3d9\uc548 \ud3bc\uccd0\uc628 \ubcd1\uc5ed\uac70\ubd80\uc6b4\ub3d9\uc758 \uacbd\uc6b0\u000d\u000a\u2018\ud3c9\ud654\u2019\ub294 \ubd80\uc815\ud560 \uc218 \uc5c6\ub294 \uc778\uac04 \uc138\uacc4\uc758 \uc911\ub300 \uac00\uce58\uc774\uc790 \uaddc\ubc94\uc73c\ub85c \uc5ec\uaca8\uc9c4\ub2e4. \uadf8\ub7f0\ub370 \uc2e0\uae30\ud558\uac8c\ub3c4 \ud3c9\ud654\u000d\u000a\uac00 \uc2e4\uc81c \ubb34\uc5c7\uc778\uc9c0 \ub610 \uadf8\uac83\uc744 \uc5b4\ub5bb\uac8c \uad6c\ud604\ud560 \uc218 \uc788\ub294\uc9c0 \ub4f1\uc5d0 \ub300\ud574\uc11c\ub294 \ub531\ud788 \uc0c1\uc774 \uc798 \uc7a1\ud788\uc9c0\ub294 \uc54a\ub294\ub2e4. \u000d\u000a\uc138\uacc410\uc704\uad8c\uc758 \u2018\ub300\uad6d\u2019\uc778 \ud55c\uad6d\uc758 \uad50\uc721\uacfc\uc815\uc774 \uadf8\ub807\ub2e4. \uac00\ub839 \ub3c5\uc77c\uc758 \uc911\ud559\uad50 \uad50\uacfc\uc11c\uc5d0\uc11c\ub294 \uc815\uce58\ub97c \u2018\uc804\uc7c1\u000d\u000a\uacfc \ud3c9\ud654\uc758 \ubb38\uc81c\ub97c \ub2e4\ub8e8\ub294 \uc2e4\ucc9c\u2019\uc73c\ub85c \uaddc\uc815\ud558\uba74\uc11c, \ud3c9\ud654\uc5d0 \ub300\ud55c \uac1c\ub150\uc801 \uc774\ud574\ub97c \uc9c4\uc791\uc2dc\ud0a4\uace0\uc790 \ud55c\ub2e4. \uc0dd\u000d\u000a\uac01\uacfc \ucc98\uc9c0\uac00 \uac01\uae30 \ub2e4\ub978 \uc774\ub4e4\uc774 \uc5bd\ud600 \uc774\ub8e8\uc5b4\uac00\ub294 \uc778\uac04\uc758 \uc0b6\ub3c4 \uadf8\ub807\uace0, \uc800\ub9c8\ub2e4\uc758 \uc774\uc775\uc744 \ucd94\uad6c\ud558\ub294 \uad6d\u000d\u000a\uac00\ub97c \uc8fc\uccb4\ub2e8\uc704\ub85c \uc0bc\ub294 \uc138\uacc4\uc9c8\uc11c\uc758 \uc791\ub3d9\uc5d0 \uc788\uc5b4\uc11c\ub3c4 \uadf8\ub807\uace0, \ud3c9\ud654\uc758 \uad6c\ud604\uc740 \uac04\ub2e8\uce58 \uc54a\uc740 \ubb38\uc81c\ub2e4. \ud3c9\u000d\u000a\ud654\ub294 \uacb0\ucf54 \uc0ac\ub78c\ub4e4\uc758 \ubcf8\ub2a5\uacfc \uc77c\uc0c1\uc801 \uccb4\ud5d8\uc5d0 \uae30\ucd08\ud574 \ub2ec\uc131\ud560 \uc218 \uc788\ub294 \uc131\uc9c8\uc758 \uac83\uc774 \uc544\ub2c8\ub2e4. \uacf5\uaca9\ubcf8\ub2a5\ub3c4 \u000d\u000a\uac16\uace0 \uc788\ub294 \uac8c \uc0ac\ub78c\uc774\uace0, \ub3c4\ucc98\uc5d0 \uc790\uc2e0\uc744 \ud574\ud558\ub294 \ud0c0\uc778\uacfc \uc801\uc774\ub77c \uc5ec\uaca8\uc9c0\ub294 \uc874\uc7ac\uac00 \uc2e4\uc7ac\ud568\uc744 \uacbd\ud5d8\ud558\uae30 \u000d\u000a\ub54c\ubb38\uc774\ub2e4. \uadf8\ub798\uc11c \ud3c9\ud654\ub294 \uccb4\ud5d8\uc774 \uc544\ub2c8\ub77c \uc778\uc2dd\uc758 \ucc28\uc6d0\uc5d0\uc11c \ub2e4\uac00\uac00\uc57c \ud560 \ubb38\uc81c\ub2e4. \u000d\u000a<\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798>\uc740 \uadf8\ub7f0 \u2018\ub2e4\uac00\uc12c\u2019\uc744 \uc704\ud55c \ud544\ub3c5\uc11c\ub2e4. \uac00\uc815\ud3ed\ub825\uacfc \ud559\uad50\ud3ed\ub825 \ub4f1\uc758 \uc77c\uc0c1\u000d\u000a\uc801 \uc0b6\uc758 \ud604\uc2e4\uc5d0\uc11c\uc758 \ubb38\uc81c\uae4c\uc9c0 \ud3ec\uad04\ud558\uba70, \ud3c9\ud654\uac00 \uc778\uac04\uacfc \uc138\uacc4\ub97c \ubc14\ub77c\ubcf4\uace0 \uc774\ud574\ud558\ub294 \u2018\uad00\uc810\u2019\uc758 \ubb38\uc81c\uc774\u000d\u000a\uace0, \uadf8\uac83\uc774 \uc65c \uc911\uc694\ud55c\uc9c0\ub97c \uc798 \uc54c\ub824\uc8fc\uace0 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \uad00\uc810\uc758 \ubb38\uc81c\ub77c\ub294 \uac83\uc758 \uc758\ubbf8\ub294 \uc778\uac04 \uc874\uc7ac\uc640 \uc0b6\u000d\u000a\uacfc \uc138\uacc4\uc758 \uc791\ub3d9 \uc591\uc0c1\uc744 \uc5b4\ub5bb\uac8c \ubcfc \uac83\uc778\uc9c0 \u2018\uc791\uc815\ud558\uace0 \ud589\ub3d9\ud558\ub294\u2019 \uc758\uc9c0 \ubc1c\ud604\uc758 \uc2e4\ucc9c\uc774\ub77c\ub294 \uac83\uc778 \ubc14, \ud3c9\u000d\u000a\ud654\ub294 \uadf8\ub7f0 \uc2e4\ucc9c\uc744 \uc218\ud589\ud558\ub294 \uc774\ub4e4\uc758 \uc5f4\uc815\uacfc \uc9c0\uc2dd\uc5d0 \uae30\ucd08\ud574 \uc2e4\ud604\ub420 \uc218 \uc788\uc74c\uc744 \uc0dd\uc0dd\ud558\uac8c \uc54c\ub824\uc8fc\uace0 \uc788\u000d\u000a\ub294 \uac83\uc774\ub2e4. <\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798: \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c>\ub97c \ud604\uc7a5\uc9c0\uc2dd\u00d7\u000d\u000a\uc88b\uc740\uc5f0\uad6c\ub85c \uc120\uc815\ud560 \uc218\ubc16\uc5d0 \uc5c6\ub294 \uc774\uc720\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720\u000d\u000a\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798 : \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c"," & 74\u000d\u000a\uc5d0 \ud544\uc694\ud55c \uc5f0\uad6c\ub97c \ud574\uc904 \ub9c8\ub545\ud55c \uc5f0\uad6c\uc790\uac00 \uc5c6\uc5b4\uc11c \ud65c\ub3d9\uac00\ub4e4\uc774 \uad6d\uac00\uc778\uad8c\uc704\uc6d0\ud68c\uc758 \uc5f0\uad6c\uc6a9\uc5ed \u000d\u000a\uc0ac\uc5c5\uc744 \ub9e1\uc544 \uc5f0\uad6c\ubcf4\uace0\uc11c\ub97c \ub0b4\uae30\ub3c4 \ud588\ub2e4. \ud65c\ub3d9\uac00\ub4e4\uc758 \uc5f0\uad6c\ub780 \ub300\uac8c \uc774\ub7f0 \uc2dd\uc73c\ub85c \uc790\uc2e0\uc774 \ud65c\ub3d9\u000d\u000a\ud558\ub294 \ud2b9\uc815\ud55c \uc601\uc5ed\uc758 \ubc95\uacfc \uc81c\ub3c4\ub97c \ud30c\uace0\ub4e4\uc5b4 \ub300\uc548\uc744 \uc81c\uc2dc\ud558\uae30 \uc704\ud55c \uc5f0\uad6c\uac00 \ub300\ubd80\ubd84\uc774\ub2e4. \u300c\uc138\u000d\u000a\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798: \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c\u300d\ub294 \ubc95\uacfc \uc81c\ub3c4 \uac1c\uc120\uc744 \u000d\u000a\uc704\ud55c \uc5f0\uad6c\ub294 \uc544\ub2c8\uc5c8\ub2e4. \uadf8\ubcf4\ub2e4\ub294 \uc880 \ub354 \uc0ac\ud68c\uc6b4\ub3d9\uc758 \uadfc\ubcf8\uc801\uc778 \uc9c8\ubb38\uc5d0 \uac00\uae4c\uc6b4 \uace0\ubbfc\uc73c\ub85c \uc5f0\uad6c\u000d\u000a\ub97c \uc2dc\uc791\ud588\ub2e4. \uc5f0\uad6c\ub97c \uc2dc\uc791\ud55c \uc9c8\ubb38\uc740 \uc774\uac83\uc774\ub2e4. \u201c\ud3c9\ud654\uc6b4\ub3d9\uc740 \uc0ac\ub78c\ub4e4\uc744 \uc5b4\ub5bb\uac8c \uc124\ub4dd\ud558\uace0, \uc6b0\u000d\u000a\ub9ac\uc640 \ud568\uaed8 \ud560 \uc218 \uc788\uc744\uae4c?\u201d\u000d\u000a\uc0ac\ud68c\uc6b4\ub3d9\uc740 \uc6d0\ub798 \uc18c\uc218\ud30c\uc77c \uc218\ubc16\uc5d0 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc0ac\ud68c\uc5d0\uc11c \uc18c\uc678\ubc1b\ub294 \uc774\ub4e4\uc758 \uad8c\ub9ac\ub97c \u000d\u000a\uc704\ud574 \uc2f8\uc6b0\uac70\ub098 \uc0ac\ud68c\uc5d0\uc11c \uc778\uc815\ubc1b\uc9c0 \ubabb\ud55c \uc0dd\uac01, \uc2e0\ub150, \uac00\uce58 \uac19\uc740 \uac83\uc744 \uc639\ud638\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \ubb3c\u000d\u000a\ub860 \uc0ac\ud68c\uc6b4\ub3d9\uc758 \ucea0\ud398\uc778\uc774 \uc131\uacf5\ud574\uc11c \ubcf4\ud3b8\uc131\uc744 \ud68d\ub4dd\ud558\uae30\ub3c4 \ud55c\ub2e4. \uc774\ub97c\ud14c\uba74 \ud638\uc8fc\uc81c \ucca0\ud3d0\ub098 \ubb34\u000d\u000a\uc0c1\uae09\uc2dd \ub3c4\uc785 \uac19\uc740 \uac83\ub4e4\uc740 \uc774\uc81c \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c\ub294 \ub108\ubb34\ub098 \ubcf4\ud3b8\uc801\uc774\uace0 \uc0c1\uc2dd\uc801\uc778 \uc77c\uc774\ub2e4. \uc774\ub7f0 \u000d\u000a\uacbd\uc6b0 \uc0ac\ud68c\uc6b4\ub3d9\uc740 \uc131\uacf5\ud55c \ucea0\ud398\uc778\uc5d0 \uba38\ubb34\ub974\uc9c0 \uc54a\uace0 \uc0c8\ub85c\uc6b4 \ucea0\ud398\uc778\uc744 \uc2dc\uc791\ud55c\ub2e4. \ubcf4\ud3b8\uc758 \uc790\ub9ac\u000d\u000a\ub97c \ub5a0\ub098 \ub2e4\uc2dc \uc18c\uc218\ud30c\uc758 \uc790\ub9ac\uc5d0\uc11c \ub610 \ub2e4\ub978 \ubcf4\ud3b8\uc744 \ub9cc\ub4e4\uc5b4 \uac00\uae30 \uc704\ud574 \ubd84\ud22c\ud55c\ub2e4. \u000d\u000a\ud2b9\ud788\ub098 \ud55c\uad6d\uc0ac\ud68c\uc5d0\uc11c \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc8fc\uc7a5\uc740 \uc18c\uc218\ud30c \uc911\uc5d0 \uc18c\uc218\ud30c\ub2e4. \uc77c\ubcf8 \uc81c\uad6d\uc8fc\uc758\uc758 \uce68\ub7b5\u000d\u000a\uc73c\ub85c \uc2dd\ubbfc\uc9c0\ubc30\ub97c \ubc1b\uc558\uace0, \uc81c2\ucc28\uc138\uacc4\ub300\uc804 \ub54c\ub294 \uc77c\ubcf8\uc758 \uc804\uc7c1\uc5d0 \ub9ce\uc740 \uc870\uc120\uc778\ub4e4\uc774 \ubb34\ucc38\ud788 \ud729\u000d\u000a\uc4f8\ub824 \ub4e4\uc5b4\uac14\uc73c\uba70, \ud574\ubc29 \ub4a4\uc5d0\ub294 \ud55c\uad6d\uc804\uc7c1\uc744 \uacaa\uc5c8\uace0 \uc804\uc7c1\uc774 \uc911\ub2e8\ub41c \ub4a4\uc5d0\ub3c4 \ud06c\uace0 \uc791\uc740 \uad6d\uc9c0\uc801\u000d\u000a\uc778 \uad70\uc0ac \ubd84\uc7c1\uc744 \uacaa\uc5c8\ub2e4. \ubca0\ud2b8\ub0a8\uc804\uc7c1\uc5d0 \uc5f0\uc778\uc6d0 30\ub9cc \uba85\uc774 \ub118\ub294 \uad70\uc778\uc744 \ud30c\ubcd1\ud588\uc73c\ub2c8 \ubca0\ud2b8\ub0a8\u000d\u000a\uc804\uc7c1 \ub610\ud55c \ud55c\uad6d\uc0ac\ud68c\uac00 \uccb4\ud5d8\ud55c \uc804\uc7c1\uc774\ub77c\uace0 \ud560 \uc218 \uc788\ub2e4. \uadf8\ub7ec\ub2e4 \ubcf4\ub2c8 \uac15\ud55c \uad70\uc0ac\ub825\uc774 \ud3c9\ud654\ub97c \u000d\u000a\uc9c0\ud0a4\ub294 \ucd5c\uc120\uc774\uace0 \uc720\uc77c\ud55c \uc218\ub2e8\uc774\ub77c\ub294 \uc0dd\uac01\uc774 \uc9c0\ubc30\uc801\uc774\ub2e4. \uc774\ub7f0 \uc0ac\ud68c\uc5d0\uc11c \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc8fc\uc7a5\u000d\u000a\uc740 \ub54c\ub85c\ub294 \uc9c4\ubcf4\uc9c4\uc601 \ub0b4\ubd80\uc5d0\uc11c\ub3c4 \uc18c\uc218\ud30c\uc778 \uacbd\uc6b0\uac00 \ub9ce\uc558\ub2e4. \uc9c0\uae08\uc740 \uc9c4\ubcf4\uc9c4\uc601\uc5d0\uc11c\ub294 \ub2f9\uc5f0\ud558\u000d\u000a\uac8c \ubc1b\uc544\ub4e4\uc774\ub294 \uc591\uc2ec\uc801 \ubcd1\uc5ed\uac70\ubd80\uc758 \uad8c\ub9ac\uc870\ucc28\ub3c4 \ubcd1\uc5ed\uac70\ubd80\uc6b4\ub3d9\uc774 \uc2dc\uc791\ub41c \ucd08\ucc3d\uae30\uc5d0\ub294 \uc9c4\ubcf4\uc9c4\u000d\u000a\uc601\uc5d0\uc11c\uc870\ucc28 \uadf8\ub9ac \ud658\uc601\ubc1b\uc9c0 \ubabb\ud588\ub2e4. \ubcd1\uc5ed\uac70\ubd80\uc6b4\ub3d9\uc774 \ucd08\ucc3d\uae30\uc5d0 \ubc18\uc804\uc774\ub098 \ud3c9\ud654\uc8fc\uc758\uc758 \ubaa9\uc18c\ub9ac\u000d\u000a\ubcf4\ub2e4 \uc591\uc2ec\uc758 \uc790\uc720 \uce68\ud574\ub77c\ub294 \uc790\uc720\uad8c\uc744 \ubd80\uac01\ud588\ub358 \uac83\uc740 \uacb0\ucf54 \uc6b0\uc5f0\uc774 \uc544\ub2c8\ub2e4. \ud55c\uad6d \uc815\ubd80\uac00 \uc6b0\u000d\u000a\ud06c\ub77c\uc774\ub098\uc5d0 \ubb34\uae30\ub97c \uc9c0\uc6d0\ud558\ub294 \uac83\uc740 \uacb0\ucf54 \uc804\uc7c1 \uc911\ub2e8\uc744 \uc55e\ub2f9\uae30\uc9c0 \ubabb\ud558\uace0 \ud3c9\ud654\ub97c \uc704\ud55c \uc77c\uc774 \uc544\u000d\u000a\ub2c8\ub77c\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc8fc\uc7a5\uc740 \uc9c4\ubcf4\uc9c4\uc601 \ub0b4\ubd80\uc5d0\uc11c \ube44\ud310\ubc1b\uae30\ub3c4 \ud588\uace0, \uc0ac\ud68c\uc801\uc73c\ub85c\ub294 \ub354\ub354\uc6b1 \uc544\u000d\u000a\uc8fc \uc791\uc740 \ubaa9\uc18c\ub9ac\uc5d0 \uadf8\uce58\uc9c0 \uc54a\uc558\ub2e4. \u000d\u000a\uc0ac\ud68c\uc6b4\ub3d9 \ucea0\ud398\uc778\uc774 \uc18c\uc218\ud30c\uc758 \ubaa9\uc18c\ub9ac\uc640 \uad8c\ub9ac\ub97c \uc639\ud638\ud558\ub294 \uc77c\uc774\uc9c0\ub9cc, \uadf8\uac83\uc774 \uc18c\uc218\ud30c\uc758 \uc790","74 75\u000d\u000a\ub9ac\uc5d0 \uba38\ubb3c\ub7ec\uc57c \ud55c\ub2e4\ub294 \uc758\ubbf8\ub294 \uc544\ub2c8\ub2e4. \ucea0\ud398\uc778\uc774 \uc131\uacf5\ud558\ub824\uba74 \ub2e4\uc218\ub97c \uc124\ub4dd\ud574\uc57c \ud55c\ub2e4. \uadf8\ub807\u000d\u000a\uae30 \ub54c\ubb38\uc5d0 \uc0ac\ud68c\uc6b4\ub3d9 \ud65c\ub3d9\uac00\ub4e4\uc740 \ub2e4\uc218\uc758 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \uc6b0\ub9ac\uc758 \uc8fc\uc7a5\uc744 \uc54c\ub9ac\uace0 \uadf8\ub4e4\uc744 \uc124\ub4dd\ud558\u000d\u000a\ub294 \uc77c\uc744 \ud574\uc57c\ud55c\ub2e4. \uc0ac\ub78c\ub4e4\uc758 \ub9c8\uc74c\uacfc \uc758\uc9c0\ub97c \uc6c0\uc9c1\uc5ec\ub9cc \uc0ac\ud68c\uc758 \ubcc0\ud654\ub97c \ub9cc\ub4e4 \uc218 \uc788\ub2e4. \uc0ac\ud68c\uc6b4\u000d\u000a\ub3d9\uc740 \ub54c\ub85c\ub294 \uc815\ubd80\ub098 \uae30\uc5c5\uc744 \uc555\ubc15\ud558\uae30\ub3c4 \ud558\uace0, \ub54c\ub85c\ub294 \uad6d\uc81c\uae30\uad6c\uc758 \uad8c\uace0\ub97c \uc774\ub04c\uc5b4\ub0b4\uae30\ub3c4 \ud558\u000d\u000a\uace0, \uc5b8\ub860\uc5d0 \uae30\uace0\ub97c \ud558\uac70\ub098 \ud37c\ud3ec\uba3c\uc2a4\uc640 \uc9c1\uc811\ud589\ub3d9 \uac19\uc740 \uac83\uc744 \ud1b5\ud574 \ubb38\uc81c\ub97c \uc0ac\ud68c\uc5d0 \uc54c\ub9ac\uae30\ub3c4 \u000d\u000a\ud558\uc9c0\ub9cc \uacb0\uad6d \uae30\ubcf8\uc801\uc73c\ub85c\ub294 \uc0ac\ub78c\ub4e4\uc758 \uc9c0\uc9c0\uc640 \uc5f0\ub300\ub97c \ub113\ud600\uac00\ub294 \uac83\uc774 \uac00\uc7a5 \uc911\uc694\ud558\ub2e4. \uc0ac\ud68c\uc6b4\u000d\u000a\ub3d9\uc774 \ucd94\uad6c\ud558\ub294 \ubcc0\ud654\ub294 \ubc95\uacfc \uc81c\ub3c4\uc758 \uac1c\uc120\uc5d0\uc11c \ub05d\ub098\uc9c0 \uc54a\uace0, \uc0ac\ub78c\ub4e4\uc758 \uc778\uc2dd \ubcc0\ud654\uac00 \ub3d9\ubc18\ub418\uc5b4\u000d\u000a\uc57c \uac00\ub2a5\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub807\ub2e4\uba74 \ud65c\ub3d9\uac00\ub294 \ub04a\uc784\uc5c6\uc774 \uc0ac\ud68c\uc6b4\ub3d9\uc774 \uc8fc\uc7a5\ud558\ub294 \uac83\uc744 \ub9d0\ud574\uc57c \ud558\u000d\u000a\uace0, \uc0ac\ub78c\ub4e4\uc744 \uc124\ub4dd\ud558\uae30 \uc704\ud574 \ub17c\ub9ac\ub97c \ub2e4\ub4ec\uace0 \uadfc\uac70\ub97c \ubcf4\ucda9\ud558\ub294 \ub178\ub825\uc744 \uae30\uc6b8\uc5ec\uc57c \ud55c\ub2e4. \uc18c\uc218\u000d\u000a\ud30c \uc911\uc5d0\uc11c\ub3c4 \uc18c\uc218\ud30c\uc778 \ud3c9\ud654\uc6b4\ub3d9\uc740 \uc6b0\ub9ac\uc758 \uc8fc\uc7a5\uc744 \uc54c\ub9ac\uace0 \uc0ac\ub78c\ub4e4\uc744 \uc124\ub4dd\ud558\uae30 \uc704\ud574 \ud2b9\ubcc4\ud788 \u000d\u000a\ub354 \ub9ce\uc740 \ub178\ub825\uc744 \ud574\uc57c \ud55c\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \u000d\u000a\u300c\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798: \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c\u300d\ub294 \uadf8\ub7f0 \ub178\ub825\uc758 \u000d\u000a\uc77c\ud658\uc774\uc5c8\ub2e4. \ud3c9\ud654\uc6b4\ub3d9\uc744 \uc54c\ub9ac\uace0, \ud3c9\ud654\uc6b4\ub3d9\uc774 \ubb34\uc5c7\uc744 \uc8fc\uc7a5\ud558\uba70, \uc5b4\ub5bb\uac8c \ud65c\ub3d9\ud558\ub294\uc9c0\ub97c \uc54c\ub9ac\u000d\u000a\ub294 \uc77c\uc774 \uc911\uc694\ud558\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4. \uc774\ub7f0 \uc77c\uc740 \uc911\uc694\ud558\ub2e4\uace0 \ubaa8\ub450\ub4e4 \uc0dd\uac01\ud558\uc9c0\ub9cc \ub2f9\uc7a5 \ub208\uc55e\uc5d0 \ub2e5\uce5c \u000d\u000a\uc2dc\uae09\ud55c \uc77c\ub4e4\uc5d0 \ubc00\ub824 \ub4b7\uc804\uc73c\ub85c \ubc00\ub9ac\uae30 \uc27d\ub2e4. \ub204\uad70\uac00 \uc774\ub7f0 \uac83\ub4e4\uc744 \uc815\ub9ac\ud574\uc8fc\uba74 \uc88b\uaca0\uc9c0\ub9cc \ud574\u000d\u000a\uc904 \uc0ac\ub78c\uc774 \uc5c6\ub2e4\ub294 \uac83\ub3c4 \uc798 \uc54c\uace0 \uc788\uc5c8\ub2e4. \ub098\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc744 \ub354 \uc798\ud558\uace0 \uc2f6\uc5b4\uc11c, \uc6b4\ub3d9\uc758 \uc8fc\uc7a5\uc744 \u000d\u000a\uc54c\ub9ac\uace0 \uc0ac\ub78c\ub4e4\uc744 \uc124\ub4dd\ud574\uc57c \ud55c\ub2e4\ub294 \uc0ac\ud68c\uc6b4\ub3d9 \ud65c\ub3d9\uac00\uc758 \ucc45\uc784\uac10\uc73c\ub85c \uc774 \uc5f0\uad6c\ub97c \ud558\uac8c \ub418\uc5c8\ub2e4. \u000d\u000a\ud55c \uac00\uc9c0 \ub367\ubd99\uc774\uc790\uba74 \uac1c\uc778\uc801\uc778 \uc695\uad6c\ub3c4 \uc788\uc5c8\ub2e4. \ub354 \uc88b\uc740 \ud65c\ub3d9\uac00\uac00 \ub418\uae30 \uc704\ud574\uc11c \uacf5\ubd80\ub97c \ud574\u000d\u000a\uc57c \ud588\ub2e4. 2018\ub144 6\uc6d4, \ud5cc\ubc95\uc7ac\ud310\uc18c\uc5d0\uc11c \ub300\uccb4\ubcf5\ubb34\uac00 \uc5c6\ub294 \ubcd1\uc5ed\ubc95\uc774 \ud5cc\ubc95\uc5d0 \ubd88\ud569\uce58\ud55c\ub2e4\ub294 \uacb0\u000d\u000a\uc815\uc744 \ub0b4\ub824 \ubcd1\uc5ed\uac70\ubd80\uad8c\uc744 \uc778\uc815\ud558\uba74\uc11c \uc804\uc7c1\uc5c6\ub294\uc138\uc0c1\uc774 \uc624\ub7ab\ub3d9\uc548 \ud574\uc654\ub358 \ubcd1\uc5ed\uac70\ubd80 \ucea0\ud398\uc778\uc758 \u000d\u000a\uc911\uc694\ud55c \ubaa9\ud45c\uac00 \ub2ec\uc131\ub418\uc5c8\ub2e4. \uc624\ub79c \uacfc\uc81c\ub97c \ud574\uacb0\ud55c \ub4a4 \uc624\ub294 \ubfcc\ub4ef\ud568\uacfc \ud568\uaed8 \uc57d\uac04\uc758 \ud5c8\ubb34\ud568\ub3c4 \ud568\u000d\u000a\uaed8 \ucc3e\uc544\uc654\ub2e4. \ubb3c\ub860 \uc544\uc9c1 \ub0a8\uc740 \uacfc\uc81c\ub4e4\uc774 \ub9ce\uc774 \uc788\uc5c8\uc9c0\ub9cc \ubb34\uac8c\uac10\uc5d0\uc11c \uacfc\uac70\uc640 \ube44\ud560 \ubc14\ub294 \uc544\ub2c8\uc5c8\u000d\u000a\ub2e4. \ud604\uc7ac\uc5d0 \uc548\uc8fc\ud558\uc9c0 \uc54a\uae30 \uc704\ud574 \uacf5\ubd80\ub97c \ud574\uc57c\uaca0\ub2e4\uace0 \uc0dd\uac01\ud588\uace0, \uacf5\ubd80\ub97c \uc704\ud574\uc11c \uae00\uc744 \uc368\uc57c\uaca0\u000d\u000a\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4. \uae00\uc744 \uc368\ubcf4\uba74 \ub0b4\uac8c \ubd80\uc871\ud55c \uac8c \ubb34\uc5c7\uc778\uc9c0, \ub0b4 \uc8fc\uc7a5\uc774 \uc5bc\ub9c8\ub098 \uadfc\uac70 \uc5c6\ub294 \uc5b5\uc9c0\uc778\u000d\u000a\uc9c0, \uc544\uc774\ub514\uc5b4\uc5d0 \ubd88\uacfc\ud55c \uac83\uc774 \ubb34\uc5c7\uc774\uace0 \ub0b4\uac00 \uc628\uc804\ud558\uac8c \ub0b4 \uc5b8\uc5b4\ub85c \uc18c\ud654\ud560 \uc218 \uc788\ub294 \uc8fc\uc7a5\uc774 \ubb34\u000d\u000a\uc5c7\uc778\uc9c0 \uc54c \uc218 \uc788\uac8c \ub41c\ub2e4. \uadf8\ub798\uc11c \uae00\uc744 \uc4f0\ub294 \uacfc\uc815\uc740 \uadf8 \ubd80\uc871\ud55c \ub17c\ub9ac\uc640 \ub17c\uac70\ub97c \uacf5\ubd80\ud558\uba70 \ucc44\uc6cc\u000d\u000a\uac00\ub294 \uacfc\uc815\uc774\ub77c\ub294 \uac83\uc744 \uc54c\uace0 \uc788\uc5c8\uac70, \uadf8 \uacfc\uc815\uc744 \ud1b5\ud574 \ub098\ub294 \uc131\uc7a5\ud558\ub294 \ud65c\ub3d9\uac00\uac00 \ub418\uace0 \uc2f6\uc5c8\ub2e4. \u000d\u000a\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798 : \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c"," & 76\u000d\u000a2. \uc5f0\uad6c\uc758 \ud2b9\uc9d5\uacfc \uc5f0\uad6c\ubcf4\uace0\uc11c\uc758 \uc8fc\uc694 \ub0b4\uc6a9\u000d\u000a\ud65c\ub3d9\uac00\ub85c\uc11c \ub0b4 \uc5f0\uad6c\uc758 \uc7a5\uc810\uacfc \ub2e8\uc810\uc774 \ubb34\uc5c7\uc77c\uc9c0, \ud2b9\uc9d5\uc774 \ubb34\uc5c7\uc77c\uc9c0 \uc0dd\uac01\ud588\ub2e4. \ub300\ud559\uc6d0\uc5d0\uc11c \u000d\u000a\ub17c\ubb38 \uc4f0\ub294 \ud6c8\ub828\uc744 \ubc1b\uc740 \uc801\uc774 \uc5c6\ub294 \ub098\ub294 \uc560\ucd08\uc5d0 \uc5b4\ub5a4 \uac00\uc124\uc744 \ub17c\uc99d\ud558\ub294 \ub958\uc758 \uc5f0\uad6c\ubcf4\uace0\uc11c\ub97c \u000d\u000a\uc4f8 \ub2a5\ub825\ub3c4 \uc548 \ub418\uc5c8\uace0 \uadf8\ub7f4 \ub9c8\uc74c\ub3c4 \uc5c6\uc5c8\ub2e4. \ub098\ub294 \uc8fc\uc7a5\ud558\uace0 \uc124\ub4dd\ud558\ub294 \uae00\uc744 \uc4f0\uace0 \uc2f6\uc5c8\ub2e4. \uadf8\u000d\u000a\ub7f0 \uba74\uc5d0\uc11c\ub294 \ub0b4\uac00 \ud65c\ub3d9\ud574 \uc628 \uacbd\ud5d8\uc774, \ud65c\ub3d9\uac00\ub77c\ub294 \uc815\uccb4\uc131\uc774 \uc624\ud788\ub824 \uac15\uc810\uc774 \ub420 \uc218 \uc788\uc744 \uac70\ub77c\u000d\u000a\uace0 \uc0dd\uac01\ud588\ub2e4. \ud3c9\ud654\ud559\uc744 \uc774\ub860\uc801\uc73c\ub85c \uc815\ub9bd\ud558\ub824\ub294 \uacf5\ubd80\ubcf4\ub2e4\ub294, \ud3c9\ud654\uc6b4\ub3d9\uc744 \uc18c\uac1c\ud558\uace0 \uc124\uba85\ud558\u000d\u000a\ub294 \uacf5\ubd80\uc640 \uae00\uc4f0\uae30\ub97c \uc9c0\ud5a5\ud588\ub2e4. \u000d\u000a\ub098\uc640 \ud3c9\ud654\ud65c\ub3d9\uac00 \ub3d9\ub8cc\ub4e4\uc774 \uacaa\uc740 \uacbd\ud5d8\ub4e4, \ucea0\ud398\uc778\uc744 \ud574\uc624\uba74\uc11c \ub0b4\uc678\ubd80\uc5d0\uc11c \ubc1b\uc740 \uc9c8\ubb38\ub4e4, \ud3c9\u000d\u000a\ud654\ud65c\ub3d9\uac00\ub4e4\uc744 \uc5b4\ub835\uac8c \ud55c \uace0\ubbfc \uc9c0\uc810\ub4e4\uc740 \uc804\ubb38\uc801\uc778 \uc5f0\uad6c\uc790\ub4e4\uc774\ub098 \uc791\uac00\ub4e4\ubcf4\ub2e4 \ud65c\ub3d9\uac00\uc778 \ub0b4\u000d\u000a\uac00 \ub354 \uc0dd\ub3d9\uac10 \uc788\uac8c \uc774\uc57c\uae30\ud560 \uc218 \uc788\uc744 \uac70 \uac19\uc558\ub2e4. \uc5f0\uad6c\ubcf4\uace0\uc11c\uc758 \uc804\ubc18\ubd80\ub294 \uc774\ub7f0 \uc0dd\uac01\ub4e4\ub85c \uad6c\u000d\u000a\uc131\ud588\ub2e4. \ud3c9\ud654\uc6b4\ub3d9\uc758 \ubaa9\ud45c, \ubc94\uc8fc, \ucca0\ud559, \ubc29\ubc95 \ub4e4\uc744 \ud3c9\ud654\ud65c\ub3d9\uac00\ub4e4\uc774 \ubc1b\uc740 \uc9c8\ubb38, \ud3c9\ud654\ud65c\ub3d9\uac00\ub4e4\u000d\u000a\uc758 \uace0\ubbfc\uacfc \uc5f0\uad00\uc9c0\uc5b4 \uad6c\uc131\ud588\ub2e4.\u000d\u000a\uccab \ubc88\uc9f8 \ucc55\ud130 \u2018\uac00\uc815\ud3ed\ub825\uc774\ub098 \ud559\uad50\ud3ed\ub825\uc740 \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc8fc\uc81c\uac00 \ub420 \uc218 \uc788\ub098\uc694?\u2019\uc5d0\uc11c\ub294 \ud3c9\ud654\u000d\u000a\uc6b4\ub3d9\uc740 \ubb34\uc5c7\uc744 \ubaa9\ud45c\ub85c \ud558\ub294\uc9c0 \uc124\uba85\ud588\ub2e4. \ud3c9\ud654\uc758 \ubc18\ub300\ub9d0\uc774 \ud3ed\ub825\uc774\ub77c\uace0 \uc0dd\uac01\ud558\ub294 \uc0ac\ub78c\uc774 \ub9ce\u000d\u000a\uace0, \uc774\ub294 \ud2c0\ub9b0 \uc778\uc2dd\uc774 \uc544\ub2c8\ub2e4. \ud3c9\ud654\uc6b4\ub3d9\uc758 \ubaa9\ud45c\uac00 \ud3ed\ub825\uc744 \uc57d\ud654\uc2dc\ud0a4\ub294 \uac83\uc774\ub77c\ub294 \uc774\uc57c\uae30\ub3c4 \u000d\u000a\ud2c0\ub9b0 \ub9d0\uc740 \uc544\ub2c8\ub2e4. \ud558\uc9c0\ub9cc \uc774\ub807\uac8c\ub9cc \ub9d0\ud55c\ub2e4\uba74 \ud3c9\ud654\uc6b4\ub3d9\uc740 \uace0\uc720\ud55c \ud2b9\uc131\uc774 \uc5c6\ub294 \uc6b4\ub3d9\uc774 \ub41c\u000d\u000a\ub2e4. \ud30c\uc5c5 \ub178\ub3d9\uc790\uc5d0\uac8c \uac00\ud574\uc9c0\ub294 \uacf5\uad8c\ub825\uc758 \ubb3c\ub9ac\uc801 \ud3ed\ub825\uacfc, \uc0ac\ud68c\uacbd\uc81c\uc801\uc778 \uad6c\uc870\uc801 \ud3ed\ub825\uc740 \ubd84\uba85 \u000d\u000a\uc0ac\ud68c \uc815\uc758\uc5d0 \uc5b4\uae0b\ub098\uace0 \uc774\ub7f0 \ud3ed\ub825\uc5d0 \uc6b0\ub9ac \ubaa8\ub450 \uc800\ud56d\ud574\uc57c \ud558\uc9c0\ub9cc \uadf8\uac83\uc740 \uc6b0\ub9ac \uc0ac\ud68c\uc758 \uacfc\uc81c\uc77c \u000d\u000a\uc21c \uc788\uc5b4\ub3c4 \ud3c9\ud654\uc6b4\ub3d9\uc758 \uace0\uc720\ud55c \uacfc\uc81c\ub294 \uc544\ub2c8\ub2e4. \uadf8\ub7f0 \uce21\uba74\uc5d0\uc11c \ud3c9\ud654\uc6b4\ub3d9\uc740 \uc804\uc7c1\uacfc \uc5f0\uad00\ub41c \ud3ed\u000d\u000a\ub825\uc5d0 \ub9de\uc11c\ub294 \uc0ac\ud68c\uc6b4\ub3d9\uc774\uace0, \uc0ac\ud68c\uc758 \ud3ed\ub825\uc774 \uc804\uc7c1 \ud639\uc740 \uad70\uc0ac\uc8fc\uc758\uc640 \uc5b4\ub5a4 \uc5f0\uad00\uc744 \ub9fa\ub294\uc9c0\ub97c \uc0b4\u000d\u000a\ud53c\ub294 \uc0ac\ud68c\uc6b4\ub3d9\uc774\ub77c\ub294 \uc810\uc744 \uc774\uc57c\uae30\ud55c\ub2e4. \u000d\u000a\ub450 \ubc88\uc9f8 \ucc55\ud130 \u2018\uc77c\ubcf8\uad70 \u2019\uc704\uc548\ubd80\u2018 \ud560\uba38\ub2c8\ub4e4\uc758 \uc6b4\ub3d9\uc740 \ud3c9\ud654\uc6b4\ub3d9\uc774 \uc544\ub2cc\uac00\uc694?\u2019\uc5d0\uc11c\ub294 \ud3c9\ud654\uc6b4\u000d\u000a\ub3d9\uc758 \ubc94\uc8fc\uc5d0 \ub300\ud55c \uc774\uc57c\uae30\ub97c \ud55c\ub2e4. \uc77c\ubcf8\uad70 \u2018\uc704\uc548\ubd80\u2019 \ud560\uba38\ub2c8\ub4e4\uc758 \ud65c\ub3d9\uc740 \uc5ec\uc131\uc5d0 \ub300\ud55c \uc804\uc2dc \uc131\u000d\u000a\ud3ed\ub825\uc744 \uace0\ubc1c\ud558\ub294 \uc5ec\uc131\uc6b4\ub3d9\uc774\uace0, \uc778\uad8c\uce68\ud574\ub97c \uace0\ubc1c\ud588\uc73c\ub2c8 \uc778\uad8c\uc6b4\ub3d9\uc774\uae30\ub3c4 \ud558\uace0, \uc81c\uad6d\uc8fc\uc758 \u000d\u000a\uc804\uc7c1\uc5d0 \uc800\ud56d\ud558\ub294 \ubc18\uc804\ud3c9\ud654\uc6b4\ub3d9\uc774\uae30\ub3c4 \ud558\ub2e4. \uc774\ucc98\ub7fc \ud558\ub098\uc758 \ucea0\ud398\uc778\uc740 \ub2e4\uc591\ud55c \uce21\uba74\uc744 \uac00\uc9c0","76 77\u000d\u000a\uace0 \uc788\ub2e4\ub294 \uc810\uc744 \uc0b4\ud53c\uba74\uc11c \ud3c9\ud654\uc6b4\ub3d9\uc774 \uc8fc\ub85c \uc5b4\ub5a4 \uc774\uc288\ub4e4\uc744 \ub2e4\ub8e8\ub294\uc9c0 \uc774\uc57c\uae30\ud55c\ub2e4. \ud3c9\ud654\uc6b4\ub3d9\u000d\u000a\uc758 \ubc94\uc8fc\ub97c \uad6c\ubd84\ud558\ub294 \uac83\uc740 \ubb34\uc5c7\uc774 \ud3c9\ud654\uc6b4\ub3d9\uc778\uc9c0 \uc544\ub2cc\uc9c0 \uac00\ub9ac\uae30 \uc704\ud55c \uac83\uc774 \uc544\ub2c8\ub77c, \uc5b4\ub5a4 \uc0ac\u000d\u000a\ud68c \ubb38\uc81c\ub97c \ud574\uacb0\ud558\ub294 \ub370 \uc788\uc5b4\uc11c \ud3c9\ud654\uc6b4\ub3d9\uc774 \uc798\ud560 \uc218 \uc788\ub294 \uac83\ub4e4\uc744 \ucc3e\uc544\ub0b4\uace0 \uadf8\uac83\uc744 \uc798 \ud574\ub0b4\u000d\u000a\uae30 \uc704\ud55c \uad6c\ubd84\uc774\ub77c\ub294 \uc810\ub3c4 \uac15\uc870\ud55c\ub2e4. \u000d\u000a\uc138 \ubc88\uc9f8 \ucc55\ud130 \u2018\ud3c9\ud654\uc6b4\ub3d9\uc740 \ud56d\uc77c\ubb34\uc7a5\ub3c5\ub9bd\uc6b4\ub3d9\uc744 \ube44\ud310\ud558\ub098\uc694?\u2019\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc758 \ucca0\ud559\uc744 \ub2e4\ub8e8\u000d\u000a\uace0 \uc788\ub2e4. \ud3c9\ud654\ud65c\ub3d9\uac00\ub4e4, \ud2b9\ud788 \ubcd1\uc5ed\uac70\ubd80\uc790\ub4e4\uc740 \uc624\ub7ab\ub3d9\uc548 \uc800\ud56d\ud3ed\ub825\uc5d0 \ub300\ud55c \uc9c8\ubb38\uc744 \ubc1b\uc544\uc654\ub2e4. \u000d\u000a\uc774\ub97c \uc124\uba85\ud558\uae30 \uc704\ud574 \ud3ed\ub825\uc774\ub780 \ubb34\uc5c7\uc778\uc9c0\ub97c \ud3ed\ub825\uc758 \ub9e5\ub77d, \ud3ed\ub825\uc758 \ub2e4\uce35\uc801\uc778 \uad6c\uc870\ub97c \ud1b5\ud574 \uc0b4\ud3b4\u000d\u000a\ubcf8\ub2e4. \uadf8\ub9ac\uace0 \ud3c9\ud654\uc6b4\ub3d9\uc774 \ube44\ud3ed\ub825\uc801\uc774\uc5b4\uc57c \ud558\ub294 \uc774\uc720\ub97c \uc124\uba85\ud55c\ub2e4. \uc0ac\ud68c\uc6b4\ub3d9\uc740 \uadf8 \uc6b4\ub3d9\uc774 \uc774\u000d\u000a\ub8e8\uace0\uc790 \ud558\ub294 \uc0ac\ud68c\uc758 \ubaa8\uc2b5\uacfc \ubc29\ubc95\uc774 \uac19\uc544\uc57c \ud55c\ub2e4. \ud3c9\ud654\uc801\uc778 \uc138\uc0c1\uc744 \ud3ed\ub825\uc801\uc778 \ubc29\uc2dd\uc73c\ub85c \ub9cc\ub4e4 \u000d\u000a\uc218 \uc5c6\ub2e4. \ub610\ud55c \ube44\ud3ed\ub825\uc774 \uc0ac\ud68c\uc6b4\ub3d9 \ucea0\ud398\uc778\uc758 \uc131\uacf5 \uac00\ub2a5\uc131\uc744 \ub192\uc778\ub2e4\ub294 \uc544\uc8fc \ud604\uc2e4\uc801\uc774\uace0 \uc2e4\uc6a9\u000d\u000a\uc801\uc778 \uc774\uc720\ub3c4 \ud3c9\ud654\uc6b4\ub3d9\uc774 \ube44\ud3ed\ub825\uc744 \uc6d0\uce59\uc73c\ub85c \uc0bc\uc544\uc57c \ud558\ub294 \uc911\uc694\ud55c \uc774\uc720\ub2e4.\u000d\u000a\ub124 \ubc88\uc9f8 \ucc55\ud130 \u2018\ud30c\ubcd1\uc744 \ub9c9\uac70\ub098 \ub300\uccb4\ubcf5\ubb34\uc81c\ub3c4\ub97c \ub9cc\ub4e4\ub824\uba74 \uad6d\ud68c\uc758\uc6d0\uc774 \ub418\ub294 \uac8c \ub354 \ube60\ub974\uc9c0 \u000d\u000a\uc54a\ub098\uc694?\u2019\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc758 \ubc29\ubc95\uc5d0 \ub300\ud55c \uc774\uc57c\uae30\ub2e4. \uc774\ub294 \ud3c9\ud654\uc6b4\ub3d9\ubfd0 \uc544\ub2c8\ub77c \uc815\ub2f9 \uc815\uce58\uc640 \ube44\uad50\u000d\u000a\ud574 \uc0ac\ud68c\uc6b4\ub3d9 \uc804\ubc18\uc758 \ud2b9\uc9d5\uacfc\ub3c4 \uc5f0\uacb0\ub418\ub294 \uc774\uc57c\uae30\ub2e4. \uae34 \ud638\ud761\uc73c\ub85c \uc0ac\ud68c\uc758 \uadfc\ubcf8\uc801\uc778 \ud328\ub7ec\ub2e4\uc784 \u000d\u000a\uc804\ud658\uc744 \ucd94\uad6c\ud558\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc774 \uc0ac\uc6a9\ud558\ub294 \ubc29\ubc95\uc758 \ud2b9\uc9d5\uc744 \uc0b4\ud3b4\ubcf8\ub2e4. \uc0ac\ud68c\uc6b4\ub3d9\uc774 \ub9de\uc11c\ub294 \uad8c\ub825\u000d\u000a\uc774\ub77c\ub294 \uac83\uc774 \uc2e4\uc740 \ub2e4\uc218\uc758 \ub3d9\uc758 \ub610\ub294 \ucd5c\uc18c\ud55c \ub2e4\uc218\uc758 \uce68\ubb35\uacfc \ubb35\uc778\uc744 \ubc14\ud0d5\uc73c\ub85c \uc791\ub3d9\ud55c\ub2e4\ub294 \uac83\u000d\u000a\uc744 \uc774\uc57c\uae30\ud558\uace0, \uc0ac\ud68c\uc6b4\ub3d9\uc774 \uc77c\ubc18\uc801\uc73c\ub85c \u2018\uc804\ud5a5-\uad8c\ub825\uc790\ub97c \uc124\ub4dd\ud574 \ub9c8\uc74c\uc744 \ubc14\uafb8\uac8c \ud558\ub294 \uac83\u2019, \u2018\u000d\u000a\ud611\uc0c1-\uc694\uad6c\uc0ac\ud56d\uc5d0\uc11c \uad8c\ub825\uc790\uc640 \uc11c\ub85c\uc758 \uc785\uc7a5 \ucc28\uc774\ub97c \uc881\ud600\uac00\ub294 \uac83\u2019, \u2018\uac15\uc81c-\uad8c\ub825\uc790\uac00 \uc0ac\ud68c\uc6b4\ub3d9\u000d\u000a\uc758 \ub73b\uc744 \ub530\ub97c \uc218\ubc16\uc5d0 \uc5c6\uac8c \ub9cc\ub4dc\ub294 \uac83\u2019\uacfc \uac19\uc740 \ubc29\uc2dd\uc73c\ub85c \uc0ac\ud68c \ubcc0\ud654\ub97c \ub9cc\ub4e0\ub2e4\ub294 \uac83\uc744 \uc124\uba85\ud55c\u000d\u000a\ub2e4. \uadf8\ub9ac\uace0 \uc65c \uc9c1\uc811\ud589\ub3d9\uc774 \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc8fc\ub41c \ubc29\ubc95\uc778\uc9c0, \uc9c1\uc811\ud589\ub3d9\uc740 \ubb34\uc5c7\uc778\uc9c0\ub97c \uc124\uba85\ud55c\ub2e4.\u000d\u000a\ub2e4\uc12f \ubc88\uc9f8 \ucc55\ud130 \u2018\ud3c9\ud654\uc6b4\ub3d9\uc740 \uc65c \uad70\uc778 \uc778\uad8c\uc740 \ub2e4\ub8e8\uc9c0 \uc54a\ub098\uc694?\u2019\ub294 \uad50\ucc28\uc131\uc5d0 \ub300\ud574 \uc774\uc57c\uae30\ud55c\u000d\u000a\ub2e4. \uc0ac\ud68c\uc758 \ub9ce\uc740 \ubb38\uc81c\ub294 \ubcf5\uc7a1\ud55c \uc0ac\ud68c \uad6c\uc870\uc801 \ubd80\uc815\uc758\uc640 \ubaa8\uc21c\uc774 \ud55c\ub370 \uc5bd\ud600\uc788\uae30 \ub54c\ubb38\uc5d0 \uc0ac\ud68c\u000d\u000a\uc6b4\ub3d9\uc740 \ub2e4\uc591\ud55c \uc601\uc5ed\uc758 \uc6b4\ub3d9\uc774 \ud568\uaed8 \uc0ac\ud68c\ubb38\uc81c\ub97c \ud574\uacb0\ud558\uae30 \uc704\ud574 \uc5f0\ub300\ud574\uc57c \ud55c\ub2e4. \ud3c9\ud654\uc6b4\ub3d9\ub3c4 \u000d\u000a\ub9c8\ucc2c\uac00\uc9c0\ub2e4. \ud3c9\ud654\uc6b4\ub3d9\uc774 \uadf8 \ud2b9\uc131\uc0c1 \ud568\uaed8 \ud574\uc57c\ud558\uace0 \uac00\uc7a5 \ub9ce\uc774 \uad50\ucc28\ud558\ub294 \uc0ac\ud68c\uc6b4\ub3d9\uc778 \uc778\uad8c\uc6b4\u000d\u000a\ub3d9, \ud398\ubbf8\ub2c8\uc998 \uc6b4\ub3d9, \ud658\uacbd\uc6b4\ub3d9\uacfc \uac01\uac01 \uc5b4\ub5bb\uac8c \uad50\ucc28\ud558\ub294\uc9c0, \uadf8\ub9ac\uace0 \uc774\ub807\uac8c \uad50\ucc28\ud560 \ub54c \uc5b4\ub5a4 \uc2dc\u000d\u000a\ub108\uc9c0\ub97c \ub0bc \uc218 \uc788\ub294\uc9c0\ub97c \uc124\uba85\ud55c\ub2e4.\u000d\u000a\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798 : \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c"," & 78\u000d\u000a\uc5f0\uad6c\ubcf4\uace0\uc11c\uc758 \ud6c4\ubc18\ubd80\ub294 \uc774 \ubcf4\uace0\uc11c\ub97c \uc77d\ub294 \uc2dc\ubbfc\ub4e4\uc5d0\uac8c \ud55c\uad6d\uc758 \ud3c9\ud654\uc6b4\ub3d9 \ucea0\ud398\uc778\uacfc \ub2e8\uccb4\ub97c \u000d\u000a\uc815\ub9ac\ud574\uc11c \uc18c\uac1c\ud558\ub294 \ub0b4\uc6a9\uc744 \ub2f4\uace0 \uc788\ub2e4. \ud3c9\ud654\uc6b4\ub3d9\uc758 \ubc94\uc8fc\ub294 \uc5f0\uad6c\ubcf4\uace0\uc11c 1\ubd80\uc5d0\uc11c \uc5b8\uae09\ud588\ub4ef\uc774 \u000d\u000a\uc5b4\ub5bb\uac8c \uaddc\uc815\ud558\ub290\ub0d0\uc5d0 \ub530\ub77c \ud06c\uac8c \ub2ec\ub77c\uc9c4\ub2e4. 2\ubd80\uc5d0\uc11c \uc18c\uac1c\ud558\ub294 \ucea0\ud398\uc778\uacfc \ub2e8\uccb4\ub294 \ubaa8\ub4e0 \ud3c9\ud654\u000d\u000a\uc6b4\ub3d9\uc744 \ucd1d\ub9dd\ub77c\ud558\uae30\ubcf4\ub2e4\ub294 \uc880 \ub354 \uc9c1\uc811\uc801\uc73c\ub85c \uc804\uc7c1\uc5d0 \uc800\ud56d\ud558\ub294 \ucea0\ud398\uc778 \uc911\uc5d0\uc11c \ub0b4\uac00 \uc9c1\uac04\uc811\u000d\u000a\uc801\uc73c\ub85c \uacbd\ud5d8\ud574\uc11c \uc2dc\ubbfc\ub4e4\uc5d0\uac8c \uc798 \uc18c\uac1c\ud560 \uc218 \uc788\ub294 \uac83\ub4e4\uc744 \uce74\ud14c\uace0\ub9ac\ubcc4\ub85c \ubb36\uc5c8\ub2e4. \uac1c\uc778\uc758 \uacbd\ud5d8\u000d\u000a\uc5d0 \uae30\ubc18\ud55c \ubd84\ub958\ub77c \uc800\uc790\uc758 \ud55c\uacc4\uac00 \uace0\uc2a4\ub780\ud788 \ub4dc\ub7ec\ub098\uae30\ub3c4 \ud558\uc9c0\ub9cc, \ud55c\ud3b8\uc73c\ub85c\ub294 \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c \u000d\u000a\ubc18\uad70\uc0ac\uc8fc\uc758 \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc9c0\ud615\uc744 \uc0b4\ud3b4\ubcfc \uc218 \uc788\ub294 \ubd84\ub958\ub77c\uace0\ub3c4 \uc0dd\uac01\ud55c\ub2e4. \u000d\u000a\uccab \ubc88\uc9f8 \ucc55\ud130 \u2018\ud3c9\ud654\ub86d\uac8c \uc0b4\uace0 \uc2f6\uc2b5\ub2c8\ub2e4: \ub9e4\ud5a5\ub9ac, \ub300\ucd94\ub9ac, \uac15\uc815\ub9c8\uc744, \uc18c\uc131\ub9ac\u2019\ub294 \uad70\uc0ac\uae30\uc9c0\u000d\u000a\uc5d0 \uc800\ud56d\ud558\ub294 \uc9c0\uc5ed\ubbfc\ub4e4\uc758 \ud3c9\ud654\uc6b4\ub3d9\uc744 \uc18c\uac1c\ud588\ub2e4. \uc804\uba74\uc804\uacfc \uad6d\uc9c0\uc801\uc774\uace0 \uc9c0\uc18d\uc801\uc778 \uad70\uc0ac\uac08\ub4f1\uc740 \u000d\u000a\ud55c\uad6d\uc744 \ubcd1\uc601\uad6d\uac00\ub85c \ub9cc\ub4e4\uc5c8\ub2e4. \uc5b8\uc81c\ub4e0 \uc5b4\ub514\uc11c\ub4e0 \uc804\uc7c1\ud560 \uc900\ube44\uac00 \ub418\uc5b4 \uc788\ub294 \ubcd1\uc601\uc0ac\ud68c\uc778 \ud55c\uad6d\u000d\u000a\uc758 \uc601\ud1a0 \uacf3\uacf3\uc5d0\ub294 \uad70\uc0ac\uae30\uc9c0\uac00 \uc788\uace0, \uad70\uc0ac\uc801 \ud544\uc694\uc5d0 \ub530\ub77c \uc0c8\ub85c\uc6b4 \uae30\uc9c0\uac00 \uac74\uc124\ub418\uac70\ub098 \uc6d0\ub798\uc758 \u000d\u000a\uae30\uc9c0\uac00 \ud655\uc7a5\ub418\uace4 \ud55c\ub2e4. \uad70\uc0ac\uae30\uc9c0\uac00 \uc0c8\ub85c \uc0dd\uae30\uac70\ub098 \ub113\uc5b4\uc9c8 \ub54c\ub9c8\ub2e4 \uadf8 \uc9c0\uc5ed\uc5d0 \uac70\uc8fc\ud558\ub358 \uc8fc\ubbfc\u000d\u000a\ub4e4\uacfc \uadf9\uc2ec\ud55c \ub9c8\ucc30\uc744 \uacaa\uae30\ub3c4 \ud558\uace0, \uad70\uc0ac\uae30\uc9c0\uac00 \uc720\uc9c0\ub418\ub294 \ub3d9\uc548\uc5d0\ub3c4 \ud658\uacbd\uc624\uc5fc\uc774\ub098 \uad70\uc778\ub4e4\uc774 \u000d\u000a\uc77c\uc73c\ud0a4\ub294 \uac01\uc885 \ubc94\uc8c4\ub85c \uc778\uadfc \uc9c0\uc5ed \uc8fc\ubbfc\ub4e4\uacfc \ub9c8\ucc30\uc744 \uacaa\ub294\ub2e4. \uc774\ub7f0 \ub9c8\ucc30\uc744 \uacaa\uc73c\uba74\uc11c \ub9ce\uc740 \uc8fc\u000d\u000a\ubbfc\ub4e4\uc774 \uad70\uc0ac\uc8fc\uc758\uc758 \ubb38\uc81c\uc810\uc744 \uae68\ub2eb\uace0 \uad70\uc0ac\uae30\uc9c0\uc5d0 \uc800\ud56d\ud558\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc744 \ud3bc\uce5c\ub2e4. \ubbf8\uad70 \uc0ac\uaca9\u000d\u000a\uc7a5\uc5d0\uc11c \uc18c\uc74c\uacfc \uc624\ubc1c \uc0ac\uace0 \ub4f1 \uc5ec\ub7ec \ubb38\uc81c\ub97c \uacaa\ub358 \uc18c\uc131\ub9ac, \ubbf8\uad70\uae30\uc9c0 \ud655\uc7a5\uc73c\ub85c \ub9c8\uc744\uc744 \uc1a1\ub450\ub9ac\u000d\u000a\uc9f8 \ube7c\uc557\uae34 \ud3c9\ud0dd \ub300\ucd94\ub9ac, \ud574\uad70\uae30\uc9c0 \uac74\uc124\uc5d0 \ub9de\uc120 \uc81c\uc8fc\ub3c4 \uac15\uc815\ub9c8\uc744, \uc0ac\ub4dc \uae30\uc9c0\uac00 \ubc30\uce58\ub41c \uc131\uc8fc \u000d\u000a\uc18c\uc2f1\ub9ac \ub9c8\uc744 \uc8fc\ubbfc\ub4e4\uc758 \ud3c9\ud654\uc6b4\ub3d9\uc744 \uc18c\uac1c\ud588\ub2e4. \uc774\ub4e4\uc740 \ubcf8\ub798 \ud3c9\ud654\ud65c\ub3d9\uac00\ub294 \uc544\ub2c8\uc5c8\uc9c0\ub9cc \uc0b6\uc758 \u000d\u000a\ud130\uc804\uc744 \uc9c0\ud0a4\uae30 \uc704\ud574 \ud3c9\ud654\ud65c\ub3d9\uac00\uac00 \ub418\uc5c8\uace0, \uad70\uc0ac\uc8fc\uc758\uac00 \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c \ub204\uad6c\ub97c \ucc29\ucde8\ud558\uace0 \ub204\u000d\u000a\uad6c\uc758 \uc774\uc775\uc5d0 \ubcf5\ubb34\ud558\ub294\uc9c0\ub97c \uc99d\uba85\ud558\ub294 \ud65c\ub3d9\uc744 \uc774\uc5b4\uac00\uace0 \uc788\ub2e4. \u000d\u000a\ub450 \ubc88\uc9f8 \ucc55\ud130 \u2018\uc804\uc7c1\uc5d0 \ub3d9\ucc38\ud558\uc9c0 \uc54a\uaca0\uc2b5\ub2c8\ub2e4: \ubcd1\uc5ed\uac70\ubd80 \ucea0\ud398\uc778\u2019\uc5d0 \ub300\ud574 \uc774\uc57c\uae30\ud55c\ub2e4. \uc885\uad50\u000d\u000a\uc801 \uc591\uc2ec\uc758 \uc790\uc720\uc5d0\uc11c \ucd9c\ubc1c\ud55c \ubcd1\uc5ed\uac70\ubd80\uac00 \uc804\uc7c1\uc5d0 \uc800\ud56d\ud558\ub294 \uc2dc\ubbfc\ubd88\ubcf5\uc885 \ud3c9\ud654\uc6b4\ub3d9\uc73c\ub85c \ubcc0\ubaa8\ud558\u000d\u000a\ub294 \uc5ed\uc0ac\ub97c \uc18c\uac1c\ud558\ub294 \ud55c \ud3b8, \ubcd1\uc5ed\uac70\ubd80\ub97c \uc785\uc601 \uc601\uc7a5\uc744 \uac70\ubd80\ud558\ub294 \ud589\uc704\ub97c \ub118\uc5b4\uc11c \uc804\uc7c1\uc5d0 \ub3d9\ucc38\u000d\u000a\ud558\uac70\ub098 \uc804\uc7c1\uc5d0 \ub3d9\uc6d0\ub418\ub294 \uac83\uc744 \ub2e4\uc591\ud55c \ud615\ud0dc\ub85c \uac70\ubd80\ud558\ub294 \ubaa8\ub4e0 \ud589\uc704\ub85c \uc758\ubbf8\ub97c \ud655\uc7a5\ud55c\ub2e4. \uad70\uc218\u000d\u000a\ubb3c\uc790\ub97c \uc0dd\uc0b0\ud558\uac70\ub098 \uc6b4\uc1a1\ud558\uae30\ub97c \uac70\ubd80\ud558\ub294 \ub178\ub3d9\uc790, \uc804\uc7c1\uc744 \ucc2c\uc591\ud558\ub294 \uc218\uc5c5 \ucee4\ub9ac\ud058\ub7fc\uc744 \uac70\ubd80\u000d\u000a\ud558\ub294 \uad50\uc0ac\uc640 \ud559\uc0dd\ub4e4\ub3c4 \ub113\uc740 \uc758\ubbf8\uc758 \ubcd1\uc5ed\uac70\ubd80\ub2e4. \ubcd1\uc5ed\uac70\ubd80\uac00 \uc774\ub807\uac8c \ud655\uc7a5\ub420 \ub54c \uc6b0\ub9ac\ub294 \uc804\uc7c1\u000d\u000a\uc5d0 \uc800\ud56d\ud558\ub294 \ub2e4\uc591\ud558\uace0 \ucc3d\uc758\uc801\uc778 \ubd88\ubcf5\uc885 \ud589\ub3d9\uc744 \ud3bc\uccd0\uac08 \uc218 \uc788\ub2e4. ","78 79\u000d\u000a\uc138 \ubc88\uc9f8 \ucc55\ud130 \u2018\ubb34\uae30\ub4e4\uc544 \uc798 \uc788\uac70\ub77c: \ub300\uc778 \uc9c0\ub8b0 \ucea0\ud398\uc778, \ud655\uc0b0\ud0c4 \uae08\uc9c0 \ucea0\ud398\uc778, \ucd5c\ub8e8\ud0c4 \uc218\ucd9c \u000d\u000a\ubc18\ub300 \ucea0\ud398\uc778, \ud575\ubb34\uae30 \uae08\uc9c0 \ucea0\ud398\uc778\u2019\uc744 \ub2e4\ub8ec\ub2e4. \ud2b9\uc815 \ubb34\uae30\ub97c \ubc18\ub300\ud558\uac70\ub098 \uc0dd\uc0b0, \uc774\uc804, \uc0ac\uc6a9\uc744 \u000d\u000a\uae08\uc9c0\uc2dc\ud0a4\ub824\ub294 \ucea0\ud398\uc778\uc740 \uad6d\uc81c\uc0ac\ud68c\uc5d0\uc11c\ub294 \uc544\uc8fc \uc804\ud1b5\uc801\uc778 \ubc29\uc2dd\uc758 \ud3c9\ud654\uc6b4\ub3d9 \ucea0\ud398\uc778\uc774\uace0 \uc5b4\ub290 \u000d\u000a\uc815\ub3c4 \uc131\uacfc\ub97c \ub0b4\uace0 \uc788\ub294 \ucea0\ud398\uc778\ub3c4 \ub9ce\uc9c0\ub9cc \ud55c\uad6d\uc5d0\uc11c\ub294 \uc544\uc9c1\uae4c\uc9c0 \ud65c\ubc1c\ud55c \ud3b8\uc740 \uc544\ub2c8\ub2e4. \ud558\uc9c0\ub9cc \u000d\u000a\ud55c\uad6d\uc774 \uc810\uc810 \ubb34\uae30 \uc218\ucd9c\uad6d\uc73c\ub85c\uc11c \uc704\uc0c1\uc744 \ub192\uc5ec\uac00\uace0 \uc788\uace0, \ub2e4\ub978 \ub098\ub77c\ub4e4\uc5d0 \ube44\ud574 \uc5ec\ub7ec \ubb34\uae30 \uae08\u000d\u000a\uc9c0 \uc870\uc57d\ub4e4\uc744 \uac00\uc785\ud558\uc9c0 \uc54a\uace0 \uc788\ub294 \ub9cc\ud07c \ud3c9\ud654\uc6b4\ub3d9\uc774 \ud560 \uc218 \uc788\ub294 \uc5ed\ud560\ub3c4 \ub9ce\uc740 \ucea0\ud398\uc778\ub4e4\uc774\ub2e4. \u000d\u000a\ub178\ubca8\ud3c9\ud654\uc0c1\uc744 \ubc1b\uc740 ICAN\uc758 \ud575\ubb34\uae30 \uae08\uc9c0 \uc870\uc57d, \ud55c\uad6d\uc0b0 \ucd5c\ub8e8\ud0c4 \ubc14\ub808\uc778 \uc218\ucd9c \uc800\uc9c0, \uc131\uacf5\uc744 \uac70\u000d\u000a\ub450\uc9c4 \ubabb\ud588\uc9c0\ub9cc \uad6d\ubbfc\uc5f0\uae08\uc744 \uc0c1\ub300\ub85c \ubc8c\uc778 \ud655\uc0b0\ud0c4 \uc0dd\uc0b0 \uae30\uc5c5 \ud22c\uc790 \ucca0\ud68c \ucea0\ud398\uc778\ub4e4\uc744 \uc18c\uac1c\ud55c\ub2e4. \u000d\u000a\ub124 \ubc88\uc9f8 \ucc55\ud130 \u2018\uc0b4\uc778\ub9c8\ubcf4\ub2e4 \ubb34\uc11c\uc6b4 \uc0b4\uc778\ubb34\uae30 \uc804\uae30\ud68c\ub97c \uba48\ucdb0\ub77c: \uc544\ub371\uc2a4 \uc800\ud56d\ud589\ub3d9\u2019\uc744 \uc18c\uac1c\u000d\u000a\ud55c\ub2e4. \uac1c\ubcc4 \ubb34\uae30\ub4e4\uc744 \uae08\uc9c0\uc2dc\ud0a4\ub294 \ucea0\ud398\uc778\uc740 \uc77c\uc815 \uc815\ub3c4 \uc131\uacf5\uc744 \uac70\ub450\uae30\ub3c4 \ud588\uc9c0\ub9cc \ud55c\uacc4\ub3c4 \uba85\ud655\u000d\u000a\ud588\ub2e4. \uc804\uc7c1\uc758 \uc911\uc694\ud55c \ucd95\uc73c\ub85c \ub9c9\ub300\ud55c \ub3c8\uc744 \ubc8c\uc5b4\ub4e4\uc774\ub294 \uad70\uc218\uc0b0\uc5c5\uacfc \uc804\uc7c1\uc758 \uad00\uacc4\ub97c \ub04a\uc5b4\ub0b4\uae30\u000d\u000a\uc5d0\ub294 \uc5ed\ubd80\uc871\uc774\uc5c8\ub2e4. \uc804\uc7c1\uc744 \uce58\ub974\ub294 \uad6d\uac00\ub294 \ubb34\uae30 \uc218\uc785 \ub8e8\ud2b8\uac00 \ub9c9\ud788\uba74 \ubb34\uae30 \uc218\uc785\uc774 \uc6a9\uc774\ud55c \u000d\u000a\ub2e4\ub978 \ub098\ub77c\uc758 \ubb34\uae30\ub97c \uc0ac\uac70\ub098 \ube44\uc2b7\ud55c \uc131\ub2a5\uc744 \uc9c0\ub2cc \ub2e4\ub978 \ubb34\uae30\ub97c \uc0ac\uba74 \uadf8\ub9cc\uc774\uc5c8\ub2e4. \uadf8\ub798\uc11c \ud3c9\u000d\u000a\ud654\ud65c\ub3d9\uac00\ub4e4\uc774 \uc8fc\ubaa9\ud55c \uac83\uc740 \ubb34\uae30\ub97c \uc0ac\uace0\ud30c\ub294 \uc2dc\uc7a5 \uadf8 \uc790\uccb4\uc600\ub2e4. \ubb34\uae30\uc0b0\uc5c5\uc758 \uc774\uc724\uacfc \uc804\uc7c1\uc758 \u000d\u000a\uc0c1\uad00\uad00\uacc4\ub97c \uba85\uc2dc\uc801\uc73c\ub85c \ubcf4\uc5ec\uc8fc\uba70, \ub098\ub0a0\uc774 \uc131\uc7a5\ud558\ub294 \ud55c\uad6d\uc758 \ubc29\uc704\uc0b0\uc5c5 \uaddc\ubaa8\uc5d0 \ub300\ud574 \uc124\uba85\ud588\u000d\u000a\ub2e4. \uc904 \ud544\uc694\uac00 \uc788\uc5c8\ub2e4. \ub610\ud55c \uc138\uacc4 \uacf3\uacf3\uc5d0\uc11c \uc5f4\ub9ac\ub294 \ubb34\uae30\ubc15\ub78c\ud68c\ub97c \uac1c\ucd5c\ud558\uc9c0 \ubabb\ud558\ub3c4\ub85d \ubc29\ud574\u000d\u000a\ud558\uace0, \uc774 \ubb34\uae30\ubc15\ub78c\ud68c\uc5d0\uc11c\ubd80\ud130 \uc804\uc7c1\uacfc \uc0b4\uc0c1\uc774 \uc2dc\uc791\ub41c\ub2e4\ub294 \uac83\uc744 \uc2dc\ubbfc\ub4e4\uc5d0\uac8c \uc54c\ub9ac\ub294 \ud3c9\ud654\ud65c\u000d\u000a\ub3d9\uac00\ub4e4\uc758 \ucea0\ud398\uc778\uc744 \uc18c\uac1c\ud588\ub2e4. \ud55c\uad6d\uc5d0\uc11c\ub3c4 \ub3d9\uc544\uc2dc\uc544 \ucd5c\ub300 \uaddc\ubaa8\uc758 \ubb34\uae30\ubc15\ub78c\ud68c\uc778 \uc11c\uc6b8 \uc544\ub371\u000d\u000a\uc2a4ADEX\uc5d0 \uc800\ud56d\ud558\ub294 \ud589\ub3d9\uc744 \ud3c9\ud654\ud65c\ub3d9\uac00\ub4e4\uc774 \ud3bc\uce58\uace0 \uc788\ub2e4. \u000d\u000a\ub2e4\uc12f \ubc88\uc9f8 \ucc55\ud130 \u2018\uac00\ud574\uc790\uc758 \uc790\ub9ac\uc5d0 \uc11c \ubcf4\uae30: \ubca0\ud2b8\ub0a8\uc804 \uc2dc\ubbfc\ubc95\uc815\u2019\uc744 \uc774\uc57c\uae30\ud55c\ub2e4. \ud55c\uad6d\uc758 \uc804\u000d\u000a\uc7c1 \ud53c\ud574\uc790\ub85c\uc11c\uc758 \uc9d1\ub2e8 \uae30\uc5b5\uc774 \uac15\ud55c \uc0ac\ud68c\ub2e4. 20\uc138\uae30 \ub3d9\uc548 \uc9c0\uc18d\uc801\uc73c\ub85c \uad75\uc9c1\ud55c \uc804\uc7c1\uc758 \ud53c\ud574\u000d\u000a\ub97c \uc628\ubab8\uc73c\ub85c \ub2f9\ud588\uc73c\ub2c8 \uc774\ub294 \uc5b4\uca4c\uba74 \ub2f9\uc5f0\ud55c \uc5ed\uc0ac\uc801 \uc778\uc2dd\uc774\ub2e4. \ud558\uc9c0\ub9cc \uc774\ub7f0 \ud53c\ud574\uc790\ub85c\uc11c\uc758 \uc790\u000d\u000a\uae30 \uc778\uc2dd\uc774 \ub54c\ub85c\ub294 \ud55c\uad6d\uad70\uc758 \uc5ed\uc0ac\uc801 \uac00\ud574\ub97c \uac00\ub9ac\uae30\ub3c4 \ud55c\ub2e4. \ubca0\ud2b8\ub0a8\uc804\uc7c1 \ub2f9\uc2dc \ud55c\uad6d\uad70\uc774 \uc800\u000d\u000a\uc9c0\ub978 \ubbfc\uac04\uc778 \ud559\uc0b4\uc758 \uc9c4\uc0c1\uc744 \uaddc\uba85\ud558\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc758\ubbf8\uc5d0 \ub300\ud574 \uc774\uc57c\uae30\ud588\ub2e4. \uac00\ud574\uc790\uc758 \uc790\ub9ac\u000d\u000a\uc5d0\uc11c \ub0b4\uac00 \uc18d\ud55c \uad6d\uac00\uc758 \ud3ed\ub825\uc5d0 \ucc45\uc784\uc744 \uc9c0\ub294 \uac83\uc744 \ub9d0\ud558\uba70, \uc2dc\ubbfc\ubc95\uc815\uc774\ub77c\ub294 \ud615\uc2dd\uc758 \uc0ac\ud68c\uc6b4\u000d\u000a\ub3d9\uc5d0 \ub300\ud574\uc11c\ub3c4 \uc18c\uac1c\ud55c\ub2e4.\u000d\u000a\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798 : \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c"," & 80\u000d\u000a3. \ud559\ubb38\uc801 \u00b7 \uc0ac\ud68c\uc801 \ubc18\uc751\uacfc \ud65c\uc6a9\u000d\u000a\u300c\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798: \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c\u300d\uac00 \ub098\uc628 \ub4a4 \uc774 \u000d\u000a\ubcf4\uace0\uc11c\ub97c \ubc14\ud0d5\uc73c\ub85c \ub2e8\ud589\ubcf8 \u300e\ud3c9\ud654\ub294 \ucc98\uc74c\uc774\ub77c\u300f(\ube68\uac04\uc18c\uae08, 2021)\uc744 \uc37c\ub2e4. \uc6d0\ub798\ub294 \uc870\uae08 \ub2e4\ub978 \ucee8\uc149\u000d\u000a\uc758 \ub2e8\ud589\ubcf8\uc744 \uc4f0\uae30\ub85c \ud55c \uc0c1\ud669\uc5d0\uc11c \ucd9c\ud310\uc0ac \ub300\ud45c\uac00 \uc774 \ubcf4\uace0\uc11c\ub97c \ubcf4\uace0\ub294, \ud3c9\ud654\uc5d0 \ub300\ud574 \uc18c\uac1c\ud558\u000d\u000a\ub294 \uc785\ubb38\uc11c\ub85c \ubc1c\uc804\uc2dc\ud0a4\uc790\uace0 \uc81c\uc548\ud574\uc11c \ucc45\uc744 \uc4f0\uac8c \ub418\uc5c8\ub2e4. \uc9d1\ud544 \uae30\uac04\uc774 \ucda9\ubd84\ud558\uc9c0 \uc54a\uc544 \ubcf4\uace0\uc11c\u000d\u000a\uc5d0 \ub2f4\uc9c0 \ubabb\ud55c \ub0b4\uc6a9\uc774 \ub9ce\uc558\uace0, \ud55c \ubc88 \uc644\uc131\ub41c \ud615\ud0dc\uc758 \uae00\uc744 \uc368\ubcf4\ub2c8 \uc5b4\ub5a4 \uc810\ub4e4\uc774 \ubd80\uc871\ud55c\uc9c0 \ubcf4\u000d\u000a\uc5ec\uc11c \uadf8 \ubd80\ubd84\uc744 \uac1c\uc120\ud558\uace0 \uc2f6\uc5c8\uae30 \ub54c\ubb38\uc5d0 \ud754\ucf8c\ud788 \ub2e8\ud589\ubcf8 \uc9d1\ud544\uc744 \uc2dc\uc791\ud588\ub2e4. \ubb3c\ub860 \ud3c9\ud654\ud65c\ub3d9\u000d\u000a\uac00\ub85c\uc11c \ud3c9\ud654\uc6b4\ub3d9\uc774 \uc8fc\uc7a5\ud558\ub294 \ubc14\uc640 \uc804\uc7c1\uc5c6\ub294\uc138\uc0c1\uc758 \ucea0\ud398\uc778\uc744 \uc2dc\ubbfc\ub4e4\uc5d0\uac8c \uc54c\ub9b4 \uc218 \uc788\ub294 \uc88b\u000d\u000a\uc740 \uae30\ud68c\ub77c\ub294 \uac83\uacfc, \ucc45\uc744 \uc4f0\ub294 \uacfc\uc815\uc774 \ub098\uc5d0\uac8c\ub3c4 \ub9ce\uc740 \uacf5\ubd80\uac00 \ub420 \uac83\uc774\ub77c\ub294 \uc810\ub3c4 \ub2e8\ud589\ubcf8\uc744 \ub0b4\u000d\u000a\uac8c \ub41c \uc911\uc694\ud55c \uc774\uc720\uc600\ub2e4. \u000d\u000a\uc5f0\uad6c\ubcf4\uace0\uc11c\ub97c \uc4f8 \ub54c \uc0dd\uac01\ud55c \ub0b4 \uc5f0\uad6c\uc758 \uc7a5\uc810\uc744 \ub2e8\ud589\ubcf8\uc5d0\uc11c\ub3c4 \uadf8\ub300\ub85c \uc0b4\ub9ac\uae30\ub85c \ud588\ub2e4. \ud65c\u000d\u000a\ub3d9\uac00\ub85c\uc11c\uc758 \uacbd\ud5d8\uc774 \uc801\uadf9\uc801\uc73c\ub85c \ub4dc\ub7ec\ub098\ub3c4\ub85d \ucc45\uc744 \uad6c\uc131\ud588\ub2e4. \uc5f0\uad6c\ubcf4\uace0\uc11c\uac00 \ub098\uc654\uc744 \ub2f9\uc2dc, \ud3c9\u000d\u000a\ud654\ud65c\ub3d9\uac00\ub4e4\uc774 \ud3c9\ud654\uc6b4\ub3d9\uc744 \ud558\uba74\uc11c \ubc1b\ub294 \uc9c8\ubb38\uc73c\ub85c \ucc55\ud130\uc758 \uc81c\ubaa9\uc744 \uc9c0\uc740 \uac83\uc774 \ubc18\uc751\uc774 \uc88b\uc558\ub2e4. \u000d\u000a\ub2e8\ud589\ubcf8\uc758 1\ubd80\ub294 \uc544\uc608 \ud3c9\ud654\ud65c\ub3d9\uac00\ub4e4\uc774 \ub9ce\uc774 \ubc1b\ub294 \uc9c8\ubb38\ub4e4-\u201c\uc804\uc7c1\uacfc \ud3ed\ub825\uc740 \uc778\uac04\uc758 \ubcf8\uc131\uc774 \uc544\u000d\u000a\ub2cc\uac00?\u201d, \u201c\uac15\ud55c \uad70\ub300\uac00 \uc788\uc5b4\uc57c \ud3c9\ud654\ub97c \uc9c0\ud0ac \uc218 \uc788\uc9c0 \uc54a\ub098?\u201d, \u201c\ub2e4\uc218\ub97c \uc704\ud55c \uc18c\uc218\uc758 \ud76c\uc0dd\uc740 \ud569\u000d\u000a\ub9ac\uc801\uc778 \uac83 \uc544\ub2cc\uac00?\u201d, \u201c\uc808\ub300\uc545\uc744 \ubab0\uc544\ub0b4\uae30 \uc704\ud55c \uc804\uc7c1\uc740 \ubd88\uac00\ud53c\ud558\uc9c0 \uc54a\ub098?\u201d\ub85c \ubaa9\ucc28\uc640 \ub0b4\uc6a9\u000d\u000a\uc744 \ucc44\uc6e0\ub2e4. \uc774 \uc9c8\ubb38\ub4e4\uc740 \ud3c9\ud654\uc6b4\ub3d9\uc774 \ud55c\uad6d\uc0ac\ud68c\uc758 \ubcf4\ud3b8\uc801\uc778 \uc0c1\uc2dd\uacfc \ucda9\ub3cc\ud558\ub294 \uc7c1\uc810\uc744 \uc0c1\uc9d5\uc801\u000d\u000a\uc73c\ub85c \ubcf4\uc5ec\uc900\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \ub2e8\ud589\ubcf8\uc758 2\ubd80\uc640 3\ubd80\ub294 \uc804\uc7c1\uc5c6\ub294\uc138\uc0c1\uc758 \uc0dd\uac01\uacfc \uc8fc\uc7a5\uc744 \ub178\uace8\uc801\u000d\u000a\uc73c\ub85c \ub4dc\ub7ec\ub0c8\ub2e4. \uc804\uc7c1\uc774 \uc77c\uc5b4\ub098\ub294 \uc6d0\uc778\uc744 \ubd84\uc11d\ud558\uace0 \uc804\uc7c1\uacfc \ub9de\uc11c\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc758 \ud798\uc5d0 \ub300\ud574 \uc774\u000d\u000a\uc57c\uae30 \ud588\ub2e4. \ub610\ud55c \ud55c\uad6d\uc740 21\uc138\uae30 \uc804\uc7c1 \uc0b0\uc5c5\uc5d0\uc11c \ud55c\uad6d\uc774 \ucc98\ud55c \uc704\uce58\ub97c \uad70\uc218\uc0b0\uc5c5\uacfc \ubb34\uae30\uc218\ucd9c\u000d\u000a\uc758 \uc131\uc7a5 \uce21\uba74\uc5d0\uc11c \uc774\uc57c\uae30\ud588\ub2e4. \uc5f0\uad6c\ubcf4\uace0\uc11c\uc758 \ud6c4\ubc18\ubd80\uc5d0\uc11c \uc18c\uac1c\ud588\ub358 \ud55c\uad6d \ud3c9\ud654\uc6b4\ub3d9 \ucea0\ud398\uc778\u000d\u000a\uacfc \ub2e8\uccb4\ub4e4 \uc18c\uac1c\ub294 \ub2e8\ud589\ubd09\uc5d0\ub294 \ub123\uc9c0 \uc54a\uc558\ub2e4. \uac01\uac01\uc758 \ucea0\ud398\uc778\uc744 \ubcd1\ub82c\uc801\uc774\uace0 \ud53c\uc0c1\uc801\uc73c\ub85c \ub2e4\ub8e8\u000d\u000a\ub294 \uac83\uc774 \ud3c9\ud654\uc6b4\ub3d9\uc744 \uc81c\ub300\ub85c \uc18c\uac1c\ud558\ub294 \ubc29\uc2dd\uc740 \uc544\ub2c8\ub77c\uace0 \ud310\ub2e8\ud588\ub2e4. \u000d\u000a\ub2e8\ud589\ubcf8\uc740 \uc5f0\uad6c\ubcf4\uace0\uc11c\ubcf4\ub2e4 \ubd84\ub7c9\ub3c4 \ub354 \uae38\uace0 \ub0b4\uc6a9\ub3c4 \ub354 \uae4a\uc774 \uc788\uac8c \ub2e4\ub8e8\ub2e4 \ubcf4\ub2c8 \uae00\uc744 \uc4f0\uae30 \uc704\u000d\u000a\ud574 \ucd94\uac00\ub85c \ub354 \ub9ce\uc740 \uacf5\ubd80\ub97c \ud574\uc57c\ud588\ub2e4. \ud3c9\ud654\ud65c\ub3d9 \ud604\uc5c5\uc5d0 \uc885\uc0ac\ud558\uba74\uc11c \ub3d9\uc2dc\uc5d0 \ucc45\uc744 \uc77d\uace0 \uae00\uc744 \u000d\u000a\uc4f0\ub294 \uac83\uc740 \uacb0\ucf54 \uc26c\uc6b4 \uacfc\uc815\uc740 \uc544\ub2c8\uc5c8\uc9c0\ub9cc \ubb34\ucc99 \uc7ac\ubc0c\uace0 \ud798\uc774 \ub098\ub294 \uacbd\ud5d8\uc774\uc5c8\ub2e4. \ud604\uc7a5\ud65c\ub3d9\uc740 \ud604","80 81\u000d\u000a\uc7a5 \ud2b9\uc720\uc758 \uc0dd\ub3d9\uac10\uacfc \uc0ac\ub78c\ub4e4\uc774 \uc8fc\ub294 \uc5d0\ub108\uc9c0\ub97c \uc5bb\uc744 \uc218 \uc788\ub2e4\ub294 \uac15\uc810\uc774 \uc788\uc9c0\ub9cc, \ube60\ub974\uac8c \ud758\ub7ec\uac00\u000d\u000a\ub294 \uc138\uc0c1\uc758 \ud638\ud761\uc5d0 \ub530\ub77c\uac00\uae30 \ubc14\ube60\uc11c \uc880 \ub354 \uadfc\uc6d0\uc801\uc778 \uc9c8\ubb38\uc744 \uc0b4\ud3b4\ubcf4\uac70\ub098 \ubd80\uc871\ud55c \uc810\uc744 \ucc44\uc6cc\uac00\u000d\u000a\uae30 \uc5b4\ub824\uc6b4\ub370 \ucc45\uc744 \uc77d\uace0 \uae00\uc744 \uc4f0\uba74\uc11c \ud55c \uacf5\ubd80\ub294 \uc774\ub7f0 \uc810\uc5d0\uc11c \ud604\uc7a5\uc5d0\uc11c \ucc44\uc6b8 \uc218 \uc5c6\ub294 \ud65c\ub825\uacfc \u000d\u000a\uc601\uac10\uc744 \ub0b4\uac8c \uc8fc\uc5c8\ub2e4. \ub610\ud55c \uacf5\ubd80\uc758 \uacb0\uacfc\ubb3c\uc774 \ub208\uc5d0 \ubcf4\uc774\ub294 \ubb3c\uc9c8\uc778 \ucc45\uc73c\ub85c \ub098\uc624\ub294 \uacbd\ud5d8\uc740, \ubb54\u000d\u000a\uac00\ub97c \ud574\ub0c8\ub2e4\ub294 \uc131\ucde8\uac10\uacfc \ud568\uaed8 \ub178\ub825\ud55c\ub2e4\uba74 \ubb34\uc5c7\uc774\ub4e0 \ud574\ub0bc \uc218 \uc788\ub2e4\ub294 \uc790\uc2e0\uac10\uc744 \uc8fc\uc5c8\ub2e4. \uc0ac\ud68c\u000d\u000a\uc6b4\ub3d9\uc740 \uc0ac\ud68c\ub97c \ubcc0\ud654\uc2dc\ud0ac \uc218 \uc788\ub294 \ud798\uc774 \uc788\uc9c0\ub9cc, \uadf8 \uacfc\uc815\uc740 \uc9c0\ub09c\ud558\uace0 \uc9c0\ub09c\ud55c \uacfc\uc815 \uc18d\uc5d0\uc11c \ud65c\u000d\u000a\ub3d9\uac00\ub4e4\uc774 \uc18c\uc9c4\ub418\ub294 \uc77c\uc774 \ube48\ubc88\ud788 \ubc1c\uc0dd\ud55c\ub2e4. \uadf8\ub807\uae30 \ub54c\ubb38\uc5d0 \uc774\ub7ec\ud55c \uc885\ub958\uc758 \uc131\ucde8\uac10\uc744 \ud65c\ub3d9\uac00\u000d\u000a\ub4e4\uc774 \uacbd\ud5d8\ud558\ub294 \uac83\uc740 \ub9e4\uc6b0 \uc911\uc694\ud558\ub2e4. \uc5f0\uad6c\ubcf4\uace0\uc11c\uac00 \ucc45\uc73c\ub85c \ubc1c\uc804\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \ub098\ub294 \ud65c\ub3d9\uac00\u000d\u000a\ub85c\uc11c \ubb34\uc5b8\uac00\ub97c \uc77c\uad6c\uc5b4\ub0c8\ub2e4\ub294, \ub0b4 \ub178\ub825\uc774 \uc0ac\ud68c\ub97c \ubcc0\ud654\uc2dc\ud0a4\ub294\ub370 \uc77c\uc870\ud588\ub2e4\ub294 \uae30\uc068\uc744 \ub290\uaf08\ub2e4. \u000d\u000a\ubb3c\ub860 \uc2a4\uc2a4\ub85c\uc758 \ub9cc\uc871\uc774 \uc5f0\uad6c\ubcf4\uace0\uc11c\ub97c \uc4f0\uace0 \ucc45\uc744 \uc4f4 \uac00\uc7a5 \ud070 \uc774\uc720\ub294 \uc544\ub2c8\uc5c8\ub2e4. \uae00\uc744 \uc4f0\uace0 \u000d\u000a\ucc45\uc744 \uc4f4 \uac00\uc7a5 \ud070 \uc774\uc720\ub294, \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc8fc\uc7a5\uc744 \uc138\uc0c1\uc5d0 \uc54c\ub9ac\uace0 \uc0ac\ub78c\ub4e4\uc744 \ub9cc\ub098 \ub300\ud654\ud558\uace0 \uc124\ub4dd\u000d\u000a\ud558\uace0 \uc2f6\uc5b4\uc11c\uc600\ub2e4. \ucc45 \ucd9c\uac04\uc744 \ud1b5\ud574 \ub098\ub294 \ub354 \ub9ce\uc740 \uc0ac\ub78c\ub4e4\uc744, \ub354 \ub2e4\uc591\ud55c \uc0ac\ub78c\ub4e4\uc744 \ub9cc\ub0a0 \uc218 \uc788\uc5c8\u000d\u000a\ub2e4. \uc804\uc7c1\uc5c6\ub294\uc138\uc0c1 \ud65c\ub3d9\uc744 \ud574\uc624\uba74\uc11c \uac15\uc758\ub098 \uac15\uc5f0 \uc694\uccad\uc774 \uc544\uc608 \uc5c6\uc5c8\ub358 \uac83\uc740 \uc544\ub2c8\uc9c0\ub9cc, \uc9c1\uc811\u000d\u000a\ud589\ub3d9\uc744 \uc8fc\ub85c \ud558\ub294 \ub2e8\uccb4\uc758 \ud65c\ub3d9 \uc131\uaca9\uc0c1 \ub2e4\uc591\ud55c \uacc4\uce35\uc758 \uc2dc\ubbfc\ub4e4\uc744 \ub9cc\ub098 \uc774\uc57c\uae30\ub97c \ub098\ub204\ub294 \uae30\ud68c\u000d\u000a\ub294 \ub9ce\uc9c0 \uc54a\uc558\ub2e4. \ud2b9\ud788 \ud3c9\ud654\uc6b4\ub3d9\uc5d0 \uc6b0\ud638\uc801\uc774\uac70\ub098 \uad00\uc2ec\uc744 \uac00\uc9c0\uace0 \uc788\ub294 \uc0ac\ub78c\ub4e4, \ub2e4\uc2dc \ub9d0\ud574 \ud3c9\u000d\u000a\ud654\uc6b4\ub3d9\uc744 \ud568\uaed8 \ud574\ub098\uac08 \uc7a0\uc7ac\uc801\uc778 \uc9c0\uc9c0\uc790\ub4e4\uc744 \ub9cc\ub0a0 \uae30\ud68c\ub294 \ud754\uce58 \uc54a\uc558\ub2e4. 2021\ub144 4\uc6d4 \ucc45 \ucd9c\u000d\u000a\uac04 \uc774\ud6c4 \uac15\uc5f0 \uc694\uccad\uc774 \ubd80\uca4d \ub298\uc5b4 \uc9c0\uae08\uae4c\uc9c0 \ubaa8\ub450 15\ud68c\uc758 \uac15\uc5f0 \ubc0f \ubd81\ud1a0\ud06c\ub97c \uc9c4\ud589\ud588\uace0 \ub2e4\uc12f \uad70\u000d\u000a\ub370 \ub9e4\uccb4\uc5d0 \uc778\ud130\ubdf0\ub098 \uae30\uace0\uae00\uc744 \ud1b5\ud574\uc11c \uc804\uc7c1\uc5c6\ub294\uc138\uc0c1\uc758 \ud3c9\ud654\uc6b4\ub3d9\uc744 \uc18c\uac1c\ud560 \uc218 \uc788\uc5c8\ub2e4. \ud2b9\ud788 \u000d\u000a\uc911\uace0\ub4f1\ud559\uad50\uc640 \ub300\uc548\ud559\uad50\uc5d0\uc11c \uac15\uc758 \uc694\uccad\uc774 \ub9ce\uc774 \uc640\uc11c \uccad\uc18c\ub144\ub4e4\uacfc \ub9cc\ub098\uc11c \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc774\uc57c\uae30\u000d\u000a\ub97c \uc804\ud560 \uc218 \uc788\ub294 \uae30\ud68c\uac00 \ubd80\uca4d \ub298\uc5c8\ub2e4\ub294 \uac83\uc774 \ud070 \uc758\ubbf8\uac00 \uc788\ub2e4. \ub0b4\uc6a9 \ubfd0\ub9cc \uc544\ub2c8\ub77c \ub17c\uc7c1\uc801\uc778 \uc9c8\u000d\u000a\ubb38\uc73c\ub85c \uad6c\uc131\ud55c \ud615\uc2dd\uc774 \ud559\uad50\uc5d0\uc11c \ud1a0\ub860 \uc218\uc5c5\uc5d0 \ud65c\uc6a9\ud558\uae30 \uc88b\ub2e4\ub294 \ud3c9\uac00\ub97c \uad50\uc0ac \ub3c5\uc790\ub4e4\uc5d0\uac8c \ub4e4\u000d\u000a\uc5c8\ub2e4. \ub0b4\uc6a9\ubfd0\ub9cc \uc544\ub2c8\ub77c \ub9d0\ud558\uae30\uc758 \ud615\uc2dd \ub610\ud55c \uc911\uc694\ud558\ub2e4\ub294 \uac83\uc744 \uae68\ub2eb\uac8c \ub418\uc5c8\ub2e4. \u000d\u000a4. \ub354 \ub9ce\uc740 \ud65c\ub3d9\uac00\ub4e4\uc774 \uc790\uc2e0\uc758 \uacbd\ud5d8\uc744 \ubc14\ud0d5\uc73c\ub85c \uae00\uc744 \uc4f0\uace0 \ucc45\uc744 \ub0b4\uae30\u000d\u000a\ucc45\uc744 \ub0b8 \ub4a4\uc5d0\ub3c4 \uc544\uc26c\uc6c0\uc740 \ub0a8\uc558\ub2e4. \ub0b4\uc6a9\uc801\uc778 \uba74\uc5d0\uc11c \ubd80\uc871\ud568\uc774 \ub208\uc5d0 \ub4e4\uc5b4\uc654\ub2e4. \ucc45\uc5d0\uc11c\ub294 \u000d\u000a\uc8fc\ub85c \uad70\uc0ac\uc8fc\uc758\uc640 \uc804\uc7c1\uc5d0 \uc800\ud56d\ud558\ub294 \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc8fc\uc7a5\uc744 \ub2e4\ub918\ub294\ub370, \ud3c9\ud654\uc6b4\ub3d9 \uac00\uc6b4\ub370\ub294 \ub300\uc548\u000d\u000a\uc801\uc774\uace0 \ube44\uad70\uc0ac\uc801\uc778 \uc2dc\uc2a4\ud15c\uc744 \ub9cc\ub4dc\ub294 \ud615\ud0dc\uc758 \uc6b4\ub3d9\ub3c4 \uc788\ub2e4. \ucc45\uc744 \uc4f8 \ub54c\ub3c4 \uc774 \uc9c0\uc810\uc774 \ubd80\uc871\ud558\u000d\u000a\uc138\uc0c1\uc744 \ubc14\uafb8\ub294 \ube44\ud3ed\ub825\uc758 \ud798 : \ud3c9\ud654\uc6b4\ub3d9\uc774 \uad81\uae08\ud55c \uc2dc\ubbfc\ub4e4\uc744 \uc704\ud55c \uc548\ub0b4\uc11c"," & 82\u000d\u000a\ub2e4\ub294 \uac83\uc744 \uc778\uc9c0\ud558\uace0\ub294 \uc788\uc5c8\uc9c0\ub9cc \uc798 \ud480\uc5b4\ub0bc \uc5ed\ub7c9\uc774 \ub418\uc9c0 \uc54a\uc544 \ud3ec\uae30\ud588\ub358 \ub0b4\uc6a9\uc774\ub2e4. \ubcd1\uc5ed\uac70\ubd80 \u000d\u000a\ub610\ud55c \ucc45\uc5d0\uc11c \ub2e4\ub8e8\uc9c0 \uc54a\uc558\ub2e4. \ubcd1\uc5ed\uac70\ubd80\uc6b4\ub3d9\uc740 \uc804\uc7c1\uc5c6\ub294\uc138\uc0c1\uc758 \uba54\uc778 \ucea0\ud398\uc778\uc774\uc5c8\uace0, \ub0b4\uac00 \uac00\u000d\u000a\uc7a5 \uae4a\uac8c \uadf8\ub9ac\uace0 \uc624\ub798 \ud574\uc628 \ucea0\ud398\uc778\uc778\ub370 \ucc45\uc774 \ub098\uc624\uace0 \ub098\uc11c\uc57c \uc774 \ub0b4\uc6a9\uc744 \ub2e4\ub8e8\uc9c0 \uc54a\uc558\ub2e4\ub294 \uac83\u000d\u000a\uc744 \uc778\uc9c0\ud588\ub2e4. \uc77c\ubd80\ub7ec \ube80 \uac83\uc740 \uc544\ub2c8\uc5c8\ub294\ub370 \ub098\ub3c4, \ud3b8\uc9d1\uc790\ub3c4, \uc6d0\uace0\ub97c \uac80\ud1a0\ud574\uc900 \ub3d9\ub8cc \ud65c\ub3d9\uac00\ub4e4\u000d\u000a\ub3c4 \ubcd1\uc5ed\uac70\ubd80\uc5d0 \ub300\ud55c \ub0b4\uc6a9\uc744 \ub123\uc9c0 \uc54a\uc740 \uac83\uc744 \ub193\uce58\uace0 \ub9d0\uc558\ub2e4. \uc774 \uc544\uc26c\uc6c0\uc740 \ub450 \ubc88\uc9f8 \ucc45\uc744 \ubcd1\uc5ed\u000d\u000a\uac70\ubd80\ub9cc\uc744 \uc8fc\uc81c\ub85c \uc4f0\uba74\uc11c \ud480 \uc218 \uc788\uc5c8\ub2e4.\u000d\u000a\u300e\ud3c9\ud654\ub294 \ucc98\uc74c\uc774\ub77c\u300f\uc5d0\uc11c \ub098\ub294 \uc774 \ucc45\uc774 \ud3c9\ud654\uc6b4\ub3d9\uc5d0 \uc2dc\ubbfc\ub4e4\uc744 \ucd08\ub300\ud558\ub294 \ucd08\ub300\uc7a5\uc774\uae30\ub97c \ubc14\u000d\u000a\ub780\ub2e4\uace0 \uc37c\ub2e4. \ub2f9\uc7a5 \ud3c9\ud654\uc6b4\ub3d9\uc774 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \ub110\ub9ac \uc54c\ub824\uc9c0\uace0, \ud3c9\ud654\uc6b4\ub3d9\uc758 \uc9c0\uc9c0\uc790\uc640 \ub3d9\ub8cc \uc2dc\u000d\u000a\ubbfc\ub4e4\uc774 \uc5c4\uccad\ub098\uac8c \ub298\uc5b4\ub098\uace0 \uadf8\ub7ec\uc9c0\ub294 \uc54a\uc558\ub2e4. \uc5f0\uad6c\ubcf4\uace0\uc11c \ud55c \uad8c, \ucc45 \ud55c \uad8c\uc744 \ub0c8\ub2e4\uace0 \uc138\uc0c1\uc774 \u000d\u000a\uc21c\uc2dd\uac04\uc5d0 \ubc14\ub00c\uac70\ub098 \uc0ac\ub78c\ub4e4\uc758 \uc0dd\uac01\uc774 \ud558\ub8fb\ubc24 \ub9cc\uc5d0 \ubc14\ub00c\uc9c0\ub294 \uc54a\ub294\ub2e4. \ubcc0\ud654\ub294 \uc624\ub798 \uac78\ub9ac\ub294 \uacfc\u000d\u000a\uc815\uc774\uba70, \ud3c9\ud654\ub294 \uc6b0\ub9ac\uac00 \ub3c4\ub2ec\ud558\ub824\ub294 \uc5b4\ub5a4 \uc0c1\ud0dc\uac00 \uc544\ub2c8\ub77c \ubd80\uc815\uc758\ub97c \uadf9\ubcf5\ud558\uace0 \ubcc0\ud654\ud558\ub294 \uacfc\uc815\u000d\u000a\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4.\u000d\u000a\ucc45\uc744 \ub0b8 \ub4a4 \ub18d\ub2f4\ucc98\ub7fc \uc8fc\ubcc0 \ud65c\ub3d9\uac00\ub4e4\uc5d0\uac8c \u201c\ubaa8\ub4e0 \uac78 \ub2e4 \uc544\ub294 \uc0ac\ub78c\ub4e4\uc740 \uae00\uc744 \uc4f0\uc9c0 \uc54a\ub294\ub2e4. \u000d\u000a\uc608\uc218\ub098 \ubd80\ucc98\uac00 \ucc45\uc744 \uc4f4 \uac78 \ubd24\ub0d0? \uae00\uc740 \uc544\ub294 \uc0ac\ub78c\uc774 \uc4f0\ub294 \uac8c \uc544\ub2c8\ub77c \ubb34\uc5bc \ubaa8\ub974\ub294 \uc0ac\ub78c\uc774 \uc790\u000d\u000a\uc2e0\uc774 \ubaa8\ub974\ub294 \uac78 \uacf5\ubd80\ud558\uae30 \uc704\ud574 \uc4f0\ub294 \uac70\ub2e4\u201d\ub77c\uba70 \uae00\uc4f0\uae30\ub97c \uad8c\ud588\ub2e4. \ub098\ub294 \ud65c\ub3d9\uac00\ub4e4\uc758 \uacbd\ud5d8\uc774 \u000d\u000a\uad49\uc7a5\ud788 \uc88b\uc740 \uae00\uac10\uc774 \ub41c\ub2e4\uace0 \uc0dd\uac01\ud558\uba70, \uacbd\ud5d8 \ub9ce\uc740 \ud65c\ub3d9\uac00\ub4e4\uc758 \uc0ac\uc720\ub294 \uc0ac\ud68c\uc758 \uacf5\uacf5\uc7ac\ub85c \ud65c\ub3d9\u000d\u000a\uac00\ub4e4\uc740 \uc790\uc2e0\uc758 \uacbd\ud5d8\uacfc \uc0ac\uc720\ub97c \uc138\uc0c1\uc5d0 \uc774\uc57c\uae30\ud574\uc57c \ud55c\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \ub354\uc6b1\uc774 \uc55e\uc11c \uc774\uc57c\uae30\ud55c \u000d\u000a\uac83\ucc98\ub7fc \uae00\uc4f0\uae30\ub294 \ud65c\ub3d9\uac00 \uc790\uc2e0\uc744 \uc784\ud30c\uc6cc\ub9c1\ud558\uba70 \uae00\uc4f0\ub294 \uacfc\uc815\uc5d0\uc11c \ud55c \uacf5\ubd80\ub294 \ub204\uad6c\ubcf4\ub2e4\ub3c4 \uc2a4\u000d\u000a\uc2a4\ub85c\uc5d0\uac8c \ud070 \ud798\uc774 \ub41c\ub2e4. \uc5f0\uad6c\ub77c\ub294 \uac70\ucc3d\ud55c \uc774\ub984\uc774 \uc544\ub2c8\ub354\ub77c\ub3c4, \uc790\uc2e0\uc758 \ud65c\ub3d9 \uacbd\ud5d8\uc744 \ud574\uc11d\ud558\u000d\u000a\uace0, \uc8fc\uc7a5\ud558\ub294 \ubc14\ub97c \ub354 \uc124\ub4dd\ub825 \uc788\uac8c \ud558\uae30 \uc704\ud574 \ub17c\ub9ac\ub97c \ubcbc\ub9ac\uace0 \uadfc\uac70\ub97c \ubc1c\uad74\ud558\ub294 \ub178\ub825\uc740 \ubd84\uba85 \u000d\u000a\ubd84\uba85\ud55c \uc131\ucde8\uac10\uacfc \ud568\uaed8 \uc0ac\ud68c\uc6b4\ub3d9 \ucea0\ud398\uc778\uc5d0\ub3c4 \uaf2d \ud544\uc694\ud55c \uc791\uc5c5\uc774 \ub420 \uac83\uc774\ub2e4. \u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","82 83\u000d\u000a\uae40\ub3c4\uade0 / \ud55c\uad6dNGO\ud559\ud68c, NGO\uc5f0\uad6c, 2017\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \u000d\u000a\uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c\u000d\u000a1. \ubb38\uc81c\uc758\uc2dd\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \uc804\ub77c\ubd81\ub3c4 \uc775\uc0b0\uc2dc \ud568\ub77c\uba74\uc5d0 \uc788\ub294 \uc7a5\uc810\ub9c8\uc744 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c \ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758\u000d\u000a\uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uc744 \ub450\uace0 \uad00\ub828 \ud589\uc704\uc790\ub4e4\uc774 \uc5b4\ub5bb\uac8c \ub300\uc751\ud558\uace0 \uc0c1\ud638\uc791\uc6a9\ud588\ub294\uc9c0 \uadf8\ub9ac\uace0 \uadf8\ub7ec\ud55c \u000d\u000a\ub300\uc751\uacfc \uc0c1\ud638\uc791\uc6a9\uc774 \ud53c\ud574\uc758 \uc778\uc815\uacfc\uc815\uc5d0 \ubbf8\uce5c \uc601\ud5a5\uc744 \ubc1d\ud600 \ubcf4\ub824 \ud55c\ub2e4. \uc7a5\uc810\ub9c8\uc744\uc740 \uc815\ubd80\uac00 \u2018\ud658\u000d\u000a\uacbd\uc624\uc5fc\uacfc \ube44\ud2b9\uc774\uc131 \uc9c8\ud658\uc778 \uc554 \ubc1c\ubcd1\u2019 \uc0ac\uc774\uc758 \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uacf5\uc2dd\uc801\uc73c\ub85c \uc778\uc815\ud55c \uad6d\ub0b4 \ucd5c\ucd08 \u000d\u000a\uc0ac\ub840\ub77c\ub294 \uc810\uc5d0\uc11c \uc8fc\ubaa9\ud560 \ub9cc\ud558\ub2e4. \uc7a5\uc810\ub9c8\uc744\uc740 \uc2e4\uc81c \uac70\uc8fc\uc790\uac00 100\uba85 \uc774\ub0b4\uc758 \ubcbc\ub18d\uc0ac \uc911\uc2ec\uc758 \u000d\u000a\uc774 \uc5f0\uad6c\ub294 \uc804\ubd81 \uc775\uc0b0\uc2dc \ud568\ub77c\uba74\uc5d0 \uc18c\uc7ac\ud55c \uc7a5\uc810\ub9c8\uc744(\uc804\ubd81 \uc775\uc0b0\uc2dc)\uc744 \uc0ac\ub840\ub85c \ud658\uacbd\uc624\uc5fc \ud53c\ud574\uc758 \uc5ed\ud559\u000d\u000a\uc801 \uc778\uc815\uacfc\uc815\uc744 \ub450\uace0 \ud589\uc704\uc790\ub4e4\uc774 \uc5b4\ub5bb\uac8c \ub300\uc751\ud558\uace0 \uc0c1\ud638\uc791\uc6a9\ud588\ub294\uc9c0 \uc0b4\ud3b4\ubcf4\uc558\ub2e4. \uc7a5\uc810\ub9c8\uc744\uc740 2001\ub144 \u000d\u000a10\uc6d4, \ub9c8\uc744 \uc778\uadfc\uc5d0\uc11c \ube44\ub8cc\uc0dd\uc0b0 \uacf5\uc7a5\uc778 \u321c\uae08\uac15\ub18d\uc0b0\uc774 \uac00\ub3d9\uc744 \uc2dc\uc791\ud558\uc5ec 2017\ub144 4\uc6d4 \uacf5\uc7a5 \uac00\ub3d9\uc744 \uc911\u000d\u000a\ub2e8\ud560 \ub54c\uae4c\uc9c0 \ub9e4\uc5f0 \ubc0f \uc545\ucde8, \ud3d0\uc218\ub85c \uc778\ud55c \ud658\uacbd\uc624\uc5fc\uc744 \uc7a5\uae30\uac04 \uacbd\ud5d8\ud588\ub2e4. \uc7a5\uc810\ub9c8\uc744 \uc0ac\ub840\ub294 \ud658\uacbd\uc624\uc5fc\uacfc \u000d\u000a\ube44\ud2b9\uc774\uc131 \uc9c8\ud658\uc778 \uc554 \uc0ac\uc774\uc758 \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uc815\ubd80\uac00 \uacf5\uc2dd \uc778\uc815\ud55c \uad6d\ub0b4 \ucd5c\ucd08 \uc0ac\ub840\ub77c\ub294 \uc810\uc5d0\uc11c \uc911\uc694\u000d\u000a\ud558\ub2e4. \uc5f0\uad6c\uc790\ub294 \uc7a5\uc810\ub9c8\uc744 \uc0ac\ub840\uac00 \uacf5\ub860\ud654\ub418\ub294 \uacfc\uc815\uc5d0 \ub300\ud55c \ubd84\uc11d\uacfc \ub354\ubd88\uc5b4 \uc7a5\uc810\ub9c8\uc744 \uad00\ub828 \uc77c\uc9c0, \uc815\ubd80\u000d\u000a\uc758 \uacf5\uc2dd\uc801\uc778 \uac74\uac15\uc5ed\ud559\uc870\uc0ac \uacb0\uacfc\ubcf4\uace0\uc11c \ub4f1\uc744 \ud65c\uc6a9\ud588\ub2e4. \uc2ec\uce35\uba74\uc811\uc740 \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4 \ucc38\uc5ec\ud55c \ubbfc\u000d\u000a\uac04\uc704\uc6d0\uacfc \uac74\uac15\uc601\ud5a5\uc870\uc0ac\ub97c \uc218\ud589\ud55c \uc6a9\uc5ed\uae30\uad00(\uc5f0\uad6c\uc18c) \uad00\uacc4\uc790\ub97c \ub300\uc0c1\uc73c\ub85c \uc9c4\ud589\ud588\ub2e4. \u000d\u000a\uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \uc5f0\uad6c\uc790\uac00 \ud55c\uad6d\ud658\uacbd\uc5f0\uad6c\uc6d0\uc758 \uae30\ubcf8\uacfc\uc81c \uc9c0\uc6d0\uc73c\ub85c \uc774 \uc5f0\uad6c\ub97c \uc218\ud589\ud55c \uc810\uc744 \uace0\ub824\ud558\uc9c0 \u000d\u000a\uc54a\uc744 \uc218 \uc5c6\uc5c8\ub2e4. \uc774 \uc5f0\uad6c\ub294 \ud55c\uad6d\ud658\uacbd\uc5f0\uad6c\uc6d0 \uc18c\uc18d \uc5f0\uad6c\uc790\uc774\ub2e4 \ubcf4\ub2c8 \ub370\uc774\ud130 \ubd84\uc11d\uc774\ub098 \ud604\uc7a5 \ucc38\uc5ec\uc5f0\uad6c \u000d\u000a\ubc29\ubc95\ub860 \uc801\uc6a9\uc5d0 \uc788\uc5b4\uc11c \ub3c5\ub9bd\uc5f0\uad6c\uc790\uc758 \uc5f0\uad6c\ubcf4\ub2e4 \uac15\uc810\uc744 \ubcf4\uc5ec\uc8fc\uace0 \uc788\ub2e4. \uc2ec\uc0ac\uacfc\uc815\uc5d0\uc11c \ub3c5\ub9bd\uc5f0\uad6c\uc790\ub4e4\u000d\u000a\uc774 \ub9ce\uc740 \ud604\uc7a5\uc5f0\uad6c \uc0c1\ud669\uc744 \uac10\uc548\ud558\uc5ec \uc0c1\ub300\uc801\uc73c\ub85c \uc5f0\uad6c\ud658\uacbd\uc774 \uc798 \uac16\ucd94\uc5b4\uc9c4 \uc5f0\uad6c\uacb0\uacfc\ubb3c\uc744 \uc5b4\ub5bb\uac8c \ud310\ub2e8\u000d\u000a\ud560 \uac83\uc778\uc9c0\ub97c \ub450\uace0 \uc2e0\uc911\ud558\uac8c \uac80\ud1a0\ud588\ub2e4. \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc5d0 \uad00\ud55c \uc9c0\uc5ed\uc0ac\ud68c\uc758 \ub300\uc751\uc5d0\uc11c \ubbfc\u000d\u000a\uad00\ud611\ub825\uacfc\uc815\uc744 \uccb4\uacc4\uc801\uc774\uace0, \uc2e4\ud6a8\uc131 \uc788\uac8c \ubd84\uc11d\ud55c \uc774 \uc5f0\uad6c\uc758 \ud604\uc7a5\uc131, \ub2f9\uc0ac\uc790\uc131, \ud65c\uc6a9\ub3c4 \uce21\uba74\uc744 \uc911\uc694\ud558\u000d\u000a\uac8c \uace0\ub824\ud588\uace0, \ud5a5\ud6c4 <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \uc120\uc815\uc5d0 \uc788\uc5b4\uc11c \uad6d\ucc45\uc5f0\uad6c\uae30\uad00\uc758 \uc5f0\uad6c\ub77c \ud558\ub354\ub77c\ub3c4 \ud604\uc7a5\uc5f0\u000d\u000a\uad6c\uc758 \uc694\uac74\uc744 \ucda9\uc2e4\ud788 \ubc18\uc601\ud558\uace0 \uc788\ub294 \uacbd\uc6b0\ub77c\uba74 \ud604\uc7a5\uc5f0\uad6c \uc8fc\uccb4\uc758 \uc678\uc5f0 \ud655\uc7a5 \ucc28\uc6d0\uc5d0\uc11c \uae0d\uc815\uc801\uc73c\ub85c \uace0\ub824\u000d\u000a\ud560 \ud544\uc694\uac00 \uc788\ub2e4\uace0 \ud310\ub2e8\ud588\ub2e4. \uc2ec\uc0ac\uc704\uc6d0\ud68c\ub294 \uc774 \uc5f0\uad6c\uac00 \ucda9\ubd84\ud788 \uc694\uac74\uc744 \ucda9\uc871\ud588\ub2e4\uace0 \ud310\ub2e8\ud558\uc600\uae30\uc5d0 \uc81c1\u000d\u000a\ud68c <\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c>\ub85c \uc120\uc815\ud588\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c "," & 84\u000d\u000a1. \ubb38\uc81c\uc758\uc2dd\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \uc804\ub77c\ubd81\ub3c4 \uc775\uc0b0\uc2dc \ud568\ub77c\uba74\uc5d0 \uc788\ub294 \uc7a5\uc810\ub9c8\uc744 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c \ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758\u000d\u000a\uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uc744 \ub450\uace0 \uad00\ub828 \ud589\uc704\uc790\ub4e4\uc774 \uc5b4\ub5bb\uac8c \ub300\uc751\ud558\uace0 \uc0c1\ud638\uc791\uc6a9\ud588\ub294\uc9c0 \uadf8\ub9ac\uace0 \uadf8\ub7ec\ud55c \u000d\u000a\ub300\uc751\uacfc \uc0c1\ud638\uc791\uc6a9\uc774 \ud53c\ud574\uc758 \uc778\uc815\uacfc\uc815\uc5d0 \ubbf8\uce5c \uc601\ud5a5\uc744 \ubc1d\ud600 \ubcf4\ub824 \ud55c\ub2e4. \uc7a5\uc810\ub9c8\uc744\uc740 \uc815\ubd80\uac00 \u2018\ud658\u000d\u000a\uacbd\uc624\uc5fc\uacfc \ube44\ud2b9\uc774\uc131 \uc9c8\ud658\uc778 \uc554 \ubc1c\ubcd1\u2019 \uc0ac\uc774\uc758 \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uacf5\uc2dd\uc801\uc73c\ub85c \uc778\uc815\ud55c \uad6d\ub0b4 \ucd5c\ucd08 \u000d\u000a\uc0ac\ub840\ub77c\ub294 \uc810\uc5d0\uc11c \uc8fc\ubaa9\ud560 \ub9cc\ud558\ub2e4. \uc7a5\uc810\ub9c8\uc744\uc740 \uc2e4\uc81c \uac70\uc8fc\uc790\uac00 100\uba85 \uc774\ub0b4\uc758 \ubcbc\ub18d\uc0ac \uc911\uc2ec\uc758 \u000d\u000a\uc791\uc740 \ub18d\ucd0c \ub9c8\uc744\uc774\ub2e4.<\uadf8\ub9bc. 1> \ucc38\uc870 2001\ub144 10\uc6d4, \ub9c8\u000d\u000a\uc744 \uc778\uadfc\uc5d0\uc11c \ube44\ub8cc\uc0dd\uc0b0 \uacf5\uc7a5\uc778 \u321c\uae08\uac15\ub18d\uc0b0\uc774 \uac00\u000d\u000a\ub3d9\uc744 \uc2dc\uc791\ud558\uc5ec 2017\ub144 4\uc6d4 \uacf5\uc7a5 \uac00\ub3d9\uc744 \uc911\ub2e8\ud560 \u000d\u000a\ub54c\uae4c\uc9c0 \ub9e4\uc5f0 \ubc0f \uc545\ucde8, \ud3d0\uc218\ub85c \uc778\ud55c \ud658\uacbd\uc624\uc5fc\uc744 \u000d\u000a\uc7a5\uae30\uac04 \uacbd\ud5d8\ud588\ub2e4. 2002\ub144 3\uc6d4 \uc775\uc0b0\ub0b4\uc77c\uc2e0\ubb38\uc5d0\u000d\u000a\uc11c \uc7a5\uc810\ub9c8\uc744 \ud658\uacbd\uc624\uc5fc \ubb38\uc81c\ub97c \ucd5c\ucd08\ub85c \ubcf4\ub3c4\ud558\uace0, \u000d\u000a\uacf5\uc7a5\uc758 \ud3d0\uc218 \ubc29\ub958\ub85c \uc778\ud574 \ub9c8\uc744 \uc800\uc218\uc9c0\uc758 \ubb3c\uace0\uae30 \u000d\u000a\uc9d1\ub2e8\ud3d0\uc0ac \uc0ac\uac74(2010, 2016\ub144)\uc774 \uc788\uc5c8\uc9c0\ub9cc, \uc9c0\uc5ed\uc0ac\ud68c\u000d\u000a\ub97c \ubc97\uc5b4\ub098\uc11c \uacf5\ub860\ud654\ub418\uc9c0\ub294 \ubabb\ud588\ub2e4. 2017\ub144 3\uc6d4\u000d\u000a\uacbd\ubd80\ud130 \uc911\uc559\uc5b8\ub860\uc744 \ud1b5\ud574 \ud658\uacbd\uc624\uc5fc\uc73c\ub85c \uc778\ud55c \uc9d1\u000d\u000a\ub2e8 \uc554 \ubc1c\ubcd1\uc774 \ubcf4\ub3c4\ub418\uba74\uc11c, \uc804\uad6d\uc801\uc778 \ud658\uacbd \ubc0f \ubcf4\u000d\u000a\uac74 \uc774\uc288\ub85c \ubd80\uc0c1\ud588\ub2e4.7)\u000d\u000a\uc804\uad6d\uc801\uc73c\ub85c \uad00\uc2ec\uc744 \ub04c\uac8c \ub420 \ubb34\ub835\uc778 2017\ub144 3\uc6d4, \uc9c0\ubc29\uc815\ubd80\uc640 \uc7a5\uc810\ub9c8\uc744 \ud53c\ud574 \uc8fc\ubbfc, \uc9c0\uc5ed\u000d\u000aNGO, \uad00\ub828 \uc804\ubb38\uac00\ub4e4\uc774 \ucc38\uc5ec\ud558\ub294 \u2018\uc775\uc0b0 \uc7a5\uc810\ub9c8\uc744 \ud658\uacbd\ube44\uc0c1\ub300\ucc45 \ubbfc\uad00\ud611\uc758\uccb4\u2019(\uc774\ud558, \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\u000d\u000a\ud611\uc758\uccb4)\ub77c\ub294 \uac70\ubc84\ub10c\uc2a4governance\uac00 \ub9cc\ub4e4\uc5b4\uc84c\ub2e4. \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uc758 \ub17c\uc758\ub97c \uac78\uccd0 \ud658\uacbd\ubd80\uc5d0 \u000d\u000a\uc8fc\ubbfc\uac74\uac15\uc5ed\ud559\uc870\uc0ac\ub97c \uccad\uc6d0\ud588\uc73c\uba70 \ud658\uacbd\ubd80\ub294 \uc774\ub97c \uc218\uc6a9\ud558\uc5ec \uc5ed\ud559\uc870\uc0ac\ub97c \uc2e4\uc2dc\ud558\uac8c \ub41c\ub2e4.(2017\ub144\u000d\u000a12\uc6d4) \uadf8\ub9ac\uace0 \uc5ed\ud559\uc870\uc0ac \uacb0\uacfc\ub85c \ud658\uacbd\ubd80\ub294 \uae08\uac15\ub18d\uc0b0\uc5d0 \uc758\ud55c \ud658\uacbd\uc624\uc5fc\uacfc \uc554 \ubc1c\ubcd1 \uc0ac\uc774\uc758 \uc5ed\ud559\uc801 \u000d\u000a\uad00\ub828\uc131\uc744 \uc778\uc815\ud558\ub294 \ucd5c\uc885 \uacb0\uacfc\ub97c \ubc1c\ud45c\ud558\uac8c \ub41c\ub2e4.(2019\ub144 11\uc6d4) \ud2b9\ud788 \uc720\uae30\uc9c8 \ube44\ub8cc\uc758 \uc6d0\ub8cc\ub85c \uc0ac\uc6a9\u000d\u000a7\u3000\ub274\uc2a4 \ube45\ub370\uc774\ud130 \ubd84\uc11d \uc0ac\uc774\ud2b8\uc778 \ube45\uce74\uc778\uc988 (Big Kinds) \ub97c \uac80\uc0c9\ud55c \uacb0\uacfc , 2017 \ub144\ubd80\ud130 \uc7a5\uc810\ub9c8\uc744 \uad00\ub828 \uae30\uc0ac\uac00 \ud070 \ud3ed\u000d\u000a\uc73c\ub85c \uc99d\uac00\ud588\ub2e4 ( \uac80\uc0c9\uc5b4 : \uc7a5\uc810\ub9c8\uc744 , \uac80\uc0c9 \uae30\uac04 : 2010.1.1.~2020.7.30.). 2017 \ub144 \uc804\uc5d0\ub294 \uc804\ubd81 \uc9c0\uc5ed \uc5b8\ub860\uc5d0\uc11c \uc81c\u000d\u000a\uacf5\ud55c \ub2e8\uc9c0 5 \uac74\uc758 \uae30\uc0ac\ub9cc \uac80\uc0c9\ub418\uc5c8\uc9c0\ub9cc , 2017 \ub144 75 \uac74\uc73c\ub85c \ub9ce\uc774 \ub298\uc5b4\ub098\uae30 \uc2dc\uc791\ud558\uc5ec 2019 \ub144\uc5d0\ub294 \ucd1d 338 \uac74\uae4c\uc9c0 \u000d\u000a\uac80\uc0c9\ub418\uc5c8\ub2e4 .\u000d\u000a<\uadf8\ub9bc 1> \uc7a5\uc810\ub9c8\uc744 \uc9c0\ud615\uacbd\uad00","84 85\u000d\u000a\ud55c \uc5f0\ucd08\ubc15(\u7159\u8349\u7c95)8)\uc758 \uac74\uc870\uacfc\uc815\uc5d0\uc11c \ubc1c\uc0dd\ud55c \u2018\ub2f4\ubc30\ud2b9\ud788\ub2c8\ud2b8\ub85c\uc0ac\ubbfc\u2019Tobacco-specific nitrosamines,\u000d\u000a\uc774\ud558 TSNAs\uc774 \ub300\uae30 \uc911\uc5d0 \ubc30\ucd9c\u00b7\ud761\uc785\ub418\uc5b4 \uc8fc\ubbfc\ub4e4\uc758 \uc554\uc744 \uc720\ubc1c\ud55c \uac83\uc73c\ub85c \ud2b9\uc815\ud588\ub2e4.(\ud658\uacbd\uc548\uc804\uac74\uac15\uc5f0\uad6c\u000d\u000a\uc18c, 2018: 620)\uc5ed\ud559\uc870\uc0ac \uacb0\uacfc\uc5d0 \uc758\ud558\uba74 \uc54c \ubc1c\ubcd1 \uc0ac\ub9dd\uc790 16\uba85, \ud22c\ubcd1\uc790 11\uba85\uc73c\ub85c \ucd1d 26\uba85\uc5d0 \uc774\ub978\u000d\u000a\ub2e4. \uc554 \uc774\uc678\uc5d0\ub3c4 \uc8fc\ubbfc\ub4e4\uc740 \uac01\uc885 \ud53c\ubd80\uc9c8\ud658\uc744 \ud638\uc18c\ud588\ub2e4.(\ud658\uacbd\uc548\uc804\uac74\uac15\uc5f0\uad6c\uc18c, 2018: 199) \uc774 \uc5f0\uad6c\ub294 \uc7a5\uc810\u000d\u000a\ub9c8\uc744 \uc0ac\ub840\ub97c \ud1b5\ud574 \ud53c\ud574\uc758 \uc778\uc815\uacfc\uc815\uc744 \ub458\ub7ec\uc2fc \ud589\uc704\uc790\ub4e4\uc758 \uc0c1\ud638\uc791\uc6a9\uacfc \uc774\ub7ec\ud55c \uc0c1\ud638\uc791\uc6a9\uc774 \u000d\u000a\ud53c\ud574\uc778\uc815\uc5d0 \uc5b4\ub5a0\ud55c \uc601\ud5a5\uc744 \ubbf8\ucce4\ub294\uc9c0\ub97c \ud655\uc778\ud558\ub294 \uc791\uc5c5\uc774\ub77c\uace0 \ud560 \uc218 \uc788\ub2e4. \ud658\uacbd\uc624\uc5fc\uacfc \uc9c8\ubcd1 \u000d\u000a\uc0ac\uc774\uc758 \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uc778\uc815\ubc1b\ub294 \uac83\uc774 \ud604\uc2e4\uc801\uc73c\ub85c \ud798\ub4e0 \uc0c1\ud669 \uc18d\uc5d0\uc11c \uc7a5\uc810\ub9c8\uc744 \uc0ac\ub840\ub294 \ub3c5\u000d\u000a\ud2b9\ud55c \uc704\uce58\ub97c \uc810\ud558\uace0 \uc788\ub2e4.\u000d\u000a2. \uc0ac\uace0\uc758 \uc804\uac1c\uc640 \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751\u000d\u000a\uadfc 17\ub144\uac04 \uc9c4\ud589\ub41c \uc7a5\uc810\ub9c8\uc744 \ud658\uacbd\uc624\uc5fc \uc0ac\uace0\uc758 \uc9c4\ud589 \uacfc\uc815\uc740 4\ub2e8\uacc4\ub85c \uad6c\ubd84\ud574 \ubcfc \uc218 \uc788\ub2e4.\u000d\u000a<\uadf8\ub9bc. 2> \ucc38\uc870 \uba3c\uc800 1\ub2e8\uacc4\ub294 \u2018\ud658\uacbd\uc624\uc5fc\ud53c\ud574 \ub178\ucd9c \ub2e8\uacc4\u2019\ub85c \uae08\uac15\ub18d\uc0b0\uc758 \uac00\ub3d9\uc73c\ub85c \ud658\uacbd\ubb38\uc81c\uac00 \ubc1c\uc0dd\u000d\u000a\ud558\uace0 \uac74\uac15\ud53c\ud574\uac00 \ud45c\uba74\ud654\ub41c \uc2dc\uae30\ub77c\uace0 \ud560 \uc218 \uc788\ub2e4. \uadf8\ub9ac\uace0 2\ub2e8\uacc4\ub294 \u2018\uacf5\ub860\ud654 \ub2e8\uacc4\u2019\ub85c \uc7a5\uc810\ub9c8\u000d\u000a\uc744 \ubb38\uc81c\uac00 \uc881\uac8c\ub294 \uc9c0\uc5ed\uc0ac\ud68c(\uc775\uc0b0\uacfc \uc804\ubd81), \ub113\uac8c\ub294 \uc804\uad6d\uc801\uc73c\ub85c \uc911\uc694\ud55c \ud658\uacbd \uc774\uc288\ub85c \ubd80\uc0c1\ud55c \uc2dc\uae30\u000d\u000a\ub2e4. 3\ub2e8\uacc4\ub294 \ud658\uacbd\ubd80\uac00 \uc8fc\ubbfc\uccad\uc6d0\uc744 \uc218\uc6a9\ud558\uc5ec \uc8fc\ubbfc\uac74\uac15\uc601\ud5a5 \uc870\uc0ac\ub97c \uc2dc\ud589\ud55c \u2018\uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\uc870\u000d\u000a\uc0ac \ub2e8\uacc4\u2019, \ub05d\uc73c\ub85c 4\ub2e8\uacc4\ub294 \u2018\uc0ac\ud6c4\ub300\ucc45\ub2e8\uacc4\u2019\ub85c \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc758 \uac74\uac15, \uc0b6 \ubc0f \uacf5\ub3d9\uccb4 \ubcf5\uc6d0\uc744 \uc704\u000d\u000a\ud55c \uc815\ubd80\uc758 \ubcf5\uad6c\uc815\ucc45\uc774 \ubcf8\uaca9\ud654\ub41c \uc2dc\uae30\ub77c\uace0 \ud560 \uc218 \uc788\ub2e4.\u000d\u000a8\u3000\uc5f0\ucd08\ubc15\uc740 \ub2f4\ubc30\ub97c \uc758\ubbf8\ud558\ub294 \u2018\uc5f0\ucd08\u2019 ( \u71c3\u8349 ) \uc640 \uc220\uc744 \uc9dc\uace0 \ub0a8\uc740 \ucc0c\uaebc\uae30\ub97c \uc758\ubbf8\ud558\ub294 \u2018\ubc15\u2019 ( \u7c95 ) \uc744 \uacb0\ud569\ud55c \ud55c\uc790\uc5b4\ub85c \u000d\u000a\ub2f4\ubc30\ub97c \ub9cc\ub4e4\uace0 \ub0a8\uc740 \ub2f4\ubc43\uc78e \ucc0c\uaebc\uae30\ub97c \uac00\ub9ac\ud0a8\ub2e4 . \uac04\ub2e8\ud558\uac8c \ub9d0\ud574 \ub2f4\ubc30\ub97c \uc0dd\uc0b0\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \ubc1c\uc0dd\ud558\ub294 \ud3d0\uae30\ubb3c\uc774\ub77c\uace0 \ud560 \u000d\u000a\uc218 \uc788\ub2e4 . \ub530\ub77c\uc11c \uc5f0\uc18c\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \ub2f4\ubc30\uc5d0\uc11c\uc640 \uac19\uc740 \ub2e4\ub7c9\uc758 \ubc1c\uc554\ubb3c\uc9c8\uc744 \ubc30\ucd9c\ud558\uac8c \ub41c\ub2e4 .\u000d\u000a<\uadf8\ub9bc 2> \uc7a5\uc810\ub9c8\uc744 \uc0ac\uace0\uc758 \uc804\uac1c \uacfc\uc815\u000d\u000a2001.10\u000d\u000a\u2022 \uae08\uac15\ub18d\uc0b0 \uacf5\uc7a5\uac00\ub3d9\u000d\u000a2007.2\u000d\u000a\u2022 \uc9c0\uc5edNGO\uac00 \ud53c\ud574\uc8fc\ubbfc\uacfc \uc5f0\ub300\u000d\u000a\u2022 \uc5b8\ub860\ubcf4\ub3c4 \uc99d\uac00 \uc2dc\uc810\u000d\u000a2017.7\u000d\u000a\u2022 \ud658\uacbd\ubd80, \uac74\uac15\uc601\ud5a5\ud3c9\uac00\u000d\u000a\uc2dc\ud589\uacb0\uc815\u000d\u000a2019.11\u000d\u000a\u2022 \ud658\uacbd\ubd80, \uac74\uac15\uc601\ud5a5\ud3c9\uac00\u000d\u000a\uacb0\uacfc \ubc1c\ud45c\u000d\u000a1\ub2e8\uacc4\u000d\u000a\ud658\uacbd\uc624\uc5fc \ud53c\ud574 \ub178\ucd9c \ub2e8\uacc4\u000d\u000a2\ub2e8\uacc4\u000d\u000a\ud658\uacbd\uc624\uc5fc \ud53c\ud574 \uacf5\ub860\ud654 \ub2e8\uacc4\u000d\u000a4\ub2e8\uacc4\u000d\u000a\uc0ac\ud6c4\ub300\ucc45\ub2e8\uacc4\u000d\u000a3\ub2e8\uacc4\u000d\u000a\uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\uc870\uc0ac \ub2e8\uacc4\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c "," & 86\u000d\u000a1) \ud658\uacbd\uc624\uc5fc\ud53c\ud574 \ub178\ucd9c \ub2e8\uacc4: 2001\ub144 10\uc6d4~2017\ub144 2\uc6d4\u000d\u000a\uc774 \uc2dc\uae30 \uc8fc\uc694\ud55c \ud589\uc704\uc790\ub294 \ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc5d0 \ub178\ucd9c\ub41c \uc7a5\uc810\ub9c8\uc744 \ud53c\ud574 \uc8fc\ubbfc\uc774\ub2e4. \ub2f9\uc2dc \uc7a5\uc810\ub9c8\u000d\u000a\uc744 \uc8fc\ubbfc\ub4e4\uc740 \uc775\uc0b0\uc2dc\ub97c \uc0c1\ub300\ub85c \ubbfc\uc6d0\uc744 \uc81c\uae30\ud558\uace0 \ube44\ub8cc\uacf5\uc7a5\uc778 \uae08\uac15\ub18d\uc0b0\uc744 \uc0c1\ub300\ub85c\ub294 \uc2dc\uc704 \ub4f1\u000d\u000a\uc758 \uc9c1\uc811\ud589\ub3d9\uc73c\ub85c \ub300\uc751\ud588\ub2e4. \uc8fc\ubbfc\ub4e4\uc740 \uae08\uac15\ub18d\uc0b0\uc774 \ub4e4\uc5b4\uc628 \uc774\ud6c4\ubd80\ud130 \ub9e4\uc5f0, \uc545\ucde8, \ud3d0\uc218\uc5d0 \ub300\u000d\u000a\ud55c \ubbfc\uc6d0\uc744 \uc775\uc0b0\uc2dc\uc5d0 \uc5ec\ub7ec \ucc28\ub840 \uc81c\uae30\ud588\uc5c8\ub2e4. \uc775\uc0b0\uc2dc \ub610\ud55c \ubb38\uc81c\uc758 \uc2ec\uac01\uc131\uc744 \uc778\uc9c0\ud558\uace0, \uae08\uac15\u000d\u000a\ub18d\uc0b0 \uce21\uc5d0 \uc545\ucde8 \uc800\uac10 \uc2dc\uc124 \uc124\uce58\ub97c \uc694\uad6c\ud558\uace0 \uc9c0\uc18d\uc801\uc778 \ud589\uc815\uc9c0\ub3c4\ub97c \ud55c \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4. \ud558\uc9c0\u000d\u000a\ub9cc 2017\ub144 4\uc6d4 \ubb34\ud5c8\uac00 \ub300\uae30\ubc30\ucd9c\uc2dc\uc124 \ud3d0\uc1c4 \uba85\ub839\uc73c\ub85c \uae08\uac15\ub18d\uc0b0\uc774 \uac00\ub3d9\uc744 \uc911\ub2e8\ub420 \ub54c\uae4c\uc9c0 \uc545\u000d\u000a\ucde8 \ubb38\uc81c\ub294 \ud574\uacb0\ub418\uc9c0 \uc54a\uc558\ub2e4. \uc7a5\uae30\uac04 \uc545\ucde8 \ubb38\uc81c\uac00 \ud574\uacb0\ub418\uc9c0 \ubabb\ud55c \uac83\uc740 \uc775\uc0b0\uc2dc\uc758 \ud0dc\ub3c4\uac00 \uc5bc\u000d\u000a\ub9c8\ub098 \ubbf8\uc628\uc801\uc774\uc5c8\ub294\uc9c0\ub97c \ubcf4\uc5ec\uc900\ub2e4. \ub2f9\uc2dc \uc9c0\ubc29\uc815\ubd80\ub97c \ud1b5\ud55c \uc81c\ub3c4\uc801 \ud574\uacb0 \ubc29\ubc95\uc744 \ucc3e\uc9c0 \ubabb\ud55c \uc0c1\u000d\u000a\ud669\uc5d0\uc11c \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc774 \uc120\ud0dd\ud560 \uc218 \uc788\ub294 \ub2e4\ub978 \ubc29\ubc95\uc740 \uae08\uac15\ub18d\uc0b0\uc744 \uc0c1\ub300\ub85c \ud55c \uc9c1\uc811\ud589\ub3d9\uc774\uc5c8\ub2e4. \u000d\u000a\ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc740 \uc554\uc73c\ub85c \uc0ac\ub9dd\uc790\uac00 \ub298\uc5b4\ub098\uba74\uc11c \uc775\uc0b0\uc2dc\uccad\uc5d0 \uac00\uc11c \ud56d\uc758\ub3c4 \ud558\uace0, \uacf5\uc7a5\uc73c\ub85c \uc62c\ub77c\u000d\u000a\uac00\ub294 \uae38\uc744 \ub9c9\uace0 \uc2dc\uc704\ub97c \ud574 \ubcf4\uc558\uc9c0\ub9cc \ud2b9\ubcc4\ud55c \uc131\uacfc\ub97c \uc5bb\uc9c0 \ubabb\ud588\ub2e4.\u000d\u000a\ud658\uacbd\uc624\uc5fc\uc758 \uc8fc\ubc94\uc73c\ub85c \uc9c0\ubaa9\ubc1b\uc558\ub358 \uae08\uac15\ub18d\uc0b0\uc740 \uc77c\uad00\ub418\uac8c \uc8fc\ubbfc\ub4e4\uc758 \uc694\uad6c\ub97c \uc678\uba74\ud558\ub294 \uacac\ud574\u000d\u000a\ub97c \ubc1d\ud614\ub2e4. \ube44\ub8cc\uacf5\uc7a5\uc774 \ub4e4\uc5b4\uc628\ub2e4\ub294 \uc18c\uc2dd\uc744 \ub4e3\uace0 \ub2f9\uc2dc \ub9c8\uc744 \uc774\uc7a5\uc774 \uae08\uac15\ub18d\uc0b0 \ub300\ud45c\ub97c \ub9cc\ub098 \u000d\u000a\uc8fc\ubbfc\ub4e4\uc744 \uc0c1\ub300\ub85c \ud55c \uc124\uba85\ud68c\ub97c \uc694\uccad\ud588\ub2e4\uace0 \ud55c\ub2e4. \ub2f9\uc2dc \uc124\uba85\ud68c\uc5d0 \ucc38\uc11d\ud55c \uae08\uac15\ub18d\uc0b0 \ub300\ud45c\ub294 \uc774 \u000d\u000a\uc77c\uc740 \uc8fc\ubbfc\uc5d0\uac8c \ud5c8\ub77d\ubc1b\uc744 \ud544\uc694\uac00 \uc5c6\ub2e4\ub294 \ud0dc\ub3c4\ub9cc\uc744 \uac04\ub7b5\ud558\uac8c \ud45c\uba85\ud588\ub2e4. \uc774\ud6c4 \uc624\uc5fc \ubb38\uc81c\uac00 \uc2ec\u000d\u000a\uac01\ud574\uc9c0\uba74\uc11c \uc8fc\ubbfc\ub4e4\uc740 \uacf5\uc7a5\uc73c\ub85c \uc62c\ub77c\uac00\ub294 \uae38\uc744 \uac00\ub85c\ub9c9\ub294 \uc2dc\uc704\ub97c \ud588\uace0, \uae08\uac15\ub18d\uc0b0 \ub300\ud45c\ub294 \uc2dc\u000d\u000a\uc704\ub97c \uc8fc\ub3c4\ud55c \uc8fc\ubbfc\uc744 \uac80\ucc30\uc5d0 \uace0\ubc1c\ud588\ub2e4.(\uc7a5\uc77c\ud638\u00b7\ub098\uacbd\ud76c\u00b7\uc774\uba85\uc775, 2020: 17) \uc989 \uac00\ud574 \uae30\uc5c5\uc778 \uae08\uac15\ub18d\uc0b0\uc740 \ubc95\u000d\u000a\uc801\uc778 \uc815\ub2f9\uc131\uc744 \ub0b4\uc138\uc6cc \uc8fc\ubbfc\ub4e4\uc758 \ud53c\ud574\ub97c \uc678\uba74\ud558\ub294 \uacac\ud574\ub97c \uace0\uc218\ud588\ub2e4.\u000d\u000a \ud658\uacbd\uc624\uc5fc\ud53c\ud574 \ub178\ucd9c \uc2dc\uae30\uc5d0\ub294 \ud53c\ud574 \uc8fc\ubbfc, \uc9c0\ubc29\uc815\ubd80, \uae08\uac15\ub18d\uc0b0 \ub4f1\uc774 \uc8fc\uc694\ud55c \ud589\uc704\uc790\ub85c \ub4f1\u000d\u000a\uc7a5\ud588\ub2e4. \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc740 \ubbfc\uc6d0\uc774\ub77c\ub294 \uc81c\ub3c4\uc801 \uc808\ucc28\uc640 \uc2dc\uc704\ub77c\ub294 \uc9c1\uc811\ud589\ub3d9\uc73c\ub85c \ubb38\uc81c\uc5d0 \ub300\uc751\ud588\u000d\u000a\ub2e4. \ud558\uc9c0\ub9cc \uc9c0\ubc29\uc815\ubd80\uc640 \uae08\uac15\ub18d\uc0b0\uc758 \ubbf8\uc628\uc801\uc778 \ud0dc\ub3c4\ub85c \uc6d0\ud558\ub294 \uc131\uacfc\ub97c \uc5bb\uc5b4\ub0bc \uc218\uac00 \uc5c6\uc5c8\ub2e4. \u000d\u000a\uc8fc\ubbfc\ub4e4\uc740 \uc554 \ubc1c\ubcd1\uacfc \uae08\uac15\ub18d\uc0b0 \uc0ac\uc774\uc758 \uc5f0\uad00\uc131\uc774 \uc788\uc744 \uac83\uc774\ub77c\ub294 \uc810\uc744 \ud658\uacbd\uc624\uc5fc\uc5d0 \ub300\ud55c \uc9c1\uc811\u000d\u000a\uc801\uc778 \uccb4\ud5d8\uc744 \ud1b5\ud574 \ud655\uc2e0\ud558\uace0 \uc788\uc5c8\uc9c0\ub9cc, \uc774\ub97c \uacfc\ud559\uc801\uc73c\ub85c \uc124\uba85\ud560 \uc218 \uc788\ub294 \uc804\ubb38\uc9c0\uc2dd\uc740 \ubd80\uc871\u000d\u000a\ud588\ub2e4. \ub530\ub77c\uc11c \ubbfc\uc6d0\uc740 \ub2e8\uc21c\ud55c \uc545\ucde8 \ubb38\uc81c\ub85c \ucd95\uc18c\ub418\uc5c8\uc73c\uba70 \uc2dc\uc704 \ub610\ud55c \ud53c\ud574 \uc8fc\ubbfc\uc744 \uc911\uc2ec\uc73c\ub85c \u000d\u000a\uc0b0\ubc1c\uc801\uc73c\ub85c \uc9c4\ud589\ub418\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uc815\uce58\uc0ac\ud68c\uc801 \ud30c\uae09\ub825\uc744 \uac16\uc9c0 \ubabb\ud588\ub2e4. \uc989 \uc774 \uc2dc\uae30\uc5d0\ub294 \uc2ec\uac01\ud55c \u000d\u000a\uc0dd\uba85 \ubc0f \uac74\uac15\ud53c\ud574\ub97c \ud3ec\ud568\ud574 \ub2e4\uc591\ud55c \uc624\uc5fc\ud53c\ud574\uac00 \ubc1c\uc0dd\ud588\uc74c\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0, \ud53c\ud574 \uc8fc\ubbfc\ub9cc\uc758 \ubb38\u000d\u000a\uc81c\ub85c \uac07\ud600 \uc788\uc5c8\ub2e4.","86 87\u000d\u000a2) \ud658\uacbd\uc624\uc5fc\ud53c\ud574 \uacf5\ub860\ud654 \ub2e8\uacc4: 2017\ub144 2\uc6d4~2017\ub144 7\uc6d4\u000d\u000a\uc55e\uc11c \uc0b4\ud3b4\ubcf8 \ud658\uacbd\ud53c\ud574 \ub178\ucd9c \ub2e8\uacc4\uc5d0\uc11c \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc774 \uc775\uc0b0\uc2dc\uc5d0 \ubbfc\uc6d0\ub3c4 \uc81c\uae30\ud558\uace0 \uc5ec\ub7ec \ucc28\u000d\u000a\ub840 \ud56d\uc758 \uc2dc\uc704\ub3c4 \ud574 \ubcf4\uc558\uc9c0\ub9cc, \uc7a5\uc810\ub9c8\uc744 \ubb38\uc81c\ub294 \ud53c\ud574\uc790(\ub9c8\uc744 \uc8fc\ubbfc)\ub4e4\ub9cc\uc758 \ubb38\uc81c\ub85c \ud55c\uc815\ub41c \uce21\uba74\uc774 \u000d\u000a\uac15\ud588\ub2e4. \ud558\uc9c0\ub9cc \uc9c0\uc5edNGO \ubc0f \uad00\ub828 \uc804\ubb38\uac00\uc640 \ud53c\ud574 \uc8fc\ubbfc\ub4e4 \uc0ac\uc774\uc758 \uc5f0\ub300, \uadf8\ub9ac\uace0 \uc5b8\ub860\ubcf4\ub3c4\uac00 \u000d\u000a\ud655\ub300\ub418\uba74\uc11c, \uc775\uc0b0 \uc9c0\uc5ed\uc0ac\ud68c\ub294 \ubb3c\ub860 \uc804\uad6d\uc801 \uc218\uc900\uc758 \ubb38\uc81c\ub85c \ubd80\uc0c1\ud558\ub294 \uacf5\ub860\ud654 \ub2e8\uacc4\ub85c \uc804\ud658\ub418\u000d\u000a\uc5c8\ub2e4. NGO\uc640 \uc804\ubb38\uac00\uc758 \uac1c\uc785\uc73c\ub85c \uc7a5\uc810\ub9c8\uc744 \uc8fc\ubbfc\ub4e4\uc740 \uc774\uc804\uc5d0 \uc790\uc2e0\ub4e4\uc774 \ub3d9\uc6d0\ud558\uae30 \ud798\ub4e0 \uc9c0\u000d\u000a\uc2dd\uacfc \uc815\ubcf4 \uadf8\ub9ac\uace0 \ud655\uc7a5\ub41c \ub124\ud2b8\uc6cc\ud06c\ub97c \ud1b5\ud574\uc11c \uc0c8\ub85c\uc6b4 \uc778\uc801\u00b7\ubb3c\uc801 \uc790\uc6d0\uc744 \uc9c0\uc6d0\ubc1b\uc558\ub2e4. \uc774 \ub2e8\u000d\u000a\uacc4\ubd80\ud130 \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc740 \uc790\uc2e0\ub4e4\uc758 \ubaa9\uc18c\ub9ac\ub97c \uc804\ubb38\uc131\uacfc \uacb0\ud569\ud560 \uc218 \uc788\uac8c \ub418\uc5c8\uace0 \uc0ac\ud68c\uc815\uce58\uc801 \uc601\u000d\u000a\ud5a5\ub825 \ub610\ud55c \ud06c\uac8c \ud655\uc7a5\ub418\uc5c8\ub2e4.\u000d\u000a\uc775\uc0b0\uc2dc\ub294 \uc778\uad6c\ub294 30\ub9cc \uaddc\ubaa8\uc758 \ub3c4\uc2dc\ub85c \uc6d0\uad11\ub300\ud559\uad50\uc640 \uc6d0\uad11\ubcf4\uac74\ub300\ud559, \uc778\uadfc\uc758 \uc804\ubd81\ub300\ud559\uad50(\u000d\u000a\uc804\uc8fc\uc2dc) \ub4f1 \uc804\ubd81 \uc9c0\uc5ed\uc758 \uaddc\ubaa8 \uc788\ub294 \ub300\ud559\ub4e4\uc774 \uc788\ub2e4. \ub530\ub77c\uc11c 1990\ub144\ub300\ubd80\ud130 \uc9c0\uc5ed \ub300\ud559 \ucd9c\uc2e0\ub4e4\u000d\u000a\uc774 \uad6c\ucd95\ud574 \uc628 \uc9c0\uc5edNGO\uc640 \uc2dc\ubbfc\uc6b4\ub3d9\uc5d0 \ub300\ud55c \uacbd\ud5d8\uacfc \ub3d9\uc6d0\ud560 \uc218 \uc788\ub294 \uc804\ubb38\uac00\ub97c \ubcf4\uc720\ud558\uace0 \uc788\u000d\u000a\uc5c8\ub2e4. 2017\ub144 3\uc6d4 \ucd08 \u2018\uc88b\uc740\uc815\uce58\uc2dc\ubbfc\ub137\u2019, \u2018\uc775\uc0b0\ud658\uacbd\uc6b4\ub3d9\uc5f0\ud569\u2019, \u2018\uc775\uc0b0\ucc38\uc5ec\uc5f0\ub300\u2019 \ub4f1 \uc775\uc0b0 \uc9c0\uc5ed\u000d\u000aNGO, \uc9c0\uc5ed \ub300\ud559\uc758 \ud658\uacbd \uad00\ub828 \uad50\uc218, \uc775\uc0b0\uc2dc \uc758\uc6d0 \ub4f1\uc774 \uae08\uac15\ub18d\uc0b0\uc744 \ubc29\ubb38\ud558\uace0 \uc7a5\uc810\ub9c8\uc744 \uc8fc\u000d\u000a\ubbfc\ub300\ud45c\uc758 \uc758\uacac\uc744 \uccad\ucde8\ud588\ub2e4.\u000d\u000a\ubc29\ubb38 \uc9c1\ud6c4 \ubb38\uc81c\uc758 \uc2ec\uac01\uc131\uc744 \uc778\uc9c0\ud558\uace0 \uc775\uc0b0\uc9c0\uc5ed NGO\uc778 \uc88b\uc740\uc815\uce58\uc2dc\ubbfc\ub137\uc758 \uc8fc\uad00\uc73c\ub85c \u2018\ud568\u000d\u000a\ub77c \uc7a5\uc810\ub9c8\uc744 \uad00\ub828 \uae34\uae09\ud1a0\ub860\ud68c\u2019(2017\ub144 3\uc6d4)\ub97c \uac1c\ucd5c\ud558\uba74\uc11c \uc9c0\uc5ed\uc0ac\ud68c\uc758 \uc8fc\uc694\ud55c \ud658\uacbd\ubb38\uc81c\ub85c \ubd80\uc0c1\u000d\u000a\ud558\uae30 \uc2dc\uc791\ud588\ub2e4. \ub2f9\uc2dc \uc7a5\uc810\ub9c8\uc744 \ubb38\uc81c\ub97c \ubd80\uac01\ud558\uac8c \uc2dc\ud0a4\ub294 \ub370 \uc911\uc694\ud55c \uc5ed\ud560\uc744 \ud588\ub358 \uc88b\uc740\uc815\uce58\uc2dc\u000d\u000a\ubbfc\ub137\uc758 B\ub300\ud45c\ub294 \uc9c0\uc5ed\uc5d0\uc11c \ub300\ud559\uc744 \uc878\uc5c5\ud55c \ud6c4 \uc775\uc0b0\ucc38\uc5ec\uc5f0\ub300 \uc0ac\ubb34\ucc98\uc7a5, \ubb34\uc18c\uc18d\uc73c\ub85c \uc775\uc0b0 \uc2dc\u000d\u000a\uc758\uc6d0\uc744 3\uc120\uae4c\uc9c0 \ud55c \uc775\uc0b0\uc2dc\uc758 \ub300\ud45c\uc801\uc778 \uc2dc\ubbfc\uc6b4\ub3d9\uac00\uc774\uc790 \ud480\ubfcc\ub9ac \uc0dd\ud65c \uc815\uce58\uc778\uc774\uc5c8\ub2e4. \ub530\ub77c\uc11c\u000d\u000a\uc9c0\uc5ed\ubbfc\uc758 \uc815\uc11c\ubfd0 \uc544\ub2c8\ub77c \uc9c0\uc5edNGO \ubc0f \uad00\ub828 \uc804\ubb38\uac00\uc640\uc758 \ub124\ud2b8\uc6cc\ud06c, \uadf8\ub9ac\uace0 \ubc95\uacfc \uc81c\ub3c4\uc758 \ud65c\u000d\u000a\uc6a9\ubc29\uc548\uc744 \uc798 \uc54c\uace0 \uc788\uc5c8\ub2e4.\u000d\u000a\uc774 \uc2dc\uae30 \uc9c0\uc5edNGO \ubc0f \uc804\ubb38\uac00\uc758 \uc8fc\uc694\ud55c \uc5ed\ud560\uc774\uc790 \uc131\uacfc\ub294 \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\ub77c\ub294 \uac70\ubc84\u000d\u000a\ub10c\uc2a4\ub97c \uc775\uc0b0\uc2dc\uc5d0 \uc694\uccad\ud558\uc5ec \uad6c\uc131\ud55c \uac83\uc774\ub2e4. \uc774 \ubbfc\uad00\ud611\uc758\uccb4\ub294 \uc774\ud6c4 \uc9c4\ud589\ub420 \uc8fc\ubbfc\uac74\uac15\uc5ed\ud559\uc870\u000d\u000a\uc0ac\ub294 \ubb3c\ub860 \ud53c\ud574\uc9c0\uc5ed \ubcf5\uad6c\ub97c \uc704\ud55c \uc0ac\ud6c4\uc9c0\uc6d0 \ub300\ucc45 \ub9c8\ub828\uc5d0\uc11c\ub3c4 \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc758 \uc0dd\uac01\uc744 \ubc18\uc601\ud558\u000d\u000a\ub294 \uc911\uc694\ud55c \ucc3d\uad6c \uae30\ub2a5\uc744 \uc218\ud589\ud558\uac8c \ub41c\ub2e4. \ubbfc\uac04\ud611\uc758\uccb4\uc758 \ubbfc\uac04\uc704\uc6d0 \uad6c\uc131\uc744 \ub450\uace0 \uc775\uc0b0\uc2dc\uc640 \uac08\ub4f1\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c "," & 88\u000d\u000a\uc774 \uc788\uc5c8\uc9c0\ub9cc, \uc9c0\uc5edNGO\uc640 \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc758 \uc694\uad6c\uac00 \ubc18\uc601\ub418\ub294 \ubc29\ud5a5\uc73c\ub85c \ubbfc\uac04\uc704\uc6d0\uc774 \uad6c\uc131\ub418\uc5c8\u000d\u000a\ub2e4. \uadf8 \uacb0\uacfc \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\ub294 \u2018\ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc744 \uc9c0\uc6d0\ud558\uae30 \uc704\ud55c \uc0ac\ud68c\uc801 \uc870\uc815\uae30\uad6c\u2019\uc758 \uc704\u000d\u000a\uc0c1\uc744 \uac16\uace0 \ucd9c\ubc1c\ud560 \uc218 \uc788\uac8c \ub418\uc5c8\ub2e4.\u000d\u000a\u201c(\uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4 \ubbfc\uac04)\uc704\uc6d0\uc744 \uad6c\uc131\ud560 \ub54c \uc120\uc218\ub97c \uc5b4\ub5bb\uac8c \ud560 \uac83\uc774\ub0d0\uace0 \ud560 \ub54c \ud589\u000d\u000a\uc815(\uc775\uc0b0\uc2dc)\uc5d0\uc11c\ub294 \u25cb\u25cb\u25cb\uc774\ub098 \u25cb\u25cb\u25cb\uc774 \ub4e4\uc5b4\uc624\ub294 \uac83\uc744 \ubd80\ub2f4\uc2a4\ub7ec\uc6cc\ud574\uc11c \uad6c\uc131\uc774 \uc880 \ub2a6\uc5b4\uc84c\u000d\u000a\ub2e4\uace0 \uc598\uae30\ub97c \ud574\uc694. \uc800\ud76c\uac00 \ubbfc\uad00\ud611\uc758\ud68c \uc704\ucd09\uc7a5\uc744 \ubc1b\uc740 \uac8c 5\uc6d4, \u2026(\uc911\ub7b5)\u2026 \uc2dc\uc7a5 \uba74\ub2f4\ud588\u000d\u000a\ub358 \uc2dc\uc808\uc778\ub370 2\uc6d4 \uc774\ud6c4\uc5d0 \ubc14\ub85c \uad6c\uc131\uc774 \ub41c \uac83\uc774 \uc544\ub2c8\ub77c, 3\uac1c\uc6d4 \uc9c0\ub098\uc11c \uad6c\uc131\ub420 \ub54c \uc704\uc6d0 \u000d\u000a\uad6c\uc131\uc5d0 \ub300\ud574\uc11c \ud658\uacbd \ucabd(\uc804\ubb38\uac00)\uc5d0 \uac70\ubd80\uac10\uc774 \uc788\uc5c8\ub2e4\uace0 \ud574\uc694. \u2026(\uc911\ub7b5)\u2026 \uadf8\ub798\ub3c4 \uc8fc\ubbfc\ub4e4\u000d\u000a\uc774 \uacc4\uc18d \ubd80\ud0c1\ud558\ub2c8\uae4c \uc2dc\uc5d0\uc11c \ubc1b\uc544\ub4e4\uc5ec \uad6c\uc131\ud558\uac8c \ub41c \uac83\uc774\uace0 \uac15\uacf5\uc6d0 \ub300\uae30 \ucabd, \uae40\uc138\ud6c8 \ud3d0\u000d\u000a\uae30\ubb3c \ucabd, \uc2dc\ubbfc\ub2e8\uccb4\ub294 \uc800, \uc815\uce58\uad8c\uc758 \uad8c\ud0dc\ud64d \uc774\ub807\uac8c \ud55c \uac70\uc8e0. \uadf8\ub54c \ubbfc\uad00\ud611\uc758\ud68c \uad6c\uc131\uc744\u000d\u000a\ud558\uba74\uc11c \uc758\ud559 \ucabd, \uc989 \uac74\uac15 \uad00\ub828\ub41c \uc804\ubb38\uac00\ub97c \ucd94\uac00\ub85c \uad6c\uc131\ud574\uc57c \ud558\uc9c0 \uc54a\ub098 \ud588\uc5b4\uc694. \uadf8\ub798\uc11c\u000d\u000a\ucd94\uac00\ub85c \ub4e4\uc5b4\uc628 \ubd84\uc774 \uc6d0\uad11\ub300 \uc608\ubc29\uc758\ud559\uacfc \uc624\uacbd\uc7ac \uad50\uc218\ub2d8\uc774 \ub4e4\uc5b4\uc654\uc8e0. \u2026(\uc911\ub7b5)\u2026 \uadf8\ub9ac\u000d\u000a\uace0 \ud64d\uc815\ud6c8 \ubcc0\ud638\uc0ac\ub3c4 \ucd94\uac00\ub85c \uc601\uc785\uc744 \ud574\uc11c \uad6c\uc131\uc744 \ud558\uac8c \ub418\uc5c8\uc8e0. \uadf8\ub7ec\uba74\uc11c \uc6b0\ub9ac\ub294 \uc9c4\uc6a9\u000d\u000a\uc744 \uac16\ucd94\uac8c \ub41c \uac70\uc8e0. \uc804\ubb38\uac00, \uc9c0\uc5ed\uc2dc\ubbfc\uc0ac\ud68c\ub2e8\uccb4, \uc815\uce58\uad8c. \uc815\uce58\uad8c\uc740 \uad6d\ud68c\uc5d0\uc11c \ud658\uacbd\ubd80\uc640\u000d\u000a\uc2f8\uc6cc \uc918\uc57c \ud558\ub2c8\uae4c\uc694. \uadf8\ub9ac\uace0 \uc8fc\ubbfc\ub4e4 \ub300\ud45c 2\uba85. \ub300\ucc45\uc704\uc6d0\uc7a5\uacfc \ud611\uc758\ud68c \uc704\uc6d0\uc7a5\uc744 \ub9e1\uc740 \uadf8\u000d\u000a\uc9c0\uc5ed \ubaa9\uc0ac. \uadf8\ub9ac\uace0 \uc2dc\uccad\uc758 \uad00\ub828 \ubd80\uc11c\ub4e4 \ud658\uacbd\uc774\ub098 \ubcf4\uac74\uc758 \uc0ac\ubb34\uad00\ub4e4\uc774 \ub4e4\uc5b4\uc640\uc11c \ucc38\uc5ec\ud55c\u000d\u000a\uac70\uc8e0. \u2026(\uc911\ub7b5)\u2026 \uac01\uacc4 \ubd84\uc57c\ub85c \uc9dc\uc9c0\ub294 \uac70\uc8e0. \uc774\ub807\uac8c \uc9dc\uc9c8 \uc218 \uc788\uc5c8\ub2e4\ub294 \uac83\uc740 \uc9c0\uc5ed\uc5d0 \ub300\u000d\u000a\ud559\uc774 \uc788\uace0 \uc2dc\ubbfc\uc0ac\ud68c\ub2e8\uccb4\uc758 \ud65c\ub3d9\uc774\ub098 \uacbd\ud5d8, \uc9c0\uc5ed\uc758 \ud658\uacbd\ubb38\uc81c\uc5d0 \ub300\ud574 \uc2f8\uc6cc\ubcf8 \uacbd\ud5d8\uacfc \uc0ac\u000d\u000a\ub840\uac00 \ub0b4\ud3ec\ub418\uc5b4 \uc788\uc5b4\uc11c \uc790\uc5f0\uc2a4\ub7fd\uac8c \ud2b9\ubcc4\ud55c \uc758\uc2ec \uc5c6\uc774 \uc774\ub7f0 \uc2dc\uc2a4\ud15c\uc73c\ub85c \ub098\uc544\uac00\uc57c \ud558\ub294\u000d\u000a\uac83\uc774 \uc544\ub2cc\uac00\uac00 \ub41c \uac70\uace0 \uc774\uac8c \uc798 \ub9de\uc544\ub5a8\uc5b4\uc9c4 \uac70\uc8e0.\u201d.(\uc2dc\ubbfc\uc6b4\ub3d9\uac00B, \uba74\uc811\uc77c\uc790: 2020.6.24.)\u000d\u000a\uc775\uc0b0\uc2dc \ub610\ud55c \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uac00 \ucd9c\ubc94\ud55c \uc774\ud6c4 \ud611\uc758\uccb4\uc758 \ub17c\uc758 \ubc0f \uacb0\uc815 \ub0b4\uc6a9\uc5d0 \ub300\ud574 \u000d\u000a\uc218\uc6a9\uc801\uc778 \ud0dc\ub3c4\ub97c \ubcf4\uc774\uba74\uc11c, \ud53c\ud574 \uc8fc\ubbfc\uacfc NGO, \uc804\ubb38\uac00 \uadf8\ub9ac\uace0 \uc775\uc0b0\uc2dc \uc0ac\uc774\uc758 \uc77c\uc815 \ubd80\ubd84\uc740 \u000d\u000a\ud611\ub825 \uc9c0\ud5a5\uc801 \uc0c1\ud638\uc791\uc6a9\uc774 \ud615\uc131\ub418\uc5c8\ub2e4. \uc775\uc0b0\uc2dc\uac00 2017\ub144\ubd80\ud130 \uc7a5\uc810\ub9c8\uc744 \ubb38\uc81c\uc5d0 \ub300\ud574 \uc804\ud5a5\uc801 \u000d\u000a\ud0dc\ub3c4\ub97c \ubcf4\uc774\uae30 \uc2dc\uc791\ud55c \ubc30\uacbd\uc5d0\ub294 \uc775\uc0b0\uc2dc\uc7a5\uc758 \uad50\uccb4 \ub610\ud55c \uc77c\ubd80 \uc601\ud5a5\uc744 \ubbf8\uce5c \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4. \u000d\u000a2016\ub144 4\uc6d4 13\uc77c \ubcf4\uad90\uc120\uac70\ub97c \ud1b5\ud574 \uc775\uc0b0\uc2dc\uc7a5\uc774 \uc0c8\ub86d\uac8c \ucde8\uc784\ud588\ub2e4. \uc0c8\ub85c \ucde8\uc784\ud55c \uc2dc\uc7a5\uc740 \uacfc\u000d\u000a\uac70 \uae08\uac15\ub18d\uc0b0 \uad00\ub9ac \ubb38\uc81c\uc5d0 \uc788\uc5b4 \uc0c1\ub300\uc801\uc73c\ub85c \uc790\uc720\ub85c\uc6b4 \uc704\uce58\uc5d0 \ub193\uc5ec \uc788\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uc815\uce58\uc801 \ubd80\u000d\u000a\ub2f4 \uc5c6\uc774 \uc774 \ubb38\uc81c\uc5d0 \uac1c\uc785\ud558\ub294 \uac83\uc774 \uac00\ub2a5\ud588\ub358 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4.","88 89\u000d\u000a\uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4 \uad6c\uc131\uc6d0\uc740 \ucd1d 11\uba85\uc774\ub2e4. \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc744 \ub300\ud45c\ud558\uc5ec \uc7a5\uc810\ub9c8\uc744\uc774 \uc18d\ud55c \u000d\u000a\uc2e0\ub4f1\ub9ac \uc77c\ub300\uc5d0\uc11c \uc624\ub7ab\ub3d9\uc548 \ubaa9\ud68c \ud65c\ub3d9\uc744 \ud574\uc628 \uc2e0\ub4f1\uad50\ud68c \ubaa9\uc0ac\uc640 \u2018\uc7a5\uc810\ub9c8\uc744 \uc8fc\ubbfc \ube44\uc0c1\ub300\ucc45\uc704\u000d\u000a\uc6d0\ud68c\u2019(\uc774\ud558 \uc7a5\uc810\ub9c8\uc744 \uc8fc\ubbfc\ub300\ucc45\uc704)\uc758 \uc8fc\ubbfc\ub300\ud45c\uac00 \ud611\uc758\uccb4\uc5d0 \ucc38\uc5ec\ud588\uc73c\uba70, \uc775\uc0b0\uc2dc\uc5d0\uc11c \ub2f4\ub2f9\uc5c5\ubb34\ub97c \uc218\ud589\ud558\u000d\u000a\ub294 \uacfc\uc7a5\uae09 \uacf5\ubb34\uc6d0 4\uc778\ub3c4 \ud568\uaed8\ud588\ub2e4. \uc8fc\ubaa9\ud574\uc11c \ubd10\uc57c \ud560 \ubd80\ubd84\uc740 \ub098\uba38\uc9c0 \ubbfc\uac04\uc704\uc6d0\uc774\ub2e4. \ud658\uacbd\uacf5\u000d\u000a\ud559\uc790, \ub300\uae30\ud658\uacbd\ud559\uc790, \uc5ed\ud559\uc804\ubb38\uac00, \uc775\uc0b0\uc5d0 \uc9c0\uc5ed\uc744 \ub450\uace0 \uc788\ub294 \uc815\uce58\uc778, \ubcc0\ud638\uc0ac, \uc9c0\uc5ed\uc2dc\ubbfc\uae30\uc790, \u000d\u000a\uadf8\ub9ac\uace0 \uc775\uc0b0\uc9c0\uc5edNGO \uad00\uacc4\uc790 3\uc778\uc774 \ucc38\uc5ec\ud588\ub2e4. \ubbfc\uac04\uc704\uc6d0\ub4e4\uc740 \uc7a5\uc810\ub9c8\uc744 \uc8fc\ubbfc\ub4e4\uacfc \uae34\ubc00\ud558\u000d\u000a\uac8c \uc18c\ud1b5\ud558\uba74\uc11c \uac01\uc790\uc758 \uc804\ubb38\uc131\uc744 \ud65c\uc6a9\ud558\uc5ec \uc7a5\uc810\ub9c8\uc744 \ubb38\uc81c\ub97c \uacf5\ub860\ud654\ud558\ub294 \uac83\uc740 \ubb3c\ub860 \ud53c\ud574 \uc8fc\u000d\u000a\ubbfc\ub4e4\uc774 \ubd80\uc871\ud55c \uc9c0\uc2dd\uacfc \uc815\ubcf4, \ub124\ud2b8\uc6cc\ud06c\ub97c \uc81c\uacf5\ud574 \uc8fc\uc5c8\ub2e4. \u000d\u000a\uc5b8\ub860\ubcf4\ub3c4 \ub610\ud55c \uc7a5\uc810\ub9c8\uc744 \uc0ac\uac74\uc744 \ub300\uc678\uc801\uc73c\ub85c \uacf5\ub860\ud654\ud558\ub294 \ub370 \uc77c\uc815\ud55c \uc601\ud5a5\uc744 \ubbf8\ucce4\ub2e4. \uc0ac\uac74\u000d\u000a\uc774 \uadf9\uc801\uc77c\uc218\ub85d \ubbf8\ub514\uc5b4\uc758 \uad00\uc2ec\uc744 \ub04c\uac8c \ub418\ub294\ub370 \uc9d1\ub2e8 \uc554 \ubc1c\ubcd1\uc774\ub77c\ub294 \uc7a5\uc810\ub9c8\uc744 \uc0ac\uac74\uc740 \uc5b8\ub860\uc774\u000d\u000a\uad00\uc2ec\uc744 \ub450\uae30\uc5d0 \ucda9\ubd84\ud588\ub2e4. 2017\ub144 \uae30\uc810\uc73c\ub85c \uc9c0\uc5ed\uc744 \ub118\uc5b4 \uc911\uc559\uc5b8\ub860\uc758 \ubcf4\ub3c4\uac00 \uae09\uc99d\ud558\uba74\uc11c,\u000d\u000a\uad6d\ubb34\ucd1d\ub9ac\uc640 \uc911\uc559\ubd80\ucc98\uc5d0\uc11c\ub3c4 \uad00\uc2ec\uc744 \uac00\uc838\uc57c \ud558\ub294 \uc804\uad6d\uc801 \ud658\uacbd \uc774\uc288\ub85c \ubd80\uc0c1\ud588\ub2e4. \uc7a5\uc810\ub9c8\uc744\u000d\u000a\uc744 \uac80\uc0c9\uc5b4\ub85c \ub274\uc2a4 \ube45\ub370\uc774\ud130 \ubd84\uc11d \uc0ac\uc774\ud2b8\uc778 \ube45\uce74\uc778\uc988Big Kinds\ub97c \uac80\uc0c9\ud574 \ubcf4\uba74, 2010\ub144\ubd80\ud130\u000d\u000a2020\ub144 7\uc6d4\uae4c\uc9c0 \ucd1d 561\uac74\uc758 \uae30\uc0ac\ub97c \ud655\uc778\ud560 \uc218 \uc788\ub2e4.<\ud45c. 1> \ucc38\uc870 2016\ub144\uae4c\uc9c0\ub294 \uc5b8\ub860\ubcf4\ub3c4 \uac74\u000d\u000a\uc218\uac00 \ubbf8\ube44\ud55c \uc218\uc900\uc774\uc5c8\ub2e4. \ud558\uc9c0\ub9cc 2017\ub144 \uc9d1\ub2e8 \uc554 \ubc1c\ubcd1\uc5d0 \ub300\ud55c \uc8fc\ubbfc\uac74\uac15\uc5ed\ud559\uc870\uc0ac \uacb0\uc815\uc744\u000d\u000a\uc804\ud6c4\ub85c \ud558\uc5ec \uc911\uc559\uc5b8\ub860\uc744 \ud1b5\ud574 \ubcf4\ub3c4\ub418\uae30 \uc2dc\uc791\ud558\uba74\uc11c \uc804\uad6d\uc801 \uad00\uc2ec \uc0ac\uc548\uc774 \ub418\uc5c8\ub2e4. \uc7a5\uc810\ub9c8\uc744\u000d\u000a\uc8fc\ubbfc \ub610\ud55c \uc5b8\ub860\uacfc\uc758 \uc778\ud130\ubdf0\ub97c \ud1b5\ud574 \uc790\uc2e0\ub4e4\uc758 \uc785\uc7a5\uacfc \ucc98\uc9c0\ub97c \uc54c\ub9ac\ub294 \ub370 \uc801\uadf9\uc801\uc774\uc5c8\ub2e4. \ub9c8\uc744\u000d\u000a\uc744 \ub300\ud45c\ud558\uc5ec \ud65c\ub3d9\ud588\ub358 \uc8fc\ubbfc\ub300\ucc45\uc704 \uad00\uacc4\uc790\ub294 \ud2b9\ud788 \uc911\uc559\ubc29\uc1a1\uc758 \ubcf4\ub3c4\uac00 \uc7a5\uc810\ub9c8\uc744 \ubb38\uc81c\ub97c \uc804\u000d\u000a\uad6d\uc801 \uc218\uc900\uc73c\ub85c \uc5ec\ub860\ud654\ud558\ub294 \ub370 \uc801\uc9c0 \uc54a\uc740 \uc5ed\ud560\uc744 \ubbf8\ucce4\ub2e4\uace0 \uad6c\uc220\ud588\ub2e4.\u000d\u000a<\uc8fc> \ube45\uce74\uc778\uc988\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 \uae30\uc0ac\ub9cc\uc744 \ucd94\ucd9c\ud558\uc600\uae30 \ub54c\ubb38\uc5d0 \uc2e4\uc81c \ubcf4\ub3c4 \uc0ac\ub840\uc640\ub294 \ucc28\uc774\uac00 \uc788\uc744 \uc218 \uc788\uc9c0\ub9cc, \uc804\uccb4\uc801\uc778 \uacbd\ud5a5\u000d\u000a\uc744 \ud655\uc778\ud558\ub294 \ub370\uc5d0\ub294 \ubb34\ub9ac\uac00 \uc5c6\uc744 \uac83\uc73c\ub85c \ud310\ub2e8\ub428.\u000d\u000a<\uc790\ub8cc> \ube45\uce74\uc778\uc988 \ubd84\uc11d \uacb0\uacfc(\ubc94\uc704: 2010\ub144\ubd80\ud130 2020\ub144 7\uc6d4 30\uc77c), \uac80\uc0c9\uc5b4 \u201c\uc7a5\uc810\ub9c8\uc744\u201d, \uac80\uc0c9\uc77c: 2020.7.25.~30\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c \u000d\u000a\uad6c\ubd84 2010 2011 2013 2017 2018 2019 2020.7.30 \ud569\uacc4(%)\u000d\u000a\uc911\uc559 - - - 16\u000d\u000a(21.3)\u000d\u000a25\u000d\u000a(33.3)\u000d\u000a175\u000d\u000a(51.8)\u000d\u000a28\u000d\u000a(41.8)\u000d\u000a244\u000d\u000a(43.5)\u000d\u000a\uc804\ubd81 1\u000d\u000a(100.0)\u000d\u000a2\u000d\u000a(100.0)\u000d\u000a3\u000d\u000a(100.0)\u000d\u000a57\u000d\u000a(76.0)\u000d\u000a48\u000d\u000a(64.0)\u000d\u000a149\u000d\u000a(44.1)\u000d\u000a 34\u000d\u000a(50.7)\u000d\u000a294\u000d\u000a(52.4)\u000d\u000a\uae30\ud0c0\uc9c0\uc5ed - - - 2\u000d\u000a(2.7)\u000d\u000a2\u000d\u000a(2.7)\u000d\u000a14\u000d\u000a(4.1)\u000d\u000a5\u000d\u000a(7.5)\u000d\u000a23\u000d\u000a(4.1)\u000d\u000a\ud569\uacc4 1\u000d\u000a(100.0)\u000d\u000a2\u000d\u000a(100.0)\u000d\u000a3\u000d\u000a(100.0)\u000d\u000a75\u000d\u000a(100.0)\u000d\u000a75\u000d\u000a(100.0)\u000d\u000a338\u000d\u000a(100.0)\u000d\u000a67\u000d\u000a(100.0)\u000d\u000a561\u000d\u000a(100.0)\u000d\u000a<\ud45c 1> \uc7a5\uc810\ub9c8\uc744 \uc5b8\ub860\ubcf4\ub3c4 \uae30\uc0ac \ud604\ud669 (\ub2e8\uc704: \uac74, %)"," & 90\u000d\u000a3) \uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\uc870\uc0ac \ub2e8\uacc4: 2017\ub144 7\uc6d4~2019\ub144 11\uc6d4\u000d\u000a\uc774 \ub2e8\uacc4\ub294 \ud658\uacbd\ubd80\uac00 \uc8fc\ubbfc\uccad\uc6d0\uc744 \uc218\uc6a9\ud558\uc5ec \uacf5\uc2dc\uc801\uc73c\ub85c \uac74\uac15\uc601\ud5a5\uc870\uc0ac\ub97c \uc2e4\uc2dc\ud558\uace0 \uc7a5\uc815\ub9c8\u000d\u000a\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uac00 \ubcf8\uaca9\uc801\uc73c\ub85c \uac00\ub3d9\ub41c \uc2dc\uae30\ub2e4. \uc774 \uc2dc\uae30 \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uc758 \ubbfc\uac04\uc704\uc6d0\uc744 \u000d\u000a\uc911\uc2ec\uc73c\ub85c \uac74\uac15\uc601\ud5a5\uc870\uc0ac \uacfc\uc815\uc5d0 \uc804\ubb38\uac00\ub85c\uc11c \ud544\uc694\ud55c \uc758\uacac\uc744 \uac1c\uc9c4\ud558\uace0, \ud53c\ud574 \uc8fc\ubbfc\uacfc \ud589\uc815\uae30\u000d\u000a\uad00\uc758 \uc758\uacac \uad50\ud658 \uacfc\uc815\uc5d0\ub3c4 \uc801\uadf9\uc801\uc73c\ub85c \uac1c\uc785\ud588\ub2e4. \ub610\ud55c \uad6d\ud68c\uc758\uc6d0\uacfc \ud568\uaed8 \uad6d\ud68c \ud1a0\ub860\ud68c\ub97c \uac1c\ucd5c\u000d\u000a\ud558\ub294 \ub4f1 \uc7a5\uc810\ub9c8\uc744 \ubb38\uc81c\ub97c \uc911\uc559\uc815\uce58\uc758 \uc774\uc288\ub85c \ub9cc\ub4e4\uae30 \uc704\ud574\uc11c \ub178\ub825\ud588\ub2e4.\u000d\u000a\ud658\uacbd\ubd80\uc758 \uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\uc870\uc0ac\uc758 \uc804\uccb4 \uacfc\uc815\uc774 \uc6d0\ud65c\ud558\uac8c \ucd94\uc9c4\ub41c \uac83\ub9cc\uc740 \uc544\ub2c8\ub2e4. \uacf5\uc2dd\uc801\uc778 \u000d\u000a\uc5f0\uad6c\uc6a9\uc5ed \uae30\uac04\uc740 2017\ub144 12\uc6d4\uc5d0\uc11c \ub2e4\uc74c \ud574 12\uc6d4\ub85c 1\ub144\uc774\uc5c8\uc9c0\ub9cc, \ucd5c\uc885 \uacb0\uacfc\ub294 2019\ub144 11\u000d\u000a\uc6d4\uc5d0 \ubc1c\ud45c\ub418\uc5c8\uc744 \uc815\ub3c4\ub85c \uacb0\uacfc \ud574\uc11d\uc744 \ub458\ub7ec\uc2fc \ub0b4\ubd80 \ub17c\uc7c1\uc740 \uce58\uc5f4\ud588\ub2e4. \uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\uc870\uc0ac\uc758 \u000d\u000a\uccab \ubc88\uc9f8 \ub09c\uad00\uc740 \uac74\uac15\uc601\ud5a5\uc870\uc0ac\ub97c \uc2dc\uc791\ud55c \uc2dc\uc810\uc774 \uc774\ubbf8 \uacf5\uc7a5\uc774 \ud3d0\uc1c4(2017\ub144 4\uc6d4 28\uc77c)\ub41c \uc9c0 1\ub144 7\uac1c\u000d\u000a\uc6d4\uc774 \uc9c0\ub09c \ud6c4\uc600\ub2e4\ub294 \uac83\uc774\ub2e4. \ub530\ub77c\uc11c \ubd84\uc11d\uc5d0 \ud544\uc694\ud55c \uc2dc\ub8cc \ucc44\ucde8\uc5d0 \uc5b4\ub824\uc6c0\uc774 \uc788\uc5c8\ub2e4. \uacf5\uc7a5\uc744 \u000d\u000a\uc7ac\uac00\ub3d9\uc2dc\ucf1c \ubcf4\uc790\ub294 \uc758\uacac\ub3c4 \uc788\uc5c8\uc9c0\ub9cc, \uad00\ub828 \uae30\uacc4\uac00 \uc774\ubbf8 \ubc18\ucd9c\ub41c \uc0c1\ud0dc\uc600\uae30 \ub54c\ubb38\uc5d0 \ud604\uc2e4\uc801\uc73c\u000d\u000a\ub85c \ubd88\uac00\ub2a5\ud588\ub2e4.\u000d\u000a\uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\uc870\uc0ac\ub97c \uc218\ud589\ud588\ub358 \uc5f0\uad6c\ucc45\uc784\uc790\uc758 \uad6c\uc220\uc5d0 \ub530\ub974\uba74 \ube44\ub8cc \uc6d0\ub8cc\ub85c \uc0ac\uc6a9\ud558\ub358 \u2018\uc5f0\u000d\u000a\ucd08\ubc15\u2019\uc744 \uc8fc\ubaa9\ud558\uac8c \ub418\uc5c8\ub2e4\uace0 \ud55c\ub2e4. \uc8fc\ubbfc\ub4e4\uacfc \uc9c4\ud589\ub41c \uc2ec\uce35 \uba74\uc811\uc870\uc0ac\uc640 \uc0ac\ud68c\ubb38\ud654\uc870\uc0ac\uc5d0\uc11c \uc7a5\u000d\u000a\uc810\ub9c8\uc744 \uc8fc\ubbfc\ub4e4\uc774 \uc77c\uad00\ub418\uac8c \uc5f0\ucd08\ubc15\uc744 \uac70\ub860\ud588\ub2e4\ub294 \uac83\uc774\ub2e4. \ub610\ud55c \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uc758 \uc694\u000d\u000a\uccad\uc73c\ub85c \uc775\uc0b0\uc2dc\uac00 \uc9c0\uc6d0\ud55c \uc608\ube44\uc870\uc0ac(2017\ub144 11\uc6d4)\uc5d0\uc11c\ub3c4 3\uac1c\uc6d4\uc774\ub77c\ub294 \uc9e7\uc740 \uc870\uc0ac \uae30\uac04\uacfc \uc801\uc740 \uc608\u000d\u000a\uc0b0\uc73c\ub85c \uc9d1\uc911\ud574\uc11c \uc5f0\ucd08\ubc15\uc744 \ubd84\uc11d\ud560 \uc218 \uc5c6\uc5c8\uc9c0\ub9cc, \uc5f0\ucd08\ubc15\uc758 \uc704\ud5d8\uc131\uc744 \uc5b8\uae09\ud588\ub2e4. \uc5f0\ucd08\ubc15\uc774 \u000d\u000a\ud568\uc720\ud55c TSNAs\ub294 \uc554\uc744 \uc720\ubc1c\ud558\ub294 \uac15\ub825\ud55c \ubc1c\uc554\ubb3c\uc9c8\uc774\ub77c\ub294 \uacfc\ud559\uc801 \uc5f0\uad6c\uac00 \uc0c1\ub2f9 \ubd80\ubd84 \ucd95\uc801\ub418\u000d\u000a\uc5b4 \uc788\uc5c8\ub2e4.\u000d\u000a\u201c\uc800\ub294 \ud604\uc7a5\uc870\uc0ac \uac00\uba74 \uc8fc\ubbfc\ub4e4 \uc774\uc57c\uae30\uc5d0 \ub2f5\uc774 \uc788\ub2e4\uace0 \uc0dd\uac01\ud574\uc694. \uc6b0\ub9ac\ub294 \uac70\uae30 \uc0b4\uc558\ub358 \u000d\u000a\uac83\ub3c4 \uc544\ub2c8\uace0 \uc798 \ubaa8\ub974\uae30 \ub54c\ubb38\uc5d0 \uc8fc\ubbfc\ub4e4\uc774 \ud558\ub294 \uc774\uc57c\uae30\ub97c \ucda9\ubd84\ud788 \ub4e4\uc5b4 \ubcf4\uace0 \ubb50\uac00 \ubb38\uc81c\u000d\u000a\ub77c\uace0 \uc0dd\uac01\ud558\ub294\uc9c0\ub97c \uc5b4\ub290 \uc815\ub3c4\ub294 \uc54c\uac8c \ub418\uc8e0. \uadf8\ub798\uc11c \uc8fc\ubbfc\ub4e4\uc774 \uc5f0\ucd08\ubc15 \uc774\uc57c\uae30\ub97c \ub9ce\uc774 \u000d\u000a\ud588\uace0, \uc5f0\ucd08\ubc15\uc774 \uac74\uc870\ub418\uc5c8\uc744 \ub54c \ubb34\uc5c7\uc744 \uc7a1\uc544\uc57c \ud558\ub294\uac00\uac00 \ucc98\uc74c \uc5f0\uad6c\uc124\uacc4\uc5d0\uc11c \uc911\uc694\ud588\uc5b4\u000d\u000a\uc694. \u2026(\uc911\ub7b5)\u2026 \uadf8\ub7f0\ub370 \uc5f0\ucd08\ubc15\uc5d0\uc11c \ub098\uc624\ub294 TSNAs\ub294 \uae08\uac15\ub18d\uc0b0\uc5d0\uc11c \ub098\uc62c \uc218\ubc16\uc5d0 \uc5c6\uace0 \u000d\u000aTSNAs\ub294 \ub2f4\ubc43\uc78e \uc548\uc5d0 \ub4e4\uc5b4 \uc788\uc5b4\uc694. (\ub300\uc804) \uc2e0\ud0c4\uc9c4 KT&G(\ub2f4\ubc30\uc778\uc0bc\uacf5\uc0ac)\uc5d0\uc11c \ud3d0\uae30\ubb3c \ucc98\ub9ac\u000d\u000a\ub85c \uae08\uac15\ub18d\uc0b0\uc774 \ubc1b\uc544 \uc640\uc11c \uacf5\uc7a5\uc5d0 \ub9c9 \uc313\uc544 \ub194\uc694.\u201d(\uac74\uac15\uc601\ud5a5\uc870\uc0ac \uc5f0\uad6c\ucc45\uc784\uc790D, \uba74\uc811\uc77c\uc790: 2020.7.7.).","90 91\u000d\u000a\uae08\uac15\ub18d\uc0b0\uc740 \ub300\uc804\uc758 \uc2e0\ud0c4\uc9c4\uc5d0 \uc788\ub294 \ub2f4\ubc30\uc778\uc0bc\uacf5\uc0acKT&G\ub85c\ubd80\ud130 \ub300\ub7c9\uc73c\ub85c \uc5f0\ucd08\ubc15\uc744 \ubc18\uc785\u000d\u000a\ud558\uc5ec \uc720\uae30\uc9c8 \ube44\ub8cc \uc6d0\ub8cc\ub85c \ubd88\ubc95 \uc0ac\uc6a9\ud588\ub2e4. \uadf8\ub9ac\uace0 \uc720\uae30\uc9c8 \ube44\ub8cc\uc758 \uac74\uc870\uacf5\uc815\uc5d0\uc11c \uc5f0\ucd08\ubc15 \ub0b4 \u000d\u000aTSNAs \ub4f1\uc758 \ubc1c\uc554\ubb3c\uc9c8\uc774 \ub300\uae30 \uc911\uc5d0 \ubc30\ucd9c\ub418\uc5c8\uace0 \uc8fc\ubbfc\ub4e4\uc774 \uc774\ub97c \ud761\uc785\ud588\ub2e4. TSNAs\uc640 \uc554 \uc0ac\u000d\u000a\uc774\uc758 \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uc778\uc815\ud558\uae30\uae4c\uc9c0 \ud658\uacbd\ubd80\uc640 \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\ud68c(\ubbfc\uad00\uc704\uc6d0) \uc0ac\uc774\uc5d0\uc11c \u000d\u000a\uce58\uc5f4\ud55c \ub17c\uc7c1\uc774 \uc9c4\ud589\ub418\uc5c8\ub2e4. \ud658\uacbd\ubd80\uc758 \uc785\uc7a5\uc740 \ucd08\uae30 \u2018\uc778\uacfc\uad00\uacc4\uc758 \ucd94\uc815\uc740 \uc5b4\ub835\ub2e4\u2019(2019\ub144 6\uc6d4 7\uc77c)\u000d\u000a\uc5d0\uc11c \ucd9c\ubc1c\ud558\uc5ec \u2018\uad00\ub828\uc131\uc744 \ucd94\uc815\ud560 \uc218 \uc788\uc73c\ub098 \uc778\uacfc\uad00\uacc4 \ud574\uc11d\uc740 \uc81c\ud55c\uc801\uc774\ub2e4.\u2019,(2019\ub144 6\uc6d4 11\uc77c) \u2018\uc554 \u000d\u000a\ubc1c\uc0dd\uc5d0 \uc601\ud5a5\uc744 \uc900 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4.\u2019,(2019\ub144 6\uc6d4 20\uc77c) \u2018\uc554 \ubc1c\uc0dd\uacfc \uacf5\uc7a5 \uac00\ub3d9 \uac04\uc5d0 \uad00\ub828\uc131\uc744 \ucd94\uc815\ud55c\u000d\u000a\ub2e4.\u2019(2019\ub144 7\uc6d4 23\uc77c) \ub4f1\uc73c\ub85c \uc804\ud5a5\uc801\uc778 \ubc29\ud5a5\uc73c\ub85c \uc804\ud658\ub418\uc5b4 \uac14\uc9c0\ub9cc, \uc774\ub294 \ud53c\ud574 \uc8fc\ubbfc \ubc0f \ubbfc\uad00\ud611\uc758\u000d\u000a\uccb4\uc758 \uc5ed\ud559 \uc804\ubb38\uac00\uc758 \uc785\uc7a5\uc5d0\uc11c\ub294 \ubc1b\uc544\ub4e4\uc77c \uc218 \uc5c6\uc5c8\ub2e4.(\uc624\uacbd\uc7ac, 2020: 15)\u000d\u000a\uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uc5d0 \ucc38\uc5ec\ud55c \uc5ed\ud559\uc804\ubb38\uac00 A\uc5d0 \ub530\ub974\uba74, \ub2f9\uc2dc \uc8fc\uc694\ud55c \uc7c1\uc810\uc740 \u2018\uc778\uc815\ud558\uae30\u000d\u000a\uc5d0\ub294 \ub300\uc0c1\uc790 \uc218\uac00 \ub108\ubb34 \uc791\ub2e4\u2019, \u2018\ubc30\ucd9c\ub7c9 \ubc0f \ub178\ucd9c\ub7c9\uc744 \uc54c \uc218 \uc5c6\ub2e4\u2019, \u2018\ub9ce\uc740 \uc0ac\ub78c\uc774 \uc0ac\ub9dd\ud574\uc11c \uc54c\u000d\u000a\uae30 \uc5b4\ub835\ub2e4\u2019 \ub4f1\uc774\uc5c8\ub2e4. \uc5ed\ud559\uc804\ubb38\uac00\uc778 \uc624\uacbd\uc7ac \uad50\uc218\ub294 \uc9d1\ub2e8 \uac04 \ube44\uad50\uc5f0\uad6c \ubc29\ubc95\uc778 \ud658\uc790-\ub300\uc870\uad70 \u000d\u000a\uc5f0\uad6c\uc5d0\uc11c\ub294 \uc801\uc740 \uc218\ub85c\ub3c4 \ucda9\ubd84\ud55c \ud0c0\ub2f9\uc131\uc744 \ud655\ubcf4\ud560 \uc218 \uc788\ub2e4\ub294 \uc810\uc744 \uac15\uc870\ud588\ub2e4.(\uc7a5\uc77c\ud638\u00b7\ub098\uacbd\ud76c\u00b7\uc774\uba85\uc775, \u000d\u000a2020: 43) \uadf8\ub9ac\uace0 \uc720\ud574 \ubb3c\uc9c8\uc758 \ubc1c\uc554\uc131\uc774 \uc774\ubbf8 \uacf5\uc778\ub418\uc5c8\uace0, \uadf8\ub7ec\ud55c \ubb3c\uc9c8\uc758 \ubc30\ucd9c\uacfc \ub178\ucd9c\uc774 \ud655\uc778\u000d\u000a\ub418\uc5c8\uc73c\uba70, \uadf8 \ubb3c\uc9c8\uc5d0 \ud2b9\uc774\uc131\uc744 \uc9c0\ub2cc \uc9c8\ud658\uc774 \uc774\ubbf8 \uc8fc\ubbfc\ub4e4\uc5d0\uac8c\uc11c \ub098\ud0c0\ub0ac\ub2e4\ub294 \uc810\uc744 \uac15\uc870\ud558\uba74\u000d\u000a\uc11c, \ubc30\ucd9c\ub7c9 \ubc0f \ub178\ucd9c\ub7c9\uc744 \uc54c \uc218 \uc5c6\uc73c\ubbc0\ub85c \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uc778\uc815\ud560 \uc218 \uc5c6\ub2e4\ub294 \uacac\ud574\uc5d0\ub3c4 \ubc18\ub300\u000d\u000a\ud588\ub2e4.(\uc7a5\uc77c\ud638\u00b7\ub098\uacbd\ud76c\u00b7\uc774\uba85\uc775, 2020: 43) \ub610\ud55c \uac74\uac15 \ubb38\uc81c\uc5d0 \uc788\uc5b4 \uc0ac\ub9dd\uc774 \uac00\uc7a5 \uacb0\uc815\uc801\uc778 \uc815\ubcf4\ub77c\ub294 \uc810\uc744 \uac15\u000d\u000a\uc870\ud588\ub2e4. \uc989 \uc0ac\ub9dd\uc73c\ub85c \uad00\ub828 \uc815\ubcf4\ub97c \ud655\uc778\ud560 \uc218 \uc5c6\uc73c\ub2c8 \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uc778\uc815\ud560 \uc218 \uc5c6\ub294 \uac83\uc774 \u000d\u000a\uc544\ub2c8\ub77c, \u2018\uc0ac\ub9dd\uc774 \uac00\uc7a5 \uac15\ub825\ud55c \uc99d\uac70\u2019\ub77c\ub294 \uac83\uc774\ub2e4.(\uc7a5\uc77c\ud638\u00b7\ub098\uacbd\ud76c\u00b7\uc774\uba85\uc775, 2020: 43)\u000d\u000a\u201c\uc778\uacfc\uad00\uacc4\uac00 \uc5c6\ub2e4\uace0 \ud574\uc11d\ud588\ub2e4 \ud558\uba74 (\uadf8 \uc774\uc720\ub85c)\ud574\ub2f9 \uc8fc\ubbfc \uc218\uac00 \uc801\ub2e4. \ubaa8\ub4e0 \uacfc\ud559\uc801\uc778 \u000d\u000a\uac74 \uc0d8\ud50c \uc0ac\uc774\uc988\uac00 \ucee4\uc57c \ud55c\ub2e4\ub294 \uac70\uc8e0. \uc774\uac74 \uad49\uc7a5\ud788 \ubb38\uc81c\uac00 \uc788\uc5b4\uc694. \uc774\uac74 \ud1b5\uacc4\uc870\uc0ac\ub098 \ud604\u000d\u000a\ud669\uc870\uc0ac\ub97c \ud560 \ub54c \uc4f0\ub294 \uac70\uc608\uc694. \ub300\uaddc\ubaa8\ub85c \ubabb\ud558\ub2c8\uae4c \uc5b4\ub290 \uc815\ub3c4\uc758 \uc0d8\ud50c\uc758 \uc0ac\uc774\uc988\uac00 \uc788\uc5b4\u000d\u000a\uc57c \ud558\uace0, \ub300\ud45c\uc131\uc744 \uc704\ud574 \ub9ce\uc740 \uc0d8\ud50c \uc0ac\uc774\uc988\uac00 \ud544\uc694\ud55c \uac70\uc8e0. \u2026(\uc911\ub7b5)\u2026\ud1b5\uacc4\ud604\ud669\uc744 \ud30c\uc545\u000d\u000a\ud558\uae30 \uc704\ud55c \uac83\uc774\uc9c0 \uc778\uacfc\uad00\uacc4\ub97c \uac80\uc99d\ud560 \ub54c \uc4f0\ub294 \uac8c \uc544\ub2c8\uc5d0\uc694. \uc778\uacfc\uad00\uacc4 \uac80\uc99d\uc740 \ub300\uc0c1\uc790 \u000d\u000a\uc218\uac00 \uc911\uc694\ud55c \uac8c \uc544\ub2c8\ub77c \ube44\uad50\uc131\uc774\uc5d0\uc694. \uc784\uc0c1\uc2e4\ud5d8\uc774\ub098 \uc9c0\uae08 \ucf54\ub85c\ub098 \ubc31\uc2e0 \uac1c\ubc1c\ub3c4 \uadf8\ub807\uace0, \u000d\u000a\uc4f4 \uc0ac\ub78c\uacfc \uc548 \uc4f4 \uc0ac\ub78c\uc774 \uc911\uc694\ud55c \uac70\uc8e0. \ud658\uc790-\ub300\uc870\uad70 \uc5f0\uad6c\ub97c \uc0dd\uac01\ud558\uc2dc\uba74 \ub3fc\uc694. \uc9c8\ubcd1 \ubc1c\u000d\u000a\uc0dd\uc790\uc640 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\uc740 \uc0ac\ub78c\uc5d0\uac8c\uc11c \ub178\ucd9c \uc815\ub3c4\uac00 \uc5b4\ub290 \uc815\ub3c4\uc778\uc9c0. \ub178\ucd9c\uc774 \uc788\ub294 \uc0ac\ub78c \uc911\u000d\u000a\uc5d0\uc11c \uc9c8\ubcd1 \ubc1c\uc0dd\uc774 \ucc28\uc774\uac00 \uc788\ub294\uac00\ub97c \ubd10\uc57c \ud574\uc694.\u201d.(\uc5ed\ud559\uc804\ubb38\uac00A, \uba74\uc811\uc77c\uc790: 2020.7.15.)\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c "," & 92\u000d\u000a\u201c\ud658\uacbd\ubd80\uac00 \uc8fc\uc7a5\ud558\ub294 \uc2dd\uc73c\ub85c \ub17c\ub9ac\ub97c \ud3b4\uba74 \ub300\ud55c\ubbfc\uad6d\uc758 \ud658\uacbd\ubb38\uc81c\ub294 \uc218\ucc9c, \uc218\ub9cc \uba85\uc774 \u000d\u000a\uc8fd\uc5b4\uc57c \ud574\uc694. \ubc30\ucd9c\ub7c9\uc774\ub098 \ub178\ucd9c\ub7c9. \ub178\ucd9c \uc5ec\ubd80\ub97c \uc54c \uc218 \uc5c6\ub2e4\ub294 \uac70\uc8e0. \uc774\uac74 \ud589\uc815\uae30\uad00\uc774 \u000d\u000a\uc774\ubbf8 \ud3d0\uc1c4 \uba85\ub839 \ub0b4\ub824 \ub193\uace0 \uadf8\ub54c\uc11c\uc57c \uc5ed\ud559\uc870\uc0ac\ub97c \ud558\ub77c\ub294 \uac74 \ud658\uacbd\ubd80\uc758 \uc9c1\ubb34 \uc720\uae30\uc8e0. \u2026\u000d\u000a(\uc911\ub7b5)\u2026 \uac00\ub3d9\ud560 \ub54c\ub294 \uc5bc\ub9c8\ub098 \ub192\uc558\uaca0\ub0d0\uace0 \uac00\uc911\uce58\ub97c \u2026(\uc911\ub7b5)\u2026 \ubd80\uc5ec\ud574\uc57c \ud558\ub294\ub370 \uc624\ud788\u000d\u000a\ub824 \uc99d\uba85 \ubabb\ud558\ub2c8\uae4c \uc778\uacfc\uad00\uacc4 \uc5c6\ub2e4\uace0 \ud55c \uac70\uc8e0. \uc774\uac74 \ub204\uac00 \ubd10\ub3c4 \uc778\uc815\ud558\uc9c0 \uc54a\uc73c\ub824\uace0 \uc18c\uadf9\u000d\u000a\uc801\uc774\uace0 \ubc29\uc5b4\uc801\uc778 \ud0dc\uc138\ub9cc \ucde8\ud558\ub294 \uac70\uc8e0. \uac70\uc758 1\ub144 \ub3d9\uc548 \uc774\ub807\uac8c \ud574 \uc654\uc5b4\uc694.\u201d.(\uc5ed\ud559\uc804\ubb38\uac00A, \uba74\u000d\u000a\uc811\uc77c\uc790: 2020.7.15.)\u000d\u000a\u201c\uadf8\ub2e4\uc74c\uc5d0 \uc0ac\ub78c\uc774 \uc0ac\ub9dd\ud574\uc11c \uc2e4\uc81c\ub85c \uc9c8\ubcd1\uc774\ub098 \uc911\uc694\ud55c \uc815\ubcf4\ub97c \uac00\uc9c0\uace0 \uc788\ub294 \uc0ac\ub78c\uc774 \uc0ac\u000d\u000a\ub9dd\ud588\uc73c\ub2c8 \uc815\ubcf4\ub97c \uc5bb\uc744 \uc218 \uc5c6\ub2e4\uace0 \ud574\uc694. \u2026(\uc911\ub7b5)\u2026 \ubaa8\ub4e0 \uc5ed\ud559\uc774\ub098 \uc0ac\ub78c\uc5d0 \ub300\ud55c \uc815\ubcf4\uc5d0\u000d\u000a\uc11c\ub294 \uc0ac\ub9dd\uc774 \ucd5c\uc885\uc801\uc778 \uacb0\uacfc\uc5d0\uc694. \uc0ac\ub9dd\ud55c \uac83\ub9cc\ud07c \ud655\uc2e4\ud55c \uc99d\uac70\ub294 \uc5c6\uc5b4\uc694. \uc774 \uc9c8\ubcd1\uc73c\ub85c \u000d\u000a\uc8fd\uc5c8\uc5b4\uc694. \uc8fd\uc740 \uac83 \uc790\uccb4\uac00 \uc774 \uc9c8\ud658\uc5d0 \uc788\uc5b4\uc11c \uac00\uc7a5 \uce58\uba85\uc801\uc73c\ub85c \uc0ac\ub9dd\ud588\ub2e4\ub294 \uc99d\uac70\uac00 \ub418\u000d\u000a\ub294 \uac70\uc608\uc694. \uc544\ub2c8\uba74 \ub178\ucd9c \uad00\ub828\ud55c \uac83\uc774\ub77c\uba74 \ub2e4 \ud30c\ub0b4\uc11c \ubd80\uac80\ud574\uc57c \ud558\ub294 \uac70\uc8e0. \uc0ac\ub9dd \uc790\ub8cc\uac00 \u000d\u000a\uc911\uc694\ud558\uac8c \uc774\ud574\ub418\uc5b4\uc57c \ud558\ub294\ub370 \uc624\ud788\ub824 \uc0ac\ub9dd\uc790\uc758 \uc815\ubcf4\ub97c \uc218\uc9d1\ud560 \uc218 \uc5c6\uc73c\ub2c8 \uc778\uacfc\uad00\uacc4\ub97c \u000d\u000a\ubd80\uc5ec\ud560 \uc218 \uc5c6\ub2e4\uace0 \ub41c \uac70\uc8e0.\u201d.(\uc5ed\ud559\uc804\ubb38\uac00A, \uba74\uc811\uc77c\uc790: 2020.7.15.)\u000d\u000a\uacb0\uad6d \uc774\ub7ec\ud55c \uacf5\ubc29 \ub05d\uc5d0, \uc5ed\ud559\uc804\ubb38\uac00\ub4e4\ub85c \uad6c\uc131\ub41c \ud55c\uad6d\uc5ed\ud559\ud68c\uc758 \uc790\ubb38\uc5d0 \ub530\ub77c \ucd5c\uc885\uc801\uc73c\ub85c \u000d\u000a\uacb0\uc815\ud558\uae30\ub85c \ud569\uc758\ud558\uac8c \ub41c\ub2e4. \ud55c\uad6d\uc5ed\ud559\ud68c\uc758 \ub17c\uc758 \uacb0\uacfc,(2019\ub144 7\uc6d4 25\uc77c) \u201c\uc554 \ubc1c\uc0dd\uacfc \uacf5\uc7a5 \uac00\ub3d9 \uac04\u000d\u000a\uc5d0 \uc5ed\ud559\uc801 \uad00\ub828\uc131\u201d\uc774 \uc788\ub294 \uac83\uc73c\ub85c \ud310\ub2e8\ud588\ub2e4.(\uc624\uacbd\uc7ac, 2020: 15) \ud658\uacbd\ubd80\ub294 \u2018\uc778\uacfc\uad00\uacc4\ub97c \ucd94\uc815\ud558\ub294 \u000d\u000a\uac83\uc5d0 \uc5b4\ub824\uc6c0\uc774 \uc788\ub2e4\u2019\ub294 \uc785\uc7a5\uc5d0\uc11c \ucd9c\ubc1c\ud558\uc5ec \ud55c\uad6d\uc5ed\ud559\ud68c \uac80\ud1a0 \uc774\ud6c4 \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc774 \uc788\ub294 \uac83\u000d\u000a\uc73c\ub85c \ucd5c\uc885\uc801\uc73c\ub85c \uacb0\uc815\ud558\uac8c \ub41c\ub2e4. \u000d\u000a\u201c\uc774\uac8c \u2026(\uc911\ub7b5)\u2026\uc544\ubb34\ub9ac \uc774\uc57c\uae30\ud574\ub3c4 \uc548 \ub418\ub294 \uac70\uc8e0. \uadf8\ub807\ub2e4\uba74 \ud55c\uad6d\uc5ed\ud559\ud68c\ub77c\ub294 \uacf3\uc774 \u000d\u000a\uc788\ub2e4. \uc5ed\ud559 \uc804\ubb38\uac00\ub4e4\uc5d0\uac8c \u2026(\uc911\ub7b5)\u2026 \ud569\uc758\ub97c \ubcf4\uace0 \uc5ec\uae30\uc5d0 \ub300\ud55c \uacb0\uacfc\ub97c \uc218\uc6a9\ud558\uaca0\ub2e4\uace0. \u000d\u000a\ub354 \uc774\uc0c1 \ubb58 \ud560 \uc218\uac00 \uc5c6\uc796\uc544\uc694. \uc5ed\ud559\ud68c\uc5d0\uc11c \uc704\uc6d0\ub4e4\uc744 \uc120\uc815\ud560 \ub54c \uad49\uc7a5\ud788 \uac1d\uad00\uc801\uc73c\ub85c \ud588\u000d\u000a\uc5b4\uc694. \uc5ed\ud559 \uc804\ubb38, \ud658\uacbd\uc5ed\ud559, \uc774\ub7f0 \ubb38\uc81c\uc5d0 \ub300\ud55c \uacbd\ud5d8\uc774 \uc788\uc5c8\ub358 \ubd84\ub4e4\uc744 \uc120\uc815\ud588\uc5b4\uc694. \uc77c\u000d\u000a\ubc18 \uc5ed\ud559\uc790\uac00 \uc544\ub2c8\ub77c \uac01 \ubd84\uc57c\uc5d0 \uac1d\uad00\uc131\uc744 \uac00\uc9c8 \uc218 \uc788\uace0 \ubb38\uc81c\uc5d0 \ub300\ud55c \uc774\ud574\uad00\uacc4\uac00 \uc5c6\ub294 \u000d\u000a\ubd84\ub4e4 \u2026(\uc911\ub7b5)\u2026 \uadf8 \ubd80\ubd84\uc774 \uc788\uc5c8\uc2b5\ub2c8\ub2e4.\u201d.(\uc5ed\ud559\uc804\ubb38\uac00A, \uba74\uc811\uc77c\uc790: 2020.7.15.) ","92 93\u000d\u000a2019\ub144 11\uc6d4 14\uc77c, \ud658\uacbd\ubd80\uac00 \uac1c\ucd5c\ud55c \uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\uc870\uc0ac \ucd5c\uc885\ubcf4\uace0\ud68c\uc5d0\uc11c \uc544\ub798\uc640 \uac19\uc774 \u000d\u000a\uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uc778\uc815\ud55c\ub2e4\uace0 \uacf5\uc2dd \ubc1c\ud45c\ud588\ub2e4. \uc7a5\uc810\ub9c8\uc744\uc740 \uad6d\ub0b4\uc5d0\uc11c \u2018\ud658\uacbd\uc624\uc5fc\uacfc \ube44\ud2b9\uc774\uc131 \u000d\u000a\uc9c8\ud658\uc778 \uc554 \uc0ac\uc774\uc758 \uc5ed\ud559\uc801 \uad00\ub828\uc131\u2019\uc744 \uc778\uc815\ubc1b\uc740 \ucd5c\ucd08\uc758 \uc0ac\ub840\uac00 \ub418\uc5c8\ub2e4. \u000d\u000a\uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uc758 \ud658\uacbd \ubd84\uc57c \uc804\ubb38\uc704\uc6d0\ub4e4\uc740 \ud53c\ud574\ub97c \uacfc\ud559\uc801\uc73c\ub85c \uc785\uc99d\ud558\uae30 \uc704\ud55c \ub17c\u000d\u000a\uc7c1 \uacfc\uc815\uc5d0 \ucc38\uc5ec\ud588\ub2e4\uba74, \uc815\ub2f9\uc815\uce58\uc778 \ubc0f \uc9c0\uc5edNGO \ud65c\ub3d9\uac00\ub4e4\uc740 \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uacfc \ud568\uaed8 \uc7a5\uc810\ub9c8\u000d\u000a\uc744 \ubb38\uc81c\ub97c \uc0ac\ud68c\uc815\uce58\uc801 \uc7c1\uc810\uc73c\ub85c \ubd80\uc0c1\uc2dc\ud0a4\ub294 \uacfc\uc815\uc5d0 \uc801\uadf9\uc801\uc73c\ub85c \uac1c\uc785\ud588\ub2e4. \uad6d\ud68c\ud658\uacbd\ub178\ub3d9\u000d\u000a\uc704\uc6d0\ud68c \uc18c\uc18d \uc815\uc758\ub2f9 \uc774\uc815\ubbf8 \uc758\uc6d0\uacfc\uc758 \uc8fc\ubbfc\uac04\ub2f4\ud68c\ub97c \ucd94\uc9c4\ud558\uace0, \uad6d\ud68c\uc5d0\uc11c \ud658\uacbd\ubd80 \uc5ed\ud559\uc870\uc0ac \u000d\u000a\uacb0\uacfc\ub97c \uaddc\ud0c4 \uad6d\ud68c\uae30\uc790\ud68c\uacac(2018\ub144 7\uc6d4 18\uc77c)\uc744 \uc9c4\ud589\ud588\ub2e4. \ub2f9\uc2dc \uc815\uc758\ub2f9\uacfc \uc815\uc758\ub2f9 \uc774\uc815\ubbf8 \uc758\uc6d0\uc2e4\u000d\u000a\uc5d0\uc11c \uc774 \ubb38\uc81c\uc5d0 \uad00\uc2ec\uc744 \ub450\uace0 \uc9c0\uc6d0\ud588\ub2e4. \uad6d\ud68c\uc5d0\uc11c\uc758 \uc774\ub7ec\ud55c \uc2e0\uc18d\ud558\uba74\uc11c\ub3c4 \uc2dc\uc758\uc801\uc808\ud55c \ud65c\ub3d9\u000d\u000a\uc740 \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4 \ubbfc\uac04\uc704\uc6d0\uc774 \uc815\uc758\ub2f9\uc5d0\uc11c \ud575\uc2ec \ub2f9\uc9c1\uc744 \ub9e1\uace0 \uc788\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uac00\ub2a5\ud588\u000d\u000a\ub358 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4. \u000d\u000a\u201c\uc6b0\ub9ac\uac00 \ubcfc \ub54c\ub294 \ub108\ubb34\ub098 \ubd84\uba85\ud55c \uacb0\ub860\uc778\ub370 \ud658\uacbd\ubd80\ub294 \ub05d\uae4c\uc9c0 \uacb0\ub860\uc744 \uace0\uc218\ud558\ub294 \uc785\uc7a5\uc774\u000d\u000a\uc5c8\uc5c8\uc5b4\uc694. \u2026(\uc911\ub7b5)\u2026 \uace0\ubbfc\uc744 \ud558\ub2e4\uac00 \uad6d\ud68c\ub97c \uc9c0\ub81b\ub300\ub85c \uc0bc\uc790\uace0 \ud574\uc11c \ud658\uacbd\ubd80 \ub098\uc624\uc138\uc694, \u000d\u000a\uad6d\ud68c\ub85c \uc624\uc138\uc694. \uadf8\ub9ac\uace0 \uc6b0\ub9ac \uc8fc\ubbfc\ub4e4 \uc62c\ub77c\uac00\uace0. \ud658\uacbd\ubd80\ub97c \uacc4\uc18d \uc555\ubc15\ud55c \uac83\uc740 \uad6d\ud68c\uc758 \uc774\u000d\u000a\uc815\ubbf8 \uc758\uc6d0\uc2e4 \ud1b5\ud574\uc11c \ud588\uc8e0. \uad6d\ud68c\uc5d0\uc11c \uac19\uc774 \ub9cc\ub0ac\uc5b4\uc694. \uadf8\ub54c \uc5b4\ub290 \uc815\ub3c4\uc758 \uc0c1\ud669\uc774\uc5c8\ub0d0\uba74 \u000d\u000a\ucd5c\uc7ac\ucca0 (\uc8fc\ubbfc\ub300\ucc45\uc704)\uc704\uc6d0\uc7a5\uc774 \u2026(\uc911\ub7b5)\u2026 \uc5ec\uae30\uae4c\uc9c0 \uc8fc\uba39\uc774 \uac14\uc5b4\uc694. \uad6d\ud68c\uc758\uc6d0\ud68c\uac00 \ub4e4\u000d\u000a\uc369\ud588\uc5b4\uc694. \uc695 \ub098\uc624\uace0 \uc8fd\uc774\ub124, \uc0b4\ub9ac\ub124 \ud558\uace0 \ub4e4\uc5c8\ub2e4 \ub1a8\ub2e4 \ud558\uace0 \ucee4\ud53c \uc5ce\uace0 \ub09c\ub9ac\uc600\uc5b4\uc694. \uadf8\u000d\u000a\ub9cc\ud07c \uaca9\uc591\uc774 \ub420 \uc815\ub3c4\ub85c \ub9d0\uc774 \uc548 \ub418\ub294 \uc598\uae30\ub97c \ud558\uace0, (\uc8fc\ubbfc)\ub300\ucc45\uc704\uc6d0\uc7a5\uc774 \ubabb \ucc38\uace0, \uc624\uacbd\u000d\u000a\uc7ac \uad50\uc218\ub294 \ubb50 \uc800\ub7f0 \u2026 (\uc911\ub7b5)\u2026 \uc529\uc529\uac70\ub9b4 \uc815\ub3c4\uc758 \uc0c1\ud669\uc774\uc5c8\uc8e0. \uadf8\ub798\uc11c \uadf8\ub7ec\uba74 \uc804\ubb38\uae30\uad00\u000d\u000a\uc73c\ub85c\ubd80\ud130 \uc6b0\ub9ac\uac00 \uac00\uc9c4 \ub370\uc774\ud130\ub97c \ub193\uace0 \uc790\ubb38\uc744 \ubc1b\uc790\ub294 \ud569\uc758\ub97c \ud588\uc8e0. \uadf8 \uc804\ubb38\uae30\uad00\uc774 \ud55c\u000d\u000a\uad6d\uc5ed\ud559\ud68c\uace0, \u2026(\uc911\ub7b5)\u2026 \ud658\uacbd\ubd80\uac00 \uc0ac\uc2e4 \uac70\ubd80\ud558\uae30\uac00 \uc5b4\ub824\uc6cc\uc84c\uc8e0. \uadf8\ub798\uc11c 2019\ub144 11\uc6d4\u000d\u000a\uc5d0 \ucd5c\uc885\uc801\uc73c\ub85c \uc778\uacfc\uad00\uacc4\uac00 \uc788\ub2e4\ub294 \uc778\uc815\uc744 \ud55c \uac70\uc8e0.\u201d.(\uc815\uce58\uc778C, \uba74\uc811\uc77c\uc790: 2020.7.3.)\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c "," & 94\u000d\u000a4) \uc0ac\ud6c4\uc9c0\uc6d0 \ub2e8\uacc4: 2019\ub144 11\uc6d4~\ud604\uc7ac\u000d\u000a4\ub2e8\uacc4\ub294 \uc554 \ubc1c\ubcd1\uacfc \ud658\uacbd\uc624\uc5fc \uc0ac\uc774\uc758 \uc5ed\ud559\uc801 \uad00\uacc4\uc131\uc744 \uc778\uc815\ud55c \uc774\ud6c4\uc5d0 \ub4a4\ub530\ub974\ub294 \uc0ac\ud6c4\uc9c0\uc6d0 \u000d\u000a\ub2e8\uacc4\ub77c\uace0 \ud560 \uc218 \uc788\ub2e4. \uad6d\ubb34\ucd1d\ub9ac\ub97c \ud3ec\ud568\ud574 \uc804\ubd81\ub3c4\uc9c0\uc0ac, \uc775\uc0b0\uc2dc\uc7a5\uc758 \uacf5\uc2dd \uc0ac\uacfc\uc640 \ud568\uaed8 \uc0ac\ud6c4\u000d\u000a\ub300\ucc45\uc744 \uc218\ub9bd\u00b7\uc9d1\ud589\ud558\ub294 \ub2e8\uacc4\ub85c \ud604\uc7ac \uc9c4\ud589 \uc911\uc774\ub2e4. \uc5ed\ud559\uc801 \uad00\ub828\uc131\uc744 \uacf5\uc2dd\uc801\uc73c\ub85c \uc778\uc815\ubc1b\uc740 \uc9c1\u000d\u000a\ud6c4, \uc775\uc0b0\uc2dc\uc7a5\uacfc \uc804\ubd81\ub3c4\uc9c0\uc0ac\uc758 \uc0ac\uacfc\uac00 \uc788\uc5c8\ub2e4. \uc774\ub099\uc5f0 \uad6d\ubb34\ucd1d\ub9ac \ub610\ud55c \uad6d\ubb34\ud68c\uc758(2019\ub144 11\uc6d4 27\uc77c)\u000d\u000a\uc5d0\uc11c \u201c\uc5ed\ub300 \uc815\ubd80\ub97c \ub300\uc2e0\ud574 \uc8fc\ubbfc\uacfc \uad6d\ubbfc \uc5ec\ub7ec\ubd84\uaed8 \uc5c4\uc911\ud788 \uc0ac\uacfc\ub4dc\ub9b0\ub2e4.\u201d\ub77c\uace0 \uc9c1\uc811 \uc0ac\uacfc \uc758\uc0ac\u000d\u000a\ub97c \ud45c\uba85\ud588\uc73c\uba70, \uc720\uc0ac\ud55c \uc0ac\ub840\uc5d0 \ub300\ud55c \uc2e0\uc18d\ud55c \uc870\uc0ac \ubc0f \ub300\ucc45 \ub9c8\ub828\uc744 \uc57d\uc18d\ud588\ub2e4. \uadf8\ub9ac\uace0 \uc7a5\uc810\ub9c8\u000d\u000a\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uc640 \uad00\ub828 \uae30\uad00\ub4e4\uc774 \ucc38\uc5ec\ud55c \uc7a5\uc810\ub9c8\uc744 \ud6c4\uc18d\ub300\ucc45 \uad00\uacc4\uae30\uad00 \ud68c\uc758\ub97c \uac1c\ucd5c\ud558\uc5ec, \uc774\u000d\u000a\ub97c \ud1b5\ud574 \uc2e0\uc18d\ud558\uac8c 2023\ub144\uae4c\uc9c0 200\uc5b5 \uc6d0 \uaddc\ubaa8\ub97c \ud22c\uc785\ud558\ub294 \uc0ac\ud6c4\ub300\ucc45 \uc0ac\uc5c5\uc744 \ubc1c\ud45c\ud588\ub2e4. \uc5ec\u000d\u000a\uae30\uc5d0 \uc775\uc0b0\uc2dc\ub294 \uce5c\ud658\uacbd \uc7a5\uc810\ub9c8\uc744(\uce5c\ud658\uacbd \uc2dc\ubc94 \ub9c8\uc744) \uc870\uc131\uc744 \uc704\ud55c \ub300\ucc45\ub3c4 \uc81c\uc2dc\ud558\uc600\ub2e4.\u000d\u000a\ud53c\ud574\uc790\ub4e4\uc774 \ud53c\ud574\ub97c \uadf9\ubcf5\ud558\uae30 \uc704\ud55c \ud544\uc218 \uc694\uc18c\ub294 \ud53c\ud574\uc5d0 \ub300\ud55c \uacbd\uc81c\uc801 \ubc30\u00b7\ubcf4\uc0c1\uacfc \ub2f9\uc2e0\ub4e4\uc774 \u000d\u000a\ud53c\ud574\uc790\ub77c\ub294 \uc0ac\ud68c\uc801 \uc778\uc815social recognition\uc774 \uc911\uc694\ud558\ub2e4. \uc774\ub294 \uac00\ud574\uc790\uc5d0\uac8c \ubc95\uc801 \ucc45\uc784\uc744 \ubb3b\ub294 \uac83\u000d\u000a\uc5d0\uc11c\ubd80\ud130 \ucd9c\ubc1c\ud55c\ub2e4. \uadf8\ub7f0\ub370 \ucc45\uc784\uc774 \uba85\ud655\ud55c \uae08\uac15\ub18d\uc0b0\uc740 \uc774\ubbf8 \ud30c\uc0b0\ud574 \ubc84\ub838\uace0 \uc0ac\uc7a5 \ub610\ud55c \uc554\u000d\u000a\uc73c\ub85c \uc0ac\ub9dd\ud55c \uc0c1\ud669\uc774\ub2e4. \ub530\ub77c\uc11c \ud604\uc7ac \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc740 \uc5f0\ucd08\ubc15\uc744 \uae08\uac15\ub18d\uc0b0\uc5d0 \ud310\ub9e4\ud55c KT&G\uc640 \u000d\u000a\uad00\ub9ac \uac10\ub3c5\uc744 \uc18c\ud640\ud788 \ud55c \uc9c0\ubc29\uc815\ubd80\ub97c \uc0c1\ub300\ub85c \ubc95\uc801 \ucc45\uc784\uc744 \ubb3b\uace0 \uc788\ub2e4. \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc740 \ubbfc\uc8fc\ud654\u000d\u000a\ub97c \uc704\ud55c \ubcc0\ud638\uc0ac \ubaa8\uc784 \uc804\ubd81 \uc9c0\ubd80\uc758 \ub3c4\uc6c0\uc744 \ubc1b\uc544 KT&G\uc640 \uc804\ub77c\ubd81\ub3c4, \uc775\uc0b0\uc2dc\ub97c \uc0c1\ub300\ub85c 170\u000d\u000a\uc5b5 \uc6d0 \uaddc\ubaa8\uc758 \uc190\ud574\ubc30\uc0c1\uc18c\uc1a1\uc744 \uc81c\uae30\ud588\ub2e4. \ub610\ud55c \uc775\uc0b0\uc2dc\ub97c \uc0c1\ub300\ub85c\ub294 \uac10\uc0ac\uc6d0 \uacf5\uc775\uac10\uc0ac \uccad\uad6c\u000d\u000a\ub97c \uc81c\uae30\ud588\ub2e4.(2019\ub144 4\uc6d4) 9) \uac10\uc0ac\uc6d0 \uac10\uc0ac \uacb0\uacfc, 5\uac74\uc758 \uc704\ubc95\u00b7\ubd80\ub2f9\ud55c \uc0ac\ud56d\uc774 \ud655\uc778\ub418\uc5c8\ub2e4. \uc774\uc5d0 \uc775\u000d\u000a\uc0b0\uc2dc\ub294 \uc778\ud5c8\uac00 \uc5c5\ubb34 \ubc0f \uc9c0\ub3c4\u00b7\uac10\ub3c5 \uc5c5\ubb34\ub97c \ubd80\uc801\uc808\ud558\uac8c \ucc98\ub9ac\ud55c \uacf5\ubb34\uc6d0 1\uba85\uc744 \ubcf4\uc9c1 \ud574\uc784\ud558\uace0, \u000d\u000a\ub2e4\ub978 \uacf5\ubb34\uc6d0 2\uc778\uc740 \uc9d5\uacc4\uc704\uc6d0\ud68c\uc5d0 \ud68c\ubd80\ud588\ub2e4. \uc8fc\ubbfc\ub4e4\uc740 \uacf5\ubb34\uc6d0\uc5d0 \ub300\ud55c \uc9d5\uacc4\uc5d0 \ub108\ubb34 \uac00\ubccd\ub2e4\u000d\u000a9\u3000\uc18c\uc1a1 \uc804\ubd80\ud130 \uc7a5\uc810\ub9c8\uc744 \uc8fc\ubbfc\ub4e4\uc774 KT&G \ub97c \ucc3e\uc544\uac00 \ud56d\uc758 \uc2dc\uc704\ub3c4 \ud558\uace0 \uba74\ub2f4\uc744 \uc694\uccad\ud558\uae30\ub3c4 \ud588\ub2e4 . \ud604\uc7ac\uae4c\uc9c0 \uacf5\uc0ac \uce21\u000d\u000a\uc740 \ubb34\ub300\uc751\uc73c\ub85c \uc77c\uad00\ud558\uace0 \uc788\ub2e4 . \uc774\ub294 2020 \ub144 10 \uc6d4 7 \uc77c \uc5f4\ub9b0 \uad6d\ud68c \ud658\uacbd\ub178\ub3d9\uc704\uc6d0\ud68c \uad6d\uc815\uac10\uc0ac\uc5d0\uc11c\ub3c4 \ud655\uc778\ud560 \uc218 \uc788\ub2e4 . \u000d\u000a\ud658\uacbd\ub178\ub3d9\uc704\uc6d0\ud68c \uad6d\uc815\uac10\uc0ac\uc5d0\uc11c \u2018\uc7a5\uc810\ub9c8\uc744 \uc9d1\ub2e8 \uc554 \ubc1c\ubcd1\uc774 \ub204\uad6c\uc758 \ucc45\uc784\uc774\ub0d0\u2019\ub77c\ub294 \uad6d\ud68c\uc758\uc6d0\uc758 \uc9c8\ubb38\uc5d0 KT&G \ub300\ud45c\ub294 \u201c\uac1c\u000d\u000a\uc778\uc801\uc73c\ub85c \uac00\uc2b4 \uc544\ud504\uc9c0\ub9cc , \ub204\uad6c\uc758 \ucc45\uc784\uc778\uc9c0 \ub9d0\ud558\uae30\ub294 \uc5b4\ub835\ub2e4 .\u201d, \u201c\ud68c\uc0ac \ucc28\uc6d0\uc5d0\uc11c\ub294 \ubc95\uc801\uc778 \uadfc\uac70\uac00 \uc788\uc5b4\uc57c \ud55c\ub2e4 .\u201d, \u201c\uc218\u000d\u000a\uc0ac\ub97c \ubc1b\uac70\ub098 \uc870\uc0ac\ub97c \ubc1b\uc744 \ub54c\ub294 \ucd5c\ub300\ud55c \uc131\uc2e4\ud558\uac8c \uc790\ub8cc \uc81c\ucd9c\uc5d0 \uc751\ud558\uaca0\ub2e4 .\u201d \ub77c\ub294 \uc6d0\ub860\uc801 \uc218\uc900\uc758 \ub2f5\ubcc0\uc73c\ub85c \uc77c\uad00\ud588\ub2e4 . \uad00\u000d\u000a\ub828 \uc9c8\ubb38\uc744 \ud55c \uad6d\ud68c\uc758\uc6d0\uc740 \ud658\uacbd\uad00\ub9ac\uacf5\ub2e8\uc758 \uc790\ub8cc\ub97c \uadfc\uac70\ud558\uc5ec KT&G \uc5d0\uc11c 2009~2018 \ub144 \uc804\uad6d\uc5d0 \uc720\ud1b5\ud55c \uc5f0\ucd08\ubc15 \ubb3c\ub7c9 \u000d\u000a5,367 \ud1a4 \uc911\uc5d0\uc11c 2,242 \ud1a4\uc774 \uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5\uc73c\ub85c \ubc18\uc785\ub418\uc5c8\uace0 , \uc774\ub85c \uc778\ud574 KT&G \uac00 6 \uc5b5 2,700 \uc5b5 \uc6d0\uc758 \uc218\uc775\uc744 \u000d\u000a\uc5bb\uc5c8\uc73c\uba70 , KT&G \uac00 2018 \ub144 \uc5f0\ucd08\ubc15\uc758 \ubc1c\uc554 \uc704\ud5d8\uc131\uc744 \uc778\uc9c0\ud55c \ud6c4\uc5d0\ub3c4 1 \ub144 \ub354 \uc720\ud1b5\ud588\ub2e4\ub294 \uc810\uc744 \uc9c0\uc801\ud558\uc600\ub2e4 ( \uc774\uc131\ud76c \u000d\u000a2020.10.7, \u201c\uc7a5\uc810\ub9c8\uc744 \u2018\uc9d1\ub2e8 \uc554 \ubc1c\ubcd1\u2019 \uc5d0 KT&G \ub300\ud45c \u2018\uac1c\uc778\uc801\uc73c\ub85c \uc720\uac10 , \ud558\uc9c0\ub9cc\u2026\u2019\u201d , \uac80\uc0c9\uc77c : 2020.10.10).","94 95\u000d\u000a\ub294 \ub73b\uc744 \ubc1c\ud45c\ud588\ub2e4. \uacbd\uc81c\uc801 \ubc30\uc0c1\uc744 \ub458\ub7ec\uc2f8\uace0\ub294 \ud5a5\ud6c4 \ub9e4\uc6b0 \uc5b4\ub824\uc6b4 \ubc95\uc801 \uc18c\uc1a1\uc774 \uc9c4\ud589\ub420 \uac83\uc73c\u000d\u000a\ub85c \ubcf4\uc774\uba70, \uc18c\uc1a1\uc758 \uacb0\uacfc\uc5d0 \ub530\ub77c \uadf8\ub3d9\uc548 \uc8fc\ubbfc\ub4e4\uc774 \ubc1b\uc740 \ud53c\ud574\ub97c \ubcf4\uc0c1\ubc1b\uc744 \uc218\ub3c4 \uc788\uace0 \uadf8\ub807\uc9c0 \u000d\u000a\uc54a\uc744 \uc218\ub3c4 \uc788\ub2e4.\u000d\u000a\uc0ac\ud6c4\uc9c0\uc6d0 \ub2e8\uacc4\uc5d0\uc11c\ub294 \uc7a5\uc810\ub9c8\uc744 \ubbfc\uac04\ud611\uc758\uccb4\uc758 \uc5ed\ud560 \ub610\ud55c \ubcc0\ud654\ud558\uace0 \uc788\ub2e4. \uc5ed\ud559\uc801 \uad00\ub828\uc131\u000d\u000a\uc744 \uc778\uc815\ubc1b\uae30 \uc704\ud55c \uacfc\uc815\uc5d0\uc11c\ub294 \ubbfc\uac04\uc704\uc6d0\ub4e4\uc758 \uc804\ubb38\uc131\uc5d0 \ubc14\ud0d5\uc73c\ub85c \ub454 \ud65c\ub3d9\uc774 \uc911\uc2ec\uc774\uc5c8\ub2e4. \uc989 \u000d\u000a\ubbfc\uac04\uc704\uc6d0\ub4e4\uc758 \uc804\ubb38\uc131\uc774 \uc911\uc694\ud588\ub2e4. \ud558\uc9c0\ub9cc \uc0ac\ud6c4\ubcf5\uc6d0 \uacfc\uc815\uc5d0\uc11c\ub294 \ubcf5\uc6d0\uc758 \uc8fc\uccb4\uc774\uc790 \ub300\uc0c1\uc778 \u000d\u000a\ub9c8\uc744 \uc8fc\ubbfc\uacfc\uc758 \uc18c\ud1b5\uc774 \ub354\uc6b1 \uac15\uc870\ub420 \uc218\ubc16\uc5d0 \uc5c6\ub2e4. \ubbfc\uad00\ud611\uc758\uccb4\uc5d0 \uc8fc\ubbfc\ub300\ud45c\uac00 \ucc38\uc5ec\ud558\uace0 \uc788\uc9c0\u000d\u000a\ub9cc, \uc774\ub4e4\ub9cc\uc73c\ub85c \uc8fc\ubbfc\uacfc \uc18c\ud1b5\ud558\uae30\uc5d0\ub294 \ubd80\uc871\ud55c \uce21\uba74\uc774 \uc788\ub2e4. \uc8fc\ubbfc\uacfc\uc758 \uc18c\ud1b5\uc744 \uac15\ud654\ud558\uae30 \uc704\ud558\u000d\u000a\uc5ec \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\ub294 \uad00\ub828 \ud68c\uc758\ub97c \ub9c8\uc744\uc5d0\uc11c \uac1c\ucd5c\ud558\uae30\ub3c4 \ud588\ub2e4. \uc774\ub97c \ud1b5\ud574 \ud68c\uc758\ub0b4\uc6a9\ub3c4 \u000d\u000a\uacf5\uac1c\ud558\uace0 \uc8fc\ubbfc\ub4e4\uc758 \ubbfc\uc6d0\uc0ac\ud56d\ub3c4 \uc9c1\uc811 \uccad\ucde8\ud558\uc5ec \uc815\ubcf4 \ubc0f \uc18c\ud1b5 \ubd80\uc871\uc5d0\uc11c \uc62c \uc218 \uc788\ub294 \uac08\ub4f1\uacfc \u000d\u000a\uc720\uc5b8\ube44\uc5b4\ub97c \uc0ac\uc804\uc5d0 \uad00\ub9ac\ud558\ub294 \ud6a8\uacfc\ub97c \uc5bb\uc744 \uc218 \uc788\uc744 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4. \uadf8\ub9ac\uace0 \uacf5\uac1c\uc801 \uc7a5\uc18c\uc5d0\uc11c \u000d\u000a\uc790\uc2e0\ub4e4\uc758 \uc758\uacac\uc744 \uac1c\uc9c4\ud568\uc73c\ub85c\uc368 \uacf5\ub3d9\uccb4 \ud68c\ubcf5\uacfc \uad00\ub828\ub41c \uacf5\uc801 \ub17c\uc758\ub97c \ucd09\ubc1c\ud560 \uc218\ub3c4 \uc788\ub2e4. \uc5ed\ud559\u000d\u000a\uc870\uc0ac \ub2e8\uacc4\uc5d0\uc11c \ubbfc\uad00\ud611\uc758\uccb4\uc758 \ubbfc\uac04\uc704\uc6d0\ub4e4\uc740 \uc8fc\ubbfc\ub4e4\uc744 \ud6c4\uc6d0\ud558\ub294 \uc5ed\ud560\uc744 \uc218\ud589\ud55c \uce21\uba74\uc774 \uac15\ud588\u000d\u000a\ub2e4. \uc989 \ud544\uc694\ud55c \ubcf4\ub3c4\uc790\ub8cc, \uc131\uba85\uc11c \uc791\uc131\uc744 \ud3ec\ud568\ud574 \uc2dc\uc704\uc5d0 \ud544\uc694\ud55c \uac83\uc774 \uc788\uc73c\uba74 \ubbfc\uad00\ud611\uc758\uccb4\uc758 \u000d\u000a\ubbfc\uac04\uc704\uc6d0\ub4e4\uc774 \uc9c0\uc6d0\ud558\uace0 \uc8fc\ubbfc\ub4e4\uc774 \uc804\uba74\uc5d0\uc11c \uc2f8\uc6b0\ub294 \uadf8\ub7f0 \uad6c\uc870\uc600\ub2e4. \ud558\uc9c0\ub9cc \ubcf5\uad6c\ub2e8\uacc4\uc5d0\uc11c\ub294 \u000d\u000a\ud6c4\uc6d0\uc790\uc5d0\uc11c \uc8fc\ubbfc\ub4e4\uc758 \uc774\uae30\uc801 \uc695\ub9dd\uc744 \uc870\uc815\ud558\uace0, \uacf5\uc801\uc774\uba74\uc11c \ud569\ub9ac\uc801 \ud310\ub2e8\uc744 \uc720\uc778\ud558\ub294 \uc870\uc815\u000d\u000a\uc790\ub85c\uc11c \ud574\uc57c \ud560 \uc5ed\ud560\uc774 \uac15\ud654\ub418\ub294 \ubc29\ud5a5\uc73c\ub85c \ubcc0\ud654\ud574 \uac00\uace0 \uc788\ub294 \uac83\uc73c\ub85c \ubcf4\uc778\ub2e4.\u000d\u000a\u201c\uc800\ud76c\uac00 \uc5bc\ub9c8 \uc804\uc5d0 32\ucc28 \ubbfc\uad00\ud611\uc758\ud68c(2020\ub144 6\uc6d4 20\uc77c \uacbd)\ub97c \uc7a5\uc810\ub9c8\uc744\uc5d0\uc11c \ud588\uc5b4\uc694. \u2026(\uc911\u000d\u000a\ub7b5)\u2026 \uac70\uae30\uc11c \ud588\ub358 \uc774\uc720\uac00 \uc18c\ud1b5\uc5d0 \uc774\uc720\uac00 \uc788\uc5b4\uc694. \uc8fc\ubbfc\ub300\ucc45\uc704\uc5d0 \ub9c8\uc744 \uc774\uc7a5\uc774\ub791 \uc8fc\ubbfc\ub3c4 \u000d\u000a\uc788\uc9c0\ub9cc \uc6b0\ub9ac\uac00 \ud68c\uc758\ud558\ub294 \ub0b4\uc6a9\uc774 \uc8fc\ubbfc\ub4e4\ud55c\ud14c \uc798 \uc804\ub2ec\uc774 \uc548 \ub418\uae30\ub3c4 \ud558\uace0, \uc8fc\ubbfc\ub4e4\uc774 \uac16\uace0 \u000d\u000a\uc788\ub294 \uace0\ubbfc\uc774 \uc798 \ud761\uc218\uac00 \uc548 \ub418\uace0, \uc0dd\uac01\ub3c4 \uc5ec\ub7ec \uac00\uc9c0\uc778 \uac83 \uac19\uace0\uc694. \uadf8 \uc804\uc5d0\ub3c4 \uadf8\ub7f0 \uc6b0\ub824\ub97c \u000d\u000a\ud588\uc8e0. \ub300\ucc45\uc704\uc6d0\uc7a5\uc774\ub098 \ub300\ucc45\uc704\uc6d0\ud68c\uc5d0 \ucc38\uc5ec\ud558\ub294 \uc5b4\ub974\uc2e0 \uadf8\ub8f9\uc774 \uc788\uace0, \ub098 \ubab0\ub77c\ub77c \ube44\ud310\ud558\u000d\u000a\uace0 \uad00\ub9dd\ud558\ub294 \uadf8\ub8f9\ub3c4 \uc788\uac70\ub4e0\uc694. \ub9c8\uc744\uc758 \uc18c\ud1b5\uc744 \uc5b4\ub5bb\uac8c \ud68c\ubcf5\uc2dc\ud0a4\ub290\ub0d0 \ud558\ub294 \ubb38\uc81c \ub54c\ubb38\uc5d0. \u000d\u000a\uc800\ud76c\uac00 \uadf8\ub798\uc11c \uc704\uc6d0\uc7a5 \uac19\uc740 \ubd84\ub4e4\ud55c\ud14c, \ub9c8\uc744\uc5d0 \uac00\uba74 \uc8fc\ubbfc\ub4e4\ub07c\ub9ac \uc758\uc0ac\ub97c \ub17c\uc758\ud558\uace0 \uacb0\u000d\u000a\uc815\ud560 \uc218 \uc788\ub294 \ud14c\uc774\ube14\uc744 \ub9cc\ub4e4\uc5b4\ub77c, \uc804\ub2ec\ub3c4 \ud558\uace0\uc694. \uadf8\ub7f0\ub370 \uc704\uc6d0\uc7a5 \uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \uadf8\uac8c \u000d\u000a\uc798 \uc548 \ub418\ub294 \uac70 \uac19\uc544\uc11c \uc5bc\ub9c8 \uc804\uc5d0\ub294 \uac70\uae30\uc11c \ud68c\uc758\ub97c \ud55c \uac83\uc774\uc8e0.\u201d.(\uc2dc\ubbfc\uc6b4\ub3d9\uac00B, \uba74\uc811\uc77c\uc790: 2020.6.22.)\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c "," & 96\u000d\u000a\u201c\ud55c 20\uba85\uc774 \ucc38\uc11d\uc744 \ud588\uc8e0. \ubbfc\uc6d0 \uc598\uae30\ub3c4 \ub4e3\uace0, \ud68c\uc758\ud558\ub294 \uac83\ub3c4 \uc598\uae30\ud558\uace0, \ud589\uc815(\uc9c0\ubc29\uc815\ubd80)\u000d\u000a\ud55c\ud14c \uc8fc\ubbfc\ub4e4 \uc758\uacac \ubc18\uc601\ud558\ub77c\uace0\ub3c4 \ud558\uace0, \uc5b4\uc81c \uc2dc\uc7a5\ud558\uace0 \uc8fc\ubbfc\ub300\ucc45 \uc704\uc6d0\uc7a5\ud558\uace0\ub3c4 \ub9cc\ub0ac\uc5b4\u000d\u000a\uc694. \uc81c\uac00 \uba74\ub2f4\uc744 \ud55c \ubc88 \ud558\ub77c\uace0 \uc598\uae30\ub97c \ud588\uc5b4\uc694. \uc8fc\ubbfc\ub4e4\uc774 \uc6d0\ud558\ub294 \uc0ac\uc5c5 \uc598\uae30\ub3c4 \ud558\uace0, \ub9c8\u000d\u000a\uc744\uc744 \ud68c\ubcf5\uc2dc\ud0a4\uace0, \uc0ac\uc5c5 \uc9c4\ud589\uc744 \ud558\ub824\uba74 \uc0ac\ub78c\ub3c4 \ud544\uc694\ud55c\ub370 \ub9c8\uc744 \uac04\uc0ac\ub3c4 \ud558\ub098 \ub2ec\ub77c\uace0 \ud558\u000d\u000a\uace0 \uc9c4\uc9c0\ud558\uac8c \ub300\ud654\ub97c \ud558\ub77c\uace0\uc694. \uc870\ub9cc\uac04 \uc2dc\uc7a5\uc774 \ub9c8\uc744 \ubc29\ubb38\uc744 \ud55c\ub2e4\uace0 \ud558\ub354\ub77c\uace0\uc694. \uadf8\ub798\u000d\u000a\uc11c \uc6d0\ud3ec\uc778\ud2b8\ub85c \ub05d\ub0b4\uc57c\uc9c0 \uc790\uafb8 \uc774 \uc0ac\ub78c \uc800 \uc0ac\ub78c \ub9d0\ud558\uc9c0 \ub9d0\uace0. \uadf8\ub798\uc11c \uc800\ud76c\ub294, \ub9c8\uc744\uc774 \u000d\u000a\uc5b4\uca0c\ub4e0 \uc815\uc0c1\uc801\uc73c\ub85c \ub3cc\uc544\uac00\uae30 \uc704\ud574\uc11c\ub294 \uc8fc\ubbfc\ub4e4\uc774 \uc911\uc694\ud558\uc796\uc544\uc694. \ud658\uacbd \ud68c\ubcf5\uc2dc\ud0a4\ub294 \uac70\u000d\u000a\uc57c \uadf8\uac83\ub3c4 \uadf8\uac70\uc9c0\ub9cc. \uc8fc\ubbfc\ub4e4\ub3c4 \ub098\ub984\ub300\ub85c \uc2b9\ub9ac\ud55c \uc790\ub85c\uc368 \uc2b9\ub9ac\uac10\ub3c4 \uac00\uc9c0\uace0, \ub9c8\uc744\uc5d0 \ub300\u000d\u000a\ud55c \uc790\ubd80\uc2ec\ub3c4 \uac00\uc9c0\uace0. \ub3c8 \ub54c\ubb38\uc5d0 \uc2f8\uc6b0\uace0 \uadf8\ub7f0 \uac8c \uc5c6\uc5b4\uc694. \ud53c\ud574 \uad6c\uc81c\ub3c4 \uc548 \ubc1b\ub294\ub2e4\uace0 \ud558\u000d\u000a\uace0, \uc18c\uc1a1\uc740 \ud558\uace0 \uc788\uc9c0\ub9cc \ub9c8\uc744 \uc8fc\ubbfc\ub4e4 \uac04\uc758 \uc18c\ud1b5\uc744 \uc5b4\ub5bb\uac8c \uc798\ud560 \uac83\uc778\uc9c0 \uc598\uae30\ud558\uc790\uace0 \ud588\u000d\u000a\uc8e0.\u201d.(\uc2dc\ubbfc\uc6b4\ub3d9\uac00B, \uba74\uc811\uc77c\uc790: 2020.6.22.)\u000d\u000a3. \uc5f0\uad6c\uacb0\uacfc\uc758 \ud559\ubb38\uc801 \u00b7 \uc0ac\ud68c\uc801 \ud65c\uc6a9\u000d\u000a\uc7a5\uc810\ub9c8\uc744 \uc0ac\ub840\ub97c \ub193\uace0 \ubcfc \ub54c \ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uc740 \ub2e8\uc21c\ud55c \uacfc\ud559\uc801 \uac80\uc99d\uacfc\u000d\u000a\uc815\uc744 \ub118\uc5b4\uc120 \ubb38\uc81c\uc784\uc744 \ud655\uc778\ud560 \uc218 \uc788\ub2e4. \uc989 \uc0c1\ud638 \ub300\ub9bd\ud558\ub294 \uacfc\ud559\uc801 \ub17c\uc7c1\uc778 \ub3d9\uc2dc\uc5d0 \ub2e4\uc591\ud55c \ud589\u000d\u000a\uc704\uc790\ub4e4\uc774 \uac1c\uc785\ud558\ub294 \uc815\uce58\uc0ac\ud68c\uc801 \uacfc\uc815\uc774. \uc7a5\uc810\ub9c8\uc744\uc740 \uacf5\ub3d9\uccb4 \ub0b4\ubd80\uc758 \ub0ae\uc740 \ubcf5\uc6d0\ub825\uc740 \uc7a5\uc810\ub9c8\u000d\u000a\uc744 \ubbfc\uad00\ud611\uc758\uccb4\ub77c\ub294 \uac70\ubc84\ub10c\uc2a4\ub97c \uad6c\ucd95\ud558\uba74\uc11c \uc77c\uc815 \ubd80\ubd84 \uadf9\ubcf5\ud560 \uc218 \uc788\uc5c8\ub2e4. \ud2b9\ud788 \ubbfc\uad00\ud611\uc758\u000d\u000a\uccb4\uc5d0 \ucc38\uc5ec\ud55c \ubbfc\uac04\uc704\uc6d0\ub4e4\uc740 \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc774 \ubd80\uc871\ud55c \uacfc\ud559\uc801 \uc9c0\uc2dd\uacfc \uc815\uce58\uc0ac\ud68c\uc801 \uc790\uc6d0\uc744 \ub3d9\uc6d0\u000d\u000a\ud560 \uc218 \uc788\ub294 \uc0ac\ud68c\uc801 \uc5f0\uacb0\ub9dd\uc73c\ub85c \ud65c\uc6a9\ub418\uc5c8\ub2e4. \ubbfc\uad00\ud611\uc758\uccb4 \uad6c\uc131\uc740 \uc9c0\ubc29\uc815\ubd80, \ud53c\ud574 \uc8fc\ubbfc, \uc9c0\uc5ed\u000d\u000aNGO \ubc0f \uc804\ubb38\uac00\ub4e4 \uc0ac\uc774\uc758 \ud611\ub825 \uc9c0\ud5a5\uc801 \uc0c1\ud638\uc791\uc6a9\uc744 \ucd09\uc9c4\ud588\uc73c\uba70, \ud658\uacbd\ubd80\uc758 \uc8fc\ubbfc\uac74\uac15\uc601\ud5a5\u000d\u000a\uc870\uc0ac \ud574\uc11d\uacfc\uc815\uc5d0\ub3c4 \uac1c\uc785\ud560 \uc218 \uc788\ub294 \uc778\uc801\uc790\uc6d0 \ubc0f \uacfc\ud559\uc801 \uc9c0\uc2dd, \uc815\uce58\uc0ac\ud68c\uc801 \uc601\ud5a5\ub825\uc744 \ud655\ubcf4\u000d\u000a\ud574 \uc8fc\uc5c8\ub2e4.\u000d\u000a\uadf8\ub807\ub2e4\uba74 \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\ub294 \uc5b4\ub5bb\uac8c \ud53c\ud574 \uc8fc\ubbfc\uc744 \uc9c0\uc6d0\ud558\uae30 \uc704\ud55c \uc0ac\ud68c\uc801 \uc870\uc815\uae30\uad6c\uc758 \u000d\u000a\uc704\uc0c1\uc744 \uac16\uace0 \ud65c\ub3d9\ud560 \uc218 \uc788\uc5c8\ub294\uac00? \uc2e4\uc81c \uac70\ubc84\ub10c\uc2a4 \uacfc\uc815\uc5d0 \ucc38\uc5ec\ud558\ub294 \ub2e4\uc591\ud55c \ud589\uc704\uc790\ub4e4(\ud639\uc740 \uc774\ud574\ub2f9\u000d\u000a\uc0ac\uc790\ub85c \ubd80\ub974\ub358) \uc0ac\uc774\uc5d0 \ub3d9\uc6d0\ud560 \uc218 \uc788\ub294 \ubb3c\uc801\u00b7\uc778\uc801\uc790\uc6d0, \uc9c0\uc2dd\uacfc \uc815\ubcf4 \ub4f1\uc5d0 \uc788\uc5b4 \u2018\uad8c\ub825\uc758 \ubd88\ud3c9\ub4f1\u2019\uc774 \u000d\u000a\uc874\uc7ac\ud55c\ub2e4. \uc989 \ud589\uc704\uc790\ub4e4\uc774 \ub3d9\ub4f1\ud55c \uc0c1\ud0dc\uc5d0\uc11c \ucc38\uc5ec\ud558\ub294 \uac83\uc774 \uc544\ub2c8\uae30 \ub54c\ubb38\uc5d0 \uac70\ubc84\ub10c\uc2a4 \uacfc\uc815\uc5d0 ","96 97\u000d\u000a\uc0c1\ub300\uc801\uc73c\ub85c \ub354 \uac15\ud55c \uad8c\ub825\uc744 \uc9c0\ub2cc \ud589\uc704\uc790\ub4e4\uc5d0 \uc758\ud574 \uc870\uc815\ub420 \uac00\ub2a5\uc131\uc774 \ud06c\ub2e4. \uc774\ub294 \uad8c\ub825\uc774 \uc57d\u000d\u000a\ud55c \ud589\uc704\uc790\uc758 \ucc98\uc9c0\uc5d0\uc11c \ubcf4\uba74, \uac70\ubc84\ub10c\uc2a4\ub294 \ud615\uc2dd\uc801 \ucc38\uc5ec\uc5d0 \ubd88\uacfc\ud558\uba70 \uc624\ud788\ub824 \uc8fc\ub3c4\uad8c\uc744 \uac00\uc9c4 \uac15\u000d\u000a\ud55c \ud589\uc704\uc790\uc758 \ud589\ub3d9\uacfc \uacb0\uc815\uc5d0 \uc815\ub2f9\uc131\ub9cc\uc744 \uc81c\uacf5\ud558\uac8c \ub41c\ub2e4. \uc7a5\uc810\ub9c8\uc744 \uc8fc\ubbfc\ub4e4\uc758 \uc0ac\ud68c\uacbd\uc81c\uc801 \ucde8\u000d\u000a\uc57d\uc131\uc744 \uace0\ub824\ud574 \ubcfc \ub54c \uc815\ubd80 \ubc0f \uac00\ud574 \uae30\uc5c5\uacfc \ube44\uad50\ud574 \ub3d9\uc6d0\ud560 \uc218 \uc788\ub294 \uc790\uc6d0\uc758 \uc591\uacfc \uc9c8\uc774 \ud604\uc800\ud558\u000d\u000a\uac8c \ub5a8\uc5b4\uc9c4\ub2e4. \uc989 \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc758 \ucc98\uc9c0\uc5d0\uc11c \ubcf4\uba74 \uc790\uc2e0\ub4e4\uc758 \ucde8\uc57d\ud55c \uad8c\ub825\uc790\uc6d0\uc744 \ubcf4\uc644\ud558\ub294 \ubc29\ud5a5\u000d\u000a\uc73c\ub85c \uac70\ubc84\ub10c\uc2a4 \ucc38\uc5ec\uc790\uac00 \uad6c\uc131\ub420 \ud544\uc694\uac00 \uc788\ub294 \uac83\uc774\ub2e4. \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\ub294 \ud53c\ud574\uc790\ub4e4\uc5d0\u000d\u000a\uac8c \uc6b0\ud638\uc801\uc778 \uc9c0\uc5edNGO\uc640 \uc804\ubb38\uac00\uc758 \ucc38\uc5ec\ub97c \ud1b5\ud574 \uc8fc\ubbfc\ub4e4\uc5d0\uac8c \ubd80\uc871\ud55c \uacfc\ud559\uc801 \uc9c0\uc2dd\uacfc \uc815\uce58\uc0ac\u000d\u000a\ud68c\uc801 \uc790\uc6d0 \ub3d9\uc6d0 \ub2a5\ub825\uc744 \ubcf4\uc644\ud574 \uc8fc\uc5c8\ub2e4. \uc989 \ubd88\ud3c9\ub4f1\ud55c \uad8c\ub825\uad00\uacc4\uc758 \ud574\uc18c\ub294 \ud611\ub825\uc744 \ud1b5\ud55c \ubb38\uc81c \u000d\u000a\ud574\uacb0\uc758 \uac00\ub2a5\uc131\uc744 \ub192\uc5ec \uad8c\ub825\uc774 \uc57d\ud55c \ud589\uc704\uc790\ub4e4\uc758 \uac70\ubc84\ub10c\uc2a4 \ucc38\uc5ec\ub97c \uc720\ub3c4\ud588\ub2e4. \uc774\ub294 \uc7a5\uc810\ub9c8\uc744 \u000d\u000a\ubbfc\uad00\ud611\uc758\uccb4\uac00 \uad6c\uc131 \ucd08\uae30\ubd80\ud130 \uae30\uacc4\uc801 \uc911\ub9bd\uc774 \uc544\ub2c8\ub77c \ud53c\ud574\uc790 \uad00\uc810\uc744 \ucde8\ud588\uae30 \ub54c\ubb38\uc5d0 \uac00\ub2a5\ud55c \u000d\u000a\uac83\uc774\uc5c8\ub2e4. \uc7a5\uc810\ub9c8\uc744 \ubbfc\uad00\ud611\uc758\uccb4\uac00 \ud611\ub825 \uc9c0\ud5a5\uc801\uc73c\ub85c \uc6b4\uc601\ub418\uba74\uc11c \ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc778\uc815\uacfc\uc815\u000d\u000a\uc5d0\uc11c \ud53c\ud574 \uc8fc\ubbfc\ub4e4\uc758 \uacac\ud574\ub97c \uc804\ub2ec\ud558\ub294 \uacf5\uc2dd\uc801\uc778 \ud1b5\ub85c\ub85c \uc911\uc694\ud55c \uad6c\uc2e4\uc744 \ud588\ub2e4.\u000d\u000a\ud658\uacbd\uc624\uc5fc\ud53c\ud574\uc758 \uc5ed\ud559\uc801 \uc778\uc815\uacfc\uc815\uacfc \ud589\uc704\uc790\ub4e4\uc758 \ub300\uc751 : \uc775\uc0b0\uc7a5\uc810\ub9c8\uc744 \ube44\ub8cc\uacf5\uc7a5 \uc0ac\ub840\ub97c \uc911\uc2ec\uc73c\ub85c \u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","98 & \u000d\u000a\ud55c\uad6d\uc5ec\uc131\ub2e8\uccb4\uc5f0\ud569 / \ud55c\uad6d\uc5ec\uc131\uc7ac\ub2e8, \ud3c9\ub4f1\uc0ac\ud68c\uc870\uc131\uc0ac\uc5c5, 2022\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \uc0b6\uc744 \uac00\ub85c\uc9c0\ub974\ub294\u000d\u000a\ubcf5\ud569\ucc28\ubcc4 \u000d\u000a1. \uc5f0\uad6c \ubb38\uc81c\uc758\uc2dd\uacfc \ubaa9\uc801\u000d\u000a\u2022 \ucc28\ubcc4\uc5d0 \ub300\ud55c \uc0ac\ud68c\uc801 \uc778\uc2dd \ud655\uc7a5\uacfc \ucf54\ub85c\ub09819\ub85c \ub354 \uc5f4\uc545\ud574\uc9c4 \uc5ec\uc131\ub178\ub3d9\uc758 \ubcf5\ud569\ucc28\ubcc4 \uac00\uc2dc\ud654\u000d\u000a\u00bb \uacbd\uc81c\uc704\uae30\uc5d0\uc11c \uba3c\uc800 \uc77c\uc790\ub9ac\ub97c \uc783\ub294 \uc0ac\ub78c\uc740 \uc5ec\uc131\uc77c \ub9cc\ud07c \ub178\ub3d9\uc790\ub85c\uc11c \uc5ec\uc131\uc758 \uc704\uce58\ub294 \ucde8\u000d\u000a\uc57d\ud558\ub2e4. \ud2b9\ud788 \uc774\ubc88 \ucf54\ub85c\ub09819 \uc704\uae30\ub294 \uadf8\ub3d9\uc548 \ubc95\uacfc \uc815\ucc45\uc5d0\uc11c \uc0ac\uac01\uc9c0\ub300\ub85c \uba85\uba85\ub418\uac70\ub098\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \uc5ec\uc131\uc774\ub77c\ub294 \uc131 \uc815\uccb4\uc131 \uc774\uc678\uc5d0 \ub2e4\uc591\ud55c \uc815\uccb4\uc131\uc774 \uad50\ucc28\ud568\uc73c\ub85c\uc368 \ubc1c\uc0dd\ud558\ub294 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\u000d\u000a\uc5d0 \ub300\ud55c \ubcf5\ud569\ucc28\ubcc4\uc5d0 \uad00\ud55c \uc5f0\uad6c\uc774\ub2e4. \uc5f0\uad6c\ub97c \uc9c4\ud589\ud55c \ud55c\uad6d\uc5ec\uc131\ub2e8\uccb4\uc5f0\ud569\uc740 \ube44\uac00\uc2dc\uc801\uc778 \uc5ec\uc131\ucc28\ubcc4\uc758 \ubb38\u000d\u000a\uc81c\ub97c \u2018\ubcf5\ud569\ucc28\ubcc4\u2019\uc774\ub77c\ub294 \uc0c8\ub85c\uc6b4 \uad00\uc810\uc73c\ub85c \uac00\uc2dc\ud654\uc2dc\ud0b4\uc73c\ub85c\uc368 \ub2e8\uc21c\ud788 \uac1c\uc778\uc774 \uac10\ub0b4\ud574\uc57c\ud560 \ubb38\uc81c\ub85c \uadc0\uacb0\u000d\u000a\uc2dc\ud0a4\ub294 \uac83\uc774 \uc544\ub2cc \uc0ac\ud68c\uc801\uc778 \uc544\uc820\ub2e4\ub85c \ud655\uc7a5\ud558\uc5ec \uc0ac\ud68c\uac00 \ud568\uaed8 \uace0\ubbfc\ud574\uc57c\ud558\ub294 \ubb38\uc81c\ub85c \uc811\uadfc\ud558\uc600\ub2e4. \uc5ec\uc131\u000d\u000a\uc744 \ub458\ub7ec\uc2fc \ud639\uc740 \uc5ec\uc131\uc73c\ub85c\uc11c \uacbd\ud5d8\ud558\ub294 \ucc28\ubcc4\uc5d0 \uad00\ud55c \uc5f0\uad6c\ub294 \uc0c8\uc0bc\uc2a4\ub7fd\uc9c0 \uc54a\uc740 \uc5f0\uad6c\uc774\ub2e4. \ud558\uc9c0\ub9cc \uc5ec\uc131\u000d\u000a\uc774\ub77c\ub294 \uc131\ubcc4 \uc911\uc2ec\uc758 \ucc28\ubcc4\uc801 \uc694\uc778 \uc774\uc678\uc5d0 \ub2e4\uc591\ud55c \uc815\uccb4\uc131\uc5d0 \ub300\ud55c \ubd80\uac00\uc801\uc778 \uc694\uc778\uc774 \ub367\ubd99\uc5ec\uc9c0\uba74\uc11c \ubc1c\uc0dd\u000d\u000a\ud558\ub294 \ubcf5\ud569\ucc28\ubcc4\uc774\ub77c\ub294 \ud604\uc0c1\uc740 \uc6b0\ub9ac \uc0ac\ud68c\uc5d0 \ub9ce\uc740 \uc9c8\ubb38\uc744 \ub358\uc9c0\uace0 \uc788\ub2e4. \uc5ec\uc131\uc774\uba74\uc11c \uc774\uc8fc\ub178\ub3d9\uc790, \uc5ec\uc131\u000d\u000a\uc774\uba74\uc11c \uc131\uc18c\uc218\uc790, \uc5ec\uc131\uc774\uba74\uc11c 1\uc778\uac00\uad6c \ub4f1 \uc5ec\uc131\ub178\ub3d9\uc790\ub85c\uc11c \uac00\uc815, \uc9c1\uc7a5, \uc0ac\ud68c\uc5d0\uc11c \ubcf5\ud569\ucc28\ubcc4\uc744 \uacbd\ud5d8\u000d\u000a\ud55c 20\uba85\uc758 \uc0ac\ub840\ub294 \uc6b0\ub9ac \uc0ac\ud68c\uc758 \ubcf5\ud569\ucc28\ubcc4\uc758 \ub2e8\uba74\uacfc \ubbfc\ub0af\uc744 \uc5ec\uacfc \uc5c6\uc774 \uc555\ucd95\uc801\uc73c\ub85c \ubcf4\uc5ec\uc8fc\uace0 \uc788\ub2e4. \ucc28\u000d\u000a\ubcc4\uc758 \uc5ec\ub7ec \ub9e5\ub77d\uc744 \ub4dc\ub7ec\ub0b4\ub294 \uac83\uc774 \ubaa9\uc801\uc774\ub77c\ub294 \uc5f0\uad6c\uc9c4\uc758 \uc758\ub3c4\uac00 \uc120\uc815\uc704\uc6d0\ud68c\uc5d0\uac8c \uace0\uc2a4\ub780\ud788 \uc804\ud574\uc84c\ub2e4. \u000d\u000a20\uba85\uc758 \uc778\ud130\ubdf0 \ub300\uc0c1\uc790\ub4e4\uc744 \uc120\uc815\ud558\uae30\uae4c\uc9c0 \uace0\uc2ec\ud55c \uc5f0\uad6c\uc9c4\ub4e4\uc758 \uace0\ubbfc\uc774 \uc5ff\ubcf4\uc778\ub2e4. \ud604\uc7a5\ub2e8\uccb4\ub85c\uc11c\uc758 \u000d\u000a\uace0\uc720\ud55c \uac10\uac01\uacfc \uacbd\ud5d8 \ub355\ubd84\uc5d0 \ub354 \uc138\uc2ec\ud55c \uace0\ubbfc\uc744 \ud558\uc600\uc744 \uac83\uc774\ub2e4. \uc5f0\uad6c\uc9c4\ub4e4\uc740 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \u000d\u000a\uc0b6\ud130\uc5d0\uc11c \ub2f9\uc5f0\ud55c \uaddc\ubc94\uc73c\ub85c \uc874\uc7ac\ud55c \uae30\uc874\uc758 \uaddc\ubc94\uc5d0 \uade0\uc5f4\uc744 \ub0b4\uae30 \uc704\ud574\uc11c\ub294 \uc800\ud56d\uacfc \uc5f0\ub300\ub97c \uc704\ud55c \uc77c\uc0c1\u000d\u000a\uc801 \uc5f0\ub300\uc758 \uc2e4\ucc9c\uacfc \uc9d1\ub2e8\uc801 \uc2e4\ucc9c\uc73c\ub85c\uc11c\uc758 \uc815\uce58\uc801 \uc5f0\ub300\uac00 \ud544\uc694\ud568\uc744 \uac15\uc870\ud558\uace0 \uc788\ub2e4. \ub108\ubb34\ub098 \uc775\uc219\ud574\uc838\u000d\u000a\ubc84\ub9b0 \uace0\uc9c8\uc801\uc778 \uc0ac\ud68c \ubb38\uc81c\ub294 \ubaa8\uc21c\uc801\uc774\uac8c\ub3c4 \ub354 \uc774\uc0c1 \ubb38\uc81c\ub85c \ub290\uaef4\uc9c0\uc9c0 \uc54a\uc744 \uc815\ub3c4\ub85c \ubb38\uc81c\uc5d0 \ub300\ud55c \uac10\uac01\u000d\u000a\uc744 \ub454\ud558\uac8c \ub9cc\ub4e0\ub2e4. \uc5f0\uad6c\uc9c4\ub4e4\uc740 \uc774\ub7ec\ud55c \uc6b0\ub9ac\uc758 \uac10\uac01\uc744 \ub208\uce58 \ucc44\uae30\ub77c\ub3c4 \ud55c \ub4ef\uc774 \ubb34\uac10\ud55c \uc6b0\ub9ac\uc758 \uac10\uac01\uc744 \u000d\u000a\ub2e4\uc2dc \uc77c\uae68\uc6b0\ub77c\uace0 \uc77c\uce68\uc744 \uc8fc\ub294 \ub4ef \ud558\ub2e4. \uc774 \uc5f0\uad6c\uac00 \uc5ec\uc131\ub178\ub3d9\uc790\uc5d0 \uad00\ud55c \ubcf5\ud569\ucc28\ubcc4\uc744 \ub2e4\ub8ec \ub9cc\ud07c \uc5ec\uc131\uc6b4\u000d\u000a\ub3d9 \uc601\uc5ed \ubfd0\ub9cc \uc544\ub2c8\ub77c \uc778\uad8c\uc6b4\ub3d9, \ub178\ub3d9\uc6b4\ub3d9 \ub4f1 \uc5ec\ub7ec \uc601\uc5ed\uc758 \uc2dc\ubbfc\uc0ac\ud68c\uc640 \uc815\ucc45\uc785\uc548\uc790\ub4e4\uc5d0\uac8c\ub3c4 \ud30c\uc7a5\ub825\u000d\u000a\uc774 \ub9de\ub2ff\uae30\ub97c \uae30\ub300\ud55c\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720","98 99\u000d\u000a\uc798 \ubcf4\uc774\uc9c0 \uc54a\uc558\ub358 \uc5ec\uc131\ub178\ub3d9\uc790\uc5d0 \ub300\ud55c \ub300\ucc45 \ub9c8\ub828\uc774 \uc2dc\uae09\ud558\ub2e4\ub294 \uc0ac\uc2e4\uc744 \ubcf4\uc5ec\uc8fc\uc5c8\ub2e4. \u000d\u000a\uc131\ucc28\ubcc4\uc758 \uacbd\uc6b0, \uc5ec\uc131\uc6b4\ub3d9\uc758 \uc9c0\uc18d\uc801\uc778 \uacf5\ub860\ud654\ub85c \ub9ce\uc740 \uc5ec\uc131\ub4e4\uc774 \u2018\uc5ec\uc131\u2019\uc73c\ub85c\uc11c \uacbd\ud5d8\ud558\u000d\u000a\ub294 \ucc28\ubcc4\uc744 \uc124\uba85\ud558\uace0 \ud574\uc18c\ub97c \uc694\uad6c\ud558\ub294 \uc8fc\uccb4\uac00 \ub418\uae30\ub3c4 \ud558\uc9c0\ub9cc \uc5ec\uc131 \uc548\uc758 \ub2e4\uc591\ud55c \ucc28\uc774\u000d\u000a\uac00 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \ucc28\ubcc4\uc740 \uc544\uc9c1 \ub9ce\uc774 \uc758\ubbf8\ud654\ub418\uc9c0 \ubabb\ud588\ub2e4. \uc131\ucc28\ubcc4\uc801\uc778 \uc5ec\uc131 \ub178\ub3d9 \ud604\uc2e4\uc744 \u000d\u000a\ubc14\uafb8\uae30 \uc704\ud574\uc11c\ub294 \u2018\uc5ec\uc131\ub178\ub3d9\uc790\u2019\ub85c\uc11c \uacaa\ub294 \ucc28\ubcc4\uacfc \ub354\ubd88\uc5b4 \u2018\uc5ec\uc131\u2019 \uc774\uc678\uc758 \ub2e4\ub978 \ucc28\ubcc4\uc774 \u000d\u000a\uad50\ucc28\uc801\uc73c\ub85c \ubc1c\uc0dd\ud558\ub294 \ubcf5\ud569\ucc28\ubcc4\uc758 \uad00\uc810\uc5d0\uc11c \ubc14\ub77c\ubcfc \ub54c \uc5ec\uc131\ub4e4\uc758 \ucde8\uc57d\ud55c \ud604\uc2e4\uc740 \ub354 \u000d\u000a\uc798 \ub4dc\ub7ec\ub098\uace0 \ub354 \uc2e4\ud6a8\uc131 \uc788\ub294 \ub300\uc548\uc744 \uac00\uc9c8 \uc218 \uc788\ub2e4. \uc774 \uc5f0\uad6c\ub294 \uc820\ub354\uac00 \uc131\ubcc4\uc774\ubd84\ubc95\uc801 \u000d\u000a\uccb4\uacc4\uc5d0\uc11c \ub2e4\ub978 \ub9ce\uc740 \uc815\uccb4\uc131\uacfc \uacb0\ud569\ud558\uc5ec \ub2e4\uce35\uc801 \ucc28\ubcc4\uc744 \ubc1c\uc0dd\uc2dc\ud0a4\ub294 \uc0ac\uc2e4\uc744 \ub4dc\ub7ec\ub0c4\uc73c\u000d\u000a\ub85c\uc368 \ucc28\ubcc4\uc5d0 \ub300\ud55c \ubcf4\ub2e4 \uadfc\uc6d0\uc801 \ud574\uacb0\ubc29\uc2dd\uc5d0 \uc811\uadfc\uac00\ub2a5\ud558\uac8c \ud558\ub294 \ub370 \ubaa9\uc801\uc744 \ub450\uace0 \uc788\ub2e4.\u000d\u000a\u2022 \ubcf5\ud569\ucc28\ubcc4 \ub2f9\uc0ac\uc790\uac00 \ubcc0\ud654\uc758 \uc8fc\uccb4\ub85c\uc11c \uc11c\ub85c\ub97c \ud655\uc778\ud558\uace0 \uc5f0\ub300\uc758 \ub2e8\ucd08 \ub9cc\ub4e4\uae30 \u000d\u000a\u00bb \uc120\uac70 \ub54c\ub9c8\ub2e4 \uc0ac\ud68c\uc801 \uc57d\uc790\uc758 \ud3b8\uc5d0 \uc11c\uaca0\ub2e4\ub294 \uc815\uce58\uc778\uc5d0\uac8c \uc5ec\uc131\ub4e4\uc740 \ud568\uaed8 \uc0b4\uc544\uac00\uba70 \uc138\uc0c1\u000d\u000a\uc744 \ubc14\uafb8\ub294 \uc874\uc7ac\uc774\uae30\ubcf4\ub2e4\ub294 \u2018\ub0b4\uac00 \ub2f9\uc2e0\uc758 \uc5b4\ub824\uc6c0\uc744 \ud574\uacb0\ud574\uc904\uac8c\u2019\ub77c\ub294 \uc2dd\uc73c\ub85c \uc885\uc885 \uc815\ucc45\u000d\u000a\uc758 \uc218\ud61c \ub300\uc0c1\uc73c\ub85c \uac04\uc8fc\ub41c\ub2e4. \ubcf8 \uc5f0\uad6c\ub294 \uc5ec\uc131\ub4e4\uc758 \uad6c\uc220\uc0ac\ub97c \ud1b5\ud574 \ub178\ub3d9\uc790\ub85c\uc11c \uacbd\ud5d8\ud558\ub294 \u000d\u000a\ubcf5\ud569\ucc28\ubcc4\uc758 \uc774\uc57c\uae30\ub97c \uc0ac\ud68c\uc5d0 \uc804\ud558\uace0 \uac19\uc9c0\ub9cc \ub2e4\ub974\uae30\ub3c4 \ud55c \ubcf5\ud569\ucc28\ubcc4 \ub2f9\uc0ac\uc790\ub4e4\uc774 \uc815\ucc45\u000d\u000a\uc758 \ub300\uc0c1, \ubcf4\ud638\uc758 \ub300\uc0c1\uc774 \uc544\ub2c8\ub77c \uacbd\ud569\ud558\ub294 \ucc28\ubcc4\uc744 \uc77d\uc5b4\ub0b4\uace0 \ubcc0\ud654\ub97c \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \uc8fc\uccb4\uac00 \u000d\u000a\ub418\ub294 \uacbd\ud5d8\uc744 \ud568\uc73c\ub85c\uc368 \uc774 \uacbd\ud5d8\uc740 \ud3c9\ub4f1\ud55c \uc0ac\ud68c\ub97c \uc704\ud55c \uc5f0\ub300\uc758 \ub2e8\ucd08\ub85c \uae30\ub2a5\ud560 \uac83\uc774\ub2e4. \u000d\u000a\u2022 \uc131\ubcc4\uc774\ubd84\ubc95 \ub2f4\ub860\uc5d0 \uade0\uc5f4\uc744 \ub0b4\uace0 \uc820\ub354\uad00\uc810\uc758 \ub178\ub3d9\uc815\ucc45 \ubc29\ud5a5 \uc81c\uc2dc\ud558\uae30 \u000d\u000a\u00bb \ud55c\uad6d \ub178\ub3d9\uc2dc\uc7a5\uc740 \uace0\uc9c8\uc801\uc778 \uc131\ubcc4\uc784\uae08\uaca9\ucc28\uc640 \ucc44\uc6a9, \ubc30\uce58, \uc2b9\uc9c4, \uc784\uae08\uc5d0\uc11c\uc758 \uc131\ucc28\ubcc4\uc740 \u000d\u000a\ubb3c\ub860 \ub178\ub3d9\uc2dc\uc7a5\uc774 \uc544\uc608 \uc131\ubcc4\uc774\uc911\uad6c\uc870\ub85c \ub418\uc5b4 \uc788\uc5b4 \ubd88\ud3c9\ub4f1\uc758 \ubb38\uc81c\uac00 \uc2ec\uac01\ud558\ub2e4. \uc5ec\uc131\u000d\u000a\uc6b4\ub3d9\uc740 \uac00\ubd80\uc7a5\uc801 \uc790\ubcf8\uc8fc\uc758\uac00 \uc5ec\uc131\uc744 \uc0dd\uacc4\ub178\ub3d9\uc790\ub85c \uc778\uc815\ud558\uc9c0 \uc54a\uace0 \uc800\uc784\uae08 \uc77c\uc790\ub9ac, \u000d\u000a\uc8fc\ubcc0\ud654 \ub41c \uc704\uce58\uc5d0 \ubc30\uce58\ud558\ub294 \ub178\ub3d9\uc2dc\uc7a5\uc758 \uc131\ucc28\ubcc4\uc744 \ud574\uc18c\ud558\uae30 \uc704\ud574 \ubd80\ub2e8\ud788 \ud65c\ub3d9\ud574\uc654\u000d\u000a\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \uc5ec\uc804\ud788 \ubfcc\ub9ac \uae4a\uc740 \ub178\ub3d9 \uc131\ucc28\ubcc4\ud574\uc18c\ub97c \uc704\ud574\uc11c\ub294 \u2018\uc5ec\uc131\u2019 \uc774\ub77c\ub294 \uacf5\ud1b5\uc758 \u000d\u000a\uacbd\ud5d8\uc740 \uac15\ub825\ud558\uace0 \ub300\uc548 \uc81c\uc2dc\uc5d0\ub3c4 \ud6a8\uacfc\uc801\uc778 \ubc29\ubc95\uc774\uc9c0\ub9cc \uc5ec\uc131\ub178\ub3d9\uc6b4\ub3d9\uc758 \uc815\ucc45\uc801 \ud6a8\u000d\u000a\uacfc\uac00 \ubcf4\ub2e4 \ub9ce\uc740 \uc5ec\uc131\ub4e4\uc758 \uc0b6\uc5d0 \uc601\ud5a5\uc744 \ubbf8\uce58\uae30 \uc704\ud574\uc11c\ub294 \ub2e4\uc591\ud55c \uc704\uce58\uc5d0 \uc788\ub294 \uc5ec\uc131\u000d\u000a\ub4e4\uc758 \ubcf5\ud569\ucc28\ubcc4\ub3c4 \ud568\uaed8 \uc774\uc57c\uae30\ub418\uc5b4\uc57c \ud55c\ub2e4. \uc774\ub4e4\uc758 \uc874\uc7ac\uc640 \ubaa9\uc18c\ub9ac\uc758 \uac00\uc2dc\ud654\ub294 \ub178\ub3d9\u000d\u000a\uc2dc\uc7a5\uc758 \uc131\ubcc4\uc774\ubd84\ubc95\uc5d0 \uade0\uc5f4\uc744 \ub0b4\uace0 \uc820\ub354\uad00\uc810\uc758 \ub178\ub3d9\uc815\ucc45\uc744 \uc704\ud55c \ubc29\ud5a5\uc744 \uc81c\uc2dc\ud55c\ub2e4.\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \uc0b6\uc744 \uac00\ub85c\uc9c0\ub974\ub294 \ubcf5\ud569\ucc28\ubcc4"," & 100\u000d\u000a2. \uc8fc\uc694 \ub0b4\uc6a9\u000d\u000a\u2018\uc131\uc778-\ub0a8\uc131-\uc774\uc131\uc560-\ube44\uc7a5\uc560\uc778-\uc120\uc8fc\ubbfc\u2019\uc774\ub77c\ub294 \uc815\uc0c1\uc131 \uc911\uc2ec\uc758 \uc0ac\ud68c\uad6c\uc870\ub294 \ub178\ub3d9\uc2dc\uc7a5\uad6c\uc870\u000d\u000a\uc758 \uae30\ud68d\uacfc \uc9c1\uacb0\ub41c\ub2e4. \uacbd\uc81c\uc131\uc7a5\uc774\ub77c\ub294 \uc804\uad6d\uac00\uc801 \ubaa9\ud45c\ub294 \uc0dd\uc0b0\uc131\uc5d0 \ub300\ud55c \uc9d1\ucc29\uacfc \uac15\ubc15\uc744 \ub3d9\ubc18\u000d\u000a\ud558\uace0, \uc774\ub294 \uc815\uc0c1\uc131 \uaddc\ubc94\uc744 \uae30\uc900\uc73c\ub85c \u2018\uc77c\ud560 \uc218 \uc788\ub294 \ubab8-\uc0dd\uc0b0\uc801\uc778 \ubab8\u2019(\uc815\uc0c1)\uacfc \u2018\uc77c\ud560 \uc218 \uc5c6\ub294 \ubab8\uc0dd\uc0b0\uc801\uc774\uc9c0 \uc54a\uc740 \ubab8\u2019(\ube44\uc815\uc0c1)\uc744 \ubd84\ub9ac\ud558\ub294\ub370 \uacf5\ubaa8\ud588\ub2e4. \u2018\uc77c\ud560 \uc218 \uc5c6\ub294 \ubab8\u2019\uc5d0 \ub300\ud55c \uaddc\uc815\uc5d0 \ub530\ub77c \u2018\u000d\u000a\ube44\uc815\uc0c1\u2019\uc758 \ubab8\ub4e4\uc740 \u2018\uc8fc\ub958 \ub178\ub3d9\uc2dc\uc7a5\u2019\uc758 \uc778\ub825\uc774 \uc544\ub2cc \u2018\uc794\uc5ec\uc801 \ub178\ub3d9\u2019\uc744 \ud558\ub294 \uc774\ub4e4\ub85c \ud3c9\uac00\uc808\ud558\ub418\u000d\u000a\uc5c8\ub2e4. \ub178\ub3d9\uc2dc\uc7a5\uc5d0\uc11c \uc800\ud3c9\uac00\ub41c \uc794\uc5ec\uc801 \ub178\ub3d9\uc778\ub825\uc5d0\uac8c\ub294 \uc800\uc784\uae08 \ubd88\uc548\uc815 \ub178\ub3d9, \uc5f4\uc545\ud55c \ub178\ub3d9\ud658\u000d\u000a\uacbd\uc774 \uc8fc\uc5b4\uc9c4\ub2e4.\u000d\u000a\uc774\uc640 \uac19\uc740 \uc815\uc0c1\uc131 \uaddc\ubc94\uc758 \uc791\ub3d9 \ub9e5\ub77d\uc740 \ub178\ub3d9\uc2dc\uc7a5\uc758 \uc774\uc911\uad6c\uc870 \ud615\uc131\u2027\uc720\uc9c0\uc640\ub3c4 \uc5f0\uacb0\ub41c\ub2e4. \u000d\u000a\uacb0\uacfc\uc801\uc73c\ub85c \uc815\uc0c1\uc131\uc5d0 \ubd80\uc5ec\ub418\ub294 \ub2e4\uce35\uc801 \uad8c\ub825\uc5d0 \uc758\ud574 \uc18c\uc218\uc790-\u2018\ube44\uc815\uc0c1\u2019\uc758 \ubab8\ub4e4\uc740 \ub178\ub3d9\uc2dc\uc7a5\uad6c\u000d\u000a\uc870/\uc0ac\ud68c\ubcf4\uc7a5\uccb4\uacc4\uc5d0\uc11c\uc758 \uc18c\uc678\uc640 \ubc30\uc81c, \uadf8\ub85c \uc778\ud55c \ube48\uace4, \ucc28\ubcc4 \ub4f1 \uc0ac\ud68c\uc801 \uc704\uae30 \uc0c1\ud669\uc73c\ub85c \ub0b4\ubab0\u000d\u000a\ub824\uc654\ub2e4. \uc804 \uc0ac\ud68c \uc601\uc5ed\uc5d0 \uac78\uce5c \uc18c\uc218\uc790\uc5d0 \ub300\ud55c \ubc30\uc81c\uc640 \ucc28\ubcc4\uc740 \uc0ac\ud68c\uc5d0 \ub9cc\uc5f0\ud55c \ucc28\ubcc4 \ud574\uc18c\ub098 \uc18c\u000d\u000a\uc218\uc790\uc640\uc758 \uc5f0\ub300\ub97c \uc6b0\uc120\ud558\uae30\ubcf4\ub2e4 \uc815\uc0c1\uc131 \uaddc\ubc94\uc758 \uc720\uc9c0\ub97c \uc704\ud574 \ub2a5\ub825\uc8fc\uc758\uc640 \uacbd\uc7c1\uc744 \ubd80\ucd94\uae30\uac70\ub098 \u000d\u000a\uc2dc\ubbfc \uac04 \uc11c\uc5f4\ud654\ub97c \ubb35\uc778\ud558\ub294 \uc0ac\ud68c \uc804\ubc18\uc758 \ubd84\uc704\uae30\ub97c \uc7ac\uc0dd\uc0b0\ud574\uc654\ub2e4.\u000d\u000a\uc6b0\ub9ac\ub294 \uc774\uc640 \uac19\uc740 \ubb38\uc81c\uc758\uc2dd\uc744 \ubc14\ud0d5\uc73c\ub85c, <\ud55c\uad6d\uc5ec\uc131\uc7ac\ub2e8>\uc758 \uc9c0\uc6d0\uc744 \ubc1b\uc544 \uc5ec\uc131\ub178\ub3d9\uc790 20\u000d\u000a\uc778\uc758 \uc0dd\uc560\uc5d0 \uac78\uce5c \ub178\ub3d9 \uacfc\uc815\uc5d0\uc11c\uc758 \uacbd\ud5d8\uc744 \uc778\ud130\ubdf0\ud558\uc600\ub2e4. \ubc30\uc81c\uc640 \ucc28\ubcc4\uc774 \uc6a9\uc778\ub418\ub294 \uc8fc\ubcc0\ud654\u000d\u000a\ub41c \uc704\uce58\ub294 \uacb0\ucf54 \ud558\ub098\uc758 \u2018\ub2e8\uc77c\ud55c\u2019 \uad8c\ub825\uccb4\uac00 \ud640\ub85c \uc8fc\ub3c4\ud558\uc5ec \ub9cc\ub4e4\uc5b4\uc9c0\uc9c0 \uc54a\uc73c\uba70, \uc624\ud788\ub824 \uacbd\uc81c\u000d\u000a\uc131\uc7a5\uc2e0\ud654\u2027\ub0a8\uc131\uc911\uc2ec\uc0ac\ud68c\u2027\uc774\uc131\uc560\uc815\uc0c1\uac00\uc871\uaddc\ubc94 \ub4f1 \uc5ed\uc0ac\uc801 \ub9e5\ub77d \ud558 \uad8c\ub825\uc774 \ubd80\uc5ec\ub418\uc5b4\uc654\ub358 \uc5ec\u000d\u000a\ub7ec \uac08\ub798\uc758 \uc815\uc0c1\uc131 \uaddc\ubc94\uc774 \uad50\ucc28\ud55c \uacb0\uacfc\ub85c \ubcf4\uc558\ub2e4. \ub530\ub77c\uc11c \ud558\ub098\uc758 \uc815\uccb4\uc131\ub9cc\uc73c\ub85c \uac1c\uc778\uc774 \uacbd\ud5d8\u000d\u000a\ud55c \ucc28\ubcc4\uc744 \ud574\uc11d\ud560 \uc2dc \ud544\uc5f0\uc801\uc73c\ub85c \ucc28\ubcc4\uc744 \ud3b8\ud611\ud558\uac8c \ud574\uc11d\ud560 \uac00\ub2a5\uc131\uc774 \ud06c\uace0, \uc5ec\ub7ec \uc815\uccb4\uc131\uc744 \ub098\u000d\u000a\uc5f4\ud558\uc5ec \ucc28\ubcc4\uc744 \uc124\uba85\ud558\ub294 \ubc29\uc2dd \uc5ed\uc2dc \uac1c\uc778\uc774 \uac00\uc9c4 \uace0\uc720\ud55c \ucc28\ubcc4\uc758 \ub9e5\ub77d\uc744 \ud0c8\ub77d\uc2dc\ud0a8\ub2e4\ub294 \ud55c\uacc4\u000d\u000a\ub97c \uba85\ud655\ud788 \ud588\ub2e4. \uc774\ub54c \u2018\ubcf5\ud569\ucc28\ubcc4\u2019 \uac1c\ub150\uc740 \uac1c\uc778\uc5d0 \uad50\ucc28\ud558\ub294 \uc131\ubcc4, \ub098\uc774, \uc7a5\uc560, \uc774\uc8fc, \uc131\uc801\uc9c0\u000d\u000a\ud5a5, \uc131\ubcc4 \uc815\uccb4\uc131 \ub4f1\uc774 \uad50\ucc28\ud558\uba70 \ub9cc\ub4e4\uc5b4\ub0b8 \ucc28\ubcc4\uc758 \ub9e5\ub77d\uc744 \ub4dc\ub7ec\ub0b4\uace0, \uc774\ub97c \ud1b5\ud574 \ucc28\ubcc4\uacfc \ubc30\uc81c\u000d\u000a\ub97c \uc7ac\uc0dd\uc0b0\ud558\ub294 \uaddc\ubc94-\uad8c\ub825\uad00\uacc4\uc758 \uc5ed\ub3d9\uc744 \uc77d\uc5b4\ub0bc \uc218 \uc788\ub294 \uc720\uc6a9\ud55c \ubd84\uc11d\ud2c0\uc774 \ub418\uc5c8\ub2e4.\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790 20\uba85\uc758 \uc774\uc57c\uae30\ub294 \ub2e8\uc21c\ud788 \ub178\ub3d9 \uacbd\ud5d8\uc5d0 \uad6d\ud55c\ub418\uc9c0 \uc54a\uace0 \uadf8\uc640 \uc5f0\uacb0\ub41c \uac00\uc871, \uc8fc\u000d\u000a\uac70, \uad00\uacc4, \ub3cc\ubd04, \uc758\ub8cc \ub4f1 \uac1c\uc778\uc758 \uc0b6\uc744 \uad6c\uc131\ud558\ub294 \uc5ec\ub7ec \uc0ac\ud68c\uc801 \uc870\uac74\u2027\uc601\uc5ed\ub4e4\uc744 \ub118\ub098\ub4e0\ub2e4. \uc774\uc640 \u000d\u000a\uac19\uc740 \uc0ac\ud68c\uc801 \uc870\uac74\ub4e4\uc744 \uace0\ub824\ud568\uc73c\ub85c\uc368 \uc774\ub4e4\uc774 \uac00\uc9c0\ub294 \ub178\ub3d9\uc0dd\uc560\uc5d0\uc11c\uc758 \ucc28\ubcc4\uc740 \ub354 \uc120\uba85\ud788 \ud574","100 101\u000d\u000a\uc11d\ub418\uc5c8\uace0, \ub178\ub3d9\uc2dc\uc7a5\uc5d0\uc11c \uacbd\ud5d8\ub418\ub294 \ucc28\ubcc4 \ud574\uc18c\ub97c \uc704\ud55c \ub300\uc548 \uc5ed\uc2dc \ud3ed\ub113\uac8c \ub17c\uc758\ub420 \uc218 \uc788\uc5c8\ub2e4.\u000d\u000a\uc608\ucee8\ub300 \u2018\uc774\uc131\uc560 \uacb0\ud63c-\ucd9c\uc0b0-\uc591\uc721-\ub3cc\ubd04-\uc8fd\uc74c\u2019\uc774\ub77c\ub294 \uc774\uc131\uc560 \uc815\uc0c1\uc131 \uc911\uc2ec\uc758 \uc0dd\uc560\uc8fc\uae30 \uc791\u000d\u000a\ub3d9\uc5d0 \ub300\ud55c \uace0\ub824 \uc5c6\uc774 \ub178\ub3d9\uc2dc\uc7a5\uc5d0\uc11c \uacbd\ud5d8\ud558\ub294 \ubc30\uc81c\uc640 \ucc28\ubcc4\uc758 \ubb38\uc81c\ub97c \ud574\uc11d\ud55c\ub2e4\uba74, \ucc28\ubcc4\uc758 \uc6d0\u000d\u000a\uc778\uc740 \uc5ec\uc131\ub178\ub3d9\uc790 \uac1c\uc778\uc758 \ub2a5\ub825\uc774\ub098 \ucc45\uc784\uc758 \ubb38\uc81c\ub85c \ub3cc\uc544\uac00\uae30 \uc26c\uc6b0\uba70 \ucc28\ubcc4 \ud574\uc18c \ub300\uc548 \uc5ed\uc2dc \uc5ec\u000d\u000a\uc131\ub178\ub3d9\uc790 \uac1c\uc778\uc758 \ub178\ub825\u2027\uc790\uae30\uacc4\ubc1c\uc758 \ubb38\uc81c\ub85c \ud55c\uc815\ub41c\ub2e4. \uc774\ub7ec\ud55c \uad00\uc810\uc740 \uc0ac\ud68c\uc815\ucc45 \uc218\ub9bd \uc2dc\uc5d0\u000d\u000a\ub3c4 \ub178\ub3d9\uc815\ucc45\uc774\ub77c\ub294 \ud558\ub098\uc758 \uc904\uae30 \ub0b4\uc5d0\uc11c\ub9cc \ud1b5\ud558\ub294 \ub2e8\ud3b8\uc801\uc778 \ud574\uacb0\ucc45\uc744 \ucc3e\uac8c\ub420 \uc218 \uc788\ub2e4. \uadf8\ub7ec\u000d\u000a\ub098 \ub178\ub3d9\uc2dc\uc7a5\uc5d0\uc11c \uacbd\ud5d8\ud558\ub294 \ubc30\uc81c\uc640 \ucc28\ubcc4\uc758 \ubb38\uc81c\ub97c \uc774\uc131\uc560 \uc815\uc0c1\uc131 \uc911\uc2ec\uc758 \uc0dd\uc560\uc8fc\uae30\uc5d0 \ub9de\ucdb0\u000d\u000a\uc9c4 \uc77c\ud130 \uc548\ud30e\uc758 \uaddc\ubc94\u2027\uc81c\ub3c4\uc640 \ubd84\ub9ac\ud558\uc9c0 \uc54a\uace0 \ud574\uc11d\ud55c\ub2e4\uba74, \ucc28\ubcc4\uc758 \uc6d0\uc778\uc740 \uc18c\uc218\uc790\ub97c \ubc30\uc81c\ud55c\u000d\u000a\ucc44\ub85c \uc720\uc9c0\ub418\uc5b4\uc628 \ucc28\ubcc4\uacfc \ud3ed\ub825\uc758 \uc2dc\uc2a4\ud15c\uc774 \ub418\uace0 \uc774\uc5d0 \ub300\ud55c \ucd1d\uccb4\uc801\uc778 \ubb38\uc81c\uc81c\uae30\uac00 \uac00\ub2a5\ud574\uc9c4\u000d\u000a\ub2e4. \uadf8\ub85c \uc778\ud574 \ucc28\ubcc4\uc758 \ubb38\uc81c\ub97c \ud574\uacb0\ud560 \ub300\uc548 \ub17c\uc758\uc758 \ud3ed\uc774 \ub113\uc5b4\uc9c0\uba70 \uc18c\uc218\uc790\ub85c \ubc30\uc81c\ub418\uace0 \ucc28\ubcc4\u000d\u000a\ubc1b\uc544\uc654\ub358 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \ubaa9\uc18c\ub9ac\ub294 \uadf8 \uc790\uccb4\ub85c \uacac\uace0\ud55c \ucc28\ubcc4\uacfc \ud3ed\ub825\uc758 \uad6c\uc870\uc5d0 \uade0\uc5f4\uc744 \ub0b8\ub2e4.\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc774 \uacbd\ud5d8\ud55c \ucc28\ubcc4\uc758 \ub9e5\ub77d\uc744 \uad6c\uc131\ud558\ub294\ub370 \ub178\ub3d9\uacbd\ud5d8\ub9cc\uc744 \ub2e8\ub3c5\uc801\uc73c\ub85c \ubd84\ub9ac\ud574\ub0bc \u000d\u000a\uc218 \uc5c6\ub294 \ub9cc\ud07c, \uc774\ub4e4\uc774 \uacbd\ud5d8\ud558\ub294 \ucc28\ubcc4 \ud574\uc18c\ub97c \uc704\ud55c \uc815\ucc45 \uc218\ub9bd \uc2dc\uc5d0\ub3c4 \uc5ed\uc2dc \uad50\ucc28\uc801\uc778 \uad00\uc810\uc774 \u000d\u000a\uace0\ub824\ub418\uc5b4\uc57c \ud55c\ub2e4. \ub178\ub3d9\uc2dc\uc7a5 \uc804\ubc18\uc5d0 \uac78\uce5c \ucc28\ubcc4\uc758 \uadf8\ubb3c\ub9dd \ud574\uccb4\ub97c \uc704\ud574\uc120 \ub178\ub3d9\uc815\ucc45\ub9cc\uc774 \uc544\ub2c8\u000d\u000a\ub77c \uac00\uc871, \ub178\ub3d9, \uc8fc\uac70, \ub3cc\ubd04, \uc758\ub8cc \ub4f1 \uc0ac\ud68c\uc815\ucc45 \uac04\uc758 \uae34\ubc00\ud55c \uc5f0\uacc4\uac00 \ud544\uc694\ud558\ub2e4. \uac00\ub839 \uad6d\uac00\uac00 \uc6d0\u000d\u000a\uac00\uc871\uc5d0\ub9cc \ub3cc\ubd04\uc744 \uc804\uac00\ud574\uc628 \uacb0\uacfc, \uc544\ud508\ubab8\uc758 \ub178\ub3d9\uc790\ub294 \uace0\ub9bd\uac10\uc758 \ubb38\uc81c\ub97c \uc548\uace0 \uc77c\uc744 \ud574\uc57c\ud588\uace0,\u000d\u000a\u2018\uc77c\ud558\ub294 \uc544\ud508\ubab8\u2019\uc744 \uc804\uc81c\ud558\uc9c0 \uc54a\ub294 \ub178\ub3d9\uc2dc\uc7a5\uc740 \uace0\uc6a9\ubd88\uc548\uac10\uc744 \uc8fc\uc5c8\ub2e4. \uc774 \ub54c \uc5ec\uc131\ub178\ub3d9\uc790\uac00 \uacbd\u000d\u000a\ud5d8\ud558\ub294 \uace0\ub9bd\uac10\uacfc \ubd88\uc548\uc815\uc740 \u2018\uac00\uc871\u2019 \uac1c\ub150\uc758 \ud611\uc18c\ud55c \uc815\uc758\uc5d0 \ub530\ub978 \uc6d0\uac00\uc871 \uc678 \uad00\uacc4\uc131\uc774\ub098 \ub3cc\ubd04\uad00\u000d\u000a\uacc4\uc758 \uc0ac\ud68c\uc801 \ubd88\uc778\uc815 \ubb38\uc81c\uc640 \ub9de\ub2ff\uc544\uc788\ub2e4. \ub610\ud55c \ub3cc\ubd04\uacfc \uc77c\uc758 \uc601\uc5ed\uc744 \ubd84\ub9ac\ud574\uc628 \ub0a8\uc131\uc0dd\uacc4\ubd80\uc591\u000d\u000a\uc790 \ubaa8\ub378\uc740 \uc81c\ub3c4\ub85c, \ubb38\ud654\ub85c \ub179\uc544\ub4e4\uc5b4 \ub0a8\uc131\uc740 \uc2b9\uc9c4\ud558\uace0 \uc77c\ud558\ub294 \uc874\uc7ac\uc774\uba70, \uc5ec\uc131\uc740 \uc5b8\uc820\uac00 \ud68c\uc0ac\u000d\u000a\ub97c \ub5a0\ub098 \ub3cc\ubd04\uc744 \uc804\ub2f4\ud558\ub294 \uc874\uc7ac\ub77c\ub294 \uc778\uc2dd\uc744 \uace0\ucc29\uc2dc\ucf30\ub2e4. \uc774 \uc5ed\uc2dc \ub3cc\ubd04\uc758 \ubb38\uc81c\ub97c \uc5ec\uc131-\uc6d0\uac00\u000d\u000a\uc871\uc5d0\uac8c \uc804\uac00\ud558\uace0 \uadf8 \uc678\uc758 \uad00\uacc4\uc131\ub4e4\uc744 \ubc30\uc81c\ud574\uc628 \ucc28\ubcc4\uc801 \uad6c\uc870\uc640 \uad00\ub828\uc774 \uc788\ub2e4. \uc774\uc640 \uac19\uc740 \uad8c\ub825\u000d\u000a\uad00\uacc4\uc758 \uc5ed\ub3d9\uc740 \ub178\ub3d9\uc815\ucc45\uacfc \uac00\uc871-\ub3cc\ubd04\uc815\ucc45\uc774 \ubd84\uc808\ub418\uc5b4 \uc218\ub9bd\ub420 \uc2dc \ubcf4\uc774\uc9c0 \uc54a\uac8c \ub41c\ub2e4.\u000d\u000a\uc774\ubbf8 \uac1c\uc778\uc758 \uc0b6\uacfc \uc77c\uc0c1\uc758 \uc804 \uc601\uc5ed\uc5d0 \uad50\ucc28\ud558\ub294 \ucc28\ubcc4\uc744 \ubcf5\ud569\uc801\uc73c\ub85c \uc0ac\uc720\ud558\uc9c0 \uc54a\ub294\ub2e4\uba74 \u2018\ube44\u000d\u000a\uc815\uc0c1\uc131\u2019\uc5d0 \ub300\ud55c \ucc28\ubcc4\uc744 \uac00\ub2a5\ud558\uac8c \ud558\ub294 \uad6c\uc870\uc640 \ub17c\ub9ac\uc5d0 \uade0\uc5f4\uc744 \ub0b4\ub294\ub370 \ud55c\uacc4\uac00 \uc788\uace0, \uc774\ub7ec\ud55c \u000d\u000a\uc0ac\uc720 \uc548\uc5d0\uc11c \uc0ac\ud68c\uc801 \ub300\uc548\uc774 \ub3c4\ucd9c\ub418\uc9c0 \uc54a\ub294\ub2e4\uba74 \uc18c\uc218\uc790\ub97c \ubc30\uc81c\ud558\uace0 \ucc28\ubcc4\ud558\ub294 \ud604\uc7ac\uc758 \uc2dc\uc2a4\ud15c\u000d\u000a\uc740 \ubcc0\ud654\ub420 \uc218 \uc5c6\ub2e4. \ucc28\ubcc4\uc5d0 \ub300\ud55c \ubcf5\ud569\uc801 \uc0ac\uc720\uac00 \ub178\ub3d9\uc2dc\uc7a5\uc758 \uc774\uc911\uad6c\uc870\uc640 \uc774\ub85c\ubd80\ud130 \ud30c\uc0dd\ub418\ub294 \u000d\u000a\uc5ec\uc131\u2027\uc18c\uc218\uc790\uc758 \uad8c\ub9ac\ubc15\ud0c8 \ubb38\uc81c \ud574\uacb0\uc744 \uc704\ud574 \ubb34\uc5c7\ubcf4\ub2e4 \uc911\uc694\ud55c \uc774\uc720\ub2e4. \u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \uc0b6\uc744 \uac00\ub85c\uc9c0\ub974\ub294 \ubcf5\ud569\ucc28\ubcc4"," & 102\u000d\u000a\ub2e4\ub974\uc9c0\ub9cc \ub2ee\uc740 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uacbd\ud5d8\uc740 \uc18c\uc218\uc790\ub97c \ubc30\uc81c\ud558\uace0 \ucc28\ubcc4\ud574\uc628 \uad6c\uc870\uc758 \uc791\ub3d9\ubc29\uc2dd\uc744 \u000d\u000a\ub2f4\uace0 \uc788\uc73c\uba70, \uc774\ub7ec\ud55c \uc2dc\uc2a4\ud15c\uc744 \ud1b5\uacfc\ud574\uc628 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \ubaa9\uc18c\ub9ac\ub294 \uae30\uc874\uc758 \uad8c\ub825\uad00\uacc4\uc640 \uaddc\u000d\u000a\ubc94\uc5d0 \ud2c8\uc744 \ub0b4\uace0 \ubcc0\ud654\ub97c \ud5a5\ud55c \uc5f0\ub300\uc758 \uae38\uc744 \ucc3e\uac8c \ud55c\ub2e4. \ud55c\uad6d \uc0ac\ud68c\ub294 \uc774\ub4e4\uc774 \uacbd\ud5d8\ud558\ub294 \ubd80\uc815\uc758\u000d\u000a\uc640 \uae30\uc874\uc758 \uad8c\ub825\uad00\uacc4\u2027\uaddc\ubc94\uc5d0 \ub300\ud55c \uc9c8\ubb38\uc5d0 \ucc28\ubcc4\uae08\uc9c0\ubc95 \uc81c\uc815\uc73c\ub85c \ub2f5\ud574\uc57c \ud558\uba70, \uac1c\uc778\uc774 \uacbd\ud5d8\u000d\u000a\ud558\ub294 \ub2e4\uce35\uc801\uc778 \ucc28\ubcc4\uc758 \ub9e5\ub77d\uc744 \ubcf5\ud569\uc801\uc73c\ub85c \uc0ac\uc720\ud558\ub294 \ubc29\uc2dd\uc774 \uc0c8\ub85c\uc6b4 \uc0ac\ud68c\uc801 \uc57d\uc18d\uc73c\ub85c\uc11c \ubfcc\u000d\u000a\ub9ac\ub0b4\ub9b4 \uc218 \uc788\uae30\ub97c \ubc14\ub780\ub2e4.\u000d\u000a3. \uc5f0\uad6c \uacb0\uacfc\uc758 \uc0ac\ud68c\uc801 \ud30c\uae09\ud6a8\uacfc\u000d\u000a\u2022 \uc131\ubcc4 \uc678 \ub2e4\uc591\ud55c \uc815\uccb4\uc131\uc774 \ubcf5\ud569\uc801\uc73c\ub85c \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \ubcf5\ud569\ucc28\ubcc4\uc758 \uac00\uc2dc\ud654\ub97c \ud1b5\ud574 \ucc28\ubcc4\uc5d0\u000d\u000a\ub300\ud55c \uc0ac\ud68c\uc801 \uac10\uac01\uacfc \uc778\uc2dd\uc744 \ud655\uc7a5\ud558\uace0 \uc774\ub97c \ud1b5\ud55c \ucc28\ubcc4 \ud574\uc18c\ub85c \uc131\ud3c9\ub4f1 \uc0ac\ud68c \ubd84\uc704\uae30 \uc870\u000d\u000a\uc131\uc5d0 \uae30\uc5ec\ud560 \uac83\uc774\ub2e4.\u000d\u000a\u2022 \uc5ec\uc131\ub4e4 \uac04\uc5d0\ub3c4 \uac01\uc790\uc758 \uc704\uce58\uc5d0 \ub530\ub77c \uc815\ucc45\uc801 \ud6a8\uacfc\ub97c \ub2e4\ub974\uac8c \uacbd\ud5d8\ud558\ub294 \uaca9\ucc28\ub97c \uc904\uc5ec\ub098\u000d\u000a\uac10\uc73c\ub85c\uc368 \uc820\ub354 \uc815\uc758 \uc2e4\ud604\uc758 \uae30\ubc18\uc774 \ub420 \uac83\uc774\ub2e4.\u000d\u000a\u2022 \ubc1c\ud654\uc758 \uacbd\ud5d8\uacfc \ube44\uc2b7\ud55c \ubc29\uc2dd\uc758 \ubcf5\ud569\ucc28\ubcc4\uc744 \uacaa\ub294 \uc0ac\ub78c\ub4e4\uacfc\uc758 \uc18c\ud1b5\uc744 \ud1b5\ud574 \uac19\uc9c0\ub9cc \ub2e4\u000d\u000a\ub974\uae30\ub3c4 \ud55c \uc11c\ub85c\ub97c \ud655\uc778\ud558\uace0 \uc2a4\uc2a4\ub85c\ub97c \uc815\ucc45\uc801 \ubcf4\ud638\uc758 \ub300\uc0c1\uc774 \uc544\ub2c8\ub77c \uc131\ud3c9\ub4f1 \uc0ac\ud68c\ub97c\u000d\u000a\uc704\ud55c \uc5f0\ub300\uc758 \uc8fc\uccb4\ub85c \uc815\uccb4\ud654\ud558\uc5ec \uc5ec\uc131\ub4e4\uc758 \uc5ed\ub7c9 \uac15\ud654\uc640 \uad11\uc758\uc758 \uc815\uce58\uc138\ub825\ud654\ub97c \uae30\ub300\ud558\u000d\u000a\uba70, \uc5ec\uc131\ub4e4\uc758 \uc0b6\uacfc \uc815\ucc45\uc758 \uac70\ub9ac\ub97c \uc881\ud788\ub294 \ud6a8\uacfc\ub97c \uac00\uc838\uc62c \uac83\uc774\ub2e4.\u000d\u000a\u2022 \ub2e4\uc591\ud55c \uc815\uccb4\uc131\uc774 \ucc28\ubcc4\uc758 \uad6c\uc870\uc640 \uc0c1\ud638\uc791\uc6a9\ud558\uba70 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \ubcf5\ud569\ucc28\ubcc4\uc758 \uc791\ub3d9 \ubc29\uc2dd\uc744\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uacbd\ud5d8\uc744 \ud1b5\ud574 \ub4dc\ub7ec\ub0b4\uc5b4 \uc131\ubcc4\uc774\ubd84\ubc95\uacfc \ub178\ub3d9\uc815\ucc45\uc548\uc758 \uc591\uc131\ud3c9\ub4f1 \ub2f4\ub860\u000d\u000a\uc5d0 \uade0\uc5f4\uc744 \ub0b4\uace0 \uc820\ub354\uad00\uc810\uc758 \ub178\ub3d9\uc815\ucc45 \uc0dd\uc0b0\uc744 \uc704\ud55c \uae30\ubc18\uc774 \ub9c8\ub828\ub420 \uac83\uc774\ub2e4.","102 103\u000d\u000a4. \uc131\ucc30\uacfc \ud6c4\uae30\u000d\u000a\uc2ec\ud654\ud558\ub294 \uc0ac\ud68c\uc801 \uc704\uae30\uc640 \uc7ac\ub09c \ub4f1\uc73c\ub85c \uc5ec\uc131\u2027\uc18c\uc218\uc790\uac00 \uacbd\ud5d8\ud558\ub294 \ucc28\ubcc4\uc758 \ubb38\uc81c\ub294 \ub354\u000d\u000a\uc6b1 \uad50\ubb18\ud574\uc9c0\uace0 \uc788\ub2e4. \uc774\ub294 \uace7 \uc0ac\ud68c\uc801 \ubc30\uc81c\uc758 \uc704\uce58\ub97c \uad6c\uc131\ud558\ub294 \uc694\uc778\uc774 \ub9e4\uc6b0 \ub2e4\uce35\uc801\uc774\u000d\u000a\uace0 \ubcf5\ud569\uc801\uc774\ub77c\ub294 \uc758\ubbf8\ub2e4. \ub530\ub77c\uc11c \ucc28\ubcc4\uc758 \ub9e5\ub77d\uc744 \uace0\uc815\uc801\uc774\uace0 \ub2e8\uc77c\ud55c \uad8c\ub825\uccb4\ub97c \uc0c1\uc815\ud558\u000d\u000a\uc5ec \uc124\uba85\ud558\ub824 \ud558\uac70\ub098 \ud558\ub098\uc758 \uc815\uccb4\uc131\uc5d0 \uae30\ubc18\ud574 \ubd84\uc11d\ud55c\ub2e4\uba74, \ud544\uc5f0\uc801\uc73c\ub85c \uc65c\uace1\ub418\uace0 \uc9c0\uc6cc\u000d\u000a\uc9c0\ub294 \uc9c0\uc810\uc774 \uc0dd\uae38 \uac83\uc774\ub2e4. \uc774\ub294 \u2018\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \uc0b6\uc744 \uac00\ub85c\uc9c0\ub974\ub294 \ubcf5\ud569\ucc28\ubcc4\u2019\u000d\u000a\uc758 \uae30\ud68d\uacfc \uc5f0\uad6c \uacfc\uc815 \uc804\ubc18\uc5d0 \uc8fc\uc694\ud558\uac8c \ub179\uc5ec\ub0b4\uace0\uc790 \ud588\ub358 \ubb38\uc81c\uc758\uc2dd\uc774\uc5c8\ub2e4. \ubcf5\ud569\ucc28\ubcc4\uc740 \u000d\u000a\ucc28\ubcc4\uc5d0 \ub300\ud55c \uad50\ucc28\uc801\uc778 \uc0ac\uc720\ub97c \ud1b5\ud574 \uc5ec\uc131\u2027\uc18c\uc218\uc790\ub97c \uc8fc\ubcc0\ubd80\uc5d0 \uba38\ubb3c\uac8c \ud558\ub294 \uad8c\ub825 \uc791\ub3d9\u000d\u000a\uc758 \uba54\ucee4\ub2c8\uc998\uc744 \uc12c\uc138\ud788 \ub4e4\uc5ec\ub2e4\ubcfc \uc218 \uc788\uac8c \ud558\ubbc0\ub85c, \ub2e4\uc591\ud55c \uc815\uccb4\uc131\uc774 \uad50\ucc28\ud558\ub294 \uc778\ud130\ubdf0 \u000d\u000a\ucc38\uc5ec\uc790\uc758 \ub178\ub3d9 \uc0dd\uc560\ub97c \ubcf4\ub2e4 \uc785\uccb4\uc801\uc73c\ub85c \ub9e5\ub77d\ud654 \ud560 \uc218 \uc788\uac8c \ud588\ub2e4. \uc774\ub97c \ud1b5\ud574 \uc131\ucc28\ubcc4 \ubb38\u000d\u000a\uc81c\ub97c \uc131\ubcc4\uc758 \ubb38\uc81c\ub85c\ub9cc, \ub178\ub3d9\uc2dc\uc7a5 \ucc28\ubcc4 \ubb38\uc81c\ub97c \ub178\ub3d9 \ubb38\uc81c\ub85c\ub9cc \uc870\uba85\ud574\uc11c\ub294 \uc628\uc804\ud788 \ud574\uc18c\u000d\u000a\ud560 \uc218 \uc5c6\uc73c\uba70 \uc5ec\uc131\ub178\ub3d9\uc790\uc758 \uc0b6\uc744 \uad6c\uc131\ud558\ub294 \uc758\ub8cc\u2027\uc8fc\uac70\u2027\ub178\ub3d9\u2027\ub3cc\ubd04\u2027\ubcf5\uc9c0 \ub4f1 \ucd1d\uccb4\uc801\u000d\u000a\uc778 \uc601\uc5ed\uacfc \uc5f0\uacb0 \uc9c0\uc5b4 \uc0ac\uc720\ud574\uc57c \ud558\uace0, \ucc28\ubcc4\uc744 \uc815\ub2f9\ud654\ud558\ub294 \ub2f4\ub860/\uc778\uc2dd\uc73c\ub85c\uc11c \uc0dd\uc0b0\uc131 \uc911\u000d\u000a\uc2ec\uc758 \uc790\ubcf8\uc8fc\uc758 \ub17c\ub9ac\u2027\uc5f0\ub839\uc8fc\uc758\u2027\ud559\ubc8c\uc8fc\uc758\u2027\uc131\ubcc4\uc774\ubd84\ubc95\u2027\uc131\ubcc4\ub178\ub3d9\ubd84\uc5c5\uc774\ub370\uc62c\ub85c\uae30\u2027\u000d\u000a\uc815\uc0c1\uac00\uc871\uc774\ub370\uc62c\ub85c\uae30\u2027\ube44\uc7a5\uc560\uc911\uc2ec\uc8fc\uc758\u2027\uc120\uc8fc\ubbfc\uc911\uc2ec\uc8fc\uc758 \ub4f1\uc5d0 \uade0\uc5f4\uc744 \ub0b4\uc57c \ud55c\ub2e4\ub294 \ubb38\u000d\u000a\uc81c\uc758\uc2dd\uc744 \uc120\uba85\ud788 \ub4dc\ub7ec\ub0bc \uc218 \uc788\uc5c8\ub2e4. \ub2e4\uce35\uc801\uc778 \ucc28\ubcc4\uc774 \uad50\ucc28\ud558\ub294 \uc2dc\uc2a4\ud15c\uc744 \ud1b5\uacfc\ud558\uba70 \uc0b4\u000d\u000a\uc544\uac00\ub294 \uc5ec\uc131\ub178\ub3d9\uc790\uc758 \ubab8\uacfc \ubaa9\uc18c\ub9ac\ub294 \uadf8 \uc790\uccb4\ub85c \uc815\uce58\uc801\uc774\ub77c\ub294 \uc778\uc2dd \uc5ed\uc2dc \ubcf8 \uc5f0\uad6c\ub97c \uad00\u000d\u000a\ud1b5\ud558\uace0 \uc788\ub294 \uc778\uc2dd \uac00\uc6b4\ub370 \ud558\ub098\ub2e4. \ud55c\ud3b8 \ub2e4\uc591\ud55c \uc815\uccb4\uc131\uc774 \uad50\ucc28\ud558\uba70 \uad6c\uc131\ub418\ub294 \uace0\uc720\ud55c \u000d\u000a\ucc28\ubcc4 \ub9e5\ub77d\uc744 \ub4dc\ub7ec\ub0b8\ub2e4\ub294 \uc810\uc5d0\uc11c \ubcf8 \uc5f0\uad6c\ub294 \uc778\ud130\ubdf0 \ucc38\uc5ec\uc790 \ubaa8\uc9d1\uacfc \uc778\ud130\ubdf0 \uc9c4\ud589\uc774 \uc27d\uc9c0 \u000d\u000a\uc54a\uc740 \uc791\uc5c5\uc774\uae30\ub3c4 \ud588\ub2e4. \uc778\ud130\ubdf0 \uc9c8\ubb38 \uad6c\uc131\uc5d0\uc11c\ubd80\ud130 \uc778\ud130\ubdf0 \uc9c4\ud589\uae4c\uc9c0 \ubbfc\uac10\ud55c \uc131\ucc30\uacfc \uc778\u000d\u000a\ud130\ubdf0 \ucc38\uc5ec\uc790\uc640\uc758 \uc12c\uc138\ud55c \uc18c\ud1b5\uc774 \ud544\uc694\ud588\ub2e4. \uadf8 \uacfc\uc815\uc774 \ub2e4\uc18c \uace0\ub41c \uc791\uc5c5\uc774\uae34 \ud588\uc5b4\ub3c4, \uacb0\u000d\u000a\uacfc\uc801\uc73c\ub85c \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc774 \ub4e4\ub824\uc8fc\ub294 \uc774\uc57c\uae30\uc5d0\uc11c \ub098\ub97c \ubcf4\uae30\ub3c4 \ud558\uace0, \uc774\ub4e4\uc758 \uc0dd\uc0dd\ud55c \uc0b6\u000d\u000a\uc744 \uae30\ub85d\ud558\uba74\uc11c \uc5f0\ub300\uac10\uacfc \uad6c\uc870\uc801 \ucc28\ubcc4\uc5d0 \uc800\ud56d\ud558\ub294\ub370 \ud544\uc694\ud55c \ub0b4\uba74\uc801\uc778 \ud798\uc744 \uc5bb\uae30\ub3c4 \ud588\u000d\u000a\ub2e4. \uc5b8\uc820\uac00 \ubaa8\ub4e0 \uc77c\ud558\ub294 \uc5ec\uc131\ub4e4\uc774 \uac00\uc9c4 \uace0\uc720\ud558\uace0 \ub2e4\uc591\ud55c \uc815\uccb4\uc131\uc774 \uc874\uc5c4\uc131\uc744 \ubc15\ud0c8\ud558\ub294 \u000d\u000a\u2018\ucc28\ubcc4 \uc0ac\uc720\u2019\ub85c \uc778\uc9c0\ub418\uc9c0 \uc54a\uace0, \uac1c\ubcc4\uc131\uacfc \ub2e4\uc591\uc131 \uc874\uc911\uc774\ub77c\ub294 \ud1a0\ub300 \uc704\uc5d0 \ud3c9\ub4f1\ud558\uac8c \ud658\ub300 \u000d\u000a\ubc1b\ub294 \uc77c\ud130\ub97c \uafc8\uafb8\uba70, \ub610 \uc774\ub7ec\ud55c \uad00\uacc4\uc640 \uc77c\ud130\uac00 \ubaa8\uc5ec \ubaa8\ub4e0 \uc0ac\ud68c\uad6c\uc131\uc6d0\uc774 \u2018\ub098\u2019\ub2f5\uac8c \uc800\ub9c8\u000d\u000a\ub2e4\uc758 \uc0b6\uc744 \ub9cc\ub4e4\uc5b4\ub098\uac00\uace0 \uc0b4\uc544\uac08 \uc218 \uc788\ub294 \uc0ac\ud68c\ub97c \uc0c1\uc0c1\ud558\uba70 \ud6c4\uae30\ub97c \ub9c8\uce5c\ub2e4. (\uad6c\uc9c0\ud61c \ud65c\ub3d9\uac00)\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \uc0b6\uc744 \uac00\ub85c\uc9c0\ub974\ub294 \ubcf5\ud569\ucc28\ubcc4"," & 104\u000d\u000a\uc131\uc0ac\uc5c5\uacc4\ud68d\uc744 \uad6c\uc0c1\ud560 \ub54c \ub5a0\uc624\ub978 \uc9c8\ubb38\uc774\uc790 \uace0\ubbfc\uc740 \ud3c9\ub4f1\uc5d0 \ub300\ud55c \uc2dc\ubbfc\ub4e4\uc758 \uc695\uad6c\uc640 \uc694\u000d\u000a\uad6c\ub294 \ub192\uc544 \ubcf4\uc774\ub294\ub370 \ud55c\uad6d \uc0ac\ud68c\uc5d0\uc11c \ucc28\ubcc4\uacfc \ud610\uc624\uac00 \uc904\uc5b4\ub4e4\uc9c0 \uc54a\uace0 \uc624\ud788\ub824 \uc591\uadf9\ud654\uac00 \uc2ec\u000d\u000a\ud654\ub418\uace0 \uc788\ub2e4\ub294 \uc810\uc774\uc5c8\ub2e4. \uadf8\ub807\ub2e4\uba74 \uc6b0\ub9ac \uc0ac\ud68c\uac00 \ucc28\ubcc4\uc744 \ubc1c\uacac\ud558\uace0 \ud574\uc18c\ud558\uae30 \uc704\ud55c \ubc29\uc2dd\u000d\u000a\ub4e4\uc5d0 \ubcc0\ud654\uac00 \ud544\uc694\ud558\uace0 \uadf8 \ubc29\uc2dd\uc740 \ucc28\ubcc4\uc758 \uc791\ub3d9\ubc29\uc2dd\uacfc \ub9e5\ub77d\uc744 \ubcf4\uc5ec\uc8fc\ub294 \uc791\uc5c5\uc774\ub77c\ub294 \uc0dd\u000d\u000a\uac01\uc5d0 \ub2e4\ub2e4\ub790\ub2e4. \ucc28\ubcc4\uc740 \ubd88\ud3c9\ub4f1\ud55c \uad8c\ub825\uad6c\uc870\uc640 \uc0ac\ud68c\uaddc\ubc94\uc774 \uac1c\uc778\uc744 \uad6c\uc131\ud558\ub294 \ub2e4\uc591\ud55c \uc815\u000d\u000a\uccb4\uc131\uacfc \uad50\ucc28\ud558\uc5ec \ubc1c\uc0dd\ud558\ubbc0\ub85c \ucc28\ubcc4\uc758 \uacb0\uacfc\ub97c \ub9cc\ub4e4\uc5b4\ub0b4\uc9c0\ub9cc \uac00\ub824\uc9c4 \ucc28\ubcc4\uc801 \uad6c\uc870\uc640 \ub9e5\u000d\u000a\ub77d\uc5d0 \ub300\ud55c \uc811\uadfc \uc5c6\uc774\ub294 \ucc28\ubcc4\uc758 \ud574\uc18c\ub3c4 \uc608\ubc29\ub3c4 \uc5b4\ub835\uae30 \ub54c\ubb38\uc774\ub2e4. \uc774\ub7ec\ud55c \ubb38\uc81c\uc758\uc2dd\uc744 \u000d\u000a\ubc14\ud0d5\uc73c\ub85c \ucc28\ubcc4 \uc778\uc2dd\uacfc \ud574\uc18c\uc5d0 \ubcf4\ub2e4 \uc785\uccb4\uc801\uc774\uace0 \uadfc\ubcf8\uc801\uc778 \uc811\uadfc\uc744 \uc704\ud574 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\u000d\u000a\uc774 \uacbd\ud5d8\ud558\uace0 \uc788\ub294 \u2018\ubcf5\ud569\ucc28\ubcc4\u2019\uc758 \ub9e5\ub77d\uc744 \ub4dc\ub7ec\ub0b4\uace0 \uc758\ubbf8\ud654 \ud558\ub294 \uc791\uc5c5\uc744 \uc9c4\ud589\ud588\ub2e4. \uadf8 \uacb0\u000d\u000a\uacfc \u2018\uc5ec\uc131\u2019\uc774\ub77c\ub294 \uc131\ucc28\ubcc4\uc758 \uad6c\uc870\uc640 \uc9c0\ubc30\uaddc\ubc94\ub4e4\uc774 \uac1c\uc778\ub4e4\uc758 \uc815\uccb4\uc131\uacfc \uad50\ucc28\ud558\ub294 \ubcf5\ud569\ucc28\u000d\u000a\ubcc4\uc740 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \ub178\ub3d9\uacbd\ud5d8\uacfc \uc0b6 \uc804\ubc18\uc5d0\uc11c \ubc1c\uc0dd\ud558\uace0 \uc601\ud5a5\uc744 \ubbf8\uce58\uace0 \uc788\uc5c8\ub2e4. \ucc28\ubcc4\u000d\u000a\uc758 \uc6d0\uc778\uc740 \uad6c\uc870\uc801\uc778 \uac83\uc784\uc5d0\ub3c4 \uad6c\uc870\ub294 \uc798 \ubcf4\uc774\uc9c0 \uc54a\uae30\uc5d0 \ucc28\ubcc4\uc758 \uacb0\uacfc \ubc0f \ud6a8\uacfc \ubaa8\ub450 \uac1c\u000d\u000a\uc778\ub4e4\uc774 \uac10\ub2f9\ud574\uc57c\ud558\ub294 \ubb38\uc81c\uac00 \ub418\uace0 \uc788\uc5c8\ub2e4. \ucc28\ubcc4\uc758 \uc6d0\uc778\uc774 \ubd88\ud3c9\ub4f1\ud55c \uad8c\ub825\uad00\uacc4\ub098 \uc9c0\ubc30\u000d\u000a\uaddc\ubc94\uc774\ub77c\ub294 \uad6c\uc870\uac00 \uc544\ub2c8\ub77c \uac1c\uc778\uc744 \ud5a5\ud560 \ub54c \uac1c\ubcc4 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc740 \uacbd\uc81c\uc801 \ubd88\uc774\uc775\ubfd0\ub9cc \u000d\u000a\uc544\ub2c8\ub77c \ubd80\ub2f9\ud55c \ucc98\uc6b0\uc5d0 \ub300\ud574 \uc790\uc2e0\uc758 \ub178\ub825\uc73c\ub85c \uadf9\ubcf5\uc744 \uc2dc\ub3c4\ud558\uba74\uc11c \uc9c0\uce58\uac70\ub098 \ubc18\ubcf5\ub418\ub294 \u000d\u000a\ucc28\ubcc4\uc5d0 \uc704\ucd95\ub418\uace0 \ubd88\uc548\ud574\uc9c0\uac70\ub098 \uc6b0\uc6b8\ud55c \uc0c1\ud0dc\uac00 \ub418\uae30\ub3c4 \ud588\ub2e4. \ubb34\uc5c7\ubcf4\ub2e4\ub3c4 \uc774\ub7f0 \uc0c1\ud669\uc740 \u000d\u000a\uac1c\uc778\ub4e4\uc774 \uc0ac\ud68c\ubcc0\ud654\ub97c \uc704\ud55c \uc5f0\ub300\uc758 \uc8fc\uccb4\uac00 \ub418\uae30 \uc5b4\ub835\uac8c \ud55c\ub2e4. \uc0ac\uc5c5 \uacb0\uacfc\ub97c \ubc1c\ud45c\ud558\ub294 \u000d\u000a\uc790\ub9ac\ub97c \ud1a0\ub860\ud68c\uac00 \uc544\ub2c8\ub77c \ud1a0\ud06c\uc1fc\ub85c \uae30\ud68d\ud55c \uac83\uc740 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc774 \uac19\uae30\ub3c4 \ud558\uace0 \ub2e4\ub974\uae30\u000d\u000a\ub3c4 \ud55c \uc11c\ub85c\uc758 \uacbd\ud5d8\uc5d0\uc11c \ucc28\ubcc4\uc744 \uc591\uc0b0\ud558\ub294 \uad6c\uc870\uc640 \ub9e5\ub77d\uc744 \ubc1c\uacac\ud558\uace0 \ucc28\ubcc4\uc758 \uad6c\uc870\ub97c \uae68\uae30 \u000d\u000a\uc704\ud55c \uc5f0\ub300\uc758 \uc8fc\uccb4\ub85c\uc11c \uc11c\ub85c\ub97c \ud655\uc778\ud558\ub294 \uc2dc\uac04\uacfc \uacf5\uac04\uc744 \uc5f4\uae30 \uc704\ud574\uc11c\uc600\ub2e4. \uadf8\ub7f0\ub370 \ud1a0\u000d\u000a\ud06c\uc1fc\ub97c \ub05d\ub0b4\uace0 \ub3cc\uc544\uac00\ub294 \uae38\uc5d0 \ubab8\ub3c4 \ub9c8\uc74c\ub3c4 \uc9c0\uce58\uace0 \uc544\ud320\uc9c0\ub9cc \uc778\ud130\ubdf0\uc640 \ud1a0\ud06c\uc1fc\uc5d0 \ucc38\uc5ec\u000d\u000a\ud558\uae38 \uc798\ud588\ub2e4\uba70 \ucc38\uc5ec\uc790\uac00 \uac74\ub128 \u201c\uc120\uc0dd\ub2d8, \ud798\ub4e4\uc5b4\ub3c4 \uc6b0\ub9ac \uadf8\ub9cc\ub450\uc9c0 \ub9d0\uc544\uc694\u201d\ub77c\ub294 \ub9d0\uc740 \uc0ac\u000d\u000a\uc5c5 \ub2f4\ub2f9\uc790\uc640 \ucc38\uc5ec\uc790\ub77c\ub294 \uacbd\uacc4\ub97c \ub118\uc5b4 \uc774\ubbf8 \uc5f0\uacb0\ub41c \uc5f0\ub300\uc758 \uc8fc\uccb4\ub85c\uc11c \uc11c\ub85c\ub97c \ud655\uc778\ud558\ub294 \u000d\u000a\ub9d0\uc774\uae30\uc5d0 \ubb34\ucc99 \uac10\ub3d9\uc801\uc774\uc5c8\uace0 \ud798\uc774 \ub418\uc5c8\ub2e4. \uc778\ud130\ubdf0\uc774 20\uba85\uc744 \uc12d\uc678\ud558\uace0 \uc9c8\ubb38\uc744 \uad6c\uc131\ud558\u000d\u000a\ub294 \uacfc\uc815\ubd80\ud130 \uc2e4\uc81c \uc778\ud130\ubdf0\ub97c \uc9c4\ud589\ud558\uace0 \uadf8 \uc774\uc57c\uae30\ub97c \uc815\ub9ac\ud558\uace0 \uc778\ud130\ubdf0\uc774\ub4e4\uacfc \ud568\uaed8 \ud1a0\ud06c\u000d\u000a\uc1fc\ub97c \uc900\ube44\ud558\ub294 \uacfc\uc815 \ubaa8\ub450\uac00 \ub0b4 \uc548\uc758 \uace0\uc815\uad00\ub150\uacfc \ud3b8\uacac\uc744 \ub9c8\uc8fc\ud558\uace0 \uae68\ub294 \uacfc\uc815\uc774\uc5c8\uace0 \uc5f0\u000d\u000a\ub300\uc758 \uc758\ubbf8\ub97c \ud655\uc7a5\ud558\ub294 \uc18c\uc911\ud55c \uc131\uc7a5\uc758 \uc2dc\uac04\uc774\uc5c8\ub2e4. \uc6b4\ub3d9\uc744 \ud558\uba74\uc11c \ud604\uc548\ub300\uc751\uc774\ub098 \uc0ac\uc548\u000d\u000a\ubcc4 \ud65c\ub3d9\uc744 \uc8fc\ub85c \ud558\ub2e4\ubcf4\uba74 \ubcf8\uc758 \uc544\ub2c8\uac8c \ud65c\ub3d9 \ub610\ud55c \ud30c\ud3b8\ud654\ub418\uae30 \uc2ed\uc0c1\uc774\ub2e4. \uadf8\ub798\uc11c \uc62c \ud55c\u000d\u000a\ud574 \ub0b4\ub0b4 \uba38\ub9bf\uc18d\uc744 \ub5a0\ub098\uc9c0 \uc54a\uc558\ub358 20\uba85\uc758 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc774\uc57c\uae30\ub97c \uc815\ub9ac\ud558\uace0 \ub9e5\ub77d\ud654 ","104 105\u000d\u000a\ud558\ub294 \uc791\uc5c5\uc774 \ub179\ub85d\uce58\ub294 \uc54a\uc558\uc9c0\ub9cc \ud604\uc7a5\uacfc \uc774\ub860, \uc6b4\ub3d9\uc758 \uc5f0\uacb0\uc744 \uafb8\uc900\ud788 \uace0\ubbfc\ud558\ub294 \uc791\uc5c5\uc774\u000d\u000a\ub77c\uc11c \uc88b\uc558\uace0 \uc758\ubbf8\uac00 \uc788\uc5c8\ub2e4. \uc774\uc81c\ub294 \uc138\uc0c1\uc744 \ud5a5\ud574 \ud3bc\uccd0\uc9c4 20\uba85 \uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \ubaa9\uc18c\u000d\u000a\ub9ac\uac00 \ucc28\ubcc4\uc744 \ub9d0\ud558\uace0 \ubc14\uafb8\ub294 \ub354 \ub9ce\uc740 \uc774\uc57c\uae30\ub85c \ubc88\uc9c8 \uc218 \uc788\ub3c4\ub85d \uace0\ubbfc\ud558\uace0 \ud65c\ub3d9\ud558\ub294 \uc77c\u000d\u000a\uc774 \ub0a8\uaca8\uc84c\ub2e4. (\ubc15\uc740\uc8fc \ud65c\ub3d9\uac00)\u000d\u000a\uc5ec\uc131\ub178\ub3d9\uc790\ub4e4\uc758 \uc77c\ud130\uc640 \uc0b6\uc744 \uac00\ub85c\uc9c0\ub974\ub294 \ubcf5\ud569\ucc28\ubcc4\u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","106 & \u000d\u000a\ubc15\uc218\ubbfc / \ube44\ud310\uc0ac\ud68c\ud559\ud68c, \uacbd\uc81c\uc640\uc0ac\ud68c, 2021\u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \u000d\u000a\uadf8\ub9bc\uc790 \ub178\ub3d9\u000d\u000a1. \uc5f0\uad6c\uc758 \ubb38\uc81c\uc758\uc2dd\u000d\u000a\uc74c\uc2dd \uc8fc\ubb38 \ud50c\ub7ab\ud3fc\uc740 \ubcf4\ub2e4 \ube60\ub978 \ubc30\ub2ec\uc744 \ub450\uace0 \uc18d\ub3c4 \uacbd\uc7c1\uc744 \ubc8c\uc774\uace0 \uc788\ub2e4. \ud558\uc9c0\ub9cc \uc74c\uc2dd \ubc30\ub2ec\u000d\u000a\uc758 \uc2e4\uc81c \ub178\ub3d9\uacfc\uc815\uc774 \uc18d\ub3c4\ub97c \ud5a5\ud55c \uc9c8\uc8fc\ub85c\ub9cc \uc774\ub8e8\uc5b4\uc9c4 \uac83\uc740 \uc544\ub2c8\ub2e4. \uae30\ub2e4\ub9bc\uc740 \ubc30\ub2ec \ub178\ub3d9\uc790\ub4e4\u000d\u000a\uc758 \uc77c\uc0c1\uc774\ub2e4. \uae30\ub2e4\ub9ac\ub294 \ub300\uc0c1\ub3c4 \uc774\uc720\ub3c4 \uac16\uac00\uc9c0\uc774\ub2e4. \uae38\uac70\ub9ac\uc5d0\uc11c \uc624\ud1a0\ubc14\uc774 \uc704\uc5d0 \uc549\uc544 \uc8fc\ubb38\u000d\u000a\uc694\uccad\uc744, \uc74c\uc2dd\uc774 \ud53d\uc5c5\uc744 \uc704\ud574 \uc900\ube44\uac00 \ub418\uae30\ub97c, \ud604\uc7a5 \uacb0\uc81c\ub97c \uc694\uccad\ud574\ub193\uace0 \uc804\ud654\ub97c \uc548 \ubc1b\ub294 \uace0\uac1d\u000d\u000a\uc744, \uac11\uc790\uae30 \ubd88\ud1b5\uc774 \ub41c \uc571\uc774 \uc81c\ub300\ub85c \uc791\ub3d9\ud558\uae30\ub97c \uae30\ub2e4\ub9b0\ub2e4. \uac70\ub9ac\ub97c \uac00\ub85c\uc9c0\ub974\uba70 \uc6c0\uc9c1\uc774\ub294 \uac83\u000d\u000a\uacfc \uac70\ub9ac\uc5d0\uc11c \ubb34\uc5b8\uac00\ub97c \uae30\ub2e4\ub9ac\ub294 \uac83 \ubaa8\ub450 \ubc30\ub2ec \ub178\ub3d9\uc790\uc758 \ubaa8\uc2b5\uc774\ub2e4. \ube60\ub978 \uc18d\ub3c4\uac00 \uadf8\ud1a0\ub85d \uc911\u000d\u000a\ubcf8 \uc5f0\uad6c\ub294 \ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\uc5d0\uc11c \ub178\ub3d9\uc740 \uc5b4\ub5bb\uac8c \uc774\uc6a9\ub418\uace0 \uc870\uc9c1\ub418\ub294\uc9c0\ub97c \ub9e4\uc6b0 \uad6c\uccb4\uc801\uc778 \ud604\uc7a5\uc758 \uc790\u000d\u000a\ub8cc\uc640 \ub370\uc774\ud130\ub97c \ubc14\ud0d5\uc73c\ub85c \ubd84\uc11d\ud55c \uc5f0\uad6c\uc774\ub2e4. \ud50c\ub7ab\ud3fc \uacbd\uc81c\uc758 \uae09\uc131\uc7a5\uacfc \ub354\ubd88\uc5b4 \ud50c\ub7ab\ud3fc \ub178\ub3d9\uc758 \ucc29\ucde8 \ubb38\u000d\u000a\uc81c\uac00 \uc911\uc694\ud55c \uc0ac\ud68c\uc801 \uc774\uc288\uac00 \ub418\uace0 \uc788\ub294 \uc0c1\ud669 \uc18d\uc5d0\uc11c \ud50c\ub7ab\ud3fc \ubc30\ub2ec\ub178\ub3d9\uc5d0 \ub300\ud55c \uc774\ub7ec\ud55c \ud604\uc7a5\uc131 \ub192\uc740 \uc5f0\u000d\u000a\uad6c\ub294 \uc2dc\uc758\uc801\uc808\ud560 \ubfd0 \uc544\ub2c8\ub77c, \uadf8 \uc2e4\ucc9c\uc801 \ud568\uc758\ub3c4 \ub9e4\uc6b0 \ud06c\ub2e4 \ud310\ub2e8\ub41c\ub2e4. \u000d\u000a\ud2b9\ud788, \uc774 \uc5f0\uad6c\ub294 \ud50c\ub7ab\ud3fc \uacbd\uc81c\uc640 \ub178\ub3d9\uc5d0 \ub300\ud55c \uae30\uc874 \uc5f0\uad6c\uc640 \uc774\ub860\uc5d0 \ub300\ud55c \ucda9\uc2e4\ud55c \ub9ac\ubdf0\ub97c \ubc14\ud0d5\uc73c\ub85c \ud50c\u000d\u000a\ub7ab\ud3fc \uacbd\uc81c\uc5d0\uc11c \ub178\ub3d9\uc758 \ube44\uac00\uc2dc\ud654\uac00 \uc2ec\ud654\ub418\ub294 \ud604\uc0c1\uc5d0 \uc8fc\ubaa9\ud558\uc5ec \ud50c\ub7ab\ud3fc \uc74c\uc2dd \ubc30\ub2ec \uc0b0\uc5c5\uc5d0\uc11c \uc0dd\uc0b0/\ube44\uc0dd\u000d\u000a\uc0b0 \uc2dc\uac04\uc774 \uc5b4\ub5bb\uac8c \uc0c8\ub86d\uac8c \uad6c\uc131\ub418\ub294\uc9c0, \ub178\ub3d9\uc758 \uc2dc\uacf5\uac04\uc740 \uc5b4\ub5bb\uac8c \ud1b5\uc81c\ub418\uace0 \uc788\ub294\uc9c0, \uadf8\ub9ac\uace0 \uc774\ub7ec\ud55c \uacfc\u000d\u000a\uc815\uc744 \ud1b5\ud574 \ub178\ub3d9\uc790\ub294 \uc5b4\ub5a0\ud55c \uc2dc\uac04 \uacbd\ud5d8\uc744 \ud558\uace0 \uc788\ub294\uc9c0\uc5d0 \uc8fc\ubaa9\ud558\uc5ec \ud50c\ub7ab\ud3fc \ubc30\ub2f9\ub178\ub3d9 \ud604\uc7a5\uc5d0 \ub300\ud55c \uacbd\u000d\u000a\ud5d8\uc5f0\uad6c\ub97c \uc218\ud589\ud558\uc600\ub2e4. \uc774\ub7ec\ud55c \uc5f0\uad6c\ub97c \ubc14\ud0d5\uc73c\ub85c \uc989\uc2dc\uc131\uc774 \uac15\uc870\ub418\ub294 \ud50c\ub7ab\ud3fc \uacbd\uc81c\uc5d0\uc11c \ube44\uac00\uc2dc\ud654\ub41c \uadf8\u000d\u000a\ub9bc\uc790 \ub178\ub3d9\uc774 \uc99d\uac00\ud558\ub294 \uacfc\uc815\uacfc \uae30\uc81c\ub97c \ub9e4\uc6b0 \uad6c\uccb4\uc801\uc73c\ub85c \ubd84\uc11d\ud558\uace0 \uc124\uba85\ud55c\ub2e4. \u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec \ub178\ub3d9\uc758 \uad6c\uccb4\uc801 \ud604\uc2e4\uc744 \uc774\ud574\ud560 \uc218 \uc788\ub294 \ub9e4\uc6b0 \ud604\uc7a5\uac10 \uc788\ub294 \uc790\ub8cc\ub4e4\uc744 \uc218\uc9d1, \uc815\ub9ac, \ubd84\u000d\u000a\uc11d\ud558\uc5ec \uc5f0\uad6c\uc758 \ud604\uc7a5\uc131\uc774 \ub9e4\uc6b0 \ub192\uc744 \ubfd0 \uc544\ub2c8\ub77c, \uc801\uc808\ud55c \uc774\ub860 \uc5f0\uad6c\uc640 \uac1c\ub150\ud654\uc5d0 \uae30\ubc18\ud558\uc5ec \uc5f0\uad6c\uc9c8\ubb38\uc744 \u000d\u000a\uc124\uc815\ud558\uace0 \uacbd\ud5d8 \uc790\ub8cc\ub4e4\uc744 \ub9e4\uc6b0 \ub17c\ub9ac\uc801\uc774\uace0 \uccb4\uacc4\uc801\uc73c\ub85c \uc124\uba85\ud558\uc5ec \u201c\uc774\ub860\uc801\uc73c\ub85c \ud0c4\ud0c4\ud55c \ud604\uc7a5 \uae30\ubc18 \uc5f0\u000d\u000a\uad6c(theoretically informed, empirical grounded research)\u201d\uc758 \ud55c \uc804\ud615\uc744 \ubcf4\uc5ec\uc900\ub2e4 \uc0dd\uac01\ub41c\ub2e4.\u000d\u000a\uc120\uc815\uc774\uc720","106 107\u000d\u000a\uc694\ud558\uac8c \uac15\uc870\ub418\ub294 \ubc30\ub2ec \uc5c5\uacc4\uc5d0\uc11c, \uc5b4\uca4c\ub2e4 \uc774\ub807\uac8c \uc544\ubb34\uac83\ub3c4 \ubabb \ud558\uace0 \uae30\ub2e4\ub9ac\ub294 \uc2dc\uac04\uc774 \ub9cc\uc5f0\ud558\u000d\u000a\uac8c \ub41c \uac83\uc77c\uae4c? \uac70\ub9ac\uc5d0\uc11c \uc624\ud1a0\ubc14\uc774\ub97c \uc138\uc6cc\ub193\uace0 \uc8fc\ubb38\uc744 \uae30\ub2e4\ub9ac\ub294 \ubc30\ub2ec \ub178\ub3d9\uc790\ub97c \ubcf4\ub294 \uac83\uc774 \u000d\u000a\uc5b4\ub835\uc9c0 \uc54a\uc740\ub370\ub3c4, \uc65c \ubc30\ub2ec \ub178\ub3d9\uc790\uc758 \ub178\ub3d9\uc5d0\uc11c\ub294 \uc9c8\uc8fc\uc640 \uc18d\ub3c4\ub9cc\uc774 \uac15\uc870\ub418\uace0 \uc788\ub294 \uac83\uc77c\uae4c? \u000d\u000a\uc9c8\uc8fc\uc640 \uae30\ub2e4\ub9bc\uc774\ub77c\ub294 \uc2dc\uac04 \uacbd\ud5d8\uc740 \ubc30\ub2ec \uc18d\ub3c4\ub97c \ube60\ub974\uac8c \ud558\ub294 \uacfc\uc815\uc5d0\uc11c \ub9cc\ub4e4\uc5b4\uc9c4 \ub3d9\uc804\uc758 \u000d\u000a\uc591\uba74\uc774\ub2e4. \uc8fc\ubb38 \uc694\uccad\uc774 \ub4e4\uc5b4\uc624\uc790\ub9c8\uc790 \ubc30\ub2ec\uc744 \ud558\ub824\uba74 \ub178\ub3d9\uc790\uac00 \uae30\ub2e4\ub9ac\uace0 \uc788\uc5b4\uc57c \ud558\uace0, \ub354\uc6b1\u000d\u000a\ub354 \ube60\ub974\uac8c \ubc30\ub2ec\uc744 \uc644\ub8cc\ud558\uae30 \uc704\ud574\uc120 \uc18d\ub3c4\ub97c \ub0b4\uc57c \ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uc18c\ube44\uc790\uac00 \uc6d0\ud558\ub294 \ube60\ub978 \ubc30\u000d\u000a\ub2ec\uc774 \ubc30\ub2ec \uacfc\uc815 \uc804\uccb4\ub97c \uc9c0\ubc30\ud558\uace0 \uc788\uace0, \uc774 \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uae30 \uc704\ud558\uc5ec \uace0\uac1d\uc758 \uc694\uad6c\uc5d0 \ub178\ub3d9\uc790\uc758 \u000d\u000a\uc2dc\uac04\uc774 \uc885\uc18d\ub41c\ub2e4.(Sharma, 2014) \uadf8\ub7ec\ub098 \uc2dc\uc7a5\uc758 \uc704\uacc4\ub294 \uc790\uc5f0\uc801\uc73c\ub85c \ubc1c\uc0dd\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \uc0c8\ub85c\u000d\u000a\uc6b4 \uacbd\uc81c\uac00 \ud615\uc131\ub418\ub294 \uacfc\uc815\uc5d0\uc11c \uc5ec\ub7ec \uc81c\ub3c4\uc640 \uae30\uc220\uc758 \uc0c1\ud638\uc791\uc6a9 \uc18d\uc5d0\uc11c \uc7ac\uc870\uc815 \ub418\ub294 \uacfc\uc815\uc5d0\uc11c \u000d\u000a\ub9cc\ub4e4\uc5b4\uc9c4\ub2e4. \uae30\uc220\uc801\u30fb\uc0ac\ud68c\uc801 \uc870\uac74\uc744 \ubc18\uc601\ud55c \ub178\ub3d9\uacfc\uc815\uacfc \ud1b5\uc81c\uc804\ub7b5\uc774 \ub4f1\uc7a5\ud558\uc5ec, \uc801\uadf9\uc801\uc73c\ub85c \u000d\u000a\ud3c9\uac00 \uae30\uc900\uc744 \uc0c8\ub86d\uac8c \uc81c\uc2dc\ud558\uace0 \uac15\uc81c\ud55c\ub2e4. \uc774 \ub17c\ubb38\uc740 \ud55c\uad6d\uc758 \ud50c\ub7ab\ud3fc \uc74c\uc2dd \ubc30\ub2ec\uc758 \uc0ac\ub840\ub97c \uc911\u000d\u000a\uc2ec\uc73c\ub85c \uc0b0\uc5c5\uc774 \ubc1c\uc804\ud574 \uc628 \uacfc\uc815\uc5d0\uc11c \uac15\uc870\ub418\ub294 \ub17c\ub9ac\uc640 \uc6b4\uc601\ubc29\uc2dd\uc744 \ubd84\uc11d\ud558\uace0, \uc774\ub7ec\ud55c \ud1b5\uc81c\uc5d0 \u000d\u000a\ub530\ub77c \ub178\ub3d9\uc790\uac00 \uacaa\ub294 \uc2dc\uac04 \uacbd\ud5d8\uacfc \uacb0\uacfc\ub97c \ubd84\uc11d\ud558\uc600\ub2e4. \uc774\ub97c \ud1b5\ud574 \ud50c\ub7ab\ud3fc \uc74c\uc2dd \ubc30\ub2ec \uc0b0\uc5c5\uc5d0\u000d\u000a\uc11c \uae30\uc5c5\uc758 \uc0dd\uc0b0\uc131\uc744 \ub192\uc774\ub294 \uc804\ub7b5\uc774 \uadf8\ub9bc\uc790 \ub178\ub3d9\uc758 \uc99d\uac00\ub85c \uc774\uc5b4\uc9c4\ub2e4\ub294 \uc810\uc744 \ubcf4\uc5ec\uc904 \uac83\uc774\ub2e4. \u000d\u000a2. \uae30\uc220\ubc1c\uc804\uc774 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \uc2dc\uac04\uc131\uacfc \uac08\ub4f1\u000d\u000a1) \uae30\uc220\uc911\uc2ec \uacbd\uc81c\uac00 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \uc989\uc2dc\uc131\u000d\u000aICT \uae30\uc220\uc744 \ud1b5\ud574 \uc0c8\ub86d\uac8c \uc7ac\uad6c\ucd95\ub41c \uc2dc\uacf5\uac04 \uacbd\ud5d8\uc740 \uc989\uc2dc\uc131Immediacy\uc5d0 \ub300\ud55c \uae30\ub300\ub97c \ub9cc\ub4e4\u000d\u000a\uc5b4\ub0b4\uace0 \uc788\ub2e4.(Tomlinson, 2007) \uc989\uc2dc\uc131\uc740 \uc2dc\uac04\uc0c1\uc73c\ub85c\ub294 \uc9c0\uccb4\uac00 \uc5c6\ub294 \uac83, \uacf5\uac04\uc801\uc73c\ub85c\ub294 \ub5a8\uc5b4\uc838 \uc788\u000d\u000a\uc9c0 \uc54a\uc740 \uac83\uc744 \uc758\ubbf8\ud55c\ub2e4. \uc774\ub294 \u201c\ub9e4\uac1c \uacfc\uc815\uc774 \uc804\ubc18\uc801\uc73c\ub85c \ubd95\uad34\ud558\ub294 \uac10\uac01\u201d\uc73c\ub85c\uc11c \uc9c0\uae08\uacfc \uc774\ud6c4, \u000d\u000a\uc695\ub9dd\uacfc \ub9cc\uc871\uc744 \ubd84\ub9ac\ud558\ub358 \uc2dc\uacf5\uac04\uc801 \uac70\ub9ac\uac10\uc774 \uc0ac\ub77c\uc9c0\ub294 \ubb38\ud654\uc801 \uacbd\ud5d8\uc774\ub2e4.(p.74) \uc21c\uc2dd\uac04\uc5d0 \uc815\ubcf4\u000d\u000a\ub97c \uc804\uc1a1\ud558\uace0 \ubc18\uc751\uc744 \ub418\ub3cc\ub824\uc8fc\ub294 \uae30\uc220\uc774 \ub4f1\uc7a5\ud558\uc5ec \uba40\ub9ac \ub5a8\uc5b4\uc9c4 \uacf3\uc744 \uc5f0\uacb0\ud558\ub294 \uacfc\uc815 \uc790\uccb4\uac00 \u000d\u000a\ube68\ub77c\uc9d0\uc5d0 \ub530\ub77c, \uc0ac\ub78c\ub4e4\uc740 \ub9e4\uac1c \uacfc\uc815\uc774 \uc5c6\ub294 \ub4ef\ud55c, \ud3b8\ub9ac\ud558\uace0 \uc989\uac01\uc801\uc778 \uc811\uc18d\uacfc \uc5f0\uacb0\uc758 \uac10\uac01\u000d\u000a\uc744 \uc990\uae30\uac8c \ub418\uc5c8\ub2e4. \uc774\ub7ec\ud55c \uc989\uc2dc\uc131\uc5d0 \ub300\ud55c \uae30\ub300\ub294 \ubbf8\ub514\uc5b4 \uc774\uc6a9 \uacbd\ud5d8\uc5d0 \uad6d\ud55c\ub41c \uac83\uc774 \uc544\ub2c8\ub2e4. \u000d\u000aICT\uc640 \ubbf8\ub514\uc5b4\ub97c \ud65c\uc6a9\ud558\ub294 \uc601\uc5ed\uc774 \uc77c\ud130, \uc18c\ube44 \uacf5\uac04 \ub4f1 \uacbd\uc81c\uc0dd\ud65c \uc804\ubc18\uc73c\ub85c \ud655\uc7a5\ub418\uc5c8\uae30 \ub54c\ubb38\u000d\u000a\uc774\ub2e4. \ucd5c\uadfc \ub298\uc5b4\ub098\uace0 \uc788\ub294 \uc2b9\ucc28 \uacf5\uc720 \uc11c\ube44\uc2a4, \uc74c\uc2dd \ubc30\ub2ec \ub4f1\uc758 \uac01\uc885 O2OOnline to Offline \uc11c\ube44\u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \uadf8\ub9bc\uc790 \ub178\ub3d9"," & 108\u000d\u000a\uc2a4\ub294 \ub354 \ud6a8\uc728\uc801\uc73c\ub85c \uadf8\ub9ac\uace0 \uc2e0\uc18d\ud558\uac8c \uc11c\ube44\uc2a4\ub97c \uc774\uc6a9\ud558\uace0 \uc2f6\uc740 \uc18c\ube44\uc790\ub4e4\uc758 \uc694\uad6c\ub97c \uaca8\ub0e5\ud558\u000d\u000a\uace0 \uc788\ub2e4.(Taylor, 2018; van der Burg et al, 2019) \uc74c\uc2dd \ubc30\ub2ec\uc740 \uc0c1\uc810\uae4c\uc9c0\uc758 \uac70\ub9ac, \uc774\ub3d9 \uc2dc\uac04\uc744 \ub6f0\uc5b4\ub118\uc5b4 \uc9c1\u000d\u000a\uc811 \uc774\ub3d9\ud558\uc9c0 \uc54a\uace0\ub3c4 \ube60\ub978 \uc2dc\uac04 \ub0b4\uc5d0 \uc2dd\uc0ac\ub97c \ud560 \uc218 \uc788\ub3c4\ub85d \ub9cc\ub4e4\uc5b4\uc900\ub2e4. \u000d\u000a\uae30\uc5c5\uc740 \uc989\uc2dc\uc131\uc5d0 \ub300\ud55c \uc18c\ube44\uc790\uc758 \uae30\ub300\ub97c \ucda9\uc871\uc2dc\ud0a4\uae30 \uc704\ud574 \ub2e4\uc591\ud55c \uae30\uc220\uc744 \ub3d9\uc6d0\ud55c\ub2e4. \uc6b0\ubc84\u000d\u000a\uc640 \uac19\uc740 \uc2b9\ucc28 \uacf5\uc720, \ubc30\ub2ec\uc758\ubbfc\uc871\uacfc \uac19\uc740 \uc74c\uc2dd \ubc30\ub2ec\uc740 \ud544\uc694\ud560 \ub54c\uc5d0 \ubc14\ub85c \ud544\uc694\ud55c \ub9cc\ud07c\ub9cc \uc790\uc6d0\u000d\u000a\uc744 \uc0ac\uc6a9\ud558\uace0 \uc774\uc5d0 \ud574\ub2f9\ud558\ub294 \uac00\uaca9\uc744 \uce58\ub974\ub294 \uc628\ub514\ub9e8\ub4dcOn-demand \uc11c\ube44\uc2a4\uc774\ub2e4. \uc628\ub514\ub9e8\ub4dc \uc11c\ube44\u000d\u000a\uc2a4\uc758 \ud2b9\uc9d5 \uc911 \ud558\ub098\ub294 \uc790\uc6d0\uacf5\uc720Resource Pooling\uc774\ub2e4.(\ub098\uc5f0\ubb35, 2011) \ub300\ub7c9\uc758 \ucef4\ud4e8\ud305 \uc778\ud504\ub77c\ub97c \ud558\ub098\uc758 \u000d\u000a\ud480pool\ub85c \ud615\uc131\ud558\uace0, \uacf5\uc720\ub41c \uc790\uc6d0 \ud480\uc744 \uc5ec\ub7ec \uace0\uac1d\uc774 \ub3d9\uc2dc\uc5d0 \uc0ac\uc6a9\ud558\ub294 \ubc29\uc2dd\uc744 \ud1b5\ud574 \uc720\ud734\uc790\uc6d0\u000d\u000a\uc744 \ucd5c\uc18c\ud654\ud560 \uc218 \uc788\ub2e4. \uc628\ub514\ub9e8\ub4dc \ucef4\ud4e8\ud305\uc740 \uacf5\uc720\ub41c \uc790\uc6d0\uc744 \uad00\ub9ac\ud558\uace0, \uace0\uac1d \uc694\uccad\uc5d0 \ub530\ub77c \uc2e4\uc2dc\u000d\u000a\uac04\uc73c\ub85c \uc790\uc6d0\uc744 \ud560\ub2f9\ud574 \uc8fc\ub294 \uc5ec\ub7ec \uae30\uc220\uc744 \ud1b5\ud574 \uc6b4\uc601\ub41c\ub2e4. \u000d\u000a\uc6b0\ubc84, \ubc30\ub2ec\uc758\ubbfc\uc871 \uac19\uc740 \uc628\ub514\ub9e8\ub4dc \uc11c\ube44\uc2a4\ub4e4\uc740 \uc774\ub7ec\ud55c \ub17c\ub9ac\ub97c \ub178\ub3d9\ub825\uc744 \uc870\uc9c1\ud558\uace0 \uad50\ud658\ud558\u000d\u000a\ub294\ub370 \uc801\uc6a9\ud558\uace0 \uc788\ub2e4. \uadf8\ub7f0\ub370 IT \uc11c\ube44\uc2a4\uc640 \ub2ec\ub9ac O2O \uc11c\ube44\uc2a4\uc758 \uacbd\uc6b0 \uacf5\uc720\uc790\uc6d0\uc774 \uc11c\ubc84\ub098 \uc2a4\u000d\u000a\ud1a0\ub9ac\uc9c0 \uac19\uc740 \uae30\uacc4\uac00 \uc544\ub2c8\ub77c \uc778\uac04\uc758 \ub178\ub3d9\ub825\uc774\ub2e4. \ub178\ub3d9\ud558\ub294 \uc778\uac04\uc758 \uacf5\uac04\uacfc \uc2dc\uac04\uc740 \uc18c\ud504\ud2b8\uc6e8\u000d\u000a\uc5b4\ub85c \ud1b5\ud569\ub41c \uac00\uc0c1\uacf5\uac04\uc774 \uc544\ub2c8\ub77c \uc2e4\uc7ac \uc138\uacc4\uc5d0 \uc18d\ud574\uc788\ub2e4. \ub530\ub77c\uc11c \ucef4\ud4e8\ud305 \ub17c\ub9ac\ub97c \ub178\ub3d9\ub825 \uc870\u000d\u000a\uc9c1\uc5d0 \uc801\uc6a9\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \uc5ec\ub7ec \uac00\uc9c0 \uc65c\uace1\uacfc \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud558\uac8c \ub41c\ub2e4. \uc608\ub97c \ub4e4\uc5b4, \uc544\ub9c8\uc874 \ubbf8\ucf00\u000d\u000a\ub2c8\uceec\ud130\ud06cAMT\uac00 \ub178\ub3d9\uc790\ub4e4\uc744 \uae30\uc874\uc758 \uae30\uc220\uc2dc\uc2a4\ud15c\uc5d0 \ub07c\uc6cc \ub123\uc5b4 \uc774\ub4e4\uc758 \ub178\ub3d9\uc744 \uae30\uacc4\uac00 \uc218\ud589\ud558\u000d\u000a\ub294 \uac83\ucc98\ub7fc \ubcf4\uc774\uac8c \ub9cc\ub4dc\ub294\ub370, \uadf8 \uacb0\uacfc \ub178\ub3d9\uc790\uc758 \uc874\uc7ac\uc640 \ub178\ub3d9\uc2dc\uc7a5\uc758 \uc704\uacc4\uc801 \uc9c8\uc11c\uac00 \ube44\uac00\uc2dc\u000d\u000a\ud654\ub41c\ub2e4.(Irani, 2015)\u000d\u000a2) \uae30\uc220\ud1b5\uc81c\uc640 \uc2dc\uac04\uacbd\ud5d8\u000d\u000a\ud50c\ub7ab\ud3fc \ub178\ub3d9\uc758 \ud655\uc0b0\uc744 \uacc4\uae30\ub85c, \ub178\ub3d9\uacfc\uc815\uc5d0\uc11c \uae30\uc220\uc758 \uc5ed\ud560\uc5d0 \ub300\ud55c \uad00\uc2ec\uc774 \ub2e4\uc2dc \ub192\uc544\uc9c0\uace0 \u000d\u000a\uc788\ub2e4. \uc54c\uace0\ub9ac\uc998 \ud1b5\uc81c\uc5d0 \ub300\ud55c \uc5f0\uad6c\ub4e4\uc740 \uc54c\uace0\ub9ac\uc998\uc744 \uc2e4\uc2dc\uac04 \uac10\uc2dc, \uc2ec\ub9ac\uc801 \ucda9\ub3d9, \ubb38\ud654\uc801 \uaddc\ubc94\u000d\u000a\uc744 \ubcf5\ud569\uc801\uc73c\ub85c \uc0ac\uc6a9\ud558\uba74\uc11c \uac15\uc555\uacfc \ub3d9\uc758\uc758 \uc804\ub7b5\uc744 \ub3d9\uc2dc\uc5d0 \uad6c\uc0ac\ud558\ub294 \ucd1d\uccb4\uc801 \ud1b5\uc81c\uc2dc\uc2a4\ud15c\uc73c\ub85c \u000d\u000a\ud3c9\uac00\ud55c\ub2e4. AMT\uc640 \uac19\uc740 \ub9c8\uc774\ud06c\ub85c \ud0dc\uc2a4\ud06c \uc5c5\ubb34 \ud50c\ub7ab\ud3fc\ub4e4\uc5d0 \ub300\ud55c \uc5f0\uad6c\uc5d0\uc11c\ub294 \ud2b9\ud788 \ub514\uc9c0\ud138 \u000d\u000a\ud14c\uc77c\ub7ec\ub9ac\uc998\uc774\ub77c\ub294 \uc6a9\uc5b4\ub97c \ud1b5\ud574 \ub178\ub3d9\uc758 \ud30c\ud3b8\ud654, \uc2e4\ud589\uacfc \uad6c\uc0c1\uc758 \ubd84\ub9ac, \ud0c8\uc219\ub828\ud654\uac00 \ub098\ud0c0\ub09c\ub2e4\u000d\u000a\ub294 \uc810\uc744 \uac15\uc870\ud55c\ub2e4.(\ube0c\ub808\uc774\ubc84\ub9cc, 1974; Irani, 2015; Altenreid, 2020) \uc74c\uc2dd \ubc30\ub2ec\uc758 \uacbd\uc6b0 \uc54c\uace0\ub9ac\uc998\uc740 \ud53d\uc5c5 \uc2dc\uac04, \u000d\u000a\ubc30\ub2ec \uc2dc\uac04, \ubc30\ub2ec \uacbd\ub85c, \uc74c\uc2dd \uc0c1\ud0dc\uc640 \uace0\uac1d \ub9cc\uc871\uc744 \ubcc4\ub3c4\uc758 \ud56d\ubaa9\uc73c\ub85c \ub9cc\ub4e4\uace0 \uad6c\uccb4\uc801\uc778 \uc218\uce58\ub97c ","108 109\u000d\u000a\ud1b5\ud574 \ud3c9\uac00\ud558\uc5ec \uc6c0\uc9c1\uc774\ub294 \ubc29\ud5a5, \uc2dc\uac04\uacfc \ud0dc\ub3c4\ub97c \uaddc\uc728\ud55c\ub2e4. \uc54c\uace0\ub9ac\uc998\uc740 \ud3c9\uc810\uc5d0 \ub530\ub77c \uc2a4\ucf00\uc904\uc744 \u000d\u000a\uc120\ud0dd\ud560 \uc218 \uc788\ub294 \uad8c\ud55c\uc5d0 \ucc28\ub4f1\uc744 \ub450\uac70\ub098, \uc791\uc5c5\uc644\uc218 \uc2dc\uac04\uc744 \uc124\uc815\ud558\uace0 \uc774\ub97c \uc7ac\ucd09\ud55c\ub2e4.(Giesbach et\u000d\u000aal., 2019) \uc774\ucc98\ub7fc \ub178\ub3d9\uacfc\uc815\uc758 \uc18d\ub3c4\uc640 \uc21c\uc11c\ub97c \uad6c\uccb4\uc801\uc73c\ub85c \uc9c0\uc2dc\ud558\uc5ec \uc2e0\uccb4\uc758 \uc6c0\uc9c1\uc784, \ub9ac\ub4ec, \ud15c\ud3ec\u000d\u000a\ub97c \uc9c1\uc811\uc801\uc73c\ub85c \uc870\uc791\ud558\ub294 \uac83\uc740 \uc2dc\uac04\uc5d0 \ub300\ud55c \ub178\ub3d9\uc790\uc758 \uc7a5\uc545\ub825\uc744 \uc57d\ud654\ud55c\ub2e4.(Edwards, 1979)\u000d\u000a\uadf8\ub7f0\ub370 \ub300\ubd80\ubd84\uc758 \uc5f0\uad6c\uc5d0\uc11c \ud50c\ub7ab\ud3fc \ub178\ub3d9\uc790\ub4e4\uc774 \uc77c\uac10\uc744 \uae30\ub2e4\ub9ac\ub294 \ub370 \uc2dc\uac04\uc744 \uc18c\uc694\ud55c\ub2e4\ub294 \u000d\u000a\uc810\uc774 \uc5b8\uae09\ub418\uace0 \uc788\uc74c\uc5d0\ub3c4, \uc77c\uc744 \uc2dc\uc791\ud558\uae30 \uc804 \uc900\ube44\ud558\ub294 \uc2dc\uac04\uc774\ub098 \uc5c5\ubb34\uac00 \ubc30\uce58\ub418\uae38 \uae30\ub2e4\ub9ac\ub294 \u000d\u000a\uc2dc\uac04 \ub4f1\uc5d0 \ub300\ud55c \uad00\uc2ec\uc740 \ud06c\uc9c0 \uc54a\ub2e4. \uc5c5\ubb34\uc2dc\uac04\uacfc \uc5c5\ubb34\ub7c9\uc774 \uc815\ud574\uc838 \uc788\uc9c0 \uc54a\uace0, \uace0\uc6a9/\ube44\uace0\uc6a9\uc758 \u000d\u000a\uacbd\uacc4\ub97c \uc624\uac00\ub294 \uc774\ub4e4\uc5d0\uac8c \uc77c\uac10\uc744 \uae30\ub2e4\ub9ac\ub294 \uc2dc\uac04\uc740 \uc0b6\uc758 \ubd88\ud655\uc2e4\uc131\uc744 \ub192\uc774\ub294 \uc2dc\uac04\uc774\ub2e4. \uc774\ubfd0\u000d\u000a\ub9cc \uc544\ub2c8\ub77c \uae30\ub2e4\ub9bc\uc774\ub77c\ub294 \uac83\uc740 \uae30\ub2e4\ub9ac\uac8c \ub9cc\ub4dc\ub294 \uad8c\ub825\uc744 \ud1b5\ud574 \uac15\uc81c\ub418\ub294 \uac83\uc774\uae30\ub3c4 \ud558\ub2e4.(Purser,\u000d\u000a2012) \uc77c\uc744 \ud558\uae30 \uc704\ud55c \ub300\uae30\uc2dc\uac04\uc740 \uc784\uae08 \ub178\ub3d9\uc744 \uc704\ud574\uc11c \ubc18\ub4dc\uc2dc \ud544\uc694\ud55c \uacfc\uc815\uc774\uc9c0\ub9cc \ubcf4\uc0c1\uc774 \uc8fc\u000d\u000a\uc5b4\uc9c0\uc9c0 \uc54a\uc73c\uba70 \ub178\ub3d9\uacfc\uc815\uc5d0\uc11c\uc758 \uad8c\ub825 \uad00\uacc4\uc5d0 \ub530\ub77c \ub9cc\ub4e4\uc5b4\uc9c0\ub294 \uadf8\ub9bc\uc790 \ub178\ub3d9\uc774\ub77c\uace0 \ubcfc \uc218 \uc788\u000d\u000a\ub2e4.(\uc77c\ub9ac\uce58, 2017) \ub530\ub77c\uc11c \ub300\uae30\uc2dc\uac04\uc774 \uc5b4\ub5bb\uac8c \ub9cc\ub4e4\uc5b4\uc9c0\uace0, \ud3c9\uac00\ubc1b\ub294\uac00\uc5d0 \ub300\ud55c \ubd84\uc11d\uc740 \ub300\uae30\uc2dc\uac04\u000d\u000a\uc744 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \ud798\uc774 \ubb34\uc5c7\uc778\uc9c0\ub97c \uc54c\ub824\uc900\ub2e4.\u000d\u000a\ud50c\ub7ab\ud3fc \ub178\ub3d9\uacfc \uac19\uc774 \uae30\uc220\uc774 \ub178\ub3d9\uacfc\uc815\uc744 \ub9e4\uac1c\ud558\ub294 \uacbd\uc6b0 \ub300\uae30\uc2dc\uac04, \uc900\ube44 \uc2dc\uac04\uc740 \uac89\uc73c\ub85c \ub4dc\u000d\u000a\ub7ec\ub098\uc9c0 \uc54a\uc744 \uc218 \uc788\ub2e4. \uc778\ub3c4\uc5d0 \ubab0\ub824\uc788\ub294 \ucf5c\uc13c\ud130\ucc98\ub7fc \ubb3c\ub9ac\uc801\uc73c\ub85c \uc0dd\uc0b0 \ud604\uc7a5\uc744 \ubd84\ub9ac\ud558\uace0 \uc228\uae30\u000d\u000a\ub294 \uac83\uc774 \uae30\uc220\uc744 \ud1b5\ud574 \ub354\uc6b1 \uc26c\uc6cc\uc84c\uace0, \ud504\ub85c\uadf8\ub7a8 \uc870\uc791\uc744 \ud1b5\ud574 \uc5c5\ubb34 \uacfc\uc815\uc758 \ud2b9\uc815 \ubd80\ubd84\uc740 \ub4dc\ub7ec\u000d\u000a\ub0b4\uace0, \ud2b9\uc815 \ubd80\ubd84\uc740 \ub4dc\ub7ec\ub098\uc9c0 \uc54a\uac8c \ub9cc\ub4e4 \uc218 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \ub178\ub3d9\uacfc\uc815\uc758 \ube44\uac00\uc2dc\ud654\ub294 \uacf5\uc815\uc758 \u000d\u000a\ub4a4\uc5d0\uc11c \uc77c\ud558\uace0 \uc788\ub294 \uc778\uac04\uc758 \uc874\uc7ac\ub97c \uc9c0\uc6b0\uba70, \ub354 \ub098\uc544\uac00 \uc778\uac04\uc774 \uae30\uacc4\uc5d0 \uc758\ud574 \ub300\uccb4\ub418\ub294 \uac83\uacfc \u000d\u000a\uac19\uc740 \ucc29\uc2dc\ub97c \ub9cc\ub4e4\uc5b4\ub0b8\ub2e4.(\uadf8\ub808\uc774\u30fb\uc218\ub9ac, 2019) \ub354\uad70\ub2e4\ub098 \uc77c\uc0c1\uc0dd\ud65c \uc18d\uc5d0 \uae30\uc220\uc774 \uc790\ub9ac \uc7a1\uc744\uc218\ub85d \uc778\uac04\u000d\u000a\uc774 \uae30\uacc4\uc758 \uc791\ub3d9\uc744 \ub3d5\uac70\ub098, \uc778\uac04\uacfc \uae30\uacc4\uac00 \ud568\uaed8 \uc77c\uc744 \ud558\ub294 \uc0c1\ud669\uc740 \ub354\uc6b1 \ub298\uc5b4\ub09c\ub2e4.(\ub7a8\ubc84\ud2b8, 2016;\u000d\u000aNardi & Ekbia, 2014) \ube44\uac00\uc2dc\ud654\ub418\uc5b4 \uc788\ub294 \ubb34\uae09\ub178\ub3d9\uc744 \ubd84\uba85\ud788 \ub4dc\ub7ec\ub0b4\ub294 \uac83\uc740 \ub178\ub3d9 \ud604\uc7a5\uc5d0\uc11c \uae30\uc220\u000d\u000a\ub9e4\uac1c\uc758 \uc5ed\ud560\uc774 \ub298\uc5b4\ub098\ub294 \uc0c1\ud669\uc5d0\uc11c \uc810\uc810 \uc911\uc694\ud55c \uacfc\uc81c\uac00 \ub418\uace0 \uc788\ub2e4.\u000d\u000a3. \uc74c\uc2dd\ubc30\ub2ec \uc6b4\uc601\ubaa8\ub378 \uc720\ud615\uacfc \uc6b4\uc601\uc804\ub7b5\u000d\u000a1) \uc5f0\uad6c\ub300\uc0c1\u000d\u000a\ubc30\ub2ec\uc758\ubbfc\uc871\uc740 \uc74c\uc2dd\uc810\uacfc \uace0\uac1d\uc744 \uc5f0\uacb0\ud558\ub294 \uc8fc\ubb38\uc571\uc774\ub2e4. \ubc30\ub2ec\uc740 \uc804\ubb38 \ubc30\ub2ec \ub300\ud589\uc0ac, \ud06c\ub77c\u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \uadf8\ub9bc\uc790 \ub178\ub3d9"," & 110\u000d\u000a\uc6b0\ub4dc\uc18c\uc2f1 \uc778\ub825 \ub4f1 \ub2e4\uc591\ud55c \ubc29\uc2dd\uc73c\ub85c \uc774\ub904\uc9c4\ub2e4. \ub124\uc774\ubc84\ub098 \uc9c0\ub9c8\ucf13\uacfc \uac19\uc740 \uc624\ud508\ub9c8\ucf13\uc5d0\uc11c \uc8fc\ubb38\u000d\u000a\uc744 \ud558\uba74 \uc6b0\uccb4\uad6d, CJ\ub300\ud55c\ud1b5\uc6b4 \ub4f1 \ub2e4\uc591\ud55c \ud68c\uc0ac\uc5d0\uc11c \ubc30\uc1a1\uc744 \ub2f4\ub2f9\ud558\ub294 \uac83\uacfc \ube44\uc2b7\ud558\ub2e4. \ubbf8\uad6d\uc774\u000d\u000a\ub098 \uc720\ub7fd\uc758 \uc74c\uc2dd \ubc30\ub2ec \ud50c\ub7ab\ud3fc\uc774 \ub300\uccb4\ub85c 3\uba74 \uc2dc\uc7a510)\uc758 \ud2b9\uc131\uc744 \ub760\ub294 \ub370 \ubc18\ud574, \uc911\uad6d\uc774\ub098 \ud55c\uad6d\u000d\u000a\uc758 \uacbd\uc6b0 \uc774\uc640\ub294 \ub2e4\ub978 \uc591\uc2dd\uc758 \uc6b4\uc601\ubaa8\ub378\uc774 \ub098\ud0c0\ub098\uace0 \uc788\ub2e4(Lei, 2021; \uc870\uaddc\uc900, 2021). \uc774 \uc5f0\u000d\u000a\uad6c\uc5d0\uc11c\ub294 \ud55c\uad6d\uc758 \ud50c\ub7ab\ud3fc \uc74c\uc2dd \ubc30\ub2ec \ub300\ud589\uc758 \uc6b4\uc601\ubaa8\ub378\uc744 \uc8fc\ubb38-\ubc30\ub2ec \ud50c\ub7ab\ud3fc\uc758 \ud1b5\ud569 \uc5ec\ubd80\uc640 \u000d\u000a\uace0\uc6a9 \uad00\uacc4\uc5d0 \ub530\ub77c \ub124 \uac00\uc9c0 \uc720\ud615\uc73c\ub85c \uad6c\ubd84\ud588\ub2e4. \ud1b5\ud569 \uc720\ud615\uc740 \uc54c\uace0\ub9ac\uc998\uc744 \ud1b5\ud574 \ub178\ub3d9\uacfc\uc815 \ud1b5\u000d\u000a\uc81c\uac00 \uc774\ub8e8\uc5b4\uc9c0\uace0, \ubd84\ub9ac\ud615\uc740 \uc0ac\ubb34\uc2e4 \uad00\ub9ac\uc790\uc758 \uc778\uc801\ud1b5\uc81c\uac00 \ub354 \uac15\ud558\ub2e4. \ud754\ud788 \uc0dd\uac01\ud558\ub294 \ubd80\uc5c5\uc73c\u000d\u000a\ub85c \uc5b8\uc81c\ub4e0 \uc571\uc5d0 \uc811\uc18d\ud558\uc5ec \ubc30\ub2ec\uc744 \ud558\ub294 \uac83\uc740 \ud1b5\ud569-\ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1 \ubaa8\ub378\uc774\uace0, \ubd84\ub9ac\ud615-\uc544\uc6c3\uc18c\u000d\u000a\uc2f1\uc758 \uacbd\uc6b0 \uc9c0\uc5ed\uc758 \ub300\ud589\uc0ac\ubb34\uc2e4\uc744 \uc911\uc2ec\uc73c\ub85c \uc778\ub825\ubaa8\uc9d1\uacfc \uc6b4\uc601\uc774 \uc774\ub8e8\uc5b4\uc9c0\ub294 \ubaa8\ub378\ub85c \uc804\uc5c5 \ubc30\u000d\u000a\ub2ec \ub178\ub3d9\uc790\uac00 \uc8fc\ub97c \uc774\ub8ec\ub2e4.\u000d\u000a2) \uc6b4\uc601\ubaa8\ub378\uc5d0 \ub530\ub978 \uc989\uc2dc\uc131 \ud655\ubcf4\uc758 \uc804\ub7b5\u000d\u000a\uc6d0\ud65c\ud558\uac8c \ubc30\ucc28\uac00 \uc774\ub8e8\uc5b4\uc838 \u2018\ucf5c\u2019(\uc8fc\ubb38\uc694\uccad)\uc774 \uc81c\ub54c \ube60\uc9c8 \uac83\uc774\ub77c\ub294 \uae30\ub300\uac00 \uc74c\uc2dd\uc810\uacfc \ubc30\ub2ec \ub300\ud589\u000d\u000a\uc0ac/\ubc30\ub2ec \ub178\ub3d9\uc790 \uc0ac\uc774 \uc704\ud0c1\uacc4\uc57d\uc758 \uadfc\uac04\uc774\ub2e4. \uc774 \uae30\ub300\uac00 \ucda9\uc871\ub418\uae30 \uc704\ud574\uc11c\ub294 \ub2f9\uc7a5 \ubc30\ub2ec\uc744 \uc2dc\u000d\u000a\uc791\ud560 \uc218 \uc788\ub294 \ubc30\ub2ec \ub178\ub3d9\uc790\uac00 \uc2dc\uc2a4\ud15c\uc5d0 \ub85c\uadf8\uc778\ud574 \uc788\uace0, \ubc30\ub2ec \ub178\ub3d9\uc790\uac00 \ubc30\ub2ec \uc694\uccad\uc744 \uac70\uc808\ud558\u000d\u000a\uc9c0 \uc54a\uc544 \ubc30\ucc28\uac00 \uc989\uc2dc \uc774\ub8e8\uc5b4\uc9c8 \uc218 \uc788\uc5b4\uc57c \ud55c\ub2e4. \uc989\uc2dc\uc131\uc744 \ub2ec\uc131\ud560 \uc218 \uc788\ub294 \uc870\uac74\uc73c\ub85c\uc11c \ub178\ub3d9\u000d\u000a\ub825\uc758 \uac00\uc6a9\uc131Availability \uc790\uccb4\uac00 \uc804\uccb4 \uc11c\ube44\uc2a4\ub97c \uad6c\uc131\ud558\ub294 \uc911\uc694\ud55c \uc0c1\ud488\uc774 \ub41c\ub2e4.(Mazmanian & Erickson, \u000d\u000a2014) \uc5b8\uc81c\ub4e0 \ubc30\ub2ec\ud560 \uc218 \uc788\ub3c4\ub85d \ub178\ub3d9\ub825\uc744 \ub300\uae30\uc2dc\ud0a4\ub294 \ubc29\uc2dd\uc740 \uc6b4\uc601\ubaa8\ub378\uc5d0 \ub530\ub77c \ub2ec\ub77c\uc9c4\ub2e4.<\ud45c 2>\u000d\u000a10\u3000\uc218\uc694\uc790 - \ud310\ub9e4\uc790\ub85c \uc774\ub8e8\uc5b4\uc9c4 \uc2dc\uc7a5\uacfc \ub2ec\ub9ac \uc218\uc694\uc790 - \ud310\ub9e4\uc790 \uc0ac\uc774\uc5d0 \ud50c\ub7ab\ud3fc \uae30\uc5c5\uc774\ub77c\ub294 \uc911\uac04\ub9e4\uac1c\uc790\uac00 \ub080 \uc2dc\uc7a5\uc744 \u000d\u000a3 \uba74 \uc2dc\uc7a5\uc774\ub77c\uace0 \ud55c\ub2e4 . \ud55c\uad6d\uc758 \uacbd\uc6b0 \ud50c\ub7ab\ud3fc \uae30\uc5c5 \uc678\uc5d0 \ubc30\ub2ec \ub300\ud589\uc0ac\uac00 \ucd94\uac00\ub41c\ub2e4 .\u000d\u000a\uad6c\ubd84 \uc8fc\ubb38-\ubc30\ub2ec \ud50c\ub7ab\ud3fc \ud1b5\ud569 \uc8fc\ubb38-\ubc30\ub2ec \ud50c\ub7ab\ud3fc \ubd84\ub9ac\u000d\u000a\uc544\uc6c3\uc18c\uc2f1 \ubc30\ubbfc\ub77c\uc774\ub354\uc2a4, \u000d\u000a\uc694\uae30\uc694 \uc775\uc2a4\ud504\ub808\uc2a4\u000d\u000a\uc9c0\uc5ed\ubcc4 (\uc790\uc601\uc5c5): \ubc30\ub2ec \ub300\ud589\uc0ac\u000d\u000a\uc804\uad6d \ud504\ub79c\ucc28\uc774\uc988: \ubd80\ub989, \uc0dd\uac01\ub300\ub85c, \ubc14\ub85c\uace0 \u000d\u000a\ub4f1 \ubc30\ub2ec\uc804\ubb38 \ud50c\ub7ab\ud3fc \uae30\uc5c5\u000d\u000a\ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1 \ubc30\ubbfc\ucee4\ub125\ud2b8, \ucfe0\ud321\uc774\uce20 \ubd80\ub989 \ud504\ub80c\uc988\u000d\u000a<\ud45c 1> \ud55c\uad6d \uc74c\uc2dd \ubc30\ub2ec \ub300\ud589\uc5c5\uc758 \uc6b4\uc601\ubaa8\ub378","110 111\u000d\u000a\uc8fc\ubb38 \ud50c\ub7ab\ud3fc\uacfc \ubd84\ub9ac\ub3fc \uc788\ub294 \uc544\uc6c3\uc18c\uc2f1 \ubc30\ub2ec \uc804\ubb38 \ud50c\ub7ab\ud3fc\uc758 \uacbd\uc6b0, \ub3d9\ub124 \uc74c\uc2dd\uc810\uacfc \uc9c1\uc811 \uc704\u000d\u000a\ud0c1\uacc4\uc57d\uc744 \ub9fa\uc740 \ub300\ud589 \uc9c0\uc0ac\uac00 \ub3d9\ub124 \ubc30\ub2ec \ubb3c\ub7c9\uc744 \uc18c\ud654\ud55c\ub2e4.11) \uc608\ub97c \ub4e4\uc5b4, A \ubc30\ub2ec \uae30\uc5c5\uc758 \uc2e0\ucd0c \u000d\u000a\uc9c0\uc5ed \ub300\ud589 \uc9c0\uc810\uc740 \uc9c1\uc811 \uc601\uc5c5\uc744 \ud574\uc11c \uc778\uadfc \uc2dd\ub2f9\ub4e4\uacfc \ubc30\ub2ec \uc704\ud0c1\uacc4\uc57d\uc744 \uccb4\uacb0\ud558\uace0, A \ub300\ud589 \uc2e0\u000d\u000a\ucd0c \uc9c0\uc810\uc5d0 \uc18d\ud55c \ubc30\ub2ec \ub178\ub3d9\uc790\ub4e4\uc774 \ud574\ub2f9 \ubb3c\ub7c9\uc744 \ucc45\uc784\uc9c4\ub2e4. \uc774\ub7ec\ud55c \uc77c\ubc18 \ubc30\ub2ec \ub300\ud589\uc758 \uacbd\uc6b0 \ud06c\u000d\u000a\ub77c\uc6b0\ub4dc\uc18c\uc2f1\uacfc \uac19\uc774 \uc778\ub825\uc744 \ub298\ub9b4 \uc218\uac00 \uc5c6\ub2e4. \uc8fc\ubb38\uc744 \ubc1b\ub294 \uac00\ub9f9\uc810\uc758 \uc218\uc640 \uc9c0\uc5ed\uc774 \ud55c\uc815\ub3fc \uc788\u000d\u000a\uae30 \ub54c\ubb38\uc5d0, \ud55c \uc0ac\ubb34\uc2e4\uc5d0 \uc18c\uc18d\ub41c \ub178\ub3d9\uc790\uc758 \uc218\uac00 \ub9ce\uc544\uc9c0\uba74 1\uc778\ub2f9 \uc218\uc785\uc774 \ub0ae\uc544\uc9c0\uace0, \uc218\uc785\uc774 \ub0ae\u000d\u000a\uc544\uc9c0\uba74 \ubc30\ub2ec \ub178\ub3d9\uc790\ub4e4\uc774 \ub2e4\ub978 \ub300\ud589\uc0ac\ub85c \uc774\uc9c1\ud560 \uac00\ub2a5\uc131\uc774 \ucee4\uc9c0\uae30 \ub54c\ubb38\uc774\ub2e4. \ub530\ub77c\uc11c \uc9c0\uc5ed\uc758 \u000d\u000a\uc77c\ubc18 \ubc30\ub2ec \ub300\ud589\uc0ac\ub294 \uac00\ub9f9\uc810 \uc218\ub294 \ub298\ub9ac\uace0 \uc778\ub825 \uaddc\ubaa8\ub294 \uc801\uc815 \uc218\uc900\uc744 \uc720\uc9c0\ud558\uba74\uc11c \ubc30\ucc28\uc18d\ub3c4\ub97c \u000d\u000a\uc720\uc9c0\ud574\uc57c \ud55c\ub2e4. \uc774\ub7ec\ud55c \uc77c\ub828\uc758 \uad00\ub9ac\ub97c \u2018\uad00\uc81c\u2019\ub77c\uace0 \ubd88\ub9ac\ub294 \uad00\ub9ac\uc790\uac00 \ub2f4\ub2f9\ud55c\ub2e4. \u000d\u000a\ud1b5\ud569-\ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1 \ud50c\ub7ab\ud3fc\uc758 \uc804\ub7b5\uc740 \uc815\ud574\uc9c4 \uc778\ub825\uc758 \uc0dd\uc0b0\uc131\uc744 \ub04c\uc5b4 \uc62c\ub9ac\ub294 \uac83\uc774 \uc544\ub2c8\ub77c, \u000d\u000a\ub2f9\uc7a5 \ubc30\ub2ec\uc744 \uc218\ud589\ud560 \uc218 \uc788\ub294 \uc778\ub825\uc744 \ucd5c\ub300\ud55c \ub9ce\uc774 \ub300\uae30\uc2dc\ud0a4\ub294 \uac83\uc774\ub2e4. \ud1b5\ud569\ud615 \ud50c\ub7ab\ud3fc\uc5d0\uc11c \u000d\u000a\ub3c4\uc785\ud558\uace0 \uc788\ub294 \uc54c\uace0\ub9ac\uc998 \uc790\ub3d9 \ubc30\ucc28 \ubc29\uc2dd\uc5d0\uc11c\ub294 \uc54c\uace0\ub9ac\uc998\uc774 \uc8fc\ubb38\uc744 \uc218\ud589\ud558\uae30\uc5d0 \uac00\uc7a5 \uc801\ud569\u000d\u000a\ud55c \ubc30\ub2ec \ub178\ub3d9\uc790\ub97c \uc120\ud0dd\ud574\uc11c \uac1c\ubcc4\uc801\uc73c\ub85c \uc8fc\ubb38\uc744 \ubc30\ub2ec \ub178\ub3d9\uc790\uc758 \uc571\uc73c\ub85c \ubc00\uc5b4 \ub123\uc5b4\uc900\ub2e4. \ub178\ub3d9\u000d\u000a\uc790\uac00 \ubc30\ucc28\ub97c \uac70\ubd80\ud558\ub294 \uacbd\uc6b0, \ud574\ub2f9 \uc8fc\ubb38\uc740 \uc790\ub3d9\uc73c\ub85c \uc778\uadfc\uc758 \ub2e4\ub978 \ub178\ub3d9\uc790\uc5d0\uac8c \uc804\ub2ec\ub41c\ub2e4. \uc5f0\u000d\u000a\uacb0\ub420 \ub54c\uae4c\uc9c0 \uc8fc\ubb38\uc744 \ub3cc\ub9ac\ub294 \uac83\uc774\ub2e4. \uc774\ub7ec\ud55c \uc790\ub3d9 \ubc30\ucc28 \uc2dc\uc2a4\ud15c\uc5d0\uc11c \ubc30\ucc28\uac00 \uc9c0\uc5f0\ub418\ub294 \uc0c1\ud669\u000d\u000a\uc744 \ub9c9\uae30 \uc704\ud574\uc11c\ub294 \ubc30\ucc28\uc640 \uad00\ub828\ud55c (\ubd88)\uc774\uc775\uc744 \ud06c\uac8c \ud558\uac70\ub098, \uc811\uc18d\ud574 \uc788\ub294 \ubc30\ub2ec \ub178\ub3d9\uc790\uc758 \uc22b\uc790 \u000d\u000a\uc790\uccb4\uac00 \ub9ce\uc544\uc838\uc57c \ud55c\ub2e4. \ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1\uc740 \uc544\uc6c3\uc18c\uc2f1\uacfc \ub2ec\ub9ac \uc911\uac04 \uad00\ub9ac\uc790\ub098 \uc0ac\ubb34\uc2e4\uc774 \uc5c6\uc5b4\uc11c \u000d\u000a\uac1c\ubcc4 \ub178\ub3d9\uc790\uc758 \uc0dd\uc0b0\uc131\uc744 \uad00\ub9ac\ud558\ub294 \uac83\uc774 \ubd88\uac00\ub2a5\ud558\uc9c0\ub9cc, \uc778\ub825\uc744 \ub298\ub9ac\ub294\ub370 \uc81c\uc57d\uc694\uc18c\uac00 \uc5c6\ub2e4.\u000d\u000a11\u3000\uc77c\ubc18 \ubc30\ub2ec \ub300\ud589\uc5d0\uc11c\ub3c4 7~8km \uc5d0 \ub2ec\ud558\ub294 \uc7a5\uac70\ub9ac \ubc30\ub2ec\uc774 \uc788\uc73c\uba70 \uc11c\uc6b8 \uc678\uacfd\uc758 \uacbd\uc6b0 \uc778\uc811\ud55c \uacbd\uae30\ub3c4 \uc9c0\uc5ed\uc774 \ubc30\ub2ec \u000d\u000a\uad8c\uc5ed\uc5d0 \ud3ec\ud568\ub418\uae30\ub3c4 \ud55c\ub2e4 . \ud558\uc9c0\ub9cc \ub300\ubd80\ubd84\uc758 \ubc30\ub2ec \ubb3c\ub7c9\uc740 \ubc18\uacbd 2~3km \uc9c0\uc5ed\uc5d0 \ubab0\ub824\uc788\ub2e4 .\u000d\u000a\uad6c\ubd84 \uc8fc\ubb38-\ubc30\ub2ec \ud50c\ub7ab\ud3fc \ud1b5\ud569 \uc8fc\ubb38-\ubc30\ub2ec \ud50c\ub7ab\ud3fc \ubd84\ub9ac\u000d\u000a\uc544\uc6c3\uc18c\uc2f1 \u000d\u000a\uc790\ub3d9\ud654(\uc54c\uace0\ub9ac\uc998/\ub2e8\uac74\ubc30\ucc28)\u000d\u000a\ubd84\ub9ac-\uc544\uc6c3\uc18c\uc2f1,\u000d\u000a\ud1b5\ud569-\ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1\uc758 \ud2b9\uc9d5\uc774 \ud63c\uc7ac\u000d\u000a\uc0dd\uc0b0\uc131 \uadf9\ub300\ud654\u000d\u000a(\uad00\ub9ac\uc790 \uac15\uc81c\ubc30\ucc28, \ucd9c\ud1f4\uadfc \uad00\ub9ac)\u000d\u000a\uc77c\ubc18\ubc30\ucc28(\ubcf5\uc218\ubc30\ucc28)\u000d\u000a\ub2e8\uac70\ub9ac \ubc30\ub2ec \uc704\uc8fc\u000d\u000a\ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1\u000d\u000a\uaddc\ubaa8\uc758 \uacbd\uc81c (\uc778\ub825\uc720\uc785)\u000d\u000a\uc790\ub3d9\ud654(\uc54c\uace0\ub9ac\uc998/\ub2e8\uac74\ubc30\ub2ec)\u000d\u000a\uc2e4\uc2dc\uac04 \ud504\ub85c\ubaa8\uc158 \u000d\u000a\ubc30\ub2ec\uac70\ub9ac \uc81c\ud55c \uc5c6\uc74c\u000d\u000a<\ud45c 2> \uc6b4\uc601\ubaa8\ub378\uc5d0 \ub530\ub978 \uc989\uc2dc\uc131 \ud655\ubcf4\uc758 \ubc29\uc2dd: \uaddc\ubaa8\uc758 \uacbd\uc81c vs. \uc0dd\uc0b0\uc131 \uadf9\ub300\ud654\u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \uadf8\ub9bc\uc790 \ub178\ub3d9"," & 112\u000d\u000a\ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1 \ubc29\uc2dd\uc73c\ub85c \uc778\ub825\uc744 \ud655\ubcf4\ud558\ub294 \uae30\uc5c5\ub4e4\uc774 \uac00\uc7a5 \uc801\uadf9\uc801\uc73c\ub85c \uc0ac\uc6a9\ud558\ub294 \ubc29\uc2dd\uc740 \uae08\u000d\u000a\uc804\uc801 \uc778\uc13c\ud2f0\ube0c\ub97c \uc81c\uacf5\ud558\ub294 \uac83\uc774\ub2e4. \ud50c\ub7ab\ud3fc \uae30\uc5c5\uc740 \ud504\ub85c\ubaa8\uc158\uc744 \ud1b5\ud574 \ub300\uae30 \uc778\ub825\uc744 \uc801\uadf9\uc801\uc73c\u000d\u000a\ub85c \uad00\ub9ac\ud558\uace0 \ub9cc\ub4e4\uc5b4\ub0b8\ub2e4. \ud50c\ub7ab\ud3fc\uc758 \ubc30\ub2ec\ube44 \uc815\ucc45\uc740 \ub0ae\uc740 \uae30\ubcf8\uc694\uae08, \uc2e4\uc2dc\uac04 \ubcc0\ud654\uc694\uae08, \ube44\uc815\u000d\u000a\uae30\uc801 \ud504\ub85c\ubaa8\uc158\uc774\ub77c\uace0 \ud560 \uc218 \uc788\ub2e4. \uc774\ub7ec\ud55c \uc694\uae08\uc81c\uc758 \ubaa9\uc801\uc740 \uc218\uc694\uac00 \ubab0\ub9ac\ub294 \uc2dc\uac04\uc5d0 \ucda9\ubd84\ud55c \u000d\u000a\ub178\ub3d9\ub825\uc744 \ub85c\uadf8\uc778\uc2dc\ud0a4\ub294 \uac83\uc774\ub2e4. \ud53c\ud06c\ud0c0\uc784 \uc774\ubca4\ud2b8\ub294 \ud53c\ud06c\ud0c0\uc784 \ub3d9\uc548 \uc628\ub77c\uc778 \uc0c1\ud0dc\ub97c \uc720\uc9c0\ud560 \u000d\u000a\uac83\uc744 \ud504\ub85c\ubaa8\uc158 \uc870\uac74\uc73c\ub85c \uc694\uad6c\ud55c\ub2e4. \ud2b9\ud788 \ucd5c\uadfc\uc5d0 \ub85c\uadf8\uc778\uc744 \ud558\uc9c0 \uc54a\uc740 \uc774\ub4e4\uc5d0\uac8c \ub354 \ub192\uc740 \uae08\u000d\u000a\uc561\uc758 \ud504\ub85c\ubaa8\uc158\uc744 \uc81c\uc2dc\ud558\uae30\ub3c4 \ud558\ub294\ub370, \uc774\ub7ec\ud55c \ud504\ub85c\ubaa8\uc158\uc740 \uad6c\ub9e4 \ud655\ub960\uc774 \ub5a8\uc5b4\uc9c0\ub294 \uace0\uac1d\uc758 \uad6c\u000d\u000a\ub9e4\uc728\uc744 \ucd09\uc9c4\ud558\uae30 \uc704\ud55c \uc18c\ube44\uc790 \ub9c8\ucf00\ud305\uacfc \ub3d9\uc77c\ud55c \ubc29\uc2dd\uc73c\ub85c \ub85c\uadf8\uc778\ud560 \ud655\ub960 \ub0ae\uc740 \uc774\ub4e4\uc744 \uc6c0\uc9c1\u000d\u000a\uc774\ub294 \uac83\uc744 \ubaa9\uc801\uc73c\ub85c \ud55c\ub2e4. \ud504\ub85c\ubaa8\uc158\ube44\ub85c \u2018\ub3c8\uc744 \ud0dc\uc6cc\uc11c\u2019 \ubc30\ub2ec \ub178\ub3d9\uc790\ub97c \ud574\ub2f9 \uc9c0\uc5ed\uc5d0 \ucd18\ucd18\ud558\u000d\u000a\uac8c \ubc30\uce58\ud574 \ub178\ub3d9\ub825\uc744 \ud655\ubcf4\ud55c\ub2e4.\u000d\u000a4. \ubc30\ub2ec \ub178\ub3d9\uc790\uc758 \uc2dc\uac04 \uacbd\ud5d8\uacfc \uadf8\ub9bc\uc790 \ub178\ub3d9\uc758 \uc99d\uac00\u000d\u000a1) \uac01 \ud50c\ub7ab\ud3fc\uc758 \uc2dc\uac04\uad00\ub9ac\uc5d0 \ub300\ud55c \ub178\ub3d9\uc790\uc758 \ub300\uc751\u000d\u000a\ud68c\uc0ac\uc758 \ubc30\ucc28\ubc29\uc2dd, \uc778\ub825 \uc6b4\uc601 \uc804\ub7b5\uc740 \ub178\ub3d9\uc790\uac00 \ub178\ub3d9\uc2dc\uac04\uc744 \uc870\uc9c1\ud558\ub294 \ubc29\uc2dd\uc5d0 \uc9c1\uc811\uc801\uc778 \uc601\ud5a5\u000d\u000a\uc744 \ubbf8\uce5c\ub2e4. \ube44\ud1b5\ud569-\uc544\uc6c3\uc18c\uc2f1 \uc720\ud615\uc758 \uacbd\uc6b0 \uac74\ub2f9 \ubc30\ub2ec\ube44\uac00 \ub0ae\uace0 \ubc30\ub2ec\ubb3c\ub7c9\uc774 \ub9ce\uae30 \ub54c\ubb38\uc5d0, \ud55c \u000d\u000a\ubc88\uc5d0 \uc5ec\ub7ec \uac1c\uc529 \ubc30\ub2ec\uc744 \ud558\ub294 \ubc29\uc2dd\uc73c\ub85c \uc804\uccb4 \ud750\ub984\uc758 \uc18d\ub3c4\uc640 \uc218\uc785\uc744 \ub192\uc778\ub2e4. \ubcf5\uc218 \ubc30\ucc28\uac00 \uc774\ub8e8\u000d\u000a\uc5b4\uc9c0\ub294 \ube44\ud1b5\ud569-\uc544\uc6c3\uc18c\uc2f1 \uc720\ud615 \ub178\ub3d9\uc790\uac00 \ubc30\ub2ec \uacfc\uc815\uc744 \uc870\uc9c1\ud558\ub294 \ud575\uc2ec\uc740 \ubc30\ucc28 \ub3d9\uc120\uc744 \uc9dc\ub294 \uac83\u000d\u000a\uc778\ub370, \uc6d0\ud558\ub294 \ub3d9\uc120\uc744 \uc9dc\ub294 \uacfc\uc815\uc5d0\uc11c \u2018\uc804\ud22c\ucf5c\u201912)\uc774\ub77c\ub294 \ubc30\ucc28 \uacbd\uc7c1\uc774 \ub098\ud0c0\ub09c\ub2e4. \uc77c\ubc18 \ubc30\ub2ec \ub300\ud589\u000d\u000a\uc5d0\uc11c \uc0ac\uc6a9\ud558\ub294 \ud504\ub85c\uadf8\ub7a8\uc740 \ud574\ub2f9 \ub300\ud589 \uc0ac\ubb34\uc2e4\uacfc \uc704\ud0c1\uacc4\uc57d\uc744 \ub9fa\uc740 \ubaa8\ub4e0 \uc74c\uc2dd\uc810\uc5d0 \ub4e4\uc5b4\uc628 \uc8fc\ubb38 \u000d\u000a\uc0c1\ud669\uc744, \ub300\ud589 \uc0ac\ubb34\uc2e4\uc5d0 \ub4f1\ub85d\ub41c \ubaa8\ub4e0 \ub178\ub3d9\uc790\uc5d0\uac8c \ub3d9\uc2dc\uc5d0 \ubcf4\uc5ec\uc900\ub2e4. \uc2dd\uc0ac \uc2dc\uac04\uc5d0\ub294 \ubc31 \uac74\uc774 \ud6cc\u000d\u000a\uca4d \ub118\uac8c \uc8fc\ubb38\uc774 \uc313\uc778\ub2e4. \ubc30\ub2ec \ub178\ub3d9\uc790\ub294 \uc774 \ub9ac\uc2a4\ud2b8\uc5d0\uc11c \uc790\uc2e0\uc774 \uc6d0\ud558\ub294 \ubc29\ud5a5\uc758 \uc8fc\ubb38\uc744 \uba87 \uac1c \uc120\u000d\u000a\ud0dd\ud558\uc5ec \uc5ee\ub294\ub370, \uc6d0\ud558\ub294 \uc8fc\ubb38\uc744 \uc7a1\uae30 \uc704\ud574\uc11c\ub294 \ube60\ub974\uac8c \uc120\ud0dd\uc744 \uc644\ub8cc\ud574\uc57c \ud55c\ub2e4. \uc0c1\ud669\uc5d0 \ub9de\uac8c \ub3d9\u000d\u000a\uc120\uc744 \uc9dc\ub294 \ubc29\uc2dd\uc73c\ub85c \ud53d\uc5c5\uc774\ub098 \uc0c1\uc810 \ubc00\uc9d1 \uc9c0\uc5ed\uc73c\ub85c \ud68c\ucc28\ud558\ub294 \uac83\uacfc \uac19\uc740 \ube44\uc6a9\uc774 \ucc45\uc815\ub418\uc9c0 \uc54a\u000d\u000a\uc740 \uc774\ub3d9 \uac70\ub9ac\ub97c \uc904\uc784\uc73c\ub85c\uc368 \ub178\ub3d9\uc790\ub4e4\uc740 \uac1c\ubcc4\uc801\uc73c\ub85c \ubd80\ub2f4\ud574\uc57c \ud560 \ube44\uc6a9\uc744 \ucd5c\uc18c\ud654 \ud560 \uc218 \uc788\ub2e4. \u000d\u000a12\u3000\ubaa8\ub450\uac00 \uc790\uc2e0\uc774 \uc6d0\ud558\ub294 \ucf5c\uc744 \uc120\ud0dd\ud558\ub824\uace0 \ub178\ub825\ud558\ub294 \uc640\uc911\uc5d0 \uc8fc\ubb38 \ucf5c\uc774 \uc21c\uc2dd\uac04\uc5d0 \uc0ac\ub77c\uc9c0\uae30 \ub54c\ubb38\uc5d0 \uc6d0\ud558\ub294 \ucf5c\uc744 \uc120\ud0dd\u000d\u000a\ud558\ub294 \uacfc\uc815\uc774 \ub9e4\uc6b0 \uacbd\uc7c1\uc801\uc778 \uc804\ud22c \uac19\ub2e4\ub294 \ub73b\uc774\ub2e4 .","112 113\u000d\u000a\uc774\uc640 \ubc18\ub300\ub85c \ud1b5\ud569\ud615 \uc720\ud615 \ud50c\ub7ab\ud3fc\uc5d0\uc11c \uc77c\ud558\ub294 \ub178\ub3d9\uc790\ub4e4\uc740 \uc804\ub7b5\uc801\uc73c\ub85c \ubc30\ub2ec\uc744 \uac70\uc808\ud558\ub294 \u000d\u000a\ub370 \uc9d1\uc911\ud55c\ub2e4. \ud2b9\ud788 \ub2e8\uac74 \ubc30\ub2ec\uc744 \ud558\ub294 \uacbd\uc6b0, \ud558\ub098\uc758 \ubc30\ub2ec\uc744 \uc120\ud0dd\ud568\uc73c\ub85c\uc368 \ub193\uce58\uac8c \ub418\ub294 \ub2e4\ub978 \u000d\u000a\ubc30\ub2ec\uc758 \uae30\ud68c\ube44\uc6a9\uc774 \ubc30\ucc28\ub97c \uc218\ub77d\ud558\ub294 \ub370 \uc788\uc5b4\uc11c \uc911\uc694\ud558\uac8c \uace0\ub824\ud574\uc57c \ud560 \uc694\uc18c\uc774\ub2e4. \uac01 \ud50c\ub7ab\ud3fc\u000d\u000a\uc740 \uc8fc\ubb38\uc774 \ubab0\ub9ac\ub294 \ud53c\ud06c \ud0c0\uc784\uc5d0 \ub178\ub3d9\uc790\ub4e4\uc744 \ubaa8\uc9d1\ud558\uae30 \uc704\ud574 \ubc30\ub2ec\ub8cc\ub97c \ud560\uc99d\ud55c\ub2e4. \ub530\ub77c\uc11c \ub178\ub3d9\u000d\u000a\uc790\ub4e4 \uc785\uc7a5\uc5d0\uc11c\ub294 \ud53c\ud06c \ud0c0\uc784\uc5d0 \ubc30\ub2ec\uc744 \ud6a8\uc728\uc801\uc73c\ub85c \uc5ec\ub7ec \uac1c \ud558\ub294 \uac83\uc774 \uc218\uc775\uc744 \ub192\uc77c \uc218 \uc788\ub294 \u000d\u000a\uae38\uc774\ub2e4. \ud558\uc9c0\ub9cc \ubc30\ucc28 \uac70\uc808\uc774\ub77c\ub294 \uc804\ub7b5\uc5d0\ub294 \ube44\uc6a9\uc774 \ub530\ub978\ub2e4. \ub354 \ub192\uc740 \uc218\uc775\uc744 \uc704\ud574 \ucf5c\uc744 \uace0\ub974\u000d\u000a\ub294 \uc2dc\uac04\uc774 \uae38\uc5b4\uc9c8\uc218\ub85d \ub3c8\uc744 \ubc8c\uc9c0 \ubabb\ud558\ub294 \uc2dc\uac04\ub3c4 \uae38\uc5b4\uc9c0\uba70, \uc218\ub77d\ub960 \ud558\ub77d\uc5d0 \ub530\ub978 \ud398\ub110\ud2f0\ub3c4 \uac10\u000d\u000a\uc218\ud574\uc57c \ud55c\ub2e4. \ud50c\ub7ab\ud3fc \ubc30\ub2ec \ud68c\uc0ac\ub294 \ubc30\ucc28 \uac70\uc808\uc744 \ubc29\uc9c0\ud558\uae30 \uc704\ud558\uc5ec \uc800\ub9c8\ub2e4 \ubc30\ucc28 \uc218\ub77d\ub960\uc774\ub77c\ub294 \u000d\u000a\ud56d\ubaa9\uc744 \ud1b5\ud574 \ub178\ub3d9\uc790\ub4e4\uc744 \uad00\ub9ac\ud558\uba70, \uad6c\uccb4\uc801\uc778 \ud1b5\uc81c \ubc29\uc2dd\uc5d0 \ub530\ub77c \ub178\ub3d9\uc790\ub4e4\uc758 \ubc30\ucc28 \uac70\uc808 \uc804\ub7b5\u000d\u000a\uc5d0 \ucc28\uc774\uac00 \ub9cc\ub4e4\uc5b4\uc9c4\ub2e4. \ud2b9\ud788 \uc218\ub77d\ub960\uc744 95% \uc218\uc900\uc73c\ub85c \uc694\uad6c\ud558\uba74\uc11c \uc774\uc5d0 \ub530\ub978 \ubcf4\uc0c1\uccb4\uacc4\ub97c \uc138\u000d\u000a\uc6b4 \ud50c\ub7ab\ud3fc\uc758 \uacbd\uc6b0, \ub178\ub3d9\uc790\ub4e4\uc740 \uac70\uc808\uc774 \uc218\ub77d\ub960\uc744 \uc720\uc9c0\ud558\uae30 \uc704\ud574 \uac70\uc808\uc744 \uac70\uc758 \ud558\uc9c0 \uc54a\ub294\ub2e4. \u000d\u000a2) \ubc30\ub2ec\ub178\ub3d9\uc790\ub4e4\uc758 \uc2dc\uac04\uacbd\ud5d8\uacfc \uadf8\ub9bc\uc790 \ub178\ub3d9\uc758 \uc99d\uac00\u000d\u000a\ubc30\ub2ec \ub178\ub3d9\uc744 \ud558\ub294 \ub3d9\uc548 \ub178\ub3d9\uc790\uac00 \uacaa\ub294 \uc2dc\uac04 \uacbd\ud5d8\uc5d0\ub294 \uc2dc\uac04 \ubd80\uc871\uc774\ub098 \uc2dc\uac04 \uc555\ubc15 \uc678\uc5d0\ub3c4 \ub2e4\u000d\u000a\uc591\ud55c \uacb0\uc774 \uc874\uc7ac\ud55c\ub2e4.<\ud45c 3> \uc6b4\uc601\uc720\ud615\uc5d0 \ub530\ub77c \uc0c1\uc774\ud55c \uacbd\ud5d8\uc744 \ud55c\ub2e4\ub294 \uc810\uc740 \ub178\ub3d9\uc790\ub4e4\uc758 \uc2dc\uac04 \uacbd\u000d\u000a\ud5d8\uc774 \uc54c\uace0\ub9ac\uc998\uacfc \uac19\uc740 \uc2e0\uae30\uc220\uc5d0 \uc758\ud574\uc11c\ub9cc \uacb0\uc815\ub418\uc9c0 \uc54a\ub294\ub2e4\ub294 \uc810\uc744 \uba85\ud655\ud788 \ub4dc\ub7ec\ub0b8\ub2e4. \uc778\ub825\u000d\u000a\uad6c\uc870\ub97c \uc5b4\ub5bb\uac8c \uc6b4\uc601\ud558\ub294\uac00, \uc0c1\ubc8c\uc758 \uae30\uc900\uc744 \uc5b4\ub5bb\uac8c \uc124\uc815\ud558\ub294\uac00, \uc815\ubcf4\uacf5\uac1c\uc758 \ubc94\uc704\ub97c \uc5bc\ub9c8\ub098 \u000d\u000a\ub113\uac8c \uac00\uc838\uac00\ub294\uac00\uc640 \uac19\uc740 \ud68c\uc0ac\uc758 \uc815\ucc45\uc5d0 \ub530\ub77c \uac1c\ubcc4 \ub178\ub3d9\uc790\uac00 \uc790\uc2e0\uc758 \uc2dc\uac04\uc744 \uc870\uc9c1\ud558\ub294 \uc804\ub7b5, \u000d\u000a\uc774\ub85c \uc778\ud55c \uc2dc\uac04 \uacbd\ud5d8\uacfc \uc2ec\ub9ac\uc801 \uac08\ub4f1, \ub178\ub3d9\uc790\uac00 \ubd80\ub2f4\ud574\uc57c \ud558\ub294 \uc2dc\uac04 \ube44\uc6a9\uc758 \uc591\uc0c1\uc774 \ub2ec\ub77c\uc9c4\ub2e4.\u000d\u000a\ud2b9\ud788 \ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1 \uc815\ucc45\uc744 \ub3c4\uc785\ud55c \ud68c\uc0ac\uc5d0\uc11c \ube44\uac00\uc2dc\ud654 \ub41c \ub178\ub3d9 \uc2dc\uac04\uc774 \ub298\uc5b4\ub098\uace0 \uc774\ub85c \uc778\u000d\u000a\ud574 \uac1c\ubcc4 \ub178\ub3d9\uc790\uc758 \ube44\uc6a9 \ubd80\ub2f4\uc774 \uc99d\uac00\ud558\ub294 \uac83\uc744 \uc0b4\ud3b4\ubcfc \ud544\uc694\uac00 \uc788\ub2e4. \uc628\ub514\ub9e8\ub4dc \uc11c\ube44\uc2a4\uac00 \ucd94\u000d\u000a\uad6c\ud558\ub294 \uac83\uc740 \uace0\uac1d\uc758 \uc694\uccad\uc5d0 \uc989\uac01\uc801\uc73c\ub85c \ubc18\uc751\ud558\ub294 \uc2dc\uc2a4\ud15c\uc774\ub2e4. \ud558\uc9c0\ub9cc \uc778\uac04\uc744 \ub3d9\uc6d0\ud574 \uc5b8\uc81c\u000d\u000a\ub098 \uc811\uc18d \uac00\ub2a5\ud558\uace0 \uc989\uac01\uc801\uc778 \uc2dc\uc2a4\ud15c\uc744 \ub9cc\ub4e4\uae30 \uc704\ud574\uc11c\ub294 \ubcc4\ub3c4\uc758 \ub178\ub825\uc774 \ud544\uc694\ud558\ub2e4. \uae30\uc5c5\ub4e4\uc740\u000d\u000a\uad6c\ubd84 \uc8fc\ubb38-\ubc30\ub2ec \ud50c\ub7ab\ud3fc \ud1b5\ud569 \uc8fc\ubb38-\ubc30\ub2ec \ud50c\ub7ab\ud3fc \ubd84\ub9ac\u000d\u000a\uc544\uc6c3\uc18c\uc2f1 \ub300\uae30\uc2dc\uac04\uc758 \uc99d\uac00 (\ubc30\ubbfc\ub77c\uc774\ub354\uc2a4)\u000d\u000a/ \uc790\uc728\uc131 \uac15\ud0c8 (\uc694\uae30\uc694\uc775\uc2a4\ud504\ub808\uc2a4) \uc2dc\uac04\uc555\ubc15\u000d\u000a\ud06c\ub77c\uc6b0\ub4dc\uc18c\uc2f1 \ubd88\uc608\uce21\uc131 \uc99d\uac00\u000d\u000a<\ud45c 3> \uc6b4\uc601\ubaa8\ub378\uc5d0 \ub530\ub978 \uc2dc\uac04 \uacbd\ud5d8\u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \uadf8\ub9bc\uc790 \ub178\ub3d9"," & 114\u000d\u000a\ub178\ub3d9\uc790\uac00 \uc2dc\uc2a4\ud15c\uc5d0 \ub85c\uadf8\uc778\ud574 \ub300\uae30\ud558\ub294 \uc2dc\uac04\uc744 \ub298\ub9ac\uace0, \uc218\uc694\uac00 \ubab0\ub9ac\ub294 \uc2dc\uac04\u30fb\uc7a5\uc18c\ub85c \ub178\ub3d9\u000d\u000a\uc790\ub97c \uc774\ub3d9\uc2dc\ucf1c \uc628\ub514\ub9e8\ub4dc \ubc30\ub2ec \uc2dc\uc2a4\ud15c\uc744 \ub9cc\ub4e4\uc5b4 \ub0b8\ub2e4. \ubb38\uc81c\ub294 \ub300\uae30\ud558\ub294 \ub178\ub3d9\uc790\uac00 \ub9ce\uc544\uc9c8\uc218\u000d\u000a\ub85d \uc77c\uac10\uc744 \ubc30\uc815\ubc1b\uc744 \ud655\ub960\uc740 \ub0ae\uc544\uc9c0\uace0 \ub300\uae30\uc2dc\uac04\uc740 \uae38\uc5b4\uc9c4\ub2e4\ub294 \uac83\uc774\ub2e4. \ud50c\ub7ab\ud3fc \ub178\ub3d9\uc5d0\uc11c\ub294 \u000d\u000a\uace0\uac1d\uacfc \uc811\uc18d\ub41c \uc2dc\uac04\ub9cc \uc0dd\uc0b0\uc131\uc744 \uc778\uc815\ubc1b\uae30\uc5d0, \ub300\uae30\uc2dc\uac04, \uc900\ube44 \uc2dc\uac04\uc740 \ubcf4\uc218\uac00 \ucc45\uc815\ub418\uc9c0 \uc54a\ub294 \u000d\u000a\ube44\uc0dd\uc0b0\uc801\uc778 \uc2dc\uac04\uc774\ub2e4. \uacb0\uacfc\uc801\uc73c\ub85c \uae30\uc5c5 \uc804\uccb4\uc758 \uc0dd\uc0b0\uc131\uc744 \ub04c\uc5b4\uc62c\ub9ac\uae30 \uc704\ud55c \uc804\ub7b5\uc774 \uac1c\ubcc4 \ub178\u000d\u000a\ub3d9\uc790\uc758 \uc218\uc775\uc131\uc744 \ub5a8\uc5b4\ub728\ub9ac\uac8c \ub41c\ub2e4. \uae30\uc5c5\uc774 \uc11c\ube44\uc2a4\ud558\ub294 \uc989\uac01\uc801 \ubc18\uc751\uc131\uacfc \ube60\ub978 \uc18d\ub3c4\ub97c \uc81c\uacf5\u000d\u000a\ud558\uae30 \uc704\ud574\uc11c\ub294 \ubc18\ub4dc\uc2dc \ud544\uc694\ud55c \ub178\ub3d9\uc774\uc9c0\ub9cc, \uace0\uac1d\ub4e4\uc5d0\uac8c \ub4dc\ub7ec\ub098\uc9c0 \uc54a\uace0 \uc81c\ub300\ub85c \uac00\uce58\uac00 \ud3c9\uac00\u000d\u000a\ub418\uc9c0 \uc54a\ub294 \uadf8\ub9bc\uc790 \ub178\ub3d9\uc774 \ub298\uc5b4\ub098\ub294 \uac83\uc774\ub2e4.(\uc77c\ub9ac\uce58, 2017)\u000d\u000a\ub300\uae30\uc2dc\uac04\uc758 \uc99d\uac00\ub294 \ubb34\uae09\ub178\ub3d9\uc758 \uc99d\uac00\ub77c\ub294 \uae08\uc804\uc801\uc778 \ubb38\uc81c\ub97c \ub118\uc5b4\uc11c \uac10\uc815\uc801\uc778 \uac08\ub4f1\uc744 \ubd88\u000d\u000a\ub7ec\uc77c\uc73c\ud0a8\ub2e4. \ub9ce\uc740 \ub178\ub3d9\uc790\ub294 \ubc14\uc05c \uac83\ubcf4\ub2e4 \uae30\ub2e4\ub9ac\ub294 \uac8c \uc5b4\ub835\ub2e4\uba70, \u201c\uae38\uc5d0\uc11c \ubb50 \ud558\uace0 \uc788\ub294 \uac74\uac00 \u000d\u000a\uadf8\ub7f0 \uc790\uad34\uac10\u201d\uc5d0 \ub300\ud574 \uc5b8\uae09\ud588\ub2e4. \ubc30\ub2ec \ub178\ub3d9\uc740 \uac74\ub2f9 \uc218\uc218\ub8cc\ub97c \ubc1b\uae30 \ub54c\ubb38\uc5d0, \uc790\uc2e0\uc774 \uc77c\ud55c \ub9cc\u000d\u000a\ud07c \uac00\uc838\uac00\ub294 \ub178\ub3d9\uc774\ub77c\uace0 \ud3c9\uac00\ubc1b\uc73c\uba70 \ubc30\ub2ec \ub178\ub3d9\uc790\ub4e4\uc740 \uc911\uac04\uc5d0 \ub0ad\ube44\ub418\ub294 \uc2dc\uac04 \uc5c6\uc774 \ubd80\uc9c0\ub7f0\u000d\u000a\ud788 \ubab8\uc744 \uc6c0\uc9c1\uc774\ub824\uace0 \ub178\ub825\ud55c\ub2e4. \uc774\ub4e4\uc740 \uc2dc\uac04\uc744 \uc950\uc5b4\uc9dc\uace0 \ucabc\uac1c\uc5b4 \uc4f0\ub294 \uc2dc\uac04\uc758 \uac1c\ucc99\uc790(Erickson \u000d\u000a& Mazmanian, 2016)\uac00 \ub420 \uac83\uc744 \uc694\uad6c\ubc1b\ub294\ub2e4. \uac00\ub9cc\ud788 \uc549\uc544 \uae30\ub2e4\ub9ac\ub294 \uac83\uc740 \uc774\ub4e4\uc5d0\uac8c\ub294 \uae08\uc804\uc801\uc778 \uc190\u000d\u000a\uc2e4\uc774\uba70, \ub3d9\uc2dc\uc5d0 \uccb4\ud654\ub41c \ub178\ub3d9\uc758 \uc6d0\uce59\uc744 \uc704\ubc18\ud558\ub294 \uac83\uc774\ub2e4. \uc790\uc2e0\uc774 \uccb4\ud654\ud55c \uc2dc\uac04 \uc804\ub7b5\uc744 \uc0ac\uc6a9\u000d\u000a\ud560 \uc218 \uc5c6\uc5b4 \ud589\uc704\uc131\uc774 \uc81c\uc57d\ub2f9\ud558\ub294 \uc0c1\ud669\uc740 \ubb34\uae30\ub825\ud568, \uadfc\ub85c \uc758\uc695\uc758 \uc800\ud558\uc640 \uac19\uc740 \uc2ec\ub9ac\uc801 \uac08\ub4f1\u000d\u000a\uc744 \ucd08\ub798\ud55c\ub2e4.\u000d\u000a\uc2e4\uc2dc\uac04\uc73c\ub85c \ubc30\ub2ec\ub8cc\uac00 \ubc14\ub00c\ub294 \uc2e4\uc2dc\uac04 \ubcc0\ub3d9\uc694\uae08\uc81c\ub294 \ub178\ub3d9\uacfc\uc815\uc5d0\uc11c \ubd88\ud655\uc2e4\uc131\uc744 \ud06c\uac8c \ub192\uc774\u000d\u000a\uba70, \ub178\ub3d9\uc790\ub4e4\uc740 \uc2e4\uc2dc\uac04\uc73c\ub85c \ubc14\ub00c\ub294 \uc815\ubcf4\uc758 \uc18d\ub3c4\ub97c \uae09\ud788 \ucad3\uc544\uc57c \ud55c\ub2e4.13) \ud1b5\ud569\ud615 \ub300\ud615 \ud50c\ub7ab\ud3fc\u000d\u000a\ub4e4\uc740 \uc2e4\uc2dc\uac04\uc73c\ub85c \ubcc0\ub3d9\ud558\ub294 \uc694\uae08\uacfc \ud568\uaed8 \ud604\uc7ac \uc8fc\ubb38\uc774 \ub9ce\uc740 \uc9d1\uc911\uc9c0\uc5ed\uc5d0 \ub300\ud55c \uc815\ubcf4\ub97c \uc81c\uc2dc\ud55c\u000d\u000a\ub2e4. \ub178\ub3d9\uc790\ub4e4\uc740 \uc694\uae08\uc774 \ub192\uc73c\uba74\uc11c \uc8fc\ubb38\uc774 \uc9d1\uc911\ub418\uace0 \uc788\ub294 \uc9c0\uc5ed\uc73c\ub85c \uc774\ub3d9\uc744 \ud558\uae30\ub3c4 \ud55c\ub2e4. \uadf8\u000d\u000a\ub7ec\ub098 \uc774\ub807\uac8c \uc774\ub3d9\uc744 \ud558\uba74\uc11c\ub3c4 \ubd88\uc548\uac10\uc744 \uc9c0\uc6b8 \uc218 \uc5c6\ub294\ub370, \ub9c9\uc0c1 \ubaa9\uc801\uc9c0\uc5d0 \ub3c4\ucc29\ud558\uace0 \ub098\uba74 \uadf8\u000d\u000a\uc0ac\uc774\uc5d0 \ub2e8\uac00\uac00 \ub5a8\uc5b4\uc838 \uc788\uac70\ub098, \uc8fc\ubb38\uc774 \ub9ce\ub2e4\uace0 \ud558\ub294\ub370 \ucf5c\uc740 \ub4e4\uc5b4\uc624\uc9c0 \uc54a\ub294 \uc0c1\ud669\uc744 \uacaa\ub294 \uc77c\u000d\u000a\uc774 \uc801\uc9c0 \uc54a\uc740 \ud0d3\uc774\ub2e4. \ub610\ud55c \uc8fc\ubb38\uc0c1\ud669\uc5d0\uc11c \uc8fc\ubb38\ub7c9\uc774 \ub9e4\uc6b0 \ub9ce\ub2e4\uace0 \ud45c\uc2dc\ub418\ub294\ub370\ub3c4 \uac00\uaca9\uc740 \ub0ae\uc740 \u000d\u000a\uacbd\uc6b0\uac00 \uc788\ub2e4. \uc8fc\ubb38\ub7c9\uc740 \ubcf4\uc5ec\uc8fc\uc9c0\ub9cc, \ud574\ub2f9 \uc9c0\uc5ed\uc5d0 \ub178\ub3d9\uc790\ub4e4\uc774 \uc5bc\ub9c8\ub098 \uc77c\ud558\uace0 \uc788\ub294\uc9c0\ub97c \uc54c\ub824\u000d\u000a13\u3000\ucd5c\uadfc\uc5d0\ub294 \ubc30\ubbfc\ub77c\uc774\ub354\uc2a4\ub3c4 \uc2e4\uc2dc\uac04 \ubc30\ub2ec\ub2e8\uac00\ub97c \uacf5\uac1c\ud558\uace0 \uc788\uc73c\uba70 , \uc694\uae08 \ubcc0\ub3d9\uc758 \uac04\uaca9\uc774 \uc810\ucc28 \uc9e7\uc544\uc9c0\uace0 \uc788\uc73c\uba70 , \ucfe0\u000d\u000a\ud321\uc774\uce20 \ub178\ub3d9\uc790\ub3c4 \ubc30\ucc28 \uac70\uc808 \uc774\ud6c4 \ucf5c\uc774 \ubcf4\uc774\uc9c0 \uc54a\uc544 \ub300\uae30\ud558\ub294 \uc2dc\uac04\uc774 \uae38\uc5b4\uc9c0\uace0 \uc788\ub2e4\uace0 \ud638\uc18c\ud558\uace0 \uc788\ub2e4 . \ubc30\ub2ec \uc571 \uc6b4\uc601 \u000d\u000a\ubc29\uc2dd\uc774 \uc790\uc8fc \ubc14\ub00c\uae30 \ub54c\ubb38\uc5d0 , \ub17c\ubb38\uc744 \uc791\uc131\ud558\ub294 \ub3d9\uc548\uc5d0\ub3c4 \uacc4\uc18d \uc0c8\ub85c\uc6b4 \ub178\ub3d9 \uacbd\ud5d8\uc774 \ub098\ud0c0\ub0ac\ub2e4 . ","114 115\u000d\u000a\uc8fc\uc9c0 \uc54a\uae30 \ub54c\ubb38\uc5d0 \uac01 \uc9c0\uc5ed\uc758 \ubc30\ub2ec\uac00\uaca9\uc744 \uc608\uce21\ud558\uae30\ub294 \ubd88\uac00\ub2a5\ud558\ub2e4. \uc571\uc5d0\uc11c\ub294 \uc2e4\uc2dc\uac04 \uc815\ubcf4\ub97c \u000d\u000a\uc81c\uacf5\ud574\uc900\ub2e4\uace0 \ud558\uc9c0\ub9cc \uae30\uc5c5\uc774 \uc81c\uc2dc\ud558\ub294 \uc815\ubcf4\ub294 \uc120\ud0dd\uc801\uc774\uae30 \ub54c\ubb38\uc5d0 \ub178\ub3d9\uc790\ub4e4\uc5d0\uac8c \ud544\uc694\ud55c \uc815\u000d\u000a\ubcf4\ub97c \uc885\ud569\uc801\uc73c\ub85c \uc81c\uacf5\ud574\uc8fc\uc9c0 \ubabb\ud55c\ub2e4. \uacb0\uad6d \uae30\uc5c5\uc774 \uc81c\uacf5\ud558\ub294 \uc815\ubcf4\uc640 \ub178\ub3d9\uc790\ub4e4\uc774 \uccb4\uac10\ud558\ub294 \u000d\u000a\uc815\ubcf4 \uc0ac\uc774\uc5d0\ub294 \ud070 \uac04\uadf9\uc774 \ubc1c\uc0dd\ud558\uac8c \ub418\uace0, \uc774\ub7ec\ud55c \uac04\uadf9\uc740 \ub178\ub3d9\uacfc\uc815\uc5d0 \ub300\ud55c \ubd88\uc608\uce21\uc131\uacfc \uc54c\uace0\u000d\u000a\ub9ac\uc998\uc5d0 \ub300\ud55c \ubd88\uc2e0\uc744 \ud0a4\uc6b0\uace0 \uc788\ub2e4.\u000d\u000a\uc81c\ud55c\ub41c \uc778\ub825\uc73c\ub85c \uc6b4\uc601\ub418\ub294 \uc544\uc6c3\uc18c\uc2f1 \ubaa8\ub378\uc758 \uacbd\uc6b0, \ub178\ub3d9\uc790\ub4e4\uc740 \uadf8\ub9bc\uc790 \ub178\ub3d9\uc758 \uc99d\uac00\ubcf4\ub2e4\u000d\u000a\ub294 \uc2dc\uac04 \uad00\ub9ac\uc5d0 \ub300\ud55c \uc555\ubc15\uc744 \ub354\uc6b1 \ud06c\uac8c \ub290\ub07c\uba70 \uc774\ub7ec\ud55c \uc555\ubc15\uc740 \ud68c\uc0ac\uc758 \uc6b4\uc601 \ubc0f \ud1b5\uc81c \uc804\ub7b5\uc5d0 \u000d\u000a\ub530\ub77c \ud06c\uac8c \ub2ec\ub77c\uc9c4\ub2e4. \ud55c \ud50c\ub7ab\ud3fc\uc740 \uc5c5\ubb34\ud3c9\uac00\uc5d0 \ub530\ub77c \ub178\ub3d9\uc790\ub4e4\uc758 \ub4f1\uae09\uc744 \ubd84\ub958\ud558\uace0, \ub4f1\uae09\uc5d0 \u000d\u000a\ub530\ub77c \uc6d0\ud558\ub294 \uadfc\ub85c \uc2a4\ucf00\uc904\uc744 \uc120\ud0dd\ud560 \uc218 \uc788\uac8c \ud558\uace0 \uc788\ub2e4. 1\ub4f1\uae09\uc5d0\uc11c 2\ub4f1\uae09\uc73c\ub85c\ub9cc \ub5a8\uc5b4\uc838\ub3c4 \u000d\u000a\uc6d0\ud558\ub294 \uc2dc\uac04\ub300\uc758 \uc2a4\ucf00\uc904\uc744 \uc7a1\uc744 \uc218 \uc5c6\uace0, \ucf5c\uc774 \ubd80\uc871\ud574 \uc6d0\ud558\ub294 \ub9cc\ud07c \uc77c\uc744 \ud560 \uc218 \uc5c6\uac8c \ub41c\ub2e4. \u000d\u000a\ub4f1\uae09\uc744 \uc720\uc9c0\ud558\uae30 \uc704\ud574 \ub178\ub3d9\uc790\ub4e4\uc740 \uc54c\uace0\ub9ac\uc998\uc758 \uc5c5\ubb34\uc9c0\uc2dc\ub97c \uc5b4\uae30\uc9c0 \uc54a\uae30 \uc704\ud574 \ucd5c\uc120\uc744 \ub2e4\ud558\u000d\u000a\ub294\ub370, \ubb38\uc81c\ub294 \uc54c\uace0\ub9ac\uc998\uc758 \ud3c9\uac00 \uae30\uc900\uc774 \uba85\ud655\ud788 \ubc1d\ud600\uc9c4 \ubc14\uac00 \uc5c6\ub2e4\ub294 \uac83\uc774\ub2e4. \ud654\uc7a5\uc2e4\uc5d0 \uac00\uac70\u000d\u000a\ub098, \uc2dd\uc0ac\ub97c \uc704\ud55c \ud734\uc2dd \uc2dc\uac04\ub3c4 \ud3c9\uc810 \ud558\ub77d\uc5d0 \uc601\ud5a5\uc744 \ubbf8\uce58\uae30\uc5d0, \ub85c\uadf8\uc778\ud55c \uc774\ud6c4\ub85c\ub294 \uc2dc\uac04\uc5d0 \ub300\u000d\u000a\ud55c \uc790\uc728\uc131\uc774 \uadf9\ub3c4\ub85c \uc81c\ud55c\ub41c\ub2e4.\u000d\u000a\ubd84\ub9ac-\uc544\uc6c3\uc18c\uc2f1(\uc77c\ubc18\ub300\ud589) \uc6b4\uc601\ubaa8\ub378\uc5d0\uc11c \uc77c\ud558\ub294 \ub178\ub3d9\uc790\ub4e4\uc740 \uc5ec\ub7ec \uac1c\uc758 \ubc30\ub2ec\uc744 \ubb36\uc5b4\uc11c \ub2e4\ub2c8\uae30 \u000d\u000a\ub54c\ubb38\uc5d0, \uc5b4\ub5a4 \ubc30\ub2ec\uc5d0\uc11c \uc608\uc0c1\uce58 \ubabb\ud55c \ubcc0\uc218\uac00 \uc0dd\uae30\uba74 \uadf8 \ub4a4\uc5d0 \uc5ee\uc778 \ubaa8\ub4e0 \ubc30\ub2ec\uc774 \uc601\ud5a5\uc744 \ubc1b\ub294\u000d\u000a\ub2e4. \uc774 \ub54c\ubb38\uc5d0 \uc870\ub9ac\ub300\uae30 \uc2dc\uac04\uc744 \ub458\ub7ec\uc2f8\uace0 \uc74c\uc2dd\uc810\uacfc \ub178\ub3d9\uc790 \uc0ac\uc774\uc5d0 \uac08\ub4f1\uc774 \ube5a\uc5b4\uc9c0\uae30\ub3c4 \ud55c\u000d\u000a\ub2e4. \ud640 \uc190\ub2d8\uacfc \ubc30\ub2ec\uc8fc\ubb38\uc774 \ub3d9\uc2dc\uc5d0 \ubab0\ub9ac\ub294 \ud53c\ud06c\uc2dc\uac04\uc5d0, \uc74c\uc2dd\uc810\uc5d0\uc11c\ub294 \ub2f9\uc7a5 \ud640\uc5d0 \uc549\uc544\uc788\ub294 \u000d\u000a\uc190\ub2d8\uc758 \uc8fc\ubb38\uc744 \uc18c\ud640\ud788 \ud560 \uc218\uac00 \uc5c6\ub2e4. \ubc30\ub2ec\uc8fc\ubb38\uc744 \uc7a0\uc2dc \uaebc\uc11c \ud750\ub984\uc744 \ub2a6\ucd94\uae30\ub3c4 \ud558\uc9c0\ub9cc, \uc8fc\ubb38\u000d\u000a\uc774 \ubc00\ub9ac\uba74\uc11c \ubc30\ub2ec \ud3ec\uc7a5\uc774 \uc608\uc0c1\ubcf4\ub2e4 \ub2a6\ub294 \uc0c1\ud669\uc774 \ubc1c\uc0dd\ud558\uae30\ub3c4 \ud55c\ub2e4. \uc8fc\ubb38\uc744 \ud53d\uc5c5\ud558\ub7ec \uac14\ub294\ub370\u000d\u000a\uc544\uc9c1 \uc870\ub9ac \uc911\uc774\uc5b4\uc11c \uc870\ub9ac\ub300\uae30 \uc2dc\uac04\uc774 \uae38\uc5b4\uc9c0\uba74, \uc774\ubbf8 \ud53d\uc5c5\uc744 \uc644\ub8cc\ud558\uace0 \ubc30\ub2ec\ud1b5\uc5d0 \ub4e4\uc5b4 \uc788\ub294\u000d\u000a\uc74c\uc2dd, \uc774\ud6c4\uc758 \ud53d\uc5c5 \uc77c\uc815\uc5d0 \ubaa8\ub450 \ucc28\uc9c8\uc774 \uc0dd\uae34\ub2e4. \uc870\ub9ac\ub300\uae30\ub85c \uc778\ud55c \uac08\ub4f1\uc740 \ubc30\ub2ec \ub178\ub3d9\uc790 \ubaa8\ub450\u000d\u000a\uac00 \uacaa\ub294 \uc77c\uc774\uc9c0\ub9cc, \ubcf5\uc218 \ubc30\ucc28\ub97c \ud558\ub294 \uacbd\uc6b0 \uc870\ub9ac\ub300\uae30\uc758 \uc5ec\ud30c\uac00 \ub2e4\ub978 \uc8fc\ubb38\uc5d0\uae4c\uc9c0 \ubbf8\uce5c\ub2e4\ub294\u000d\u000a\uc810\uc5d0\uc11c \uc2dc\uac04 \uc555\ubc15\uc744 \ub354\uc6b1 \ud06c\uac8c \ub290\ub084 \uc218\ubc16\uc5d0 \uc5c6\ub2e4.\u000d\u000a\ud50c\ub7ab\ud3fc \ubc30\ub2ec\uacbd\uc81c\ub97c \ub4b7\ubc1b\uce68\ud558\ub294 \uc989\uc2dc\uc131\uc758 \ubb38\ud654\uc640 \uadf8\ub9bc\uc790 \ub178\ub3d9"," & 116\u000d\u000a5. \ub098\uac00\uba70\u000d\u000a\uc774 \uc5f0\uad6c\ub294 \ub178\ub3d9\uc5d0 \ub300\ud55c \uae30\uc220\ud1b5\uc81c\uc640 \uc0ac\ud68c\uc801 \ud1b5\uc81c\uc758 \uc0c1\ud638\uc791\uc6a9\uc744 \uc2dc\uac04\uc758 \uce21\uba74\uc5d0\uc11c \uc811\uadfc\ud588\u000d\u000a\ub2e4. \ud2b9\ud788 \ud50c\ub7ab\ud3fc \uae30\uc5c5\uc758 \uc6b4\uc601\uc804\ub7b5 \ubc0f \ud1b5\uc81c \ubc29\uc2dd\uc5d0 \uc758\ud574 \uadf8\ub9bc\uc790 \ub178\ub3d9\uc774 \uc99d\uac00\ud558\uace0 \uc788\ub2e4\ub294 \uc810\u000d\u000a\uc744 \ubc1d\ud614\ub2e4. \ub300\uae30\uc2dc\uac04\uc740 \ub178\ub3d9\uc2dc\uac04\uc774 \ubd88\uaddc\uce59\ud558\uc5ec \uc608\uce21\ub418\uc9c0 \uc54a\uae30 \ub54c\ubb38\uc5d0 \uc0dd\uae30\uc9c0\ub9cc \ud50c\ub7ab\ud3fc \uc74c\u000d\u000a\uc2dd \ubc30\ub2ec \uc0b0\uc5c5\uc5d0\uc11c \ub300\uae30\uc2dc\uac04\uc740 \uc8fc\ubb38\uc758 \ubd88\uc608\uce21\uc131 \uc774\uc678\uc5d0 \ub178\ub3d9\ub825\uc758 \uacf5\uae09\uc73c\ub85c \uc778\ud574 \ub298\uc5b4\ub098\uace0 \u000d\u000a\uc788\ub2e4. \uc989, \uc218\uc694\uc758 \ubd88\uc608\uce21\uc131\uc774 \uc544\ub2cc \ud68c\uc0ac\uc758 \uc6b4\uc601\uc804\ub7b5\uc5d0 \ub530\ub77c \ub9cc\ub4e4\uc5b4\uc9c4\ub2e4. \uc774\ub7ec\ud55c \uc774\uc720\ub85c \ucd5c\u000d\u000a\uadfc \ud574\uc678 \uc815\ubd80\uc640 \ubc95\uc6d0\uc5d0\uc11c\ub294 \ub300\ud45c\uc801\uc778 \uadf8\ub9bc\uc790 \ub178\ub3d9\uc778 \ub300\uae30\uc2dc\uac04\uc744 \uaddc\uc81c\ud558\uace0, \ub098\uc544\uac00 \ub300\uae30\uc2dc\u000d\u000a\uac04\uc5d0\ub3c4 \ubcf4\uc218\ub97c \ucc45\uc815\ud558\ub824\ub294 \uc6c0\uc9c1\uc784\uc774 \ubcf4\uc778\ub2e4. \ud2b9\ud788 \ubbf8\uad6d \ub274\uc695\uc2dc\uc758 \uacbd\uc6b0 \ub300\uae30\uc2dc\uac04\uc774 \ub298\uc5b4\ub098\u000d\u000a\uba74 \ucd5c\uc800 \uc6b4\uc784\uc774 \uc62c\ub77c\uac00\ub3c4\ub85d \uc694\uad6c\ud568\uc73c\ub85c\uc368, \uaddc\ubaa8\uc758 \uacbd\uc81c \ub2ec\uc131 \uc804\ub7b5\uc5d0 \ub530\ub77c \ubc1c\uc0dd\ud55c \ube44\uc6a9\uc744 \u000d\u000a\uc77c\ubc29\uc801\uc73c\ub85c \ub178\ub3d9\uc790\uc5d0\uac8c \ubd80\uacfc\ud558\ub294 \uac83\uc744 \ub9c9\uc558\ub2e4. \uc0c8\ub85c\uc6b4 \uae30\uc220\uc758 \ub3c4\uc785\uc73c\ub85c \ub178\ub3d9\uacfc\uc815\uc774 \uc7ac\uad6c\u000d\u000a\uc131\ub418\ub294 \uacfc\uc815\uc5d0\uc11c \uc0dd\uc0b0/\ube44\uc0dd\uc0b0\uc758 \uad6c\ubd84\uc740 \uc0c8\ub86d\uac8c \uad6c\ucd95\ub41c\ub2e4. \ubb34\uc5c7\uc774 (\ube44)\uc0dd\uc0b0\uc801\uc778 \uac83\uc73c\ub85c \uc815\u000d\u000a\uc758\ub418\uba70, \uc774\uc5d0 \ub530\ub77c \ubb34\uc5c7\uc774 \ube44\uac00\uc2dc\ud654\ub418\uace0 \uc788\ub294\uac00\uc5d0 \uc8fc\ubaa9\ud558\ub294 \uac83\uc740 \uc0c8\ub85c\uc6b4 \ub178\ub3d9 \ud1b5\uc81c\uc758 \ubc29\uc2dd\u000d\u000a\uacfc \uadf8 \uacb0\uacfc\ub97c \uc774\ud574\ud558\ub294 \uc5f0\uacb0\uc810\uc774\ub2e4. \u000d\u000a\uc6b0\ub9ac\ub294 \uc9c0\uae08 \uc774\ub978\ubc14 \u2018\uac00\uc131\ube44\u2019\uc758 \uc2dc\ub300\ub97c \uc0b4\uace0 \uc788\ub2e4. \ud22c\uc785\ud55c \uc790\uc6d0 \ub300\ube44 \uc0b0\ucd9c\ud55c \uacb0\uacfc\uc758 \ube44\uc728\u000d\u000a\uc774 \ub192\uc544\uc57c \ud55c\ub2e4\ub294 \uac00\uc131\ube44\ub77c\ub294 \ub17c\ub9ac\ub294 \uae08\uc735\uc790\ubcf8\uc758 \ub17c\ub9ac\uac00 \uc77c\uc0c1\uc0dd\ud65c\uc5d0 \ub110\ub9ac \uce68\ud22c\ud588\uc74c\uc744 \uc54c\u000d\u000a\ub824\uc900\ub2e4. \uc0dd\uc0b0\uc5d0\uc11c \ud22c\uc790\ub85c, \uc774\uc81c \ud22c\uc790\uc5d0\uc11c \uae30\uc220\ub85c \uc790\ubcf8\uc758 \uc131\uaca9\uc774 \ubc14\ub00c\uace0 \uc788\ub2e4. \uadf8\ub807\ub2e4\uba74 \uc0c8\u000d\u000a\ub86d\uac8c \ub4f1\uc7a5\ud55c \uae30\uc220\uc790\ubcf8\uc740 \ubb34\uc5c7\uc744 \uc0dd\uc0b0\uc801\uc778 \uac83\uc73c\ub85c \ud310\ub2e8\ud558\uace0, \uc0dd\uc0b0\uc801\uc778 \uac83\uc744 \ub9cc\ub4e4\uc5b4 \ub0b4\uae30 \uc704\u000d\u000a\ud574 \ubb34\uc5c7\uc744 \ube44\uc0dd\uc0b0\uc801\uc778 \uac83\uc73c\ub85c \ub9cc\ub4e4 \uac83\uc778\uac00. \uae30\uc220\uc744 \uc774\uc6a9\ud558\ub294 \ubc29\uc2dd\uc5d0 \ub530\ub77c \uadf8\ub9bc\uc790 \ub178\ub3d9\uc774\u000d\u000a\ub298\uc5b4\ub098\uace0 \uadf8 \ube44\uc6a9\uc774 \ub178\ub3d9\uc790\uc5d0\uac8c \uc804\uac00\ub420 \uc218 \uc788\ub2e4\ub294 \uac83\uc740, \uae30\uacc4\uc640 \uc778\uac04\uc774 \ud568\uaed8 \ud558\ub294 \uc0ac\ud68c\uc5d0\u000d\u000a\ub300\ud574 \uc0dd\uac01\ud574\ubcf4\uc544\uc57c \ud560 \ub610 \ub2e4\ub978 \uc9c0\uc810\uc744 \ubcf4\uc5ec\uc900\ub2e4.\u000d\u000a\uc6d0\ubb38\uc77d\uae30 & QR","116 117\u000d\u000a3\u000d\u000a\uc5d0\ud544\ub85c\uadf8"," & \u000d\u000a\ucee8\ud37c\ub7f0\uc2a4\u000d\u000a\uc138\uc158 \uc694\uc57d\u000d\u000a\ucee8\ud37c\ub7f0\uc2a4\uc758 \ub9c8\uc9c0\ub9c9 \uc138\uc158\uc740 \uc218\uc0c1\uc790\ub4e4\uc758 \ubc1c\ud45c\uc640 \ucc38\uc11d\uc790\ub4e4\uc758 \uc9c8\uc758\uc751\ub2f5\uc73c\ub85c \uad6c\uc131\ub418\uc5c8\ub2e4. \uc218\uc0c1\u000d\u000a\uc790\ub4e4\uc774 \uacf5\ud1b5\uc801\uc73c\ub85c \uac15\uc870\ud588\ub358 \uc774\uc57c\uae30\ub294 \uaf2d \ud544\uc694\ud55c \uc5f0\uad6c\uc784\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \ud558\ub294 \uc774\ub4e4\uc774 \uc5c6\uc5b4 \uc9c1\uc811 \u000d\u000a\ud558\uac8c \ub418\uc5c8\ub2e4\ub294 \uac83\uc774\uc5c8\ub2e4. \uc0ac\ud68c\ub97c \ub9e1\uc740 \uc774\uc815\ud544 \uc120\uc815\uc704\uc6d0 \uac04\uc0ac\ub294 \uc774\ub97c \u201c\ub0a8\ub4e4\uc774 \uc548 \ud558\uba74 \ub0b4\uac00 \ud55c\u000d\u000a\ub2e4\u201d\ub294 \uc790\uc138\ub85c \uc694\uc57d\uc744 \ud588\ub2e4. \uc218\uc0c1\uc790\ub4e4\uc758 \ubc1c\ud45c \ub0b4\uc6a9\uc740 \ud604\uc7a5\uc131\uc774\ub780 \ubb34\uc5c7\uc774\uace0, \uc5b4\ub5bb\uac8c \uc5f0\uad6c\ub97c \ud588\ub294\u000d\u000a\uc9c0, \uadf8\ub9ac\uace0 \uae30\uc874\uc758 \ud559\uacc4\uc5d0 \ubc14\ub77c\ub294 \ubc14\ub294 \ubb34\uc5c7\uc778\uc9c0 \ub4f1\uc5d0 \ub300\ud55c \uac83\uc774\uc5c8\ub2e4. \uac00\ub839, \uc11c\uc6b8\ub300\ud559\uad50 \ud658\uacbd\ub300\u000d\u000a\ud559\uc6d0\uc5d0\uc11c \ubc15\uc0ac\ud559\uc704\ub17c\ubb38\uc744 \uc4f0\uace0 \uc788\ub294 \uae40\uc6b0\ucc3d \ub2d8\uc740 \uc6d4\uc131 \ud575\ubc1c\uc804\uc18c \ubd80\uadfc \ub9c8\uc744\uc5d0\uc11c 8\uac1c\uc6d4 \ub3d9\uc548 \uba38\u000d\u000a\ubb3c\ub824 \uc5f0\uad6c\ud588\ub358 \uacbd\ud5d8\uc744 \ubc14\ud0d5\uc73c\ub85c \ubc1c\ud45c\ud588\ub2e4. \uadf8\ub294 \uc790\uc2e0\uc774 \ud638\uae30\uc2ec\uc744 \uac00\uc9c4 \uc774\uc288\uac00 \uc788\ub294, \uc774\uc57c\uae30 \ud560 \u000d\u000a\uacf3\uc774 \uc788\ub294 \uacf3, \uc774 \ub9e5\ub77d\uc5d0\uc11c \uc2a4\uc2a4\ub85c\uac00 \uc704\uce58\ud558\uace0 \uc788\ub294 \uacf3\uc744 \ud604\uc7a5\uc73c\ub85c \uaddc\uc815\ud588\ub2e4. \ub610\ud55c, \uc5f0\uad6c\uc18c \uc5ed\u000d\u000a\uc2dc\ub3c4 \ud604\uc7a5\uc784\uc744 \uac15\uc870\ud558\uae30\ub3c4 \ud588\ub2e4. \uae40\uc0c8\ub86c \uc5f0\uad6c\uc6d0\uc740 \uc790\uc2e0\uc758 \uc5f0\uad6c\uac00 3,000\uba85 \uc774\uc0c1\uc758 \ud45c\ubcf8\uc744 \ud65c\uc6a9\u000d\u000a\ud55c \ucd5c\ucd08\uc758 \uc131\uc18c\uc218\uc790 \uc5f0\uad6c\uc784\uc744 \uac15\uc870\ud558\uba74\uc11c\ub3c4 \ub2e8\uc21c\ud788 \uacc4\ub7c9\uc801 \uc9c0\ud45c\ub97c \ucc3d\ucd9c\ud558\ub824\ub294 \uac8c \uc544\ub2c8\ub77c \uadf8\ub4e4\u000d\u000a\uc758 \ubaa9\uc18c\ub9ac\ub97c \uacbd\uccad\ud558\ub294 \uc790\uc138\ub85c \uc5f0\uad6c\ud588\uc74c\uc744 \ubc1d\ud614\ub2e4. \ud604\uc7ac \uc9c4\uc2e4\uacfc\ud654\ud574\uc704\uc6d0\ud68c\uc5d0\uc11c \uc77c\ud558\uace0 \uc788\ub294 \ud558\u000d\u000a\uacbd\ucca0 \uc5f0\uad6c\uc6d0\uc740 \ud22c\uc7c1\uc758 \uc5ed\uc0ac\ub9cc\uc774 \uc544\ub2c8\ub77c \ud53c\ud574\uc758 \uacfc\uc815\uc744 \uae30\ub85d\ud558\ub294 \ub370 \uad00\uc2ec\uc744 \ub450\uace0 \uc788\ub2e4\uace0 \ubc1d\ud614\u000d\u000a\ub2e4. \ub610\ud55c, \uadf8 \uacfc\uc815\uc5d0\uc11c \ubc29\ubc95\ub860\uacfc \uad00\ub828\ub41c \uc5ec\ub7ec \uace0\ubbfc\ub4e4\uc744 \ud1a0\ub85c\ud558\uae30\ub3c4 \ud588\ub2e4. \ub958\ud55c\uc2b9 \uacf5\uacf5\uc6b4\uc218\ub178\uc870 \u000d\u000a\uc870\uc9c1\ubcf8\ubd80\uc7a5\uc740 \ucd5c\uadfc \ud50c\ub7ab\ud3fc\uc774\ub098 \uc820\ub354 \ubb38\uc81c\uc5d0 \ub300\ud55c \uad00\uc2ec\uc758 \uc99d\uac00\uc5d0 \ub530\ub77c \ub178\ub3d9 \ubb38\uc81c\uc5d0 \ub300\ud55c \uc5f0\uad6c\uac00 \u000d\u000a\uc904\uc5b4\ub4e4\uace0 \uc788\ub294 \uc0c1\ud669\uc5d0 \ub300\ud574 \uc544\uc26c\uc6c0\uc744 \ud45c\ud604\ud558\uae30\ub3c4 \ud588\ub2e4. \uc774 \ub0a0\uc758 \ud589\uc0ac\ub294 \ud604\uc7a5\uc9c0\uc2dd\uc5d0 \ub300\ud55c \uaddc\uc815, \u000d\u000a\uc544\uc6b8\ub7ec \uac01\uc790\uac00 \ud544\uc694\ub85c \ud558\ub294 \uc5f0\uad6c\ub4e4\uc774 \ub2e4\ub978 \uc0c1\ud669\uc5d0\uc11c \uc9c0\uc2dd\uc758 \ucd95\uc801\uacfc \uacf5\uc720, \uadf8\ub9ac\uaca8 \uc5f0\uacb0\uc774 \uc911\uc694\u000d\u000a\ud558\ub2e4\ub294 \uc810\uc744 \ud655\uc778\ud558\uba70 \ub9c8\ubb34\ub9ac\ub418\uc5c8\ub2e4.","!"," & \u000d\u000a\ubcf8 \ucc45\uc790\ub294 \uc7ac\ub2e8\ubc95\uc778 \ube0c\ub77c\uc774\uc5b8\uc784\ud329\ud2b8\uc758 \uc9c0\uc6d0\uc73c\ub85c \uc81c\uc791\ub418\uc5c8\uc2b5\ub2c8\ub2e4.\u000d\u000a\ud3b4 \ub0b8 \ub0a0\u000d\u000a\uc5ee \uc740 \uc774\u000d\u000a\ud3b4 \ub0b8 \uacf3\u000d\u000a\ub514 \uc790 \uc778\u000d\u000a\uc8fc \uc18c\u000d\u000a\uc804 \ud654\u000d\u000a\ud648\ud398\uc774\uc9c0\u000d\u000aISBN\u000d\u000a2023\ub144 06\uc6d4 23\uc77c\u000d\u000a<\ud604\uc7a5\uc9c0\uc2dd\u00d7\uc88b\uc740\uc5f0\uad6c> \uc120\uc815\uc704\uc6d0\ud68c\u000d\u000a\uc0ac\ub2e8\ubc95\uc778 \uc2dc\ubbfc\u000d\u000a\uc18c\uc15c\ubca4\ucc98 (\uc8fc)\ub3c5\ub9bd\ucd9c\ud310\uc2dd\ubaa9\uc77c\u000d\u000a\uc11c\uc6b8\uc2dc \uc885\ub85c\uad6c \ub3d9\uc22d\uae38 68, 3\uce35 (\ub3d9\uc22d\ub3d9, \ud638\uc740\ube4c\ub529)\u000d\u000a070-7733-3925\u000d\u000awww.simin.or.kr\u000d\u000a979-11-973824-2-0 (95300)\u000d\u000a9 791197 382420\u000d\u000a95300\u000d\u000a ISBN 979-11-973824-2-0"]